INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 101101, 464);
