INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 101201, 511);
