INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 101201, 513);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 101201, 514);
