INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 101702, 634);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 101702, 635);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ハイターイ！　元気な\nあいさつが一番さ～！", 101702, 636);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 101702, 637);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 101702, 638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 101702, 639);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 101702, 640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 101702, 641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 101702, 642);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 101702, 643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 101702, 644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 101702, 645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 101702, 646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 101702, 647);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…もっと\nおかわりがほしいさー", 101702, 648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 101702, 649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 101801, 651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 101801, 652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 101801, 653);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "キンコンカンコ～ン♪\n授業をはじめま～す", 101801, 654);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "はー…\nちょっと肩が\nこっちゃった…", 101801, 655);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 101801, 656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 101801, 657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 101801, 658);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 101801, 659);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 101801, 660);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 101801, 661);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 101801, 662);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 101801, 663);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "学校に\n遅刻しないように\n早く準備しないとね", 101801, 664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 101801, 665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 101801, 666);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "もしかして…\n少し露出多いかしら？", 101801, 667);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 101801, 668);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪　あなたも\n私の生徒になる？", 101801, 669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 101801, 670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 101801, 671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動部の顧問も\nできるかしら？", 101801, 672);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "学校の給食でも\n食べたいわぁ～", 101801, 673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "いい参考書を\n見つけちゃった♪", 101801, 674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 101802, 676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 101802, 677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 101802, 678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 101802, 679);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 101802, 680);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 101802, 681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 101802, 682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 101802, 683);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 101802, 684);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 101802, 685);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 101802, 686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 101802, 687);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 101802, 688);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝の特別授業\nしてあげましょうか？", 101802, 689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 101802, 690);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 101802, 691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "もしかして…\n少し露出多いかしら？", 101802, 692);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "私の膝枕と、どっちが\nフカフカかしら？", 101802, 693);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 101802, 694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 101802, 695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 101802, 696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動部の顧問も\nできるかしら？　", 101802, 697);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あん！\nお胸にこぼしちゃった", 101802, 698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 101802, 699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "おにいちゃんは、\nにんじん食べられる？", 102001, 701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "えへへ、\nお絵かきしよ～♪", 102001, 702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あのね、ミミと\nあそんでほしいな～", 102001, 703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 102001, 704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "うさぎさん、\nぴょんぴょん\nぴょーん♪", 102001, 705);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "おにいちゃんの絵、\nかいてあげるね♪", 102001, 706);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 102001, 707);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ふわぁ！\nおにいちゃんの手、\nおっきいね～", 102001, 708);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "やった～！\nおにいちゃんが\nほめてくれた～", 102001, 709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ねえねえ\nおにいちゃん、\n一緒にあそぼっ♪", 102001, 710);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "おにいちゃん、\n遊んでくれるの？\n　わぁい♪", 102001, 711);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "わぁい！\nおにいちゃんだ～♪\nぎゅ～っ！", 102001, 712);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あのねあのね、\nミミの頭ポンポンって\nしてほしいな", 102001, 713);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おひさまさん、\nおはよ～！\n毎朝、早おきだね～", 102001, 714);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "うわぁ！\nお日さまがすごーく\n高いところにある～", 102001, 715);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おにいちゃん\nおやすみのご本\nよんで～", 102001, 716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "ミミも、\nうさぎさんみたいに、\nかわいくなりたいな♪", 102001, 717);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "のんびり～♪\nゆったり～♪\nまったり～♪", 102001, 718);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わぁ～かわいい～♪\nミミ、幸せ～♪\n抱きついていいかな？", 102001, 719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いいにおい～♪\nお鼻ピクピク♪\nうさぎさんも喜ぶよ♪", 102001, 720);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～♪\nぬくぬく～♪\nポカポカ～♪", 102001, 721);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "ふ、ふぇ～　ミミ、\n運動苦手だよ～", 102001, 722);
