INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 102201, 804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "楽しいゲームって\nやめどきが\n見つからないわ", 102201, 805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 102201, 806);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 102201, 807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 102201, 808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 102201, 809);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 102201, 810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 102201, 811);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 102201, 812);
