INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 102201, 814);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 102201, 815);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜はこれからよ！\n朝まで\nゲームするからね！", 102201, 816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "アカリみたいに\n可愛くなるのは\n難しいかな", 102201, 817);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 102201, 818);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "可愛い…けど、\n私には似合わないかな", 102201, 819);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はあ、リラックスして\nゲームができるわ", 102201, 820);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ん…暖かくて、\n寝落ちしちゃいそう", 102201, 821);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 102201, 822);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 102201, 823);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 102201, 824);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 102202, 826);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 102202, 827);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "レアカードをゲット！\nすごいでしょ！？", 102202, 828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 102202, 829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "楽しいゲームって\nやめどきが\n見つからないわ", 102202, 830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 102202, 831);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 102202, 832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 102202, 833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 102202, 834);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ねえ、見て！\n新しいゲームを\n買って来たの！", 102202, 835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 102202, 836);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 102202, 837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 102202, 838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 102202, 839);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 102202, 840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜はこれからよ！\n朝まで\nゲームするからね！", 102202, 841);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 102202, 842);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 102202, 843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "可愛い…けど、\n私には似合わないかな", 102202, 844);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 102202, 845);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 102202, 846);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "最近ゲームばかりだし\nたまにはいいかな？", 102202, 847);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 102202, 848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 102202, 849);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 102301, 851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 102301, 852);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 102301, 853);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 102301, 854);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この部屋、ぷうきちを\n置く場所はないのかな", 102301, 855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 102301, 856);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 102301, 857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 102301, 858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 102301, 859);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 102301, 860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 102301, 861);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 102301, 862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 102301, 863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 102301, 864);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 102301, 865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 102301, 866);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 102301, 868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 102301, 869);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 102301, 870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 102301, 871);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしい～\nぷうきちも\n食べてみる？", 102301, 873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ぷうきち！\nこの本、読んで～！", 102301, 874);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 102501, 901);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 102501, 902);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "どうしたら転ばなく\nなるんでしょうか？", 102501, 903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 102501, 904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 102501, 905);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "おいしい料理で\nお出迎えしなくちゃ！", 102501, 906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "一生懸命\nご奉仕しますよ～♪", 102501, 907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "耳かきしましょうか？\nえ？　こわい？", 102501, 908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 102501, 909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "おかえりなさいませ\nご主人さま♪", 102501, 910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "すみません\nすみません\nすみません～～", 102501, 911);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 102501, 912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私のこと、\n専属メイドと思って\n頼ってくださいね", 102501, 913);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 102501, 914);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 102501, 915);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 102501, 916);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 102501, 917);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 102501, 918);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\nあとでお洗濯\nしてあげますね", 102501, 919);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 102501, 920);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここでお洗濯ものを\n乾かしましょうか", 102501, 921);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "あわわわっ…\nこ、転びそうです～！", 102501, 922);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これくらい\nおいしいものを\n作ってみたいです～", 102501, 923);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 102501, 924);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 102502, 926);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 102502, 927);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "どうやったら、立派な\nメイドになれるのかな", 102502, 928);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 102502, 929);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ご用があれば\nなんでも言って\nくださいね", 102502, 930);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 102502, 931);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 102502, 932);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 102502, 933);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 102502, 934);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私、またなにか\nやっちゃいましたか？", 102502, 935);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 102502, 936);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "転んだふりであの人に\n…って！\nなんでもないですよ！", 102502, 937);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 102502, 938);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 102502, 939);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 102502, 940);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 102502, 941);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 102502, 942);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 102502, 943);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\nあとでお洗濯\nしてあげますね", 102502, 944);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 102502, 945);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 102502, 946);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動しようとすると\nいつも怪我するん\nですよね…", 102502, 947);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 102502, 948);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 102502, 949);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 102701, 951);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 102701, 952);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 102701, 953);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 102701, 954);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 102701, 955);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 102701, 956);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 102701, 957);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 102701, 958);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 102701, 959);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お願いがあるのです。\n新しい研究の実験台に\n…", 102701, 960);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 102701, 961);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 102701, 962);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 102701, 963);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 102701, 964);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 102701, 965);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 102701, 966);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 102701, 967);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はっ！\nリラックスしたら\n閃きましたわ！", 102701, 968);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あら、キメラの素体に\nぴったりですわ", 102701, 969);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 102701, 970);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あたたかい…\n加熱調合にも\n使えそうですわね", 102701, 971);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 102701, 972);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 102701, 973);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 102701, 974);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 102702, 976);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 102702, 977);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "新しい薬\nどうやってあの方に\n飲ませようかしら", 102702, 978);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 102702, 979);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 102702, 980);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 102702, 981);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 102702, 982);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 102702, 983);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 102702, 984);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お願いがあるのです\n新しい研究の\n実験台に…", 102702, 985);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 102702, 986);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 102702, 987);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 102702, 988);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 102702, 989);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 102702, 990);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 102702, 991);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "私ももう少し\n外見に気を配る\nべきでしょうか…", 102702, 992);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はっ！\nリラックスしたら\n閃きましたわ！", 102702, 993);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 102702, 994);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 102702, 995);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 102702, 996);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 102702, 997);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 102702, 998);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 102702, 999);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 102801, 1001);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 102801, 1002);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 102801, 1003);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 102801, 1004);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 102801, 1005);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 102801, 1006);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "焼きそばパンが\n食べたいな～\nあいつと…", 102801, 1007);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ねぇあんた、どこか\n安いお店知らない？", 102801, 1008);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 102801, 1009);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 102801, 1010);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 102801, 1011);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 102801, 1012);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 102801, 1013);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 102801, 1014);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 102801, 1015);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 102801, 1016);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 102801, 1017);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "これで仕事は一段落ね\nちょっと休憩…", 102801, 1018);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うふふ、\nかわいいものを\n見るのは幸せね～", 102801, 1019);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 102801, 1020);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "風邪を引かないように\n身体を温めなくちゃ", 102801, 1021);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 102801, 1022);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 102801, 1023);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "知識から過去を学んで\n未来に活かすわよ", 102801, 1024);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "安売りのお店の情報\n整理しないと…", 102802, 1026);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 102802, 1027);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 102802, 1028);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 102802, 1029);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 102802, 1030);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "焼きそばパンが\n食べたいな～\nあいつと…", 102802, 1031);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あいつと一緒に…\n色々なところに\nいきたいな", 102802, 1032);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今度買い物に\n付き合いなさい\nセールがあるのよ", 102802, 1033);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 102802, 1034);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 102802, 1035);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あんたとこうして\nのんびりするのも\nいいものね", 102802, 1036);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 102802, 1037);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 102802, 1038);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 102802, 1039);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 102802, 1040);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 102802, 1041);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 102802, 1042);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "これで仕事は一段落ね\nちょっと休憩…", 102802, 1043);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 102802, 1044);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 102802, 1045);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "風邪を引かないように\n身体を温めなくちゃ", 102802, 1046);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 102802, 1047);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "しっかり食べて\n栄養補給、栄養補給！", 102802, 1048);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "知識から過去を学んで\n未来に活かすわよ", 102802, 1049);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 102901, 1051);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 102901, 1052);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 102901, 1053);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "彼とデュエット\nしてみたいな～♪", 102901, 1054);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 102901, 1055);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 102901, 1056);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "写真集見なくても、\n本物がここに\nいるのにな～", 102901, 1057);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "握手会の練習、\n付き合ってくれない？", 102901, 1058);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "写真集のポーズ\n練習中～！\nど、どうかな？", 102901, 1059);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 102901, 1060);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 102901, 1061);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 102901, 1062);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 102901, 1063);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 102901, 1064);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 102901, 1065);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 102901, 1066);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 102901, 1067);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 102901, 1068);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい～♪\nこれは強力な\nライバル登場かな！", 102901, 1069);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 102901, 1070);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 102901, 1071);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 102901, 1072);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 102901, 1073);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 102901, 1074);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 102902, 1076);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 102902, 1077);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "みんながくれた\nファンレター…\n私の大事な宝物…", 102902, 1078);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "彼とデュエット\nしてみたいな～♪", 102902, 1079);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 102902, 1080);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 102902, 1081);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 102902, 1082);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 102902, 1083);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 102902, 1084);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 102902, 1085);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 102902, 1086);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 102902, 1087);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 102902, 1088);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 102902, 1089);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 102902, 1090);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 102902, 1091);
