INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 102902, 1095);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 102902, 1096);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 102902, 1097);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 102902, 1098);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 102902, 1099);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 103001, 1101);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 103001, 1102);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 103001, 1103);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "むむ、アクダイカンの\n気配がするデース", 103001, 1104);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "クニトリ合戦、\n楽しみデース！", 103001, 1105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ショーグンと\n出会えて、\nワタシは果報者デス！", 103001, 1106);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 103001, 1107);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 103001, 1108);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 103001, 1109);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 103001, 1110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 103001, 1111);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 103001, 1112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 103001, 1113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようデス！\n今日も一日\n忍法日和デス！", 103001, 1114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 103001, 1115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日もきっと\nいい日デスよ、\nショーグン！", 103001, 1116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "ちょんまげに\nしてみるのも\n面白いかもデス！", 103001, 1117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 103001, 1118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 103001, 1119);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！", 103001, 1120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "身体を暖めて\nイクサに向かうデス！", 103001, 1121);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 103001, 1122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "うーん、\n美味しいデス…\n幸せデス～", 103001, 1123);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 103001, 1124);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 103002, 1126);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 103002, 1127);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 103002, 1128);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "むむ、アクダイカンの\n気配がするデース", 103002, 1129);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "クニトリ合戦\n楽しみデース！", 103002, 1130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 103002, 1131);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ショーグンの身は\n何があってもワタシが\n守るデス！", 103002, 1132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 103002, 1133);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 103002, 1134);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 103002, 1135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 103002, 1136);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 103002, 1137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 103002, 1138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 103002, 1139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "もうお昼デス\nお昼寝するデスか？", 103002, 1140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろおネム\nデース…\n一緒に寝るデス？", 103002, 1141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 103002, 1142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 103002, 1143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 103002, 1144);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！", 103002, 1145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "身体を暖めてイクサに\n向かうデス！", 103002, 1146);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 103002, 1147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "美味しいデス…\n酢昆布を和えて\nみたいデス", 103002, 1148);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "物事を知って\n来たるべきイクサに\n活かすデス", 103002, 1149);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 103101, 1151);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "父は、いつも男性に\nきびしいですね", 103101, 1152);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "占ってほしい人は、\nいつでも言って\nください", 103101, 1153);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 103101, 1154);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この部屋、風水的にも\n運気が上がりそうです", 103101, 1155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 103101, 1156);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 103101, 1157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "女難の相が\n出ていますね…\n気をつけてください", 103101, 1158);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "今日のあなたの運勢は\n大きょ…\n大吉です", 103101, 1159);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 103101, 1160);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 103101, 1161);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 103101, 1162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 103101, 1163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n今日の運勢を\n占いましょうか？", 103101, 1164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 103101, 1165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 103101, 1166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 103101, 1167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "一休みしましょう。\n憑かれて疲れました", 103101, 1168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい…\n付喪神が宿らないかな", 103101, 1169);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 103101, 1170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "気持ちいいです…\n極楽浄土みたい…", 103101, 1171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 103101, 1172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お父さんも\n食べたいの？\n死んでるのに？", 103101, 1173);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本…\nもしかして予言書？", 103101, 1174);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ここにいる幽霊は\nみんな幸せそうです", 103102, 1176);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 103102, 1177);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 103102, 1178);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 103102, 1179);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 103102, 1180);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 103102, 1181);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 103102, 1182);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この部屋、風水的にも\n運気が上がりそうです", 103102, 1183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "シノブには指一本\n触れさせん！\nガブッ！！", 103102, 1184);
