INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 103102, 1187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "前世って信じますか？\n私とあなたは\nもしかすると…", 103102, 1188);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 103102, 1189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "明るい時間は\nあまり得意では\nないんです…", 103102, 1190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 103102, 1191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 103102, 1192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "うとうと…\nあれ…\n走馬灯が見える…", 103102, 1193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい…\nお父さんみたい…", 103102, 1194);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 103102, 1195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "快適です…\n幽霊もどんどん\n集まってきますね…", 103102, 1196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 103102, 1197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お父さんの分も、\n私が食べるね", 103102, 1198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本の占い\n私の占いと違う…", 103102, 1199);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 103201, 1201);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 103201, 1202);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "一日も早く、\n一人前の商人に\nなりたいものです", 103201, 1203);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 103201, 1204);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 103201, 1205);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 103201, 1206);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 103201, 1207);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 103201, 1208);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 103201, 1209);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 103201, 1210);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お金で解決するのは\n悪いことでは\nありませんわよね？", 103201, 1211);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "その、よろしければ\n私とお散歩など\nいかがです？", 103201, 1212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 103201, 1213);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 103201, 1214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 103201, 1215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 103201, 1216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 103201, 1217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 103201, 1218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 103201, 1219);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 103201, 1220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 103201, 1221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 103201, 1222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 103201, 1223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "経済の仕組みを\nしっかりと\n学ばなくては…", 103201, 1224);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 103202, 1226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 103202, 1227);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 103202, 1228);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 103202, 1229);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この狭さも\n慣れれば\n快適ですわ♪", 103202, 1230);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 103202, 1231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 103202, 1232);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 103202, 1233);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 103202, 1234);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 103202, 1235);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今度一緒に…\nパーティにでも\n行きませんこと？", 103202, 1236);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "その、よろしければ\n私とお散歩など\nいかがです？", 103202, 1237);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたさまの優しさは\nお金には\n代えられませんわね", 103202, 1238);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 103202, 1239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 103202, 1240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 103202, 1241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 103202, 1242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 103202, 1243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 103202, 1244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 103202, 1245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 103202, 1246);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "目標は遠くに\n置いた方が励みに\nなりますわ！", 103202, 1247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 103202, 1248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "商人って、物語では\n悪役にされやすいの\nですね…", 103202, 1249);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "家の中のものを使って\n物ボケ、考えるべ！", 103301, 1251);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 103301, 1252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "うちに塀ができたら、\nへー、って言いたい！", 103301, 1253);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 103301, 1254);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "エリザベスも\n連れてきてぇ～", 103301, 1255);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 103301, 1256);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 103301, 1257);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 103301, 1258);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 103301, 1259);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 103301, 1260);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 103301, 1261);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 103301, 1262);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 103301, 1263);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 103301, 1264);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼だ～腹が減っては\n笑いはできぬ～！", 103301, 1265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 103301, 1266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 103301, 1267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ナイスなイスに\n座りたい、\nなんちゃって！", 103301, 1268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "めんこいのは、\nスベっても許される\nからずるいべな～？", 103301, 1269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "必要以上に\n癒されちまうべや～", 103301, 1270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽかだ…\nウケた会場みてぇだ", 103301, 1271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "お笑い芸人は、\n体が資本だべさ～！", 103301, 1272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 103301, 1273);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ネタ作りのヒントが\n載ってねぇか探すべ", 103301, 1274);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "さぁ今日も張り切って\n漫才の練習するべさ！", 103302, 1276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この部屋でも、商品\n売っていいんだべか？", 103302, 1277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 103302, 1278);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 103302, 1279);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "エリザベスも\n連れてきてぇ～", 103302, 1280);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 103302, 1281);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 103302, 1282);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 103302, 1283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あんちゃん、百連\nツッコミ特訓だ～！", 103302, 1284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 103302, 1285);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 103302, 1286);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 103302, 1287);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 103302, 1288);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 103302, 1289);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 103302, 1290);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 103302, 1291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "うーん…衣装、\nずれてねぇべか？", 103302, 1292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ナイスなイスに\n座りたい\nなんちゃって！", 103302, 1293);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "はう～\nなまらめんこいべや～", 103302, 1294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "必要以上に\n癒されちまうべや～", 103302, 1295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "笑いをとることを\n忘れるほど\nあったけぇべ～", 103302, 1296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 103302, 1297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 103302, 1298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "幅広く教養を\n身につけて\nいいネタ作るべ！", 103302, 1299);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "通りにいる\nカップル…爆発\nすればいいのに…", 103401, 1301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 103401, 1302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "今日は一日、\n外に出ない！\nもう決めたわ！", 103401, 1303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 103401, 1304);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 103401, 1305);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "デートとか…\n誘っても嫌がられ\nないかな…？", 103401, 1306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もしかして運命の\n相手だったり…\nするのかな", 103401, 1307);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 103401, 1308);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 103401, 1309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 103401, 1310);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 103401, 1311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 103401, 1312);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 103401, 1313);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 103401, 1314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おっひるー♪\nなに食べよっかな～♪", 103401, 1315);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 103401, 1316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 103401, 1317);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 103401, 1318);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうの男の子に\nプレゼントして\nもらいたいな…", 103401, 1319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "うわあ、\nこれ、私の好きな\n香りだー！", 103401, 1320);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 103401, 1321);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 103401, 1322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nおーいしー！\n…太らないよね？", 103401, 1323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 103401, 1324);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 103402, 1326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今日は\nのんびり\n過ごそうかな～", 103402, 1327);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あ…\nお皿、洗って\nなかった…", 103402, 1328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "もう少し、大人の\n魅力が出せるように\nなりたいな", 103402, 1329);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "素敵な彼氏…\n欲しいなぁ…", 103402, 1330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "彼と私\n釣り合うかな…\n大丈夫よね？", 103402, 1331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 103402, 1332);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 103402, 1333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 103402, 1334);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 103402, 1335);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お姉さんが～\n可愛がって\nあげようか～？", 103402, 1336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 103402, 1337);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 103402, 1338);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "さーて、今日も\nいっちょやるぞー！", 103402, 1339);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "後でのんびり\nストレッチでも\nしようっと♪", 103402, 1340);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 103402, 1341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "仕事の前に、髪とか\n整えないと…", 103402, 1342);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 103402, 1343);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 103402, 1344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "うわあ\nこれ、私の好きな\n香りだー！", 103402, 1345);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかーい…\nだ、だらけ\nちゃいそう…", 103402, 1346);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "地道な努力が\nリア充への\n第一歩！", 103402, 1347);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おーいしっ♪\nこのために\n生きてるわねえ", 103402, 1348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 103402, 1349);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 103801, 1351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 103801, 1352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 103801, 1353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ちょっと、休憩…\n本でも読もうかな", 103801, 1354);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "もっとたくさんの本に\n囲まれて生活したいな", 103801, 1355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "むかしむかしある所に\nシオリと\nあの人が…", 103801, 1356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 103801, 1357);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 103801, 1358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あ…お姉ちゃんと\n約束があったんだ…", 103801, 1359);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 103801, 1360);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 103801, 1361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 103801, 1362);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 103801, 1363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 103801, 1364);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 103801, 1365);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 103801, 1366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？", 103801, 1367);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "少し休もう\n…やっぱり\nたくさん休もう", 103801, 1368);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいものが\n枕元にあると\n安心できるんですよ", 103801, 1369);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 103801, 1370);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "こうして温まって\nいると、病気も\n治る気がします", 103801, 1371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 103801, 1372);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "少食ですけど\nこれならたくさん\n食べられそうです", 103801, 1373);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "続きが気になって\n読むのを止める\nタイミングが…", 103801, 1374);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "しっかり休まないと\n早く回復するように", 103802, 1376);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 103802, 1377);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 103802, 1378);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 103802, 1379);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 103802, 1380);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "むかしむかしある所に\nシオリと\nあの人が…", 103802, 1381);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人の\nためにも早く\n病気を治さないと…", 103802, 1382);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 103802, 1383);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "新記録です！\n一週間連続、\n平熱を保ちました！", 103802, 1384);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 103802, 1385);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 103802, 1386);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 103802, 1387);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 103802, 1388);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 103802, 1389);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 103802, 1390);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 103802, 1391);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 103802, 1392);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "少し休もう\n…やっぱり\nたくさん休もう", 103802, 1393);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子がいれば\n病室に一人でも\n寂しくないですね", 103802, 1394);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 103802, 1395);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "こうして温まって\nいると、病気も\n治る気がします", 103802, 1396);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "私も、少しは\n体力をつけないと\nいけませんよね　", 103802, 1397);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いっぱい食べて\n健康な体にならないと", 103802, 1398);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "続きが気になって\n読むのを止める\nタイミングが…", 103802, 1399);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 104001, 1401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あ、忘れずに\nお花に水を\nあげなきゃ…", 104001, 1402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 104001, 1403);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 104001, 1404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 104001, 1405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 104001, 1406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 104001, 1407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 104001, 1408);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 104001, 1409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 104001, 1410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 104001, 1411);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 104001, 1412);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 104001, 1413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 104001, 1414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 104001, 1415);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 104001, 1416);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "私がもっと\n可愛ければ、\n友達できるかなあ？", 104001, 1417);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 104001, 1418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいなあ。\nこの子とも\n友達になりたい…", 104001, 1419);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りです…\n精神統一して\n友達のイメトレ…", 104001, 1420);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "灯りを見てると、\n心がなごみます～", 104001, 1421);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 104001, 1422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 104001, 1423);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 104001, 1424);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "友達作りは、焦らず\n慌てず、じっくりと…", 104002, 1426);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 104002, 1427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 104002, 1428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "今度、みなさんと\nピクニックとか…\nいってみたいな", 104002, 1429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ぼっちでもいい\nたくましく育てば\n…なんて", 104002, 1430);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 104002, 1431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 104002, 1432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 104002, 1433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 104002, 1434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 104002, 1435);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 104002, 1436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 104002, 1437);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 104002, 1438);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 104002, 1439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 104002, 1440);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 104002, 1441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "私…見た目\nそこまで変じゃ\nないですよね…？", 104002, 1442);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ほわあ～…\nふかふかって\nいいものですね～", 104002, 1443);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子みたいな\nあいくるしさを\n目指したいです！", 104002, 1444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "灯りを見てると\n心がなごみます～", 104002, 1445);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 104002, 1446);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "身体を鍛えて\nみなさんを\n守ります！", 104002, 1447);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 104002, 1448);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 104002, 1449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 104201, 1451);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 104201, 1452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 104201, 1453);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 104201, 1454);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "あとで曲を復習して\nおかないと…", 104201, 1455);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 104201, 1456);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 104201, 1457);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 104201, 1458);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 104201, 1459);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 104201, 1460);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 104201, 1461);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 104201, 1462);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 104201, 1463);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます♪\n少し歌の練習を\nしてきますね", 104201, 1464);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 104201, 1465);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 104201, 1466);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 104201, 1467);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "歌の調子をよくする\nためには休息も\n必要ですよね…", 104201, 1468);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子たちを\n相手に練習して\nみようかな…♪", 104201, 1469);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 104201, 1470);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…", 104201, 1471);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "歌うためには\n体力づくりは\n欠かせません", 104201, 1472);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "歌った後は\nお腹が空いて\nしまうんです", 104201, 1473);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "音楽について\n理論も勉強\nしないと…", 104201, 1474);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 104202, 1476);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 104202, 1477);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 104202, 1478);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 104202, 1479);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 104202, 1480);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 104202, 1481);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私の中にある\n私だけの歌…\n作ってみようかな？", 104202, 1482);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 104202, 1483);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 104202, 1484);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "魔物退治ですか！？\n…違います？\n良かった…", 104202, 1485);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 104202, 1486);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 104202, 1487);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 104202, 1488);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 104202, 1489);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "少し休んだら\n午後の練習に\n行ってきますね", 104202, 1490);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 104202, 1491);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 104202, 1492);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 104202, 1493);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 104202, 1494);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 104202, 1495);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…", 104202, 1496);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 104202, 1497);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 104202, 1498);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "読書は歌に\n新しい風を\n吹き込んでくれます", 104202, 1499);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "店の新メニューでも\n考えるかな", 104301, 1501);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あたしの店から、\n出前を届けようか？", 104301, 1502);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "パトロール、\nパトロール…\nここは異常なしかな？", 104301, 1503);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 104301, 1504);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 104301, 1505);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 104301, 1506);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今日の献立は…\nあいつの好きなものに\nしてやるか", 104301, 1507);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 104301, 1508);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "天気がいい日は\n外で運動したく\nならねぇか？", 104301, 1509);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "腹減ったか？　なんか\n作ってやろうか？", 104301, 1510);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 104301, 1511);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 104301, 1512);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 104301, 1513);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 104301, 1514);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 104301, 1515);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 104301, 1516);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 104301, 1517);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわぁ、\n一休みすっかなぁ～", 104301, 1518);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うっ！　か、かわいい\nじゃねぇか", 104301, 1519);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 104301, 1520);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 104301, 1521);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "オラオラオラーーー！\n燃えるぜーーー！", 104301, 1522);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 104301, 1523);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 104301, 1524);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "パトロール終了～\n本日も異常なしっと！", 104302, 1526);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あたしの店から\n出前を届けようか？", 104302, 1527);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あいつ、早く\n帰ってこないかな", 104302, 1528);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あいつ\n腹すかせてないと\nいいんだけど…", 104302, 1529);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 104302, 1530);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 104302, 1531);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今日の献立は…\nあいつの好きなものに\nしてやるか", 104302, 1532);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 104302, 1533);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "天気がいい日は\n外で運動したく\nならねぇか？", 104302, 1534);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "腹減ったか？　なんか\n作ってやろうか？", 104302, 1535);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 104302, 1536);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 104302, 1537);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 104302, 1538);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 104302, 1539);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 104302, 1540);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 104302, 1541);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 104302, 1542);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぅ～♪\n疲れがとれるな～", 104302, 1543);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うっ！　か、かわいい\nじゃねぇか", 104302, 1544);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 104302, 1545);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 104302, 1546);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "オラオラオラーーー！\n燃えるぜーーー！", 104302, 1547);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 104302, 1548);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 104302, 1549);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 104501, 1551);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 104501, 1552);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 104501, 1553);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 104501, 1554);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 104501, 1555);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 104501, 1556);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 104501, 1557);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 104501, 1558);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 104501, 1559);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 104501, 1560);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 104501, 1561);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 104501, 1562);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 104501, 1563);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 104501, 1564);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 104501, 1565);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 104501, 1566);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 104501, 1567);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 104501, 1568);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 104501, 1569);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 104501, 1570);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 104501, 1571);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 104501, 1572);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 104501, 1573);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本の下に、\nそういう本を\nカムフラージュ？", 104501, 1574);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 104502, 1576);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 104502, 1577);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "も、妄想が\n足りません…\n禁断症状がっ…！", 104502, 1578);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 104502, 1579);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 104502, 1580);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 104502, 1581);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 104502, 1582);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 104502, 1583);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 104502, 1584);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 104502, 1585);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 104502, 1586);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 104502, 1587);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 104502, 1588);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 104502, 1589);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 104502, 1590);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今夜は一晩中…\nなんですね？\nぐふふ…", 104502, 1591);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 104502, 1592);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 104502, 1593);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 104502, 1594);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 104502, 1595);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 104502, 1596);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 104502, 1597);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "こんなにいっぱい\n入りません…\nお腹にっ！", 104502, 1598);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "どんな本も\nよく見ると\nいやらしいです…！", 104502, 1599);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 104601, 1601);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 104601, 1602);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 104601, 1603);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 104601, 1604);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 104601, 1605);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 104601, 1606);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 104601, 1607);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 104601, 1608);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 104601, 1609);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 104601, 1610);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 104601, 1611);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 104601, 1612);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 104601, 1613);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 104601, 1614);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 104601, 1615);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 104601, 1616);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 104601, 1617);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "うにゃ～！\nお昼寝に\nぴったりにゃ", 104601, 1618);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 104601, 1619);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 104601, 1620);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 104601, 1621);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "本当は外で\n運動したいん\nだけどにゃ", 104601, 1622);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 104601, 1623);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…猫の\nしつけ方は、と…", 104601, 1624);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 104602, 1626);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 104602, 1627);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 104602, 1628);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 104602, 1629);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 104602, 1630);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 104602, 1631);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 104602, 1632);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 104602, 1633);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 104602, 1634);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 104602, 1635);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 104602, 1636);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 104602, 1637);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 104602, 1638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 104602, 1639);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろおっひる～\nにゃーんか\n食べたい…", 104602, 1640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 104602, 1641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "にゃにゃ…\n髪が乱れてるにゃ", 104602, 1642);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 104602, 1643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 104602, 1644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "猫でもこわくない\n優しい灯りにゃ！", 104602, 1645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽっかぽか\nしあわせな気分にゃ～", 104602, 1646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 104602, 1647);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 104602, 1648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 104602, 1649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "内職手伝える人\nいないかしら？", 104801, 1651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 104801, 1652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "次の特売セールに\n向けて、しっかり\n準備しないと！", 104801, 1653);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 104801, 1654);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 104801, 1655);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 104801, 1656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 104801, 1657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 104801, 1658);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 104801, 1659);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "背後の気配なら\nすぐ分かるわ\n試してみる？", 104801, 1660);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 104801, 1661);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 104801, 1662);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 104801, 1663);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\n今日も朝から\nバイトよ！", 104801, 1664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 104801, 1665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 104801, 1666);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 104801, 1667);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ～バイトで\n疲れたから癒される～", 104801, 1668);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これを内職で\n作ったら\n儲かるかしら？", 104801, 1669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 104801, 1670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 104801, 1671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "働き続けるには\n健康第一！　それには\n運動が一番よ", 104801, 1672);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 104801, 1673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "日々絶えず学んで、\n生活に活かすのよ！", 104801, 1674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 104802, 1676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 104802, 1677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 104802, 1678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 104802, 1679);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 104802, 1680);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 104802, 1681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 104802, 1682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 104802, 1683);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 104802, 1684);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今から\n休憩するところなの\n一緒にどう？", 104802, 1685);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ん？　どうしたの？\n私のことが\n気になるの？", 104802, 1686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 104802, 1687);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 104802, 1688);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 104802, 1689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼時ね\n少し休憩したら\nまた働くわよ！", 104802, 1690);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 104802, 1691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "女の子らしく…\nどうも忘れちゃう\nのよね", 104802, 1692);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ここで少し\n休んだら\nまたバイトよ！", 104802, 1693);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これを内職で\n作ったら\n儲かるかしら？", 104802, 1694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 104802, 1695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 104802, 1696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 104802, 1697);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 104802, 1698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "日々絶えず学んで\n生活に活かすのよ！", 104802, 1699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 104901, 1701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 104901, 1702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 104901, 1703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "くんくん、\n弟くんのにおい…\n近くにいるね？", 104901, 1704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 104901, 1705);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "この部屋を、\n私と弟くんの\n愛の巣に…", 104901, 1706);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "はぁ～…弟くん\n禁断症状だよ～", 104901, 1707);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 104901, 1708);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 104901, 1709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "姉と弟の外出は\nデートって\nいうのかな？", 104901, 1710);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 104901, 1711);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 104901, 1712);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 104901, 1713);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 104901, 1714);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 104901, 1715);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 104901, 1716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "きれいなお姉ちゃん\n好きだよね♪", 104901, 1717);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 104901, 1718);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\nお姉ちゃんチョップ！", 104901, 1719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り、\n弟くん好きかな？", 104901, 1720);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これ、弟くんみたいに\nあったかいね～", 104901, 1721);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 104901, 1722);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あ、これ\n弟くんが好きな\n味付けだね！", 104901, 1723);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 104901, 1724);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 104902, 1726);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 104902, 1727);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "弟くんのお友達は\nみんな私の妹だよ♪", 104902, 1728);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 104902, 1729);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "はっ！\n弟くんが困ってる\n気がする！", 104902, 1730);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "はぁ～…弟くん、\n禁断症状だよ～", 104902, 1731);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 104902, 1732);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 104902, 1733);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 104902, 1734);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 104902, 1735);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 104902, 1736);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 104902, 1737);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 104902, 1738);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 104902, 1739);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 104902, 1740);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 104902, 1741);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "弟くんを\n誘惑しちゃうぞ～♪", 104902, 1742);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 104902, 1743);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\nお姉ちゃんチョップ！", 104902, 1744);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お肌に潤いが\nでてきたみたい～♪", 104902, 1745);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これ、弟くんみたいに\nあったかいね～", 104902, 1746);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 104902, 1747);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 104902, 1748);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 104902, 1749);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 105001, 1751);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 105001, 1752);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "アタシの大人コーデで\nあいつも夢中ね♪", 105001, 1753);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "朝顔の観察したら\n理系女子になれる\nかしら？", 105001, 1754);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 105001, 1755);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 105001, 1756);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 105001, 1757);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "わーい、アメだー♪\n食べる食べるー♪", 105001, 1758);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 105001, 1759);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 105001, 1760);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 105001, 1761);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 105001, 1762);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 105001, 1763);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝のスキンケアは\n大事なの！", 105001, 1764);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹すいたー\nダイエット\nやめちゃおうかなー", 105001, 1765);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "エイジングケアを\nしないとねー", 105001, 1766);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 105001, 1767);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 105001, 1768);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 105001, 1769);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "オシャレ～♪\n大人なレディって\n感じよね♪", 105001, 1770);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかーい♪\nここで宿題\nしようかしら？", 105001, 1771);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動したら成長\nしないかしら？\n背とか胸とか…", 105001, 1772);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 105001, 1773);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 105001, 1774);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 105002, 1776);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "恋人にするなら\nイケメンか\nただイケよね～", 105002, 1777);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 105002, 1778);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "朝顔の観察したら\n理系女子になれる\nかしら？", 105002, 1779);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 105002, 1780);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 105002, 1781);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 105002, 1782);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なーに？\nアタシに甘えたいの？", 105002, 1783);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 105002, 1784);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 105002, 1785);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 105002, 1786);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 105002, 1787);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 105002, 1788);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "レディの朝は\n忙しいのよ", 105002, 1789);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹すいたー\nダイエット\nやめちゃおうかなー", 105002, 1790);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "エイジングケアを\nしないとねー", 105002, 1791);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 105002, 1792);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 105002, 1793);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 105002, 1794);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "オシャレ～♪\n大人なレディって\n感じよね♪", 105002, 1795);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかーい♪\nここで宿題\nしようかしら？", 105002, 1796);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動したら成長\nしないかしら？\n背とか胸とか…", 105002, 1797);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "レディは、食べ物にも\n気を使うのよ", 105002, 1798);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 105002, 1799);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "魔法と科学\nどっちも極めて\nみせるわ", 105101, 1801);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "仕事も片付いたし\n部屋で\n実験実験…♪", 105101, 1802);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "たまった治療代の\n請求書を\nまとめないと…", 105101, 1803);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 105101, 1804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 105101, 1805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 105101, 1806);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 105101, 1807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あら、ヒマなら\n薬草の整理を\n手伝って", 105101, 1808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 105101, 1809);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "具合が悪い？\nなら診てあげる\nまずは触診から…", 105101, 1810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 105101, 1811);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 105101, 1812);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 105101, 1813);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 105101, 1814);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 105101, 1815);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 105101, 1816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "医者たるもの\nいつだって\n清潔に…♪", 105101, 1817);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 105101, 1818);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ぬいぐるみなら\n動物も…", 105101, 1819);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 105101, 1820);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここにいると\nついウトウト\nしちゃうわ…", 105101, 1821);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 105101, 1822);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "研究していると\nつい食べるのを\n忘れちゃうのよね", 105101, 1823);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "実験もいいけど\nいつか成果を\n本にしないと…", 105101, 1824);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 105102, 1826);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 105102, 1827);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 105102, 1828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 105102, 1829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 105102, 1830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 105102, 1831);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 105102, 1832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 105102, 1833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 105102, 1834);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "欲しい薬が\nあったら言ってね\n調合してみるから", 105102, 1835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 105102, 1836);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 105102, 1837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 105102, 1838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 105102, 1839);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 105102, 1840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 105102, 1841);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "身なりについて\n研究するのも\n楽しそうね", 105102, 1842);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 105102, 1843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 105102, 1844);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 105102, 1845);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 105102, 1846);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "運動しないと\n体型の維持が…", 105102, 1847);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "適度な食事も\n大切よね", 105102, 1848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 105102, 1849);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ぺっぺっ！\n…い、いけないっ！", 105201, 1851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "いっけな～い！\nひづめで床に傷が！", 105201, 1852);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 105201, 1853);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 105201, 1854);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "みんな、体重\nどれくらいなのかな？", 105201, 1855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 105201, 1856);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 105201, 1857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 105201, 1858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 105201, 1859);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 105201, 1860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "力仕事ならまかせて！", 105201, 1861);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 105201, 1862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 105201, 1863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 105201, 1864);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 105201, 1865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 105201, 1866);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 105201, 1867);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ミシッっていった！\n私、重すぎるかも！？", 105201, 1868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あ！　あなたも\n美人さんね～♪", 105201, 1869);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 105201, 1870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 105201, 1871);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "な、なんだか野生に\n帰っちゃいそう！", 105201, 1872);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 105201, 1873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 105201, 1874);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 105202, 1876);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 105202, 1877);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "動物苑…って\n嫌なギルド名ね…", 105202, 1878);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 105202, 1879);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 105202, 1880);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 105202, 1881);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 105202, 1882);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 105202, 1883);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 105202, 1884);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ぺっぺっ！\n…い、いけないっ！", 105202, 1885);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 105202, 1886);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 105202, 1887);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 105202, 1888);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 105202, 1889);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ダイエット中だから\nお昼は抜いてるの～", 105202, 1890);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 105202, 1891);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 105202, 1892);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ミシッっていった！\n私、重すぎるかも！？", 105202, 1893);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あ！　あなたも\n美人さんね～♪", 105202, 1894);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 105202, 1895);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 105202, 1896);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "な、なんだか野生に\n帰っちゃいそう！", 105202, 1897);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "何を食べるかより\n誰と食べるかが\n大事なのよね", 105202, 1898);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 105202, 1899);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 105301, 1901);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "私は子供ではない…！\nが、お菓子は\nいただこう！", 105301, 1902);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 105301, 1903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 105301, 1904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 105301, 1905);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 105301, 1906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 105301, 1907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 105301, 1908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 105301, 1909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 105301, 1910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "私ともっと高く、\n共に飛ぼう！", 105301, 1911);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 105301, 1912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 105301, 1913);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 105301, 1914);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 105301, 1915);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 105301, 1916);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 105301, 1917);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 105301, 1918);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 105301, 1919);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 105301, 1920);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 105301, 1921);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 105301, 1922);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 105301, 1923);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "これを読んで、新たな\n戦術を編み出すぞっ！", 105301, 1924);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 105302, 1926);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 105302, 1927);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 105302, 1928);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 105302, 1929);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 105302, 1930);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 105302, 1931);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 105302, 1932);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 105302, 1933);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 105302, 1934);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 105302, 1935);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 105302, 1936);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 105302, 1937);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 105302, 1938);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 105302, 1939);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 105302, 1940);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "寝る前に…あと少し\nだけ、鍛錬を…", 105302, 1941);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 105302, 1942);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 105302, 1943);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 105302, 1944);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 105302, 1945);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 105302, 1946);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 105302, 1947);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 105302, 1948);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 105302, 1949);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "私たちの騎空艇\nどんなデザインに\nしよう…？", 105701, 1951);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "団員募集の\nポスター\n作ろうっと♪", 105701, 1952);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "父さん…\n今頃どこで\n何してるんだろ？", 105701, 1953);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 105701, 1954);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "父さん以上の\n世界一の\n騎空士になりたい！", 105701, 1955);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 105701, 1956);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "父さん…\n私にもね、大切な\n仲間ができたよ", 105701, 1957);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "仕事の依頼？\nすぐに支度\nしてくるね！", 105701, 1958);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 105701, 1959);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なーにー？\nどうしたの？\n今呼んだよね？", 105701, 1960);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 105701, 1961);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 105701, 1962);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 105701, 1963);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 105701, 1964);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 105701, 1965);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 105701, 1966);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 105701, 1967);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 105701, 1968);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 105701, 1969);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り～♪\n気持ちが\n落ち着く～…", 105701, 1970);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これって\n騎空艇に\n置けるかな…？", 105701, 1971);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "どんな仕事でも\nこなせるような\n体力がないとね…！", 105701, 1972);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 105701, 1973);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "空を冒険するには\n色んなことを\n知っておかないと…", 105701, 1974);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 105702, 1976);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "暇だよ…\nどこかに強い人は\nいないか？", 105702, 1977);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 105702, 1978);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "今のうちに、\n魔物退治の\n仕事に行くね！", 105702, 1979);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ハァ～…\nうん、気の巡りは\n問題ないね！", 105702, 1980);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "私の力の源は\n仲間との\n絆だよ！", 105702, 1981);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 105702, 1982);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 105702, 1983);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "私はいつも通りに\n喋ってるつもり\nだよ？", 105702, 1984);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どうした？\nもしかして手合せ\nしたいか？", 105702, 1985);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 105702, 1986);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "背中はあなたに\n預けたよ！\nよろしくね！", 105702, 1987);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 105702, 1988);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 105702, 1989);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "腹減ったね…\nなにか食べに\n行くよ！", 105702, 1990);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "修行のしすぎは\n良くないよ？\n気をつけるね", 105702, 1991);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "美しい技は\n美しい体が\nあってこそ！", 105702, 1992);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 105702, 1993);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "…練習に\n使うのはさすがに\n可哀そうね", 105702, 1994);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これは…\n瞑想の時にも\n使ってみたいね", 105702, 1995);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "出来ればもっと\n熱いほうが\n嬉しいよ", 105702, 1996);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "ここでも\n修行出来るなんて\n最高だよ！", 105702, 1997);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "気力の源は体力！\n体力の源は\n健康とご飯！", 105702, 1998);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "んー…\n難しいことは\nわかんないよ！", 105702, 1999);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "わたしたちの\nギルドハウスへ\nようこそ～！", 105801, 2001);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 105801, 2002);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ん？\n食べ物のにおい？", 105801, 2003);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "みんなと仲良く食卓を\n囲みたいですね～", 105801, 2004);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この部屋が、わたしの\n帰る場所です♪", 105801, 2005);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 105801, 2006);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人、\n大食いの子って\n嫌いかな？", 105801, 2007);
