INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "魔法を使うにも\n知識が必要だものね", 106002, 2099);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この空間は主さまの\n香りがします\n…ふふふ♪", 105901, 2101);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "炊事、洗濯、お掃除、\nお買いものもしなくて\nはいけませんね", 105901, 2102);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "アメスさまへの、\nお祈りができそうな\n場所は…", 105901, 2103);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "さすが都会…\nおいしいものが\nたくさんあるのですね", 105901, 2104);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "主さまが不自由なく\n使える部屋に\nしなくては…", 105901, 2105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "できるなら…\n主さまと二人だけの\n部屋の方が…", 105901, 2106);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "主さまの隣には\nわたくしが…\nわたくしの隣には、\n主さまが…幸せです", 105901, 2107);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 105901, 2108);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "もしお疲れでしたら\n肩でも\nお揉みしましょうか？", 105901, 2109);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 105901, 2110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今日のおこづかいです\n何かおいしいものを\n召し上がってください", 105901, 2111);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "主さまのおてて\n温かいですね…♪", 105901, 2112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "主さまのおそばに\nいられて、わたくし、\n嬉しいです…", 105901, 2113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます。\n洗濯をしなければ…", 105901, 2114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろお昼ですね。\nおいしいものを\n食べましょう", 105901, 2115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おやすみになる時は、\nお声をかけてください\nお供致します", 105901, 2116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "もう少し…\nおっきくなりたい\nです", 105901, 2117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このふかふかの優しさ\n主さまのようです", 105901, 2118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\n主さまにそっくり♪", 105901, 2119);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 105901, 2120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここで洗濯物を\n乾かせるでしょうか", 105901, 2121);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "都会の人々は\nこんなふうに\n運動を…！？", 105901, 2122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 105901, 2123);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "よく学んで、\n主さまのお役に\n立たなければ", 105901, 2124);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 105902, 2126);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 105902, 2127);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "アメスさまへの\nお祈りができそうな\n場所は…", 105902, 2128);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "瞑想をすると\n心が安らぎます…", 105902, 2129);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 105902, 2130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 8, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 105902, 2131);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 105902, 2132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 105902, 2133);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 105902, 2134);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お出かけですか？\nわたくしもお供\nいたします", 105902, 2135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ひゃん！\n主さま…\nいじわるです", 105902, 2136);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 8, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 105902, 2137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "主さまの優しさに\nわたくしはいつも\n癒されています…", 105902, 2138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 105902, 2139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 105902, 2140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 105902, 2141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 105902, 2142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このふかふかの優しさ\n主さまのようです", 105902, 2143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\n主さまにそっくり♪", 105902, 2144);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この心地よさ…\n主さまのようです♪", 105902, 2145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここで洗濯物を\n乾かせるでしょうか　", 105902, 2146);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 105902, 2147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 105902, 2148);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "都会のことを\n学ばないと…\nふむふむ", 105902, 2149);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ふぁ～…あっ\n見られちゃい\nましたか？", 190801, 2151);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ギルドハウスの\n使い心地は\nどうですか？", 190801, 2152);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ご用があれば\nなんでも言って\nくださいね～♪", 190801, 2153);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 190801, 2154);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます！\nお掃除の方、\nはじめていきますね", 190801, 2155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 190801, 2156);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ここは夜でも\nにぎやかですね", 190801, 2157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "…\n少しうとうと\nしてしまった", 104701, 2158);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 104701, 2159);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 104701, 2160);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 104701, 2161);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 104701, 2162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "少年のことを考えると\n顔がにやけてしまう", 104701, 2163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "少年は今頃\n平和に過ごせて\nいるかな…", 104701, 2164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ん？　悪いが\n兜は脱がないぞ", 104701, 2165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 104701, 2166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 104701, 2167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 104701, 2168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "君といると、兜の中が\nいつもより暑いんだ", 104701, 2169);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 104701, 2170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 104701, 2171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 104701, 2172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 104701, 2173);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 104701, 2175);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 104701, 2176);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 104701, 2177);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 104701, 2178);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 104701, 2180);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 104701, 2181);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 102601, 2183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ドングリの木\n近くに生えてない？", 102601, 2184);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "快適な寝床があれば\nそれだけで幸せだねぇ", 102601, 2185);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 102601, 2186);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 102601, 2187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あんパン買ったし\nあいつにも\n分けてあげよう", 102601, 2188);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 102601, 2189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 102601, 2190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 102601, 2191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ドングリあげるから\nあんパン買ってきて～", 102601, 2192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "う～\n疲れちゃったよ～\nおんぶして運んで～", 102601, 2193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あんたといると\nリア充って\n感じがするよ", 102601, 2194);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 102601, 2195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 102601, 2196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろお昼寝でも\nしたい気分だねぇ", 102601, 2197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 102601, 2198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 102601, 2200);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 102601, 2201);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 102601, 2202);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 102601, 2203);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 102601, 2205);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 102601, 2206);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ランドソル…\nおっきな街だなぁ…", 106301, 2208);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ロザリア…\nきっと、いるよね？", 106301, 2209);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "最近、弓の練習\nサボっちゃってるな…", 106301, 2210);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "今日の私は\n森番じゃなくて\n家番だよ！", 106301, 2211);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ちょっとだけ\n慣れてきたかも？", 106301, 2212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "いつか、\nあの人と一緒に…", 106301, 2213);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "…なんか最近、\nあの人のこと\nばっかり…", 106301, 2214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "もしよかったら\n街に出かけない？", 106301, 2215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この部屋、\n妖精さんも\n気に入るかも！", 106301, 2216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今日は\nなににしよっか？", 106301, 2217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今日はどこ行く？", 106301, 2218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "たまには二人で…\nな、なんちゃって！", 106301, 2219);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 106301, 2220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\n今日も一緒に\n頑張ろうね！", 106301, 2221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 106301, 2222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ランドソルは\n夜でも賑やかだね！", 106301, 2223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "わぁっ！\nすっごく\nふわふわだよ！", 106301, 2225);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 106301, 2226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん\nなんか…いい匂い？", 106301, 2227);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "すっごいなー\nあったかいなー！", 106301, 2228);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 106301, 2230);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 106301, 2231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 103601, 2233);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "カップケーキが\n食べたいな…", 103601, 2234);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "予習が終わりました！\nさて、次は復習を\nしないと…", 103601, 2235);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 103601, 2236);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "男の人は、みんな\nへんたいだと\n聞きました…", 103601, 2237);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 103601, 2238);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "早く大人になりたいな\n…あの人と同じくらい", 103601, 2239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ぼ、防犯魔石を\n鳴らしますよ！？", 103601, 2240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 103601, 2241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "これ宿題です\nやっておいて\nくださいね", 103601, 2242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あ、遅刻ですよ\n待っていたんですから", 103601, 2243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 103601, 2244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あの…何歳まで\n子供だと思いますか？", 103601, 2245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 103601, 2246);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 103601, 2247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 103601, 2248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 103601, 2250);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "つ、つぶらな瞳には\n弱いんです", 103601, 2251);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "リラックスできますね\n集中力も\n上がりそうです", 103601, 2252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 103601, 2253);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ピーマンもこれくらい\nおいしければ\nいいのに…", 103601, 2255);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 103601, 2256);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "今日はみんなに\nなにを読んで\nあげようかしら", 101501, 2258);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "子どもたちと同じ\n目線で考えるのが\n大事よね", 101501, 2259);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 101501, 2260);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 101501, 2261);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お遊戯会の配役、\n考え出すと\n大変だわ～", 101501, 2262);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "彼は優しいから、\nきっと良い\nお父さんになるわね", 101501, 2263);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 101501, 2264);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 101501, 2265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 101501, 2266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "よかったら一緒に\n絵本を作らない？", 101501, 2267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あなたはいつも元気ね\n先生も見習わないと♪", 101501, 2268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 101501, 2269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 101501, 2270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう～\nちゃんと朝ごはんは\n食べた？", 101501, 2271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 101501, 2272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 101501, 2273);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 101501, 2275);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 101501, 2276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りね～\n保育園にも\n置こうかしら", 101501, 2277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 101501, 2278);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 101501, 2280);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 101501, 2281);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "世界の前に、\nこの部屋を征服\nしてやろう♪", 104401, 2283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "拠点とはやはり\n斯くあるべきじゃの", 104401, 2284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 104401, 2285);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 104401, 2286);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "うるさいのう～\nまったく、近頃の\n若いもんは…", 104401, 2287);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 104401, 2288);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 104401, 2289);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 104401, 2290);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 104401, 2291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 104401, 2292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 104401, 2293);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 104401, 2294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 104401, 2295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 104401, 2296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 104401, 2297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 104401, 2298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "よい座り心地じゃ\nわらわの玉座としよう", 104401, 2300);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 104401, 2301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 104401, 2302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あたたかい…\n身も心も\n浄化されるようじゃ", 104401, 2303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 104401, 2305);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 104401, 2306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 105401, 2308);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 105401, 2309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 105401, 2310);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 105401, 2311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ノゾミさんの\n衣装はああして\nチカさんのは…", 105401, 2312);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 105401, 2313);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "まさか私が\nアイドルになるなんて\n思ってなかったです", 105401, 2314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 105401, 2315);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 105401, 2316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 105401, 2317);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 105401, 2318);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "どうしました？\n新しい服が\n欲しくなりましたか？", 105401, 2319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 105401, 2320);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 105401, 2321);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 105401, 2322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の方が\n作業が捗るのは\n私だけですかね？", 105401, 2323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このふかふか具合\nいい素材を\n使ってますね", 105401, 2325);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 105401, 2326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 105401, 2327);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 105401, 2328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "何をするにも\n食事は大切！\n体力が資本です！", 105401, 2330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "私もいつか\nデザインを\nまとめて本に…", 105401, 2331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "またお魚を\n捕りにいこうかな♪", 107501, 2333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 107501, 2334);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ここなら、\n日焼けの心配は\nありませんね！", 107501, 2335);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 107501, 2336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "泳ぐのも食べるのも\n大得意です！", 107501, 2337);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 107501, 2338);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 107501, 2339);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 107501, 2340);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 107501, 2341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 107501, 2342);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あなたの水着、\n私が選んであげます！", 107501, 2343);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 107501, 2344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 107501, 2345);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝の海も\n幻想的で\nいいですよねぇ～！", 107501, 2346);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 107501, 2347);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 107501, 2348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "泳ぎ疲れたので\nちょっと休憩です～", 107501, 2350);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あなたにも、\n可愛い水着を\n着せてあげたいです", 107501, 2351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "なんだか、\nリゾート気分です…", 107501, 2352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 107501, 2353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 107501, 2355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "南国のフルーツ図鑑\n…じゅるり…", 107501, 2356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "わ～♪　おいしそ…\nか、かわいいですね～", 107501, 2358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 107501, 2359);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 107601, 2360);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 107601, 2361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 107601, 2362);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 107601, 2363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "クロールの練習なら、\n部屋でもできそうです", 107601, 2364);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 107601, 2365);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 107601, 2366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 107601, 2367);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 107601, 2368);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "海の美しさは、\n森とはちがう\n魅力がありました", 107601, 2369);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この髪飾りですか？\n造花ですから\n水の中も平気です", 107601, 2370);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 107601, 2371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "はじめての海が、\n主さまと一緒で\n幸せです♪", 107601, 2372);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます。\n泳ぎに行く準備は\n万端です", 107601, 2373);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 107601, 2374);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 107601, 2375);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 107601, 2377);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 107601, 2378);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り…\nきっと主さまも\n気に入ります", 107601, 2379);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 107601, 2380);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "泳いだ後の食事は\n一段と美味しく\n感じます", 107601, 2382);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 107601, 2383);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 107601, 2385);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ふかふかですね\n主さまのようです♪", 107601, 2386);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 107701, 2387);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 107701, 2388);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "今日はドジを\nしませんように！", 107701, 2389);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "接客業を極めれば\nメイドとして\n成長できるかも？", 107701, 2390);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "水着って\n動きやすいんですけど\n人の目が気になります", 107701, 2391);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 107701, 2392);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "水着でご奉仕したら、\nあの人は喜ぶかな？", 107701, 2393);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 107701, 2394);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 107701, 2395);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 107701, 2396);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 107701, 2397);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 107701, 2398);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 107701, 2399);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 107701, 2400);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 107701, 2401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 107701, 2402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 107701, 2404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "きれいにすれば\nかわいさも倍増\nですよ～♪", 107701, 2405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 107701, 2406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 107701, 2407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 107701, 2409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 107701, 2410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "えへへ♪\n南の島まで\n乗せてください！", 107701, 2412);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "なんだか、\n人魚姫に\nなった気分です♪", 107701, 2413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 107801, 2414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 107801, 2415);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ビーチにいると\n日焼けが心配だわ", 107801, 2416);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 107801, 2417);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お、泳ぎの練習なんて\nしないからね！", 107801, 2418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 107801, 2419);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 107801, 2420);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 107801, 2421);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 107801, 2422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 107801, 2423);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 107801, 2424);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 107801, 2425);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 107801, 2426);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 107801, 2427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 107801, 2428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 107801, 2429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 107801, 2431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こんな魔物がいたら\n操るのも楽しいかも", 107801, 2432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "にゃう～♪\nまるで南国にいる\n気分ね♪", 107801, 2433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 107801, 2434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 107801, 2436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 107801, 2437);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 107801, 2439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "リゾートリゾートっ\nのんびりしましょ♪", 107801, 2440);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ここなら思う存分\nデスクワークに\n励めるわね！", 108001, 2441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 108001, 2442);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 108001, 2443);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "少し休んだら\n次のバイトに\n行ってくるわね！", 108001, 2444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この格好でここに\nいるのって\n気恥ずかしいわね…", 108001, 2445);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 108001, 2446);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "キミだけね…\n私のことをここまで\n理解してくれるのは", 108001, 2447);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "普段と違う格好\nだからって\nそんなに見ないでよ…", 108001, 2448);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 108001, 2449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 108001, 2450);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 108001, 2451);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 108001, 2452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 108001, 2453);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 108001, 2454);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 108001, 2455);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 108001, 2456);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 108001, 2458);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 108001, 2459);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りね…\n働く意欲が\n湧いてくるわ", 108001, 2460);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 108001, 2461);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 108001, 2463);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "座学も大切ね\nもっと知識を\n身につけないと…", 108001, 2464);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 108001, 2466);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "落ち着くわ…\n海とひとつになった\n気分ね！", 108001, 2467);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 107901, 2468);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "猫グッズで\n海の家を飾りたいにゃ", 107901, 2469);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "海の幸は美味しいけど\n水はイヤだにゃ…", 107901, 2470);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 107901, 2471);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "海と言えば水着にゃ\n似合ってるかにゃ？", 107901, 2472);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 107901, 2473);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あたしの宝は\nここにあるにゃ～", 107901, 2474);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 107901, 2475);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "今からキミを\nあたしの相棒に\n任命するにゃ！", 107901, 2476);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 107901, 2477);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 107901, 2478);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 107901, 2479);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 107901, 2480);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 107901, 2481);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 107901, 2482);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 107901, 2483);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 107901, 2485);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "にゃにゃ～\nなんてかわいさにゃ～", 107901, 2486);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 107901, 2487);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 107901, 2488);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 107901, 2490);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 107901, 2491);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 107901, 2493);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 107901, 2494);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "人の家って、道場とは\n違った香りがするね", 103701, 2495);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 103701, 2496);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 103701, 2497);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 103701, 2498);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "もっと\n上手く立ち回って\n出世したいな", 103701, 2499);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 103701, 2500);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 103701, 2501);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 103701, 2502);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 103701, 2503);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 103701, 2504);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 103701, 2505);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 103701, 2506);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 103701, 2507);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 103701, 2508);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 103701, 2509);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 103701, 2510);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いざという時のため\nにも、休憩は大事だね", 103701, 2512);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 103701, 2513);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 103701, 2514);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいなぁ。\nたまにはのんびり\nしようか", 103701, 2515);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 103701, 2517);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 103701, 2518);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "正義の刃で\n悪を挫く！\n…キマったッス♪", 100501, 2520);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 100501, 2521);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 100501, 2522);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "一日一善！\nヒーローだったら\n当たり前ッス♪", 100501, 2523);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "どうッスか\nこのポーズ！\nイイ感じッスか？", 100501, 2524);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 100501, 2525);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 100501, 2526);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 100501, 2527);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 100501, 2528);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 100501, 2529);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 100501, 2530);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 100501, 2531);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 100501, 2532);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 100501, 2533);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 100501, 2534);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜のパトロールで\n一緒に平和を\n守るッス！", 100501, 2535);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 100501, 2537);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 100501, 2538);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 100501, 2539);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 100501, 2540);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 100501, 2542);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 100501, 2543);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 108101, 2545);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 108101, 2546);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 108101, 2547);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 108101, 2548);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 108101, 2549);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 108101, 2550);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 108101, 2551);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 108101, 2552);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 108101, 2553);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 108101, 2554);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 108101, 2555);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 108101, 2556);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 108101, 2557);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 108101, 2558);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 108101, 2559);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 108101, 2560);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 108101, 2562);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "つん、つん。ふふっ♪\n癒やされます…", 108101, 2563);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 108101, 2564);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいね\nお父さん…\nえ？　分からない？", 108101, 2565);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "霊のみなさんは\n食べられないので…\n私がいただきます", 108101, 2567);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 108101, 2568);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "わぁ、お菓子が沢山…", 108101, 2570);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 108201, 2572);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 108201, 2573);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 108201, 2574);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 108201, 2575);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 108201, 2576);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 108201, 2577);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 108201, 2578);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 108201, 2579);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 108201, 2580);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 108201, 2581);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "プリンの可能性は\n無限大なの～", 108201, 2582);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 108201, 2583);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 108201, 2584);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 108201, 2585);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ランチは豪華に\nプリンアラモードなの", 108201, 2586);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 108201, 2587);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 108201, 2589);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 108201, 2590);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "癒されるの～\nまるで天国なの～", 108201, 2591);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここでなら、\n焼きプリンが\n作れるの！", 108201, 2592);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "腹が減っては\nハロウィンは\nできぬなの！", 108201, 2594);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 108201, 2595);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 108201, 2597);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 108301, 2599);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 108301, 2600);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 108301, 2601);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この格好で学院に\n行ったら、\nみんな驚くかしら", 108301, 2602);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 108301, 2603);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 108301, 2604);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アイツを魅了する\n魔法とかないかしら？", 108301, 2605);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 108301, 2606);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 108301, 2607);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 108301, 2608);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 108301, 2609);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 108301, 2610);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 108301, 2611);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 108301, 2612);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 108301, 2613);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 108301, 2614);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あう～、お菓子の\n食べすぎで\n動けな～い", 108301, 2616);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 108301, 2617);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "大人な香りに\n包まれてる～♪", 108301, 2618);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 108301, 2619);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 108301, 2621);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 108301, 2622);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 108301, 2624);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "次はどんな策で団長を\nからかってみようか…", 107101, 2626);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "む？\n血の臭いがするな\n祭りの時間か☆", 107101, 2627);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 107101, 2628);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 107101, 2629);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 107101, 2630);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 107101, 2631);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 107101, 2632);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 107101, 2633);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 107101, 2634);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 107101, 2635);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 107101, 2636);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 107101, 2637);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 107101, 2638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 107101, 2639);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 107101, 2640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 107101, 2641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 107101, 2643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 107101, 2644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 107101, 2645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 107101, 2646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 107101, 2648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 107101, 2649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 105601, 2651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 105601, 2652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 105601, 2653);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 105601, 2654);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ふあぁ～…\n昼寝でもするか", 105601, 2655);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ふふっ…\nあの子、見た目より\n根性あるんだね", 105601, 2656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 105601, 2657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 105601, 2658);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 105601, 2659);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 105601, 2660);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 105601, 2661);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 105601, 2662);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 105601, 2663);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 105601, 2664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 105601, 2665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 105601, 2666);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 105601, 2668);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 105601, 2669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 105601, 2670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 105601, 2671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 105601, 2673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 105601, 2674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 101301, 2676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 101301, 2677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 101301, 2678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 101301, 2679);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 101301, 2680);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "次はどの漫画を\n貸そうかなー♪", 101301, 2681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 101301, 2682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 101301, 2683);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 101301, 2684);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 101301, 2685);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "新魔法思いついた！\n私って天才！？", 101301, 2686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 101301, 2687);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 101301, 2688);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 101301, 2689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 101301, 2690);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜からが、\nオタ活本番でしょ！", 101301, 2691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふいー！\nくつろいで本を読む！\n最高だねー！", 101301, 2693);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 101301, 2694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 101301, 2695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 101301, 2696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ウマ―！\nこれの詳細きぼんぬ！", 101301, 2698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "こ、古代語の本！？\n現代語でおk…", 101301, 2699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 190901, 2701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 190901, 2702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 190901, 2703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 190901, 2704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 190901, 2705);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 190901, 2706);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 190901, 2707);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 190901, 2708);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 190901, 2709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 190901, 2710);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 190901, 2711);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 190901, 2712);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 190901, 2714);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 190901, 2715);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 190901, 2716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 190901, 2717);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 190901, 2719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 190901, 2720);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "メリークリスマス！\n歌で幸せを\n届けます…！", 108401, 2722);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 108401, 2723);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 108401, 2724);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 108401, 2725);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 108401, 2726);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 108401, 2727);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 108401, 2728);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 108401, 2729);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "歌を届ける\nサンタさんに\nなりたいな…", 108401, 2730);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 108401, 2731);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 108401, 2732);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 108401, 2733);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 108401, 2734);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日も一日\n幸せな歌で世界が\n包まれますように…", 108401, 2735);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 108401, 2736);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 108401, 2737);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 108401, 2739);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 108401, 2740);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 108401, 2741);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "この光みたいに\n灯火になるような\n歌を歌いたい…", 108401, 2742);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 108401, 2744);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "物語には\n曲作りのヒントが\n詰まっているんです", 108401, 2745);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 108401, 2747);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 108401, 2748);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 108501, 2749);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 108501, 2750);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 108501, 2751);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 108501, 2752);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 108501, 2753);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 108501, 2754);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 108501, 2755);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "メリー\nクリスマスです、\nお兄ちゃん♪", 108501, 2756);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 108501, 2757);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 108501, 2758);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 108501, 2759);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 108501, 2760);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 108501, 2761);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 108501, 2762);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 108501, 2763);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 108501, 2764);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 108501, 2766);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子と一緒に\nおままごとしたいな♪", 108501, 2767);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 108501, 2768);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 108501, 2769);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 108501, 2771);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 108501, 2772);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 108501, 2774);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 108501, 2775);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 108601, 2776);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "毎日が\nクリスマスだったら\nいいのにな～♪", 108601, 2777);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 108601, 2778);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 108601, 2779);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 108601, 2780);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 108601, 2781);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 108601, 2782);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 108601, 2783);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 108601, 2784);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 108601, 2785);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 108601, 2786);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 108601, 2787);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 108601, 2788);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 108601, 2789);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 108601, 2790);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 108601, 2791);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 108601, 2793);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 108601, 2794);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 108601, 2795);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 108601, 2796);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 108601, 2798);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 108601, 2799);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 108601, 2801);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 108601, 2802);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "着物も慣れると\n動きやすいものさ", 108901, 2803);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 108901, 2804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 108901, 2805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 108901, 2806);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 108901, 2807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "これからもずっと\n仲間と、彼と\n一緒に…", 108901, 2808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 108901, 2809);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 108901, 2810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 108901, 2811);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 108901, 2812);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 108901, 2813);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "キミといると\n気持ちが段々と\n安らいでいくよ", 108901, 2814);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 108901, 2815);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう\n朝の修行を始めるけど\nキミもどう？", 108901, 2816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 108901, 2817);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 108901, 2818);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "…疲れていたのかな\nもう少しで眠る\nところだった…", 108901, 2820);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 108901, 2821);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 108901, 2822);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 108901, 2823);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 108901, 2825);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "外を出歩くのも\nいいけど、中で\n読書もありだな", 108901, 2826);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 108801, 2828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 108801, 2829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "今年は\nどんな福袋を\n買おうかな…♪", 108801, 2830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 108801, 2831);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 108801, 2832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 108801, 2833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 108801, 2834);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 108801, 2835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 108801, 2836);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 108801, 2837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 108801, 2838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 108801, 2839);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 108801, 2840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう\n太陽の光に当たると\n元気が出るよ", 108801, 2841);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 108801, 2842);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 108801, 2843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぅ…\n帯も少し\nゆるめちゃおう…", 108801, 2845);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "お正月用に\n着物を縫って\nあげたいな", 108801, 2846);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これでお肌が\n保湿出来たら\nいいのになぁ", 108801, 2847);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ん～…\nぬくぬくしてて\n動きたくないよ～", 108801, 2848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 108801, 2850);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "年末年始は\n告白のチャンス…\nむ、無理無理！", 108801, 2851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 108701, 2853);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 108701, 2854);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 108701, 2855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "初詣楽しみー！\n出店はどこから\n行こうかな？", 108701, 2856);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 108701, 2857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "うーん…\n騎士くんはお正月\n何してるんだろ…", 108701, 2858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 108701, 2859);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 108701, 2860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 108701, 2861);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 108701, 2862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 108701, 2863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 108701, 2864);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 108701, 2865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよー！\n今日も明るく元気に\nがんばろー！", 108701, 2866);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 108701, 2867);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 108701, 2868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 108701, 2870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 108701, 2871);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はぁー♪\nいい香りー…\n気分スッキリ♪", 108701, 2872);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 108701, 2873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ど、どうしよう…！\n手が、止まんないよ！", 108701, 2875);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 108701, 2876);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 109001, 2878);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ここを私のチョコで\n埋め尽くしますわ", 109001, 2879);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 109001, 2880);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 109001, 2881);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 109001, 2882);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 109001, 2883);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 109001, 2884);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あなた様。私に\n隠しているものなど\nありませんよね…？", 109001, 2885);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 109001, 2886);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 109001, 2887);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "解毒薬はどこに\nいれたかしら…？", 109001, 2888);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 109001, 2889);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 109001, 2890);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 109001, 2891);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 109001, 2892);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 109001, 2893);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 109001, 2895);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この中、何か\n入りそうですわね\n…チョコレート、とか", 109001, 2896);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 109001, 2897);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 109001, 2898);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 109001, 2900);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 109001, 2901);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 109101, 2903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 109101, 2904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 109101, 2905);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 109101, 2906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 109101, 2907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お姉ちゃんのチョコ\n食べさせてあげたい…", 109101, 2908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 109101, 2909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 109101, 2910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 109101, 2911);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ナデナデするのは\nお姉ちゃんの仕事！", 109101, 2912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 109101, 2913);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 109101, 2914);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 109101, 2915);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 109101, 2916);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 109101, 2917);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 109101, 2918);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 109101, 2920);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "このかわいさは\nお姉ちゃんも大満足♪", 109101, 2921);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り、チョコに\n使えるかな…？", 109101, 2922);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "弟くんと一緒に\nぬくぬくしたいな…♪", 109101, 2923);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 109101, 2925);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 109101, 2926);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "アタシって…\nここにいても\nいいのか…？", 106101, 2928);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 106101, 2929);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 106101, 2930);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 106101, 2931);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 106101, 2932);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "みんなが早く\nアタシのこと\n思い出さないかな", 106101, 2933);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 106101, 2934);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 106101, 2935);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どうした？\n戦うのか！？\n…なんだ、違うのか", 106101, 2936);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "身長のことは\nあんまり言うな！\n気にしてるからな！", 106101, 2937);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 106101, 2938);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 106101, 2939);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 106101, 2940);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 106101, 2941);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 106101, 2942);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 106101, 2943);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぃ～…\n極楽極楽…", 106101, 2945);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 106101, 2946);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん…\nたまにはこういうのも\n悪くないかもな", 106101, 2947);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あー…\nもっと前にコイツと\n出会いたかった…", 106101, 2948);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 106101, 2950);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 106101, 2951);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 101401, 2953);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "事件のオチは\n往々にして\n取るに足らないものさ", 101401, 2954);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "読書で得る知識が\n探偵の\n血肉になるんだよ", 101401, 2955);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この前の\n捜査資料…\nどこにあったかな？", 101401, 2956);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 101401, 2957);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 101401, 2958);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 101401, 2959);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "珈琲、淹れたてだよ\n一杯どうだい？", 101401, 2960);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 101401, 2961);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 101401, 2962);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 101401, 2963);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 101401, 2964);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "助手くんは\n私にとって\n最高の相棒だよ☆", 101401, 2965);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 101401, 2966);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "一休みしたら\n推理の答え合わせを\nしてみようか？", 101401, 2967);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 101401, 2968);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 101401, 2970);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この愛らしさ…\n要捜査対象だね！", 101401, 2971);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "思考が活性化される\n素敵な香りだね…", 101401, 2972);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 101401, 2973);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 101401, 2975);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "探偵だからね\nミステリには\n一家言あるんだ", 101401, 2976);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 105501, 2978);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "せ、先輩がいた部屋！\n私には刺激が強い…", 105501, 2979);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "先輩を観察しやすい\n場所はあるかな…", 105501, 2980);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 105501, 2981);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 105501, 2982);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 105501, 2983);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "せ、先輩になら\n観察されてもいいなぁ", 105501, 2984);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 105501, 2985);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 105501, 2986);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 105501, 2987);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ま、また…\n先輩と一緒に\nバイトしたい…です", 105501, 2988);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 105501, 2989);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 105501, 2990);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "せ、先輩…！\nおひゃっ！\nおはようございます！", 105501, 2991);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 105501, 2992);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 105501, 2993);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 105501, 2995);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 105501, 2996);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ここから先輩の\n香りがすればなあ", 105501, 2997);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 105501, 2998);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいし～\nこれは\n先輩も好きな味かな", 105501, 3000);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "す、好きな人に\n近づく方法…", 105501, 3001);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 109201, 3003);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 109201, 3004);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 109201, 3005);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "わー！\nあれもこれも\n気になっちゃうな！", 109201, 3006);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 109201, 3007);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 109201, 3008);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 109201, 3009);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "歩き方がきれい？\nえへへ、そうかな…？", 109201, 3010);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 109201, 3011);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 109201, 3012);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 109201, 3013);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 109201, 3014);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お茶の時間に\nしたいな～…\n一緒にどう？", 109201, 3015);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようっ！\n今日も素敵な\n一日にしようね！", 109201, 3016);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 109201, 3017);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 109201, 3018);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 109201, 3020);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 109201, 3021);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り～！\nなんだか気持ちが\n落ち着くね", 109201, 3022);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 109201, 3023);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "こ、これ美味し～！\n止められないよー！", 109201, 3025);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 109201, 3026);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 109301, 3028);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 109301, 3029);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 109301, 3030);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 109301, 3031);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "なにか面白そうなもの\nないですかね～？", 109301, 3032);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ここのみんなとも\nもうお友だちですぅ！", 109301, 3033);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "おなかが減ったです…", 109301, 3034);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 109301, 3035);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 109301, 3036);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あなたもお勉強\n苦手ですか？", 109301, 3037);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 109301, 3038);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お友だちいっぱいで、\nルゥはうれしいです♪", 109301, 3039);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 109301, 3040);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 109301, 3041);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "午後もいっぱい\n遊ぶですよ～！", 109301, 3042);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 109301, 3043);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 109301, 3045);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 109301, 3046);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 109301, 3047);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 109301, 3048);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 109301, 3050);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 109301, 3051);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ピアノ…\nどこかにないかな？", 109401, 3053);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ふぅ…\n落ち着くな…", 109401, 3054);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "マナリアはいつも\n賑やかなんだ…", 109401, 3055);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "みんな…\n今頃何してるかな？", 109401, 3056);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 109401, 3057);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "人と話すのは\n得意じゃないな…", 109401, 3058);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 109401, 3059);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "やっぱり、私の翼や\n尻尾…気になる？", 109401, 3060);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 109401, 3061);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 109401, 3062);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 109401, 3063);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 109401, 3064);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 109401, 3065);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 109401, 3066);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 109401, 3067);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 109401, 3068);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "羽を伸ばしたく\nなっちゃうね…ふふ", 109401, 3070);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 109401, 3071);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\nこれ、\nアンも好きそう…", 109401, 3072);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 109401, 3073);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 109401, 3075);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 109401, 3076);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 109501, 3078);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 109501, 3079);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 109501, 3080);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 109501, 3081);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 109501, 3082);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 109501, 3083);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 109501, 3084);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 109501, 3085);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 109501, 3086);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "こんな場所で\nなんというプレイを！\n…妄想でした", 109501, 3087);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 109501, 3088);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 109501, 3089);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 109501, 3090);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 109501, 3091);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 109501, 3092);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 109501, 3093);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 109501, 3095);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 109501, 3096);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 109501, 3097);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 109501, 3098);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 109501, 3100);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "クウカの探している\nジャンルはどこに\nありますかね？", 109501, 3101);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 109601, 3103);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 109601, 3104);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 109601, 3105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 109601, 3106);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 109601, 3107);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 109601, 3108);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 109601, 3109);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 109601, 3110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 109601, 3111);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 109601, 3112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 109601, 3113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 109601, 3114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 109601, 3115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 109601, 3116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 109601, 3117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 109601, 3118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "天下分け目の\n戦いに備えて…\nマッタリデース", 109601, 3120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "口寄せの術で\n召喚したいくらい\nかわいいデース", 109601, 3121);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 109601, 3122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 109601, 3123);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 109601, 3125);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 109601, 3126);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 109701, 3128);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "異世界の演劇…\n許されるならば、\n観てみたいです", 109701, 3129);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 109701, 3130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 109701, 3131);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 109701, 3132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 109701, 3133);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 109701, 3134);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お出かけでしたら、\nレムも同行\nさせてくださいね", 109701, 3135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "レムにできること\nでしたら、なんでも\nお申し付けください", 109701, 3136);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 109701, 3137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 109701, 3138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 109701, 3139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 109701, 3140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 109701, 3141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食の時間ですね\n何か食べたいものは\nありますか？", 109701, 3142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 109701, 3143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 109701, 3145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 109701, 3146);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "よい香りですね\n魔女教の悪臭とは\n大違いです", 109701, 3147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 109701, 3148);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 109701, 3150);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 109701, 3151);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 109801, 3153);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 109801, 3154);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 109801, 3155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 109801, 3156);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 109801, 3157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 109801, 3158);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 109801, 3159);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 109801, 3160);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 109801, 3161);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 109801, 3162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 109801, 3163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 109801, 3164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 109801, 3165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 109801, 3166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 109801, 3167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 109801, 3168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "…ここから動かない\nという、誓いを\n立てたくなるわね", 109801, 3170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 109801, 3171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 109801, 3172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 109801, 3173);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 109801, 3175);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 109801, 3176);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 109901, 3178);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 109901, 3179);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 109901, 3180);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 109901, 3181);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "今日のご飯…\nピーマルは\n入ってないわよね…？", 109901, 3182);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "また、みんなで\n一緒に買い物に\n行きたいな", 109901, 3183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 109901, 3184);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今からパックの\n毛繕いをするけど…\n一緒にする？", 109901, 3185);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 109901, 3186);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 109901, 3187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 109901, 3188);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 109901, 3189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 109901, 3190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 109901, 3191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 109901, 3192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 109901, 3193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 109901, 3195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 109901, 3196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 109901, 3197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 109901, 3198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 109901, 3200);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 109901, 3201);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 110001, 3203);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 110001, 3204);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 110001, 3205);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 110001, 3206);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 110001, 3207);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 110001, 3208);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 110001, 3209);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 110001, 3210);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 110001, 3211);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 110001, 3212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 110001, 3213);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 110001, 3214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 110001, 3215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 110001, 3216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 110001, 3217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 110001, 3218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 110001, 3220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 110001, 3221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 110001, 3222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～い♪\n夏の太陽みたい！", 110001, 3223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 110001, 3225);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "夏のために\n最新ファッションを\n勉強ちう！", 110001, 3226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 110001, 3228);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 110001, 3229);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 110101, 3230);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 110101, 3231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 110101, 3232);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 110101, 3233);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 110101, 3234);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "海が楽しいって\n思えたのも\nあの子のおかげね", 110101, 3235);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 110101, 3236);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 110101, 3237);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 110101, 3238);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 110101, 3239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 110101, 3240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 110101, 3241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 110101, 3242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 110101, 3243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 110101, 3244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 110101, 3245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 110101, 3247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 110101, 3248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この匂い…\nなんだか夏の海を\n思い出すわ～", 110101, 3249);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 110101, 3250);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 110101, 3252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "夏休み中でも\nしっかりお勉強\nしないと！", 110101, 3253);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 110101, 3255);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 110101, 3256);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 110301, 3257);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 110301, 3258);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "子どもたちのために\n浮き輪を用意して\nおかないと…", 110301, 3259);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 110301, 3260);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 110301, 3261);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 110301, 3262);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 110301, 3263);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 110301, 3264);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 110301, 3265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 110301, 3266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 110301, 3267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 110301, 3268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 110301, 3269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 110301, 3270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 110301, 3271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 110301, 3272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 110301, 3274);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 110301, 3275);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "夏らしい\nいい香りね…\nなんの香りかしら？", 110301, 3276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 110301, 3277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 110301, 3279);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 110301, 3280);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 110301, 3282);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 110301, 3283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 110401, 3284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "いてて…\nちょっと日焼け\nしすぎたかな", 110401, 3285);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 110401, 3286);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "夏ならではの\n料理…か\n何がいいかな…", 110401, 3287);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "やっぱりここは\n涼しくて快適だな", 110401, 3288);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 110401, 3289);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "水着で部屋の中に\nいるのって\n変な感じだな…", 110401, 3290);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 110401, 3291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 110401, 3292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "夏バテしてないか？\nここで休んでけよ", 110401, 3293);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "…そう見られると\n照れるっていうか…", 110401, 3294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 110401, 3295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "おまえと\n夏の思い出ができて\nよかったよ", 110401, 3296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 110401, 3297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 110401, 3298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日もお疲れ\n明日のパトロールも\nよろしくな", 110401, 3299);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "泳ぎ疲れたからな\n少し休憩だ…", 110401, 3301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいい…\nいや、なんでもねぇ！", 110401, 3302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 110401, 3303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 110401, 3304);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "海で食べると\nまた一味違うよな", 110401, 3306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "夏のサバイバル特集…\nちょっと読んでみるか", 110401, 3307);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 110401, 3309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 110401, 3310);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 110501, 3311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 110501, 3312);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 110501, 3313);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 110501, 3314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "クロールも\n犬かきも\n大得意さ～！", 110501, 3315);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "なんだか歌いたい\n気分さ～\nキミもどう～？", 110501, 3316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 110501, 3317);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 110501, 3318);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 110501, 3319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 110501, 3320);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 110501, 3321);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 110501, 3322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 110501, 3323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 110501, 3324);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 110501, 3325);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 110501, 3326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 110501, 3328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 110501, 3329);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 110501, 3330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 110501, 3331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 110501, 3333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 110501, 3334);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 110501, 3336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 110501, 3337);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あの人が\nわたしの騎士だったら\nいいのにな…", 105801, 3338);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 105801, 3339);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 105901, 3340);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 105901, 3341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 106001, 3342);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 106001, 3343);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 110601, 3344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 110601, 3345);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 110601, 3346);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 110601, 3347);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 110601, 3348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 110601, 3349);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 110601, 3350);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 110601, 3351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 110601, 3352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "日が落ちたら\n線香花火\nやりたいわぁ♪", 110601, 3353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "木陰でのんびり\nお昼寝…\n最高やね～", 110601, 3354);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 110601, 3355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 110601, 3356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 110601, 3357);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 110601, 3358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 110601, 3359);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 110601, 3361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 110601, 3362);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 110601, 3363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これで\n冷えた身体も\nポカポカやねぇ", 110601, 3364);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 110601, 3366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 110601, 3367);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 110601, 3369);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 110601, 3370);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "真似したくなる\n人がここには\n大勢いますね", 107001, 3371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "偽物を一人\n紛れこませました\n…冗談です", 107001, 3372);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "動物に変身して\nその辺りを\n散歩してきます", 107001, 3373);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "失った記憶は\nどの程度回復するの\nでしょうね", 107001, 3374);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 107001, 3375);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "次は彼に\nどんなことを\n教えましょうか…", 107001, 3376);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "知識はともかく\n私の力はまだ\n必要なようですね", 107001, 3377);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうしました？\n…何でもない？\n…そうですか", 107001, 3378);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "退屈しています\n芸でもして\n楽しませなさい", 107001, 3379);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "研究のためです\n少し話し相手に\nなりなさい", 107001, 3380);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 107001, 3381);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "貴方にはまだ色々と\n教えるべきことが\nありますね", 107001, 3382);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "随分多くの女性と\n知り合いなのですね\n驚きました", 107001, 3383);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "…\nおはようございます\n…まだ朝ですよ", 107001, 3384);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "食事にします\n…どうしました？\n早く持ってきなさい", 107001, 3385);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 107001, 3386);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "マサキより\n座り心地は\nいいですね", 107001, 3388);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "意外に複雑な\n構造ですね", 107001, 3389);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "真似るなら\n香りまで\n再現しなくては", 107001, 3390);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "発火現象の\n模倣…なかなか\n難しいものです", 107001, 3391);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 107001, 3393);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "暇つぶしには\nちょうどよさそうです\n…", 107001, 3394);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "学院かぁ…\n友達の作り方の\n授業はないかな？", 110701, 3396);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 110701, 3397);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 110701, 3398);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 110701, 3399);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 110701, 3400);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 110701, 3401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 110701, 3402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 110701, 3403);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 110701, 3404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 110701, 3405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 110701, 3406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "学院でも、\nBB団の活動を\nしていきましょうね♪", 110701, 3407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 110701, 3408);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 110701, 3409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 110701, 3410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 110701, 3411);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 110701, 3413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 110701, 3414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 110701, 3415);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 110701, 3416);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 110701, 3418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 110701, 3419);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あんた、何してんの。\nいいけど、なんでも", 110801, 3421);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 110801, 3422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 110801, 3423);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 110801, 3424);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 110801, 3425);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 110801, 3426);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 110801, 3427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 110801, 3428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 110801, 3429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 110801, 3430);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 110801, 3431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 110801, 3432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 110801, 3433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 110801, 3434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 110801, 3435);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 110801, 3436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 110801, 3438);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なんだこれ\nかわいいかよ", 110801, 3439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふーん。\nこの匂い…\nなんかいいじゃん", 110801, 3440);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 110801, 3441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 110801, 3443);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 110801, 3444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "イタズラってなにを\nしたらいいのかな？", 111101, 3446);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "カップケーキ\n上手に作れました\n食べてくれるかな…", 111101, 3447);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 111101, 3448);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "オバケ…いないよね？\n怖くなんてないけど！", 111101, 3449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 111101, 3450);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 111101, 3451);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 111101, 3452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 111101, 3453);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "触りましたね？\nイタズラしましたね？\nへんた～い！", 111101, 3454);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 111101, 3455);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 111101, 3456);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 111101, 3457);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 111101, 3458);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 111101, 3459);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 111101, 3460);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 111101, 3461);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 111101, 3463);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 111101, 3464);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "甘い匂いがいっぱい…\nお菓子の香りかな？", 111101, 3465);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 111101, 3466);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ニンジンもこれくらい\nおいしければ\nいいのに…", 111101, 3468);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 111101, 3469);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 111101, 3471);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "たくさんいたずら\nできるから\nハロウィン大好き！", 111201, 3473);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 111201, 3474);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 111201, 3475);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 111201, 3476);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "どうすればみんなを\nびっくりさせられる\nかな～？", 111201, 3477);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 111201, 3478);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 111201, 3479);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 111201, 3480);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 111201, 3481);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 111201, 3482);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 111201, 3483);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 111201, 3484);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 111201, 3485);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 111201, 3486);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 111201, 3487);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 111201, 3488);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 111201, 3490);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 111201, 3491);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 111201, 3492);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 111201, 3493);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 111201, 3495);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "本のなかに\nいたずらのヒント\nないかな～？", 111201, 3496);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 111201, 3498);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 101101, 3500);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 101101, 3501);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 111301, 3502);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 111301, 3503);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 111301, 3504);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 111301, 3505);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 111301, 3506);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 111301, 3507);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 111301, 3508);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 111301, 3509);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 111301, 3510);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 111301, 3511);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 111301, 3512);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 111301, 3513);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 111301, 3514);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 111301, 3515);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 111301, 3516);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 111301, 3517);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 111301, 3519);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 111301, 3520);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 111301, 3521);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 111301, 3522);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしい～♪\nおにいちゃんも\n食べようよー！", 111301, 3524);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 111301, 3525);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 111301, 3527);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ここ、好きだよ。\nみんな優しいから…", 111401, 3529);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "なんかさみしいな", 111401, 3530);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "知らない人怖いな", 111401, 3531);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お部屋おっきいねー", 111401, 3532);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ルナね…\nお昼寝したいかも", 111401, 3533);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "おにいさん！\n今日は何して遊ぶの？", 111401, 3534);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あのね、\nお料理教えてほしい！", 111401, 3535);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あっ、おにいさんだ！", 111401, 3536);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ルナは元気だよ！\nいいこだもん！", 111401, 3537);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今日は何しようかな", 111401, 3538);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ルナと遊んでよ！", 111401, 3539);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ねーねー、\nルナとお出かけして！", 111401, 3540);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "おにいさんと、\n遊びたいなー！", 111401, 3541);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふふっ！\nなんかまぶしいね！", 111401, 3542);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ルナ、お腹空いたな", 111401, 3543);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "どこに行こうかな？", 111401, 3544);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ルナ、これ好きだな", 111401, 3546);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい！　好き！", 111401, 3547);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "なんかいい匂い！", 111401, 3548);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいねー", 111401, 3549);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ふふっ\nおいしいね", 111401, 3551);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ルナ、\n眠くなっちゃう…", 111401, 3552);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 105201, 3554);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あなたとなら\nどんなことだって\nできる気がするわ♪", 105201, 3555);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 106501, 3556);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 106501, 3557);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 106501, 3558);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 106501, 3559);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 106501, 3560);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 106501, 3561);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 106501, 3562);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "よっす！\nなんかオイシイ話でも\nあったりしない？", 106501, 3563);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 106501, 3564);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 106501, 3565);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 106501, 3566);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 106501, 3567);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 106501, 3568);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 106501, 3569);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 106501, 3570);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 106501, 3571);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 106501, 3573);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 106501, 3574);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 106501, 3575);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 106501, 3576);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いつでも全力で\nケンカするために\n力を蓄えないとな", 106501, 3578);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 106501, 3579);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 111501, 3581);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 111501, 3582);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 111501, 3583);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 111501, 3584);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 111501, 3585);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 111501, 3586);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 111501, 3587);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 111501, 3588);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 111501, 3589);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 111501, 3590);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 111501, 3591);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 111501, 3592);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 111501, 3593);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 111501, 3594);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 111501, 3595);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 111501, 3596);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 111501, 3598);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 111501, 3599);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 111501, 3600);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体が温まってくると\nひと暴れしたくなるな", 111501, 3601);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 111501, 3603);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 111501, 3604);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 111501, 3606);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 111501, 3607);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 111601, 3608);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 111601, 3609);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 111601, 3610);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 111601, 3611);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 111601, 3612);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 111601, 3613);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 111601, 3614);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 111601, 3615);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 111601, 3616);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 111601, 3617);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 111601, 3618);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 111601, 3619);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 111601, 3620);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 111601, 3621);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 111601, 3622);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 111601, 3623);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 111601, 3625);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 111601, 3626);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 111601, 3627);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 111601, 3628);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 111601, 3630);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 111601, 3631);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "面白ーい！\nこの演出、ライブでも\nできないかな？", 111601, 3633);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 111601, 3634);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 101801, 3635);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 101801, 3636);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 111701, 3637);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 111701, 3638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 111701, 3639);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 111701, 3640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 111701, 3641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 111701, 3642);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 111701, 3643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 111701, 3644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 111701, 3645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 111701, 3646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 111701, 3647);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 111701, 3648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 111701, 3649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 111701, 3650);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 111701, 3651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 111701, 3652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 111701, 3654);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 111701, 3655);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 111701, 3656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 111701, 3657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 111701, 3659);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 111701, 3660);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 111701, 3662);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 111701, 3663);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 112001, 3664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 112001, 3665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "初日の出って\n特別な感じがして\nいいわよね…", 112001, 3666);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 112001, 3667);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 112001, 3668);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 112001, 3669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今年もあいつと\n居られること…\n結構うれしいわ", 112001, 3670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 112001, 3671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 112001, 3672);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 112001, 3673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "門松飾るから\nちょっと手伝って\nくれる？", 112001, 3674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 112001, 3675);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 112001, 3676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 112001, 3677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おせちに飽きたら\n次は何を食べよう\nかしら…", 112001, 3678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 112001, 3679);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 112001, 3681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 112001, 3682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂いね\n落ち着いて\n眠れそう…", 112001, 3683);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 112001, 3684);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 112001, 3686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "魔物を使わない\nおせちを\n調べておかなきゃ…", 112001, 3687);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 112101, 3689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 112101, 3690);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 112101, 3691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 112101, 3692);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 112101, 3693);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 112101, 3694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 112101, 3695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 112101, 3696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 112101, 3697);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 112101, 3698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 112101, 3699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 112101, 3700);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 112101, 3701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 112101, 3702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 112101, 3703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 112101, 3704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 112101, 3706);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 112101, 3707);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 112101, 3708);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 112101, 3709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 112101, 3711);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 112101, 3712);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 103401, 3714);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 103401, 3715);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 111901, 3716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "年を越したあとに\n食べるおそばも\nおいしいです", 111901, 3717);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "甘酒を飲むと\n心まで温まる\n気がいたします", 111901, 3718);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 111901, 3719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 111901, 3720);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "主さまの温もりは\n心まで温めてくれる\nようです", 111901, 3721);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 111901, 3722);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "主さま、この晴れ着は\n似合っている\nでしょうか？", 111901, 3723);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お餅はゆっくりと\n召し上がって\nくださいまし", 111901, 3724);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 111901, 3725);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 111901, 3726);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 111901, 3727);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 111901, 3728);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 111901, 3729);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 111901, 3730);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 111901, 3731);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふか…\nこのまま\n寝てしまいそうです", 111901, 3733);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 111901, 3734);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 111901, 3735);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "風邪をひきませんよう\n暖かくいたしましょう", 111901, 3736);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べ過ぎは\n正月太りの\nもとだというのに…", 111901, 3738);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "主さまのために\n参拝の作法を\n予習しておきましょう", 111901, 3739);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 112201, 3741);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 112201, 3742);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 112201, 3743);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 112201, 3744);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 112201, 3745);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 112201, 3746);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 112201, 3747);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 112201, 3748);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 112201, 3749);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 112201, 3750);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 112201, 3751);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 112201, 3752);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 112201, 3753);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 112201, 3754);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 112201, 3755);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 112201, 3756);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 112201, 3758);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 112201, 3759);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 112201, 3760);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 112201, 3761);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 112201, 3763);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 112201, 3764);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 112301, 3766);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 112301, 3767);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ふふ…\n今日はなんだか\n調子がいいかも", 112301, 3768);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 112301, 3769);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 112301, 3770);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人がいると\nなんだかホッとする\n不思議…", 112301, 3771);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 112301, 3772);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 112301, 3773);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "よかったら魔法の\n練習に付き合って\nくれませんか？", 112301, 3774);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あの…\nこの格好どうですか？\n似合ってると…いいな", 112301, 3775);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 112301, 3776);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "私、あなたのそばに\nいられたら、それで…", 112301, 3777);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 112301, 3778);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 112301, 3779);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 112301, 3780);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 112301, 3781);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 112301, 3783);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 112301, 3784);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 112301, 3785);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 112301, 3786);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 112301, 3788);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふふ…\n魔法少女になっても\n読書はやめられないな", 112301, 3789);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 101001, 3791);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 101001, 3792);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 180401, 3793);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 180401, 3794);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 180401, 3795);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 180401, 3796);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 180401, 3797);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 180401, 3798);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 180401, 3799);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 180401, 3800);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 180401, 3801);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 180401, 3802);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 180401, 3803);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 180401, 3804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 180401, 3805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 180401, 3806);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 180401, 3807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 180401, 3808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 180401, 3810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 180401, 3811);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 180401, 3812);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 180401, 3813);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 180401, 3815);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 180401, 3816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 112401, 3818);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 112401, 3819);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 112401, 3820);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 112401, 3821);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 112401, 3822);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 112401, 3823);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 112401, 3824);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 112401, 3825);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 112401, 3826);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 112401, 3827);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 112401, 3828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 112401, 3829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 112401, 3830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます！\n今日も一日\n頑張りましょうね！", 112401, 3831);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 112401, 3832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 112401, 3833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 112401, 3835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 112401, 3836);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "リラックスできる\nいい香りですね～♪", 112401, 3837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 112401, 3838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 112401, 3840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 112401, 3841);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 112601, 3843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 112601, 3844);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 112601, 3845);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 112601, 3846);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 112601, 3847);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 112601, 3848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 112601, 3849);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なになにー？\nミオちゃんに\n何か用かな～？", 112601, 3850);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 112601, 3851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 112601, 3852);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 112601, 3853);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 112601, 3854);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 112601, 3855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 112601, 3856);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 112601, 3857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 112601, 3858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 112601, 3860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 112601, 3861);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 112601, 3862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 112601, 3863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 112601, 3865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 112601, 3866);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 100101, 3868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 100101, 3869);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "騎士クンの前で\n好きって言えるように\n練習しないと…", 100201, 3870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 100201, 3871);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 100301, 3872);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 100301, 3873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 112501, 3874);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 112501, 3875);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 112501, 3876);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 112501, 3877);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 112501, 3878);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 112501, 3879);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 112501, 3880);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 112501, 3881);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 112501, 3882);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 112501, 3883);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 112501, 3884);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 112501, 3885);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 112501, 3886);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 112501, 3887);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 112501, 3888);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 112501, 3889);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 112501, 3891);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 112501, 3892);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 112501, 3893);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 112501, 3894);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 112501, 3896);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 112501, 3897);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 111001, 3899);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 111001, 3900);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 111001, 3901);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 111001, 3902);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 111001, 3903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 111001, 3904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 111001, 3905);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 111001, 3906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 111001, 3907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 111001, 3908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 111001, 3909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 111001, 3910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 111001, 3911);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 111001, 3912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 111001, 3913);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 111001, 3914);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 111001, 3916);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 111001, 3917);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 111001, 3918);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "炎はいいぞ\nそこには人類発展の\nドラマがある", 111001, 3919);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "フオオー！\n次から次へと固形食物\n原理気になるー！", 111001, 3921);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 111001, 3922);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 101201, 3924);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 101201, 3925);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ちぇるーん☆", 192101, 3933);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 110901, 3951);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "どこかに楽しいもの\n落ちてないかにゃー", 110901, 3952);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 110901, 3953);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 110901, 3954);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 110901, 3955);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 110901, 3956);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 110901, 3957);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "え、なんですか？\n告白だったら\n間に合ってますけど", 110901, 3958);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 110901, 3959);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 110901, 3960);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 110901, 3961);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 110901, 3962);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 110901, 3963);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはちぇる～ん☆\nハイ、ご一緒に～？", 110901, 3964);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日のランチは\nどこで\n映えよっかにゃー", 110901, 3965);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 110901, 3966);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかチエルン\n略して、ふかルン♪", 110901, 3968);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 110901, 3969);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 110901, 3970);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "マシュマロは\n焼かない方のが\nかわいくないです？", 110901, 3971);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "チエル食べても\n太らない体質\nなんでぇ～！", 110901, 3973);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 110901, 3974);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 112701, 3976);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 112701, 3977);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あたしの代わりに誰か\n働いてくれないかね", 112701, 3978);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "労働の後に食べる\nあんパン最高～", 112701, 3979);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 112701, 3980);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 112701, 3981);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "次の休みはあいつと\n一日中だらだら\nしたいな～", 112701, 3982);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 112701, 3983);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お、差し入れ？\n気が利くねぇ\nあんがと", 112701, 3984);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "新しい依頼？\n今日はもう無理～\n閉店で～す", 112701, 3985);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 112701, 3986);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 112701, 3987);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 112701, 3988);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 112701, 3989);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 112701, 3990);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 112701, 3991);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 112701, 3993);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 112701, 3994);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "あんパンの香りの\nアロマとか\nあればいいのにな～", 112701, 3995);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 112701, 3996);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 112701, 3998);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 112701, 3999);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 104601, 4001);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 104601, 4002);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "フロンティア\nスピリッツを\nお見せするべ！", 112801, 4003);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この鎧を着ると\n気が引き締まる\n感じがするべ", 112801, 4004);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 112801, 4005);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 112801, 4006);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 112801, 4007);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "みんなオラより\n若ぇのに働いてて\n立派だべな", 112801, 4008);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 112801, 4009);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 112801, 4010);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 112801, 4011);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 112801, 4012);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 112801, 4013);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 112801, 4014);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 112801, 4015);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 112801, 4016);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 112801, 4017);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 112801, 4018);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "牧草みたいに\nふっかふかだべ～", 112801, 4020);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子、目の感じが\nエリザベスに似てるべ", 112801, 4021);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂いだべ～\n疲れた体に\nしみるべな～", 112801, 4022);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったけぇな～\n牧場にも\n置いてみっかな～", 112801, 4023);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "うまいべ！\n生産者のこだわりを\n感じるべさ", 112801, 4025);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "酪農も戦いも\nまずは知識だべ！", 112801, 4026);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 113001, 4028);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 113001, 4029);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 113001, 4030);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "なんだか先輩の\n気配がするような…\n気のせいかな？", 113001, 4031);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 113001, 4032);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 113001, 4033);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 113001, 4034);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 113001, 4035);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 113001, 4036);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 113001, 4037);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 113001, 4038);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 113001, 4039);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 113001, 4040);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 113001, 4041);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 113001, 4042);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 113001, 4043);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 113001, 4045);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 113001, 4046);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 113001, 4047);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 113001, 4048);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 113001, 4050);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 113001, 4051);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 180501, 4053);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 180501, 4054);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "月々の食費が\n増えてしまいました\n申し訳ございません…", 180501, 4055);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 180501, 4056);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 180501, 4057);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 180501, 4058);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 180501, 4059);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おでかけですか？\nではわたくしも\nお供いたします", 180501, 4060);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 180501, 4061);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 180501, 4062);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 180501, 4063);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 180501, 4064);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 180501, 4065);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 180501, 4066);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 180501, 4067);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 180501, 4068);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 180501, 4070);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 180501, 4071);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 180501, 4072);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "主さまの心と\n繋がったときのような\n温もりを感じます", 180501, 4073);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 180501, 4075);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 180501, 4076);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 104801, 4078);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 104801, 4079);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 112901, 4080);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 112901, 4081);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 112901, 4082);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 112901, 4083);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 112901, 4084);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 112901, 4085);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 112901, 4086);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 112901, 4087);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 112901, 4088);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 112901, 4089);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 112901, 4090);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 112901, 4091);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 112901, 4092);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 112901, 4093);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 112901, 4094);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 112901, 4095);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 112901, 4097);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 112901, 4098);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 112901, 4099);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 112901, 4100);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 112901, 4102);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 112901, 4103);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "退屈です…\nなにか楽しいことは\nないですか？", 106601, 4105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 106601, 4106);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 106601, 4107);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 106601, 4108);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 106601, 4109);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 106601, 4110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 106601, 4111);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 106601, 4112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 106601, 4113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 106601, 4114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お兄さんは\nヒマそうですね…\nうらやましいです", 106601, 4115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 106601, 4116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 106601, 4117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "デキるギャングの\n朝は早いのです…\nふぁあ…", 106601, 4118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 106601, 4119);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 106601, 4120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ～…\nもうここから一歩も\n動きたくないです", 106601, 4122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 106601, 4123);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "なんかおしゃれな\n匂いがするですね…", 106601, 4124);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 106601, 4125);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 106601, 4127);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふーむ…\nもっと楽しい本は\nないですかね？", 106601, 4128);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 113201, 4130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 113201, 4131);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "アターック！\nいや、もっとこう…\nアタァーック！", 113201, 4132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 113201, 4133);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 113201, 4134);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 113201, 4135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 113201, 4136);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 113201, 4137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 113201, 4138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 113201, 4139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 113201, 4140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 113201, 4141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 113201, 4142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 113201, 4143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 113201, 4144);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 113201, 4145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 113201, 4147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 113201, 4148);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "心地よい香りで\n我が精神も安らぐ…", 113201, 4149);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 113201, 4150);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 113201, 4152);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 113201, 4153);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 113201, 4155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 113201, 4156);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 180201, 4157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 180201, 4158);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 180201, 4159);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 180201, 4160);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 180201, 4161);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "騎士クン…\n今どこにいるの…？\n…会いたいな", 180201, 4162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 180201, 4163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 180201, 4164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 180201, 4165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 180201, 4166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 180201, 4167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 180201, 4168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 180201, 4169);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 180201, 4170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 180201, 4171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 180201, 4172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 180201, 4174);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 180201, 4175);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 180201, 4176);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "温かい…\n騎士クンがくれる\n力みたい…", 180201, 4177);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 180201, 4179);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 180201, 4180);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 104901, 4182);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 104901, 4183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 113101, 4184);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 113101, 4185);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 113101, 4186);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 113101, 4187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 113101, 4188);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 113101, 4189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 113101, 4190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 113101, 4191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 113101, 4192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 113101, 4193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 113101, 4194);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 113101, 4195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 113101, 4196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 113101, 4197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 113101, 4198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 113101, 4199);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 113101, 4201);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あんたたちも\n海を楽しみたいって\n思っているのかね", 113101, 4202);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 113101, 4203);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "こういう格好だと\n少し冷えちまうんだよ", 113101, 4204);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 113101, 4206);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 113101, 4207);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "へえ…\nなかなかいい\n乗り心地じゃないか", 113101, 4209);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 113101, 4210);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 113301, 4211);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 113301, 4212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "今のナナカちゃんは\nたぶん水属性！", 113301, 4213);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 113301, 4214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 113301, 4215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 113301, 4216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 113301, 4217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 113301, 4218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 113301, 4219);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 113301, 4220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 113301, 4221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 113301, 4222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 113301, 4223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 113301, 4224);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 113301, 4225);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 113301, 4226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 113301, 4228);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 113301, 4229);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 113301, 4230);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "夜になると急に冷えて\n肌寒いんだよね～…", 113301, 4231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 113301, 4233);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 113301, 4234);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 113301, 4236);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ナナカちゃんの\n誕生～\nなんちゃって", 113301, 4237);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ファイトファイト☆\n一生懸命\n応援するね！", 113401, 4238);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 113401, 4239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 113401, 4240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 113401, 4241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 113401, 4242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 113401, 4243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "キミへの応援\nちゃんと\n届いてるかな…", 113401, 4244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 113401, 4245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どうしたの？\n元気がないなら\n私が応援するよ☆", 113401, 4246);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 113401, 4247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 113401, 4248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 113401, 4249);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミを応援\nすることができて…\n私、すごく幸せ…", 113401, 4250);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 113401, 4251);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 113401, 4252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 113401, 4253);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 113401, 4255);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 113401, 4256);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 113401, 4257);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 113401, 4258);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 113401, 4260);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 113401, 4261);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 113401, 4263);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 113401, 4264);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 113501, 4265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 113501, 4266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 113501, 4267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 113501, 4268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 113501, 4269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 113501, 4270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 113501, 4271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 113501, 4272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 113501, 4273);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 113501, 4274);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 113501, 4275);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 113501, 4276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 113501, 4277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 113501, 4278);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 113501, 4279);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 113501, 4280);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 113501, 4282);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 113501, 4283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 113501, 4284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 113501, 4285);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 113501, 4287);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 113501, 4288);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 113501, 4290);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 113501, 4291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 102301, 4292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 102301, 4293);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 113601, 4294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この格好で\n過ごすのは\n少し勇気がいるな…", 113601, 4295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 113601, 4296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 113601, 4297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 113601, 4298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 113601, 4299);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "少年との\n海のバカンス…\nまた行きたい\nものだな", 113601, 4300);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 113601, 4301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 113601, 4302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 113601, 4303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 113601, 4304);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 113601, 4305);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 113601, 4306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 113601, 4307);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食のお誘いかい？\nいいよ、付き合おう", 113601, 4308);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もう夜か…\n一日が経つのは\nはやいものだね", 113601, 4309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 113601, 4311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うん…ふわふわの\n感触が\nよくわかるな…", 113601, 4312);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 113601, 4313);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 113601, 4314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "兜を気にせず\n食べられるのは\n楽でいいな…", 113601, 4316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむ…鎧がないと\nページがめくりやすい", 113601, 4317);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 113601, 4319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 113601, 4320);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 106801, 4321);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 106801, 4322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "色んな人を観察できて\n眼福眼福…♪", 106801, 4323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 106801, 4324);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 106801, 4325);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 106801, 4326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 106801, 4327);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 106801, 4328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 106801, 4329);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 106801, 4330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 106801, 4331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 106801, 4332);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 106801, 4333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 106801, 4334);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 106801, 4335);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 106801, 4336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 106801, 4338);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "…これ\nネネカが変身\nしてないよね？", 106801, 4339);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りだ…\n解析完了\n今度生成しようっと♪", 106801, 4340);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "安心するな～…\nアジトに置けないから\n羨ましいよ", 106801, 4341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 106801, 4343);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 106801, 4344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 113701, 4346);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 113701, 4347);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 113701, 4348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 113701, 4349);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 113701, 4350);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 113701, 4351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 113701, 4352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 113701, 4353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 113701, 4354);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 113701, 4355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 113701, 4356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 113701, 4357);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 113701, 4358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 113701, 4359);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 113701, 4360);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 113701, 4361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 113701, 4363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 113701, 4364);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 113701, 4365);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 113701, 4366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 113701, 4368);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 113701, 4369);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あの方の前では\n常に気高く\nありたいですわね", 103201, 4371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 103201, 4372);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 102801, 4373);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 102801, 4374);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 113801, 4375);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 113801, 4376);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 113801, 4377);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 113801, 4378);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "エンジェルになると\n人見知りがなおる！\n…わけないか", 113801, 4379);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 113801, 4380);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 113801, 4381);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 113801, 4382);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 113801, 4383);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 113801, 4384);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 113801, 4385);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 113801, 4386);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 113801, 4387);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 113801, 4388);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ふぅ、いいことした\n後のランチは\nおいしいわね", 113801, 4389);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 113801, 4390);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 113801, 4392);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 113801, 4393);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 113801, 4394);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 113801, 4395);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 113801, 4397);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 113801, 4398);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 113901, 4400);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 113901, 4401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 113901, 4402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 113901, 4403);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "マントは動きに\nメリハリがついて\nいいですね", 113901, 4404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 113901, 4405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 113901, 4406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 113901, 4407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 113901, 4408);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 113901, 4409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 113901, 4410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 113901, 4411);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 113901, 4412);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 113901, 4413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 113901, 4414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 113901, 4415);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このフィット感\nどうにか服作りにも\n活かせれば…！", 113901, 4417);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 113901, 4418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 113901, 4419);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 113901, 4420);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 113901, 4422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 113901, 4423);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 113901, 4425);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 114101, 4427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 114101, 4428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 114101, 4429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 114101, 4430);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 114101, 4431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 114101, 4432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 114101, 4433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 114101, 4434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 114101, 4435);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 114101, 4436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 114101, 4437);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あんたも一緒に\n新しい技の練習\nどうッスか？", 114101, 4438);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 114101, 4439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 114101, 4440);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 114101, 4441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 114101, 4442);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 114101, 4444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 114101, 4445);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 114101, 4446);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 114101, 4447);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 114101, 4449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 114101, 4450);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 114101, 4452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 103001, 4454);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 103001, 4455);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 114001, 4456);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 114001, 4457);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 114001, 4458);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 114001, 4459);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 114001, 4460);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 114001, 4461);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 114001, 4462);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 114001, 4463);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 114001, 4464);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どうしたの？\n出かけるなら\n私も同行しようか", 114001, 4465);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 114001, 4466);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 114001, 4467);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 114001, 4468);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 114001, 4469);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 114001, 4470);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 114001, 4471);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ハロウィンはまだ\nこれからだ\n一休みしようか", 114001, 4473);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 114001, 4474);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 114001, 4475);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 114001, 4476);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 114001, 4478);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 114001, 4479);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 114001, 4481);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 114201, 4483);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 114201, 4484);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 114201, 4485);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 114201, 4486);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 114201, 4487);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 114201, 4488);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 114201, 4489);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 114201, 4490);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 114201, 4491);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 114201, 4492);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 114201, 4493);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 114201, 4494);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 114201, 4495);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 114201, 4496);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 114201, 4497);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 114201, 4498);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 114201, 4500);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 114201, 4501);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 114201, 4502);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "この温かさ…\n愛と希望に\n通じるものがある…", 114201, 4503);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 114201, 4505);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 114201, 4506);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 103301, 4508);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 103301, 4509);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ぴっぷる！", 192501, 4510);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ぴっぷるぴっぷる♪", 192501, 4511);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ぴっ！", 192501, 4512);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ぷっぷー", 192501, 4513);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ぴぃ？", 192501, 4514);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ぴっぷるんるん♪", 192501, 4515);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ぷぅ！", 192501, 4516);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ぴぴっ♪", 192501, 4517);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ぷぅー…", 192501, 4518);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ちゅ！", 192501, 4519);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ぷはー", 192501, 4520);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ぴぇー", 192501, 4521);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 114301, 4531);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 114301, 4532);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 114301, 4533);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 114301, 4534);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 114301, 4535);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 114301, 4536);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 114301, 4537);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "もし時間があるなら、\n私と街の見回りに\n行かない？", 114301, 4538);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 114301, 4539);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 114301, 4540);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 114301, 4541);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 114301, 4542);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 114301, 4543);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 114301, 4544);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 114301, 4545);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 114301, 4546);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 114301, 4548);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 114301, 4549);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 114301, 4550);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 114301, 4551);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "こ、これは…\nおいしすぎて手が\n止まらないかも", 114301, 4553);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 114301, 4554);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 114401, 4556);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 114401, 4557);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 114401, 4558);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 114401, 4559);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 114401, 4560);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 114401, 4561);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 114401, 4562);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 114401, 4563);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 114401, 4564);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 114401, 4565);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 114401, 4566);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 114401, 4567);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 114401, 4568);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 114401, 4569);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 114401, 4570);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 114401, 4571);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "適度な休息は\n成功の秘訣ですわ！", 114401, 4573);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 114401, 4574);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 114401, 4575);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 114401, 4576);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 114401, 4578);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 114401, 4579);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 114401, 4581);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 114401, 4582);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 114601, 4583);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 114601, 4584);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 114601, 4585);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 114601, 4586);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 114601, 4587);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 114601, 4588);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 114601, 4589);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 114601, 4590);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 114601, 4591);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 114601, 4592);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お姉さんの\n頼れるところ\n見せてあげるわね", 114601, 4593);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "何かあったら\n頼ってくれても\nいいんだからね？", 114601, 4594);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 114601, 4595);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 114601, 4596);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 114601, 4597);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 114601, 4598);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 114601, 4600);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "私だって…\nかわいいものは\n好きでいいわよね？", 114601, 4601);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 114601, 4602);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 114601, 4603);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 114601, 4605);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 114601, 4606);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 114601, 4608);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 114601, 4609);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 101601, 4610);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 101601, 4611);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 114501, 4612);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 114501, 4613);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 114501, 4614);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 114501, 4615);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 114501, 4616);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 114501, 4617);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 114501, 4618);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 114501, 4619);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 114501, 4620);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 114501, 4621);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 114501, 4622);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 114501, 4623);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 114501, 4624);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 114501, 4625);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 114501, 4626);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 114501, 4627);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 114501, 4629);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 114501, 4630);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "華やかな香りね\nクリスマスに\nピッタリだわ", 114501, 4631);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 114501, 4632);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 114501, 4634);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 114501, 4635);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 114501, 4637);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 114501, 4638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 114701, 4639);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今年こそ、もう少し\n身長が伸びます\nように…", 114701, 4640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 114701, 4641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 114701, 4642);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 114701, 4643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 114701, 4644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 114701, 4645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 114701, 4646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 114701, 4647);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 114701, 4648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 114701, 4649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 114701, 4650);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 114701, 4651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 114701, 4652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 114701, 4653);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 114701, 4654);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 114701, 4656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なんだ？\nオマエも初詣に\n行きたいのか？", 114701, 4657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 114701, 4658);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "コイツがあれば\nうまい魚が\n焼けそうだな…", 114701, 4659);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 114701, 4661);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "おみくじって\n書いてあることが\nむずかしいんだよな…", 114701, 4662);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 180101, 4664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 180101, 4665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 180101, 4666);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 180101, 4667);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 180101, 4668);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 180101, 4669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 180101, 4670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 180101, 4671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 180101, 4672);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 180101, 4673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 180101, 4674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 180101, 4675);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 180101, 4676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 180101, 4677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 180101, 4678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 180101, 4679);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 180101, 4681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 180101, 4682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 180101, 4683);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 180101, 4684);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 180101, 4686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 180101, 4687);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 111801, 4689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 111801, 4690);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 111801, 4691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 111801, 4692);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 111801, 4693);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ずっとこうして\n年の始まりを\n過ごしたいですね", 111801, 4694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "いつか二人きりで\n初日の出を\n見たいです", 111801, 4695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お餅おかわりです！\nいくらでも\n食べられますよ！", 111801, 4696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 111801, 4697);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 111801, 4698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 111801, 4699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 111801, 4700);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 111801, 4701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "年明けの朝は\n心なしか\n気持ちいいですね☆", 111801, 4702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 111801, 4703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あたたかくして\n幸せな初夢を\n見ましょうね", 111801, 4704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 111801, 4706);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 111801, 4707);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 111801, 4708);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 111801, 4709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしすぎて\n食べる手が\n止まりません～", 111801, 4711);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 111801, 4712);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 100601, 4714);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 100601, 4715);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 115001, 4716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 115001, 4717);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 115001, 4718);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 115001, 4719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 115001, 4720);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 115001, 4721);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 115001, 4722);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 115001, 4723);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 115001, 4724);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 115001, 4725);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 115001, 4726);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 115001, 4727);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 115001, 4728);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 115001, 4729);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 115001, 4730);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 115001, 4731);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "少し休みます…\n時間が来たら\n起こすように", 115001, 4733);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 115001, 4734);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 115001, 4735);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 115001, 4736);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いかなる強者でも\n兵站、補給は\n必要なものです", 115001, 4738);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 115001, 4739);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 115501, 4741);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 115501, 4742);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "主さまも精霊の召喚を\n試してみますか？", 115501, 4743);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 115501, 4744);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 115501, 4745);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "進化した精霊に\n負けぬよう\n修行に励みます", 115501, 4746);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 115501, 4747);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 115501, 4748);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 115501, 4749);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "里で採れた果物を\n持って参りました\n召し上がってください", 115501, 4750);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 115501, 4751);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 115501, 4752);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 115501, 4753);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 115501, 4754);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 115501, 4755);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 115501, 4756);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "この癒し…\n主さまにも味わって\nいただきたい…", 115501, 4758);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 115501, 4759);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 115501, 4760);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "瞑想するのに\nちょうどよい暖かさで\nございます", 115501, 4761);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 115501, 4763);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "風の精霊について\nもっと詳しく知って\nおかなければ…", 115501, 4764);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 115601, 4766);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 115601, 4767);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 115601, 4768);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 115601, 4769);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 115601, 4770);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 115601, 4771);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 115601, 4772);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 115601, 4773);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 115601, 4774);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 115601, 4775);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 115601, 4776);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 115601, 4777);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 115601, 4778);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 115601, 4779);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 115601, 4780);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 115601, 4781);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 115601, 4783);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 115601, 4784);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 115601, 4785);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 115601, 4786);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "自然の恵みに\n感謝して…\nいただきます♪", 115601, 4788);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 115601, 4789);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 102201, 4791);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 102201, 4792);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 106401, 4793);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 106401, 4794);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 106401, 4795);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 106401, 4796);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 106401, 4797);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 106401, 4798);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 106401, 4799);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 106401, 4800);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 106401, 4801);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 106401, 4802);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 106401, 4803);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 106401, 4804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 106401, 4805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 106401, 4806);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 106401, 4807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 106401, 4808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 106401, 4810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 106401, 4811);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 106401, 4812);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 106401, 4813);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 106401, 4815);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ん…なるほどね。\n勉強になるわ…", 106401, 4816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 115701, 4818);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "依頼があれば\n海でも山でも\n赴くさ！", 115701, 4819);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 115701, 4820);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 115701, 4821);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 115701, 4822);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 115701, 4823);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 115701, 4824);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 115701, 4825);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 115701, 4826);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 115701, 4827);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 115701, 4828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 115701, 4829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 115701, 4830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 115701, 4831);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 115701, 4832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 115701, 4833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 115701, 4835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 115701, 4836);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 115701, 4837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ポカポカするね…\n助手くん\n君もおいでよ", 115701, 4838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 115701, 4840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 115701, 4841);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 115701, 4843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 115701, 4844);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 180601, 4845);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 180601, 4846);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 180601, 4847);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 180601, 4848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 180601, 4849);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 180601, 4850);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 180601, 4851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 180601, 4852);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 180601, 4853);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 180601, 4854);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 180601, 4855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 180601, 4856);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 180601, 4857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 180601, 4858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 180601, 4859);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 180601, 4860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 180601, 4862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 180601, 4863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 180601, 4864);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 180601, 4865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 180601, 4867);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 180601, 4868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 115801, 4870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 115801, 4871);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 115801, 4872);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 115801, 4873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 115801, 4874);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 115801, 4875);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 115801, 4876);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうかしら？\n私、きちんとドレス\n着られてる？", 115801, 4877);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 115801, 4878);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 115801, 4879);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 115801, 4880);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 115801, 4881);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 115801, 4882);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 115801, 4883);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 115801, 4884);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 115801, 4885);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 115801, 4887);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 115801, 4888);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "すてきな香り…\nお花かしら？\n森で探してみようかな", 115801, 4889);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 115801, 4890);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "レッスンのために\n栄養補給して\nおかないと！", 115801, 4892);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "教養を身に着ける\nことも大切よね！\nなになに…", 115801, 4893);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 115901, 4895);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 115901, 4896);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 115901, 4897);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 115901, 4898);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 115901, 4899);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 115901, 4900);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 115901, 4901);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 115901, 4902);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 115901, 4903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 115901, 4904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 115901, 4905);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 115901, 4906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 115901, 4907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 115901, 4908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 115901, 4909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 115901, 4910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 115901, 4912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 115901, 4913);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 115901, 4914);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 115901, 4915);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 115901, 4917);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 115901, 4918);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 102001, 4920);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 102001, 4921);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 100401, 4922);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 100401, 4923);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 103601, 4924);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 103601, 4925);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "くるくる〜♪\nうちのドレス\nかわええなぁ", 116001, 4926);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 116001, 4927);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 116001, 4928);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 116001, 4929);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 116001, 4930);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 116001, 4931);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 116001, 4932);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 116001, 4933);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 116001, 4934);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 116001, 4935);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 116001, 4936);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 116001, 4937);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 116001, 4938);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 116001, 4939);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 116001, 4940);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 116001, 4941);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 116001, 4943);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 116001, 4944);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 116001, 4945);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 116001, 4946);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 116001, 4948);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 116001, 4949);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 116301, 4951);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 116301, 4952);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 116301, 4953);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 116301, 4954);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 116301, 4955);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 116301, 4956);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 116301, 4957);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 116301, 4958);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 116301, 4959);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 116301, 4960);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 116301, 4961);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 116301, 4962);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 116301, 4963);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 116301, 4964);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 116301, 4965);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 116301, 4966);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 116301, 4968);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 116301, 4969);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 116301, 4970);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 116301, 4971);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 116301, 4973);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 116301, 4974);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 102901, 4976);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 102901, 4977);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 116201, 4978);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 116201, 4979);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 116201, 4980);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 116201, 4981);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 116201, 4982);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 116201, 4983);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 116201, 4984);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 116201, 4985);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 116201, 4986);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 116201, 4987);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 116201, 4988);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 116201, 4989);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 116201, 4990);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 116201, 4991);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 116201, 4992);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 116201, 4993);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 116201, 4995);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 116201, 4996);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 116201, 4997);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 116201, 4998);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 116201, 5000);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 116201, 5001);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 116501, 5003);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 116501, 5004);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 116501, 5005);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 116501, 5006);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 116501, 5007);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 116501, 5008);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 116501, 5009);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 116501, 5010);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 116501, 5011);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 116501, 5012);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 116501, 5013);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 116501, 5014);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 116501, 5015);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 116501, 5016);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹が空きました…\nご飯食べにいきますよ", 116501, 5017);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 116501, 5018);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 116501, 5020);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 116501, 5021);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 116501, 5022);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 116501, 5023);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 116501, 5025);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 116501, 5026);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 102501, 5028);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 102501, 5029);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 116601, 5030);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 116601, 5031);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 116601, 5032);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 116601, 5033);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 116601, 5034);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 116601, 5035);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 116601, 5036);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 116601, 5037);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 116601, 5038);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 116601, 5039);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 116601, 5040);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 116601, 5041);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 116601, 5042);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 116601, 5043);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 116601, 5044);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 116601, 5045);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 116601, 5047);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 116601, 5048);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 116601, 5049);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 116601, 5050);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 116601, 5052);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 116601, 5053);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 116701, 5055);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 116701, 5056);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 116701, 5057);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 116701, 5058);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 116701, 5059);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 116701, 5060);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 116701, 5061);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 116701, 5062);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 116701, 5063);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 116701, 5064);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 116701, 5065);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 116701, 5066);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 116701, 5067);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 116701, 5068);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 116701, 5069);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 116701, 5070);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 116701, 5072);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 116701, 5073);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 116701, 5074);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽか…\n幸せなひとときですね", 116701, 5075);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 116701, 5077);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 116701, 5078);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 116801, 5080);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 116801, 5081);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 116801, 5082);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 116801, 5083);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 116801, 5084);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 116801, 5085);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 116801, 5086);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 116801, 5087);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 116801, 5088);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 116801, 5089);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 116801, 5090);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 116801, 5091);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 116801, 5092);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 116801, 5093);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 116801, 5094);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 116801, 5095);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 116801, 5097);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 116801, 5098);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 116801, 5099);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 116801, 5100);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 116801, 5102);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 116801, 5103);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 102101, 5105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 102101, 5106);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 116901, 5107);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "他のバイトとの\nスケジュールを\n調整しないと…", 116901, 5108);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 116901, 5109);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 116901, 5110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 116901, 5111);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 116901, 5112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 116901, 5113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 116901, 5114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 116901, 5115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 116901, 5116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 116901, 5117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 116901, 5118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 116901, 5119);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 116901, 5120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 116901, 5121);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 116901, 5122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 116901, 5124);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 116901, 5125);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 116901, 5126);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 116901, 5127);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 116901, 5129);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 116901, 5130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "変身している間に\n色々と試しておこう\nまずは…", 180301, 5132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 180301, 5133);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 180301, 5134);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 180301, 5135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 180301, 5136);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 180301, 5137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 180301, 5138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 180301, 5139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 180301, 5140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 180301, 5141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 180301, 5142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 180301, 5143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 180301, 5144);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 180301, 5145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 180301, 5146);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 180301, 5147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 180301, 5149);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 180301, 5150);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 180301, 5151);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ありがたいな\n少し足元が\n冷えていたところだ", 180301, 5152);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これは…！\n持ち運べるように\n改良できないかな？", 180301, 5154);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 180301, 5155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 117001, 5157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 117001, 5158);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 117001, 5159);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 117001, 5160);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 117001, 5161);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あのおかたの全てが\n早く私のものに\nなりますように…", 117001, 5162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 117001, 5163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 117001, 5164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 117001, 5165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 117001, 5166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 117001, 5167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 117001, 5168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 117001, 5169);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 117001, 5170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 117001, 5171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 117001, 5172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 117001, 5174);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 117001, 5175);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 117001, 5176);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あのおかたの\nぬくもりを\n思い出しますわ…", 117001, 5177);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 117001, 5179);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 117001, 5180);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "二人乗りができると\nいいですわね…", 117001, 5182);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 117001, 5183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 101701, 5184);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 101701, 5185);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この水着…\n一番に弟くんに\n見せたいな♪", 117101, 5186);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 117101, 5187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 117101, 5188);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 117101, 5189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 117101, 5190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 117101, 5191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 117101, 5192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 117101, 5193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 117101, 5194);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "海で迷子に\nならないように\n手を繋ごうね♪", 117101, 5195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 117101, 5196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 117101, 5197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 117101, 5198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 117101, 5199);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 117101, 5200);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 117101, 5201);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 117101, 5203);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 117101, 5204);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 117101, 5205);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 117101, 5206);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 117101, 5208);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 117101, 5209);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 117101, 5211);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 117101, 5212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 117201, 5213);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 117201, 5214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "とっておきの\nサマーソングを\nみんなに届けるよ！", 117201, 5215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 117201, 5216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 117201, 5217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 117201, 5218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 117201, 5219);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 117201, 5220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 117201, 5221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 117201, 5222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 117201, 5223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 117201, 5224);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 117201, 5225);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 117201, 5226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 117201, 5227);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 117201, 5228);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 117201, 5230);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 117201, 5231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 117201, 5232);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 117201, 5233);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 117201, 5235);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 117201, 5236);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 117201, 5238);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 117201, 5239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 117401, 5240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 117401, 5241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 117401, 5242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 117401, 5243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 117401, 5244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 117401, 5245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 117401, 5246);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "騎士さんいいところに\nこの衣装\nどう思います？", 117401, 5247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 117401, 5248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 117401, 5249);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "仕立て屋も\nアイドルも\n諦めません！", 117401, 5250);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 117401, 5251);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 117401, 5252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 117401, 5253);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 117401, 5254);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 117401, 5255);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 117401, 5257);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 117401, 5258);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りですね～\n波の音が聞こえて\nきそうです…", 117401, 5259);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "泳いだ後は\n少し寒いので\nちょうどいいですね", 117401, 5260);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "夏バテしないように\n栄養はしっかり\n取らないと！", 117401, 5262);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 117401, 5263);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "デザインはもちろん\n触り心地も\n最高ですね♪", 117401, 5265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 117401, 5266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 104001, 5267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 104001, 5268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この衣装で\nライブするのは\n恥ずかしいな…", 117301, 5269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 117301, 5270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 117301, 5271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 117301, 5272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 117301, 5273);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 117301, 5274);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "水着、似合ってるって\n言ってもらえるかな…", 117301, 5275);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "サインして、ですか？\nいいですけど…\n少し照れますね…", 117301, 5276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 117301, 5277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なんでしょう？\nもしかして…\nボイトレに興味が？", 117301, 5278);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 117301, 5279);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 117301, 5280);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 117301, 5281);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 117301, 5282);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 117301, 5283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 117301, 5284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 117301, 5286);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 117301, 5287);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 117301, 5288);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 117301, 5289);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "アイドルとして、\n食べすぎには\n気をつけないと…", 117301, 5291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 117301, 5292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "この子に乗りながら\nライブをする…\nというのはどうですか", 117301, 5294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "まるで人魚になった\n気分…なんて、ふふ", 117301, 5295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 118301, 5296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 118301, 5297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 118301, 5298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 118301, 5299);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 118301, 5300);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 118301, 5301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 118301, 5302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 118301, 5303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 118301, 5304);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 118301, 5305);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 118301, 5306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 118301, 5307);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 118301, 5308);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 118301, 5309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 118301, 5310);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 118301, 5311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 118301, 5313);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 118301, 5314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 118301, 5315);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 118301, 5316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 118301, 5318);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 118301, 5319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 118401, 5322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 118401, 5323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 118401, 5324);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 118401, 5325);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 118401, 5326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 118401, 5327);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 118401, 5328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 118401, 5329);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 118401, 5330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 118401, 5331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 118401, 5332);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 118401, 5333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 118401, 5334);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 118401, 5335);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 118401, 5336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 118401, 5337);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 118401, 5339);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 118401, 5340);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 118401, 5341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 118401, 5342);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 118401, 5344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 118401, 5345);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 117501, 5348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 117501, 5349);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 117501, 5350);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "新薬開発のために\n薬草を採取して\nおこうかしら", 117501, 5351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "いつ急患が来ても\n対応できるように\nしておきましょうか", 117501, 5352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 117501, 5353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 117501, 5354);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 117501, 5355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 117501, 5356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 117501, 5357);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 117501, 5358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 117501, 5359);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 117501, 5360);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 117501, 5361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 117501, 5362);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 117501, 5363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 117501, 5365);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 117501, 5366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 117501, 5367);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 117501, 5368);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 117501, 5370);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 117501, 5371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 117601, 5400);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "美しさとは\n日々の努力が\n作るものなんだよ", 117601, 5401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "大変だ…\nボクは美しく\nなりすぎたかも", 117601, 5402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 117601, 5403);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "どんな服装をしても\nボクの美しさは\n変わらないね", 117601, 5404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 117601, 5405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 117601, 5406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 117601, 5407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 117601, 5408);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 117601, 5409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 117601, 5410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 117601, 5411);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 117601, 5412);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "さすがボク\n目が覚めるような\n美しさでしょ？", 117601, 5413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "美は食で作られる\n美容にいいものを\n食べようよ！", 117601, 5414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 117601, 5415);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふう～…\nリラックスする\nボクも美しい", 117601, 5417);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 117601, 5418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "美しいボクは\n常にいい香りを\nしていなくちゃね", 117601, 5419);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 117601, 5420);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "バランスのいい食事が\n美しいボクを\n作るのさ", 117601, 5422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 117601, 5423);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 105701, 5425);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 105701, 5426);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 118001, 5427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 118001, 5428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 118001, 5429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 118001, 5430);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 118001, 5431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 118001, 5432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 118001, 5433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 118001, 5434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 118001, 5435);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 118001, 5436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 118001, 5437);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 118001, 5438);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 118001, 5439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 118001, 5440);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 118001, 5441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 118001, 5442);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 118001, 5444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 118001, 5445);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 118001, 5446);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 118001, 5447);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 118001, 5449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 118001, 5450);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 117701, 5452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 117701, 5453);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 117701, 5454);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 117701, 5455);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 117701, 5456);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 117701, 5457);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 117701, 5458);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 117701, 5459);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この制服、\nハロウィンらしくて\n大好きさ～♪", 117701, 5460);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 117701, 5461);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 117701, 5462);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 117701, 5463);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 117701, 5464);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 117701, 5465);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 117701, 5466);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 117701, 5467);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 117701, 5469);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 117701, 5470);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 117701, 5471);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 117701, 5472);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 117701, 5474);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 117701, 5475);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 117701, 5477);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 117801, 5479);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 117801, 5480);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 117801, 5481);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 117801, 5482);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 117801, 5483);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "幽霊になる前に\n天下統一を\n果たしたいデスね", 117801, 5484);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 117801, 5485);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ショーグン！\n悪霊退治に\n出かけるデース！", 117801, 5486);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "妖魔を家来に\nしてみるのは\nどうデス？", 117801, 5487);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "マントを使って\nウツセミの術の精度を\n高めるデス！", 117801, 5488);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 117801, 5489);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あの世でも\nショーグンに\nお仕えしマスよー！", 117801, 5490);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 117801, 5491);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 117801, 5492);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 117801, 5493);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 117801, 5494);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 117801, 5496);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 117801, 5497);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 117801, 5498);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "パレードの熱気に\n負けないくらい\n熱いデス！", 117801, 5499);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 117801, 5501);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 117801, 5502);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 117801, 5504);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 101301, 5531);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 101301, 5532);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 117901, 5533);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 117901, 5534);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 117901, 5535);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 117901, 5536);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 117901, 5537);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 117901, 5538);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 117901, 5539);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 117901, 5540);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 117901, 5541);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 117901, 5542);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 117901, 5543);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 117901, 5544);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 117901, 5545);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 117901, 5546);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 117901, 5547);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 117901, 5548);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 117901, 5550);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 117901, 5551);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 117901, 5552);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "やばばば…\nあったかくて\n眠くなっちゃう～", 117901, 5553);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これ食べて\nもうひとがんばり\nしよーっと！", 117901, 5555);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 117901, 5556);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 117901, 5558);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 118501, 5560);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 118501, 5561);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 118501, 5562);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 118501, 5563);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 118501, 5564);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 118501, 5565);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 118501, 5566);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "何かわからないことが\nあるときは私に\n聞いてくださいね", 118501, 5567);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 118501, 5568);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 118501, 5569);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 118501, 5570);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 118501, 5571);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 118501, 5572);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日も一日\n管理人として\nがんばります♪", 118501, 5573);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹がすきました…\nそろそろお昼に\nしませんか？", 118501, 5574);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 118501, 5575);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "良質なお仕事は\n良質な休憩から\nです♪", 118501, 5577);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 118501, 5578);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 118501, 5579);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 118501, 5580);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 118501, 5582);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "新条例はこのように\n活用されている、と…\nふむふむ…", 118501, 5583);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 106701, 5585);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 106701, 5586);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 106701, 5587);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 106701, 5588);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 106701, 5589);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 106701, 5590);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 106701, 5591);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 106701, 5592);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 106701, 5593);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 106701, 5594);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 106701, 5595);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 106701, 5596);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 106701, 5597);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 106701, 5598);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 106701, 5599);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 106701, 5600);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 106701, 5602);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 106701, 5603);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 106701, 5604);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 106701, 5605);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "この味付け…\nあっちでも再現\nできるかな？", 106701, 5607);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 106701, 5608);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 119001, 5610);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 119001, 5611);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 119001, 5612);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 119001, 5613);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 119001, 5614);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 119001, 5615);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 119001, 5616);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 119001, 5617);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 119001, 5618);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 119001, 5619);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 119001, 5620);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 119001, 5621);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "君との秘密が\nまた一つ\n増えちゃったね♪", 119001, 5622);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 119001, 5623);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 119001, 5624);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 119001, 5625);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 119001, 5627);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 119001, 5628);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 119001, 5629);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 119001, 5630);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "生徒の見本に\nなれるように\n好き嫌いはしないの", 119001, 5632);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 119001, 5633);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 106301, 5635);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 106301, 5636);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 105301, 5637);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 105301, 5638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ああ～\nドＭミストが\nあふれてしまいます～", 119101, 5639);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 119101, 5640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 119101, 5641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 119101, 5642);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 119101, 5643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 119101, 5644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 119101, 5645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 119101, 5646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 119101, 5647);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 119101, 5648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 119101, 5649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 119101, 5650);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 119101, 5651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 119101, 5652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 119101, 5653);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 119101, 5654);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 119101, 5656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 119101, 5657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 119101, 5658);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 119101, 5659);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 119101, 5661);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 119101, 5662);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 119901, 5664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 119901, 5665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 119901, 5666);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 119901, 5667);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 119901, 5668);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 119901, 5669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 119901, 5670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 119901, 5671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 119901, 5672);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 119901, 5673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 119901, 5674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 119901, 5675);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 119901, 5676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 119901, 5677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 119901, 5678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 119901, 5679);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 119901, 5681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 119901, 5682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 119901, 5683);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 119901, 5684);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これはオマエがくれる\nプリンとは\n別腹なの～", 119901, 5686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 119901, 5687);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 119901, 5689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 119901, 5690);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 119201, 5691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 119201, 5692);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 119201, 5693);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 119201, 5694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 119201, 5695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 119201, 5696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 119201, 5697);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 119201, 5698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 119201, 5699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 119201, 5700);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 119201, 5701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 119201, 5702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 119201, 5703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 119201, 5704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 119201, 5705);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 119201, 5706);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 119201, 5708);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 119201, 5709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 119201, 5710);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 119201, 5711);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 119201, 5713);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 119201, 5714);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 119201, 5716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 119201, 5717);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 105001, 5718);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 105001, 5719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 119301, 5720);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 119301, 5721);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 119301, 5722);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 119301, 5723);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 119301, 5724);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 119301, 5725);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 119301, 5726);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 119301, 5727);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 119301, 5728);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 119301, 5729);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 119301, 5730);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 119301, 5731);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 119301, 5732);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 119301, 5733);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 119301, 5734);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 119301, 5735);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 119301, 5737);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 119301, 5738);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 119301, 5739);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 119301, 5740);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 119301, 5742);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 119301, 5743);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 119301, 5745);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 119301, 5746);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 120601, 5747);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 120601, 5748);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 120601, 5749);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 120601, 5750);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 120601, 5751);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 120601, 5752);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 120601, 5753);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 120601, 5754);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "きゃあっ！？\nへ、変なところを\n触らないでください！", 120601, 5755);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 120601, 5756);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 120601, 5757);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 120601, 5758);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 120601, 5759);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 120601, 5760);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 120601, 5761);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 120601, 5762);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 120601, 5764);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 120601, 5765);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 120601, 5766);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 120601, 5767);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 120601, 5769);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 120601, 5770);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 120401, 5773);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 120401, 5774);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 120401, 5775);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 120401, 5776);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 120401, 5777);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 120401, 5778);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 120401, 5779);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 120401, 5780);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 120401, 5781);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 120401, 5782);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 120401, 5783);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 120401, 5784);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 120401, 5785);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 120401, 5786);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 120401, 5787);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 120401, 5788);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 120401, 5790);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 120401, 5791);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 120401, 5792);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 120401, 5793);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 120401, 5795);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 120401, 5796);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 120501, 5799);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 120501, 5800);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 120501, 5801);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 120501, 5802);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 120501, 5803);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 120501, 5804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 120501, 5805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 120501, 5806);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 120501, 5807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 120501, 5808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 120501, 5809);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 120501, 5810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 120501, 5811);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 120501, 5812);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 120501, 5813);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 120501, 5814);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 120501, 5816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 120501, 5817);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "においをうまく使った\nイタズラって…\nなにかないかな？", 120501, 5818);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 120501, 5819);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 120501, 5821);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 120501, 5822);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 120801, 5825);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 120801, 5826);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 120801, 5827);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 120801, 5828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 120801, 5829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 120801, 5830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 120801, 5831);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 120801, 5832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 120801, 5833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 120801, 5834);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 120801, 5835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 120801, 5836);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 120801, 5837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 120801, 5838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 120801, 5839);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 120801, 5840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 120801, 5842);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 120801, 5843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これは冬の花の香り…\n風情があって\nいいじゃないか", 120801, 5844);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 120801, 5845);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 120801, 5847);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 120801, 5848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 120701, 5850);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 120701, 5851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この格好も\n慣れてくれば\n動きやすいわ", 120701, 5852);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 120701, 5853);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "おせち料理\nネビアの分も\n用意しなくちゃね", 120701, 5854);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 120701, 5855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 120701, 5856);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 120701, 5857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 120701, 5858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 120701, 5859);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 120701, 5860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 120701, 5861);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 120701, 5862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 120701, 5863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 120701, 5864);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 120701, 5865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 120701, 5867);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふっ\nかわいいものって\n癒されるわね…", 120701, 5868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "…ん\nこれが梅の香り？\n…いい匂いね", 120701, 5869);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 120701, 5870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 120701, 5872);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 120701, 5873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 104201, 5875);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 104201, 5876);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 120901, 5877);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 120901, 5878);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 120901, 5879);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 120901, 5880);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 120901, 5881);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 120901, 5882);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 120901, 5883);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 120901, 5884);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 120901, 5885);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 120901, 5886);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 120901, 5887);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 120901, 5888);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 120901, 5889);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 120901, 5890);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 120901, 5891);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 120901, 5892);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 120901, 5894);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 120901, 5895);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 120901, 5896);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 120901, 5897);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 120901, 5899);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 120901, 5900);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 121001, 5902);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 121001, 5903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 121001, 5904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 121001, 5905);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 121001, 5906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 121001, 5907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 121001, 5908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 121001, 5909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 121001, 5910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 121001, 5911);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あなたの想いが\n全力全開を超える\n力をくれました！", 121001, 5912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 121001, 5913);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 121001, 5914);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 121001, 5915);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 121001, 5916);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 121001, 5917);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 121001, 5919);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 121001, 5920);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 121001, 5921);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 121001, 5922);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 121001, 5924);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 121001, 5925);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 121201, 5927);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 121201, 5928);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 121201, 5929);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 121201, 5930);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 121201, 5931);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "少年が\n目を覚ましてくれて\n本当によかった…", 121201, 5932);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 121201, 5933);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 121201, 5934);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 121201, 5935);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 121201, 5936);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 121201, 5937);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 121201, 5938);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 121201, 5939);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 121201, 5940);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 121201, 5941);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 121201, 5942);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 121201, 5944);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 121201, 5945);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 121201, 5946);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 121201, 5947);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食の大切さは\nいつの食事からも\n感じられるね♪", 121201, 5949);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむ…面白い！\n今度全巻\n借りにこようっと", 121201, 5950);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 116401, 5952);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 116401, 5953);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 116401, 5954);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 116401, 5955);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 116401, 5956);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 116401, 5957);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 116401, 5958);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 116401, 5959);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 116401, 5960);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 116401, 5961);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 116401, 5962);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 116401, 5963);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 116401, 5964);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 116401, 5965);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 116401, 5966);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 116401, 5967);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 116401, 5969);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 116401, 5970);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 116401, 5971);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 116401, 5972);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 116401, 5974);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 116401, 5975);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 121101, 5977);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 121101, 5978);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あいつのこと、\n全部受け入れるって\n決めたから！", 121101, 5979);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 121101, 5980);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 121101, 5981);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 121101, 5982);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 121101, 5983);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 121101, 5984);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 121101, 5985);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 121101, 5986);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 121101, 5987);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 121101, 5988);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 121101, 5989);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 121101, 5990);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 121101, 5991);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 121101, 5992);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 121101, 5994);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 121101, 5995);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 121101, 5996);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 121101, 5997);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 121101, 5999);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 121101, 6000);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お友だち…\nできるかしら…？\nこんなわたしでも…", 118101, 6002);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 118101, 6003);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 118101, 6004);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "～♪", 118101, 6005);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 118101, 6006);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 118101, 6007);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 118101, 6008);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ええと…\nお腹、空いてる？\nおねむじゃない？", 118101, 6009);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 118101, 6010);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お喋り…？\nわたしでいいの…？\nうれしい…", 118101, 6011);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あなたの好きな歌…\n教えて…？\n練習、するから…", 118101, 6012);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 118101, 6013);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "安心、して…\nわたしの歌で、\nあなたを守るから…", 118101, 6014);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 118101, 6015);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 118101, 6016);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 118101, 6017);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こんな特等席…\n恐縮、です…", 118101, 6019);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "い、今のうちに、\nこの子でお世話の\n練習を…", 118101, 6020);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\n…つらい気持ちも\n癒してくれる", 118101, 6021);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかい…\nいつまでも、\nこうしていられそう…", 118101, 6022);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 118101, 6024);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 118101, 6025);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 121301, 6027);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 121301, 6028);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 121301, 6029);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 121301, 6030);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 121301, 6031);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 121301, 6032);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 121301, 6033);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 121301, 6034);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 121301, 6035);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 121301, 6036);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 121301, 6037);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 121301, 6038);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 121301, 6039);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 121301, 6040);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 121301, 6041);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 121301, 6042);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 121301, 6044);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "今日も演技の\n練習に付き合ってね♪", 121301, 6045);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 121301, 6046);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 121301, 6047);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 121301, 6049);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 121301, 6050);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 107501, 6052);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 107501, 6053);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 107601, 6054);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 107601, 6055);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 107801, 6056);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 107801, 6057);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 121401, 6058);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 121401, 6059);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 121401, 6060);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 121401, 6061);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 121401, 6062);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 121401, 6063);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 121401, 6064);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 121401, 6065);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 121401, 6066);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 121401, 6067);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 121401, 6068);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 121401, 6069);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 121401, 6070);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 121401, 6071);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 121401, 6072);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 121401, 6073);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 121401, 6075);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 121401, 6076);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 121401, 6077);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 121401, 6078);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 121401, 6080);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 121401, 6081);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 121501, 6083);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 121501, 6084);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 121501, 6085);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 121501, 6086);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 121501, 6087);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 121501, 6088);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 121501, 6089);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 121501, 6090);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 121501, 6091);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 121501, 6092);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 121501, 6093);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 121501, 6094);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 121501, 6095);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 121501, 6096);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 121501, 6097);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 121501, 6098);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 121501, 6100);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 121501, 6101);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 121501, 6102);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 121501, 6103);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 121501, 6105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 121501, 6106);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 104501, 6108);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 104501, 6109);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 121601, 6110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 121601, 6111);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 121601, 6112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 121601, 6113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 121601, 6114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 121601, 6115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 121601, 6116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 121601, 6117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 121601, 6118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 121601, 6119);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "しーっ！\n今、尾行中なんです\n話はあとで…", 121601, 6120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 121601, 6121);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 121601, 6122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 121601, 6123);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 121601, 6124);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 121601, 6125);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 121601, 6127);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "くう…！\nこのかわいさは\n盗めないです…♪", 121601, 6128);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 121601, 6129);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 121601, 6130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 121601, 6132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 121601, 6133);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あら、サレンさんは\nどこに行かれた\nのかしら？", 121701, 6135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 121701, 6136);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 121701, 6137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 121701, 6138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 121701, 6139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 121701, 6140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 121701, 6141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ふふ、ごきげんよう\n私、絶好調でしてよ", 121701, 6142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 121701, 6143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 121701, 6144);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 121701, 6145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 121701, 6146);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 121701, 6147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "さあ！\n今日も一日\n全力で参りますわ！", 121701, 6148);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 121701, 6149);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もう少しだけ…\n明日の準備をして\nおかないと…", 121701, 6150);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 121701, 6152);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 121701, 6153);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 121701, 6154);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 121701, 6155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 121701, 6157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 121701, 6158);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 121801, 6161);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 121801, 6162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 121801, 6163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 121801, 6164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 121801, 6165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 121801, 6166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 121801, 6167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 121801, 6168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 121801, 6169);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 121801, 6170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 121801, 6171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 121801, 6172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 121801, 6173);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 121801, 6174);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 121801, 6175);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 121801, 6176);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 121801, 6178);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 121801, 6179);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 121801, 6180);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 121801, 6181);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 121801, 6183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 121801, 6184);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 122001, 6187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 122001, 6188);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 122001, 6189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 122001, 6190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 122001, 6191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 122001, 6192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 122001, 6193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 122001, 6194);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 122001, 6195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 122001, 6196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 122001, 6197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "船の上では食材が\n限られるので料理の\nいい練習になります", 122001, 6198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 122001, 6199);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 122001, 6200);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "船の上では\n規則正しい生活が\n重要ですから", 122001, 6201);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 122001, 6202);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "海に揺られるような\n心地よさですね\n気が休まります…", 122001, 6204);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 122001, 6205);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 122001, 6206);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "温かい…\n潮風で冷えた体に\n沁みますね", 122001, 6207);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 122001, 6209);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 122001, 6210);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 102601, 6212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 102601, 6213);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 121901, 6214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 121901, 6215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 121901, 6216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 121901, 6217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 121901, 6218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 121901, 6219);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 121901, 6220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 121901, 6221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 121901, 6222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 121901, 6223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 121901, 6224);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 121901, 6225);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 121901, 6226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 121901, 6227);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 121901, 6228);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 121901, 6229);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 121901, 6231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 121901, 6232);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 121901, 6233);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 121901, 6234);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 121901, 6236);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 121901, 6237);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 122101, 6239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 122101, 6240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 122101, 6241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 122101, 6242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 122101, 6243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 122101, 6244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 122101, 6245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 122101, 6246);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 122101, 6247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 122101, 6248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 122101, 6249);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 122101, 6250);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 122101, 6251);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 122101, 6252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 122101, 6253);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 122101, 6254);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 122101, 6256);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 122101, 6257);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 122101, 6258);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 122101, 6259);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 122101, 6261);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 122101, 6262);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 122201, 6264);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 122201, 6265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 122201, 6266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 122201, 6267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 122201, 6268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 122201, 6269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 122201, 6270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 122201, 6271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 122201, 6272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 122201, 6273);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 122201, 6274);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 122201, 6275);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 122201, 6276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 122201, 6277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 122201, 6278);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 122201, 6279);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 122201, 6281);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 122201, 6282);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 122201, 6283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 122201, 6284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 122201, 6286);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 122201, 6287);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 100701, 6289);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 100701, 6290);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 122301, 6291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 122301, 6292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 122301, 6293);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 122301, 6294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 122301, 6295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 122301, 6296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 122301, 6297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 122301, 6298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "よしよし！\n今日のけんぞくも\nいい子だね♪", 122301, 6299);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 122301, 6300);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 122301, 6301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 122301, 6302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 122301, 6303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 122301, 6304);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 122301, 6305);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 122301, 6306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "わー！\nふかふかだ！\nきもちぃー♪", 122301, 6308);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 122301, 6309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 122301, 6310);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 122301, 6311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "はむはむ…\nんー！\nおいしー♪", 122301, 6313);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 122301, 6314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "夏といえば\nかき氷だよね\n近くにお店は…", 122501, 6316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 122501, 6317);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 122501, 6318);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 122501, 6319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 122501, 6320);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "終わらない夏は\nないけれど…\nつい願ってしまうな", 122501, 6321);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 122501, 6322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 122501, 6323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 122501, 6324);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 122501, 6325);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 122501, 6326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 122501, 6327);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 122501, 6328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "潮騒の音で\n目覚めるのも\nいいものだね", 122501, 6329);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 122501, 6330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 122501, 6331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 122501, 6333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "肌触りのよさが\nこの格好だと\nよく分かるよ", 122501, 6334);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 122501, 6335);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 122501, 6336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "夏を楽しみきるには\nエネルギーが\n必要だからね", 122501, 6338);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 122501, 6339);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 122501, 6341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 122501, 6342);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 122401, 6343);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 122401, 6344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 122401, 6345);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 122401, 6346);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 122401, 6347);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 122401, 6348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 122401, 6349);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 122401, 6350);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 122401, 6351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 122401, 6352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 122401, 6353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 122401, 6354);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 122401, 6355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 122401, 6356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 122401, 6357);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 122401, 6358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 122401, 6360);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 122401, 6361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 122401, 6362);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 122401, 6363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 122401, 6365);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 122401, 6366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 122401, 6368);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 122401, 6369);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 101501, 6370);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 101501, 6371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 122601, 6372);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 122601, 6373);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 122601, 6374);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 122601, 6375);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 122601, 6376);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 122601, 6377);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 122601, 6378);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 122601, 6379);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 122601, 6380);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 122601, 6381);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 122601, 6382);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 122601, 6383);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 122601, 6384);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 122601, 6385);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 122601, 6386);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 122601, 6387);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 122601, 6389);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいい～♪\n日光浴させて\nあげたいな♪", 122601, 6390);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 122601, 6391);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 122601, 6392);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 122601, 6394);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 122601, 6395);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 122601, 6397);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 122601, 6398);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 122901, 6399);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 122901, 6400);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 122901, 6401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 122901, 6402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 122901, 6403);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 122901, 6404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 122901, 6405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 122901, 6406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 122901, 6407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 122901, 6408);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 122901, 6409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 122901, 6410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 122901, 6411);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 122901, 6412);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 122901, 6413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 122901, 6414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 122901, 6416);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 122901, 6417);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 122901, 6418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 122901, 6419);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 122901, 6421);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 122901, 6422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 122901, 6424);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 122901, 6425);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 122801, 6426);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 122801, 6427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 122801, 6428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 122801, 6429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 122801, 6430);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 122801, 6431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 122801, 6432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "にいちゃん！\n早く海に行く準備\nしなよ～！", 122801, 6433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 122801, 6434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 122801, 6435);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 122801, 6436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 122801, 6437);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 122801, 6438);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 122801, 6439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 122801, 6440);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 122801, 6441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 122801, 6443);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 122801, 6444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 122801, 6445);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 122801, 6446);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 122801, 6448);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 122801, 6449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 122801, 6451);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 122801, 6452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 102701, 6453);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 102701, 6454);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 122701, 6455);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 122701, 6456);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 122701, 6457);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 122701, 6458);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 122701, 6459);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 122701, 6460);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 122701, 6461);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 122701, 6462);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 122701, 6463);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 122701, 6464);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 122701, 6465);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 122701, 6466);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 122701, 6467);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 122701, 6468);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 122701, 6469);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 122701, 6470);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 122701, 6472);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 122701, 6473);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 122701, 6474);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "濡れた体を\n乾かしましょう\nタオル、タオル…", 122701, 6475);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 122701, 6477);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 122701, 6478);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 122701, 6480);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 122701, 6481);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 123001, 6482);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 123001, 6483);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 123001, 6484);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 123001, 6485);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 123001, 6486);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 123001, 6487);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 123001, 6488);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おいしい店とか\n知らないの？\n食べに行きましょ", 123001, 6489);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 123001, 6490);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 123001, 6491);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 123001, 6492);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 123001, 6493);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "どこまでだって\nあんたと一緒に\nいてやるんだから", 123001, 6494);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 123001, 6495);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 123001, 6496);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 123001, 6497);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 123001, 6499);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 123001, 6500);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りね\n塔にもこういうの\n置いておこうかしら", 123001, 6501);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 123001, 6502);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 123001, 6504);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 123001, 6505);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 123101, 6507);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 123101, 6508);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 123101, 6509);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 123101, 6510);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 123101, 6511);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 123101, 6512);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 123101, 6513);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 123101, 6514);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 123101, 6515);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 123101, 6516);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 123101, 6517);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 123101, 6518);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 123101, 6519);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 123101, 6520);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "支度が終わったら\n今日の冒険に\n出かけまひょか", 123101, 6521);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 123101, 6522);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 123101, 6524);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 123101, 6525);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 123101, 6526);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 123101, 6527);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "この料理…\n冒険するときに\n作ってみまひょか", 123101, 6529);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 123101, 6530);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 103101, 6532);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 103101, 6533);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 104401, 6534);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 104401, 6535);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 123201, 6536);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 123201, 6537);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 123201, 6538);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 123201, 6539);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 123201, 6540);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 123201, 6541);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 123201, 6542);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 123201, 6543);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 123201, 6544);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 123201, 6545);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 123201, 6546);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 123201, 6547);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 123201, 6548);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 123201, 6549);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 123201, 6550);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 123201, 6551);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 123201, 6553);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 123201, 6554);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 123201, 6555);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 123201, 6556);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 123201, 6558);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 123201, 6559);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 123301, 6561);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 123301, 6562);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 123301, 6563);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 123301, 6564);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 123301, 6565);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 123301, 6566);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 123301, 6567);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 123301, 6568);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "んー？\n今はだるいからパース\nあとでねー", 123301, 6569);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 123301, 6570);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 123301, 6571);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 123301, 6572);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 123301, 6573);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 123301, 6574);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 123301, 6575);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 123301, 6576);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あーラクー\nいっしょーここで\nキューケーできるわー", 123301, 6578);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 123301, 6579);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 123301, 6580);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったか～♪\n溶けてスライム\n戻りそ～", 123301, 6581);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 123301, 6583);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "わ～なんかムズー\n読めなー\nウケるー", 123301, 6584);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 123501, 6586);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 123501, 6587);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 123501, 6588);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 123501, 6589);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 123501, 6590);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 123501, 6591);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 123501, 6592);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 123501, 6593);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 123501, 6594);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 123501, 6595);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 123501, 6596);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 123501, 6597);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 123501, 6598);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 123501, 6599);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 123501, 6600);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 123501, 6601);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 123501, 6603);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 123501, 6604);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 123501, 6605);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 123501, 6606);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 123501, 6608);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 123501, 6609);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 123501, 6611);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 123601, 6613);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 123601, 6614);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 123601, 6615);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 123601, 6616);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 123601, 6617);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 123601, 6618);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 123601, 6619);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 123601, 6620);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 123601, 6621);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 123601, 6622);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 123601, 6623);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 123601, 6624);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 123601, 6625);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\n今日も朝から\nパトロールに行こう！", 123601, 6626);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 123601, 6627);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 123601, 6628);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 123601, 6630);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "…かわいい\nこの子もパレードに\n連れて行きたいな", 123601, 6631);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 123601, 6632);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 123601, 6633);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 123601, 6635);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 123601, 6636);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 123601, 6638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 103701, 6640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 103701, 6641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "やっぱり\nコスプレしてると\nテンションあがる～♪", 123701, 6642);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 123701, 6643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 123701, 6644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 123701, 6645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 123701, 6646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 123701, 6647);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 123701, 6648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 123701, 6649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 123701, 6650);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 123701, 6651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 123701, 6652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 123701, 6653);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 123701, 6654);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 123701, 6655);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 123701, 6656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 123701, 6657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 123701, 6659);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "いや～\nかわいいものをみると\n癒されますな～", 123701, 6660);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 123701, 6661);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 123701, 6662);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 123701, 6664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 123701, 6665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 12, 1, 0, 1, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 123701, 6667);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 123801, 6669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 123801, 6670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 123801, 6671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 123801, 6672);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 123801, 6673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 123801, 6674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 123801, 6675);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 123801, 6676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 123801, 6677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 123801, 6678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 123801, 6679);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 123801, 6680);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 123801, 6681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 123801, 6682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 123801, 6683);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 123801, 6684);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 123801, 6686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 123801, 6687);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 123801, 6688);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 123801, 6689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 123801, 6691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ウサギ族は地獄耳？\nワタシとの共通点だな", 123801, 6692);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 106401, 6694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 106401, 6695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 123901, 6696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 123901, 6697);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 123901, 6698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 123901, 6699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 123901, 6700);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 123901, 6701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 123901, 6702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 123901, 6703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 123901, 6704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 123901, 6705);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 123901, 6706);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 123901, 6707);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 123901, 6708);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 123901, 6709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 123901, 6710);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 123901, 6711);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 123901, 6713);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 123901, 6714);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 123901, 6715);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 123901, 6716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 123901, 6718);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 123901, 6719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 106901, 6721);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 106901, 6722);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 106901, 6723);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 106901, 6724);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 106901, 6725);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 106901, 6726);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 106901, 6727);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 106901, 6728);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 106901, 6729);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 106901, 6730);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "視力は権能に\n影響しないわよ\n当たり前じゃない", 106901, 6731);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 106901, 6732);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 106901, 6733);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "人生朝露…\n悔いのない一日を\n過ごしなさい", 106901, 6734);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 106901, 6735);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 106901, 6736);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 106901, 6738);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 106901, 6739);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 106901, 6740);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 106901, 6741);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "栄養価はともかく\n味のバランスは\n悪くないわね", 106901, 6743);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 106901, 6744);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 124001, 6746);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 124001, 6747);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 124001, 6748);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 124001, 6749);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 124001, 6750);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 124001, 6751);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 124001, 6752);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 124001, 6753);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 124001, 6754);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 124001, 6755);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 124001, 6756);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 124001, 6757);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 124001, 6758);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 124001, 6759);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 124001, 6760);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 124001, 6761);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 124001, 6763);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 124001, 6764);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 124001, 6765);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 124001, 6766);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 124001, 6768);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 124001, 6769);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 124001, 6771);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 124001, 6772);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 124101, 6773);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 124101, 6774);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 124101, 6775);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 124101, 6776);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 124101, 6777);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 124101, 6778);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 124101, 6779);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 124101, 6780);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 124101, 6781);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 124101, 6782);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 124101, 6783);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 124101, 6784);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 124101, 6785);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 124101, 6786);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 124101, 6787);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 124101, 6788);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 124101, 6790);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 124101, 6791);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 124101, 6792);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 124101, 6793);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お腹いっぱい\n食べられるのも\n幸せなことよね", 124101, 6795);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 124101, 6796);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 124101, 6798);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 124101, 6799);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 105101, 6800);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 105101, 6801);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 124201, 6802);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 124201, 6803);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 124201, 6804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 124201, 6805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 124201, 6806);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 124201, 6807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 124201, 6808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 124201, 6809);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 124201, 6810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 124201, 6811);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 124201, 6812);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 124201, 6813);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 124201, 6814);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 124201, 6815);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 124201, 6816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 124201, 6817);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 124201, 6819);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 124201, 6820);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 124201, 6821);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 124201, 6822);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 124201, 6824);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 124201, 6825);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "わっ！\n…………いや\n驚いてなどいない", 124201, 6827);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 124201, 6828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 124301, 6829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 124301, 6830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 124301, 6831);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 124301, 6832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "グレアも誘って\nランドソルをもっと\n探索したい！", 124301, 6833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "私たちも\nあの人を見習って\nバイトとかしよう！", 124301, 6834);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "いつかあの人を\n私たちの学院に\n招待したいな！", 124301, 6835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 124301, 6836);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "これからなにするの？\n私たちもついて行って\nいいかな！？", 124301, 6837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 124301, 6838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 124301, 6839);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 124301, 6840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたは\nグレアと私の友達！\nこれからもずっと！", 124301, 6841);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 124301, 6842);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 124301, 6843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 124301, 6844);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 124301, 6846);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 124301, 6847);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 124301, 6848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 124301, 6849);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 124301, 6851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 124301, 6852);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 124401, 6855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 124401, 6856);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 124401, 6857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 124401, 6858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 124401, 6859);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 124401, 6860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 124401, 6861);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 124401, 6862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 124401, 6863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 124401, 6864);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 124401, 6865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 124401, 6866);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 124401, 6867);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 124401, 6868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 124401, 6869);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 124401, 6870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 124401, 6872);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "これ…\nアンに見せたら\n喜ぶだろうな…", 124401, 6873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り…\nアンの感想も\n聞いてみたいな", 124401, 6874);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 124401, 6875);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 124401, 6877);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "アンがおススメ\nしてくれた本は\nこれかな…？", 124401, 6878);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 124701, 6881);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 124701, 6882);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 124701, 6883);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 124701, 6884);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 124701, 6885);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 124701, 6886);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 124701, 6887);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 124701, 6888);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 124701, 6889);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 124701, 6890);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 124701, 6891);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 124701, 6892);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 124701, 6893);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 124701, 6894);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 124701, 6895);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 124701, 6896);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 124701, 6898);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 124701, 6899);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 124701, 6900);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 124701, 6901);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 124701, 6903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 124701, 6904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 124601, 6906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 124601, 6907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 124601, 6908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 124601, 6909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 124601, 6910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 124601, 6911);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 124601, 6912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 124601, 6913);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 124601, 6914);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 124601, 6915);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 124601, 6916);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 124601, 6917);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 124601, 6918);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 124601, 6919);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 124601, 6920);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 124601, 6921);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 124601, 6923);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 124601, 6924);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 124601, 6925);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 124601, 6926);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 124601, 6928);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 124601, 6929);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 104701, 6931);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 104701, 6932);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お正月だからって\nギャングのボスは\n油断できないよ～？", 124501, 6933);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 124501, 6934);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 124501, 6935);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 124501, 6936);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 124501, 6937);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 124501, 6938);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 124501, 6939);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 124501, 6940);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 124501, 6941);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 124501, 6942);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 124501, 6943);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 124501, 6944);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 124501, 6945);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 124501, 6946);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 124501, 6947);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 124501, 6948);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 124501, 6950);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 124501, 6951);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りだけど\n着物に移らないように\n気をつけないとね", 124501, 6952);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 124501, 6953);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 124501, 6955);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 124501, 6956);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 124801, 6958);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 124801, 6959);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 124801, 6960);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 124801, 6961);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 124801, 6962);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 124801, 6963);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 124801, 6964);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 124801, 6965);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 124801, 6966);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 124801, 6967);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 124801, 6968);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 124801, 6969);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 124801, 6970);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 124801, 6971);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 124801, 6972);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 124801, 6973);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 124801, 6975);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 124801, 6976);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 124801, 6977);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 124801, 6978);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 124801, 6980);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 124801, 6981);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 124901, 6983);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 124901, 6984);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 124901, 6985);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 124901, 6986);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 124901, 6987);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 124901, 6988);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 124901, 6989);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 124901, 6990);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 124901, 6991);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 124901, 6992);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 124901, 6993);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 124901, 6994);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 124901, 6995);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 124901, 6996);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 124901, 6997);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 124901, 6998);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 124901, 7000);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 124901, 7001);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "嗅いだことない\n香りだな\nなかなか気に入った！", 124901, 7002);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 124901, 7003);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 124901, 7005);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 124901, 7006);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 105501, 7008);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 105501, 7009);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 125001, 7010);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 125001, 7011);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 125001, 7012);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 125001, 7013);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 125001, 7014);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 125001, 7015);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 125001, 7016);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 125001, 7017);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 125001, 7018);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 125001, 7019);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 125001, 7020);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 125001, 7021);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 125001, 7022);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 125001, 7023);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 125001, 7024);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 125001, 7025);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 125001, 7027);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 125001, 7028);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 125001, 7029);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 125001, 7030);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 125001, 7032);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 125001, 7033);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 118201, 7035);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 118201, 7036);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 118201, 7037);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 118201, 7038);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "オトモダチでも\n作ってみますかー\nくすっ★", 118201, 7039);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "退屈ですねー\n騎士さん、構って\nくれないかなー", 118201, 7040);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 118201, 7041);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 118201, 7042);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お出かけします？\nそれともおうちで\nまったりしますー？", 118201, 7043);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 118201, 7044);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 118201, 7045);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 118201, 7046);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 118201, 7047);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます★\nあれれー？\nまだおねむですかー？", 118201, 7048);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 118201, 7049);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 118201, 7050);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 118201, 7052);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 118201, 7053);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん★\nうーん…\nたぶん素敵な香り♪", 118201, 7054);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかーい★\n心も体も\nぽっかぽかですねー★", 118201, 7055);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 118201, 7057);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "へー★\nほほー★\nふんふーん★", 118201, 7058);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 125101, 7060);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 125101, 7061);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 125101, 7062);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 125101, 7063);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 125101, 7064);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 125101, 7065);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 125101, 7066);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 125101, 7067);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 125101, 7068);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 125101, 7069);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 125101, 7070);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 125101, 7071);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 125101, 7072);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 125101, 7073);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 125101, 7074);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 125101, 7075);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 125101, 7077);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 125101, 7078);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 125101, 7079);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 125101, 7080);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 125101, 7082);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 125101, 7083);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 107101, 7085);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 107101, 7086);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 125201, 7087);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 125201, 7088);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 125201, 7089);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 125201, 7090);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 125201, 7091);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 125201, 7092);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 125201, 7093);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 125201, 7094);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 125201, 7095);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 125201, 7096);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 125201, 7097);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 125201, 7098);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 125201, 7099);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 125201, 7100);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 125201, 7101);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 125201, 7102);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 125201, 7104);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 125201, 7105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 125201, 7106);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 125201, 7107);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 125201, 7109);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 125201, 7110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 120001, 7112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 120001, 7113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 120001, 7114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 120001, 7115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 120001, 7116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 120001, 7117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 120001, 7118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 120001, 7119);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 120001, 7120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 120001, 7121);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 120001, 7122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 120001, 7123);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 120001, 7124);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 120001, 7125);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 120001, 7126);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 120001, 7127);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 120001, 7129);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 120001, 7130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 120001, 7131);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 120001, 7132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 120001, 7134);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 120001, 7135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 125301, 7137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 125301, 7138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 125301, 7139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 125301, 7140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 125301, 7141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 125301, 7142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 125301, 7143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 125301, 7144);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 125301, 7145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 125301, 7146);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 125301, 7147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 125301, 7148);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 125301, 7149);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 125301, 7150);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 125301, 7151);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 125301, 7152);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 125301, 7154);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 125301, 7155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 125301, 7156);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 125301, 7157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 125301, 7159);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 125301, 7160);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 100901, 7162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 100901, 7163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 125401, 7164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ふふ…\nこれがレンジャーの\n制服ですか", 125401, 7165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "そろそろ\nパトロールの\n時間だ…", 125401, 7166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "魔物について\nもっと知りたいな\n本を探しに行こう", 125401, 7167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "レンジャーになって\n体調を崩す日が\n減った気がします…", 125401, 7168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 125401, 7169);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "こんな私にも\nできることはある…\nそう信じています", 125401, 7170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 125401, 7171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 125401, 7172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 125401, 7173);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ニャットちゃん\nあなたにも\n懐いてきましたね", 125401, 7174);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 125401, 7175);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 125401, 7176);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 125401, 7177);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 125401, 7178);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 125401, 7179);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 125401, 7181);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 125401, 7182);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "森の香りがします…\n今日はよく\n眠れそうです…", 125401, 7183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ニャットちゃんと\n同じぐらい\n暖かいです…", 125401, 7184);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "レンジャーの仕事を\nしていると\nお腹が空きます", 125401, 7186);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "どんなに忙しくても\n読書だけは\nやめられませんね", 125401, 7187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 125801, 7189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 125801, 7190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 125801, 7191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 125801, 7192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 125801, 7193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 125801, 7194);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 125801, 7195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 125801, 7196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 125801, 7197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 125801, 7198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 125801, 7199);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 125801, 7200);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 125801, 7201);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 125801, 7202);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 125801, 7203);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 125801, 7204);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 125801, 7206);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "と、飛びかかっては\nこない…ですよね…？", 125801, 7207);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 125801, 7208);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 125801, 7209);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "こんなに食べていたら\nはしたない…\nでしょうか？", 125801, 7211);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 125801, 7212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 125501, 7214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 125501, 7215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 125501, 7216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 125501, 7217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 125501, 7218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 125501, 7219);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 125501, 7220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 125501, 7221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 125501, 7222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 125501, 7223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 125501, 7224);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 125501, 7225);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 125501, 7226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 125501, 7227);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 125501, 7228);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 125501, 7229);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 125501, 7231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 125501, 7232);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂い…\nそれになんだか\n集中力が増したかも", 125501, 7233);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 125501, 7234);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 125501, 7236);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 125501, 7237);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 125701, 7239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 125701, 7240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 125701, 7241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "せっかくですから\nより良い素材を\n自分で集めたいですね", 125701, 7242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "このポーションが\nみなさんの役に立つと\nうれしいですね", 125701, 7243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 125701, 7244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 125701, 7245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 125701, 7246);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 125701, 7247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "依頼で気になる\nところがあれば\n何でもご確認ください", 125701, 7248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 125701, 7249);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 125701, 7250);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 125701, 7251);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 125701, 7252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 125701, 7253);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 125701, 7254);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 125701, 7256);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 125701, 7257);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 125701, 7258);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "この火力…\n錬金術の調合に\n使えそうですね", 125701, 7259);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 125701, 7261);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 125701, 7262);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 106101, 7264);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 106101, 7265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "みんなー！\nギルドハウスで\nぼくと握手！", 125601, 7266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 125601, 7267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 125601, 7268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 125601, 7269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 125601, 7270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 125601, 7271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 125601, 7272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 125601, 7273);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 125601, 7274);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 125601, 7275);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 125601, 7276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 125601, 7277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 125601, 7278);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 125601, 7279);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 125601, 7280);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 125601, 7281);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 125601, 7283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 125601, 7284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 125601, 7285);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 125601, 7286);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 125601, 7288);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 125601, 7289);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 126201, 7291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 126201, 7292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 126201, 7293);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 126201, 7294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 126201, 7295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 126201, 7296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 126201, 7297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 126201, 7298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 126201, 7299);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 126201, 7300);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 126201, 7301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 126201, 7302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 126201, 7303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 126201, 7304);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 126201, 7305);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 126201, 7306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 126201, 7308);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 126201, 7309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 126201, 7310);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 126201, 7311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 126201, 7313);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 126201, 7314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 100801, 7316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 100801, 7317);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 126301, 7318);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 126301, 7319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 126301, 7320);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 126301, 7321);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 126301, 7322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 126301, 7323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 126301, 7324);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 126301, 7325);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 126301, 7326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 126301, 7327);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 126301, 7328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 126301, 7329);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 126301, 7330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 126301, 7331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 126301, 7332);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 126301, 7333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 126301, 7335);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 126301, 7336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 126301, 7337);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 126301, 7338);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 126301, 7340);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 126301, 7341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 126401, 7343);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 126401, 7344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 126401, 7345);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 126401, 7346);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 126401, 7347);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 126401, 7348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 126401, 7349);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 126401, 7350);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 126401, 7351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 126401, 7352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 126401, 7353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 126401, 7354);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 126401, 7355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 126401, 7356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 126401, 7357);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 126401, 7358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 126401, 7360);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 126401, 7361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 126401, 7362);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 126401, 7363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 126401, 7365);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 126401, 7366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 126701, 7368);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 126701, 7369);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 126701, 7370);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 126701, 7371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 126701, 7372);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 126701, 7373);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 126701, 7374);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 126701, 7375);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 126701, 7376);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 126701, 7377);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 126701, 7378);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 126701, 7379);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 126701, 7380);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 126701, 7381);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 126701, 7382);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 126701, 7383);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 126701, 7385);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 126701, 7386);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 126701, 7387);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 126701, 7388);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 126701, 7390);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "夏のトレンドを\n押さえておくのも\n大事ですわね", 126701, 7391);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 126701, 7393);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 126701, 7394);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 126601, 7395);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 126601, 7396);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 126601, 7397);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 126601, 7398);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 126601, 7399);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 126601, 7400);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 126601, 7401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 126601, 7402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 126601, 7403);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 126601, 7404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 126601, 7405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 126601, 7406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 126601, 7407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 126601, 7408);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 126601, 7409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 126601, 7410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 126601, 7412);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なかなか気に入った…\nというのは\n誰にも秘密です", 126601, 7413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 126601, 7414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 126601, 7415);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "私がどのような味が\n好きなのかは…\n当然秘密です", 126601, 7417);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 126601, 7418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 126601, 7420);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 126601, 7421);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 105401, 7422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 105401, 7423);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 126801, 7424);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 126801, 7425);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 126801, 7426);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 126801, 7427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 126801, 7428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 126801, 7429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 126801, 7430);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 126801, 7431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 126801, 7432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 126801, 7433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 126801, 7434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 126801, 7435);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 126801, 7436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 126801, 7437);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 126801, 7438);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 126801, 7439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 126801, 7441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 126801, 7442);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 126801, 7443);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 126801, 7444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 126801, 7446);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "試験に使った参考書、\nたまには読み返して\nおかないとね", 126801, 7447);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "ふふ♪\nいい手触りね～", 126801, 7449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "…はふう～…\nゴロゴロするのって\n最高ね～", 126801, 7450);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 126901, 7451);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 126901, 7452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 126901, 7453);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 126901, 7454);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 126901, 7455);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 126901, 7456);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 126901, 7457);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 126901, 7458);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 126901, 7459);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 126901, 7460);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 126901, 7461);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 126901, 7462);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 126901, 7463);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 126901, 7464);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 126901, 7465);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 126901, 7466);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "素肌にふわふわが…\n気持ちいい…♪", 126901, 7468);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うふふ…\n一緒に海…行く？", 126901, 7469);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 126901, 7470);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 126901, 7471);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 126901, 7473);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 126901, 7474);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "これで合ってる…？\n遊び方わからなくて…", 126901, 7476);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ふわふわ…♪\n海の底にいるみたい…", 126901, 7477);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 127001, 7478);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 127001, 7479);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 127001, 7480);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 127001, 7481);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 127001, 7482);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 127001, 7483);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 127001, 7484);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 127001, 7485);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 127001, 7486);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 127001, 7487);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 127001, 7488);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 127001, 7489);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 127001, 7490);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 127001, 7491);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 127001, 7492);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 127001, 7493);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 127001, 7495);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 127001, 7496);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 127001, 7497);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 127001, 7498);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "クウカの口の中に\nたくさん…♪\n止まりません…！", 127001, 7500);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 127001, 7501);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 127001, 7503);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "これは…\n妄想に役立ちそうな\n感触です…！", 127001, 7504);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 100501, 7505);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 100501, 7506);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 127101, 7507);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 127101, 7508);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 127101, 7509);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 127101, 7510);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 127101, 7511);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 127101, 7512);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 127101, 7513);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 127101, 7514);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 127101, 7515);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 127101, 7516);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 127101, 7517);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 127101, 7518);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 127101, 7519);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 127101, 7520);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 127101, 7521);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 127101, 7522);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 127101, 7524);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 127101, 7525);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 127101, 7526);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 127101, 7527);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 127101, 7529);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 127101, 7530);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 127101, 7532);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 127101, 7533);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 126501, 7534);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 126501, 7535);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 126501, 7536);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 126501, 7537);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 126501, 7538);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 126501, 7539);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 126501, 7540);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 126501, 7541);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 126501, 7542);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 126501, 7543);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 126501, 7544);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 126501, 7545);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 126501, 7546);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 126501, 7547);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 126501, 7548);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 126501, 7549);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 126501, 7551);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 126501, 7552);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 126501, 7553);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 126501, 7554);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 126501, 7556);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 126501, 7557);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 127201, 7559);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 127201, 7560);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 127201, 7561);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 127201, 7562);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 127201, 7563);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 127201, 7564);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 127201, 7565);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 127201, 7566);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 127201, 7567);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 127201, 7568);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 127201, 7569);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 127201, 7570);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 127201, 7571);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 127201, 7572);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 127201, 7573);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 127201, 7574);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 127201, 7576);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 127201, 7577);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 127201, 7578);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 127201, 7579);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 127201, 7581);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 127201, 7582);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 104301, 7584);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 104301, 7585);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "魔法の練習！\nむむむ～…\nそれっ…！", 127301, 7586);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 127301, 7587);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 127301, 7588);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 127301, 7589);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 127301, 7590);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 127301, 7591);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 127301, 7592);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 127301, 7593);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 127301, 7594);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 127301, 7595);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 127301, 7596);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 127301, 7597);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 127301, 7598);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 127301, 7599);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 127301, 7600);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 127301, 7601);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "充電ちう～\n…ガッコーのイスも\nこれにして～", 127301, 7603);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 127301, 7604);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "リフレッシュの\n魔法発見！\nいい香り～♪", 127301, 7605);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 127301, 7606);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "超おいし～♪\n学食にあったら\n毎日リピする～", 127301, 7608);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\nねむみパない…\n続きはまた今度～", 127301, 7609);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 127501, 7611);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 127501, 7612);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 127501, 7613);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 127501, 7614);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 127501, 7615);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 127501, 7616);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 127501, 7617);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 127501, 7618);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 127501, 7619);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 127501, 7620);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 127501, 7621);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 127501, 7622);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 127501, 7623);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 127501, 7624);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 127501, 7625);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 127501, 7626);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 127501, 7628);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 127501, 7629);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 127501, 7630);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 127501, 7631);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 127501, 7633);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 127501, 7634);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 101401, 7636);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 101401, 7637);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 127601, 7638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "たまには違う服装\n違う得物を使うのも\n悪くないね", 127601, 7639);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 127601, 7640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 127601, 7641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 127601, 7642);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 127601, 7643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 127601, 7644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 127601, 7645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 127601, 7646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 127601, 7647);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 127601, 7648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 127601, 7649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 127601, 7650);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 127601, 7651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 127601, 7652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 127601, 7653);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 127601, 7655);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 127601, 7656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この香り…\nサラサリア土産の\n香木かい？", 127601, 7657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 127601, 7658);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 127601, 7660);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 127601, 7661);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 126001, 7663);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 126001, 7664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 126001, 7665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 126001, 7666);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 126001, 7667);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 126001, 7668);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 126001, 7669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 126001, 7670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 126001, 7671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 126001, 7672);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 126001, 7673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 126001, 7674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 126001, 7675);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 126001, 7676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 126001, 7677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 126001, 7678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 126001, 7680);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 126001, 7681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 126001, 7682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 126001, 7683);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 126001, 7685);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 126001, 7686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 103801, 7688);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 103801, 7689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お兄の、けはい…\nん、みつけた\nぎゅ", 126101, 7690);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "きょろきょろ\nここも\nぱとろる、するよ", 126101, 7691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "おうち\nにぎやか\nさみしく、ない", 126101, 7692);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 126101, 7693);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "はっぴーちゃん\nどこ？\nお兄、たべた？", 126101, 7694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 126101, 7695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 126101, 7696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 126101, 7697);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "くろぷれしあ\nかっこいい？\nん…あざます", 126101, 7698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 126101, 7699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 126101, 7700);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "かみ、りぼん\nおてつだい\nよろ\nです", 126101, 7701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ここ\nすむ\nね？\nまいにち、いっしょ", 126101, 7702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ぐっもにん\nあさごはん\nあさごはん", 126101, 7703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お兄\nおつかれ？\nれっつ、まいぺーす", 126101, 7704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 126101, 7705);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふかふか\nパパの、おなか\nみたい", 126101, 7707);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 126101, 7708);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい、におい\nでも、お兄\nもっと、いい、におい", 126101, 7709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "とりま\nお肉、やこ", 126101, 7710);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nむげん肉まん、も\nきぼう", 126101, 7712);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 126101, 7713);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 127901, 7715);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 127901, 7716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 127901, 7717);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 127901, 7718);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 127901, 7719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 127901, 7720);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 127901, 7721);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 127901, 7722);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 127901, 7723);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 127901, 7724);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 127901, 7725);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 127901, 7726);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 127901, 7727);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 127901, 7728);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 127901, 7729);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 127901, 7730);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 127901, 7732);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 127901, 7733);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 127901, 7734);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 127901, 7735);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 127901, 7737);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 127901, 7738);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 127901, 7740);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 127901, 7741);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 106601, 7742);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 106601, 7743);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 128001, 7744);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 128001, 7745);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 128001, 7746);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 128001, 7747);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 128001, 7748);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 128001, 7749);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 128001, 7750);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 128001, 7751);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 128001, 7752);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 128001, 7753);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 128001, 7754);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 128001, 7755);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 128001, 7756);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 128001, 7757);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 128001, 7758);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 128001, 7759);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 128001, 7761);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 128001, 7762);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 128001, 7763);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 128001, 7764);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 128001, 7766);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 128001, 7767);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 128001, 7769);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 128001, 7770);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 128101, 7771);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 128101, 7772);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 128101, 7773);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 128101, 7774);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 128101, 7775);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 128101, 7776);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 128101, 7777);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 128101, 7778);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 128101, 7779);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 128101, 7780);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 128101, 7781);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 128101, 7782);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 128101, 7783);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 128101, 7784);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 128101, 7785);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 128101, 7786);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 128101, 7788);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 128101, 7789);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 128101, 7790);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 128101, 7791);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 128101, 7793);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 128101, 7794);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 128201, 7797);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 128201, 7798);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 128201, 7799);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 128201, 7800);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 128201, 7801);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 128201, 7802);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 128201, 7803);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 128201, 7804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 128201, 7805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 128201, 7806);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 128201, 7807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 128201, 7808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 128201, 7809);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 128201, 7810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 128201, 7811);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 128201, 7812);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 128201, 7814);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 128201, 7815);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 128201, 7816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 128201, 7817);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 128201, 7819);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 128201, 7820);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 128301, 7823);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 128301, 7824);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 128301, 7825);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 128301, 7826);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 128301, 7827);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 128301, 7828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 128301, 7829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 128301, 7830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 128301, 7831);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 128301, 7832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 128301, 7833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 128301, 7834);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 128301, 7835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 128301, 7836);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 128301, 7837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 128301, 7838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 128301, 7840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 128301, 7841);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 128301, 7842);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 128301, 7843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 128301, 7845);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 128301, 7846);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 128401, 7848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 128401, 7849);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 128401, 7850);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 128401, 7851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 128401, 7852);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 128401, 7853);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "来年も彼の隣で\n初日の出が\n見られるといいな～", 128401, 7854);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 128401, 7855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 128401, 7856);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 128401, 7857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 128401, 7858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 128401, 7859);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 128401, 7860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 128401, 7861);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 128401, 7862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 128401, 7863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 128401, 7865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 128401, 7866);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 128401, 7867);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 128401, 7868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 128401, 7870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 128401, 7871);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 106501, 7873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 106501, 7874);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 128501, 7875);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 128501, 7876);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 128501, 7877);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 128501, 7878);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 128501, 7879);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 128501, 7880);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 128501, 7881);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 128501, 7882);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 128501, 7883);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 128501, 7884);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 128501, 7885);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 128501, 7886);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 128501, 7887);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 128501, 7888);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 128501, 7889);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 128501, 7890);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 128501, 7892);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 128501, 7893);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 128501, 7894);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 128501, 7895);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 128501, 7897);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 128501, 7898);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この新しい力…\nさらに使いこなせる\nようになるには…", 128801, 7900);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 128801, 7901);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 128801, 7902);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "風の力の制御…\n段々とコツが\n掴めてきたな", 128801, 7903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 128801, 7904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 128801, 7905);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 128801, 7906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 128801, 7907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 128801, 7908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 128801, 7909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 128801, 7910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 128801, 7911);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 128801, 7912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 128801, 7913);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 128801, 7914);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 128801, 7915);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休める時は休む…\n戦い続けるには\n必要なことだからね", 128801, 7917);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 128801, 7918);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 128801, 7919);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 128801, 7920);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "この味付け…\n今度試してみようか\n味見は任せたからね", 128801, 7922);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 128801, 7923);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 128901, 7925);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 128901, 7926);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 128901, 7927);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 128901, 7928);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 128901, 7929);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 128901, 7930);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 128901, 7931);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 128901, 7932);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 128901, 7933);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 128901, 7934);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 128901, 7935);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 128901, 7936);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 128901, 7937);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 128901, 7938);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 128901, 7939);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 128901, 7940);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 128901, 7942);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 128901, 7943);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 128901, 7944);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 128901, 7945);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 128901, 7947);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 128901, 7948);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 128701, 7950);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 128701, 7951);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 128701, 7952);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 128701, 7953);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 128701, 7954);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 128701, 7955);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 128701, 7956);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 128701, 7957);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 128701, 7958);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 128701, 7959);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 128701, 7960);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 128701, 7961);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 128701, 7962);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 128701, 7963);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 128701, 7964);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 128701, 7965);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 128701, 7967);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 128701, 7968);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 128701, 7969);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 128701, 7970);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 128701, 7972);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 128701, 7973);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 129001, 7975);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 129001, 7976);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 129001, 7977);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 129001, 7978);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 129001, 7979);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 129001, 7980);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 129001, 7981);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 129001, 7982);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 129001, 7983);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 129001, 7984);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 129001, 7985);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 129001, 7986);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 129001, 7987);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 129001, 7988);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 129001, 7989);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 129001, 7990);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 129001, 7992);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 129001, 7993);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 129001, 7994);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 129001, 7995);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 129001, 7997);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 129001, 7998);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 129301, 8000);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 129301, 8001);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 129301, 8002);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 129301, 8003);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 129301, 8004);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 129301, 8005);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 129301, 8006);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 129301, 8007);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 129301, 8008);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 129301, 8009);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 129301, 8010);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 129301, 8011);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 129301, 8012);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 129301, 8013);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 129301, 8014);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 129301, 8015);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 129301, 8017);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 129301, 8018);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 129301, 8019);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 129301, 8020);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 129301, 8022);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 129301, 8023);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 129301, 8025);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 129301, 8026);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 108901, 8027);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 108901, 8028);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 108801, 8029);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 108801, 8030);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 108701, 8031);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 108701, 8032);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 129401, 8033);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 129401, 8034);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 129401, 8035);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 129401, 8036);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 129401, 8037);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 129401, 8038);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 129401, 8039);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 129401, 8040);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 129401, 8041);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 129401, 8042);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 129401, 8043);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 129401, 8044);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 129401, 8045);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 129401, 8046);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 129401, 8047);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 129401, 8048);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 129401, 8050);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 129401, 8051);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 129401, 8052);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 129401, 8053);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 129401, 8055);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 129401, 8056);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 129401, 8058);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 129401, 8059);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "さてと…\n明日の訓練メニュー\n考えねえとな", 129801, 8060);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 129801, 8061);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 129801, 8062);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 129801, 8063);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 129801, 8064);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 129801, 8065);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 129801, 8066);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 129801, 8067);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 129801, 8068);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 129801, 8069);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 129801, 8070);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 129801, 8071);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 129801, 8072);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 129801, 8073);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 129801, 8074);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 129801, 8075);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 129801, 8077);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 129801, 8078);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 129801, 8079);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 129801, 8080);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 129801, 8082);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 129801, 8083);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 129901, 8085);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 129901, 8086);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 129901, 8087);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 129901, 8088);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 129901, 8089);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 129901, 8090);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 129901, 8091);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 129901, 8092);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 129901, 8093);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 129901, 8094);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 129901, 8095);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 129901, 8096);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 129901, 8097);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 129901, 8098);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 129901, 8099);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 129901, 8100);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 129901, 8102);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 129901, 8103);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 129901, 8104);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 129901, 8105);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 129901, 8107);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 129901, 8108);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "……（そわそわ）", 129601, 8110);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ここに寄らずの花陣…\n作ったらダメですよね", 129601, 8111);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 129601, 8112);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "身を隠せる場所が\nあると…\n助かるんですけど", 129601, 8113);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 129601, 8114);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 129601, 8115);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この家なら安全─\nかもしれません\n魔物が出ないので", 129601, 8116);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 129601, 8117);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "長話─苦手です\n疲れるので…\nふぅ…", 129601, 8118);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 129601, 8119);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "世話をするのは\n得意です\n…花の、ですよ？", 129601, 8120);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 129601, 8121);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 129601, 8122);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "花に、お水─？\n…いりません", 129601, 8123);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 129601, 8124);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜番。私がします\nあまり眠らない\n性質なので…", 129601, 8125);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ここなら根を張れる\nかもしれません\n…張りませんけど", 129601, 8127);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 129601, 8128);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "花みたいに─\nいい匂い、です", 129601, 8129);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 129601, 8130);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食事も立派な戦支度\n媛が言ってました", 129601, 8132);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 129601, 8133);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 130701, 8135);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あ、どこからか\nお花の匂いが…\nいい香りですね", 130701, 8136);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 130701, 8137);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 130701, 8138);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 130701, 8139);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 130701, 8140);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 130701, 8141);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 130701, 8142);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 130701, 8143);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 130701, 8144);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 130701, 8145);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 130701, 8146);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 130701, 8147);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 130701, 8148);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 130701, 8149);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 130701, 8150);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 130701, 8152);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 130701, 8153);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 130701, 8154);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 130701, 8155);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 130701, 8157);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 130701, 8158);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 130801, 8160);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 130801, 8161);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 130801, 8162);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 130801, 8163);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 130801, 8164);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 130801, 8165);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 130801, 8166);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 130801, 8167);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 130801, 8168);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 130801, 8169);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 130801, 8170);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 130801, 8171);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 130801, 8172);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "はう、そうでした\nお花にお水を\nあげる時間です！", 130801, 8173);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 130801, 8174);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 130801, 8175);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 130801, 8177);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 130801, 8178);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 130801, 8179);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 130801, 8180);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 130801, 8182);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 130801, 8183);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 131001, 8185);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 131001, 8186);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 131001, 8187);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 131001, 8188);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 131001, 8189);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 131001, 8190);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 131001, 8191);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 131001, 8192);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 131001, 8193);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 131001, 8194);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 131001, 8195);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 131001, 8196);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 131001, 8197);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 131001, 8198);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 131001, 8199);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 131001, 8200);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 131001, 8202);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 131001, 8203);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 131001, 8204);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 131001, 8205);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 131001, 8207);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 131001, 8208);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 105601, 8210);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 105601, 8211);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 130901, 8212);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 130901, 8213);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 130901, 8214);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 130901, 8215);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 130901, 8216);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 130901, 8217);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 130901, 8218);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 130901, 8219);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 130901, 8220);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 130901, 8221);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 130901, 8222);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 130901, 8223);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 130901, 8224);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 130901, 8225);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 130901, 8226);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 130901, 8227);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 130901, 8229);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 130901, 8230);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 130901, 8231);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 130901, 8232);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 130901, 8234);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "パークの\nガイドブックとか\nあったらいいですね♪", 130901, 8235);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 131101, 8237);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 131101, 8238);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 131101, 8239);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 131101, 8240);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 131101, 8241);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 131101, 8242);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 131101, 8243);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 131101, 8244);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 131101, 8245);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 131101, 8246);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 131101, 8247);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 131101, 8248);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 131101, 8249);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 131101, 8250);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 131101, 8251);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 131101, 8252);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 131101, 8254);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 131101, 8255);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 131101, 8256);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 131101, 8257);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 131101, 8259);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 131101, 8260);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 131101, 8262);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "あーやべ\n一瞬で寝そー\nヤバだわ～", 131101, 8263);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 129701, 8264);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 129701, 8265);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 129701, 8266);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 129701, 8267);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 129701, 8268);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 129701, 8269);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 129701, 8270);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 129701, 8271);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 129701, 8272);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 129701, 8273);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 129701, 8274);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 129701, 8275);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 129701, 8276);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 129701, 8277);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 129701, 8278);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 129701, 8279);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 129701, 8281);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 129701, 8282);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 129701, 8283);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 129701, 8284);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 129701, 8286);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 129701, 8287);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 131201, 8289);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "海への行き来は\nテレポーテーションに\nお任せです★", 131201, 8290);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 131201, 8291);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 131201, 8292);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 131201, 8293);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 131201, 8294);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 131201, 8295);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 131201, 8296);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 131201, 8297);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 131201, 8298);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 131201, 8299);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 131201, 8300);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 131201, 8301);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 131201, 8302);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 131201, 8303);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 131201, 8304);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 131201, 8306);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 131201, 8307);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 131201, 8308);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 131201, 8309);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 131201, 8311);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 131201, 8312);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 131201, 8314);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 131201, 8315);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 131601, 8316);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 131601, 8317);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 131601, 8318);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 131601, 8319);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 131601, 8320);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 131601, 8321);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 131601, 8322);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 131601, 8323);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 131601, 8324);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 131601, 8325);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 131601, 8326);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 131601, 8327);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 131601, 8328);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 131601, 8329);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 131601, 8330);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 131601, 8331);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 131601, 8333);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 131601, 8334);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 131601, 8335);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 131601, 8336);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 131601, 8338);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 131601, 8339);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 131601, 8341);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 131601, 8342);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 131701, 8343);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 131701, 8344);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 131701, 8345);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 131701, 8346);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 131701, 8347);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 131701, 8348);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 131701, 8349);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 131701, 8350);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 131701, 8351);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 131701, 8352);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 131701, 8353);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 131701, 8354);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 131701, 8355);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 131701, 8356);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 131701, 8357);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 131701, 8358);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 131701, 8360);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 131701, 8361);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 131701, 8362);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 131701, 8363);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 131701, 8365);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "文字を読むのと\nデータを読むのは\nまったく違うわ", 131701, 8366);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 131701, 8368);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 131701, 8369);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 118601, 8370);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 118601, 8371);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 118601, 8372);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 118601, 8373);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 118601, 8374);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 118601, 8375);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 118601, 8376);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 118601, 8377);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 118601, 8378);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 118601, 8379);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 118601, 8380);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 118601, 8381);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 118601, 8382);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 118601, 8383);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 118601, 8384);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 118601, 8385);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 118601, 8387);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 118601, 8388);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 118601, 8389);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "なんていうか…\n心があたたかいわ\n…変な感じ！", 118601, 8390);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 118601, 8392);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 118601, 8393);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 131801, 8395);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 131801, 8396);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 131801, 8397);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 131801, 8398);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 131801, 8399);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 131801, 8400);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 131801, 8401);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 131801, 8402);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 131801, 8403);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "熱中症には\n気を付けないと\nだめだよ～？", 131801, 8404);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 131801, 8405);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 131801, 8406);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 131801, 8407);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 131801, 8408);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ふふ♪\n波音が心地良い\n時間だね～☆", 131801, 8409);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 131801, 8410);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 131801, 8412);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 131801, 8413);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 131801, 8414);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 131801, 8415);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 131801, 8417);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 131801, 8418);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 131801, 8420);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 131801, 8421);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 181201, 8422);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 181201, 8423);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 181201, 8424);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 181201, 8425);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 181201, 8426);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 181201, 8427);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 181201, 8428);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 181201, 8429);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 181201, 8430);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 181201, 8431);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 181201, 8432);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 181201, 8433);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 181201, 8434);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 181201, 8435);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 181201, 8436);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 181201, 8437);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 181201, 8439);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 181201, 8440);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 181201, 8441);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 181201, 8442);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 181201, 8444);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 181201, 8445);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 131901, 8447);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 131901, 8448);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 131901, 8449);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 131901, 8450);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 131901, 8451);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 131901, 8452);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 131901, 8453);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 131901, 8454);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 131901, 8455);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 131901, 8456);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 131901, 8457);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 131901, 8458);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 131901, 8459);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 131901, 8460);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 131901, 8461);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 131901, 8462);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 131901, 8464);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "細かいところまで\nすごーく丁寧に\n作られてる…", 131901, 8465);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 131901, 8466);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 131901, 8467);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 131901, 8469);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 131901, 8470);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 131901, 8472);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 131901, 8473);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 132101, 8474);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 132101, 8475);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 132101, 8476);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 132101, 8477);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 132101, 8478);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 132101, 8479);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 132101, 8480);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 132101, 8481);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 132101, 8482);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 132101, 8483);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 132101, 8484);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 132101, 8485);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 132101, 8486);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 132101, 8487);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 132101, 8488);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 132101, 8489);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 132101, 8491);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 132101, 8492);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 132101, 8493);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 132101, 8494);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 132101, 8496);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "海に関する詩文なども\nあるのでしょうか…\n気になります", 132101, 8497);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 132101, 8499);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 132101, 8500);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "これがこの世界の\n居住環境か…\n面白い構造だね", 132001, 8501);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 132001, 8502);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "目新しいものが\nたくさんあるね\n胸が躍るよ", 132001, 8503);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 132001, 8504);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 132001, 8505);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 132001, 8506);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 132001, 8507);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 132001, 8508);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 132001, 8509);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この世界においては\nボクは異物だからね\n大人しくしているさ", 132001, 8510);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 132001, 8511);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 132001, 8512);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 132001, 8513);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 132001, 8514);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 132001, 8515);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 132001, 8516);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 132001, 8518);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 132001, 8519);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 132001, 8520);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 132001, 8521);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 132001, 8523);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "素晴らしいね\nここには叡智が\n詰まっている", 132001, 8524);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 132001, 8526);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 132001, 8527);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 109701, 8528);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 109701, 8529);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 109801, 8530);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 109801, 8531);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 103, 1, 0, 12, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 109901, 8532);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 103, 1, 0, 12, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 109901, 8533);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 132201, 8534);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 132201, 8535);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 132201, 8536);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 132201, 8537);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 132201, 8538);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 132201, 8539);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 132201, 8540);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 132201, 8541);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 132201, 8542);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 132201, 8543);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 132201, 8544);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 132201, 8545);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 132201, 8546);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 132201, 8547);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 132201, 8548);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 132201, 8549);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 132201, 8551);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 132201, 8552);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 132201, 8553);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 132201, 8554);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 132201, 8556);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 132201, 8557);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 127701, 8559);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 127701, 8560);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 127701, 8561);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 127701, 8562);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 127701, 8563);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 127701, 8564);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 127701, 8565);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 127701, 8566);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 127701, 8567);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 127701, 8568);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 127701, 8569);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 127701, 8570);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 127701, 8571);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 127701, 8572);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 127701, 8573);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 127701, 8574);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 127701, 8576);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 127701, 8577);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 127701, 8578);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 127701, 8579);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 127701, 8581);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 127701, 8582);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 127801, 8584);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 127801, 8585);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 127801, 8586);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 127801, 8587);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 127801, 8588);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 127801, 8589);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 127801, 8590);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 127801, 8591);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 127801, 8592);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 127801, 8593);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 127801, 8594);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 127801, 8595);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 127801, 8596);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 127801, 8597);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 127801, 8598);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "よく休んでください\n布団は干しといたんで\n…姉さんが", 127801, 8599);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふー…\nこのままずーっと\nここに居たい…", 127801, 8601);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "あんたも一緒に\nサボっちゃうか～\n…なんて", 127801, 8602);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 127801, 8603);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 127801, 8604);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 127801, 8606);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "食に関する本が\nやけに多い…\nお国柄ですかね", 127801, 8607);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 130201, 8609);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 130201, 8610);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 130201, 8611);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 130201, 8612);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 130201, 8613);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 130201, 8614);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 130201, 8615);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 130201, 8616);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 130201, 8617);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 130201, 8618);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 130201, 8619);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 130201, 8620);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 130201, 8621);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 130201, 8622);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 130201, 8623);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 130201, 8624);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 130201, 8626);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 130201, 8627);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 130201, 8628);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 130201, 8629);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 130201, 8631);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 130201, 8632);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 130001, 8634);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 130001, 8635);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 130001, 8636);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 130001, 8637);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 130001, 8638);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 130001, 8639);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 130001, 8640);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 130001, 8641);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 130001, 8642);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 130001, 8643);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 130001, 8644);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 130001, 8645);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 130001, 8646);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 130001, 8647);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 130001, 8648);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 130001, 8649);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 130001, 8651);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 130001, 8652);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 130001, 8653);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 130001, 8654);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 130001, 8656);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 130001, 8657);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 130101, 8659);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 130101, 8660);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 130101, 8661);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 130101, 8662);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 130101, 8663);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 130101, 8664);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 130101, 8665);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 130101, 8666);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 130101, 8667);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 130101, 8668);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 130101, 8669);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 130101, 8670);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 130101, 8671);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 130101, 8672);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 130101, 8673);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 130101, 8674);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "これ…割れ物防止の\nクッションに\n使えるかも…？", 130101, 8676);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 130101, 8677);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 130101, 8678);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 130101, 8679);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "これがあったら\nもうお料理を\n作らなくて済むかも？", 130101, 8681);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 130101, 8682);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 132801, 8684);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 132801, 8685);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 132801, 8686);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 132801, 8687);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 132801, 8688);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 132801, 8689);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 132801, 8690);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 132801, 8691);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 132801, 8692);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 132801, 8693);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 132801, 8694);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 132801, 8695);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 132801, 8696);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 132801, 8697);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 132801, 8698);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 132801, 8699);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 132801, 8701);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 132801, 8702);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 132801, 8703);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 132801, 8704);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 132801, 8706);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 132801, 8707);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 132803, 8709);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 132804, 8710);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 132901, 8711);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 132901, 8712);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 132901, 8713);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 132901, 8714);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 132901, 8715);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 132901, 8716);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 132901, 8717);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 132901, 8718);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 132901, 8719);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 132901, 8720);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 132901, 8721);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 132901, 8722);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 132901, 8723);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 132901, 8724);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 132901, 8725);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 132901, 8726);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 132901, 8728);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 132901, 8729);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 132901, 8730);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 132901, 8731);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 132901, 8733);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 132901, 8734);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 14, 1, 0, 1, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 132901, 8736);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 15, 1, 0, 1, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 132901, 8737);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 129501, 8738);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 129501, 8739);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 129501, 8740);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 129501, 8741);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 129501, 8742);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 129501, 8743);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 129501, 8744);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 129501, 8745);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 129501, 8746);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 129501, 8747);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 129501, 8748);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 129501, 8749);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 129501, 8750);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 129501, 8751);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 129501, 8752);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 129501, 8753);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 129501, 8755);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 129501, 8756);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 129501, 8757);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 129501, 8758);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 129501, 8760);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 129501, 8761);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 10, 1, 0, 1, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 129501, 8763);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 11, 1, 0, 1, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 129501, 8764);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 133001, 8765);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 133001, 8766);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 133001, 8767);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 133001, 8768);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 133001, 8769);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 133001, 8770);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 133001, 8771);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 133001, 8772);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 133001, 8773);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 133001, 8774);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 133001, 8775);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 133001, 8776);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 133001, 8777);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 133001, 8778);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 133001, 8779);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 133001, 8780);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 133001, 8782);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 133001, 8783);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 133001, 8784);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 133001, 8785);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 133001, 8787);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 133001, 8788);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 133201, 8790);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 133201, 8791);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 133201, 8792);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 133201, 8793);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 133201, 8794);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 133201, 8795);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 133201, 8796);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 133201, 8797);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 133201, 8798);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 133201, 8799);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 133201, 8800);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 133201, 8801);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 133201, 8802);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 133201, 8803);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 133201, 8804);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 133201, 8805);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "これに座って飲む\n甘酒は格別だな", 133201, 8807);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 133201, 8808);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 133201, 8809);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 133201, 8810);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 133201, 8812);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 133201, 8813);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 133501, 8815);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 133501, 8816);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 133501, 8817);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 133501, 8818);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 133501, 8819);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 133501, 8820);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 133501, 8821);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 133501, 8822);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 133501, 8823);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 133501, 8824);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 133501, 8825);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 133501, 8826);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 133501, 8827);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 133501, 8828);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 133501, 8829);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 133501, 8830);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 133501, 8832);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 133501, 8833);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 133501, 8834);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 133501, 8835);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 133501, 8837);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 133501, 8838);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 133601, 8840);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "お姉ちゃん…\n早く会いたいなぁ…", 133601, 8841);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 133601, 8842);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 133601, 8843);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 133601, 8844);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 133601, 8845);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 133601, 8846);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 133601, 8847);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 133601, 8848);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 133601, 8849);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 133601, 8850);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 133601, 8851);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 133601, 8852);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 133601, 8853);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 133601, 8854);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 133601, 8855);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 133601, 8857);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 133601, 8858);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 133601, 8859);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 133601, 8860);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 133601, 8862);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 133601, 8863);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 133701, 8865);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 133701, 8866);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 133701, 8867);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 133701, 8868);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 133701, 8869);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 133701, 8870);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 133701, 8871);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 133701, 8872);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 133701, 8873);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 133701, 8874);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 133701, 8875);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 133701, 8876);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 133701, 8877);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 133701, 8878);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 133701, 8879);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 133701, 8880);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 133701, 8882);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 133701, 8883);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 133701, 8884);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 133701, 8885);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 133701, 8887);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 133701, 8888);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 1, 1, 0, 1, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 133901, 8890);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 2, 1, 0, 1, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 133901, 8891);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 3, 1, 0, 1, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 133901, 8892);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 4, 1, 0, 1, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 133901, 8893);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 5, 1, 0, 1, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 133901, 8894);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 101, 1, 0, 4, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 133901, 8895);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (1, 0, 102, 1, 0, 8, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 133901, 8896);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 0, 1, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 133901, 8897);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 0, 1, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 133901, 8898);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 0, 1, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 133901, 8899);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 4, 1, 0, 1, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 133901, 8900);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 101, 1, 0, 4, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 133901, 8901);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 102, 1, 0, 8, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 133901, 8902);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 1, 1, 1, 1, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 133901, 8903);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 2, 1, 2, 1, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 133901, 8904);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (2, 0, 3, 1, 3, 1, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 133901, 8905);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 1, 1, 0, 1, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 133901, 8906);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 2, 1, 0, 1, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 133901, 8907);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 3, 1, 0, 1, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 133901, 8908);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 4, 1, 0, 1, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 133901, 8909);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 5, 1, 0, 1, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 133901, 8910);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 6, 1, 0, 1, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 133901, 8911);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 7, 1, 0, 1, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 133901, 8912);
INSERT INTO `v1_bc7600892a049695123ec87de870d3efe2f195af5192559c31e1843ccbaaf77b` VALUES (3, 0, 8, 1, 0, 1, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 133901, 8913);
