INSERT INTO `v1_bc7884dabd1b594fb6023b2764f584d7d1ed342fa2f3f6bcdaf62a2d65373068` VALUES (147, "炎天烈日", "一定時間が経過するごとに、敵、味方全体に最大ＨＰに応じた最大20万の割合ダメージを与える。さらに、付与される継続ダメージが上昇する。", 2017);
