INSERT INTO `v1_bc7884dabd1b594fb6023b2764f584d7d1ed342fa2f3f6bcdaf62a2d65373068` VALUES (149, "寒気凛冽", "敵、味方全体の行動速度を小ダウンさせる。このスキルの効果は他の行動速度変化スキルと重複する。さらに一定時間が経過するごとに、初期値より行動速度が低い状態のキャラに凍結状態を付与する。", 2017);
