INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (1, "2030/12/17 14:59:59", 100, "2024/03/31 12:00:00", 129901, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 7, 10.85, 24, "エリコ", 100, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (1, "2030/12/17 14:59:59", 101, "2024/03/31 12:00:00", 129901, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 2, 11.15, 24, "エリコ", 101, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (7, "2030/12/17 14:59:59", 102, "2024/03/31 12:00:00", 129901, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 5, 6.4, 24, "エリコ", 102, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (3, "2030/12/17 14:59:59", 103, "2024/03/31 12:00:00", 129901, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 3, 0.0, 24, "エリコ", 103, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 104, "2024/03/31 12:00:00", 129901, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 8, 2.43, 24, "エリコ", 104, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (4, "2030/12/17 14:59:59", 105, "2024/03/31 12:00:00", 129901, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 3, 9.08, 24, "エリコ", 105, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (6, "2030/12/17 14:59:59", 106, "2024/03/31 12:00:00", 129901, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 6, 0.0, 24, "エリコ", 106, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (3, "2030/12/17 14:59:59", 107, "2024/03/31 12:00:00", 129901, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 8, 10.6, 24, "エリコ", 107, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (6, "2030/12/17 14:59:59", 108, "2024/03/31 12:00:00", 129901, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 4, 7.54, 24, "エリコ", 108, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 109, "2024/03/31 12:00:00", 129901, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 7, 11.5, 24, "エリコ", 109, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 110, "2024/03/31 12:00:00", 129901, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 1, 8.83, 24, "エリコ", 110, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (1, "2030/12/17 14:59:59", 111, "2024/03/31 12:00:00", 129901, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 2, 6.68, 24, "エリコ", 111, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (6, "2030/12/17 14:59:59", 112, "2024/03/31 12:00:00", 129901, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 2, 8.8, 24, "エリコ", 112, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 113, "2024/03/31 12:00:00", 129901, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 1, 8.9, 24, "エリコ", 113, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 114, "2024/03/31 12:00:00", 129901, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 5, 3.7, 24, "エリコ", 114, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (1, "2030/12/17 14:59:59", 200, "2024/03/31 12:00:00", 129801, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 2, 7.6, 24, "マコト", 200, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 201, "2024/03/31 12:00:00", 129801, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 1, 2.25, 24, "マコト", 201, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 202, "2024/03/31 12:00:00", 129801, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 1, 3.28, 24, "マコト", 202, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (4, "2030/12/17 14:59:59", 203, "2024/03/31 12:00:00", 129801, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 1, 2.98, 24, "マコト", 203, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (4, "2030/12/17 14:59:59", 204, "2024/03/31 12:00:00", 129801, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 1, 3.0, 24, "マコト", 204, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (1, "2030/12/17 14:59:59", 205, "2024/03/31 12:00:00", 129801, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 2, 10.3, 24, "マコト", 205, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (6, "2030/12/17 14:59:59", 206, "2024/03/31 12:00:00", 129801, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 1, 2.93, 24, "マコト", 206, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (3, "2030/12/17 14:59:59", 207, "2024/03/31 12:00:00", 129801, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 1, 9.3, 24, "マコト", 207, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (4, "2030/12/17 14:59:59", 208, "2024/03/31 12:00:00", 129801, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 1, 6.9, 24, "マコト", 208, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 209, "2024/03/31 12:00:00", 129801, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 2, 0.0, 24, "マコト", 209, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (1, "2030/12/17 14:59:59", 210, "2024/03/31 12:00:00", 129801, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 2, 5.95, 24, "マコト", 210, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 211, "2024/03/31 12:00:00", 129801, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 1, 6.73, 24, "マコト", 211, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (2, "2030/12/17 14:59:59", 212, "2024/03/31 12:00:00", 129801, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 2, 0.0, 24, "マコト", 212, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (1, "2030/12/17 14:59:59", 213, "2024/03/31 12:00:00", 129801, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 2, 6.65, 24, "マコト", 213, 0.0, 10142, 0.0);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (6, "2030/12/17 14:59:59", 214, "2024/03/31 12:00:00", 129801, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 1, 3.05, 24, "マコト", 214, 0.0, 10142, 0.0);
