INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 7, 100, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 1, 100, 24, 10.85, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 101, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 1, 101, 24, 11.15, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 5, 102, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 7, 102, 24, 6.4, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 3, 103, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 3, 103, 24, 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 8, 104, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 2, 104, 24, 2.43, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 3, 105, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 4, 105, 24, 9.08, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 6, 106, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 6, 106, 24, 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 8, 107, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 3, 107, 24, 10.6, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 4, 108, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 6, 108, 24, 7.54, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 7, 109, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 2, 109, 24, 11.5, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 110, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 2, 110, 24, 8.83, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 111, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 1, 111, 24, 6.68, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 112, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 6, 112, 24, 8.8, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 113, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 2, 113, 24, 8.9, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 5, 114, 129901, "エリコ", 0.0, "2024/03/31 12:00:00", 0.0, 2, 114, 24, 3.7, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 200, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 1, 200, 24, 7.6, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 201, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 2, 201, 24, 2.25, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 202, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 2, 202, 24, 3.28, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 203, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 4, 203, 24, 2.98, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 204, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 4, 204, 24, 3.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 205, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 1, 205, 24, 10.3, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 206, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 6, 206, 24, 2.93, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 207, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 3, 207, 24, 9.3, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 208, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 4, 208, 24, 6.9, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 209, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 2, 209, 24, 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 210, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 1, 210, 24, 5.95, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 211, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 2, 211, 24, 6.73, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 212, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 2, 212, 24, 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 2, 213, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 1, 213, 24, 6.65, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ");
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES ("2030/12/17 14:59:59", 10142, 1, 214, 129801, "マコト", 0.0, "2024/03/31 12:00:00", 0.0, 6, 214, 24, 3.05, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！");
