INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (100, 10142, 0.0, "エリコ", 0.0, 10.85, "2030/12/17 14:59:59", 24, 1, 100, "2024/03/31 12:00:00", 7, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (101, 10142, 0.0, "エリコ", 0.0, 11.15, "2030/12/17 14:59:59", 24, 1, 101, "2024/03/31 12:00:00", 2, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (102, 10142, 0.0, "エリコ", 0.0, 6.4, "2030/12/17 14:59:59", 24, 7, 102, "2024/03/31 12:00:00", 5, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (103, 10142, 0.0, "エリコ", 0.0, 0.0, "2030/12/17 14:59:59", 24, 3, 103, "2024/03/31 12:00:00", 3, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (104, 10142, 0.0, "エリコ", 0.0, 2.43, "2030/12/17 14:59:59", 24, 2, 104, "2024/03/31 12:00:00", 8, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (105, 10142, 0.0, "エリコ", 0.0, 9.08, "2030/12/17 14:59:59", 24, 4, 105, "2024/03/31 12:00:00", 3, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (106, 10142, 0.0, "エリコ", 0.0, 0.0, "2030/12/17 14:59:59", 24, 6, 106, "2024/03/31 12:00:00", 6, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (107, 10142, 0.0, "エリコ", 0.0, 10.6, "2030/12/17 14:59:59", 24, 3, 107, "2024/03/31 12:00:00", 8, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (108, 10142, 0.0, "エリコ", 0.0, 7.54, "2030/12/17 14:59:59", 24, 6, 108, "2024/03/31 12:00:00", 4, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (109, 10142, 0.0, "エリコ", 0.0, 11.5, "2030/12/17 14:59:59", 24, 2, 109, "2024/03/31 12:00:00", 7, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (110, 10142, 0.0, "エリコ", 0.0, 8.83, "2030/12/17 14:59:59", 24, 2, 110, "2024/03/31 12:00:00", 1, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (111, 10142, 0.0, "エリコ", 0.0, 6.68, "2030/12/17 14:59:59", 24, 1, 111, "2024/03/31 12:00:00", 2, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (112, 10142, 0.0, "エリコ", 0.0, 8.8, "2030/12/17 14:59:59", 24, 6, 112, "2024/03/31 12:00:00", 2, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (113, 10142, 0.0, "エリコ", 0.0, 8.9, "2030/12/17 14:59:59", 24, 2, 113, "2024/03/31 12:00:00", 1, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (114, 10142, 0.0, "エリコ", 0.0, 3.7, "2030/12/17 14:59:59", 24, 2, 114, "2024/03/31 12:00:00", 5, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 129901);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (200, 10142, 0.0, "マコト", 0.0, 7.6, "2030/12/17 14:59:59", 24, 1, 200, "2024/03/31 12:00:00", 2, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (201, 10142, 0.0, "マコト", 0.0, 2.25, "2030/12/17 14:59:59", 24, 2, 201, "2024/03/31 12:00:00", 1, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (202, 10142, 0.0, "マコト", 0.0, 3.28, "2030/12/17 14:59:59", 24, 2, 202, "2024/03/31 12:00:00", 1, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (203, 10142, 0.0, "マコト", 0.0, 2.98, "2030/12/17 14:59:59", 24, 4, 203, "2024/03/31 12:00:00", 1, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (204, 10142, 0.0, "マコト", 0.0, 3.0, "2030/12/17 14:59:59", 24, 4, 204, "2024/03/31 12:00:00", 1, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (205, 10142, 0.0, "マコト", 0.0, 10.3, "2030/12/17 14:59:59", 24, 1, 205, "2024/03/31 12:00:00", 2, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (206, 10142, 0.0, "マコト", 0.0, 2.93, "2030/12/17 14:59:59", 24, 6, 206, "2024/03/31 12:00:00", 1, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (207, 10142, 0.0, "マコト", 0.0, 9.3, "2030/12/17 14:59:59", 24, 3, 207, "2024/03/31 12:00:00", 1, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (208, 10142, 0.0, "マコト", 0.0, 6.9, "2030/12/17 14:59:59", 24, 4, 208, "2024/03/31 12:00:00", 1, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (209, 10142, 0.0, "マコト", 0.0, 0.0, "2030/12/17 14:59:59", 24, 2, 209, "2024/03/31 12:00:00", 2, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (210, 10142, 0.0, "マコト", 0.0, 5.95, "2030/12/17 14:59:59", 24, 1, 210, "2024/03/31 12:00:00", 2, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (211, 10142, 0.0, "マコト", 0.0, 6.73, "2030/12/17 14:59:59", 24, 2, 211, "2024/03/31 12:00:00", 1, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (212, 10142, 0.0, "マコト", 0.0, 0.0, "2030/12/17 14:59:59", 24, 2, 212, "2024/03/31 12:00:00", 2, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (213, 10142, 0.0, "マコト", 0.0, 6.65, "2030/12/17 14:59:59", 24, 1, 213, "2024/03/31 12:00:00", 2, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 129801);
INSERT INTO `v1_be20a9b90695975294ec14c19d87b756cedf937d2e569073346434e8abc33a14` VALUES (214, 10142, 0.0, "マコト", 0.0, 3.05, "2030/12/17 14:59:59", 24, 6, 214, "2024/03/31 12:00:00", 1, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 129801);
