INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1000001, "2030-04-01 14:00:00", 13, 1000, 190801, 0, "2020-02-15 15:00:00", "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1000002, "2030-04-01 14:00:00", 13, 1000, 190801, 0, "2020-02-15 15:00:00", "楽しいミニゲームをご紹介！\nあなたはどのミニゲームで\n遊びますか？");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1001001, "2030-04-01 14:00:00", 13, 1001, 190801, 0, "2020-02-15 15:00:00", "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1002001, "2030-04-01 14:00:00", 12, 1002, 190801, 0, "2020-02-15 15:00:00", "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1003001, "2030-04-01 14:00:00", 11, 1003, 190801, 0, "2020-02-15 15:00:00", "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1004001, "2030-04-01 14:00:00", 3, 1004, 190801, 0, "2020-02-15 15:00:00", "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1005001, "2030-04-01 14:00:00", 7, 1005, 190801, 0, "2020-02-15 15:00:00", "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1006001, "2030-04-01 14:00:00", 2, 1006, 190801, 0, "2020-05-31 12:00:00", "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1007001, "2030-04-01 14:00:00", 4, 1007, 190801, 0, "2020-08-15 15:00:00", "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1008001, "2030-04-01 14:00:00", 6, 1008, 190801, 0, "2021-08-31 12:00:00", "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1009001, "2030-04-01 14:00:00", 13, 1009, 190801, 0, "2021-12-18 15:00:00", "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1010001, "2030-04-01 14:00:00", 4, 1010, 190801, 0, "2022-02-15 15:00:00", "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1011001, "2030-04-01 14:00:00", 6, 1011, 190801, 0, "2022-07-18 15:00:00", "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1012001, "2030-04-01 14:00:00", 6, 1012, 190801, 0, "2023-09-18 15:00:00", "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (1013001, "2030-04-01 14:00:00", 6, 1013, 190801, 0, "2024-04-18 15:00:00", "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪");
