INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (13, "2030-04-01 14:00:00", 190801, 1000, "2020-02-15 15:00:00", 1000001, 0, "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (13, "2030-04-01 14:00:00", 190801, 1000, "2020-02-15 15:00:00", 1000002, 0, "楽しいミニゲームをご紹介！\nあなたはどのミニゲームで\n遊びますか？");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (13, "2030-04-01 14:00:00", 190801, 1001, "2020-02-15 15:00:00", 1001001, 0, "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (12, "2030-04-01 14:00:00", 190801, 1002, "2020-02-15 15:00:00", 1002001, 0, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (11, "2030-04-01 14:00:00", 190801, 1003, "2020-02-15 15:00:00", 1003001, 0, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (3, "2030-04-01 14:00:00", 190801, 1004, "2020-02-15 15:00:00", 1004001, 0, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (7, "2030-04-01 14:00:00", 190801, 1005, "2020-02-15 15:00:00", 1005001, 0, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (2, "2030-04-01 14:00:00", 190801, 1006, "2020-05-31 12:00:00", 1006001, 0, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (4, "2030-04-01 14:00:00", 190801, 1007, "2020-08-15 15:00:00", 1007001, 0, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (6, "2030-04-01 14:00:00", 190801, 1008, "2021-08-31 12:00:00", 1008001, 0, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (13, "2030-04-01 14:00:00", 190801, 1009, "2021-12-18 15:00:00", 1009001, 0, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (4, "2030-04-01 14:00:00", 190801, 1010, "2022-02-15 15:00:00", 1010001, 0, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (6, "2030-04-01 14:00:00", 190801, 1011, "2022-07-18 15:00:00", 1011001, 0, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (6, "2030-04-01 14:00:00", 190801, 1012, "2023-09-18 15:00:00", 1012001, 0, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (6, "2030-04-01 14:00:00", 190801, 1013, "2024-04-18 15:00:00", 1013001, 0, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪");
