INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2020-02-15 15:00:00", 13, 1000002, "楽しいミニゲームをご紹介！\nあなたはどのミニゲームで\n遊びますか？", 190801, 1000, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2020-02-15 15:00:00", 13, 1001001, "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！", 190801, 1001, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2020-02-15 15:00:00", 12, 1002001, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！", 190801, 1002, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2020-02-15 15:00:00", 11, 1003001, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", 190801, 1003, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2020-02-15 15:00:00", 3, 1004001, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", 190801, 1004, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2020-02-15 15:00:00", 7, 1005001, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", 190801, 1005, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2020-05-31 12:00:00", 2, 1006001, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", 190801, 1006, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2020-08-15 15:00:00", 4, 1007001, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう", 190801, 1007, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2021-08-31 12:00:00", 6, 1008001, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n", 190801, 1008, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2021-12-18 15:00:00", 13, 1009001, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。", 190801, 1009, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2022-02-15 15:00:00", 4, 1010001, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", 190801, 1010, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2022-07-18 15:00:00", 6, 1011001, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", 190801, 1011, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2023-09-18 15:00:00", 6, 1012001, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪", 190801, 1012, "2030-04-01 14:00:00");
INSERT INTO `v1_bf39b2453c29a8cbce79450589c9dbdb360ccccb80dcf61c8677bd64ec52a029` VALUES (0, "2024-04-18 15:00:00", 6, 1013001, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪", 190801, 1013, "2030-04-01 14:00:00");
