INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 1, "2024/03/31 12:00:00", 100, 0.0, 10.85, 100, "エリコ", 10142, 7, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 1, "2024/03/31 12:00:00", 101, 0.0, 11.15, 101, "エリコ", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 7, "2024/03/31 12:00:00", 102, 0.0, 6.4, 102, "エリコ", 10142, 5, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 3, "2024/03/31 12:00:00", 103, 0.0, 0.0, 103, "エリコ", 10142, 3, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 2, "2024/03/31 12:00:00", 104, 0.0, 2.43, 104, "エリコ", 10142, 8, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 4, "2024/03/31 12:00:00", 105, 0.0, 9.08, 105, "エリコ", 10142, 3, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 6, "2024/03/31 12:00:00", 106, 0.0, 0.0, 106, "エリコ", 10142, 6, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 3, "2024/03/31 12:00:00", 107, 0.0, 10.6, 107, "エリコ", 10142, 8, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 6, "2024/03/31 12:00:00", 108, 0.0, 7.54, 108, "エリコ", 10142, 4, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 2, "2024/03/31 12:00:00", 109, 0.0, 11.5, 109, "エリコ", 10142, 7, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 2, "2024/03/31 12:00:00", 110, 0.0, 8.83, 110, "エリコ", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 1, "2024/03/31 12:00:00", 111, 0.0, 6.68, 111, "エリコ", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 6, "2024/03/31 12:00:00", 112, 0.0, 8.8, 112, "エリコ", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 2, "2024/03/31 12:00:00", 113, 0.0, 8.9, 113, "エリコ", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129901, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 2, "2024/03/31 12:00:00", 114, 0.0, 3.7, 114, "エリコ", 10142, 5, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 1, "2024/03/31 12:00:00", 200, 0.0, 7.6, 200, "マコト", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 2, "2024/03/31 12:00:00", 201, 0.0, 2.25, 201, "マコト", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 2, "2024/03/31 12:00:00", 202, 0.0, 3.28, 202, "マコト", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 4, "2024/03/31 12:00:00", 203, 0.0, 2.98, 203, "マコト", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 4, "2024/03/31 12:00:00", 204, 0.0, 3.0, 204, "マコト", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 1, "2024/03/31 12:00:00", 205, 0.0, 10.3, 205, "マコト", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 6, "2024/03/31 12:00:00", 206, 0.0, 2.93, 206, "マコト", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 3, "2024/03/31 12:00:00", 207, 0.0, 9.3, 207, "マコト", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 4, "2024/03/31 12:00:00", 208, 0.0, 6.9, 208, "マコト", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 2, "2024/03/31 12:00:00", 209, 0.0, 0.0, 209, "マコト", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 1, "2024/03/31 12:00:00", 210, 0.0, 5.95, 210, "マコト", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 2, "2024/03/31 12:00:00", 211, 0.0, 6.73, 211, "マコト", 10142, 1, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 2, "2024/03/31 12:00:00", 212, 0.0, 0.0, 212, "マコト", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 1, "2024/03/31 12:00:00", 213, 0.0, 6.65, 213, "マコト", 10142, 2, "2030/12/17 14:59:59");
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (24, 0.0, 129801, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 6, "2024/03/31 12:00:00", 214, 0.0, 3.05, 214, "マコト", 10142, 1, "2030/12/17 14:59:59");
