INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 100, 1, 10.85, "エリコ", 100, "2030/12/17 14:59:59", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "2024/03/31 12:00:00", 0.0, 10142, 7, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 101, 1, 11.15, "エリコ", 101, "2030/12/17 14:59:59", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 102, 7, 6.4, "エリコ", 102, "2030/12/17 14:59:59", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "2024/03/31 12:00:00", 0.0, 10142, 5, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 103, 3, 0.0, "エリコ", 103, "2030/12/17 14:59:59", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "2024/03/31 12:00:00", 0.0, 10142, 3, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 104, 2, 2.43, "エリコ", 104, "2030/12/17 14:59:59", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "2024/03/31 12:00:00", 0.0, 10142, 8, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 105, 4, 9.08, "エリコ", 105, "2030/12/17 14:59:59", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "2024/03/31 12:00:00", 0.0, 10142, 3, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 106, 6, 0.0, "エリコ", 106, "2030/12/17 14:59:59", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "2024/03/31 12:00:00", 0.0, 10142, 6, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 107, 3, 10.6, "エリコ", 107, "2030/12/17 14:59:59", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "2024/03/31 12:00:00", 0.0, 10142, 8, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 108, 6, 7.54, "エリコ", 108, "2030/12/17 14:59:59", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "2024/03/31 12:00:00", 0.0, 10142, 4, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 109, 2, 11.5, "エリコ", 109, "2030/12/17 14:59:59", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "2024/03/31 12:00:00", 0.0, 10142, 7, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 110, 2, 8.83, "エリコ", 110, "2030/12/17 14:59:59", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 111, 1, 6.68, "エリコ", 111, "2030/12/17 14:59:59", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 112, 6, 8.8, "エリコ", 112, "2030/12/17 14:59:59", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 113, 2, 8.9, "エリコ", 113, "2030/12/17 14:59:59", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 114, 2, 3.7, "エリコ", 114, "2030/12/17 14:59:59", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "2024/03/31 12:00:00", 0.0, 10142, 5, 24, 129901);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 200, 1, 7.6, "マコト", 200, "2030/12/17 14:59:59", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 201, 2, 2.25, "マコト", 201, "2030/12/17 14:59:59", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 202, 2, 3.28, "マコト", 202, "2030/12/17 14:59:59", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 203, 4, 2.98, "マコト", 203, "2030/12/17 14:59:59", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 204, 4, 3.0, "マコト", 204, "2030/12/17 14:59:59", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 205, 1, 10.3, "マコト", 205, "2030/12/17 14:59:59", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 206, 6, 2.93, "マコト", 206, "2030/12/17 14:59:59", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 207, 3, 9.3, "マコト", 207, "2030/12/17 14:59:59", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 208, 4, 6.9, "マコト", 208, "2030/12/17 14:59:59", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 209, 2, 0.0, "マコト", 209, "2030/12/17 14:59:59", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 210, 1, 5.95, "マコト", 210, "2030/12/17 14:59:59", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 211, 2, 6.73, "マコト", 211, "2030/12/17 14:59:59", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 212, 2, 0.0, "マコト", 212, "2030/12/17 14:59:59", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 213, 1, 6.65, "マコト", 213, "2030/12/17 14:59:59", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "2024/03/31 12:00:00", 0.0, 10142, 2, 24, 129801);
INSERT INTO `v1_c08605f0cf1d25b946a34b1c4be1d5d2e25296f8bf2b98bdfb6a4a61456f70b0` VALUES (0.0, 214, 6, 3.05, "マコト", 214, "2030/12/17 14:59:59", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "2024/03/31 12:00:00", 0.0, 10142, 1, 24, 129801);
