INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190804, 0, 1, 1, 60000, 1, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 2, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 3, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60000, 2, 4, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 5, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 6, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60000, 2, 7, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 8, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 9, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 10, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 11, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60000, 2, 12, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 13, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 14, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 15, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60000, 2, 16, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60000, 2, 17, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190804, 0, 0, 2, 60000, 3, 18, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190804, 7, 0, 3, 60000, 3, 19, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101201, 1, 0, 1, 80001, 0, 20, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", "ハツネ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101201, 1, 0, 2, 80001, 0, 21, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", "ハツネ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103801, 1, 0, 1, 80001, 1, 22, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", "シオリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103801, 1, 0, 2, 80001, 1, 23, "来年も、\n一緒にいられたら…\nうれしいな", "シオリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103601, 2, 0, 1, 80001, 0, 24, "あなたと一緒にいるようになって、\nもう1年ですか…", "キョウカ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103601, 2, 0, 2, 80001, 0, 25, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", "キョウカ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102001, 2, 0, 1, 80001, 1, 26, "おにいちゃんとあってから、\n1年たったよ～！", "ミミ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102001, 2, 0, 2, 80001, 1, 27, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", "ミミ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100401, 2, 0, 1, 80001, 2, 28, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", "ミソギ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100401, 2, 0, 2, 80001, 2, 29, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", "ミソギ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101001, 3, 0, 1, 80001, 0, 30, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", "マホ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101001, 3, 0, 2, 80001, 0, 31, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", "マホ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104401, 3, 0, 1, 80001, 1, 32, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", "イリヤ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104401, 3, 0, 2, 80001, 1, 33, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", "イリヤ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101701, 3, 0, 1, 80001, 2, 34, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", "カオリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101701, 3, 0, 2, 80001, 2, 35, "これからも、どうぞよろしく\nお願いするさ～！", "カオリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102801, 4, 0, 1, 80001, 0, 36, "あんたと出会ってからもうそんなに\nたつのね～", "サレン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102801, 4, 0, 2, 80001, 0, 37, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", "サレン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102501, 4, 0, 1, 80001, 1, 38, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", "スズメ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102501, 4, 0, 2, 80001, 1, 39, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", "スズメ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104601, 5, 0, 1, 80001, 0, 40, "振り返れば、今までいろんなことが\nあったにゃあ…", "タマキ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104601, 5, 0, 2, 80001, 0, 41, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", "タマキ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104801, 5, 0, 1, 80001, 1, 42, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", "ミフユ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104801, 5, 0, 2, 80001, 1, 43, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", "ミフユ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103201, 6, 0, 1, 80001, 0, 44, "あなたさまと出会ってもう1年…", "アキノ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103201, 6, 0, 2, 80001, 0, 45, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", "アキノ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103401, 6, 0, 1, 80001, 1, 46, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", "ユカリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103401, 6, 0, 2, 80001, 1, 47, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", "ユカリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (107101, 7, 0, 1, 80001, 0, 48, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", "クリスティーナ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (107101, 7, 0, 2, 80001, 0, 49, "ともかくこれからも\n愉しませてくれよ☆", "クリスティーナ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104701, 7, 0, 1, 80001, 1, 50, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", "ジュン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104701, 7, 0, 2, 80001, 1, 51, "見えないから分からないって？\nそれもそうだな…", "ジュン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103701, 8, 0, 1, 80001, 0, 52, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", "トモ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103701, 8, 0, 2, 80001, 0, 53, "これからもよろしくね！", "トモ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100501, 8, 0, 1, 80001, 1, 54, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", "マツリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100501, 8, 0, 2, 80001, 1, 55, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", "マツリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100701, 9, 0, 1, 80001, 0, 56, "オマエと会って1年なの！\n全然成仏もできなかったし、", "ミヤコ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100701, 9, 0, 2, 80001, 0, 57, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", "ミヤコ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103101, 9, 0, 1, 80001, 1, 58, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", "シノブ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103101, 9, 0, 2, 80001, 1, 59, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", "シノブ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100901, 10, 0, 1, 80001, 0, 60, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", "アンナ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100901, 10, 0, 2, 80001, 0, 61, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", "アンナ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101301, 10, 0, 1, 80001, 1, 62, "君と出会ってもう1年だって！\n早いねー！", "ナナカ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101301, 10, 0, 2, 80001, 1, 63, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", "ナナカ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105101, 11, 0, 1, 80001, 0, 64, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", "ミツキ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105101, 11, 0, 2, 80001, 0, 65, "これからも、いろんなところを\n調べさせてね？", "ミツキ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105601, 11, 0, 1, 80001, 1, 66, "お前さんと出会ってもう1年か\n助太刀するつもりが", "ルカ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105601, 11, 0, 2, 80001, 1, 67, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", "ルカ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102701, 11, 0, 1, 80001, 2, 68, "早いものですわね、あなた様と\nお会いして、もう1年…", "エリコ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102701, 11, 0, 2, 80001, 2, 69, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", "エリコ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102301, 12, 0, 1, 80001, 0, 70, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", "アヤネ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102301, 12, 0, 2, 80001, 0, 71, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", "アヤネ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104201, 12, 0, 1, 80001, 1, 72, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", "チカ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104201, 12, 0, 2, 80001, 1, 73, "私のことを見守ってください\nできることなら、ずっと…", "チカ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102101, 12, 0, 1, 80001, 2, 74, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", "クルミ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102101, 12, 0, 2, 80001, 2, 75, "で、でもこれからもずっと…\n一緒にいたいなぁ…", "クルミ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104901, 13, 0, 1, 80001, 0, 76, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", "シズル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104901, 13, 0, 2, 80001, 0, 77, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", "シズル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101101, 13, 0, 1, 80001, 1, 78, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", "リノ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101101, 13, 0, 2, 80001, 1, 79, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", "リノ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100201, 14, 0, 1, 80001, 0, 80, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", "ユイ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100201, 14, 0, 2, 80001, 0, 81, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", "ユイ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100101, 14, 0, 1, 80001, 1, 82, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", "ヒヨリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100101, 14, 0, 2, 80001, 1, 83, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", "ヒヨリ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100301, 14, 0, 1, 80001, 2, 84, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", "レイ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100301, 14, 0, 2, 80001, 2, 85, "キミに背中を預けられる日が、\n早くきますように", "レイ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 1, 80001, 0, 86, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 2, 80001, 0, 87, "これからもずっと、覚えていて\nくださいね！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 1, 80001, 1, 88, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 2, 80001, 1, 89, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 1, 80001, 2, 90, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 2, 80001, 2, 91, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (112101, 0, 1, 1, 60001, 1, 92, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", "スズメ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 93, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 94, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 95, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 96, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60001, 2, 97, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 98, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 99, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 100, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 101, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60001, 2, 102, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 103, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 104, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 105, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 106, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 107, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 108, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60001, 2, 109, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 110, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 111, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 112, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 113, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 114, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60001, 2, 115, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60001, 2, 116, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (112101, 0, 0, 2, 60001, 3, 117, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", "スズメ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (112101, 7, 0, 3, 60001, 3, 118, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", "スズメ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 1, 0, 1, 80003, 0, 119, "ヒューヒュー♪\nドンドンパフパフー♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 1, 0, 1, 80003, 1, 120, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 1, 0, 2, 80003, 1, 121, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 1, 0, 1, 80003, 2, 122, "え？　ちょっと\nいきなりなにっ？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 1, 0, 2, 80003, 2, 123, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 1, 0, 2, 80003, 0, 124, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 1, 0, 3, 80003, 0, 125, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 1, 0, 3, 80003, 1, 126, "ですからその日に向けて\n今から気分を盛り上げているのです", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 1, 0, 3, 80003, 2, 127, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 1, 0, 4, 80003, 1, 128, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 1, 0, 4, 80003, 0, 129, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 1, 0, 4, 80003, 2, 130, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 1, 0, 5, 80003, 1, 131, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 5, 80003, 0, 132, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 2, 0, 1, 80003, 1, 133, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 6, 80003, 0, 134, "げっ…カリンじゃない\nなんでもないわよ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 2, 0, 2, 80003, 1, 135, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 7, 80003, 0, 136, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 2, 0, 3, 80003, 1, 137, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 8, 80003, 0, 138, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 9, 80003, 0, 139, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 2, 0, 4, 80003, 1, 140, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 10, 80003, 0, 141, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 2, 0, 5, 80003, 1, 142, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 11, 80003, 0, 143, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 6, 80003, 0, 144, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 5, 80003, 1, 145, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 7, 80003, 0, 146, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 8, 80003, 0, 147, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 6, 80003, 1, 148, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 7, 80003, 1, 149, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 9, 80003, 0, 150, "あとは、牧場に素敵な温泉が\n出来たとか…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 8, 80003, 1, 151, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 10, 80003, 0, 152, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 9, 80003, 1, 153, "えへへ、ありがとうございます\nコッコロちゃん！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 11, 80003, 0, 154, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 10, 80003, 1, 155, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 4, 0, 11, 80003, 0, 156, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 12, 80003, 1, 157, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 4, 0, 12, 80003, 0, 158, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 13, 80003, 1, 159, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 4, 0, 13, 80003, 0, 160, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 14, 80003, 1, 161, "…積極的？\nなんの話よ？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 4, 0, 14, 80003, 0, 162, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 4, 0, 15, 80003, 0, 163, "ムーサの町で\nきゃるきゃる～ん☆　って！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 15, 80003, 1, 164, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 4, 0, 16, 80003, 0, 165, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 16, 80003, 1, 166, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 17, 80003, 1, 167, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 18, 80003, 0, 168, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 5, 0, 12, 80003, 1, 169, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 19, 80003, 0, 170, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 5, 0, 13, 80003, 1, 171, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 20, 80003, 0, 172, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 5, 0, 14, 80003, 1, 173, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 21, 80003, 0, 174, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 5, 0, 15, 80003, 1, 175, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 22, 80003, 0, 176, "かくし芸？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 5, 0, 16, 80003, 1, 177, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 23, 80003, 0, 178, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 6, 0, 17, 80003, 0, 179, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 24, 80003, 2, 180, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 6, 0, 17, 80003, 1, 181, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 6, 0, 18, 80003, 0, 182, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 25, 80003, 2, 183, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 6, 0, 18, 80003, 1, 184, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 26, 80003, 2, 185, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 27, 80003, 2, 186, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 28, 80003, 2, 187, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 6, 0, 19, 80003, 0, 188, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 6, 0, 19, 80003, 1, 189, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 29, 80003, 2, 190, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 30, 80003, 0, 191, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 31, 80003, 0, 192, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 32, 80003, 0, 193, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 33, 80003, 0, 194, "あー\nあー\n…声、これで大丈夫かしら？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 34, 80003, 0, 195, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 35, 80003, 0, 196, "オイッスー☆", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 36, 80003, 0, 197, "……\nいや、ないわ\nいつもどおりが一番ね、うん", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 37, 80003, 0, 198, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 38, 80003, 0, 199, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 20, 80003, 0, 200, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 21, 80003, 0, 201, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 22, 80003, 0, 202, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 23, 80003, 0, 203, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 24, 80003, 0, 204, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 25, 80003, 0, 205, "オイッスー☆", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 26, 80003, 0, 206, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 27, 80003, 0, 207, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 28, 80003, 0, 208, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 20, 80003, 0, 209, "オイッスー☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 21, 80003, 0, 210, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 22, 80003, 0, 211, "あと６日で2.5周年ですか…", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 23, 80003, 0, 212, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 24, 80003, 0, 213, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 25, 80003, 0, 214, "オイッスー☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 26, 80003, 0, 215, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 27, 80003, 0, 216, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 28, 80003, 0, 217, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 29, 80003, 0, 218, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 9, 0, 30, 80003, 0, 219, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 10, 0, 31, 80003, 0, 220, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 29, 80003, 1, 221, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 10, 0, 32, 80003, 0, 222, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 10, 0, 39, 80003, 2, 223, "…………", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 30, 80003, 1, 224, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 10, 0, 40, 80003, 2, 225, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 10, 0, 33, 80003, 0, 226, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 10, 0, 41, 80003, 2, 227, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 10, 0, 34, 80003, 0, 228, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 10, 0, 35, 80003, 0, 229, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 10, 0, 42, 80003, 2, 230, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 31, 80003, 1, 231, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 10, 0, 43, 80003, 2, 232, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 10, 0, 36, 80003, 0, 233, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 6, 80003, 0, 234, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 7, 80003, 0, 235, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 8, 80003, 0, 236, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 9, 80003, 0, 237, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 10, 80003, 0, 238, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 11, 80003, 0, 239, "次にコッコロさんですが\nこちらは発声練習をしています", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 12, 80003, 0, 240, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 13, 80003, 0, 241, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 14, 80003, 0, 242, "あれ？\nもしかして緊張で気を失って…？", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 15, 80003, 0, 243, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 16, 80003, 0, 244, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 11, 0, 17, 80003, 0, 245, "それでは\nまた明日お会いしましょう♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 12, 0, 44, 80003, 0, 246, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 12, 0, 37, 80003, 2, 247, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 12, 0, 32, 80003, 1, 248, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 12, 0, 45, 80003, 0, 249, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 12, 0, 38, 80003, 2, 250, "ひどいですよぉキャルちゃ～ん！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 12, 0, 33, 80003, 1, 251, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 12, 0, 39, 80003, 2, 252, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 12, 0, 46, 80003, 0, 253, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 12, 0, 34, 80003, 1, 254, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 12, 0, 40, 80003, 2, 255, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 41, 80003, 0, 256, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 47, 80003, 2, 257, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 42, 80003, 0, 258, "忘れてきちゃいました☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 48, 80003, 2, 259, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 13, 0, 35, 80003, 1, 260, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 49, 80003, 2, 261, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 43, 80003, 0, 262, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 50, 80003, 2, 263, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 13, 0, 36, 80003, 1, 264, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 51, 80003, 2, 265, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 13, 0, 37, 80003, 1, 266, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 52, 80003, 2, 267, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 44, 80003, 0, 268, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 13, 0, 38, 80003, 1, 269, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 45, 80003, 0, 270, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 53, 80003, 2, 271, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 46, 80003, 0, 272, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 18, 80003, 0, 273, "こんにちは\n【ギルド管理協会】のカリンです", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 19, 80003, 0, 274, "昨日の生放送は\nいかがだったでしょうか？", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 20, 80003, 0, 275, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 21, 80003, 0, 276, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 22, 80003, 0, 277, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 23, 80003, 0, 278, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 24, 80003, 0, 279, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 25, 80003, 0, 280, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 26, 80003, 0, 281, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 14, 0, 27, 80003, 0, 282, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 47, 80003, 0, 283, "準備はいいですかっ？\n…いっきますよー！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 54, 80003, 2, 284, "祝！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 39, 80003, 1, 285, "2.5周年！", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 48, 80003, 0, 286, "おめでとうございま～す☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 55, 80003, 2, 287, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 40, 80003, 1, 288, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 49, 80003, 0, 289, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 56, 80003, 2, 290, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 41, 80003, 1, 291, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 57, 80003, 2, 292, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 50, 80003, 0, 293, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 58, 80003, 2, 294, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 51, 80003, 0, 295, "記念日だから\n平気平気♪\nやばいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 59, 80003, 2, 296, "意味分かんないわよ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 42, 80003, 1, 297, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 43, 80003, 1, 298, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 52, 80003, 0, 299, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 60, 80003, 2, 300, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 44, 80003, 1, 301, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 53, 80003, 0, 302, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 54, 80003, 0, 303, "2.5周年\nありがとうございます！", "ペコリーヌ・コッコロ・キャル", 1, "105901,45,106001,61", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190804, 0, 1, 1, 60002, 1, 304, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 305, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 306, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 307, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 308, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60002, 2, 309, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 310, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 311, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 312, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 313, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60002, 2, 314, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 315, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 316, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 317, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 318, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 319, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 320, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60002, 2, 321, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 322, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 323, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 324, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 325, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 326, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60002, 2, 327, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60002, 2, 328, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", 0, "", 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190804, 0, 0, 2, 60002, 3, 329, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190804, 7, 0, 3, 60002, 3, 330, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 1, 0, 1, 80005, 4, 331, "ささ、シェフィさまはこちらへ…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 1, 0, 1, 80005, 0, 332, "コッコロさん、どこへ行くの…？\n…え？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 1, 0, 1, 80005, 3, 333, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 1, 0, 2, 80005, 4, 334, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 1, 0, 2, 80005, 0, 335, "ええと…\n二人とも何をしているの？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 1, 0, 1, 80005, 1, 336, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 1, 0, 3, 80005, 0, 337, "あの日…？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 1, 0, 2, 80005, 3, 338, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 1, 0, 3, 80005, 3, 339, "『プリンセスコネクト！\nRe:Dive』3.5周年！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 1, 0, 1, 80005, 2, 340, "やばいですね☆", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 1, 0, 2, 80005, 1, 341, "って、なにしれっと\n参加してんのよカリン！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 1, 0, 2, 80005, 2, 342, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 1, 0, 3, 80005, 2, 343, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 1, 0, 4, 80005, 3, 344, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 1, 0, 4, 80005, 0, 345, "えっ！？　ええとええと…！\nあ、あと14日…！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 2, 0, 5, 80005, 3, 346, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 2, 0, 3, 80005, 1, 347, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 2, 0, 4, 80005, 1, 348, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 2, 0, 6, 80005, 3, 349, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 2, 0, 5, 80005, 1, 350, "さすがでございます\nペコリーヌさま", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 3, 80005, 0, 351, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 2, 0, 5, 80005, 2, 352, "しゃなり、しゃなり…\nふふ、案外できるものね", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 2, 0, 7, 80005, 3, 353, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 2, 0, 6, 80005, 1, 354, "さ、次はキャルさまの\n番でございますよ", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 4, 80005, 0, 355, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 2, 0, 6, 80005, 2, 356, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 5, 80005, 0, 357, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 6, 80005, 0, 358, "いたたた…もぉお～！\nなんであたしがこんな目に～！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 2, 0, 7, 80005, 1, 359, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 2, 0, 7, 80005, 0, 360, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 8, 80005, 1, 361, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 8, 80005, 3, 362, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 9, 80005, 1, 363, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 3, 0, 7, 80005, 2, 364, "聖学祭…って、なにかしら？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 3, 0, 10, 80005, 1, 365, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 3, 0, 8, 80005, 0, 366, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 3, 0, 9, 80005, 0, 367, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 9, 80005, 3, 368, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 3, 0, 10, 80005, 0, 369, "いや、そのまんますぎでしょ…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 3, 0, 8, 80005, 2, 370, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 3, 0, 10, 80005, 3, 371, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 3, 0, 9, 80005, 2, 372, "えっ？　ええ…\nぎゅーっ…？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 11, 80005, 0, 373, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 4, 0, 10, 80005, 2, 374, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 12, 80005, 0, 375, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 4, 0, 11, 80005, 1, 376, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 13, 80005, 0, 377, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 4, 0, 11, 80005, 3, 378, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 14, 80005, 0, 379, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 15, 80005, 0, 380, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 4, 0, 11, 80005, 2, 381, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 4, 0, 12, 80005, 3, 382, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 4, 0, 12, 80005, 1, 383, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 4, 0, 16, 80005, 0, 384, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 4, 0, 12, 80005, 2, 385, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 5, 0, 13, 80005, 3, 386, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 5, 0, 14, 80005, 3, 387, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 5, 0, 15, 80005, 3, 388, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 17, 80005, 0, 389, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 18, 80005, 0, 390, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 5, 0, 13, 80005, 2, 391, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 5, 0, 13, 80005, 1, 392, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 5, 0, 16, 80005, 3, 393, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 5, 0, 19, 80005, 0, 394, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 5, 0, 17, 80005, 3, 395, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 5, 0, 18, 80005, 3, 396, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 5, 0, 19, 80005, 3, 397, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 6, 0, 4, 80005, 2, 398, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 6, 0, 5, 80005, 2, 399, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 20, 80005, 1, 400, "…えっ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 6, 0, 20, 80005, 3, 401, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 6, 0, 14, 80005, 0, 402, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 6, 0, 14, 80005, 4, 403, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 6, 0, 15, 80005, 0, 404, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 21, 80005, 1, 405, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 6, 0, 21, 80005, 3, 406, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 6, 0, 22, 80005, 1, 407, "ぶっ殺すぞ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 6, 0, 6, 80005, 2, 408, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 7, 0, 15, 80005, 1, 409, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 23, 80005, 0, 410, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 7, 0, 16, 80005, 2, 411, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 7, 0, 22, 80005, 3, 412, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 24, 80005, 0, 413, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 7, 0, 17, 80005, 2, 414, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 7, 0, 16, 80005, 1, 415, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 7, 0, 18, 80005, 2, 416, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 7, 0, 17, 80005, 1, 417, "もちろんでございます\n仲良くお勉強いたしましょう", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 25, 80005, 0, 418, "しれっと教育ママが\n生まれてる…？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 7, 0, 23, 80005, 3, 419, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 7, 0, 26, 80005, 0, 420, "え～～～～！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 8, 0, 27, 80005, 0, 421, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 8, 0, 19, 80005, 2, 422, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 18, 80005, 1, 423, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 8, 0, 24, 80005, 3, 424, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 8, 0, 20, 80005, 2, 425, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 8, 0, 28, 80005, 0, 426, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 8, 0, 19, 80005, 1, 427, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 8, 0, 21, 80005, 2, 428, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 8, 0, 29, 80005, 0, 429, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 8, 0, 30, 80005, 0, 430, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 8, 0, 25, 80005, 3, 431, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 8, 0, 31, 80005, 0, 432, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 22, 80005, 0, 433, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 23, 80005, 0, 434, "…えっ？\n生放送の意気込み！？\n突然なに！？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 24, 80005, 0, 435, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 25, 80005, 0, 436, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 26, 80005, 0, 437, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 27, 80005, 0, 438, "それから、それから…！\nぅ、ぅぅっ…！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 28, 80005, 0, 439, "―びえぇぇ～んっ！！！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 9, 0, 20, 80005, 1, 440, "どうされましたか、シェフィさま", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 29, 80005, 0, 441, "ううっ…ひっく…\nママッ…！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 9, 0, 21, 80005, 1, 442, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 9, 0, 22, 80005, 1, 443, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 9, 0, 30, 80005, 0, 444, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 23, 80005, 0, 445, "主さま\n3.5周年まであと５日\nでございます", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 24, 80005, 0, 446, "ですがその前に\n生放送がありますね", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 25, 80005, 0, 447, "…生放送に向けた\n意気込みでございますか？", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 26, 80005, 0, 448, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 27, 80005, 0, 449, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 28, 80005, 0, 450, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 29, 80005, 0, 451, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 30, 80005, 0, 452, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 10, 0, 31, 80005, 0, 453, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 11, 0, 26, 80005, 0, 454, "3.5周年まであと４日！\nやばいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 11, 0, 32, 80005, 1, 455, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 11, 0, 27, 80005, 0, 456, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 11, 0, 28, 80005, 0, 457, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 11, 0, 29, 80005, 0, 458, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 11, 0, 33, 80005, 1, 459, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 11, 0, 30, 80005, 0, 460, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 11, 0, 31, 80005, 0, 461, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 11, 0, 32, 80005, 0, 462, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 11, 0, 34, 80005, 1, 463, "だからあたしはやるなんて一言も…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 11, 0, 33, 80005, 0, 464, "オイッスー☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 11, 0, 35, 80005, 1, 465, "聞きなさいっての！！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 12, 0, 31, 80005, 2, 466, "3.5周年まであと３日…\nそして生放送は、明日…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 12, 0, 32, 80005, 2, 467, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 12, 0, 33, 80005, 2, 468, "おいっす！\n…こうじゃなかったわね\nええと確か…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 12, 0, 34, 80005, 2, 469, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 12, 0, 35, 80005, 2, 470, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 12, 0, 34, 80005, 3, 471, "やってますねシェフィちゃん！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 12, 0, 36, 80005, 0, 472, "なによ、考えることは\nみんな同じってこと？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 12, 0, 32, 80005, 1, 473, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 12, 0, 36, 80005, 2, 474, "みんな…もちろんよ！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 12, 0, 35, 80005, 3, 475, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 13, 0, 7, 80005, 2, 476, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 13, 0, 8, 80005, 2, 477, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 36, 80005, 3, 478, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 13, 0, 9, 80005, 2, 479, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 13, 0, 33, 80005, 4, 480, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 13, 0, 10, 80005, 2, 481, "さすが【美食殿】のママですね！\nそしてキャルさんは…", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 37, 80005, 1, 482, "大丈夫…大丈夫…\nあたしは大丈夫…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 13, 0, 11, 80005, 2, 483, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 13, 0, 37, 80005, 0, 484, "あうー？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 37, 80005, 4, 485, "あ…", "みんな", 1, "105901,34,106001,38,190801,12", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 13, 0, 39, 80005, 1, 486, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 13, 0, 35, 80005, 4, 487, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 13, 0, 38, 80005, 3, 488, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 13, 0, 13, 80005, 2, 489, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 14, 0, 38, 80005, 2, 490, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 14, 0, 39, 80005, 3, 491, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 14, 0, 40, 80005, 0, 492, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 14, 0, 36, 80005, 1, 493, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 14, 0, 39, 80005, 2, 494, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 14, 0, 40, 80005, 3, 495, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 14, 0, 37, 80005, 1, 496, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 14, 0, 41, 80005, 0, 497, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 14, 0, 40, 80005, 2, 498, "へ、へぇ…", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 14, 0, 41, 80005, 3, 499, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 14, 0, 41, 80005, 2, 500, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 14, 0, 42, 80005, 2, 501, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 42, 80005, 3, 502, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 42, 80005, 1, 503, "祝！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 38, 80005, 4, 504, "3.5周年！", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 15, 0, 43, 80005, 0, 505, "おめでとうございます！", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 15, 0, 14, 80005, 2, 506, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190801, 15, 0, 15, 80005, 2, 507, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 43, 80005, 3, 508, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 44, 80005, 3, 509, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 43, 80005, 1, 510, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106001, 15, 0, 44, 80005, 1, 511, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", "キャル", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 15, 0, 44, 80005, 0, 512, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106401, 15, 0, 45, 80005, 0, 513, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", "シェフィ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 39, 80005, 4, 514, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 40, 80005, 4, 515, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105901, 15, 0, 41, 80005, 4, 516, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", "コッコロ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 45, 80005, 3, 517, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", "ペコリーヌ", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105801, 15, 0, 46, 80005, 4, 518, "これからも\nよろしくおねがいします！", "みんな", 1, "105901,42,106001,45,106401,46,190801,16", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190804, 0, 1, 1, 60003, 1, 519, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "カリン", 0, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 520, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 521, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 522, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 523, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60003, 2, 524, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", "", 0, "", 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 525, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 526, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 527, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 528, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60003, 2, 529, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", "", 0, "", 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 530, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 531, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 532, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 533, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 534, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, 0, 60003, 2, 535, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", 0, "", 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, 0, 60003, 2, 536, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", "", 0, "", 3);
