INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 0, 190804, 1, 1, 0, 60000, "", 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60000, "", 2, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60000, "", 3, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 1, 60000, "", 4, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60000, "", 5, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60000, "", 6, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 2, 60000, "", 7, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60000, "", 8, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60000, "", 9, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60000, "", 10, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60000, "", 11, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 3, 60000, "", 12, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60000, "", 13, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60000, "", 14, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60000, "", 15, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60000, "", 16, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 4, 60000, "", 17, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 0, 190804, 3, 0, 0, 60000, "", 18, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 7, 190804, 3, 0, 0, 60000, "", 19, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ハツネ", 1, 101201, 0, 0, 0, 80001, "", 20, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ハツネ", 1, 101201, 0, 0, 0, 80001, "", 21, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シオリ", 1, 103801, 1, 0, 0, 80001, "", 22, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シオリ", 1, 103801, 1, 0, 0, 80001, "", 23, "来年も、\n一緒にいられたら…\nうれしいな", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キョウカ", 2, 103601, 0, 0, 0, 80001, "", 24, "あなたと一緒にいるようになって、\nもう1年ですか…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キョウカ", 2, 103601, 0, 0, 0, 80001, "", 25, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミミ", 2, 102001, 1, 0, 0, 80001, "", 26, "おにいちゃんとあってから、\n1年たったよ～！", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミミ", 2, 102001, 1, 0, 0, 80001, "", 27, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミソギ", 2, 100401, 2, 0, 0, 80001, "", 28, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミソギ", 2, 100401, 2, 0, 0, 80001, "", 29, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("マホ", 3, 101001, 0, 0, 0, 80001, "", 30, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("マホ", 3, 101001, 0, 0, 0, 80001, "", 31, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("イリヤ", 3, 104401, 1, 0, 0, 80001, "", 32, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("イリヤ", 3, 104401, 1, 0, 0, 80001, "", 33, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カオリ", 3, 101701, 2, 0, 0, 80001, "", 34, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カオリ", 3, 101701, 2, 0, 0, 80001, "", 35, "これからも、どうぞよろしく\nお願いするさ～！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("サレン", 4, 102801, 0, 0, 0, 80001, "", 36, "あんたと出会ってからもうそんなに\nたつのね～", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("サレン", 4, 102801, 0, 0, 0, 80001, "", 37, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("スズメ", 4, 102501, 1, 0, 0, 80001, "", 38, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("スズメ", 4, 102501, 1, 0, 0, 80001, "", 39, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("タマキ", 5, 104601, 0, 0, 0, 80001, "", 40, "振り返れば、今までいろんなことが\nあったにゃあ…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("タマキ", 5, 104601, 0, 0, 0, 80001, "", 41, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミフユ", 5, 104801, 1, 0, 0, 80001, "", 42, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミフユ", 5, 104801, 1, 0, 0, 80001, "", 43, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("アキノ", 6, 103201, 0, 0, 0, 80001, "", 44, "あなたさまと出会ってもう1年…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("アキノ", 6, 103201, 0, 0, 0, 80001, "", 45, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ユカリ", 6, 103401, 1, 0, 0, 80001, "", 46, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ユカリ", 6, 103401, 1, 0, 0, 80001, "", 47, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("クリスティーナ", 7, 107101, 0, 0, 0, 80001, "", 48, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("クリスティーナ", 7, 107101, 0, 0, 0, 80001, "", 49, "ともかくこれからも\n愉しませてくれよ☆", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ジュン", 7, 104701, 1, 0, 0, 80001, "", 50, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ジュン", 7, 104701, 1, 0, 0, 80001, "", 51, "見えないから分からないって？\nそれもそうだな…", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("トモ", 8, 103701, 0, 0, 0, 80001, "", 52, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("トモ", 8, 103701, 0, 0, 0, 80001, "", 53, "これからもよろしくね！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("マツリ", 8, 100501, 1, 0, 0, 80001, "", 54, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("マツリ", 8, 100501, 1, 0, 0, 80001, "", 55, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミヤコ", 9, 100701, 0, 0, 0, 80001, "", 56, "オマエと会って1年なの！\n全然成仏もできなかったし、", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミヤコ", 9, 100701, 0, 0, 0, 80001, "", 57, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シノブ", 9, 103101, 1, 0, 0, 80001, "", 58, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シノブ", 9, 103101, 1, 0, 0, 80001, "", 59, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("アンナ", 10, 100901, 0, 0, 0, 80001, "", 60, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("アンナ", 10, 100901, 0, 0, 0, 80001, "", 61, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ナナカ", 10, 101301, 1, 0, 0, 80001, "", 62, "君と出会ってもう1年だって！\n早いねー！", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ナナカ", 10, 101301, 1, 0, 0, 80001, "", 63, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミツキ", 11, 105101, 0, 0, 0, 80001, "", 64, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ミツキ", 11, 105101, 0, 0, 0, 80001, "", 65, "これからも、いろんなところを\n調べさせてね？", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ルカ", 11, 105601, 1, 0, 0, 80001, "", 66, "お前さんと出会ってもう1年か\n助太刀するつもりが", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ルカ", 11, 105601, 1, 0, 0, 80001, "", 67, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("エリコ", 11, 102701, 2, 0, 0, 80001, "", 68, "早いものですわね、あなた様と\nお会いして、もう1年…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("エリコ", 11, 102701, 2, 0, 0, 80001, "", 69, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("アヤネ", 12, 102301, 0, 0, 0, 80001, "", 70, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("アヤネ", 12, 102301, 0, 0, 0, 80001, "", 71, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("チカ", 12, 104201, 1, 0, 0, 80001, "", 72, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("チカ", 12, 104201, 1, 0, 0, 80001, "", 73, "私のことを見守ってください\nできることなら、ずっと…", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("クルミ", 12, 102101, 2, 0, 0, 80001, "", 74, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("クルミ", 12, 102101, 2, 0, 0, 80001, "", 75, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シズル", 13, 104901, 0, 0, 0, 80001, "", 76, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シズル", 13, 104901, 0, 0, 0, 80001, "", 77, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("リノ", 13, 101101, 1, 0, 0, 80001, "", 78, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("リノ", 13, 101101, 1, 0, 0, 80001, "", 79, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ユイ", 14, 100201, 0, 0, 0, 80001, "", 80, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ユイ", 14, 100201, 0, 0, 0, 80001, "", 81, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ヒヨリ", 14, 100101, 1, 0, 0, 80001, "", 82, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ヒヨリ", 14, 100101, 1, 0, 0, 80001, "", 83, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("レイ", 14, 100301, 2, 0, 0, 80001, "", 84, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("レイ", 14, 100301, 2, 0, 0, 80001, "", 85, "キミに背中を預けられる日が、\n早くきますように", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80001, "", 86, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80001, "", 87, "これからもずっと、覚えていて\nくださいね！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 1, 0, 0, 80001, "", 88, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 1, 0, 0, 80001, "", 89, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80001, "", 90, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80001, "", 91, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("スズメ", 0, 112101, 1, 1, 0, 60001, "", 92, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60001, "", 93, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60001, "", 94, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60001, "", 95, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60001, "", 96, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 1, 60001, "", 97, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60001, "", 98, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60001, "", 99, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60001, "", 100, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60001, "", 101, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 2, 60001, "", 102, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60001, "", 103, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60001, "", 104, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60001, "", 105, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60001, "", 106, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60001, "", 107, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60001, "", 108, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 3, 60001, "", 109, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60001, "", 110, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60001, "", 111, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60001, "", 112, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60001, "", 113, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60001, "", 114, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60001, "", 115, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 4, 60001, "", 116, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("スズメ", 0, 112101, 3, 0, 0, 60001, "", 117, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("スズメ", 7, 112101, 3, 0, 0, 60001, "", 118, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 1, 105801, 0, 0, 0, 80003, "", 119, "ヒューヒュー♪\nドンドンパフパフー♪", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 1, 105901, 1, 0, 0, 80003, "", 120, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 1, 105901, 1, 0, 0, 80003, "", 121, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 1, 106001, 2, 0, 0, 80003, "", 122, "え？　ちょっと\nいきなりなにっ？", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 1, 106001, 2, 0, 0, 80003, "", 123, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 1, 105801, 0, 0, 0, 80003, "", 124, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 1, 105801, 0, 0, 0, 80003, "", 125, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 1, 105901, 1, 0, 0, 80003, "", 126, "ですからその日に向けて\n今から気分を盛り上げているのです", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 1, 106001, 2, 0, 0, 80003, "", 127, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 1, 105901, 1, 0, 0, 80003, "", 128, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 1, 105801, 0, 0, 0, 80003, "", 129, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 1, 106001, 2, 0, 0, 80003, "", 130, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 1, 105901, 1, 0, 0, 80003, "", 131, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80003, "", 132, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 2, 190801, 1, 0, 0, 80003, "", 133, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80003, "", 134, "げっ…カリンじゃない\nなんでもないわよ！", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 2, 190801, 1, 0, 0, 80003, "", 135, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80003, "", 136, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 2, 190801, 1, 0, 0, 80003, "", 137, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80003, "", 138, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80003, "", 139, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 2, 190801, 1, 0, 0, 80003, "", 140, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80003, "", 141, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 2, 190801, 1, 0, 0, 80003, "", 142, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80003, "", 143, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 0, 0, 0, 80003, "", 144, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 1, 0, 0, 80003, "", 145, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 0, 0, 0, 80003, "", 146, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 0, 0, 0, 80003, "", 147, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 1, 0, 0, 80003, "", 148, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 1, 0, 0, 80003, "", 149, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 0, 0, 0, 80003, "", 150, "あとは、牧場に素敵な温泉が\n出来たとか…", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 1, 0, 0, 80003, "", 151, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 0, 0, 0, 80003, "", 152, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 1, 0, 0, 80003, "", 153, "えへへ、ありがとうございます\nコッコロちゃん！", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 0, 0, 0, 80003, "", 154, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 1, 0, 0, 80003, "", 155, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 4, 105801, 0, 0, 0, 80003, "", 156, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 1, 0, 0, 80003, "", 157, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 12, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 4, 105801, 0, 0, 0, 80003, "", 158, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 12, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 1, 0, 0, 80003, "", 159, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 4, 105801, 0, 0, 0, 80003, "", 160, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 1, 0, 0, 80003, "", 161, "…積極的？\nなんの話よ？", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 4, 105801, 0, 0, 0, 80003, "", 162, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 4, 105801, 0, 0, 0, 80003, "", 163, "ムーサの町で\nきゃるきゃる～ん☆　って！", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 1, 0, 0, 80003, "", 164, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 4, 105801, 0, 0, 0, 80003, "", 165, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 16, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 1, 0, 0, 80003, "", 166, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 16, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 1, 0, 0, 80003, "", 167, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 17, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80003, "", 168, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 18, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 5, 105901, 1, 0, 0, 80003, "", 169, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 12, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80003, "", 170, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 19, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 5, 105901, 1, 0, 0, 80003, "", 171, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80003, "", 172, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 20, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 5, 105901, 1, 0, 0, 80003, "", 173, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80003, "", 174, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 21, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 5, 105901, 1, 0, 0, 80003, "", 175, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80003, "", 176, "かくし芸？", 22, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 5, 105901, 1, 0, 0, 80003, "", 177, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 16, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80003, "", 178, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 23, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 6, 105801, 0, 0, 0, 80003, "", 179, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 17, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 2, 0, 0, 80003, "", 180, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 24, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 6, 105901, 1, 0, 0, 80003, "", 181, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 17, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 6, 105801, 0, 0, 0, 80003, "", 182, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 18, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 2, 0, 0, 80003, "", 183, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 25, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 6, 105901, 1, 0, 0, 80003, "", 184, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 18, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 2, 0, 0, 80003, "", 185, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 26, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 2, 0, 0, 80003, "", 186, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 27, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 2, 0, 0, 80003, "", 187, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 28, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 6, 105801, 0, 0, 0, 80003, "", 188, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 19, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 6, 105901, 1, 0, 0, 80003, "", 189, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 19, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 2, 0, 0, 80003, "", 190, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 29, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 191, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 30, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 192, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 31, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 193, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 32, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 194, "あー\nあー\n…声、これで大丈夫かしら？", 33, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 195, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 34, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 196, "オイッスー☆", 35, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 197, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 36, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 198, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 37, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80003, "", 199, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 38, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 200, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 20, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 201, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 21, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 202, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 22, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 203, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 23, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 204, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 24, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 205, "オイッスー☆", 25, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 206, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 26, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 207, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 27, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 0, 0, 0, 80003, "", 208, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 28, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 209, "オイッスー☆", 20, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 210, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 21, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 211, "あと６日で2.5周年ですか…", 22, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 212, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 23, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 213, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 24, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 214, "オイッスー☆", 25, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 215, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 26, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 216, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 27, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 217, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 28, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 218, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 29, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 9, 105801, 0, 0, 0, 80003, "", 219, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 30, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 10, 105801, 0, 0, 0, 80003, "", 220, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 31, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 1, 0, 0, 80003, "", 221, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 29, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 10, 105801, 0, 0, 0, 80003, "", 222, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 32, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 10, 106001, 2, 0, 0, 80003, "", 223, "…………", 39, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 1, 0, 0, 80003, "", 224, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 30, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 10, 106001, 2, 0, 0, 80003, "", 225, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 40, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 10, 105801, 0, 0, 0, 80003, "", 226, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 33, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 10, 106001, 2, 0, 0, 80003, "", 227, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 41, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 10, 105801, 0, 0, 0, 80003, "", 228, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 34, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 10, 105801, 0, 0, 0, 80003, "", 229, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 35, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 10, 106001, 2, 0, 0, 80003, "", 230, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 42, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 1, 0, 0, 80003, "", 231, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 31, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 10, 106001, 2, 0, 0, 80003, "", 232, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 43, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 10, 105801, 0, 0, 0, 80003, "", 233, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 36, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 234, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 235, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 236, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 237, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 238, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 239, "次にコッコロさんですが\nこちらは発声練習をしています", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 240, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 12, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 241, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 242, "あれ？\nもしかして緊張で気を失って…？", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 243, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 244, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 16, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 11, 190801, 0, 0, 0, 80003, "", 245, "それでは\nまた明日お会いしましょう♪", 17, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 12, 106001, 0, 0, 0, 80003, "", 246, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 44, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 12, 105801, 2, 0, 0, 80003, "", 247, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 37, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 12, 105901, 1, 0, 0, 80003, "", 248, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 32, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 12, 106001, 0, 0, 0, 80003, "", 249, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 45, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 12, 105801, 2, 0, 0, 80003, "", 250, "ひどいですよぉキャルちゃ～ん！", 38, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 12, 105901, 1, 0, 0, 80003, "", 251, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 33, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 12, 105801, 2, 0, 0, 80003, "", 252, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 39, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 12, 106001, 0, 0, 0, 80003, "", 253, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 46, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 12, 105901, 1, 0, 0, 80003, "", 254, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 34, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 12, 105801, 2, 0, 0, 80003, "", 255, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 40, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 13, 105801, 0, 0, 0, 80003, "", 256, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 41, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 2, 0, 0, 80003, "", 257, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 47, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 13, 105801, 0, 0, 0, 80003, "", 258, "忘れてきちゃいました☆", 42, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 2, 0, 0, 80003, "", 259, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 48, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 13, 105901, 1, 0, 0, 80003, "", 260, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 35, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 2, 0, 0, 80003, "", 261, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 49, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 13, 105801, 0, 0, 0, 80003, "", 262, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 43, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 2, 0, 0, 80003, "", 263, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 50, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 13, 105901, 1, 0, 0, 80003, "", 264, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 36, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 2, 0, 0, 80003, "", 265, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 51, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 13, 105901, 1, 0, 0, 80003, "", 266, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 37, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 2, 0, 0, 80003, "", 267, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 52, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 13, 105801, 0, 0, 0, 80003, "", 268, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 44, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 13, 105901, 1, 0, 0, 80003, "", 269, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 38, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 13, 105801, 0, 0, 0, 80003, "", 270, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 45, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 2, 0, 0, 80003, "", 271, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 53, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 13, 105801, 0, 0, 0, 80003, "", 272, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 46, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 273, "こんにちは\n【ギルド管理協会】のカリンです", 18, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 274, "昨日の生放送は\nいかがだったでしょうか？", 19, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 275, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 20, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 276, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 21, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 277, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 22, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 278, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 23, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 279, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 24, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 280, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 25, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 281, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 26, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 14, 190801, 0, 0, 0, 80003, "", 282, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 27, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80003, "", 283, "準備はいいですかっ？\n…いっきますよー！", 47, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80003, "", 284, "祝！", 54, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 1, 0, 0, 80003, "", 285, "2.5周年！", 39, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80003, "", 286, "おめでとうございま～す☆", 48, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80003, "", 287, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 55, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 1, 0, 0, 80003, "", 288, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 40, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80003, "", 289, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 49, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80003, "", 290, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 56, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 1, 0, 0, 80003, "", 291, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 41, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80003, "", 292, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 57, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80003, "", 293, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 50, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80003, "", 294, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 58, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80003, "", 295, "記念日だから\n平気平気♪\nやばいですね☆", 51, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80003, "", 296, "意味分かんないわよ！", 59, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 1, 0, 0, 80003, "", 297, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 42, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 1, 0, 0, 80003, "", 298, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 43, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80003, "", 299, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 52, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 2, 0, 0, 80003, "", 300, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 60, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 1, 0, 0, 80003, "", 301, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 44, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 0, 0, 0, 80003, "", 302, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 53, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ・コッコロ・キャル", 15, 105801, 0, 0, 0, 80003, "105901,45,106001,61", 303, "2.5周年\nありがとうございます！", 54, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 0, 190804, 1, 1, 0, 60002, "", 304, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60002, "", 305, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60002, "", 306, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60002, "", 307, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60002, "", 308, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 1, 60002, "", 309, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60002, "", 310, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60002, "", 311, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60002, "", 312, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60002, "", 313, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 2, 60002, "", 314, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60002, "", 315, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60002, "", 316, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60002, "", 317, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60002, "", 318, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60002, "", 319, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60002, "", 320, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 3, 60002, "", 321, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60002, "", 322, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60002, "", 323, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60002, "", 324, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60002, "", 325, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60002, "", 326, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60002, "", 327, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 4, 60002, "", 328, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 0, 190804, 3, 0, 0, 60002, "", 329, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 7, 190804, 3, 0, 0, 60002, "", 330, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 1, 105901, 4, 0, 0, 80005, "", 331, "ささ、シェフィさまはこちらへ…", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 1, 106401, 0, 0, 0, 80005, "", 332, "コッコロさん、どこへ行くの…？\n…え？", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 1, 105801, 3, 0, 0, 80005, "", 333, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 1, 105901, 4, 0, 0, 80005, "", 334, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 1, 106401, 0, 0, 0, 80005, "", 335, "ええと…\n二人とも何をしているの？", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 1, 106001, 1, 0, 0, 80005, "", 336, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 1, 106401, 0, 0, 0, 80005, "", 337, "あの日…？", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 1, 105801, 3, 0, 0, 80005, "", 338, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 1, 105801, 3, 0, 0, 80005, "", 339, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 1, 190801, 2, 0, 0, 80005, "", 340, "やばいですね☆", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 1, 106001, 1, 0, 0, 80005, "", 341, "って、なにしれっと\n参加してんのよカリン！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 1, 190801, 2, 0, 0, 80005, "", 342, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 1, 190801, 2, 0, 0, 80005, "", 343, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 1, 105801, 3, 0, 0, 80005, "", 344, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 1, 106401, 0, 0, 0, 80005, "", 345, "えっ！？　ええとええと…！\nあ、あと14日…！", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 2, 105801, 3, 0, 0, 80005, "", 346, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 2, 105901, 1, 0, 0, 80005, "", 347, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 2, 105901, 1, 0, 0, 80005, "", 348, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 2, 105801, 3, 0, 0, 80005, "", 349, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 2, 105901, 1, 0, 0, 80005, "", 350, "さすがでございます\nペコリーヌさま", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80005, "", 351, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 3, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 2, 106401, 2, 0, 0, 80005, "", 352, "しゃなり、しゃなり…\nふふ、案外できるものね", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 2, 105801, 3, 0, 0, 80005, "", 353, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 2, 105901, 1, 0, 0, 80005, "", 354, "さ、次はキャルさまの\n番でございますよ", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80005, "", 355, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 2, 106401, 2, 0, 0, 80005, "", 356, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80005, "", 357, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80005, "", 358, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 2, 105901, 1, 0, 0, 80005, "", 359, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 2, 106001, 0, 0, 0, 80005, "", 360, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 1, 0, 0, 80005, "", 361, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 3, 0, 0, 80005, "", 362, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 1, 0, 0, 80005, "", 363, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 3, 106401, 2, 0, 0, 80005, "", 364, "聖学祭…って、なにかしら？", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 3, 105901, 1, 0, 0, 80005, "", 365, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 3, 106001, 0, 0, 0, 80005, "", 366, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 3, 106001, 0, 0, 0, 80005, "", 367, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 3, 0, 0, 80005, "", 368, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 3, 106001, 0, 0, 0, 80005, "", 369, "いや、そのまんますぎでしょ…", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 3, 106401, 2, 0, 0, 80005, "", 370, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 3, 105801, 3, 0, 0, 80005, "", 371, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 3, 106401, 2, 0, 0, 80005, "", 372, "えっ？　ええ…\nぎゅーっ…？", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 0, 0, 0, 80005, "", 373, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 4, 106401, 2, 0, 0, 80005, "", 374, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 0, 0, 0, 80005, "", 375, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 12, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 4, 105901, 1, 0, 0, 80005, "", 376, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 0, 0, 0, 80005, "", 377, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 4, 105801, 3, 0, 0, 80005, "", 378, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 0, 0, 0, 80005, "", 379, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 0, 0, 0, 80005, "", 380, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 4, 106401, 2, 0, 0, 80005, "", 381, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 4, 105801, 3, 0, 0, 80005, "", 382, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 12, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 4, 105901, 1, 0, 0, 80005, "", 383, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 12, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 4, 106001, 0, 0, 0, 80005, "", 384, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 16, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 4, 106401, 2, 0, 0, 80005, "", 385, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 12, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 5, 105801, 3, 0, 0, 80005, "", 386, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 5, 105801, 3, 0, 0, 80005, "", 387, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 5, 105801, 3, 0, 0, 80005, "", 388, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80005, "", 389, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 17, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80005, "", 390, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 18, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 5, 106401, 2, 0, 0, 80005, "", 391, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 5, 105901, 1, 0, 0, 80005, "", 392, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 5, 105801, 3, 0, 0, 80005, "", 393, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 16, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 5, 106001, 0, 0, 0, 80005, "", 394, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 19, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 5, 105801, 3, 0, 0, 80005, "", 395, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 17, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 5, 105801, 3, 0, 0, 80005, "", 396, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 18, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 5, 105801, 3, 0, 0, 80005, "", 397, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 19, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 6, 190801, 2, 0, 0, 80005, "", 398, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 4, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 6, 190801, 2, 0, 0, 80005, "", 399, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 5, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 1, 0, 0, 80005, "", 400, "…えっ！？", 20, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 6, 105801, 3, 0, 0, 80005, "", 401, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 20, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 6, 106401, 0, 0, 0, 80005, "", 402, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 6, 105901, 4, 0, 0, 80005, "", 403, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 6, 106401, 0, 0, 0, 80005, "", 404, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 1, 0, 0, 80005, "", 405, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 21, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 6, 105801, 3, 0, 0, 80005, "", 406, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 21, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 6, 106001, 1, 0, 0, 80005, "", 407, "ぶっ殺すぞ！？", 22, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 6, 190801, 2, 0, 0, 80005, "", 408, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 6, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 7, 105901, 1, 0, 0, 80005, "", 409, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80005, "", 410, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 23, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 7, 106401, 2, 0, 0, 80005, "", 411, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 16, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 7, 105801, 3, 0, 0, 80005, "", 412, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 22, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80005, "", 413, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 24, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 7, 106401, 2, 0, 0, 80005, "", 414, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 17, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 7, 105901, 1, 0, 0, 80005, "", 415, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 16, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 7, 106401, 2, 0, 0, 80005, "", 416, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 18, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 7, 105901, 1, 0, 0, 80005, "", 417, "もちろんでございます\n仲良くお勉強いたしましょう", 17, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80005, "", 418, "しれっと教育ママが\n生まれてる…？", 25, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 7, 105801, 3, 0, 0, 80005, "", 419, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 23, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 7, 106001, 0, 0, 0, 80005, "", 420, "え～～～～！？", 26, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 8, 106001, 0, 0, 0, 80005, "", 421, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 27, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 8, 106401, 2, 0, 0, 80005, "", 422, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 19, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 1, 0, 0, 80005, "", 423, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 18, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 8, 105801, 3, 0, 0, 80005, "", 424, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 24, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 8, 106401, 2, 0, 0, 80005, "", 425, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 20, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 8, 106001, 0, 0, 0, 80005, "", 426, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 28, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 8, 105901, 1, 0, 0, 80005, "", 427, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 19, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 8, 106401, 2, 0, 0, 80005, "", 428, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 21, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 8, 106001, 0, 0, 0, 80005, "", 429, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 29, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 8, 106001, 0, 0, 0, 80005, "", 430, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 30, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 8, 105801, 3, 0, 0, 80005, "", 431, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 25, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 8, 106001, 0, 0, 0, 80005, "", 432, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 31, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 433, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 22, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 434, "…えっ？\n生放送の意気込み！？\n突然なに！？", 23, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 435, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 24, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 436, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 25, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 437, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 26, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 438, "それから、それから…！\nぅ、ぅぅっ…！", 27, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 439, "―びえぇぇ～んっ！！！", 28, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 9, 105901, 1, 0, 0, 80005, "", 440, "どうされましたか、シェフィさま", 20, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 441, "ううっ…ひっく…\nママッ…！", 29, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 9, 105901, 1, 0, 0, 80005, "", 442, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 21, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 9, 105901, 1, 0, 0, 80005, "", 443, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 22, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 9, 106401, 0, 0, 0, 80005, "", 444, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 30, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 445, "主さま\n3.5周年まであと５日\nでございます", 23, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 446, "ですがその前に\n生放送がありますね", 24, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 447, "…生放送に向けた\n意気込みでございますか？", 25, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 448, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 26, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 449, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 27, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 450, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 28, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 451, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 29, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 452, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 30, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 10, 105901, 0, 0, 0, 80005, "", 453, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 31, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 11, 105801, 0, 0, 0, 80005, "", 454, "3.5周年まであと４日！\nやばいですね☆", 26, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 11, 106001, 1, 0, 0, 80005, "", 455, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 32, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 11, 105801, 0, 0, 0, 80005, "", 456, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 27, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 11, 105801, 0, 0, 0, 80005, "", 457, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 28, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 11, 105801, 0, 0, 0, 80005, "", 458, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 29, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 11, 106001, 1, 0, 0, 80005, "", 459, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 33, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 11, 105801, 0, 0, 0, 80005, "", 460, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 30, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 11, 105801, 0, 0, 0, 80005, "", 461, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 31, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 11, 105801, 0, 0, 0, 80005, "", 462, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 32, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 11, 106001, 1, 0, 0, 80005, "", 463, "だからあたしはやるなんて一言も…", 34, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 11, 105801, 0, 0, 0, 80005, "", 464, "オイッスー☆", 33, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 11, 106001, 1, 0, 0, 80005, "", 465, "聞きなさいっての！！", 35, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 12, 106401, 2, 0, 0, 80005, "", 466, "3.5周年まであと３日…\nそして生放送は、明日…", 31, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 12, 106401, 2, 0, 0, 80005, "", 467, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 32, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 12, 106401, 2, 0, 0, 80005, "", 468, "おいっす！\n…こうじゃなかったわね\nええと確か…", 33, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 12, 106401, 2, 0, 0, 80005, "", 469, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 34, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 12, 106401, 2, 0, 0, 80005, "", 470, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 35, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 12, 105801, 3, 0, 0, 80005, "", 471, "やってますねシェフィちゃん！", 34, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 12, 106001, 0, 0, 0, 80005, "", 472, "なによ、考えることは\nみんな同じってこと？", 36, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 12, 105901, 1, 0, 0, 80005, "", 473, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 32, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 12, 106401, 2, 0, 0, 80005, "", 474, "みんな…もちろんよ！", 36, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 12, 105801, 3, 0, 0, 80005, "", 475, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 35, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 13, 190801, 2, 0, 0, 80005, "", 476, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 7, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 13, 190801, 2, 0, 0, 80005, "", 477, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 8, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 13, 105801, 3, 0, 0, 80005, "", 478, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 36, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 13, 190801, 2, 0, 0, 80005, "", 479, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 9, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 13, 105901, 4, 0, 0, 80005, "", 480, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 33, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 13, 190801, 2, 0, 0, 80005, "", 481, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 10, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 1, 0, 0, 80005, "", 482, "大丈夫…大丈夫…\nあたしは大丈夫…", 37, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 13, 190801, 2, 0, 0, 80005, "", 483, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 11, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 13, 106401, 0, 0, 0, 80005, "", 484, "あうー？", 37, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("みんな", 13, 105801, 4, 0, 0, 80005, "105901,34,106001,38,190801,12", 485, "あ…", 37, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 13, 106001, 1, 0, 0, 80005, "", 486, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 39, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 13, 105901, 4, 0, 0, 80005, "", 487, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 35, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 13, 105801, 3, 0, 0, 80005, "", 488, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 38, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 13, 190801, 2, 0, 0, 80005, "", 489, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 13, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 14, 106401, 2, 0, 0, 80005, "", 490, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 38, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 14, 105801, 3, 0, 0, 80005, "", 491, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 39, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 14, 106001, 0, 0, 0, 80005, "", 492, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 40, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 14, 105901, 1, 0, 0, 80005, "", 493, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 36, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 14, 106401, 2, 0, 0, 80005, "", 494, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 39, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 14, 105801, 3, 0, 0, 80005, "", 495, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 40, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 14, 105901, 1, 0, 0, 80005, "", 496, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 37, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 14, 106001, 0, 0, 0, 80005, "", 497, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 41, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 14, 106401, 2, 0, 0, 80005, "", 498, "へ、へぇ…", 40, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 14, 105801, 3, 0, 0, 80005, "", 499, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 41, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 14, 106401, 2, 0, 0, 80005, "", 500, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 41, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 14, 106401, 2, 0, 0, 80005, "", 501, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 42, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 3, 0, 0, 80005, "", 502, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 42, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 1, 0, 0, 80005, "", 503, "祝！", 42, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 4, 0, 0, 80005, "", 504, "3.5周年！", 38, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 15, 106401, 0, 0, 0, 80005, "", 505, "おめでとうございます！", 43, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 15, 190801, 2, 0, 0, 80005, "", 506, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 14, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 15, 190801, 2, 0, 0, 80005, "", 507, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 15, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 3, 0, 0, 80005, "", 508, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 43, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 3, 0, 0, 80005, "", 509, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 44, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 1, 0, 0, 80005, "", 510, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 43, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("キャル", 15, 106001, 1, 0, 0, 80005, "", 511, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 44, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 15, 106401, 0, 0, 0, 80005, "", 512, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 44, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("シェフィ", 15, 106401, 0, 0, 0, 80005, "", 513, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 45, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 4, 0, 0, 80005, "", 514, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 39, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 4, 0, 0, 80005, "", 515, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 40, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("コッコロ", 15, 105901, 4, 0, 0, 80005, "", 516, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 41, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("ペコリーヌ", 15, 105801, 3, 0, 0, 80005, "", 517, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 45, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("みんな", 15, 105801, 4, 0, 0, 80005, "105901,42,106001,45,106401,46,190801,16", 518, "これからも\nよろしくおねがいします！", 46, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("カリン", 0, 190804, 1, 1, 0, 60003, "", 519, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60003, "", 520, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60003, "", 521, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60003, "", 522, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 1, 60003, "", 523, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 1, 60003, "", 524, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60003, "", 525, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60003, "", 526, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60003, "", 527, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 2, 60003, "", 528, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 2, 60003, "", 529, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60003, "", 530, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60003, "", 531, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60003, "", 532, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60003, "", 533, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60003, "", 534, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 3, 60003, "", 535, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 2, 3, 60003, "", 536, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60003, "", 537, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60003, "", 538, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60003, "", 539, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES ("", 0, 0, 2, 1, 4, 60003, "", 540, "今日の運勢は小吉！　難しい頼み事をされるかもしれません！　　うまくやれればきっと成長につながります、挑戦してみましょう！", 0, 0);
