INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 1, 0, "", 0, 190804, 0, 1, "カリン", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 2, 0, 0, "", 0, 0, 1, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 2, 0, 0, "", 0, 0, 1, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 2, 0, 0, "", 0, 0, 1, 2, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 0, 0, "", 0, 0, 2, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 0, 0, "", 0, 0, 2, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 0, "", 0, 0, 2, 2, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 2, 0, 0, "", 0, 0, 3, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 2, 0, 0, "", 0, 0, 3, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 2, 0, 0, "", 0, 0, 3, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 2, 0, 0, "", 0, 0, 3, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 2, 0, 0, "", 0, 0, 3, 2, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 2, 0, 0, "", 0, 0, 4, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 2, 0, 0, "", 0, 0, 4, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 2, 0, 0, "", 0, 0, 4, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (16, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 2, 0, 0, "", 0, 0, 4, 1, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (17, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 0, "", 0, 0, 4, 2, "", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (18, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 3, 2, 0, "", 0, 190804, 0, 0, "カリン", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (19, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, 3, 0, "", 7, 190804, 0, 0, "カリン", 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (20, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 1, 0, "", 1, 101201, 0, 0, "ハツネ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (21, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 2, 0, "", 1, 101201, 0, 0, "ハツネ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (22, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 1, 1, 0, "", 1, 103801, 0, 0, "シオリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (23, "来年も、\n一緒にいられたら…\nうれしいな", 1, 2, 0, "", 1, 103801, 0, 0, "シオリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (24, "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 1, 0, "", 2, 103601, 0, 0, "キョウカ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (25, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 2, 0, "", 2, 103601, 0, 0, "キョウカ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (26, "おにいちゃんとあってから、\n1年たったよ～！", 1, 1, 0, "", 2, 102001, 0, 0, "ミミ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (27, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 1, 2, 0, "", 2, 102001, 0, 0, "ミミ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (28, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 2, 1, 0, "", 2, 100401, 0, 0, "ミソギ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (29, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 2, 2, 0, "", 2, 100401, 0, 0, "ミソギ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (30, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 1, 0, "", 3, 101001, 0, 0, "マホ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (31, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 2, 0, "", 3, 101001, 0, 0, "マホ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (32, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 1, 1, 0, "", 3, 104401, 0, 0, "イリヤ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (33, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 1, 2, 0, "", 3, 104401, 0, 0, "イリヤ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (34, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 2, 1, 0, "", 3, 101701, 0, 0, "カオリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (35, "これからも、どうぞよろしく\nお願いするさ～！", 2, 2, 0, "", 3, 101701, 0, 0, "カオリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (36, "あんたと出会ってからもうそんなに\nたつのね～", 0, 1, 0, "", 4, 102801, 0, 0, "サレン", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (37, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 2, 0, "", 4, 102801, 0, 0, "サレン", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (38, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 1, 1, 0, "", 4, 102501, 0, 0, "スズメ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (39, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 1, 2, 0, "", 4, 102501, 0, 0, "スズメ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (40, "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 1, 0, "", 5, 104601, 0, 0, "タマキ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (41, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 2, 0, "", 5, 104601, 0, 0, "タマキ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (42, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 1, 1, 0, "", 5, 104801, 0, 0, "ミフユ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (43, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 1, 2, 0, "", 5, 104801, 0, 0, "ミフユ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (44, "あなたさまと出会ってもう1年…", 0, 1, 0, "", 6, 103201, 0, 0, "アキノ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (45, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 2, 0, "", 6, 103201, 0, 0, "アキノ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (46, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 1, 1, 0, "", 6, 103401, 0, 0, "ユカリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (47, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 1, 2, 0, "", 6, 103401, 0, 0, "ユカリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (48, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 1, 0, "", 7, 107101, 0, 0, "クリスティーナ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (49, "ともかくこれからも\n愉しませてくれよ☆", 0, 2, 0, "", 7, 107101, 0, 0, "クリスティーナ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (50, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 1, 1, 0, "", 7, 104701, 0, 0, "ジュン", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (51, "見えないから分からないって？\nそれもそうだな…", 1, 2, 0, "", 7, 104701, 0, 0, "ジュン", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (52, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 1, 0, "", 8, 103701, 0, 0, "トモ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (53, "これからもよろしくね！", 0, 2, 0, "", 8, 103701, 0, 0, "トモ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (54, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 1, 1, 0, "", 8, 100501, 0, 0, "マツリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (55, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 1, 2, 0, "", 8, 100501, 0, 0, "マツリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (56, "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 1, 0, "", 9, 100701, 0, 0, "ミヤコ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (57, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 2, 0, "", 9, 100701, 0, 0, "ミヤコ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (58, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 1, 1, 0, "", 9, 103101, 0, 0, "シノブ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (59, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 1, 2, 0, "", 9, 103101, 0, 0, "シノブ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (60, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 1, 0, "", 10, 100901, 0, 0, "アンナ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (61, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 2, 0, "", 10, 100901, 0, 0, "アンナ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (62, "君と出会ってもう1年だって！\n早いねー！", 1, 1, 0, "", 10, 101301, 0, 0, "ナナカ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (63, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 1, 2, 0, "", 10, 101301, 0, 0, "ナナカ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (64, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 1, 0, "", 11, 105101, 0, 0, "ミツキ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (65, "これからも、いろんなところを\n調べさせてね？", 0, 2, 0, "", 11, 105101, 0, 0, "ミツキ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (66, "お前さんと出会ってもう1年か\n助太刀するつもりが", 1, 1, 0, "", 11, 105601, 0, 0, "ルカ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (67, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 1, 2, 0, "", 11, 105601, 0, 0, "ルカ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (68, "早いものですわね、あなた様と\nお会いして、もう1年…", 2, 1, 0, "", 11, 102701, 0, 0, "エリコ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (69, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 2, 2, 0, "", 11, 102701, 0, 0, "エリコ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (70, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 1, 0, "", 12, 102301, 0, 0, "アヤネ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (71, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 2, 0, "", 12, 102301, 0, 0, "アヤネ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (72, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 1, 1, 0, "", 12, 104201, 0, 0, "チカ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (73, "私のことを見守ってください\nできることなら、ずっと…", 1, 2, 0, "", 12, 104201, 0, 0, "チカ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (74, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 2, 1, 0, "", 12, 102101, 0, 0, "クルミ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (75, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 2, 2, 0, "", 12, 102101, 0, 0, "クルミ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (76, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 1, 0, "", 13, 104901, 0, 0, "シズル", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (77, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 2, 0, "", 13, 104901, 0, 0, "シズル", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (78, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 1, 1, 0, "", 13, 101101, 0, 0, "リノ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (79, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 1, 2, 0, "", 13, 101101, 0, 0, "リノ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (80, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 1, 0, "", 14, 100201, 0, 0, "ユイ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (81, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 2, 0, "", 14, 100201, 0, 0, "ユイ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (82, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 1, 1, 0, "", 14, 100101, 0, 0, "ヒヨリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (83, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 1, 2, 0, "", 14, 100101, 0, 0, "ヒヨリ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (84, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 2, 1, 0, "", 14, 100301, 0, 0, "レイ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (85, "キミに背中を預けられる日が、\n早くきますように", 2, 2, 0, "", 14, 100301, 0, 0, "レイ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (86, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 1, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (87, "これからもずっと、覚えていて\nくださいね！", 0, 2, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (88, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 1, 1, 0, "", 15, 105901, 0, 0, "コッコロ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (89, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 1, 2, 0, "", 15, 105901, 0, 0, "コッコロ", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (90, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 2, 1, 0, "", 15, 106001, 0, 0, "キャル", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (91, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 2, 2, 0, "", 15, 106001, 0, 0, "キャル", 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (92, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 1, 1, 0, "", 0, 112101, 0, 1, "スズメ", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (93, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 2, 0, 0, "", 0, 0, 1, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (94, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 2, 0, 0, "", 0, 0, 1, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (95, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 2, 0, 0, "", 0, 0, 1, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (96, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 2, 0, 0, "", 0, 0, 1, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (97, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 2, 0, 0, "", 0, 0, 1, 2, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (98, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 0, 0, "", 0, 0, 2, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (99, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 0, 0, "", 0, 0, 2, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (100, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 0, 0, "", 0, 0, 2, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (101, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 0, 0, "", 0, 0, 2, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (102, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 0, "", 0, 0, 2, 2, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (103, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 2, 0, 0, "", 0, 0, 3, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (104, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 2, 0, 0, "", 0, 0, 3, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (105, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 2, 0, 0, "", 0, 0, 3, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (106, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 2, 0, 0, "", 0, 0, 3, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (107, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 2, 0, 0, "", 0, 0, 3, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (108, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 2, 0, 0, "", 0, 0, 3, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (109, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 2, 0, 0, "", 0, 0, 3, 2, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (110, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 2, 0, 0, "", 0, 0, 4, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (111, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 2, 0, 0, "", 0, 0, 4, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (112, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 2, 0, 0, "", 0, 0, 4, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (113, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 2, 0, 0, "", 0, 0, 4, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (114, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 2, 0, 0, "", 0, 0, 4, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (115, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 2, 0, 0, "", 0, 0, 4, 1, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (116, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 0, "", 0, 0, 4, 2, "", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (117, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 3, 2, 0, "", 0, 112101, 0, 0, "スズメ", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (118, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 3, 3, 0, "", 7, 112101, 0, 0, "スズメ", 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (119, "ヒューヒュー♪\nドンドンパフパフー♪", 0, 1, 0, "", 1, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (120, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 1, 1, 0, "", 1, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (121, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 1, 2, 0, "", 1, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (122, "え？　ちょっと\nいきなりなにっ？", 2, 1, 0, "", 1, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (123, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 2, 2, 0, "", 1, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (124, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 2, 0, "", 1, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (125, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 3, 0, "", 1, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (126, "ですからその日に向けて\n今から気分を盛り上げているのです", 1, 3, 0, "", 1, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (127, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 2, 3, 0, "", 1, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (128, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 1, 4, 0, "", 1, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (129, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 4, 0, "", 1, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (130, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 2, 4, 0, "", 1, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (131, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 1, 5, 0, "", 1, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (132, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 5, 0, "", 2, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (133, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 1, 1, 0, "", 2, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (134, "げっ…カリンじゃない\nなんでもないわよ！", 0, 6, 0, "", 2, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (135, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 1, 2, 0, "", 2, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (136, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 7, 0, "", 2, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (137, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 1, 3, 0, "", 2, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (138, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 8, 0, "", 2, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (139, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 9, 0, "", 2, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (140, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 1, 4, 0, "", 2, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (141, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 10, 0, "", 2, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (142, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 1, 5, 0, "", 2, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (143, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 11, 0, "", 2, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (144, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 6, 0, "", 3, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (145, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 1, 5, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (146, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 7, 0, "", 3, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (147, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 8, 0, "", 3, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (148, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 1, 6, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (149, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 1, 7, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (150, "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 9, 0, "", 3, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (151, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 1, 8, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (152, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 10, 0, "", 3, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (153, "えへへ、ありがとうございます\nコッコロちゃん！", 1, 9, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (154, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 11, 0, "", 3, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (155, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 1, 10, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (156, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 11, 0, "", 4, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (157, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 1, 12, 0, "", 4, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (158, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 12, 0, "", 4, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (159, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 1, 13, 0, "", 4, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (160, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 13, 0, "", 4, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (161, "…積極的？\nなんの話よ？", 1, 14, 0, "", 4, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (162, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 14, 0, "", 4, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (163, "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 15, 0, "", 4, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (164, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 1, 15, 0, "", 4, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (165, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 16, 0, "", 4, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (166, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 1, 16, 0, "", 4, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (167, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 1, 17, 0, "", 4, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (168, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 18, 0, "", 5, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (169, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 1, 12, 0, "", 5, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (170, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 19, 0, "", 5, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (171, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 1, 13, 0, "", 5, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (172, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 20, 0, "", 5, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (173, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 1, 14, 0, "", 5, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (174, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 21, 0, "", 5, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (175, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 1, 15, 0, "", 5, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (176, "かくし芸？", 0, 22, 0, "", 5, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (177, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 1, 16, 0, "", 5, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (178, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 23, 0, "", 5, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (179, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 17, 0, "", 6, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (180, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 2, 24, 0, "", 6, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (181, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 1, 17, 0, "", 6, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (182, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 18, 0, "", 6, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (183, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 2, 25, 0, "", 6, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (184, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 1, 18, 0, "", 6, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (185, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 2, 26, 0, "", 6, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (186, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 2, 27, 0, "", 6, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (187, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 2, 28, 0, "", 6, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (188, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 19, 0, "", 6, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (189, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 1, 19, 0, "", 6, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (190, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 2, 29, 0, "", 6, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (191, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 30, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (192, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 31, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (193, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 32, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (194, "あー\nあー\n…声、これで大丈夫かしら？", 0, 33, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (195, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 34, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (196, "オイッスー☆", 0, 35, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (197, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 36, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (198, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 37, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (199, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 38, 0, "", 7, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (200, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 20, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (201, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 21, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (202, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 22, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (203, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 23, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (204, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 24, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (205, "オイッスー☆", 0, 25, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (206, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 26, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (207, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 27, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (208, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 28, 0, "", 8, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (209, "オイッスー☆", 0, 20, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (210, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 21, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (211, "あと６日で2.5周年ですか…", 0, 22, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (212, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 23, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (213, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 24, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (214, "オイッスー☆", 0, 25, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (215, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 26, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (216, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 27, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (217, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 28, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (218, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 29, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (219, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 30, 0, "", 9, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (220, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 31, 0, "", 10, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (221, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 1, 29, 0, "", 10, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (222, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 32, 0, "", 10, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (223, "…………", 2, 39, 0, "", 10, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (224, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 1, 30, 0, "", 10, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (225, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 2, 40, 0, "", 10, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (226, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 33, 0, "", 10, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (227, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 2, 41, 0, "", 10, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (228, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 34, 0, "", 10, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (229, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 35, 0, "", 10, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (230, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 2, 42, 0, "", 10, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (231, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 1, 31, 0, "", 10, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (232, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 2, 43, 0, "", 10, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (233, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 36, 0, "", 10, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (234, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 6, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (235, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 7, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (236, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 8, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (237, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 9, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (238, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 10, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (239, "次にコッコロさんですが\nこちらは発声練習をしています", 0, 11, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (240, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 12, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (241, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 13, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (242, "あれ？\nもしかして緊張で気を失って…？", 0, 14, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (243, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 15, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (244, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 16, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (245, "それでは\nまた明日お会いしましょう♪", 0, 17, 0, "", 11, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (246, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 44, 0, "", 12, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (247, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 2, 37, 0, "", 12, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (248, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 1, 32, 0, "", 12, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (249, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 45, 0, "", 12, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (250, "ひどいですよぉキャルちゃ～ん！", 2, 38, 0, "", 12, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (251, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 1, 33, 0, "", 12, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (252, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 2, 39, 0, "", 12, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (253, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 46, 0, "", 12, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (254, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 1, 34, 0, "", 12, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (255, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 2, 40, 0, "", 12, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (256, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 41, 0, "", 13, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (257, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 2, 47, 0, "", 13, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (258, "忘れてきちゃいました☆", 0, 42, 0, "", 13, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (259, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 2, 48, 0, "", 13, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (260, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 1, 35, 0, "", 13, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (261, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 2, 49, 0, "", 13, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (262, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 43, 0, "", 13, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (263, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 2, 50, 0, "", 13, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (264, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 1, 36, 0, "", 13, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (265, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 2, 51, 0, "", 13, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (266, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 1, 37, 0, "", 13, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (267, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 2, 52, 0, "", 13, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (268, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 44, 0, "", 13, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (269, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 1, 38, 0, "", 13, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (270, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 45, 0, "", 13, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (271, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 2, 53, 0, "", 13, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (272, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 46, 0, "", 13, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (273, "こんにちは\n【ギルド管理協会】のカリンです", 0, 18, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (274, "昨日の生放送は\nいかがだったでしょうか？", 0, 19, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (275, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 20, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (276, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 21, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (277, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 22, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (278, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 23, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (279, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 24, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (280, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 25, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (281, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 26, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (282, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 27, 0, "", 14, 190801, 0, 0, "カリン", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (283, "準備はいいですかっ？\n…いっきますよー！", 0, 47, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (284, "祝！", 2, 54, 0, "", 15, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (285, "2.5周年！", 1, 39, 0, "", 15, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (286, "おめでとうございま～す☆", 0, 48, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (287, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 2, 55, 0, "", 15, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (288, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 1, 40, 0, "", 15, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (289, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 49, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (290, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 2, 56, 0, "", 15, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (291, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 1, 41, 0, "", 15, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (292, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 2, 57, 0, "", 15, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (293, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 50, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (294, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 2, 58, 0, "", 15, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (295, "記念日だから\n平気平気♪\nやばいですね☆", 0, 51, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (296, "意味分かんないわよ！", 2, 59, 0, "", 15, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (297, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 1, 42, 0, "", 15, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (298, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 1, 43, 0, "", 15, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (299, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 52, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (300, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 2, 60, 0, "", 15, 106001, 0, 0, "キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (301, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 1, 44, 0, "", 15, 105901, 0, 0, "コッコロ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (302, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 53, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (303, "2.5周年\nありがとうございます！", 0, 54, 1, "105901,45,106001,61", 15, 105801, 0, 0, "ペコリーヌ・コッコロ・キャル", 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (304, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 1, 0, "", 0, 190804, 0, 1, "カリン", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (305, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 2, 0, 0, "", 0, 0, 1, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (306, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 2, 0, 0, "", 0, 0, 1, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (307, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 2, 0, 0, "", 0, 0, 1, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (308, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 2, 0, 0, "", 0, 0, 1, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (309, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 2, 0, 0, "", 0, 0, 1, 2, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (310, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 0, 0, "", 0, 0, 2, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (311, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 0, 0, "", 0, 0, 2, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (312, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 0, 0, "", 0, 0, 2, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (313, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 0, 0, "", 0, 0, 2, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (314, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 0, "", 0, 0, 2, 2, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (315, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 2, 0, 0, "", 0, 0, 3, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (316, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 2, 0, 0, "", 0, 0, 3, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (317, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 2, 0, 0, "", 0, 0, 3, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (318, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 2, 0, 0, "", 0, 0, 3, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (319, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 2, 0, 0, "", 0, 0, 3, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (320, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 2, 0, 0, "", 0, 0, 3, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (321, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 2, 0, 0, "", 0, 0, 3, 2, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (322, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 2, 0, 0, "", 0, 0, 4, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (323, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 2, 0, 0, "", 0, 0, 4, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (324, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 2, 0, 0, "", 0, 0, 4, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (325, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 2, 0, 0, "", 0, 0, 4, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (326, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 2, 0, 0, "", 0, 0, 4, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (327, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 2, 0, 0, "", 0, 0, 4, 1, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (328, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 0, "", 0, 0, 4, 2, "", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (329, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 3, 2, 0, "", 0, 190804, 0, 0, "カリン", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (330, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, 3, 0, "", 7, 190804, 0, 0, "カリン", 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (331, "ささ、シェフィさまはこちらへ…", 4, 1, 0, "", 1, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (332, "コッコロさん、どこへ行くの…？\n…え？", 0, 1, 0, "", 1, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (333, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 3, 1, 0, "", 1, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (334, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 4, 2, 0, "", 1, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (335, "ええと…\n二人とも何をしているの？", 0, 2, 0, "", 1, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (336, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 1, 1, 0, "", 1, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (337, "あの日…？", 0, 3, 0, "", 1, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (338, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 3, 2, 0, "", 1, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (339, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 3, 3, 0, "", 1, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (340, "やばいですね☆", 2, 1, 0, "", 1, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (341, "って、なにしれっと\n参加してんのよカリン！", 1, 2, 0, "", 1, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (342, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 2, 2, 0, "", 1, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (343, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 2, 3, 0, "", 1, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (344, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 3, 4, 0, "", 1, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (345, "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 4, 0, "", 1, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (346, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 3, 5, 0, "", 2, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (347, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 1, 3, 0, "", 2, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (348, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 1, 4, 0, "", 2, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (349, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 3, 6, 0, "", 2, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (350, "さすがでございます\nペコリーヌさま", 1, 5, 0, "", 2, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (351, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 3, 0, "", 2, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (352, "しゃなり、しゃなり…\nふふ、案外できるものね", 2, 5, 0, "", 2, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (353, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 3, 7, 0, "", 2, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (354, "さ、次はキャルさまの\n番でございますよ", 1, 6, 0, "", 2, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (355, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 4, 0, "", 2, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (356, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 2, 6, 0, "", 2, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (357, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 5, 0, "", 2, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (358, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 6, 0, "", 2, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (359, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 1, 7, 0, "", 2, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (360, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 7, 0, "", 2, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (361, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 1, 8, 0, "", 3, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (362, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 3, 8, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (363, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 1, 9, 0, "", 3, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (364, "聖学祭…って、なにかしら？", 2, 7, 0, "", 3, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (365, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 1, 10, 0, "", 3, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (366, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 8, 0, "", 3, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (367, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 9, 0, "", 3, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (368, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 3, 9, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (369, "いや、そのまんますぎでしょ…", 0, 10, 0, "", 3, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (370, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 2, 8, 0, "", 3, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (371, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 3, 10, 0, "", 3, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (372, "えっ？　ええ…\nぎゅーっ…？", 2, 9, 0, "", 3, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (373, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 11, 0, "", 4, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (374, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 2, 10, 0, "", 4, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (375, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 12, 0, "", 4, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (376, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 1, 11, 0, "", 4, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (377, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 13, 0, "", 4, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (378, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 3, 11, 0, "", 4, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (379, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 14, 0, "", 4, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (380, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 15, 0, "", 4, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (381, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 2, 11, 0, "", 4, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (382, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 3, 12, 0, "", 4, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (383, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 1, 12, 0, "", 4, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (384, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 16, 0, "", 4, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (385, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 2, 12, 0, "", 4, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (386, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 3, 13, 0, "", 5, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (387, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 3, 14, 0, "", 5, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (388, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 3, 15, 0, "", 5, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (389, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 17, 0, "", 5, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (390, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 18, 0, "", 5, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (391, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 2, 13, 0, "", 5, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (392, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 1, 13, 0, "", 5, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (393, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 3, 16, 0, "", 5, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (394, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 19, 0, "", 5, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (395, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 3, 17, 0, "", 5, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (396, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 3, 18, 0, "", 5, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (397, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 3, 19, 0, "", 5, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (398, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 2, 4, 0, "", 6, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (399, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 2, 5, 0, "", 6, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (400, "…えっ！？", 1, 20, 0, "", 6, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (401, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 3, 20, 0, "", 6, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (402, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 14, 0, "", 6, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (403, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 4, 14, 0, "", 6, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (404, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 15, 0, "", 6, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (405, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 1, 21, 0, "", 6, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (406, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 3, 21, 0, "", 6, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (407, "ぶっ殺すぞ！？", 1, 22, 0, "", 6, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (408, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 2, 6, 0, "", 6, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (409, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 1, 15, 0, "", 7, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (410, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 23, 0, "", 7, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (411, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 2, 16, 0, "", 7, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (412, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 3, 22, 0, "", 7, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (413, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 24, 0, "", 7, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (414, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 2, 17, 0, "", 7, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (415, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 1, 16, 0, "", 7, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (416, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 2, 18, 0, "", 7, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (417, "もちろんでございます\n仲良くお勉強いたしましょう", 1, 17, 0, "", 7, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (418, "しれっと教育ママが\n生まれてる…？", 0, 25, 0, "", 7, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (419, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 3, 23, 0, "", 7, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (420, "え～～～～！？", 0, 26, 0, "", 7, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (421, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 27, 0, "", 8, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (422, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 2, 19, 0, "", 8, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (423, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 1, 18, 0, "", 8, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (424, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 3, 24, 0, "", 8, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (425, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 2, 20, 0, "", 8, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (426, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 28, 0, "", 8, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (427, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 1, 19, 0, "", 8, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (428, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 2, 21, 0, "", 8, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (429, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 29, 0, "", 8, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (430, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 30, 0, "", 8, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (431, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 3, 25, 0, "", 8, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (432, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 31, 0, "", 8, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (433, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 22, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (434, "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 23, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (435, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 24, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (436, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 25, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (437, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 26, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (438, "それから、それから…！\nぅ、ぅぅっ…！", 0, 27, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (439, "―びえぇぇ～んっ！！！", 0, 28, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (440, "どうされましたか、シェフィさま", 1, 20, 0, "", 9, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (441, "ううっ…ひっく…\nママッ…！", 0, 29, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (442, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 1, 21, 0, "", 9, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (443, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 1, 22, 0, "", 9, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (444, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 30, 0, "", 9, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (445, "主さま\n3.5周年まであと５日\nでございます", 0, 23, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (446, "ですがその前に\n生放送がありますね", 0, 24, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (447, "…生放送に向けた\n意気込みでございますか？", 0, 25, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (448, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 26, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (449, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 27, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (450, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 28, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (451, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 29, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (452, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 30, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (453, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 31, 0, "", 10, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (454, "3.5周年まであと４日！\nやばいですね☆", 0, 26, 0, "", 11, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (455, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 1, 32, 0, "", 11, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (456, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 27, 0, "", 11, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (457, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 28, 0, "", 11, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (458, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 29, 0, "", 11, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (459, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 1, 33, 0, "", 11, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (460, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 30, 0, "", 11, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (461, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 31, 0, "", 11, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (462, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 32, 0, "", 11, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (463, "だからあたしはやるなんて一言も…", 1, 34, 0, "", 11, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (464, "オイッスー☆", 0, 33, 0, "", 11, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (465, "聞きなさいっての！！", 1, 35, 0, "", 11, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (466, "3.5周年まであと３日…\nそして生放送は、明日…", 2, 31, 0, "", 12, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (467, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 2, 32, 0, "", 12, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (468, "おいっす！\n…こうじゃなかったわね\nええと確か…", 2, 33, 0, "", 12, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (469, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 2, 34, 0, "", 12, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (470, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 2, 35, 0, "", 12, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (471, "やってますねシェフィちゃん！", 3, 34, 0, "", 12, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (472, "なによ、考えることは\nみんな同じってこと？", 0, 36, 0, "", 12, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (473, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 1, 32, 0, "", 12, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (474, "みんな…もちろんよ！", 2, 36, 0, "", 12, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (475, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 3, 35, 0, "", 12, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (476, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 2, 7, 0, "", 13, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (477, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 2, 8, 0, "", 13, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (478, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 3, 36, 0, "", 13, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (479, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 2, 9, 0, "", 13, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (480, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 4, 33, 0, "", 13, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (481, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 2, 10, 0, "", 13, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (482, "大丈夫…大丈夫…\nあたしは大丈夫…", 1, 37, 0, "", 13, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (483, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 2, 11, 0, "", 13, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (484, "あうー？", 0, 37, 0, "", 13, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (485, "あ…", 4, 37, 1, "105901,34,106001,38,190801,12", 13, 105801, 0, 0, "みんな", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (486, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 1, 39, 0, "", 13, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (487, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 4, 35, 0, "", 13, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (488, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 3, 38, 0, "", 13, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (489, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 2, 13, 0, "", 13, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (490, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 2, 38, 0, "", 14, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (491, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 3, 39, 0, "", 14, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (492, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 40, 0, "", 14, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (493, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 1, 36, 0, "", 14, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (494, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 2, 39, 0, "", 14, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (495, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 3, 40, 0, "", 14, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (496, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 1, 37, 0, "", 14, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (497, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 41, 0, "", 14, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (498, "へ、へぇ…", 2, 40, 0, "", 14, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (499, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 3, 41, 0, "", 14, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (500, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 2, 41, 0, "", 14, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (501, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 2, 42, 0, "", 14, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (502, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 3, 42, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (503, "祝！", 1, 42, 0, "", 15, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (504, "3.5周年！", 4, 38, 0, "", 15, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (505, "おめでとうございます！", 0, 43, 0, "", 15, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (506, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 2, 14, 0, "", 15, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (507, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 2, 15, 0, "", 15, 190801, 0, 0, "カリン", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (508, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 3, 43, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (509, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 3, 44, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (510, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 1, 43, 0, "", 15, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (511, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 1, 44, 0, "", 15, 106001, 0, 0, "キャル", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (512, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 44, 0, "", 15, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (513, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 45, 0, "", 15, 106401, 0, 0, "シェフィ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (514, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 4, 39, 0, "", 15, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (515, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 4, 40, 0, "", 15, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (516, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 4, 41, 0, "", 15, 105901, 0, 0, "コッコロ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (517, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 3, 45, 0, "", 15, 105801, 0, 0, "ペコリーヌ", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (518, "これからも\nよろしくおねがいします！", 4, 46, 1, "105901,42,106001,45,106401,46,190801,16", 15, 105801, 0, 0, "みんな", 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (519, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 1, 0, "", 0, 190804, 0, 1, "カリン", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (520, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 2, 0, 0, "", 0, 0, 1, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (521, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 2, 0, 0, "", 0, 0, 1, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (522, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 2, 0, 0, "", 0, 0, 1, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (523, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 2, 0, 0, "", 0, 0, 1, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (524, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 2, 0, 0, "", 0, 0, 1, 2, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (525, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 0, 0, "", 0, 0, 2, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (526, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 0, 0, "", 0, 0, 2, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (527, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 2, 0, 0, "", 0, 0, 2, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (528, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 2, 0, 0, "", 0, 0, 2, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (529, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 0, "", 0, 0, 2, 2, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (530, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 2, 0, 0, "", 0, 0, 3, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (531, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 2, 0, 0, "", 0, 0, 3, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (532, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 2, 0, 0, "", 0, 0, 3, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (533, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 2, 0, 0, "", 0, 0, 3, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (534, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 2, 0, 0, "", 0, 0, 3, 1, "", 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (535, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 2, 0, 0, "", 0, 0, 3, 1, "", 60003);
