INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 1, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "", "カリン", 60000, 0, 190804, 0, 1, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 2, 2, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", "", "", 60000, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 3, 2, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", "", 60000, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 4, 2, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", "", "", 60000, 0, 0, 1, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 5, 2, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", "", "", 60000, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 6, 2, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", "", "", 60000, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 7, 2, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", "", "", 60000, 0, 0, 2, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 8, 2, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", "", "", 60000, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 9, 2, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", "", "", 60000, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 10, 2, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", "", 60000, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 11, 2, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", "", 60000, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 12, 2, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", "", "", 60000, 0, 0, 3, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 13, 2, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", "", "", 60000, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 14, 2, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", "", "", 60000, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 15, 2, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", "", "", 60000, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 16, 2, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", "", 60000, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 17, 2, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", "", 60000, 0, 0, 4, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 18, 3, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", "", "カリン", 60000, 0, 190804, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 19, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", "", "カリン", 60000, 7, 190804, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 20, 0, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", "", "ハツネ", 80001, 1, 101201, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 21, 0, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", "", "ハツネ", 80001, 1, 101201, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 22, 1, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", "", "シオリ", 80001, 1, 103801, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 23, 1, "来年も、\n一緒にいられたら…\nうれしいな", "", "シオリ", 80001, 1, 103801, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 24, 0, "あなたと一緒にいるようになって、\nもう1年ですか…", "", "キョウカ", 80001, 2, 103601, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 25, 0, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", "", "キョウカ", 80001, 2, 103601, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 26, 1, "おにいちゃんとあってから、\n1年たったよ～！", "", "ミミ", 80001, 2, 102001, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 27, 1, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", "", "ミミ", 80001, 2, 102001, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 28, 2, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", "", "ミソギ", 80001, 2, 100401, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 29, 2, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", "", "ミソギ", 80001, 2, 100401, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 30, 0, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", "", "マホ", 80001, 3, 101001, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 31, 0, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", "", "マホ", 80001, 3, 101001, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 32, 1, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", "", "イリヤ", 80001, 3, 104401, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 33, 1, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", "", "イリヤ", 80001, 3, 104401, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 34, 2, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", "", "カオリ", 80001, 3, 101701, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 35, 2, "これからも、どうぞよろしく\nお願いするさ～！", "", "カオリ", 80001, 3, 101701, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 36, 0, "あんたと出会ってからもうそんなに\nたつのね～", "", "サレン", 80001, 4, 102801, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 37, 0, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", "", "サレン", 80001, 4, 102801, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 38, 1, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", "", "スズメ", 80001, 4, 102501, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 39, 1, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", "", "スズメ", 80001, 4, 102501, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 40, 0, "振り返れば、今までいろんなことが\nあったにゃあ…", "", "タマキ", 80001, 5, 104601, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 41, 0, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", "", "タマキ", 80001, 5, 104601, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 42, 1, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", "", "ミフユ", 80001, 5, 104801, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 43, 1, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", "", "ミフユ", 80001, 5, 104801, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 44, 0, "あなたさまと出会ってもう1年…", "", "アキノ", 80001, 6, 103201, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 45, 0, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", "", "アキノ", 80001, 6, 103201, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 46, 1, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", "", "ユカリ", 80001, 6, 103401, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 47, 1, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", "", "ユカリ", 80001, 6, 103401, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 48, 0, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", "", "クリスティーナ", 80001, 7, 107101, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 49, 0, "ともかくこれからも\n愉しませてくれよ☆", "", "クリスティーナ", 80001, 7, 107101, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 50, 1, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", "", "ジュン", 80001, 7, 104701, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 51, 1, "見えないから分からないって？\nそれもそうだな…", "", "ジュン", 80001, 7, 104701, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 52, 0, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", "", "トモ", 80001, 8, 103701, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 53, 0, "これからもよろしくね！", "", "トモ", 80001, 8, 103701, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 54, 1, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", "", "マツリ", 80001, 8, 100501, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 55, 1, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", "", "マツリ", 80001, 8, 100501, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 56, 0, "オマエと会って1年なの！\n全然成仏もできなかったし、", "", "ミヤコ", 80001, 9, 100701, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 57, 0, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", "", "ミヤコ", 80001, 9, 100701, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 58, 1, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", "", "シノブ", 80001, 9, 103101, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 59, 1, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", "", "シノブ", 80001, 9, 103101, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 60, 0, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", "", "アンナ", 80001, 10, 100901, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 61, 0, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", "", "アンナ", 80001, 10, 100901, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 62, 1, "君と出会ってもう1年だって！\n早いねー！", "", "ナナカ", 80001, 10, 101301, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 63, 1, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", "", "ナナカ", 80001, 10, 101301, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 64, 0, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", "", "ミツキ", 80001, 11, 105101, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 65, 0, "これからも、いろんなところを\n調べさせてね？", "", "ミツキ", 80001, 11, 105101, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 66, 1, "お前さんと出会ってもう1年か\n助太刀するつもりが", "", "ルカ", 80001, 11, 105601, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 67, 1, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", "", "ルカ", 80001, 11, 105601, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 68, 2, "早いものですわね、あなた様と\nお会いして、もう1年…", "", "エリコ", 80001, 11, 102701, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 69, 2, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", "", "エリコ", 80001, 11, 102701, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 70, 0, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", "", "アヤネ", 80001, 12, 102301, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 71, 0, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", "", "アヤネ", 80001, 12, 102301, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 72, 1, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", "", "チカ", 80001, 12, 104201, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 73, 1, "私のことを見守ってください\nできることなら、ずっと…", "", "チカ", 80001, 12, 104201, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 74, 2, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", "", "クルミ", 80001, 12, 102101, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 75, 2, "で、でもこれからもずっと…\n一緒にいたいなぁ…", "", "クルミ", 80001, 12, 102101, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 76, 0, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", "", "シズル", 80001, 13, 104901, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 77, 0, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", "", "シズル", 80001, 13, 104901, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 78, 1, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", "", "リノ", 80001, 13, 101101, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 79, 1, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", "", "リノ", 80001, 13, 101101, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 80, 0, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", "", "ユイ", 80001, 14, 100201, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 81, 0, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", "", "ユイ", 80001, 14, 100201, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 82, 1, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", "", "ヒヨリ", 80001, 14, 100101, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 83, 1, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", "", "ヒヨリ", 80001, 14, 100101, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 84, 2, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", "", "レイ", 80001, 14, 100301, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 85, 2, "キミに背中を預けられる日が、\n早くきますように", "", "レイ", 80001, 14, 100301, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 86, 0, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", "", "ペコリーヌ", 80001, 15, 105801, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 87, 0, "これからもずっと、覚えていて\nくださいね！", "", "ペコリーヌ", 80001, 15, 105801, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 88, 1, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", "", "コッコロ", 80001, 15, 105901, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 89, 1, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", "", "コッコロ", 80001, 15, 105901, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 90, 2, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", "", "キャル", 80001, 15, 106001, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 91, 2, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", "", "キャル", 80001, 15, 106001, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 92, 1, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", "", "スズメ", 60001, 0, 112101, 0, 1, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 93, 2, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", "", "", 60001, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 94, 2, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", "", 60001, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 95, 2, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", "", "", 60001, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 96, 2, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", "", "", 60001, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 97, 2, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", "", "", 60001, 0, 0, 1, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 98, 2, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", "", "", 60001, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 99, 2, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", "", "", 60001, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 100, 2, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", "", "", 60001, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 101, 2, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", "", "", 60001, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 102, 2, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", "", "", 60001, 0, 0, 2, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 103, 2, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", "", "", 60001, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 104, 2, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", "", "", 60001, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 105, 2, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", "", 60001, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 106, 2, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", "", 60001, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 107, 2, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", "", "", 60001, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 108, 2, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", "", "", 60001, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 109, 2, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", "", "", 60001, 0, 0, 3, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 110, 2, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", "", "", 60001, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 111, 2, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", "", "", 60001, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 112, 2, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", "", "", 60001, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 113, 2, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", "", 60001, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 114, 2, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", "", "", 60001, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 115, 2, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", "", "", 60001, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 116, 2, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", "", 60001, 0, 0, 4, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 117, 3, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", "", "スズメ", 60001, 0, 112101, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 118, 3, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", "", "スズメ", 60001, 7, 112101, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 119, 0, "ヒューヒュー♪\nドンドンパフパフー♪", "", "ペコリーヌ", 80003, 1, 105801, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 120, 1, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", "", "コッコロ", 80003, 1, 105901, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 121, 1, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", "", "コッコロ", 80003, 1, 105901, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 122, 2, "え？　ちょっと\nいきなりなにっ？", "", "キャル", 80003, 1, 106001, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 123, 2, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", "", "キャル", 80003, 1, 106001, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 124, 0, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", "", "ペコリーヌ", 80003, 1, 105801, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 125, 0, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", "", "ペコリーヌ", 80003, 1, 105801, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 126, 1, "ですからその日に向けて\n今から気分を盛り上げているのです", "", "コッコロ", 80003, 1, 105901, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 127, 2, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", "", "キャル", 80003, 1, 106001, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 128, 1, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", "", "コッコロ", 80003, 1, 105901, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 129, 0, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", "", "ペコリーヌ", 80003, 1, 105801, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 130, 2, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", "", "キャル", 80003, 1, 106001, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 131, 1, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", "", "コッコロ", 80003, 1, 105901, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 132, 0, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", "", "キャル", 80003, 2, 106001, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 133, 1, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", "", "カリン", 80003, 2, 190801, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 134, 0, "げっ…カリンじゃない\nなんでもないわよ！", "", "キャル", 80003, 2, 106001, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 135, 1, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", "", "カリン", 80003, 2, 190801, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 136, 0, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", "", "キャル", 80003, 2, 106001, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 137, 1, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", "", "カリン", 80003, 2, 190801, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 138, 0, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", "", "キャル", 80003, 2, 106001, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 139, 0, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", "", "キャル", 80003, 2, 106001, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 140, 1, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", "", "カリン", 80003, 2, 190801, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 141, 0, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", "", "キャル", 80003, 2, 106001, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 142, 1, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", "", "カリン", 80003, 2, 190801, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 143, 0, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", "", "キャル", 80003, 2, 106001, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 144, 0, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", "", "コッコロ", 80003, 3, 105901, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 145, 1, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", "", "ペコリーヌ", 80003, 3, 105801, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 146, 0, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", "", "コッコロ", 80003, 3, 105901, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 147, 0, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", "", "コッコロ", 80003, 3, 105901, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 148, 1, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", "", "ペコリーヌ", 80003, 3, 105801, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 149, 1, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", "", "ペコリーヌ", 80003, 3, 105801, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 150, 0, "あとは、牧場に素敵な温泉が\n出来たとか…", "", "コッコロ", 80003, 3, 105901, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 151, 1, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", "", "ペコリーヌ", 80003, 3, 105801, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 152, 0, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", "", "コッコロ", 80003, 3, 105901, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 153, 1, "えへへ、ありがとうございます\nコッコロちゃん！", "", "ペコリーヌ", 80003, 3, 105801, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 154, 0, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", "", "コッコロ", 80003, 3, 105901, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 155, 1, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", "", "ペコリーヌ", 80003, 3, 105801, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 156, 0, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", "", "ペコリーヌ", 80003, 4, 105801, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 157, 1, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", "", "キャル", 80003, 4, 106001, 0, 0, 12);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 158, 0, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", "", "ペコリーヌ", 80003, 4, 105801, 0, 0, 12);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 159, 1, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", "", "キャル", 80003, 4, 106001, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 160, 0, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", "", "ペコリーヌ", 80003, 4, 105801, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 161, 1, "…積極的？\nなんの話よ？", "", "キャル", 80003, 4, 106001, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 162, 0, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", "", "ペコリーヌ", 80003, 4, 105801, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 163, 0, "ムーサの町で\nきゃるきゃる～ん☆　って！", "", "ペコリーヌ", 80003, 4, 105801, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 164, 1, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", "", "キャル", 80003, 4, 106001, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 165, 0, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", "", "ペコリーヌ", 80003, 4, 105801, 0, 0, 16);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 166, 1, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", "", "キャル", 80003, 4, 106001, 0, 0, 16);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 167, 1, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", "", "キャル", 80003, 4, 106001, 0, 0, 17);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 168, 0, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", "", "キャル", 80003, 5, 106001, 0, 0, 18);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 169, 1, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", "", "コッコロ", 80003, 5, 105901, 0, 0, 12);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 170, 0, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", "", "キャル", 80003, 5, 106001, 0, 0, 19);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 171, 1, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", "", "コッコロ", 80003, 5, 105901, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 172, 0, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", "", "キャル", 80003, 5, 106001, 0, 0, 20);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 173, 1, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", "", "コッコロ", 80003, 5, 105901, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 174, 0, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", "", "キャル", 80003, 5, 106001, 0, 0, 21);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 175, 1, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", "", "コッコロ", 80003, 5, 105901, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 176, 0, "かくし芸？", "", "キャル", 80003, 5, 106001, 0, 0, 22);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 177, 1, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", "", "コッコロ", 80003, 5, 105901, 0, 0, 16);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 178, 0, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", "", "キャル", 80003, 5, 106001, 0, 0, 23);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 179, 0, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", "", "ペコリーヌ", 80003, 6, 105801, 0, 0, 17);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 180, 2, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", "", "キャル", 80003, 6, 106001, 0, 0, 24);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 181, 1, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", "", "コッコロ", 80003, 6, 105901, 0, 0, 17);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 182, 0, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", "", "ペコリーヌ", 80003, 6, 105801, 0, 0, 18);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 183, 2, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", "", "キャル", 80003, 6, 106001, 0, 0, 25);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 184, 1, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", "", "コッコロ", 80003, 6, 105901, 0, 0, 18);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 185, 2, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", "", "キャル", 80003, 6, 106001, 0, 0, 26);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 186, 2, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", "", "キャル", 80003, 6, 106001, 0, 0, 27);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 187, 2, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", "", "キャル", 80003, 6, 106001, 0, 0, 28);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 188, 0, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", "", "ペコリーヌ", 80003, 6, 105801, 0, 0, 19);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 189, 1, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", "", "コッコロ", 80003, 6, 105901, 0, 0, 19);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 190, 2, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", "", "キャル", 80003, 6, 106001, 0, 0, 29);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 191, 0, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", "", "キャル", 80003, 7, 106001, 0, 0, 30);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 192, 0, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", "", "キャル", 80003, 7, 106001, 0, 0, 31);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 193, 0, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", "", "キャル", 80003, 7, 106001, 0, 0, 32);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 194, 0, "あー\nあー\n…声、これで大丈夫かしら？", "", "キャル", 80003, 7, 106001, 0, 0, 33);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 195, 0, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", "", "キャル", 80003, 7, 106001, 0, 0, 34);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 196, 0, "オイッスー☆", "", "キャル", 80003, 7, 106001, 0, 0, 35);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 197, 0, "……\nいや、ないわ\nいつもどおりが一番ね、うん", "", "キャル", 80003, 7, 106001, 0, 0, 36);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 198, 0, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", "", "キャル", 80003, 7, 106001, 0, 0, 37);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 199, 0, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", "", "キャル", 80003, 7, 106001, 0, 0, 38);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 200, 0, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", "", "コッコロ", 80003, 8, 105901, 0, 0, 20);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 201, 0, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", "", "コッコロ", 80003, 8, 105901, 0, 0, 21);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 202, 0, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", "", "コッコロ", 80003, 8, 105901, 0, 0, 22);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 203, 0, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", "", "コッコロ", 80003, 8, 105901, 0, 0, 23);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 204, 0, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", "", "コッコロ", 80003, 8, 105901, 0, 0, 24);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 205, 0, "オイッスー☆", "", "コッコロ", 80003, 8, 105901, 0, 0, 25);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 206, 0, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", "", "コッコロ", 80003, 8, 105901, 0, 0, 26);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 207, 0, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", "", "コッコロ", 80003, 8, 105901, 0, 0, 27);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 208, 0, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", "", "コッコロ", 80003, 8, 105901, 0, 0, 28);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 209, 0, "オイッスー☆", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 20);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 210, 0, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 21);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 211, 0, "あと６日で2.5周年ですか…", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 22);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 212, 0, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 23);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 213, 0, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 24);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 214, 0, "オイッスー☆", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 25);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 215, 0, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 26);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 216, 0, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 27);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 217, 0, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 28);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 218, 0, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 29);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 219, 0, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", "", "ペコリーヌ", 80003, 9, 105801, 0, 0, 30);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 220, 0, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", "", "ペコリーヌ", 80003, 10, 105801, 0, 0, 31);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 221, 1, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", "", "コッコロ", 80003, 10, 105901, 0, 0, 29);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 222, 0, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", "", "ペコリーヌ", 80003, 10, 105801, 0, 0, 32);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 223, 2, "…………", "", "キャル", 80003, 10, 106001, 0, 0, 39);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 224, 1, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", "", "コッコロ", 80003, 10, 105901, 0, 0, 30);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 225, 2, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", "", "キャル", 80003, 10, 106001, 0, 0, 40);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 226, 0, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", "", "ペコリーヌ", 80003, 10, 105801, 0, 0, 33);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 227, 2, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", "", "キャル", 80003, 10, 106001, 0, 0, 41);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 228, 0, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", "", "ペコリーヌ", 80003, 10, 105801, 0, 0, 34);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 229, 0, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", "", "ペコリーヌ", 80003, 10, 105801, 0, 0, 35);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 230, 2, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", "", "キャル", 80003, 10, 106001, 0, 0, 42);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 231, 1, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", "", "コッコロ", 80003, 10, 105901, 0, 0, 31);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 232, 2, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", "", "キャル", 80003, 10, 106001, 0, 0, 43);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 233, 0, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", "", "ペコリーヌ", 80003, 10, 105801, 0, 0, 36);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 234, 0, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", "", "カリン", 80003, 11, 190801, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 235, 0, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", "", "カリン", 80003, 11, 190801, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 236, 0, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", "", "カリン", 80003, 11, 190801, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 237, 0, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", "", "カリン", 80003, 11, 190801, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 238, 0, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", "", "カリン", 80003, 11, 190801, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 239, 0, "次にコッコロさんですが\nこちらは発声練習をしています", "", "カリン", 80003, 11, 190801, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 240, 0, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", "", "カリン", 80003, 11, 190801, 0, 0, 12);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 241, 0, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", "", "カリン", 80003, 11, 190801, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 242, 0, "あれ？\nもしかして緊張で気を失って…？", "", "カリン", 80003, 11, 190801, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 243, 0, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", "", "カリン", 80003, 11, 190801, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 244, 0, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", "", "カリン", 80003, 11, 190801, 0, 0, 16);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 245, 0, "それでは\nまた明日お会いしましょう♪", "", "カリン", 80003, 11, 190801, 0, 0, 17);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 246, 0, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", "", "キャル", 80003, 12, 106001, 0, 0, 44);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 247, 2, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", "", "ペコリーヌ", 80003, 12, 105801, 0, 0, 37);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 248, 1, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", "", "コッコロ", 80003, 12, 105901, 0, 0, 32);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 249, 0, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", "", "キャル", 80003, 12, 106001, 0, 0, 45);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 250, 2, "ひどいですよぉキャルちゃ～ん！", "", "ペコリーヌ", 80003, 12, 105801, 0, 0, 38);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 251, 1, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", "", "コッコロ", 80003, 12, 105901, 0, 0, 33);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 252, 2, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", "", "ペコリーヌ", 80003, 12, 105801, 0, 0, 39);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 253, 0, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", "", "キャル", 80003, 12, 106001, 0, 0, 46);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 254, 1, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", "", "コッコロ", 80003, 12, 105901, 0, 0, 34);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 255, 2, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", "", "ペコリーヌ", 80003, 12, 105801, 0, 0, 40);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 256, 0, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", "", "ペコリーヌ", 80003, 13, 105801, 0, 0, 41);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 257, 2, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", "", "キャル", 80003, 13, 106001, 0, 0, 47);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 258, 0, "忘れてきちゃいました☆", "", "ペコリーヌ", 80003, 13, 105801, 0, 0, 42);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 259, 2, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", "", "キャル", 80003, 13, 106001, 0, 0, 48);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 260, 1, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", "", "コッコロ", 80003, 13, 105901, 0, 0, 35);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 261, 2, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", "", "キャル", 80003, 13, 106001, 0, 0, 49);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 262, 0, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", "", "ペコリーヌ", 80003, 13, 105801, 0, 0, 43);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 263, 2, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", "", "キャル", 80003, 13, 106001, 0, 0, 50);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 264, 1, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", "", "コッコロ", 80003, 13, 105901, 0, 0, 36);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 265, 2, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", "", "キャル", 80003, 13, 106001, 0, 0, 51);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 266, 1, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", "", "コッコロ", 80003, 13, 105901, 0, 0, 37);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 267, 2, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", "", "キャル", 80003, 13, 106001, 0, 0, 52);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 268, 0, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", "", "ペコリーヌ", 80003, 13, 105801, 0, 0, 44);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 269, 1, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", "", "コッコロ", 80003, 13, 105901, 0, 0, 38);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 270, 0, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", "", "ペコリーヌ", 80003, 13, 105801, 0, 0, 45);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 271, 2, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", "", "キャル", 80003, 13, 106001, 0, 0, 53);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 272, 0, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", "", "ペコリーヌ", 80003, 13, 105801, 0, 0, 46);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 273, 0, "こんにちは\n【ギルド管理協会】のカリンです", "", "カリン", 80003, 14, 190801, 0, 0, 18);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 274, 0, "昨日の生放送は\nいかがだったでしょうか？", "", "カリン", 80003, 14, 190801, 0, 0, 19);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 275, 0, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", "", "カリン", 80003, 14, 190801, 0, 0, 20);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 276, 0, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", "", "カリン", 80003, 14, 190801, 0, 0, 21);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 277, 0, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", "", "カリン", 80003, 14, 190801, 0, 0, 22);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 278, 0, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", "", "カリン", 80003, 14, 190801, 0, 0, 23);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 279, 0, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", "", "カリン", 80003, 14, 190801, 0, 0, 24);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 280, 0, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", "", "カリン", 80003, 14, 190801, 0, 0, 25);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 281, 0, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", "", "カリン", 80003, 14, 190801, 0, 0, 26);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 282, 0, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", "", "カリン", 80003, 14, 190801, 0, 0, 27);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 283, 0, "準備はいいですかっ？\n…いっきますよー！", "", "ペコリーヌ", 80003, 15, 105801, 0, 0, 47);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 284, 2, "祝！", "", "キャル", 80003, 15, 106001, 0, 0, 54);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 285, 1, "2.5周年！", "", "コッコロ", 80003, 15, 105901, 0, 0, 39);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 286, 0, "おめでとうございま～す☆", "", "ペコリーヌ", 80003, 15, 105801, 0, 0, 48);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 287, 2, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", "", "キャル", 80003, 15, 106001, 0, 0, 55);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 288, 1, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", "", "コッコロ", 80003, 15, 105901, 0, 0, 40);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 289, 0, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", "", "ペコリーヌ", 80003, 15, 105801, 0, 0, 49);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 290, 2, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", "", "キャル", 80003, 15, 106001, 0, 0, 56);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 291, 1, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", "", "コッコロ", 80003, 15, 105901, 0, 0, 41);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 292, 2, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", "", "キャル", 80003, 15, 106001, 0, 0, 57);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 293, 0, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", "", "ペコリーヌ", 80003, 15, 105801, 0, 0, 50);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 294, 2, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", "", "キャル", 80003, 15, 106001, 0, 0, 58);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 295, 0, "記念日だから\n平気平気♪\nやばいですね☆", "", "ペコリーヌ", 80003, 15, 105801, 0, 0, 51);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 296, 2, "意味分かんないわよ！", "", "キャル", 80003, 15, 106001, 0, 0, 59);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 297, 1, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", "", "コッコロ", 80003, 15, 105901, 0, 0, 42);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 298, 1, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", "", "コッコロ", 80003, 15, 105901, 0, 0, 43);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 299, 0, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", "", "ペコリーヌ", 80003, 15, 105801, 0, 0, 52);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 300, 2, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", "", "キャル", 80003, 15, 106001, 0, 0, 60);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 301, 1, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", "", "コッコロ", 80003, 15, 105901, 0, 0, 44);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 302, 0, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", "", "ペコリーヌ", 80003, 15, 105801, 0, 0, 53);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 303, 0, "2.5周年\nありがとうございます！", "105901,45,106001,61", "ペコリーヌ・コッコロ・キャル", 80003, 15, 105801, 0, 0, 54);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 304, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "", "カリン", 60002, 0, 190804, 0, 1, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 305, 2, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", "", "", 60002, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 306, 2, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", "", 60002, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 307, 2, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", "", "", 60002, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 308, 2, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", "", "", 60002, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 309, 2, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", "", "", 60002, 0, 0, 1, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 310, 2, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", "", "", 60002, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 311, 2, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", "", "", 60002, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 312, 2, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", "", "", 60002, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 313, 2, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", "", "", 60002, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 314, 2, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", "", "", 60002, 0, 0, 2, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 315, 2, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", "", "", 60002, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 316, 2, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", "", "", 60002, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 317, 2, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", "", 60002, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 318, 2, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", "", "", 60002, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 319, 2, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", "", "", 60002, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 320, 2, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", "", 60002, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 321, 2, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", "", "", 60002, 0, 0, 3, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 322, 2, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", "", "", 60002, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 323, 2, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", "", "", 60002, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 324, 2, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", "", "", 60002, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 325, 2, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", "", 60002, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 326, 2, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", "", "", 60002, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 327, 2, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", "", "", 60002, 0, 0, 4, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 328, 2, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", "", 60002, 0, 0, 4, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 329, 3, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", "", "カリン", 60002, 0, 190804, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 330, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", "", "カリン", 60002, 7, 190804, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 331, 4, "ささ、シェフィさまはこちらへ…", "", "コッコロ", 80005, 1, 105901, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 332, 0, "コッコロさん、どこへ行くの…？\n…え？", "", "シェフィ", 80005, 1, 106401, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 333, 3, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", "", "ペコリーヌ", 80005, 1, 105801, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 334, 4, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", "", "コッコロ", 80005, 1, 105901, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 335, 0, "ええと…\n二人とも何をしているの？", "", "シェフィ", 80005, 1, 106401, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 336, 1, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", "", "キャル", 80005, 1, 106001, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 337, 0, "あの日…？", "", "シェフィ", 80005, 1, 106401, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 338, 3, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", "", "ペコリーヌ", 80005, 1, 105801, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 339, 3, "『プリンセスコネクト！\nRe:Dive』3.5周年！", "", "ペコリーヌ", 80005, 1, 105801, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 340, 2, "やばいですね☆", "", "カリン", 80005, 1, 190801, 0, 0, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 341, 1, "って、なにしれっと\n参加してんのよカリン！", "", "キャル", 80005, 1, 106001, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 342, 2, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", "", "カリン", 80005, 1, 190801, 0, 0, 2);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 343, 2, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", "", "カリン", 80005, 1, 190801, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 344, 3, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", "", "ペコリーヌ", 80005, 1, 105801, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 345, 0, "えっ！？　ええとええと…！\nあ、あと14日…！", "", "シェフィ", 80005, 1, 106401, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 346, 3, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", "", "ペコリーヌ", 80005, 2, 105801, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 347, 1, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", "", "コッコロ", 80005, 2, 105901, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 348, 1, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", "", "コッコロ", 80005, 2, 105901, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 349, 3, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", "", "ペコリーヌ", 80005, 2, 105801, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 350, 1, "さすがでございます\nペコリーヌさま", "", "コッコロ", 80005, 2, 105901, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 351, 0, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", "", "キャル", 80005, 2, 106001, 0, 0, 3);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 352, 2, "しゃなり、しゃなり…\nふふ、案外できるものね", "", "シェフィ", 80005, 2, 106401, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 353, 3, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", "", "ペコリーヌ", 80005, 2, 105801, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 354, 1, "さ、次はキャルさまの\n番でございますよ", "", "コッコロ", 80005, 2, 105901, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 355, 0, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", "", "キャル", 80005, 2, 106001, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 356, 2, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", "", "シェフィ", 80005, 2, 106401, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 357, 0, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", "", "キャル", 80005, 2, 106001, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 358, 0, "いたたた…もぉお～！\nなんであたしがこんな目に～！", "", "キャル", 80005, 2, 106001, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 359, 1, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", "", "コッコロ", 80005, 2, 105901, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 360, 0, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", "", "キャル", 80005, 2, 106001, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 361, 1, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", "", "コッコロ", 80005, 3, 105901, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 362, 3, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", "", "ペコリーヌ", 80005, 3, 105801, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 363, 1, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", "", "コッコロ", 80005, 3, 105901, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 364, 2, "聖学祭…って、なにかしら？", "", "シェフィ", 80005, 3, 106401, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 365, 1, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", "", "コッコロ", 80005, 3, 105901, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 366, 0, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", "", "キャル", 80005, 3, 106001, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 367, 0, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", "", "キャル", 80005, 3, 106001, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 368, 3, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", "", "ペコリーヌ", 80005, 3, 105801, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 369, 0, "いや、そのまんますぎでしょ…", "", "キャル", 80005, 3, 106001, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 370, 2, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", "", "シェフィ", 80005, 3, 106401, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 371, 3, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", "", "ペコリーヌ", 80005, 3, 105801, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 372, 2, "えっ？　ええ…\nぎゅーっ…？", "", "シェフィ", 80005, 3, 106401, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 373, 0, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", "", "キャル", 80005, 4, 106001, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 374, 2, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", "", "シェフィ", 80005, 4, 106401, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 375, 0, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", "", "キャル", 80005, 4, 106001, 0, 0, 12);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 376, 1, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", "", "コッコロ", 80005, 4, 105901, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 377, 0, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", "", "キャル", 80005, 4, 106001, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 378, 3, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", "", "ペコリーヌ", 80005, 4, 105801, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 379, 0, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", "", "キャル", 80005, 4, 106001, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 380, 0, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", "", "キャル", 80005, 4, 106001, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 381, 2, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", "", "シェフィ", 80005, 4, 106401, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 382, 3, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", "", "ペコリーヌ", 80005, 4, 105801, 0, 0, 12);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 383, 1, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", "", "コッコロ", 80005, 4, 105901, 0, 0, 12);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 384, 0, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", "", "キャル", 80005, 4, 106001, 0, 0, 16);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 385, 2, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", "", "シェフィ", 80005, 4, 106401, 0, 0, 12);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 386, 3, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", "", "ペコリーヌ", 80005, 5, 105801, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 387, 3, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", "", "ペコリーヌ", 80005, 5, 105801, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 388, 3, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", "", "ペコリーヌ", 80005, 5, 105801, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 389, 0, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", "", "キャル", 80005, 5, 106001, 0, 0, 17);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 390, 0, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", "", "キャル", 80005, 5, 106001, 0, 0, 18);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 391, 2, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", "", "シェフィ", 80005, 5, 106401, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 392, 1, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", "", "コッコロ", 80005, 5, 105901, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 393, 3, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", "", "ペコリーヌ", 80005, 5, 105801, 0, 0, 16);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 394, 0, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", "", "キャル", 80005, 5, 106001, 0, 0, 19);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 395, 3, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", "", "ペコリーヌ", 80005, 5, 105801, 0, 0, 17);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 396, 3, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", "", "ペコリーヌ", 80005, 5, 105801, 0, 0, 18);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 397, 3, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", "", "ペコリーヌ", 80005, 5, 105801, 0, 0, 19);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 398, 2, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", "", "カリン", 80005, 6, 190801, 0, 0, 4);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 399, 2, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", "", "カリン", 80005, 6, 190801, 0, 0, 5);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 400, 1, "…えっ！？", "", "キャル", 80005, 6, 106001, 0, 0, 20);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 401, 3, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", "", "ペコリーヌ", 80005, 6, 105801, 0, 0, 20);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 402, 0, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", "", "シェフィ", 80005, 6, 106401, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 403, 4, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", "", "コッコロ", 80005, 6, 105901, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 404, 0, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", "", "シェフィ", 80005, 6, 106401, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 405, 1, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", "", "キャル", 80005, 6, 106001, 0, 0, 21);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 406, 3, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", "", "ペコリーヌ", 80005, 6, 105801, 0, 0, 21);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 407, 1, "ぶっ殺すぞ！？", "", "キャル", 80005, 6, 106001, 0, 0, 22);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 408, 2, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", "", "カリン", 80005, 6, 190801, 0, 0, 6);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 409, 1, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", "", "コッコロ", 80005, 7, 105901, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 410, 0, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", "", "キャル", 80005, 7, 106001, 0, 0, 23);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 411, 2, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", "", "シェフィ", 80005, 7, 106401, 0, 0, 16);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 412, 3, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", "", "ペコリーヌ", 80005, 7, 105801, 0, 0, 22);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 413, 0, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", "", "キャル", 80005, 7, 106001, 0, 0, 24);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 414, 2, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", "", "シェフィ", 80005, 7, 106401, 0, 0, 17);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 415, 1, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", "", "コッコロ", 80005, 7, 105901, 0, 0, 16);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 416, 2, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", "", "シェフィ", 80005, 7, 106401, 0, 0, 18);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 417, 1, "もちろんでございます\n仲良くお勉強いたしましょう", "", "コッコロ", 80005, 7, 105901, 0, 0, 17);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 418, 0, "しれっと教育ママが\n生まれてる…？", "", "キャル", 80005, 7, 106001, 0, 0, 25);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 419, 3, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", "", "ペコリーヌ", 80005, 7, 105801, 0, 0, 23);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 420, 0, "え～～～～！？", "", "キャル", 80005, 7, 106001, 0, 0, 26);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 421, 0, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", "", "キャル", 80005, 8, 106001, 0, 0, 27);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 422, 2, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", "", "シェフィ", 80005, 8, 106401, 0, 0, 19);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 423, 1, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", "", "コッコロ", 80005, 8, 105901, 0, 0, 18);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 424, 3, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", "", "ペコリーヌ", 80005, 8, 105801, 0, 0, 24);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 425, 2, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", "", "シェフィ", 80005, 8, 106401, 0, 0, 20);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 426, 0, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", "", "キャル", 80005, 8, 106001, 0, 0, 28);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 427, 1, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", "", "コッコロ", 80005, 8, 105901, 0, 0, 19);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 428, 2, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", "", "シェフィ", 80005, 8, 106401, 0, 0, 21);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 429, 0, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", "", "キャル", 80005, 8, 106001, 0, 0, 29);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 430, 0, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", "", "キャル", 80005, 8, 106001, 0, 0, 30);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 431, 3, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", "", "ペコリーヌ", 80005, 8, 105801, 0, 0, 25);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 432, 0, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", "", "キャル", 80005, 8, 106001, 0, 0, 31);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 433, 0, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", "", "シェフィ", 80005, 9, 106401, 0, 0, 22);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 434, 0, "…えっ？\n生放送の意気込み！？\n突然なに！？", "", "シェフィ", 80005, 9, 106401, 0, 0, 23);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 435, 0, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", "", "シェフィ", 80005, 9, 106401, 0, 0, 24);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 436, 0, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", "", "シェフィ", 80005, 9, 106401, 0, 0, 25);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 437, 0, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", "", "シェフィ", 80005, 9, 106401, 0, 0, 26);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 438, 0, "それから、それから…！\nぅ、ぅぅっ…！", "", "シェフィ", 80005, 9, 106401, 0, 0, 27);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 439, 0, "―びえぇぇ～んっ！！！", "", "シェフィ", 80005, 9, 106401, 0, 0, 28);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 440, 1, "どうされましたか、シェフィさま", "", "コッコロ", 80005, 9, 105901, 0, 0, 20);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 441, 0, "ううっ…ひっく…\nママッ…！", "", "シェフィ", 80005, 9, 106401, 0, 0, 29);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 442, 1, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", "", "コッコロ", 80005, 9, 105901, 0, 0, 21);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 443, 1, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", "", "コッコロ", 80005, 9, 105901, 0, 0, 22);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 444, 0, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", "", "シェフィ", 80005, 9, 106401, 0, 0, 30);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 445, 0, "主さま\n3.5周年まであと５日\nでございます", "", "コッコロ", 80005, 10, 105901, 0, 0, 23);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 446, 0, "ですがその前に\n生放送がありますね", "", "コッコロ", 80005, 10, 105901, 0, 0, 24);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 447, 0, "…生放送に向けた\n意気込みでございますか？", "", "コッコロ", 80005, 10, 105901, 0, 0, 25);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 448, 0, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", "", "コッコロ", 80005, 10, 105901, 0, 0, 26);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 449, 0, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", "", "コッコロ", 80005, 10, 105901, 0, 0, 27);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 450, 0, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", "", "コッコロ", 80005, 10, 105901, 0, 0, 28);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 451, 0, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", "", "コッコロ", 80005, 10, 105901, 0, 0, 29);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 452, 0, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", "", "コッコロ", 80005, 10, 105901, 0, 0, 30);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 453, 0, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", "", "コッコロ", 80005, 10, 105901, 0, 0, 31);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 454, 0, "3.5周年まであと４日！\nやばいですね☆", "", "ペコリーヌ", 80005, 11, 105801, 0, 0, 26);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 455, 1, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", "", "キャル", 80005, 11, 106001, 0, 0, 32);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 456, 0, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", "", "ペコリーヌ", 80005, 11, 105801, 0, 0, 27);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 457, 0, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", "", "ペコリーヌ", 80005, 11, 105801, 0, 0, 28);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 458, 0, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", "", "ペコリーヌ", 80005, 11, 105801, 0, 0, 29);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 459, 1, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", "", "キャル", 80005, 11, 106001, 0, 0, 33);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 460, 0, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", "", "ペコリーヌ", 80005, 11, 105801, 0, 0, 30);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 461, 0, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", "", "ペコリーヌ", 80005, 11, 105801, 0, 0, 31);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 462, 0, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", "", "ペコリーヌ", 80005, 11, 105801, 0, 0, 32);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 463, 1, "だからあたしはやるなんて一言も…", "", "キャル", 80005, 11, 106001, 0, 0, 34);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 464, 0, "オイッスー☆", "", "ペコリーヌ", 80005, 11, 105801, 0, 0, 33);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 465, 1, "聞きなさいっての！！", "", "キャル", 80005, 11, 106001, 0, 0, 35);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 466, 2, "3.5周年まであと３日…\nそして生放送は、明日…", "", "シェフィ", 80005, 12, 106401, 0, 0, 31);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 467, 2, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", "", "シェフィ", 80005, 12, 106401, 0, 0, 32);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 468, 2, "おいっす！\n…こうじゃなかったわね\nええと確か…", "", "シェフィ", 80005, 12, 106401, 0, 0, 33);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 469, 2, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", "", "シェフィ", 80005, 12, 106401, 0, 0, 34);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 470, 2, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", "", "シェフィ", 80005, 12, 106401, 0, 0, 35);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 471, 3, "やってますねシェフィちゃん！", "", "ペコリーヌ", 80005, 12, 105801, 0, 0, 34);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 472, 0, "なによ、考えることは\nみんな同じってこと？", "", "キャル", 80005, 12, 106001, 0, 0, 36);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 473, 1, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", "", "コッコロ", 80005, 12, 105901, 0, 0, 32);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 474, 2, "みんな…もちろんよ！", "", "シェフィ", 80005, 12, 106401, 0, 0, 36);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 475, 3, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", "", "ペコリーヌ", 80005, 12, 105801, 0, 0, 35);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 476, 2, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", "", "カリン", 80005, 13, 190801, 0, 0, 7);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 477, 2, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", "", "カリン", 80005, 13, 190801, 0, 0, 8);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 478, 3, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", "", "ペコリーヌ", 80005, 13, 105801, 0, 0, 36);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 479, 2, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", "", "カリン", 80005, 13, 190801, 0, 0, 9);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 480, 4, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", "", "コッコロ", 80005, 13, 105901, 0, 0, 33);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 481, 2, "さすが【美食殿】のママですね！\nそしてキャルさんは…", "", "カリン", 80005, 13, 190801, 0, 0, 10);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 482, 1, "大丈夫…大丈夫…\nあたしは大丈夫…", "", "キャル", 80005, 13, 106001, 0, 0, 37);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 483, 2, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", "", "カリン", 80005, 13, 190801, 0, 0, 11);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 484, 0, "あうー？", "", "シェフィ", 80005, 13, 106401, 0, 0, 37);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 485, 4, "あ…", "105901,34,106001,38,190801,12", "みんな", 80005, 13, 105801, 0, 0, 37);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 486, 1, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", "", "キャル", 80005, 13, 106001, 0, 0, 39);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 487, 4, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", "", "コッコロ", 80005, 13, 105901, 0, 0, 35);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 488, 3, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", "", "ペコリーヌ", 80005, 13, 105801, 0, 0, 38);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 489, 2, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", "", "カリン", 80005, 13, 190801, 0, 0, 13);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 490, 2, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", "", "シェフィ", 80005, 14, 106401, 0, 0, 38);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 491, 3, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", "", "ペコリーヌ", 80005, 14, 105801, 0, 0, 39);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 492, 0, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", "", "キャル", 80005, 14, 106001, 0, 0, 40);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 493, 1, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", "", "コッコロ", 80005, 14, 105901, 0, 0, 36);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 494, 2, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", "", "シェフィ", 80005, 14, 106401, 0, 0, 39);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 495, 3, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", "", "ペコリーヌ", 80005, 14, 105801, 0, 0, 40);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 496, 1, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", "", "コッコロ", 80005, 14, 105901, 0, 0, 37);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 497, 0, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", "", "キャル", 80005, 14, 106001, 0, 0, 41);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 498, 2, "へ、へぇ…", "", "シェフィ", 80005, 14, 106401, 0, 0, 40);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 499, 3, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", "", "ペコリーヌ", 80005, 14, 105801, 0, 0, 41);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 500, 2, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", "", "シェフィ", 80005, 14, 106401, 0, 0, 41);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 501, 2, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", "", "シェフィ", 80005, 14, 106401, 0, 0, 42);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 502, 3, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", "", "ペコリーヌ", 80005, 15, 105801, 0, 0, 42);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 503, 1, "祝！", "", "キャル", 80005, 15, 106001, 0, 0, 42);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 504, 4, "3.5周年！", "", "コッコロ", 80005, 15, 105901, 0, 0, 38);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 505, 0, "おめでとうございます！", "", "シェフィ", 80005, 15, 106401, 0, 0, 43);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 506, 2, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", "", "カリン", 80005, 15, 190801, 0, 0, 14);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 507, 2, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", "", "カリン", 80005, 15, 190801, 0, 0, 15);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 508, 3, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", "", "ペコリーヌ", 80005, 15, 105801, 0, 0, 43);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 509, 3, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", "", "ペコリーヌ", 80005, 15, 105801, 0, 0, 44);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 510, 1, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", "", "キャル", 80005, 15, 106001, 0, 0, 43);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 511, 1, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", "", "キャル", 80005, 15, 106001, 0, 0, 44);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 512, 0, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", "", "シェフィ", 80005, 15, 106401, 0, 0, 44);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 513, 0, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", "", "シェフィ", 80005, 15, 106401, 0, 0, 45);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 514, 4, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", "", "コッコロ", 80005, 15, 105901, 0, 0, 39);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 515, 4, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", "", "コッコロ", 80005, 15, 105901, 0, 0, 40);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 516, 4, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", "", "コッコロ", 80005, 15, 105901, 0, 0, 41);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 517, 3, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", "", "ペコリーヌ", 80005, 15, 105801, 0, 0, 45);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 518, 4, "これからも\nよろしくおねがいします！", "105901,42,106001,45,106401,46,190801,16", "みんな", 80005, 15, 105801, 0, 0, 46);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 519, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "", "カリン", 60003, 0, 190804, 0, 1, 1);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 520, 2, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", "", "", 60003, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 521, 2, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", "", 60003, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 522, 2, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", "", "", 60003, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 523, 2, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", "", "", 60003, 0, 0, 1, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 524, 2, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", "", "", 60003, 0, 0, 1, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 525, 2, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", "", "", 60003, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 526, 2, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", "", "", 60003, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 527, 2, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", "", "", 60003, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 528, 2, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", "", "", 60003, 0, 0, 2, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 529, 2, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", "", "", 60003, 0, 0, 2, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 530, 2, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", "", "", 60003, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 531, 2, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", "", "", 60003, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 532, 2, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", "", 60003, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 533, 2, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", "", "", 60003, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 534, 2, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", "", "", 60003, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 535, 2, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", "", 60003, 0, 0, 3, 1, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 536, 2, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", "", "", 60003, 0, 0, 3, 2, 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 537, 2, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", "", "", 60003, 0, 0, 4, 1, 0);
