INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 1, "カリン", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 1, 1, 60000, 0, 1, "", 190804);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 2, "", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 0, 2, 60000, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 3, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 0, 2, 60000, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 4, "", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 0, 2, 60000, 1, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 5, "", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 0, 2, 60000, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 6, "", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 0, 2, 60000, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 7, "", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 0, 2, 60000, 2, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 8, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 0, 2, 60000, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 9, "", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 0, 2, 60000, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 10, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 0, 2, 60000, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 11, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 0, 2, 60000, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 12, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 0, 2, 60000, 3, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 13, "", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 0, 2, 60000, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 14, "", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 0, 2, 60000, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 15, "", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 0, 2, 60000, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 16, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 0, 2, 60000, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 17, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 0, 2, 60000, 4, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 18, "カリン", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 2, 3, 60000, 0, 0, "", 190804);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 19, "カリン", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 3, 3, 60000, 0, 0, "", 190804);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 20, "ハツネ", "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 1, 0, 80001, 0, 0, "", 101201);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 21, "ハツネ", "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 2, 0, 80001, 0, 0, "", 101201);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 22, "シオリ", "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0, 1, 1, 80001, 0, 0, "", 103801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 23, "シオリ", "来年も、\n一緒にいられたら…\nうれしいな", 0, 2, 1, 80001, 0, 0, "", 103801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 24, "キョウカ", "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 1, 0, 80001, 0, 0, "", 103601);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 25, "キョウカ", "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 2, 0, 80001, 0, 0, "", 103601);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 26, "ミミ", "おにいちゃんとあってから、\n1年たったよ～！", 0, 1, 1, 80001, 0, 0, "", 102001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 27, "ミミ", "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0, 2, 1, 80001, 0, 0, "", 102001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 28, "ミソギ", "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0, 1, 2, 80001, 0, 0, "", 100401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 29, "ミソギ", "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0, 2, 2, 80001, 0, 0, "", 100401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 30, "マホ", "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 1, 0, 80001, 0, 0, "", 101001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 31, "マホ", "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 2, 0, 80001, 0, 0, "", 101001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 32, "イリヤ", "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0, 1, 1, 80001, 0, 0, "", 104401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 33, "イリヤ", "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0, 2, 1, 80001, 0, 0, "", 104401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 34, "カオリ", "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0, 1, 2, 80001, 0, 0, "", 101701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 35, "カオリ", "これからも、どうぞよろしく\nお願いするさ～！", 0, 2, 2, 80001, 0, 0, "", 101701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 36, "サレン", "あんたと出会ってからもうそんなに\nたつのね～", 0, 1, 0, 80001, 0, 0, "", 102801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 37, "サレン", "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 2, 0, 80001, 0, 0, "", 102801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 38, "スズメ", "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0, 1, 1, 80001, 0, 0, "", 102501);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 39, "スズメ", "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0, 2, 1, 80001, 0, 0, "", 102501);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 40, "タマキ", "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 1, 0, 80001, 0, 0, "", 104601);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 41, "タマキ", "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 2, 0, 80001, 0, 0, "", 104601);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 42, "ミフユ", "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0, 1, 1, 80001, 0, 0, "", 104801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 43, "ミフユ", "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0, 2, 1, 80001, 0, 0, "", 104801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 44, "アキノ", "あなたさまと出会ってもう1年…", 0, 1, 0, 80001, 0, 0, "", 103201);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 45, "アキノ", "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 2, 0, 80001, 0, 0, "", 103201);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 46, "ユカリ", "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0, 1, 1, 80001, 0, 0, "", 103401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 47, "ユカリ", "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0, 2, 1, 80001, 0, 0, "", 103401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 48, "クリスティーナ", "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 1, 0, 80001, 0, 0, "", 107101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 49, "クリスティーナ", "ともかくこれからも\n愉しませてくれよ☆", 0, 2, 0, 80001, 0, 0, "", 107101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 50, "ジュン", "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0, 1, 1, 80001, 0, 0, "", 104701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 51, "ジュン", "見えないから分からないって？\nそれもそうだな…", 0, 2, 1, 80001, 0, 0, "", 104701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 52, "トモ", "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 1, 0, 80001, 0, 0, "", 103701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 53, "トモ", "これからもよろしくね！", 0, 2, 0, 80001, 0, 0, "", 103701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 54, "マツリ", "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0, 1, 1, 80001, 0, 0, "", 100501);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 55, "マツリ", "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0, 2, 1, 80001, 0, 0, "", 100501);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 56, "ミヤコ", "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 1, 0, 80001, 0, 0, "", 100701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 57, "ミヤコ", "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 2, 0, 80001, 0, 0, "", 100701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 58, "シノブ", "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0, 1, 1, 80001, 0, 0, "", 103101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 59, "シノブ", "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0, 2, 1, 80001, 0, 0, "", 103101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 60, "アンナ", "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 1, 0, 80001, 0, 0, "", 100901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 61, "アンナ", "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 2, 0, 80001, 0, 0, "", 100901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 62, "ナナカ", "君と出会ってもう1年だって！\n早いねー！", 0, 1, 1, 80001, 0, 0, "", 101301);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 63, "ナナカ", "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0, 2, 1, 80001, 0, 0, "", 101301);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 64, "ミツキ", "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 1, 0, 80001, 0, 0, "", 105101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 65, "ミツキ", "これからも、いろんなところを\n調べさせてね？", 0, 2, 0, 80001, 0, 0, "", 105101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 66, "ルカ", "お前さんと出会ってもう1年か\n助太刀するつもりが", 0, 1, 1, 80001, 0, 0, "", 105601);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 67, "ルカ", "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0, 2, 1, 80001, 0, 0, "", 105601);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 68, "エリコ", "早いものですわね、あなた様と\nお会いして、もう1年…", 0, 1, 2, 80001, 0, 0, "", 102701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 69, "エリコ", "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0, 2, 2, 80001, 0, 0, "", 102701);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 70, "アヤネ", "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 1, 0, 80001, 0, 0, "", 102301);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 71, "アヤネ", "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 2, 0, 80001, 0, 0, "", 102301);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 72, "チカ", "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0, 1, 1, 80001, 0, 0, "", 104201);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 73, "チカ", "私のことを見守ってください\nできることなら、ずっと…", 0, 2, 1, 80001, 0, 0, "", 104201);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 74, "クルミ", "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0, 1, 2, 80001, 0, 0, "", 102101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 75, "クルミ", "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0, 2, 2, 80001, 0, 0, "", 102101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 76, "シズル", "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 1, 0, 80001, 0, 0, "", 104901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 77, "シズル", "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 2, 0, 80001, 0, 0, "", 104901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 78, "リノ", "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0, 1, 1, 80001, 0, 0, "", 101101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 79, "リノ", "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0, 2, 1, 80001, 0, 0, "", 101101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 80, "ユイ", "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 1, 0, 80001, 0, 0, "", 100201);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 81, "ユイ", "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 2, 0, 80001, 0, 0, "", 100201);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 82, "ヒヨリ", "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0, 1, 1, 80001, 0, 0, "", 100101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 83, "ヒヨリ", "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0, 2, 1, 80001, 0, 0, "", 100101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 84, "レイ", "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0, 1, 2, 80001, 0, 0, "", 100301);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 85, "レイ", "キミに背中を預けられる日が、\n早くきますように", 0, 2, 2, 80001, 0, 0, "", 100301);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 86, "ペコリーヌ", "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 1, 0, 80001, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 87, "ペコリーヌ", "これからもずっと、覚えていて\nくださいね！", 0, 2, 0, 80001, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 88, "コッコロ", "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0, 1, 1, 80001, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 89, "コッコロ", "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0, 2, 1, 80001, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 90, "キャル", "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0, 1, 2, 80001, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 91, "キャル", "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0, 2, 2, 80001, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 92, "スズメ", "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 0, 1, 1, 60001, 0, 1, "", 112101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 93, "", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 0, 2, 60001, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 94, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 0, 2, 60001, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 95, "", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 0, 2, 60001, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 96, "", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 0, 2, 60001, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 97, "", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 0, 2, 60001, 1, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 98, "", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 0, 2, 60001, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 99, "", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 0, 2, 60001, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 100, "", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 0, 2, 60001, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 101, "", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 0, 2, 60001, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 102, "", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 0, 2, 60001, 2, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 103, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 0, 2, 60001, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 104, "", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 0, 2, 60001, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 105, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 0, 2, 60001, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 106, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 0, 2, 60001, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 107, "", "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0, 0, 2, 60001, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 108, "", "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0, 0, 2, 60001, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 109, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 0, 2, 60001, 3, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 110, "", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 0, 2, 60001, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 111, "", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 0, 2, 60001, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 112, "", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 0, 2, 60001, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 113, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 0, 2, 60001, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 114, "", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 0, 2, 60001, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 115, "", "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0, 0, 2, 60001, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 116, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 0, 2, 60001, 4, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 117, "スズメ", "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0, 2, 3, 60001, 0, 0, "", 112101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 118, "スズメ", "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0, 3, 3, 60001, 0, 0, "", 112101);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 119, "ペコリーヌ", "ヒューヒュー♪\nドンドンパフパフー♪", 0, 1, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 120, "コッコロ", "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0, 1, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 121, "コッコロ", "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0, 2, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 122, "キャル", "え？　ちょっと\nいきなりなにっ？", 0, 1, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 123, "キャル", "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0, 2, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 124, "ペコリーヌ", "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 2, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 125, "ペコリーヌ", "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 3, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 126, "コッコロ", "ですからその日に向けて\n今から気分を盛り上げているのです", 0, 3, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 127, "キャル", "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0, 3, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 128, "コッコロ", "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0, 4, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 129, "ペコリーヌ", "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 4, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 130, "キャル", "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0, 4, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 131, "コッコロ", "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0, 5, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 132, "キャル", "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 5, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 133, "カリン", "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0, 1, 1, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 134, "キャル", "げっ…カリンじゃない\nなんでもないわよ！", 0, 6, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 135, "カリン", "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0, 2, 1, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 136, "キャル", "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 7, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 137, "カリン", "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0, 3, 1, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 138, "キャル", "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 8, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 139, "キャル", "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 9, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 140, "カリン", "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0, 4, 1, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 141, "キャル", "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 10, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 142, "カリン", "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0, 5, 1, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 143, "キャル", "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 11, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 144, "コッコロ", "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 6, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 145, "ペコリーヌ", "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0, 5, 1, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 146, "コッコロ", "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 7, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 147, "コッコロ", "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 8, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 148, "ペコリーヌ", "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0, 6, 1, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 149, "ペコリーヌ", "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0, 7, 1, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 150, "コッコロ", "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 9, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 151, "ペコリーヌ", "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0, 8, 1, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 152, "コッコロ", "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 10, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 153, "ペコリーヌ", "えへへ、ありがとうございます\nコッコロちゃん！", 0, 9, 1, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 154, "コッコロ", "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 11, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 155, "ペコリーヌ", "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0, 10, 1, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 156, "ペコリーヌ", "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 11, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 157, "キャル", "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0, 12, 1, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 158, "ペコリーヌ", "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 12, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 159, "キャル", "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0, 13, 1, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 160, "ペコリーヌ", "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 13, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 161, "キャル", "…積極的？\nなんの話よ？", 0, 14, 1, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 162, "ペコリーヌ", "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 14, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 163, "ペコリーヌ", "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 15, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 164, "キャル", "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0, 15, 1, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 165, "ペコリーヌ", "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 16, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 166, "キャル", "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0, 16, 1, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 167, "キャル", "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0, 17, 1, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 168, "キャル", "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 18, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 169, "コッコロ", "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0, 12, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 170, "キャル", "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 19, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 171, "コッコロ", "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0, 13, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 172, "キャル", "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 20, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 173, "コッコロ", "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0, 14, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 174, "キャル", "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 21, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 175, "コッコロ", "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0, 15, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 176, "キャル", "かくし芸？", 0, 22, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 177, "コッコロ", "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0, 16, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 178, "キャル", "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 23, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 179, "ペコリーヌ", "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 17, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 180, "キャル", "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0, 24, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 181, "コッコロ", "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0, 17, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 182, "ペコリーヌ", "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 18, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 183, "キャル", "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0, 25, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 184, "コッコロ", "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0, 18, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 185, "キャル", "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0, 26, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 186, "キャル", "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0, 27, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 187, "キャル", "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0, 28, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 188, "ペコリーヌ", "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 19, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 189, "コッコロ", "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0, 19, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 190, "キャル", "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0, 29, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 191, "キャル", "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 30, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 192, "キャル", "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 31, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 193, "キャル", "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 32, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 194, "キャル", "あー\nあー\n…声、これで大丈夫かしら？", 0, 33, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 195, "キャル", "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 34, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 196, "キャル", "オイッスー☆", 0, 35, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 197, "キャル", "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 36, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 198, "キャル", "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 37, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 199, "キャル", "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 38, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 200, "コッコロ", "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 20, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 201, "コッコロ", "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 21, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 202, "コッコロ", "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 22, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 203, "コッコロ", "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 23, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 204, "コッコロ", "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 24, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 205, "コッコロ", "オイッスー☆", 0, 25, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 206, "コッコロ", "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 26, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 207, "コッコロ", "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 27, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 208, "コッコロ", "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 28, 0, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 209, "ペコリーヌ", "オイッスー☆", 0, 20, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 210, "ペコリーヌ", "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 21, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 211, "ペコリーヌ", "あと６日で2.5周年ですか…", 0, 22, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 212, "ペコリーヌ", "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 23, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 213, "ペコリーヌ", "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 24, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 214, "ペコリーヌ", "オイッスー☆", 0, 25, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 215, "ペコリーヌ", "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 26, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 216, "ペコリーヌ", "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 27, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 217, "ペコリーヌ", "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 28, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 218, "ペコリーヌ", "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 29, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 219, "ペコリーヌ", "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 30, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 220, "ペコリーヌ", "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 31, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 221, "コッコロ", "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0, 29, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 222, "ペコリーヌ", "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 32, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 223, "キャル", "…………", 0, 39, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 224, "コッコロ", "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0, 30, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 225, "キャル", "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0, 40, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 226, "ペコリーヌ", "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 33, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 227, "キャル", "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0, 41, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 228, "ペコリーヌ", "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 34, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 229, "ペコリーヌ", "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 35, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 230, "キャル", "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0, 42, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 231, "コッコロ", "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0, 31, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 232, "キャル", "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0, 43, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 233, "ペコリーヌ", "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 36, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 234, "カリン", "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 6, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 235, "カリン", "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 7, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 236, "カリン", "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 8, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 237, "カリン", "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 9, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 238, "カリン", "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 10, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 239, "カリン", "次にコッコロさんですが\nこちらは発声練習をしています", 0, 11, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 240, "カリン", "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 12, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 241, "カリン", "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 13, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 242, "カリン", "あれ？\nもしかして緊張で気を失って…？", 0, 14, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 243, "カリン", "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 15, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 244, "カリン", "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 16, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 245, "カリン", "それでは\nまた明日お会いしましょう♪", 0, 17, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 246, "キャル", "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 44, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 247, "ペコリーヌ", "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0, 37, 2, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 248, "コッコロ", "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0, 32, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 249, "キャル", "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 45, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 250, "ペコリーヌ", "ひどいですよぉキャルちゃ～ん！", 0, 38, 2, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 251, "コッコロ", "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0, 33, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 252, "ペコリーヌ", "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0, 39, 2, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 253, "キャル", "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 46, 0, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 254, "コッコロ", "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0, 34, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 255, "ペコリーヌ", "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0, 40, 2, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 256, "ペコリーヌ", "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 41, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 257, "キャル", "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0, 47, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 258, "ペコリーヌ", "忘れてきちゃいました☆", 0, 42, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 259, "キャル", "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0, 48, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 260, "コッコロ", "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0, 35, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 261, "キャル", "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0, 49, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 262, "ペコリーヌ", "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 43, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 263, "キャル", "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0, 50, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 264, "コッコロ", "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0, 36, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 265, "キャル", "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0, 51, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 266, "コッコロ", "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0, 37, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 267, "キャル", "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0, 52, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 268, "ペコリーヌ", "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 44, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 269, "コッコロ", "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0, 38, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 270, "ペコリーヌ", "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 45, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 271, "キャル", "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0, 53, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 272, "ペコリーヌ", "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 46, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 273, "カリン", "こんにちは\n【ギルド管理協会】のカリンです", 0, 18, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 274, "カリン", "昨日の生放送は\nいかがだったでしょうか？", 0, 19, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 275, "カリン", "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 20, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 276, "カリン", "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 21, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 277, "カリン", "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 22, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 278, "カリン", "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 23, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 279, "カリン", "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 24, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 280, "カリン", "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 25, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 281, "カリン", "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 26, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 282, "カリン", "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 27, 0, 80003, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 283, "ペコリーヌ", "準備はいいですかっ？\n…いっきますよー！", 0, 47, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 284, "キャル", "祝！", 0, 54, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 285, "コッコロ", "2.5周年！", 0, 39, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 286, "ペコリーヌ", "おめでとうございま～す☆", 0, 48, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 287, "キャル", "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0, 55, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 288, "コッコロ", "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0, 40, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 289, "ペコリーヌ", "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 49, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 290, "キャル", "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0, 56, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 291, "コッコロ", "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0, 41, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 292, "キャル", "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0, 57, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 293, "ペコリーヌ", "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 50, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 294, "キャル", "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0, 58, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 295, "ペコリーヌ", "記念日だから\n平気平気♪\nやばいですね☆", 0, 51, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 296, "キャル", "意味分かんないわよ！", 0, 59, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 297, "コッコロ", "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0, 42, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 298, "コッコロ", "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0, 43, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 299, "ペコリーヌ", "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 52, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 300, "キャル", "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0, 60, 2, 80003, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 301, "コッコロ", "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0, 44, 1, 80003, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 302, "ペコリーヌ", "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 53, 0, 80003, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 303, "ペコリーヌ・コッコロ・キャル", "2.5周年\nありがとうございます！", 1, 54, 0, 80003, 0, 0, "105901,45,106001,61", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 304, "カリン", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 1, 1, 60002, 0, 1, "", 190804);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 305, "", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 0, 2, 60002, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 306, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 0, 2, 60002, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 307, "", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 0, 2, 60002, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 308, "", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 0, 2, 60002, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 309, "", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 0, 2, 60002, 1, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 310, "", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 0, 2, 60002, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 311, "", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 0, 2, 60002, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 312, "", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 0, 2, 60002, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 313, "", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 0, 2, 60002, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 314, "", "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 0, 2, 60002, 2, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 315, "", "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 0, 2, 60002, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 316, "", "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 0, 2, 60002, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 317, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 0, 2, 60002, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 318, "", "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 0, 2, 60002, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 319, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0, 0, 2, 60002, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 320, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 0, 2, 60002, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 321, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0, 0, 2, 60002, 3, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 322, "", "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 0, 2, 60002, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 323, "", "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, 0, 2, 60002, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 324, "", "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, 0, 2, 60002, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 325, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 0, 2, 60002, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 326, "", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 0, 2, 60002, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 327, "", "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0, 0, 2, 60002, 4, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 328, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 0, 2, 60002, 4, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 329, "カリン", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 2, 3, 60002, 0, 0, "", 190804);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 330, "カリン", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 3, 3, 60002, 0, 0, "", 190804);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 331, "コッコロ", "ささ、シェフィさまはこちらへ…", 0, 1, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 332, "シェフィ", "コッコロさん、どこへ行くの…？\n…え？", 0, 1, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 333, "ペコリーヌ", "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0, 1, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 334, "コッコロ", "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0, 2, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 335, "シェフィ", "ええと…\n二人とも何をしているの？", 0, 2, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 336, "キャル", "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0, 1, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 337, "シェフィ", "あの日…？", 0, 3, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 338, "ペコリーヌ", "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0, 2, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 339, "ペコリーヌ", "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0, 3, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 340, "カリン", "やばいですね☆", 0, 1, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 341, "キャル", "って、なにしれっと\n参加してんのよカリン！", 0, 2, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 342, "カリン", "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0, 2, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 343, "カリン", "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0, 3, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 344, "ペコリーヌ", "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0, 4, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 345, "シェフィ", "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 4, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 346, "ペコリーヌ", "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0, 5, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 347, "コッコロ", "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0, 3, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 348, "コッコロ", "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0, 4, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 349, "ペコリーヌ", "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0, 6, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 350, "コッコロ", "さすがでございます\nペコリーヌさま", 0, 5, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 351, "キャル", "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 3, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 352, "シェフィ", "しゃなり、しゃなり…\nふふ、案外できるものね", 0, 5, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 353, "ペコリーヌ", "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0, 7, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 354, "コッコロ", "さ、次はキャルさまの\n番でございますよ", 0, 6, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 355, "キャル", "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 4, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 356, "シェフィ", "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0, 6, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 357, "キャル", "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 5, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 358, "キャル", "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 6, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 359, "コッコロ", "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0, 7, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 360, "キャル", "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 7, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 361, "コッコロ", "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0, 8, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 362, "ペコリーヌ", "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0, 8, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 363, "コッコロ", "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0, 9, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 364, "シェフィ", "聖学祭…って、なにかしら？", 0, 7, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 365, "コッコロ", "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0, 10, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 366, "キャル", "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 8, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 367, "キャル", "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 9, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 368, "ペコリーヌ", "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0, 9, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 369, "キャル", "いや、そのまんますぎでしょ…", 0, 10, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 370, "シェフィ", "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0, 8, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 371, "ペコリーヌ", "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0, 10, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 372, "シェフィ", "えっ？　ええ…\nぎゅーっ…？", 0, 9, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 373, "キャル", "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 11, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 374, "シェフィ", "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0, 10, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 375, "キャル", "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 12, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 376, "コッコロ", "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0, 11, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 377, "キャル", "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 13, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 378, "ペコリーヌ", "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0, 11, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 379, "キャル", "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 14, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 380, "キャル", "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 15, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 381, "シェフィ", "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0, 11, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 382, "ペコリーヌ", "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0, 12, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 383, "コッコロ", "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0, 12, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 384, "キャル", "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 16, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 385, "シェフィ", "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0, 12, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 386, "ペコリーヌ", "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0, 13, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 387, "ペコリーヌ", "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0, 14, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 388, "ペコリーヌ", "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0, 15, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 389, "キャル", "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 17, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 390, "キャル", "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 18, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 391, "シェフィ", "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0, 13, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 392, "コッコロ", "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0, 13, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 393, "ペコリーヌ", "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0, 16, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 394, "キャル", "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 19, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 395, "ペコリーヌ", "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0, 17, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 396, "ペコリーヌ", "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0, 18, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (5, 397, "ペコリーヌ", "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0, 19, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 398, "カリン", "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0, 4, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 399, "カリン", "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0, 5, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 400, "キャル", "…えっ！？", 0, 20, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 401, "ペコリーヌ", "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0, 20, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 402, "シェフィ", "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 14, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 403, "コッコロ", "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0, 14, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 404, "シェフィ", "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 15, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 405, "キャル", "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0, 21, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 406, "ペコリーヌ", "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0, 21, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 407, "キャル", "ぶっ殺すぞ！？", 0, 22, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (6, 408, "カリン", "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0, 6, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 409, "コッコロ", "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0, 15, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 410, "キャル", "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 23, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 411, "シェフィ", "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0, 16, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 412, "ペコリーヌ", "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0, 22, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 413, "キャル", "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 24, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 414, "シェフィ", "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0, 17, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 415, "コッコロ", "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0, 16, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 416, "シェフィ", "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0, 18, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 417, "コッコロ", "もちろんでございます\n仲良くお勉強いたしましょう", 0, 17, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 418, "キャル", "しれっと教育ママが\n生まれてる…？", 0, 25, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 419, "ペコリーヌ", "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0, 23, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (7, 420, "キャル", "え～～～～！？", 0, 26, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 421, "キャル", "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 27, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 422, "シェフィ", "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0, 19, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 423, "コッコロ", "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0, 18, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 424, "ペコリーヌ", "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0, 24, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 425, "シェフィ", "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0, 20, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 426, "キャル", "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 28, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 427, "コッコロ", "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0, 19, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 428, "シェフィ", "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0, 21, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 429, "キャル", "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 29, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 430, "キャル", "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 30, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 431, "ペコリーヌ", "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0, 25, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (8, 432, "キャル", "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 31, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 433, "シェフィ", "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 22, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 434, "シェフィ", "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 23, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 435, "シェフィ", "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 24, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 436, "シェフィ", "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 25, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 437, "シェフィ", "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 26, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 438, "シェフィ", "それから、それから…！\nぅ、ぅぅっ…！", 0, 27, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 439, "シェフィ", "―びえぇぇ～んっ！！！", 0, 28, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 440, "コッコロ", "どうされましたか、シェフィさま", 0, 20, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 441, "シェフィ", "ううっ…ひっく…\nママッ…！", 0, 29, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 442, "コッコロ", "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0, 21, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 443, "コッコロ", "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0, 22, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (9, 444, "シェフィ", "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 30, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 445, "コッコロ", "主さま\n3.5周年まであと５日\nでございます", 0, 23, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 446, "コッコロ", "ですがその前に\n生放送がありますね", 0, 24, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 447, "コッコロ", "…生放送に向けた\n意気込みでございますか？", 0, 25, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 448, "コッコロ", "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 26, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 449, "コッコロ", "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 27, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 450, "コッコロ", "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 28, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 451, "コッコロ", "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 29, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 452, "コッコロ", "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 30, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (10, 453, "コッコロ", "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 31, 0, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 454, "ペコリーヌ", "3.5周年まであと４日！\nやばいですね☆", 0, 26, 0, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 455, "キャル", "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0, 32, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 456, "ペコリーヌ", "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 27, 0, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 457, "ペコリーヌ", "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 28, 0, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 458, "ペコリーヌ", "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 29, 0, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 459, "キャル", "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0, 33, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 460, "ペコリーヌ", "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 30, 0, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 461, "ペコリーヌ", "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 31, 0, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 462, "ペコリーヌ", "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 32, 0, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 463, "キャル", "だからあたしはやるなんて一言も…", 0, 34, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 464, "ペコリーヌ", "オイッスー☆", 0, 33, 0, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (11, 465, "キャル", "聞きなさいっての！！", 0, 35, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 466, "シェフィ", "3.5周年まであと３日…\nそして生放送は、明日…", 0, 31, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 467, "シェフィ", "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0, 32, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 468, "シェフィ", "おいっす！\n…こうじゃなかったわね\nええと確か…", 0, 33, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 469, "シェフィ", "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0, 34, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 470, "シェフィ", "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0, 35, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 471, "ペコリーヌ", "やってますねシェフィちゃん！", 0, 34, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 472, "キャル", "なによ、考えることは\nみんな同じってこと？", 0, 36, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 473, "コッコロ", "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0, 32, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 474, "シェフィ", "みんな…もちろんよ！", 0, 36, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (12, 475, "ペコリーヌ", "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0, 35, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 476, "カリン", "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0, 7, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 477, "カリン", "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0, 8, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 478, "ペコリーヌ", "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0, 36, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 479, "カリン", "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0, 9, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 480, "コッコロ", "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0, 33, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 481, "カリン", "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0, 10, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 482, "キャル", "大丈夫…大丈夫…\nあたしは大丈夫…", 0, 37, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 483, "カリン", "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0, 11, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 484, "シェフィ", "あうー？", 0, 37, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 485, "みんな", "あ…", 1, 37, 4, 80005, 0, 0, "105901,34,106001,38,190801,12", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 486, "キャル", "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0, 39, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 487, "コッコロ", "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0, 35, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 488, "ペコリーヌ", "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0, 38, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (13, 489, "カリン", "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0, 13, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 490, "シェフィ", "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0, 38, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 491, "ペコリーヌ", "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0, 39, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 492, "キャル", "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 40, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 493, "コッコロ", "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0, 36, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 494, "シェフィ", "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0, 39, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 495, "ペコリーヌ", "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0, 40, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 496, "コッコロ", "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0, 37, 1, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 497, "キャル", "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 41, 0, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 498, "シェフィ", "へ、へぇ…", 0, 40, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 499, "ペコリーヌ", "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0, 41, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 500, "シェフィ", "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0, 41, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (14, 501, "シェフィ", "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0, 42, 2, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 502, "ペコリーヌ", "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0, 42, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 503, "キャル", "祝！", 0, 42, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 504, "コッコロ", "3.5周年！", 0, 38, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 505, "シェフィ", "おめでとうございます！", 0, 43, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 506, "カリン", "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0, 14, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 507, "カリン", "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0, 15, 2, 80005, 0, 0, "", 190801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 508, "ペコリーヌ", "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0, 43, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 509, "ペコリーヌ", "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0, 44, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 510, "キャル", "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0, 43, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 511, "キャル", "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0, 44, 1, 80005, 0, 0, "", 106001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 512, "シェフィ", "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 44, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 513, "シェフィ", "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 45, 0, 80005, 0, 0, "", 106401);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 514, "コッコロ", "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 0, 39, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 515, "コッコロ", "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 0, 40, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 516, "コッコロ", "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 0, 41, 4, 80005, 0, 0, "", 105901);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 517, "ペコリーヌ", "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 0, 45, 3, 80005, 0, 0, "", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (15, 518, "みんな", "これからも\nよろしくおねがいします！", 1, 46, 4, 80005, 0, 0, "105901,42,106001,45,106401,46,190801,16", 105801);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 519, "カリン", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 1, 1, 60003, 0, 1, "", 190804);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 520, "", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 0, 2, 60003, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 521, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 0, 2, 60003, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 522, "", "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 0, 0, 2, 60003, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 523, "", "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 0, 0, 2, 60003, 1, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 524, "", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 0, 2, 60003, 1, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 525, "", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 0, 2, 60003, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 526, "", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 0, 2, 60003, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 527, "", "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 0, 0, 2, 60003, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 528, "", "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 0, 0, 2, 60003, 2, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 529, "", "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 0, 2, 60003, 2, 2, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 530, "", "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 0, 2, 60003, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 531, "", "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 0, 2, 60003, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 532, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 0, 2, 60003, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 533, "", "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 0, 2, 60003, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 534, "", "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 0, 0, 2, 60003, 3, 1, "", 0);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 535, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 0, 2, 60003, 3, 1, "", 0);
