INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 1, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 190804, 0, 0, "", "カリン", 1, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 1, 0, "", "", 2, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 1, 0, "", "", 3, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 1, 0, "", "", 4, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 2, 0, "", "", 5, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 2, 0, "", "", 6, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 2, 0, "", "", 7, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 3, 0, "", "", 8, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 3, 0, "", "", 9, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 3, 0, "", "", 10, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 3, 0, "", "", 11, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 3, 0, "", "", 12, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 4, 0, "", "", 13, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 4, 0, "", "", 14, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 4, 0, "", "", 15, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 4, 0, "", "", 16, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 4, 0, "", "", 17, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 190804, 0, 0, "", "カリン", 18, 0, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 190804, 0, 0, "", "カリン", 19, 7, 60000);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 101201, 0, 0, "", "ハツネ", 20, 1, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 101201, 0, 0, "", "ハツネ", 21, 1, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 103801, 0, 0, "", "シオリ", 22, 1, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "来年も、\n一緒にいられたら…\nうれしいな", 103801, 0, 0, "", "シオリ", 23, 1, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "あなたと一緒にいるようになって、\nもう1年ですか…", 103601, 0, 0, "", "キョウカ", 24, 2, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 103601, 0, 0, "", "キョウカ", 25, 2, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "おにいちゃんとあってから、\n1年たったよ～！", 102001, 0, 0, "", "ミミ", 26, 2, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 102001, 0, 0, "", "ミミ", 27, 2, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 1, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 100401, 0, 0, "", "ミソギ", 28, 2, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 2, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 100401, 0, 0, "", "ミソギ", 29, 2, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 101001, 0, 0, "", "マホ", 30, 3, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 101001, 0, 0, "", "マホ", 31, 3, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 104401, 0, 0, "", "イリヤ", 32, 3, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 104401, 0, 0, "", "イリヤ", 33, 3, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 1, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 101701, 0, 0, "", "カオリ", 34, 3, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 2, "これからも、どうぞよろしく\nお願いするさ～！", 101701, 0, 0, "", "カオリ", 35, 3, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "あんたと出会ってからもうそんなに\nたつのね～", 102801, 0, 0, "", "サレン", 36, 4, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 102801, 0, 0, "", "サレン", 37, 4, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 102501, 0, 0, "", "スズメ", 38, 4, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 102501, 0, 0, "", "スズメ", 39, 4, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "振り返れば、今までいろんなことが\nあったにゃあ…", 104601, 0, 0, "", "タマキ", 40, 5, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 104601, 0, 0, "", "タマキ", 41, 5, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 104801, 0, 0, "", "ミフユ", 42, 5, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 104801, 0, 0, "", "ミフユ", 43, 5, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "あなたさまと出会ってもう1年…", 103201, 0, 0, "", "アキノ", 44, 6, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 103201, 0, 0, "", "アキノ", 45, 6, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 103401, 0, 0, "", "ユカリ", 46, 6, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 103401, 0, 0, "", "ユカリ", 47, 6, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 107101, 0, 0, "", "クリスティーナ", 48, 7, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "ともかくこれからも\n愉しませてくれよ☆", 107101, 0, 0, "", "クリスティーナ", 49, 7, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 104701, 0, 0, "", "ジュン", 50, 7, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "見えないから分からないって？\nそれもそうだな…", 104701, 0, 0, "", "ジュン", 51, 7, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 103701, 0, 0, "", "トモ", 52, 8, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "これからもよろしくね！", 103701, 0, 0, "", "トモ", 53, 8, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 100501, 0, 0, "", "マツリ", 54, 8, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 100501, 0, 0, "", "マツリ", 55, 8, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "オマエと会って1年なの！\n全然成仏もできなかったし、", 100701, 0, 0, "", "ミヤコ", 56, 9, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 100701, 0, 0, "", "ミヤコ", 57, 9, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 103101, 0, 0, "", "シノブ", 58, 9, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 103101, 0, 0, "", "シノブ", 59, 9, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 100901, 0, 0, "", "アンナ", 60, 10, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 100901, 0, 0, "", "アンナ", 61, 10, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "君と出会ってもう1年だって！\n早いねー！", 101301, 0, 0, "", "ナナカ", 62, 10, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 101301, 0, 0, "", "ナナカ", 63, 10, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 105101, 0, 0, "", "ミツキ", 64, 11, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "これからも、いろんなところを\n調べさせてね？", 105101, 0, 0, "", "ミツキ", 65, 11, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "お前さんと出会ってもう1年か\n助太刀するつもりが", 105601, 0, 0, "", "ルカ", 66, 11, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 105601, 0, 0, "", "ルカ", 67, 11, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 1, "早いものですわね、あなた様と\nお会いして、もう1年…", 102701, 0, 0, "", "エリコ", 68, 11, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 2, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 102701, 0, 0, "", "エリコ", 69, 11, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 102301, 0, 0, "", "アヤネ", 70, 12, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 102301, 0, 0, "", "アヤネ", 71, 12, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 104201, 0, 0, "", "チカ", 72, 12, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "私のことを見守ってください\nできることなら、ずっと…", 104201, 0, 0, "", "チカ", 73, 12, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 1, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 102101, 0, 0, "", "クルミ", 74, 12, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 2, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 102101, 0, 0, "", "クルミ", 75, 12, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 104901, 0, 0, "", "シズル", 76, 13, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 104901, 0, 0, "", "シズル", 77, 13, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 101101, 0, 0, "", "リノ", 78, 13, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 101101, 0, 0, "", "リノ", 79, 13, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 100201, 0, 0, "", "ユイ", 80, 14, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 100201, 0, 0, "", "ユイ", 81, 14, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 100101, 0, 0, "", "ヒヨリ", 82, 14, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 100101, 0, 0, "", "ヒヨリ", 83, 14, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 1, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 100301, 0, 0, "", "レイ", 84, 14, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 2, "キミに背中を預けられる日が、\n早くきますように", 100301, 0, 0, "", "レイ", 85, 14, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 105801, 0, 0, "", "ペコリーヌ", 86, 15, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "これからもずっと、覚えていて\nくださいね！", 105801, 0, 0, "", "ペコリーヌ", 87, 15, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 105901, 0, 0, "", "コッコロ", 88, 15, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 105901, 0, 0, "", "コッコロ", 89, 15, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 1, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 106001, 0, 0, "", "キャル", 90, 15, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 2, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 106001, 0, 0, "", "キャル", 91, 15, 80001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 1, 1, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 112101, 0, 0, "", "スズメ", 92, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 1, 0, "", "", 93, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 1, 0, "", "", 94, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 1, 0, "", "", 95, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 1, 0, "", "", 96, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 1, 0, "", "", 97, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 2, 0, "", "", 98, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 2, 0, "", "", 99, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 2, 0, "", "", 100, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 2, 0, "", "", 101, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 2, 0, "", "", 102, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 3, 0, "", "", 103, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 3, 0, "", "", 104, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 3, 0, "", "", 105, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 3, 0, "", "", 106, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0, 3, 0, "", "", 107, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0, 3, 0, "", "", 108, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 3, 0, "", "", 109, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 4, 0, "", "", 110, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 4, 0, "", "", 111, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 4, 0, "", "", 112, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 4, 0, "", "", 113, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 4, 0, "", "", 114, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0, 4, 0, "", "", 115, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 4, 0, "", "", 116, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 2, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 112101, 0, 0, "", "スズメ", 117, 0, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 3, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 112101, 0, 0, "", "スズメ", 118, 7, 60001);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "ヒューヒュー♪\nドンドンパフパフー♪", 105801, 0, 0, "", "ペコリーヌ", 119, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 105901, 0, 0, "", "コッコロ", 120, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 105901, 0, 0, "", "コッコロ", 121, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 1, "え？　ちょっと\nいきなりなにっ？", 106001, 0, 0, "", "キャル", 122, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 2, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 106001, 0, 0, "", "キャル", 123, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 105801, 0, 0, "", "ペコリーヌ", 124, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 3, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 105801, 0, 0, "", "ペコリーヌ", 125, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 3, "ですからその日に向けて\n今から気分を盛り上げているのです", 105901, 0, 0, "", "コッコロ", 126, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 3, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 106001, 0, 0, "", "キャル", 127, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 4, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 105901, 0, 0, "", "コッコロ", 128, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 4, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 105801, 0, 0, "", "ペコリーヌ", 129, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 4, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 106001, 0, 0, "", "キャル", 130, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 5, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 105901, 0, 0, "", "コッコロ", 131, 1, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 5, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 106001, 0, 0, "", "キャル", 132, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 190801, 0, 0, "", "カリン", 133, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 6, "げっ…カリンじゃない\nなんでもないわよ！", 106001, 0, 0, "", "キャル", 134, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 190801, 0, 0, "", "カリン", 135, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 7, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 106001, 0, 0, "", "キャル", 136, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 3, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 190801, 0, 0, "", "カリン", 137, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 8, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 106001, 0, 0, "", "キャル", 138, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 9, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 106001, 0, 0, "", "キャル", 139, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 4, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 190801, 0, 0, "", "カリン", 140, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 10, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 106001, 0, 0, "", "キャル", 141, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 5, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 190801, 0, 0, "", "カリン", 142, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 11, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 106001, 0, 0, "", "キャル", 143, 2, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 6, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 105901, 0, 0, "", "コッコロ", 144, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 5, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 105801, 0, 0, "", "ペコリーヌ", 145, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 7, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 105901, 0, 0, "", "コッコロ", 146, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 8, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 105901, 0, 0, "", "コッコロ", 147, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 6, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 105801, 0, 0, "", "ペコリーヌ", 148, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 7, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 105801, 0, 0, "", "ペコリーヌ", 149, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 9, "あとは、牧場に素敵な温泉が\n出来たとか…", 105901, 0, 0, "", "コッコロ", 150, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 8, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 105801, 0, 0, "", "ペコリーヌ", 151, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 10, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 105901, 0, 0, "", "コッコロ", 152, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 9, "えへへ、ありがとうございます\nコッコロちゃん！", 105801, 0, 0, "", "ペコリーヌ", 153, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 11, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 105901, 0, 0, "", "コッコロ", 154, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 10, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 105801, 0, 0, "", "ペコリーヌ", 155, 3, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 11, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 105801, 0, 0, "", "ペコリーヌ", 156, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 12, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 106001, 0, 0, "", "キャル", 157, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 12, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 105801, 0, 0, "", "ペコリーヌ", 158, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 13, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 106001, 0, 0, "", "キャル", 159, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 13, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 105801, 0, 0, "", "ペコリーヌ", 160, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 14, "…積極的？\nなんの話よ？", 106001, 0, 0, "", "キャル", 161, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 14, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 105801, 0, 0, "", "ペコリーヌ", 162, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 15, "ムーサの町で\nきゃるきゃる～ん☆　って！", 105801, 0, 0, "", "ペコリーヌ", 163, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 15, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 106001, 0, 0, "", "キャル", 164, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 16, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 105801, 0, 0, "", "ペコリーヌ", 165, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 16, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 106001, 0, 0, "", "キャル", 166, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 17, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 106001, 0, 0, "", "キャル", 167, 4, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 18, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 106001, 0, 0, "", "キャル", 168, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 12, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 105901, 0, 0, "", "コッコロ", 169, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 19, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 106001, 0, 0, "", "キャル", 170, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 13, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 105901, 0, 0, "", "コッコロ", 171, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 20, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 106001, 0, 0, "", "キャル", 172, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 14, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 105901, 0, 0, "", "コッコロ", 173, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 21, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 106001, 0, 0, "", "キャル", 174, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 15, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 105901, 0, 0, "", "コッコロ", 175, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 22, "かくし芸？", 106001, 0, 0, "", "キャル", 176, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 16, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 105901, 0, 0, "", "コッコロ", 177, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 23, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 106001, 0, 0, "", "キャル", 178, 5, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 17, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 105801, 0, 0, "", "ペコリーヌ", 179, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 24, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 106001, 0, 0, "", "キャル", 180, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 17, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 105901, 0, 0, "", "コッコロ", 181, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 18, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 105801, 0, 0, "", "ペコリーヌ", 182, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 25, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 106001, 0, 0, "", "キャル", 183, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 18, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 105901, 0, 0, "", "コッコロ", 184, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 26, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 106001, 0, 0, "", "キャル", 185, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 27, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 106001, 0, 0, "", "キャル", 186, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 28, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 106001, 0, 0, "", "キャル", 187, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 19, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 105801, 0, 0, "", "ペコリーヌ", 188, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 19, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 105901, 0, 0, "", "コッコロ", 189, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 29, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 106001, 0, 0, "", "キャル", 190, 6, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 30, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 106001, 0, 0, "", "キャル", 191, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 31, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 106001, 0, 0, "", "キャル", 192, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 32, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 106001, 0, 0, "", "キャル", 193, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 33, "あー\nあー\n…声、これで大丈夫かしら？", 106001, 0, 0, "", "キャル", 194, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 34, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 106001, 0, 0, "", "キャル", 195, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 35, "オイッスー☆", 106001, 0, 0, "", "キャル", 196, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 36, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 106001, 0, 0, "", "キャル", 197, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 37, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 106001, 0, 0, "", "キャル", 198, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 38, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 106001, 0, 0, "", "キャル", 199, 7, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 20, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 105901, 0, 0, "", "コッコロ", 200, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 21, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 105901, 0, 0, "", "コッコロ", 201, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 22, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 105901, 0, 0, "", "コッコロ", 202, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 23, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 105901, 0, 0, "", "コッコロ", 203, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 24, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 105901, 0, 0, "", "コッコロ", 204, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 25, "オイッスー☆", 105901, 0, 0, "", "コッコロ", 205, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 26, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 105901, 0, 0, "", "コッコロ", 206, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 27, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 105901, 0, 0, "", "コッコロ", 207, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 28, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 105901, 0, 0, "", "コッコロ", 208, 8, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 20, "オイッスー☆", 105801, 0, 0, "", "ペコリーヌ", 209, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 21, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 105801, 0, 0, "", "ペコリーヌ", 210, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 22, "あと６日で2.5周年ですか…", 105801, 0, 0, "", "ペコリーヌ", 211, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 23, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 105801, 0, 0, "", "ペコリーヌ", 212, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 24, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 105801, 0, 0, "", "ペコリーヌ", 213, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 25, "オイッスー☆", 105801, 0, 0, "", "ペコリーヌ", 214, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 26, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 105801, 0, 0, "", "ペコリーヌ", 215, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 27, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 105801, 0, 0, "", "ペコリーヌ", 216, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 28, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 105801, 0, 0, "", "ペコリーヌ", 217, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 29, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 105801, 0, 0, "", "ペコリーヌ", 218, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 30, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 105801, 0, 0, "", "ペコリーヌ", 219, 9, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 31, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 105801, 0, 0, "", "ペコリーヌ", 220, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 29, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 105901, 0, 0, "", "コッコロ", 221, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 32, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 105801, 0, 0, "", "ペコリーヌ", 222, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 39, "…………", 106001, 0, 0, "", "キャル", 223, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 30, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 105901, 0, 0, "", "コッコロ", 224, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 40, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 106001, 0, 0, "", "キャル", 225, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 33, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 105801, 0, 0, "", "ペコリーヌ", 226, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 41, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 106001, 0, 0, "", "キャル", 227, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 34, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 105801, 0, 0, "", "ペコリーヌ", 228, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 35, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 105801, 0, 0, "", "ペコリーヌ", 229, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 42, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 106001, 0, 0, "", "キャル", 230, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 31, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 105901, 0, 0, "", "コッコロ", 231, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 43, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 106001, 0, 0, "", "キャル", 232, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 36, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 105801, 0, 0, "", "ペコリーヌ", 233, 10, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 6, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 190801, 0, 0, "", "カリン", 234, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 7, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 190801, 0, 0, "", "カリン", 235, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 8, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 190801, 0, 0, "", "カリン", 236, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 9, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 190801, 0, 0, "", "カリン", 237, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 10, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 190801, 0, 0, "", "カリン", 238, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 11, "次にコッコロさんですが\nこちらは発声練習をしています", 190801, 0, 0, "", "カリン", 239, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 12, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 190801, 0, 0, "", "カリン", 240, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 13, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 190801, 0, 0, "", "カリン", 241, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 14, "あれ？\nもしかして緊張で気を失って…？", 190801, 0, 0, "", "カリン", 242, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 15, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 190801, 0, 0, "", "カリン", 243, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 16, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 190801, 0, 0, "", "カリン", 244, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 17, "それでは\nまた明日お会いしましょう♪", 190801, 0, 0, "", "カリン", 245, 11, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 44, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 106001, 0, 0, "", "キャル", 246, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 37, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 105801, 0, 0, "", "ペコリーヌ", 247, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 32, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 105901, 0, 0, "", "コッコロ", 248, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 45, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 106001, 0, 0, "", "キャル", 249, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 38, "ひどいですよぉキャルちゃ～ん！", 105801, 0, 0, "", "ペコリーヌ", 250, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 33, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 105901, 0, 0, "", "コッコロ", 251, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 39, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 105801, 0, 0, "", "ペコリーヌ", 252, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 46, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 106001, 0, 0, "", "キャル", 253, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 34, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 105901, 0, 0, "", "コッコロ", 254, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 40, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 105801, 0, 0, "", "ペコリーヌ", 255, 12, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 41, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 105801, 0, 0, "", "ペコリーヌ", 256, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 47, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 106001, 0, 0, "", "キャル", 257, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 42, "忘れてきちゃいました☆", 105801, 0, 0, "", "ペコリーヌ", 258, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 48, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 106001, 0, 0, "", "キャル", 259, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 35, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 105901, 0, 0, "", "コッコロ", 260, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 49, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 106001, 0, 0, "", "キャル", 261, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 43, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 105801, 0, 0, "", "ペコリーヌ", 262, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 50, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 106001, 0, 0, "", "キャル", 263, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 36, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 105901, 0, 0, "", "コッコロ", 264, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 51, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 106001, 0, 0, "", "キャル", 265, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 37, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 105901, 0, 0, "", "コッコロ", 266, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 52, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 106001, 0, 0, "", "キャル", 267, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 44, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 105801, 0, 0, "", "ペコリーヌ", 268, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 38, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 105901, 0, 0, "", "コッコロ", 269, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 45, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 105801, 0, 0, "", "ペコリーヌ", 270, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 53, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 106001, 0, 0, "", "キャル", 271, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 46, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 105801, 0, 0, "", "ペコリーヌ", 272, 13, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 18, "こんにちは\n【ギルド管理協会】のカリンです", 190801, 0, 0, "", "カリン", 273, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 19, "昨日の生放送は\nいかがだったでしょうか？", 190801, 0, 0, "", "カリン", 274, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 20, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 190801, 0, 0, "", "カリン", 275, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 21, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 190801, 0, 0, "", "カリン", 276, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 22, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 190801, 0, 0, "", "カリン", 277, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 23, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 190801, 0, 0, "", "カリン", 278, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 24, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 190801, 0, 0, "", "カリン", 279, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 25, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 190801, 0, 0, "", "カリン", 280, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 26, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 190801, 0, 0, "", "カリン", 281, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 27, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 190801, 0, 0, "", "カリン", 282, 14, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 47, "準備はいいですかっ？\n…いっきますよー！", 105801, 0, 0, "", "ペコリーヌ", 283, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 54, "祝！", 106001, 0, 0, "", "キャル", 284, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 39, "2.5周年！", 105901, 0, 0, "", "コッコロ", 285, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 48, "おめでとうございま～す☆", 105801, 0, 0, "", "ペコリーヌ", 286, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 55, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 106001, 0, 0, "", "キャル", 287, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 40, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 105901, 0, 0, "", "コッコロ", 288, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 49, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 105801, 0, 0, "", "ペコリーヌ", 289, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 56, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 106001, 0, 0, "", "キャル", 290, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 41, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 105901, 0, 0, "", "コッコロ", 291, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 57, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 106001, 0, 0, "", "キャル", 292, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 50, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 105801, 0, 0, "", "ペコリーヌ", 293, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 58, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 106001, 0, 0, "", "キャル", 294, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 51, "記念日だから\n平気平気♪\nやばいですね☆", 105801, 0, 0, "", "ペコリーヌ", 295, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 59, "意味分かんないわよ！", 106001, 0, 0, "", "キャル", 296, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 42, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 105901, 0, 0, "", "コッコロ", 297, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 43, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 105901, 0, 0, "", "コッコロ", 298, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 52, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 105801, 0, 0, "", "ペコリーヌ", 299, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 60, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 106001, 0, 0, "", "キャル", 300, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 44, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 105901, 0, 0, "", "コッコロ", 301, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 53, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 105801, 0, 0, "", "ペコリーヌ", 302, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 54, "2.5周年\nありがとうございます！", 105801, 0, 1, "105901,45,106001,61", "ペコリーヌ・コッコロ・キャル", 303, 15, 80003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 1, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 190804, 0, 0, "", "カリン", 304, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 1, 0, "", "", 305, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 1, 0, "", "", 306, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 1, 0, "", "", 307, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 1, 0, "", "", 308, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 1, 0, "", "", 309, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 2, 0, "", "", 310, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 2, 0, "", "", 311, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 2, 0, "", "", 312, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 2, 0, "", "", 313, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 2, 0, "", "", 314, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 3, 0, "", "", 315, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 3, 0, "", "", 316, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 3, 0, "", "", 317, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 3, 0, "", "", 318, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0, 3, 0, "", "", 319, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 3, 0, "", "", 320, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0, 3, 0, "", "", 321, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 4, 0, "", "", 322, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, 4, 0, "", "", 323, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, 4, 0, "", "", 324, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 4, 0, "", "", 325, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 4, 0, "", "", 326, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0, 4, 0, "", "", 327, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 4, 0, "", "", 328, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 190804, 0, 0, "", "カリン", 329, 0, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 190804, 0, 0, "", "カリン", 330, 7, 60002);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 1, "ささ、シェフィさまはこちらへ…", 105901, 0, 0, "", "コッコロ", 331, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 1, "コッコロさん、どこへ行くの…？\n…え？", 106401, 0, 0, "", "シェフィ", 332, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 1, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 105801, 0, 0, "", "ペコリーヌ", 333, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 2, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 105901, 0, 0, "", "コッコロ", 334, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 2, "ええと…\n二人とも何をしているの？", 106401, 0, 0, "", "シェフィ", 335, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 1, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 106001, 0, 0, "", "キャル", 336, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 3, "あの日…？", 106401, 0, 0, "", "シェフィ", 337, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 2, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 105801, 0, 0, "", "ペコリーヌ", 338, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 3, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 105801, 0, 0, "", "ペコリーヌ", 339, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 1, "やばいですね☆", 190801, 0, 0, "", "カリン", 340, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 2, "って、なにしれっと\n参加してんのよカリン！", 106001, 0, 0, "", "キャル", 341, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 2, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 190801, 0, 0, "", "カリン", 342, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 3, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 190801, 0, 0, "", "カリン", 343, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 4, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 105801, 0, 0, "", "ペコリーヌ", 344, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 4, "えっ！？　ええとええと…！\nあ、あと14日…！", 106401, 0, 0, "", "シェフィ", 345, 1, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 5, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 105801, 0, 0, "", "ペコリーヌ", 346, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 3, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 105901, 0, 0, "", "コッコロ", 347, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 4, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 105901, 0, 0, "", "コッコロ", 348, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 6, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 105801, 0, 0, "", "ペコリーヌ", 349, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 5, "さすがでございます\nペコリーヌさま", 105901, 0, 0, "", "コッコロ", 350, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 3, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 106001, 0, 0, "", "キャル", 351, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 5, "しゃなり、しゃなり…\nふふ、案外できるものね", 106401, 0, 0, "", "シェフィ", 352, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 7, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 105801, 0, 0, "", "ペコリーヌ", 353, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 6, "さ、次はキャルさまの\n番でございますよ", 105901, 0, 0, "", "コッコロ", 354, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 4, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 106001, 0, 0, "", "キャル", 355, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 6, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 106401, 0, 0, "", "シェフィ", 356, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 5, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 106001, 0, 0, "", "キャル", 357, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 6, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 106001, 0, 0, "", "キャル", 358, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 7, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 105901, 0, 0, "", "コッコロ", 359, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 7, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 106001, 0, 0, "", "キャル", 360, 2, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 8, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 105901, 0, 0, "", "コッコロ", 361, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 8, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 105801, 0, 0, "", "ペコリーヌ", 362, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 9, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 105901, 0, 0, "", "コッコロ", 363, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 7, "聖学祭…って、なにかしら？", 106401, 0, 0, "", "シェフィ", 364, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 10, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 105901, 0, 0, "", "コッコロ", 365, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 8, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 106001, 0, 0, "", "キャル", 366, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 9, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 106001, 0, 0, "", "キャル", 367, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 9, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 105801, 0, 0, "", "ペコリーヌ", 368, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 10, "いや、そのまんますぎでしょ…", 106001, 0, 0, "", "キャル", 369, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 8, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 106401, 0, 0, "", "シェフィ", 370, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 10, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 105801, 0, 0, "", "ペコリーヌ", 371, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 9, "えっ？　ええ…\nぎゅーっ…？", 106401, 0, 0, "", "シェフィ", 372, 3, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 11, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 106001, 0, 0, "", "キャル", 373, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 10, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 106401, 0, 0, "", "シェフィ", 374, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 12, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 106001, 0, 0, "", "キャル", 375, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 11, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 105901, 0, 0, "", "コッコロ", 376, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 13, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 106001, 0, 0, "", "キャル", 377, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 11, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 105801, 0, 0, "", "ペコリーヌ", 378, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 14, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 106001, 0, 0, "", "キャル", 379, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 15, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 106001, 0, 0, "", "キャル", 380, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 11, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 106401, 0, 0, "", "シェフィ", 381, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 12, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 105801, 0, 0, "", "ペコリーヌ", 382, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 12, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 105901, 0, 0, "", "コッコロ", 383, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 16, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 106001, 0, 0, "", "キャル", 384, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 12, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 106401, 0, 0, "", "シェフィ", 385, 4, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 13, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 105801, 0, 0, "", "ペコリーヌ", 386, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 14, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 105801, 0, 0, "", "ペコリーヌ", 387, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 15, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 105801, 0, 0, "", "ペコリーヌ", 388, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 17, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 106001, 0, 0, "", "キャル", 389, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 18, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 106001, 0, 0, "", "キャル", 390, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 13, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 106401, 0, 0, "", "シェフィ", 391, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 13, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 105901, 0, 0, "", "コッコロ", 392, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 16, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 105801, 0, 0, "", "ペコリーヌ", 393, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 19, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 106001, 0, 0, "", "キャル", 394, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 17, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 105801, 0, 0, "", "ペコリーヌ", 395, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 18, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 105801, 0, 0, "", "ペコリーヌ", 396, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 19, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 105801, 0, 0, "", "ペコリーヌ", 397, 5, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 4, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 190801, 0, 0, "", "カリン", 398, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 5, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 190801, 0, 0, "", "カリン", 399, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 20, "…えっ！？", 106001, 0, 0, "", "キャル", 400, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 20, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 105801, 0, 0, "", "ペコリーヌ", 401, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 14, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 106401, 0, 0, "", "シェフィ", 402, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 14, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 105901, 0, 0, "", "コッコロ", 403, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 15, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 106401, 0, 0, "", "シェフィ", 404, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 21, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 106001, 0, 0, "", "キャル", 405, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 21, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 105801, 0, 0, "", "ペコリーヌ", 406, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 22, "ぶっ殺すぞ！？", 106001, 0, 0, "", "キャル", 407, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 6, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 190801, 0, 0, "", "カリン", 408, 6, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 15, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 105901, 0, 0, "", "コッコロ", 409, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 23, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 106001, 0, 0, "", "キャル", 410, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 16, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 106401, 0, 0, "", "シェフィ", 411, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 22, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 105801, 0, 0, "", "ペコリーヌ", 412, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 24, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 106001, 0, 0, "", "キャル", 413, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 17, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 106401, 0, 0, "", "シェフィ", 414, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 16, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 105901, 0, 0, "", "コッコロ", 415, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 18, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 106401, 0, 0, "", "シェフィ", 416, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 17, "もちろんでございます\n仲良くお勉強いたしましょう", 105901, 0, 0, "", "コッコロ", 417, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 25, "しれっと教育ママが\n生まれてる…？", 106001, 0, 0, "", "キャル", 418, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 23, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 105801, 0, 0, "", "ペコリーヌ", 419, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 26, "え～～～～！？", 106001, 0, 0, "", "キャル", 420, 7, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 27, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 106001, 0, 0, "", "キャル", 421, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 19, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 106401, 0, 0, "", "シェフィ", 422, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 18, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 105901, 0, 0, "", "コッコロ", 423, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 24, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 105801, 0, 0, "", "ペコリーヌ", 424, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 20, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 106401, 0, 0, "", "シェフィ", 425, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 28, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 106001, 0, 0, "", "キャル", 426, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 19, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 105901, 0, 0, "", "コッコロ", 427, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 21, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 106401, 0, 0, "", "シェフィ", 428, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 29, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 106001, 0, 0, "", "キャル", 429, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 30, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 106001, 0, 0, "", "キャル", 430, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 25, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 105801, 0, 0, "", "ペコリーヌ", 431, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 31, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 106001, 0, 0, "", "キャル", 432, 8, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 22, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 106401, 0, 0, "", "シェフィ", 433, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 23, "…えっ？\n生放送の意気込み！？\n突然なに！？", 106401, 0, 0, "", "シェフィ", 434, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 24, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 106401, 0, 0, "", "シェフィ", 435, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 25, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 106401, 0, 0, "", "シェフィ", 436, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 26, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 106401, 0, 0, "", "シェフィ", 437, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 27, "それから、それから…！\nぅ、ぅぅっ…！", 106401, 0, 0, "", "シェフィ", 438, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 28, "―びえぇぇ～んっ！！！", 106401, 0, 0, "", "シェフィ", 439, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 20, "どうされましたか、シェフィさま", 105901, 0, 0, "", "コッコロ", 440, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 29, "ううっ…ひっく…\nママッ…！", 106401, 0, 0, "", "シェフィ", 441, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 21, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 105901, 0, 0, "", "コッコロ", 442, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 22, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 105901, 0, 0, "", "コッコロ", 443, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 30, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 106401, 0, 0, "", "シェフィ", 444, 9, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 23, "主さま\n3.5周年まであと５日\nでございます", 105901, 0, 0, "", "コッコロ", 445, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 24, "ですがその前に\n生放送がありますね", 105901, 0, 0, "", "コッコロ", 446, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 25, "…生放送に向けた\n意気込みでございますか？", 105901, 0, 0, "", "コッコロ", 447, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 26, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 105901, 0, 0, "", "コッコロ", 448, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 27, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 105901, 0, 0, "", "コッコロ", 449, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 28, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 105901, 0, 0, "", "コッコロ", 450, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 29, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 105901, 0, 0, "", "コッコロ", 451, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 30, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 105901, 0, 0, "", "コッコロ", 452, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 31, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 105901, 0, 0, "", "コッコロ", 453, 10, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 26, "3.5周年まであと４日！\nやばいですね☆", 105801, 0, 0, "", "ペコリーヌ", 454, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 32, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 106001, 0, 0, "", "キャル", 455, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 27, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 105801, 0, 0, "", "ペコリーヌ", 456, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 28, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 105801, 0, 0, "", "ペコリーヌ", 457, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 29, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 105801, 0, 0, "", "ペコリーヌ", 458, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 33, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 106001, 0, 0, "", "キャル", 459, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 30, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 105801, 0, 0, "", "ペコリーヌ", 460, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 31, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 105801, 0, 0, "", "ペコリーヌ", 461, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 32, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 105801, 0, 0, "", "ペコリーヌ", 462, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 34, "だからあたしはやるなんて一言も…", 106001, 0, 0, "", "キャル", 463, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 33, "オイッスー☆", 105801, 0, 0, "", "ペコリーヌ", 464, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 35, "聞きなさいっての！！", 106001, 0, 0, "", "キャル", 465, 11, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 31, "3.5周年まであと３日…\nそして生放送は、明日…", 106401, 0, 0, "", "シェフィ", 466, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 32, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 106401, 0, 0, "", "シェフィ", 467, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 33, "おいっす！\n…こうじゃなかったわね\nええと確か…", 106401, 0, 0, "", "シェフィ", 468, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 34, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 106401, 0, 0, "", "シェフィ", 469, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 35, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 106401, 0, 0, "", "シェフィ", 470, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 34, "やってますねシェフィちゃん！", 105801, 0, 0, "", "ペコリーヌ", 471, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 36, "なによ、考えることは\nみんな同じってこと？", 106001, 0, 0, "", "キャル", 472, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 32, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 105901, 0, 0, "", "コッコロ", 473, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 36, "みんな…もちろんよ！", 106401, 0, 0, "", "シェフィ", 474, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 35, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 105801, 0, 0, "", "ペコリーヌ", 475, 12, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 7, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 190801, 0, 0, "", "カリン", 476, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 8, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 190801, 0, 0, "", "カリン", 477, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 36, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 105801, 0, 0, "", "ペコリーヌ", 478, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 9, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 190801, 0, 0, "", "カリン", 479, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 33, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 105901, 0, 0, "", "コッコロ", 480, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 10, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 190801, 0, 0, "", "カリン", 481, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 37, "大丈夫…大丈夫…\nあたしは大丈夫…", 106001, 0, 0, "", "キャル", 482, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 11, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 190801, 0, 0, "", "カリン", 483, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 37, "あうー？", 106401, 0, 0, "", "シェフィ", 484, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 37, "あ…", 105801, 0, 1, "105901,34,106001,38,190801,12", "みんな", 485, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 39, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 106001, 0, 0, "", "キャル", 486, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 35, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 105901, 0, 0, "", "コッコロ", 487, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 38, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 105801, 0, 0, "", "ペコリーヌ", 488, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 13, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 190801, 0, 0, "", "カリン", 489, 13, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 38, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 106401, 0, 0, "", "シェフィ", 490, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 39, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 105801, 0, 0, "", "ペコリーヌ", 491, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 40, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 106001, 0, 0, "", "キャル", 492, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 36, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 105901, 0, 0, "", "コッコロ", 493, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 39, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 106401, 0, 0, "", "シェフィ", 494, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 40, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 105801, 0, 0, "", "ペコリーヌ", 495, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 37, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 105901, 0, 0, "", "コッコロ", 496, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 41, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 106001, 0, 0, "", "キャル", 497, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 40, "へ、へぇ…", 106401, 0, 0, "", "シェフィ", 498, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 41, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 105801, 0, 0, "", "ペコリーヌ", 499, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 41, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 106401, 0, 0, "", "シェフィ", 500, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 42, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 106401, 0, 0, "", "シェフィ", 501, 14, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 42, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 105801, 0, 0, "", "ペコリーヌ", 502, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 42, "祝！", 106001, 0, 0, "", "キャル", 503, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 38, "3.5周年！", 105901, 0, 0, "", "コッコロ", 504, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 43, "おめでとうございます！", 106401, 0, 0, "", "シェフィ", 505, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 14, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 190801, 0, 0, "", "カリン", 506, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 0, 15, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 190801, 0, 0, "", "カリン", 507, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 43, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 105801, 0, 0, "", "ペコリーヌ", 508, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 44, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 105801, 0, 0, "", "ペコリーヌ", 509, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 43, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 106001, 0, 0, "", "キャル", 510, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 0, 44, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 106001, 0, 0, "", "キャル", 511, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 44, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 106401, 0, 0, "", "シェフィ", 512, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (0, 0, 45, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 106401, 0, 0, "", "シェフィ", 513, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 39, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 105901, 0, 0, "", "コッコロ", 514, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 40, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 105901, 0, 0, "", "コッコロ", 515, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 41, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 105901, 0, 0, "", "コッコロ", 516, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (3, 0, 45, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 105801, 0, 0, "", "ペコリーヌ", 517, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (4, 0, 46, "これからも\nよろしくおねがいします！", 105801, 0, 1, "105901,42,106001,45,106401,46,190801,16", "みんな", 518, 15, 80005);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (1, 1, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 190804, 0, 0, "", "カリン", 519, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 1, 0, "", "", 520, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 1, 0, "", "", 521, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 0, 1, 0, "", "", 522, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 0, 1, 0, "", "", 523, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 1, 0, "", "", 524, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 2, 0, "", "", 525, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 2, 0, "", "", 526, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 0, 2, 0, "", "", 527, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 0, 2, 0, "", "", 528, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 2, 0, "", "", 529, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 3, 0, "", "", 530, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 3, 0, "", "", 531, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 3, 0, "", "", 532, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 3, 0, "", "", 533, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 0, 3, 0, "", "", 534, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 3, 0, "", "", 535, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 2, 0, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 0, 3, 0, "", "", 536, 0, 60003);
INSERT INTO `v1_c2f34a4bface46c095efa5f618221c47512fd8c54abd735a4ba7194620b150ef` VALUES (2, 1, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 4, 0, "", "", 537, 0, 60003);
