INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (12, 0, 1002, 190801, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！", "2030-04-01 14:00:00", 1002001, "2020-02-15 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (11, 0, 1003, 190801, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", "2030-04-01 14:00:00", 1003001, "2020-02-15 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (3, 0, 1004, 190801, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", "2030-04-01 14:00:00", 1004001, "2020-02-15 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (7, 0, 1005, 190801, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", "2030-04-01 14:00:00", 1005001, "2020-02-15 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (2, 0, 1006, 190801, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", "2030-04-01 14:00:00", 1006001, "2020-05-31 12:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (4, 0, 1007, 190801, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう", "2030-04-01 14:00:00", 1007001, "2020-08-15 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (6, 0, 1008, 190801, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n", "2030-04-01 14:00:00", 1008001, "2021-08-31 12:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (13, 0, 1009, 190801, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。", "2030-04-01 14:00:00", 1009001, "2021-12-18 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (4, 0, 1010, 190801, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", "2030-04-01 14:00:00", 1010001, "2022-02-15 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (6, 0, 1011, 190801, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", "2030-04-01 14:00:00", 1011001, "2022-07-18 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (6, 0, 1012, 190801, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪", "2030-04-01 14:00:00", 1012001, "2023-09-18 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (6, 0, 1013, 190801, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪", "2030-04-01 14:00:00", 1013001, "2024-04-18 15:00:00");
INSERT INTO `v1_c44921a00e79832d530f775b4b49ce759d7627534cb0109ede7b40f0c0866512` VALUES (6, 0, 1014, 190801, "バンディ・シスターズのみなさん\nと戦車バトルに挑戦です！\nパーツやメンバーの組み合わせで\nマシンの特徴が変化しますよ♪", "2030-04-01 14:00:00", 1014001, "2024-11-18 15:00:00");
