INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 0, "カリン", 0, 1, 1, 190804, 1, "", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 60000, 1);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 60000, 2);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 60000, 3);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 60000, 4);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 60000, 5);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 60000, 6);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 60000, 7);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 60000, 8);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 60000, 9);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 60000, 10);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 60000, 11);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 60000, 12);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 60000, 13);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 60000, 14);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 60000, 15);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 60000, 16);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 60000, 17);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 0, "カリン", 0, 2, 3, 190804, 0, "", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 60000, 18);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "カリン", 0, 3, 3, 190804, 0, "", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 60000, 19);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ハツネ", 0, 1, 0, 101201, 0, "", "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 80001, 20);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ハツネ", 0, 2, 0, 101201, 0, "", "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 80001, 21);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "シオリ", 0, 1, 1, 103801, 0, "", "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 80001, 22);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "シオリ", 0, 2, 1, 103801, 0, "", "来年も、\n一緒にいられたら…\nうれしいな", 80001, 23);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キョウカ", 0, 1, 0, 103601, 0, "", "あなたと一緒にいるようになって、\nもう1年ですか…", 80001, 24);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キョウカ", 0, 2, 0, 103601, 0, "", "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 80001, 25);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "ミミ", 0, 1, 1, 102001, 0, "", "おにいちゃんとあってから、\n1年たったよ～！", 80001, 26);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "ミミ", 0, 2, 1, 102001, 0, "", "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 80001, 27);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "ミソギ", 0, 1, 2, 100401, 0, "", "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 80001, 28);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "ミソギ", 0, 2, 2, 100401, 0, "", "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 80001, 29);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "マホ", 0, 1, 0, 101001, 0, "", "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 80001, 30);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "マホ", 0, 2, 0, 101001, 0, "", "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 80001, 31);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "イリヤ", 0, 1, 1, 104401, 0, "", "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 80001, 32);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "イリヤ", 0, 2, 1, 104401, 0, "", "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 80001, 33);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "カオリ", 0, 1, 2, 101701, 0, "", "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 80001, 34);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "カオリ", 0, 2, 2, 101701, 0, "", "これからも、どうぞよろしく\nお願いするさ～！", 80001, 35);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "サレン", 0, 1, 0, 102801, 0, "", "あんたと出会ってからもうそんなに\nたつのね～", 80001, 36);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "サレン", 0, 2, 0, 102801, 0, "", "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 80001, 37);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "スズメ", 0, 1, 1, 102501, 0, "", "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 80001, 38);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "スズメ", 0, 2, 1, 102501, 0, "", "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 80001, 39);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "タマキ", 0, 1, 0, 104601, 0, "", "振り返れば、今までいろんなことが\nあったにゃあ…", 80001, 40);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "タマキ", 0, 2, 0, 104601, 0, "", "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 80001, 41);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ミフユ", 0, 1, 1, 104801, 0, "", "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 80001, 42);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ミフユ", 0, 2, 1, 104801, 0, "", "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 80001, 43);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "アキノ", 0, 1, 0, 103201, 0, "", "あなたさまと出会ってもう1年…", 80001, 44);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "アキノ", 0, 2, 0, 103201, 0, "", "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 80001, 45);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "ユカリ", 0, 1, 1, 103401, 0, "", "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 80001, 46);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "ユカリ", 0, 2, 1, 103401, 0, "", "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 80001, 47);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "クリスティーナ", 0, 1, 0, 107101, 0, "", "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 80001, 48);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "クリスティーナ", 0, 2, 0, 107101, 0, "", "ともかくこれからも\n愉しませてくれよ☆", 80001, 49);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "ジュン", 0, 1, 1, 104701, 0, "", "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 80001, 50);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "ジュン", 0, 2, 1, 104701, 0, "", "見えないから分からないって？\nそれもそうだな…", 80001, 51);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "トモ", 0, 1, 0, 103701, 0, "", "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 80001, 52);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "トモ", 0, 2, 0, 103701, 0, "", "これからもよろしくね！", 80001, 53);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "マツリ", 0, 1, 1, 100501, 0, "", "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 80001, 54);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "マツリ", 0, 2, 1, 100501, 0, "", "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 80001, 55);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ミヤコ", 0, 1, 0, 100701, 0, "", "オマエと会って1年なの！\n全然成仏もできなかったし、", 80001, 56);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ミヤコ", 0, 2, 0, 100701, 0, "", "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 80001, 57);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シノブ", 0, 1, 1, 103101, 0, "", "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 80001, 58);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シノブ", 0, 2, 1, 103101, 0, "", "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 80001, 59);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "アンナ", 0, 1, 0, 100901, 0, "", "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 80001, 60);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "アンナ", 0, 2, 0, 100901, 0, "", "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 80001, 61);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "ナナカ", 0, 1, 1, 101301, 0, "", "君と出会ってもう1年だって！\n早いねー！", 80001, 62);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "ナナカ", 0, 2, 1, 101301, 0, "", "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 80001, 63);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ミツキ", 0, 1, 0, 105101, 0, "", "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 80001, 64);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ミツキ", 0, 2, 0, 105101, 0, "", "これからも、いろんなところを\n調べさせてね？", 80001, 65);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ルカ", 0, 1, 1, 105601, 0, "", "お前さんと出会ってもう1年か\n助太刀するつもりが", 80001, 66);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ルカ", 0, 2, 1, 105601, 0, "", "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 80001, 67);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "エリコ", 0, 1, 2, 102701, 0, "", "早いものですわね、あなた様と\nお会いして、もう1年…", 80001, 68);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "エリコ", 0, 2, 2, 102701, 0, "", "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 80001, 69);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "アヤネ", 0, 1, 0, 102301, 0, "", "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 80001, 70);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "アヤネ", 0, 2, 0, 102301, 0, "", "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 80001, 71);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "チカ", 0, 1, 1, 104201, 0, "", "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 80001, 72);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "チカ", 0, 2, 1, 104201, 0, "", "私のことを見守ってください\nできることなら、ずっと…", 80001, 73);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "クルミ", 0, 1, 2, 102101, 0, "", "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 80001, 74);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "クルミ", 0, 2, 2, 102101, 0, "", "で、でもこれからもずっと…\n一緒にいたいなぁ…", 80001, 75);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "シズル", 0, 1, 0, 104901, 0, "", "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 80001, 76);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "シズル", 0, 2, 0, 104901, 0, "", "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 80001, 77);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "リノ", 0, 1, 1, 101101, 0, "", "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 80001, 78);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "リノ", 0, 2, 1, 101101, 0, "", "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 80001, 79);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "ユイ", 0, 1, 0, 100201, 0, "", "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 80001, 80);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "ユイ", 0, 2, 0, 100201, 0, "", "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 80001, 81);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "ヒヨリ", 0, 1, 1, 100101, 0, "", "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 80001, 82);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "ヒヨリ", 0, 2, 1, 100101, 0, "", "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 80001, 83);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "レイ", 0, 1, 2, 100301, 0, "", "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 80001, 84);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "レイ", 0, 2, 2, 100301, 0, "", "キミに背中を預けられる日が、\n早くきますように", 80001, 85);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 1, 0, 105801, 0, "", "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 80001, 86);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 2, 0, 105801, 0, "", "これからもずっと、覚えていて\nくださいね！", 80001, 87);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 1, 1, 105901, 0, "", "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 80001, 88);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 2, 1, 105901, 0, "", "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 80001, 89);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 1, 2, 106001, 0, "", "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 80001, 90);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 2, 2, 106001, 0, "", "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 80001, 91);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 0, "スズメ", 0, 1, 1, 112101, 1, "", "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 60001, 92);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 60001, 93);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 60001, 94);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 60001, 95);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 60001, 96);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 60001, 97);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 60001, 98);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 60001, 99);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 60001, 100);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 60001, 101);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 60001, 102);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 60001, 103);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 60001, 104);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 60001, 105);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 60001, 106);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 60001, 107);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 60001, 108);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 60001, 109);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 60001, 110);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 60001, 111);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 60001, 112);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 60001, 113);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 60001, 114);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 60001, 115);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 60001, 116);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 0, "スズメ", 0, 2, 3, 112101, 0, "", "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 60001, 117);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "スズメ", 0, 3, 3, 112101, 0, "", "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 60001, 118);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ペコリーヌ", 0, 1, 0, 105801, 0, "", "ヒューヒュー♪\nドンドンパフパフー♪", 80003, 119);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "コッコロ", 0, 1, 1, 105901, 0, "", "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 80003, 120);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "コッコロ", 0, 2, 1, 105901, 0, "", "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 80003, 121);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "キャル", 0, 1, 2, 106001, 0, "", "え？　ちょっと\nいきなりなにっ？", 80003, 122);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "キャル", 0, 2, 2, 106001, 0, "", "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 80003, 123);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ペコリーヌ", 0, 2, 0, 105801, 0, "", "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 80003, 124);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ペコリーヌ", 0, 3, 0, 105801, 0, "", "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 80003, 125);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "コッコロ", 0, 3, 1, 105901, 0, "", "ですからその日に向けて\n今から気分を盛り上げているのです", 80003, 126);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "キャル", 0, 3, 2, 106001, 0, "", "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 80003, 127);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "コッコロ", 0, 4, 1, 105901, 0, "", "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 80003, 128);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ペコリーヌ", 0, 4, 0, 105801, 0, "", "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 80003, 129);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "キャル", 0, 4, 2, 106001, 0, "", "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 80003, 130);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "コッコロ", 0, 5, 1, 105901, 0, "", "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 80003, 131);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 5, 0, 106001, 0, "", "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 80003, 132);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "カリン", 0, 1, 1, 190801, 0, "", "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 80003, 133);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 6, 0, 106001, 0, "", "げっ…カリンじゃない\nなんでもないわよ！", 80003, 134);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "カリン", 0, 2, 1, 190801, 0, "", "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 80003, 135);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 7, 0, 106001, 0, "", "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 80003, 136);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "カリン", 0, 3, 1, 190801, 0, "", "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 80003, 137);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 8, 0, 106001, 0, "", "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 80003, 138);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 9, 0, 106001, 0, "", "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 80003, 139);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "カリン", 0, 4, 1, 190801, 0, "", "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 80003, 140);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 10, 0, 106001, 0, "", "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 80003, 141);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "カリン", 0, 5, 1, 190801, 0, "", "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 80003, 142);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 11, 0, 106001, 0, "", "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 80003, 143);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 6, 0, 105901, 0, "", "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 80003, 144);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 5, 1, 105801, 0, "", "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 80003, 145);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 7, 0, 105901, 0, "", "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 80003, 146);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 8, 0, 105901, 0, "", "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 80003, 147);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 6, 1, 105801, 0, "", "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 80003, 148);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 7, 1, 105801, 0, "", "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 80003, 149);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 9, 0, 105901, 0, "", "あとは、牧場に素敵な温泉が\n出来たとか…", 80003, 150);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 8, 1, 105801, 0, "", "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 80003, 151);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 10, 0, 105901, 0, "", "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 80003, 152);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 9, 1, 105801, 0, "", "えへへ、ありがとうございます\nコッコロちゃん！", 80003, 153);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 11, 0, 105901, 0, "", "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 80003, 154);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 10, 1, 105801, 0, "", "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 80003, 155);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "ペコリーヌ", 0, 11, 0, 105801, 0, "", "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 80003, 156);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 12, 1, 106001, 0, "", "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 80003, 157);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "ペコリーヌ", 0, 12, 0, 105801, 0, "", "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 80003, 158);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 13, 1, 106001, 0, "", "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 80003, 159);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "ペコリーヌ", 0, 13, 0, 105801, 0, "", "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 80003, 160);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 14, 1, 106001, 0, "", "…積極的？\nなんの話よ？", 80003, 161);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "ペコリーヌ", 0, 14, 0, 105801, 0, "", "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 80003, 162);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "ペコリーヌ", 0, 15, 0, 105801, 0, "", "ムーサの町で\nきゃるきゃる～ん☆　って！", 80003, 163);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 15, 1, 106001, 0, "", "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 80003, 164);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "ペコリーヌ", 0, 16, 0, 105801, 0, "", "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 80003, 165);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 16, 1, 106001, 0, "", "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 80003, 166);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 17, 1, 106001, 0, "", "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 80003, 167);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 18, 0, 106001, 0, "", "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 80003, 168);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "コッコロ", 0, 12, 1, 105901, 0, "", "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 80003, 169);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 19, 0, 106001, 0, "", "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 80003, 170);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "コッコロ", 0, 13, 1, 105901, 0, "", "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 80003, 171);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 20, 0, 106001, 0, "", "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 80003, 172);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "コッコロ", 0, 14, 1, 105901, 0, "", "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 80003, 173);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 21, 0, 106001, 0, "", "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 80003, 174);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "コッコロ", 0, 15, 1, 105901, 0, "", "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 80003, 175);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 22, 0, 106001, 0, "", "かくし芸？", 80003, 176);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "コッコロ", 0, 16, 1, 105901, 0, "", "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 80003, 177);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 23, 0, 106001, 0, "", "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 80003, 178);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "ペコリーヌ", 0, 17, 0, 105801, 0, "", "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 80003, 179);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 24, 2, 106001, 0, "", "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 80003, 180);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "コッコロ", 0, 17, 1, 105901, 0, "", "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 80003, 181);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "ペコリーヌ", 0, 18, 0, 105801, 0, "", "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 80003, 182);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 25, 2, 106001, 0, "", "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 80003, 183);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "コッコロ", 0, 18, 1, 105901, 0, "", "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 80003, 184);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 26, 2, 106001, 0, "", "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 80003, 185);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 27, 2, 106001, 0, "", "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 80003, 186);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 28, 2, 106001, 0, "", "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 80003, 187);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "ペコリーヌ", 0, 19, 0, 105801, 0, "", "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 80003, 188);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "コッコロ", 0, 19, 1, 105901, 0, "", "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 80003, 189);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 29, 2, 106001, 0, "", "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 80003, 190);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 30, 0, 106001, 0, "", "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 80003, 191);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 31, 0, 106001, 0, "", "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 80003, 192);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 32, 0, 106001, 0, "", "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 80003, 193);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 33, 0, 106001, 0, "", "あー\nあー\n…声、これで大丈夫かしら？", 80003, 194);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 34, 0, 106001, 0, "", "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 80003, 195);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 35, 0, 106001, 0, "", "オイッスー☆", 80003, 196);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 36, 0, 106001, 0, "", "……\nいや、ないわ\nいつもどおりが一番ね、うん", 80003, 197);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 37, 0, 106001, 0, "", "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 80003, 198);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 38, 0, 106001, 0, "", "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 80003, 199);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 20, 0, 105901, 0, "", "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 80003, 200);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 21, 0, 105901, 0, "", "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 80003, 201);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 22, 0, 105901, 0, "", "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 80003, 202);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 23, 0, 105901, 0, "", "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 80003, 203);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 24, 0, 105901, 0, "", "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 80003, 204);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 25, 0, 105901, 0, "", "オイッスー☆", 80003, 205);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 26, 0, 105901, 0, "", "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 80003, 206);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 27, 0, 105901, 0, "", "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 80003, 207);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 28, 0, 105901, 0, "", "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 80003, 208);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 20, 0, 105801, 0, "", "オイッスー☆", 80003, 209);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 21, 0, 105801, 0, "", "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 80003, 210);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 22, 0, 105801, 0, "", "あと６日で2.5周年ですか…", 80003, 211);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 23, 0, 105801, 0, "", "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 80003, 212);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 24, 0, 105801, 0, "", "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 80003, 213);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 25, 0, 105801, 0, "", "オイッスー☆", 80003, 214);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 26, 0, 105801, 0, "", "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 80003, 215);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 27, 0, 105801, 0, "", "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 80003, 216);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 28, 0, 105801, 0, "", "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 80003, 217);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 29, 0, 105801, 0, "", "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 80003, 218);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "ペコリーヌ", 0, 30, 0, 105801, 0, "", "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 80003, 219);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "ペコリーヌ", 0, 31, 0, 105801, 0, "", "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 80003, 220);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 29, 1, 105901, 0, "", "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 80003, 221);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "ペコリーヌ", 0, 32, 0, 105801, 0, "", "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 80003, 222);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "キャル", 0, 39, 2, 106001, 0, "", "…………", 80003, 223);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 30, 1, 105901, 0, "", "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 80003, 224);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "キャル", 0, 40, 2, 106001, 0, "", "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 80003, 225);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "ペコリーヌ", 0, 33, 0, 105801, 0, "", "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 80003, 226);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "キャル", 0, 41, 2, 106001, 0, "", "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 80003, 227);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "ペコリーヌ", 0, 34, 0, 105801, 0, "", "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 80003, 228);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "ペコリーヌ", 0, 35, 0, 105801, 0, "", "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 80003, 229);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "キャル", 0, 42, 2, 106001, 0, "", "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 80003, 230);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 31, 1, 105901, 0, "", "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 80003, 231);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "キャル", 0, 43, 2, 106001, 0, "", "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 80003, 232);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "ペコリーヌ", 0, 36, 0, 105801, 0, "", "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 80003, 233);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 6, 0, 190801, 0, "", "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 80003, 234);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 7, 0, 190801, 0, "", "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 80003, 235);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 8, 0, 190801, 0, "", "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 80003, 236);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 9, 0, 190801, 0, "", "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 80003, 237);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 10, 0, 190801, 0, "", "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 80003, 238);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 11, 0, 190801, 0, "", "次にコッコロさんですが\nこちらは発声練習をしています", 80003, 239);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 12, 0, 190801, 0, "", "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 80003, 240);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 13, 0, 190801, 0, "", "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 80003, 241);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 14, 0, 190801, 0, "", "あれ？\nもしかして緊張で気を失って…？", 80003, 242);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 15, 0, 190801, 0, "", "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 80003, 243);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 16, 0, 190801, 0, "", "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 80003, 244);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "カリン", 0, 17, 0, 190801, 0, "", "それでは\nまた明日お会いしましょう♪", 80003, 245);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "キャル", 0, 44, 0, 106001, 0, "", "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 80003, 246);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "ペコリーヌ", 0, 37, 2, 105801, 0, "", "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 80003, 247);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "コッコロ", 0, 32, 1, 105901, 0, "", "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 80003, 248);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "キャル", 0, 45, 0, 106001, 0, "", "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 80003, 249);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "ペコリーヌ", 0, 38, 2, 105801, 0, "", "ひどいですよぉキャルちゃ～ん！", 80003, 250);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "コッコロ", 0, 33, 1, 105901, 0, "", "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 80003, 251);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "ペコリーヌ", 0, 39, 2, 105801, 0, "", "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 80003, 252);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "キャル", 0, 46, 0, 106001, 0, "", "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 80003, 253);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "コッコロ", 0, 34, 1, 105901, 0, "", "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 80003, 254);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "ペコリーヌ", 0, 40, 2, 105801, 0, "", "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 80003, 255);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "ペコリーヌ", 0, 41, 0, 105801, 0, "", "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 80003, 256);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 47, 2, 106001, 0, "", "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 80003, 257);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "ペコリーヌ", 0, 42, 0, 105801, 0, "", "忘れてきちゃいました☆", 80003, 258);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 48, 2, 106001, 0, "", "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 80003, 259);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "コッコロ", 0, 35, 1, 105901, 0, "", "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 80003, 260);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 49, 2, 106001, 0, "", "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 80003, 261);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "ペコリーヌ", 0, 43, 0, 105801, 0, "", "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 80003, 262);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 50, 2, 106001, 0, "", "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 80003, 263);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "コッコロ", 0, 36, 1, 105901, 0, "", "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 80003, 264);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 51, 2, 106001, 0, "", "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 80003, 265);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "コッコロ", 0, 37, 1, 105901, 0, "", "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 80003, 266);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 52, 2, 106001, 0, "", "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 80003, 267);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "ペコリーヌ", 0, 44, 0, 105801, 0, "", "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 80003, 268);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "コッコロ", 0, 38, 1, 105901, 0, "", "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 80003, 269);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "ペコリーヌ", 0, 45, 0, 105801, 0, "", "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 80003, 270);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 53, 2, 106001, 0, "", "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 80003, 271);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "ペコリーヌ", 0, 46, 0, 105801, 0, "", "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 80003, 272);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 18, 0, 190801, 0, "", "こんにちは\n【ギルド管理協会】のカリンです", 80003, 273);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 19, 0, 190801, 0, "", "昨日の生放送は\nいかがだったでしょうか？", 80003, 274);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 20, 0, 190801, 0, "", "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 80003, 275);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 21, 0, 190801, 0, "", "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 80003, 276);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 22, 0, 190801, 0, "", "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 80003, 277);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 23, 0, 190801, 0, "", "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 80003, 278);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 24, 0, 190801, 0, "", "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 80003, 279);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 25, 0, 190801, 0, "", "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 80003, 280);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 26, 0, 190801, 0, "", "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 80003, 281);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "カリン", 0, 27, 0, 190801, 0, "", "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 80003, 282);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 47, 0, 105801, 0, "", "準備はいいですかっ？\n…いっきますよー！", 80003, 283);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 54, 2, 106001, 0, "", "祝！", 80003, 284);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 39, 1, 105901, 0, "", "2.5周年！", 80003, 285);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 48, 0, 105801, 0, "", "おめでとうございま～す☆", 80003, 286);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 55, 2, 106001, 0, "", "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 80003, 287);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 40, 1, 105901, 0, "", "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 80003, 288);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 49, 0, 105801, 0, "", "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 80003, 289);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 56, 2, 106001, 0, "", "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 80003, 290);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 41, 1, 105901, 0, "", "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 80003, 291);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 57, 2, 106001, 0, "", "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 80003, 292);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 50, 0, 105801, 0, "", "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 80003, 293);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 58, 2, 106001, 0, "", "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 80003, 294);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 51, 0, 105801, 0, "", "記念日だから\n平気平気♪\nやばいですね☆", 80003, 295);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 59, 2, 106001, 0, "", "意味分かんないわよ！", 80003, 296);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 42, 1, 105901, 0, "", "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 80003, 297);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 43, 1, 105901, 0, "", "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 80003, 298);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 52, 0, 105801, 0, "", "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 80003, 299);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 60, 2, 106001, 0, "", "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 80003, 300);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 44, 1, 105901, 0, "", "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 80003, 301);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 53, 0, 105801, 0, "", "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 80003, 302);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ・コッコロ・キャル", 1, 54, 0, 105801, 0, "105901,45,106001,61", "2.5周年\nありがとうございます！", 80003, 303);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 0, "カリン", 0, 1, 1, 190804, 1, "", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 60002, 304);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 60002, 305);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 60002, 306);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 60002, 307);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 60002, 308);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 60002, 309);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 60002, 310);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 60002, 311);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 60002, 312);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 60002, 313);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 60002, 314);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 60002, 315);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 60002, 316);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 60002, 317);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 60002, 318);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 60002, 319);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 60002, 320);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (3, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 60002, 321);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 60002, 322);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 60002, 323);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 60002, 324);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 60002, 325);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 60002, 326);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 60002, 327);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (4, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 60002, 328);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 0, "カリン", 0, 2, 3, 190804, 0, "", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 60002, 329);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "カリン", 0, 3, 3, 190804, 0, "", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 60002, 330);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "コッコロ", 0, 1, 4, 105901, 0, "", "ささ、シェフィさまはこちらへ…", 80005, 331);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "シェフィ", 0, 1, 0, 106401, 0, "", "コッコロさん、どこへ行くの…？\n…え？", 80005, 332);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ペコリーヌ", 0, 1, 3, 105801, 0, "", "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 80005, 333);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "コッコロ", 0, 2, 4, 105901, 0, "", "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 80005, 334);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "シェフィ", 0, 2, 0, 106401, 0, "", "ええと…\n二人とも何をしているの？", 80005, 335);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "キャル", 0, 1, 1, 106001, 0, "", "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 80005, 336);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "シェフィ", 0, 3, 0, 106401, 0, "", "あの日…？", 80005, 337);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ペコリーヌ", 0, 2, 3, 105801, 0, "", "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 80005, 338);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ペコリーヌ", 0, 3, 3, 105801, 0, "", "『プリンセスコネクト！\nRe:Dive』3.5周年！", 80005, 339);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "カリン", 0, 1, 2, 190801, 0, "", "やばいですね☆", 80005, 340);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "キャル", 0, 2, 1, 106001, 0, "", "って、なにしれっと\n参加してんのよカリン！", 80005, 341);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "カリン", 0, 2, 2, 190801, 0, "", "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 80005, 342);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "カリン", 0, 3, 2, 190801, 0, "", "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 80005, 343);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "ペコリーヌ", 0, 4, 3, 105801, 0, "", "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 80005, 344);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 1, "シェフィ", 0, 4, 0, 106401, 0, "", "えっ！？　ええとええと…！\nあ、あと14日…！", 80005, 345);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "ペコリーヌ", 0, 5, 3, 105801, 0, "", "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 80005, 346);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "コッコロ", 0, 3, 1, 105901, 0, "", "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 80005, 347);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "コッコロ", 0, 4, 1, 105901, 0, "", "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 80005, 348);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "ペコリーヌ", 0, 6, 3, 105801, 0, "", "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 80005, 349);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "コッコロ", 0, 5, 1, 105901, 0, "", "さすがでございます\nペコリーヌさま", 80005, 350);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 3, 0, 106001, 0, "", "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 80005, 351);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "シェフィ", 0, 5, 2, 106401, 0, "", "しゃなり、しゃなり…\nふふ、案外できるものね", 80005, 352);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "ペコリーヌ", 0, 7, 3, 105801, 0, "", "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 80005, 353);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "コッコロ", 0, 6, 1, 105901, 0, "", "さ、次はキャルさまの\n番でございますよ", 80005, 354);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 4, 0, 106001, 0, "", "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 80005, 355);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "シェフィ", 0, 6, 2, 106401, 0, "", "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 80005, 356);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 5, 0, 106001, 0, "", "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 80005, 357);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 6, 0, 106001, 0, "", "いたたた…もぉお～！\nなんであたしがこんな目に～！", 80005, 358);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "コッコロ", 0, 7, 1, 105901, 0, "", "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 80005, 359);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 2, "キャル", 0, 7, 0, 106001, 0, "", "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 80005, 360);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 8, 1, 105901, 0, "", "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 80005, 361);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 8, 3, 105801, 0, "", "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 80005, 362);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 9, 1, 105901, 0, "", "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 80005, 363);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "シェフィ", 0, 7, 2, 106401, 0, "", "聖学祭…って、なにかしら？", 80005, 364);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "コッコロ", 0, 10, 1, 105901, 0, "", "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 80005, 365);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "キャル", 0, 8, 0, 106001, 0, "", "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 80005, 366);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "キャル", 0, 9, 0, 106001, 0, "", "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 80005, 367);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 9, 3, 105801, 0, "", "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 80005, 368);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "キャル", 0, 10, 0, 106001, 0, "", "いや、そのまんますぎでしょ…", 80005, 369);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "シェフィ", 0, 8, 2, 106401, 0, "", "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 80005, 370);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "ペコリーヌ", 0, 10, 3, 105801, 0, "", "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 80005, 371);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 3, "シェフィ", 0, 9, 2, 106401, 0, "", "えっ？　ええ…\nぎゅーっ…？", 80005, 372);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 11, 0, 106001, 0, "", "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 80005, 373);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "シェフィ", 0, 10, 2, 106401, 0, "", "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 80005, 374);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 12, 0, 106001, 0, "", "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 80005, 375);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "コッコロ", 0, 11, 1, 105901, 0, "", "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 80005, 376);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 13, 0, 106001, 0, "", "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 80005, 377);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "ペコリーヌ", 0, 11, 3, 105801, 0, "", "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 80005, 378);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 14, 0, 106001, 0, "", "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 80005, 379);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 15, 0, 106001, 0, "", "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 80005, 380);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "シェフィ", 0, 11, 2, 106401, 0, "", "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 80005, 381);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "ペコリーヌ", 0, 12, 3, 105801, 0, "", "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 80005, 382);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "コッコロ", 0, 12, 1, 105901, 0, "", "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 80005, 383);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "キャル", 0, 16, 0, 106001, 0, "", "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 80005, 384);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 4, "シェフィ", 0, 12, 2, 106401, 0, "", "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 80005, 385);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ペコリーヌ", 0, 13, 3, 105801, 0, "", "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 80005, 386);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ペコリーヌ", 0, 14, 3, 105801, 0, "", "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 80005, 387);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ペコリーヌ", 0, 15, 3, 105801, 0, "", "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 80005, 388);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 17, 0, 106001, 0, "", "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 80005, 389);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 18, 0, 106001, 0, "", "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 80005, 390);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "シェフィ", 0, 13, 2, 106401, 0, "", "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 80005, 391);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "コッコロ", 0, 13, 1, 105901, 0, "", "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 80005, 392);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ペコリーヌ", 0, 16, 3, 105801, 0, "", "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 80005, 393);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "キャル", 0, 19, 0, 106001, 0, "", "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 80005, 394);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ペコリーヌ", 0, 17, 3, 105801, 0, "", "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 80005, 395);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ペコリーヌ", 0, 18, 3, 105801, 0, "", "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 80005, 396);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 5, "ペコリーヌ", 0, 19, 3, 105801, 0, "", "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 80005, 397);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "カリン", 0, 4, 2, 190801, 0, "", "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 80005, 398);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "カリン", 0, 5, 2, 190801, 0, "", "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 80005, 399);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 20, 1, 106001, 0, "", "…えっ！？", 80005, 400);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "ペコリーヌ", 0, 20, 3, 105801, 0, "", "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 80005, 401);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "シェフィ", 0, 14, 0, 106401, 0, "", "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 80005, 402);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "コッコロ", 0, 14, 4, 105901, 0, "", "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 80005, 403);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "シェフィ", 0, 15, 0, 106401, 0, "", "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 80005, 404);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 21, 1, 106001, 0, "", "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 80005, 405);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "ペコリーヌ", 0, 21, 3, 105801, 0, "", "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 80005, 406);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "キャル", 0, 22, 1, 106001, 0, "", "ぶっ殺すぞ！？", 80005, 407);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 6, "カリン", 0, 6, 2, 190801, 0, "", "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 80005, 408);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "コッコロ", 0, 15, 1, 105901, 0, "", "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 80005, 409);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 23, 0, 106001, 0, "", "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 80005, 410);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "シェフィ", 0, 16, 2, 106401, 0, "", "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 80005, 411);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "ペコリーヌ", 0, 22, 3, 105801, 0, "", "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 80005, 412);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 24, 0, 106001, 0, "", "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 80005, 413);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "シェフィ", 0, 17, 2, 106401, 0, "", "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 80005, 414);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "コッコロ", 0, 16, 1, 105901, 0, "", "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 80005, 415);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "シェフィ", 0, 18, 2, 106401, 0, "", "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 80005, 416);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "コッコロ", 0, 17, 1, 105901, 0, "", "もちろんでございます\n仲良くお勉強いたしましょう", 80005, 417);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 25, 0, 106001, 0, "", "しれっと教育ママが\n生まれてる…？", 80005, 418);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "ペコリーヌ", 0, 23, 3, 105801, 0, "", "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 80005, 419);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 7, "キャル", 0, 26, 0, 106001, 0, "", "え～～～～！？", 80005, 420);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "キャル", 0, 27, 0, 106001, 0, "", "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 80005, 421);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "シェフィ", 0, 19, 2, 106401, 0, "", "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 80005, 422);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 18, 1, 105901, 0, "", "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 80005, 423);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "ペコリーヌ", 0, 24, 3, 105801, 0, "", "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 80005, 424);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "シェフィ", 0, 20, 2, 106401, 0, "", "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 80005, 425);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "キャル", 0, 28, 0, 106001, 0, "", "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 80005, 426);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "コッコロ", 0, 19, 1, 105901, 0, "", "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 80005, 427);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "シェフィ", 0, 21, 2, 106401, 0, "", "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 80005, 428);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "キャル", 0, 29, 0, 106001, 0, "", "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 80005, 429);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "キャル", 0, 30, 0, 106001, 0, "", "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 80005, 430);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "ペコリーヌ", 0, 25, 3, 105801, 0, "", "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 80005, 431);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 8, "キャル", 0, 31, 0, 106001, 0, "", "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 80005, 432);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 22, 0, 106401, 0, "", "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 80005, 433);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 23, 0, 106401, 0, "", "…えっ？\n生放送の意気込み！？\n突然なに！？", 80005, 434);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 24, 0, 106401, 0, "", "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 80005, 435);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 25, 0, 106401, 0, "", "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 80005, 436);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 26, 0, 106401, 0, "", "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 80005, 437);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 27, 0, 106401, 0, "", "それから、それから…！\nぅ、ぅぅっ…！", 80005, 438);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 28, 0, 106401, 0, "", "―びえぇぇ～んっ！！！", 80005, 439);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "コッコロ", 0, 20, 1, 105901, 0, "", "どうされましたか、シェフィさま", 80005, 440);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 29, 0, 106401, 0, "", "ううっ…ひっく…\nママッ…！", 80005, 441);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "コッコロ", 0, 21, 1, 105901, 0, "", "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 80005, 442);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "コッコロ", 0, 22, 1, 105901, 0, "", "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 80005, 443);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 9, "シェフィ", 0, 30, 0, 106401, 0, "", "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 80005, 444);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 23, 0, 105901, 0, "", "主さま\n3.5周年まであと５日\nでございます", 80005, 445);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 24, 0, 105901, 0, "", "ですがその前に\n生放送がありますね", 80005, 446);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 25, 0, 105901, 0, "", "…生放送に向けた\n意気込みでございますか？", 80005, 447);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 26, 0, 105901, 0, "", "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 80005, 448);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 27, 0, 105901, 0, "", "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 80005, 449);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 28, 0, 105901, 0, "", "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 80005, 450);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 29, 0, 105901, 0, "", "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 80005, 451);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 30, 0, 105901, 0, "", "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 80005, 452);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 10, "コッコロ", 0, 31, 0, 105901, 0, "", "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 80005, 453);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ペコリーヌ", 0, 26, 0, 105801, 0, "", "3.5周年まであと４日！\nやばいですね☆", 80005, 454);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "キャル", 0, 32, 1, 106001, 0, "", "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 80005, 455);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ペコリーヌ", 0, 27, 0, 105801, 0, "", "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 80005, 456);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ペコリーヌ", 0, 28, 0, 105801, 0, "", "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 80005, 457);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ペコリーヌ", 0, 29, 0, 105801, 0, "", "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 80005, 458);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "キャル", 0, 33, 1, 106001, 0, "", "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 80005, 459);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ペコリーヌ", 0, 30, 0, 105801, 0, "", "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 80005, 460);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ペコリーヌ", 0, 31, 0, 105801, 0, "", "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 80005, 461);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ペコリーヌ", 0, 32, 0, 105801, 0, "", "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 80005, 462);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "キャル", 0, 34, 1, 106001, 0, "", "だからあたしはやるなんて一言も…", 80005, 463);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "ペコリーヌ", 0, 33, 0, 105801, 0, "", "オイッスー☆", 80005, 464);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 11, "キャル", 0, 35, 1, 106001, 0, "", "聞きなさいっての！！", 80005, 465);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "シェフィ", 0, 31, 2, 106401, 0, "", "3.5周年まであと３日…\nそして生放送は、明日…", 80005, 466);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "シェフィ", 0, 32, 2, 106401, 0, "", "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 80005, 467);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "シェフィ", 0, 33, 2, 106401, 0, "", "おいっす！\n…こうじゃなかったわね\nええと確か…", 80005, 468);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "シェフィ", 0, 34, 2, 106401, 0, "", "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 80005, 469);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "シェフィ", 0, 35, 2, 106401, 0, "", "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 80005, 470);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "ペコリーヌ", 0, 34, 3, 105801, 0, "", "やってますねシェフィちゃん！", 80005, 471);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "キャル", 0, 36, 0, 106001, 0, "", "なによ、考えることは\nみんな同じってこと？", 80005, 472);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "コッコロ", 0, 32, 1, 105901, 0, "", "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 80005, 473);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "シェフィ", 0, 36, 2, 106401, 0, "", "みんな…もちろんよ！", 80005, 474);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 12, "ペコリーヌ", 0, 35, 3, 105801, 0, "", "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 80005, 475);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "カリン", 0, 7, 2, 190801, 0, "", "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 80005, 476);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "カリン", 0, 8, 2, 190801, 0, "", "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 80005, 477);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "ペコリーヌ", 0, 36, 3, 105801, 0, "", "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 80005, 478);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "カリン", 0, 9, 2, 190801, 0, "", "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 80005, 479);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "コッコロ", 0, 33, 4, 105901, 0, "", "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 80005, 480);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "カリン", 0, 10, 2, 190801, 0, "", "さすが【美食殿】のママですね！\nそしてキャルさんは…", 80005, 481);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 37, 1, 106001, 0, "", "大丈夫…大丈夫…\nあたしは大丈夫…", 80005, 482);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "カリン", 0, 11, 2, 190801, 0, "", "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 80005, 483);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "シェフィ", 0, 37, 0, 106401, 0, "", "あうー？", 80005, 484);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "みんな", 1, 37, 4, 105801, 0, "105901,34,106001,38,190801,12", "あ…", 80005, 485);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "キャル", 0, 39, 1, 106001, 0, "", "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 80005, 486);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "コッコロ", 0, 35, 4, 105901, 0, "", "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 80005, 487);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "ペコリーヌ", 0, 38, 3, 105801, 0, "", "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 80005, 488);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 13, "カリン", 0, 13, 2, 190801, 0, "", "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 80005, 489);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "シェフィ", 0, 38, 2, 106401, 0, "", "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 80005, 490);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "ペコリーヌ", 0, 39, 3, 105801, 0, "", "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 80005, 491);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "キャル", 0, 40, 0, 106001, 0, "", "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 80005, 492);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "コッコロ", 0, 36, 1, 105901, 0, "", "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 80005, 493);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "シェフィ", 0, 39, 2, 106401, 0, "", "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 80005, 494);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "ペコリーヌ", 0, 40, 3, 105801, 0, "", "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 80005, 495);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "コッコロ", 0, 37, 1, 105901, 0, "", "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 80005, 496);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "キャル", 0, 41, 0, 106001, 0, "", "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 80005, 497);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "シェフィ", 0, 40, 2, 106401, 0, "", "へ、へぇ…", 80005, 498);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "ペコリーヌ", 0, 41, 3, 105801, 0, "", "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 80005, 499);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "シェフィ", 0, 41, 2, 106401, 0, "", "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 80005, 500);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 14, "シェフィ", 0, 42, 2, 106401, 0, "", "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 80005, 501);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 42, 3, 105801, 0, "", "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 80005, 502);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 42, 1, 106001, 0, "", "祝！", 80005, 503);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 38, 4, 105901, 0, "", "3.5周年！", 80005, 504);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "シェフィ", 0, 43, 0, 106401, 0, "", "おめでとうございます！", 80005, 505);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "カリン", 0, 14, 2, 190801, 0, "", "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 80005, 506);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "カリン", 0, 15, 2, 190801, 0, "", "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 80005, 507);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 43, 3, 105801, 0, "", "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 80005, 508);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 44, 3, 105801, 0, "", "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 80005, 509);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 43, 1, 106001, 0, "", "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 80005, 510);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "キャル", 0, 44, 1, 106001, 0, "", "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 80005, 511);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "シェフィ", 0, 44, 0, 106401, 0, "", "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 80005, 512);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "シェフィ", 0, 45, 0, 106401, 0, "", "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 80005, 513);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 39, 4, 105901, 0, "", "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 80005, 514);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 40, 4, 105901, 0, "", "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 80005, 515);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "コッコロ", 0, 41, 4, 105901, 0, "", "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 80005, 516);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "ペコリーヌ", 0, 45, 3, 105801, 0, "", "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 80005, 517);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 15, "みんな", 1, 46, 4, 105801, 0, "105901,42,106001,45,106401,46,190801,16", "これからも\nよろしくおねがいします！", 80005, 518);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (0, 0, "カリン", 0, 1, 1, 190804, 1, "", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 60003, 519);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 60003, 520);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 60003, 521);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 60003, 522);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 60003, 523);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (1, 0, "", 0, 0, 2, 0, 2, "", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 60003, 524);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 60003, 525);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 60003, 526);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 60003, 527);
INSERT INTO `v1_c5a03aaee70559de2b41a3da79d7da16336eff3ff62ccf730227c40476a4d5f1` VALUES (2, 0, "", 0, 0, 2, 0, 1, "", "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 60003, 528);
