INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "キャルの絶品五つ星バーガー", "キャルの\n絶品五つ星バーガー", 52003, 1, 0, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", 2, 1, "平凡な未鑑定トレジャーを1個獲得する。", "このターンに振るダイスの目が必ず「5」になる。", 20004, 1, 0, 1, 10, 51003);
