INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "アユミの隠密ガトーインビジブル", "アユミの隠密\nガトーインビジブル", 52024, 3, 0, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 8, 0, "次にダイスを振った際、3マス多く進める。", "RANK2のマイルショップを開く。", 2, 3, 0, 3, 11, 51024);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "アネモネの花爛漫ステーキサラダ", "アネモネの\n花爛漫ステーキサラダ", 52025, 3, 0, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", 9, 1, "なし", "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 3, 2, 0, 0, 0, 51025);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "アンナの煉獄蕃茄煮混沌卵", "アンナの\n煉獄蕃茄煮混沌卵", 52026, 1, 0, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", 4, 0, "福引券を2枚獲得する。", "ランダムなイベントを発生させる。", 1, 3, 0, 8, 10, 51026);
