INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "リンのナッツ入りぐうたらあんパン", "リンの\nナッツ入りぐうたらあんパン", 52028, 3, 0, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 2, 1, "なし", "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 30003, 1, 0, 9, 10, 51028);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "シェフィの氷晶煌めく手作りアイス", "シェフィの\n氷晶煌めく手作りアイス", 52029, 2, 0, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 3, 1, "なし", "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 30004, 1, 0, 0, 0, 51029);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "ミソラの気まぐれ味噌ラーメン★", "ミソラの\n気まぐれ味噌ラーメン★", 52030, 1, 0, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 12, 3, "なし", "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 1, 3, 0, 0, 0, 51030);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "イロハの最強クワガタゼリー（人間用）", "イロハの\n最強クワガタゼリー（人間用）", 52031, 3, 0, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 2, 1, "なし", "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 20009, 1, 0, 0, 0, 51031);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "グレイスの目ん玉飛び出るカップケーキ", "グレイスの\n目ん玉飛び出るカップケーキ", 52032, 3, 0, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 9, 2, "次にダイスを振った際、3マス多く進める。", "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 4, 2, 0, 3, 11, 51032);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "ソノの今日は特別ご褒美オムライス", "ソノの今日は特別ご褒美オムライス", 52033, 1, 0, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", 8, 0, "なし", "RANK3のマイルショップを開く。", 3, 3, 0, 0, 0, 51033);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "ジータのグリルド・グラッジチャンク", "ジータの\nグリルド・グラッジチャンク", 52034, 1, 0, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", 9, 2, "平凡な未鑑定トレジャーを1個獲得する。", "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, 2, 0, 1, 10, 51034);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "ルイズマリーの成功しすぎてるフライ", "ルイズマリーの\n成功しすぎてるフライ", 52035, 1, 0, "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。", 4, 0, "貴重な未鑑定トレジャーを1個獲得する。", "ランダムなイベントを発生させる。", 1, 3, 0, 10, 10, 51035);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "ホウオウの食べる権利をあげます！饅頭", "ホウオウの\n食べる権利をあげます！饅頭", 52036, 3, 1, "ジオ・ゲヘナの空の覇者にして煉華のホウオウが\n己への信仰心を集めるべく作らせたお菓子。中の\n餡はしっとり甘く、上品な味わい。ジオ・ゲヘナ\n銘菓の名を恣にする日も近い、かもしれない。", 7, 3, "なし", "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「12」に、次とさらにその次のターンに振るダイスの目を必ず「1」にする。", 1, 1, 0, 0, 0, 51036);
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES (500, "魔法少女ナナカちゃんウエハース", "魔法少女\nナナカちゃんウエハース", 52037, 3, 0, "ナナカ考案＆監修のウエハース。おまけシールの\nコレクション性を高くした結果ランドソル中で\n大ブーム…だが肝心の菓子が棄てられる事態に。\n解決のため、王女が自ら口を開いたのだった。", 6, 0, "平凡な未鑑定トレジャーを3個獲得する。", "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計1回止まるまで継続する。", 1, 2, 1, 11, 10, 51037);
