INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ペコリーヌの満腹ツインおにぎり", 52001, 0, 1, 1, "500マイルを獲得する。", 51001, 1, 2, 500, 10, 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 2, "ペコリーヌの\n満腹ツインおにぎり", 0, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("コッコロのゆるりと休憩サラダ", 52002, 0, 3, 1, "500マイルを獲得する。", 51002, 1, 2, 500, 10, 2, "このターンに振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 30001, "コッコロの\nゆるりと休憩サラダ", 0, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("キャルの絶品五つ星バーガー", 52003, 0, 2, 1, "平凡な未鑑定トレジャーを1個獲得する。", 51003, 1, 1, 500, 10, 1, "このターンに振るダイスの目が必ず「5」になる。", 20004, "キャルの\n絶品五つ星バーガー", 0, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("リリのコツコツまい進かしわ餅", 52004, 0, 2, 1, "800マイルを獲得する。", 51004, 3, 4, 500, 10, 3, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 20001, "リリのコツコツ\nまい進かしわ餅", 0, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("クリアの彩り華やかバラエティ弁当", 52005, 0, 4, 3, "次にダイスを振った際、1マス多く進める。", 51005, 0, 1, 500, 11, 3, "ランダムなイベントを発生させる。", 1, "クリアの彩り華やか\nバラエティ弁当", 0, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("プレシアの食いしん坊ぶーぶー肉まん", 52006, 0, 2, 1, "800マイルを獲得する。", 51006, 1, 4, 500, 10, 1, "このターンに振るダイスの目が「4・5・6」のみになる。", 20002, "プレシアの食いしん坊\nぶーぶー肉まん", 0, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("カヤとイノリの食欲倍増ケバブサンド", 52007, 0, 7, 1, "福引券を1枚獲得する。", 51007, 2, 3, 500, 10, 1, "このターンに振るダイスの目を必ず「3」に、次のターンに振るダイスの目を必ず「6」にする。", 1, "カヤとイノリの\n食欲倍増ケバブサンド", 0, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("タマキの金運招くレインボーたい焼き", 52008, 0, 5, 2, "500マイルを獲得する。", 51008, 0, 2, 500, 10, 2, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 200, "タマキの金運招く\nレインボーたい焼き", 1, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("クローチェのフルパワーポテトサラダ", 52009, 1, 7, 1, "平凡な未鑑定トレジャーを1個獲得する。", 51009, 2, 1, 500, 10, 2, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「8」に、次のターンに振るダイスの目を必ず「1」にする。", 1, "クローチェの\nフルパワーポテトサラダ", 0, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("アヤネとクルミの青空ピクニックセット", 52010, 0, 6, 2, "500マイルを獲得する。", 51010, 0, 2, 500, 10, 3, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 1, "アヤネとクルミの\n青空ピクニックセット", 2, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("リマの不思議なスイートアップル", 52011, 0, 7, 1, "福引券を1枚獲得する。", 51011, 2, 3, 500, 10, 3, "このターンに振るダイスの目を必ず「2」に、次のターンに振るダイスの目を必ず「7」にする。", 1, "リマの不思議な\nスイートアップル", 0, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ニノンのオリジナル一二三寿司", 52012, 0, 3, 1, "平凡な未鑑定トレジャーを1個獲得する。", 51012, 1, 1, 500, 10, 2, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 30002, "ニノンのオリジナル\n一二三寿司", 0, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("クレジッタの開運お菓子盛り合わせ", 52013, 0, 8, 3, "800マイルを獲得する。", 51013, 0, 4, 500, 10, 3, "RANK2のマイルショップを開く。", 2, "クレジッタの開運\nお菓子盛り合わせ", 0, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("リノの「時は金時」かき氷", 52014, 0, 9, 1, "次にダイスを振った際、1マス多く進める。", 51014, 2, 1, 500, 11, 2, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, "リノの\n「時は金時」かき氷", 0, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("シズルの愛情盛り盛りハンバーグ", 52015, 0, 1, 1, "次にダイスを振った際、1マス多く進める。", 51015, 1, 1, 500, 11, 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 2, "シズルの愛情\n盛り盛りハンバーグ", 0, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("アオイの疾走サンドイッチ", 52016, 0, 2, 1, "なし", 51016, 1, 0, 500, 0, 3, "このターンに振るダイスの目が必ず「6」になる。", 20005, "アオイの疾走\nサンドイッチ", 0, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("クウカの緊縛触手活け造り", 52017, 1, 2, 1, "なし", 51017, 2, 5, 500, 10, 3, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 1, "クウカの緊縛\n触手活け造り", 0, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ランファの世話焼き三昧肉じゃが", 52018, 0, 2, 1, "1000マイルを獲得する。", 51018, 1, 6, 500, 10, 1, "このターンに振るダイスの目が必ず「3」になる。", 20006, "ランファの世話焼き三昧肉じゃが", 0, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ワカナの節約腹八分目カレー", 52019, 0, 2, 1, "なし", 0, 1, 0, 500, 0, 1, "このターンに振るダイスの目が必ず「8」になる。", 20007, "ワカナの節約腹八分目カレー", 0, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("リノの「時は金時」かき氷", 52020, 0, 9, 2, "次にダイスを振った際、1マス多く進める。", 51014, 2, 1, 500, 11, 2, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, "リノの\n「時は金時」かき氷", 0, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("カスミの真実見抜くボンボン・ショコラ", 52021, 0, 9, 2, "	平凡な未鑑定トレジャーを2個獲得する。", 51021, 3, 7, 500, 10, 3, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 2, "カスミの真実見抜く\nボンボン・ショコラ", 0, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("アキノのゲヘニアンチキンプレート", 52022, 0, 2, 1, "なし", 51022, 3, 0, 500, 0, 1, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 20008, "アキノのゲヘニアン\nチキンプレート", 0, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ミミのコロコロお手玉おにぎり", 52023, 1, 7, 1, "平凡な未鑑定トレジャーを1個獲得する。", 51023, 2, 1, 500, 10, 1, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", 1, "ミミのコロコロ\nお手玉おにぎり", 0, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("アユミの隠密ガトーインビジブル", 52024, 0, 8, 3, "次にダイスを振った際、3マス多く進める。", 51024, 0, 3, 500, 11, 3, "RANK2のマイルショップを開く。", 2, "アユミの隠密\nガトーインビジブル", 0, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("アネモネの花爛漫ステーキサラダ", 52025, 0, 9, 2, "なし", 51025, 1, 0, 500, 0, 3, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 3, "アネモネの\n花爛漫ステーキサラダ", 0, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("アンナの煉獄蕃茄煮混沌卵", 52026, 0, 4, 3, "福引券を2枚獲得する。", 51026, 0, 8, 500, 10, 1, "ランダムなイベントを発生させる。", 1, "アンナの\n煉獄蕃茄煮混沌卵", 0, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ユイの栄養満点まごころ弁当", 52027, 0, 2, 1, "なし", 51027, 2, 0, 500, 0, 3, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 20004, "ユイの\n栄養満点まごころ弁当", 0, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("リンのナッツ入りぐうたらあんパン", 52028, 0, 2, 1, "なし", 51028, 1, 9, 500, 10, 3, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 30003, "リンの\nナッツ入りぐうたらあんパン", 0, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("シェフィの氷晶煌めく手作りアイス", 52029, 0, 3, 1, "なし", 51029, 1, 0, 500, 0, 2, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 30004, "シェフィの\n氷晶煌めく手作りアイス", 0, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ミソラの気まぐれ味噌ラーメン★", 52030, 0, 12, 3, "なし", 51030, 3, 0, 500, 0, 1, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 1, "ミソラの\n気まぐれ味噌ラーメン★", 0, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("イロハの最強クワガタゼリー（人間用）", 52031, 0, 2, 1, "なし", 51031, 1, 0, 500, 0, 3, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 20009, "イロハの\n最強クワガタゼリー（人間用）", 0, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("グレイスの目ん玉飛び出るカップケーキ", 52032, 0, 9, 2, "次にダイスを振った際、3マス多く進める。", 51032, 2, 3, 500, 11, 3, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 4, "グレイスの\n目ん玉飛び出るカップケーキ", 0, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ソノの今日は特別ご褒美オムライス", 52033, 0, 8, 3, "なし", 51033, 0, 0, 500, 0, 1, "RANK3のマイルショップを開く。", 3, "ソノの今日は特別ご褒美オムライス", 0, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ジータのグリルド・グラッジチャンク", 52034, 0, 9, 2, "平凡な未鑑定トレジャーを1個獲得する。", 51034, 2, 1, 500, 10, 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, "ジータの\nグリルド・グラッジチャンク", 0, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ルイズマリーの成功しすぎてるフライ", 52035, 0, 4, 3, "貴重な未鑑定トレジャーを1個獲得する。", 51035, 0, 10, 500, 10, 1, "ランダムなイベントを発生させる。", 1, "ルイズマリーの\n成功しすぎてるフライ", 0, "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("ホウオウの食べる権利をあげます！饅頭", 52036, 1, 7, 1, "なし", 51036, 3, 0, 500, 0, 3, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「12」に、次とさらにその次のターンに振るダイスの目を必ず「1」にする。", 1, "ホウオウの\n食べる権利をあげます！饅頭", 0, "ジオ・ゲヘナの空の覇者にして煉華のホウオウが\n己への信仰心を集めるべく作らせたお菓子。中の\n餡はしっとり甘く、上品な味わい。ジオ・ゲヘナ\n銘菓の名を恣にする日も近い、かもしれない。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("魔法少女ナナカちゃんウエハース", 52037, 0, 6, 2, "平凡な未鑑定トレジャーを3個獲得する。", 51037, 0, 11, 500, 10, 3, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計1回止まるまで継続する。", 1, "魔法少女\nナナカちゃんウエハース", 1, "ナナカ考案＆監修のウエハース。おまけシールの\nコレクション性を高くした結果ランドソル中で\n大ブーム…だが肝心の菓子が棄てられる事態に。\n解決のため、王女が自ら口を開いたのだった。");
INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("アリサがお届け♪リノセウスの包み焼き", 52038, 0, 1, 1, "福引券を1枚獲得する。", 51038, 1, 3, 500, 10, 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 2, "アリサがお届け♪\nリノセウスの包み焼き", 0, "アリサ特製の包み焼き。伝承の怪物とよく似た\n魔物を材料にしているため、いつしかその怪物と\n同じ名で呼ばれるようになったという。鋭い鎌と\n火傷に気を付けながら、熱々を召し上がれ。");
