INSERT INTO `v1_c704bc8bcfe1fe83563f4ad9c6cb6cde60194b4f4f78f273cbc2fc5164fb6e1d` VALUES ("[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", 1, 500, 0, "ミミのコロコロ\nお手玉おにぎり", 10, 51023, 7, 1, 1, 2, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", 1, "ミミのコロコロお手玉おにぎり", 1, 52023, "平凡な未鑑定トレジャーを1個獲得する。");
