INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 2, 0, 51001, 1, 1, 2, "ペコリーヌの\n満腹ツインおにぎり", 10, 1, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", "ペコリーヌの満腹ツインおにぎり", 500, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 52001, "500マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 30001, 0, 51002, 2, 1, 2, "コッコロの\nゆるりと休憩サラダ", 10, 3, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", "コッコロのゆるりと休憩サラダ", 500, "次に振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 52002, "500マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 20004, 0, 51003, 1, 1, 1, "キャルの\n絶品五つ星バーガー", 10, 2, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", "キャルの絶品五つ星バーガー", 500, "次に振るダイスの目が必ず「5」になる。", 52003, "平凡な未鑑定トレジャーを1個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (3, 0, 20001, 0, 51004, 3, 1, 4, "リリのコツコツ\nまい進かしわ餅", 10, 2, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", "リリのコツコツまい進かしわ餅", 500, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 52004, "800マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 0, 1, 0, 51005, 3, 3, 1, "クリアの彩り華やか\nバラエティ弁当", 11, 4, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", "クリアの彩り華やかバラエティ弁当", 500, "ランダムなイベントを発生させる。", 52005, "次にダイスを振った際、1マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 20002, 0, 51006, 1, 1, 4, "プレシアの食いしん坊\nぶーぶー肉まん", 10, 2, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", "プレシアの食いしん坊ぶーぶー肉まん", 500, "次に振るダイスの目が「4・5・6」のみになる。", 52006, "800マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, 0, 51007, 1, 1, 3, "カヤとイノリの\n食欲倍増ケバブサンド", 10, 7, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", "カヤとイノリの食欲倍増ケバブサンド", 500, "次に振るダイスの目を必ず「3」に、その次に振るダイスの目を必ず「6」にする。", 52007, "福引券を1枚獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 1, 200, 0, 51008, 2, 2, 2, "タマキの金運招く\nレインボーたい焼き", 10, 5, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", "タマキの金運招くレインボーたい焼き", 500, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 52008, "500マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, 1, 51009, 2, 1, 1, "クローチェの\nフルパワーポテトサラダ", 10, 7, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", "クローチェのフルパワーポテトサラダ", 500, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「8」に、その次に振るダイスの目を必ず「1」にする。", 52009, "平凡な未鑑定トレジャーを1個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 2, 1, 0, 51010, 3, 2, 2, "アヤネとクルミの\n青空ピクニックセット", 10, 6, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", "アヤネとクルミの青空ピクニックセット", 500, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 52010, "500マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, 0, 51011, 3, 1, 3, "リマの不思議な\nスイートアップル", 10, 7, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", "リマの不思議なスイートアップル", 500, "次に振るダイスの目を必ず「2」に、その次に振るダイスの目を必ず「7」にする。", 52011, "福引券を1枚獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 30002, 0, 51012, 2, 1, 1, "ニノンのオリジナル\n一二三寿司", 10, 3, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", "ニノンのオリジナル一二三寿司", 500, "次に振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 52012, "平凡な未鑑定トレジャーを1個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 0, 2, 0, 51013, 3, 3, 4, "クレジッタの開運\nお菓子盛り合わせ", 10, 8, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", "クレジッタの開運お菓子盛り合わせ", 500, "RANK2のマイルショップを開く。", 52013, "800マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, 0, 51014, 2, 1, 1, "リノの\n「時は金時」かき氷", 11, 9, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "リノの「時は金時」かき氷", 500, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 52014, "次にダイスを振った際、1マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 2, 0, 51015, 1, 1, 1, "シズルの愛情\n盛り盛りハンバーグ", 11, 1, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", "シズルの愛情盛り盛りハンバーグ", 500, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 52015, "次にダイスを振った際、1マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 20005, 0, 51016, 3, 1, 0, "アオイの疾走\nサンドイッチ", 0, 2, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", "アオイの疾走サンドイッチ", 500, "次に振るダイスの目が必ず「6」になる。", 52016, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, 1, 51017, 3, 1, 5, "クウカの緊縛\n触手活け造り", 10, 2, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", "クウカの緊縛触手活け造り", 500, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 52017, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 20006, 0, 51018, 1, 1, 6, "ランファの世話焼き三昧肉じゃが", 10, 2, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", "ランファの世話焼き三昧肉じゃが", 500, "次に振るダイスの目が必ず「3」になる。", 52018, "1000マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 20007, 0, 0, 1, 1, 0, "ワカナの節約腹八分目カレー", 0, 2, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", "ワカナの節約腹八分目カレー", 500, "次に振るダイスの目が必ず「8」になる。", 52019, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, 0, 51014, 2, 2, 1, "リノの\n「時は金時」かき氷", 11, 9, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "リノの「時は金時」かき氷", 500, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 52020, "次にダイスを振った際、1マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (3, 0, 2, 0, 51021, 3, 2, 7, "カスミの真実見抜く\nボンボン・ショコラ", 10, 9, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", "カスミの真実見抜くボンボン・ショコラ", 500, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 52021, "	平凡な未鑑定トレジャーを2個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (3, 0, 20008, 0, 51022, 1, 1, 0, "アキノのゲヘニアン\nチキンプレート", 0, 2, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", "アキノのゲヘニアンチキンプレート", 500, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 52022, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, 1, 51023, 1, 1, 1, "ミミのコロコロ\nお手玉おにぎり", 10, 7, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", "ミミのコロコロお手玉おにぎり", 500, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「6」に、その次に振るダイスの目を必ず「3」にする。", 52023, "平凡な未鑑定トレジャーを1個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 0, 2, 0, 51024, 3, 3, 3, "アユミの隠密\nガトーインビジブル", 11, 8, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", "アユミの隠密ガトーインビジブル", 500, "RANK2のマイルショップを開く。", 52024, "次にダイスを振った際、3マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 3, 0, 51025, 3, 2, 0, "アネモネの\n花爛漫ステーキサラダ", 0, 9, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", "アネモネの花爛漫ステーキサラダ", 500, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 52025, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 0, 1, 0, 51026, 1, 3, 8, "アンナの\n煉獄蕃茄煮混沌卵", 10, 4, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", "アンナの煉獄蕃茄煮混沌卵", 500, "ランダムなイベントを発生させる。", 52026, "福引券を2枚獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 20004, 0, 51027, 3, 1, 0, "ユイの\n栄養満点まごころ弁当", 0, 2, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", "ユイの栄養満点まごころ弁当", 500, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 52027, "なし");
