INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "ペコリーヌの満腹ツインおにぎり", 1, 51001, "ペコリーヌの\n満腹ツインおにぎり", 10, 52001, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 2, 0, 2, 500, 1, "500マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "コッコロのゆるりと休憩サラダ", 2, 51002, "コッコロの\nゆるりと休憩サラダ", 10, 52002, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", "次に振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 30001, 0, 2, 500, 3, "500マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "キャルの絶品五つ星バーガー", 1, 51003, "キャルの\n絶品五つ星バーガー", 10, 52003, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", "次に振るダイスの目が必ず「5」になる。", 20004, 0, 1, 500, 2, "平凡な未鑑定トレジャーを1個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (3, 0, 1, "リリのコツコツまい進かしわ餅", 3, 51004, "リリのコツコツ\nまい進かしわ餅", 10, 52004, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 20001, 0, 4, 500, 2, "800マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 0, 3, "クリアの彩り華やかバラエティ弁当", 3, 51005, "クリアの彩り華やか\nバラエティ弁当", 11, 52005, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", "ランダムなイベントを発生させる。", 1, 0, 1, 500, 4, "次にダイスを振った際、1マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "プレシアの食いしん坊ぶーぶー肉まん", 1, 51006, "プレシアの食いしん坊\nぶーぶー肉まん", 10, 52006, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", "次に振るダイスの目が「4・5・6」のみになる。", 20002, 0, 4, 500, 2, "800マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, "カヤとイノリの食欲倍増ケバブサンド", 1, 51007, "カヤとイノリの\n食欲倍増ケバブサンド", 10, 52007, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", "次に振るダイスの目を必ず「3」に、その次に振るダイスの目を必ず「6」にする。", 1, 0, 3, 500, 7, "福引券を1枚獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 1, 2, "タマキの金運招くレインボーたい焼き", 2, 51008, "タマキの金運招く\nレインボーたい焼き", 10, 52008, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", "次にマイルマスに止まった際の獲得マイルが200%アップする。", 200, 0, 2, 500, 5, "500マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, "クローチェのフルパワーポテトサラダ", 2, 51009, "クローチェの\nフルパワーポテトサラダ", 10, 52009, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「8」に、その次に振るダイスの目を必ず「1」にする。", 1, 1, 1, 500, 7, "平凡な未鑑定トレジャーを1個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 2, 2, "アヤネとクルミの青空ピクニックセット", 3, 51010, "アヤネとクルミの\n青空ピクニックセット", 10, 52010, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 1, 0, 2, 500, 6, "500マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, "リマの不思議なスイートアップル", 3, 51011, "リマの不思議な\nスイートアップル", 10, 52011, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", "次に振るダイスの目を必ず「2」に、その次に振るダイスの目を必ず「7」にする。", 1, 0, 3, 500, 7, "福引券を1枚獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "ニノンのオリジナル一二三寿司", 2, 51012, "ニノンのオリジナル\n一二三寿司", 10, 52012, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", "次に振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 30002, 0, 1, 500, 3, "平凡な未鑑定トレジャーを1個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 0, 3, "クレジッタの開運お菓子盛り合わせ", 3, 51013, "クレジッタの開運\nお菓子盛り合わせ", 10, 52013, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", "RANK2のマイルショップを開く。", 2, 0, 4, 500, 8, "800マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, "リノの「時は金時」かき氷", 2, 51014, "リノの\n「時は金時」かき氷", 11, 52014, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, 0, 1, 500, 9, "次にダイスを振った際、1マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "シズルの愛情盛り盛りハンバーグ", 1, 51015, "シズルの愛情\n盛り盛りハンバーグ", 11, 52015, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 2, 0, 1, 500, 1, "次にダイスを振った際、1マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "アオイの疾走サンドイッチ", 3, 51016, "アオイの疾走\nサンドイッチ", 0, 52016, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", "次に振るダイスの目が必ず「6」になる。", 20005, 0, 0, 500, 2, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, "クウカの緊縛触手活け造り", 3, 51017, "クウカの緊縛\n触手活け造り", 10, 52017, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 1, 1, 5, 500, 2, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "ランファの世話焼き三昧肉じゃが", 1, 51018, "ランファの世話焼き三昧肉じゃが", 10, 52018, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", "次に振るダイスの目が必ず「3」になる。", 20006, 0, 6, 500, 2, "1000マイルを獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 1, "ワカナの節約腹八分目カレー", 1, 0, "ワカナの節約腹八分目カレー", 0, 52019, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", "次に振るダイスの目が必ず「8」になる。", 20007, 0, 0, 500, 2, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 2, "リノの「時は金時」かき氷", 2, 51014, "リノの\n「時は金時」かき氷", 11, 52020, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, 0, 1, 500, 9, "次にダイスを振った際、1マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (3, 0, 2, "カスミの真実見抜くボンボン・ショコラ", 3, 51021, "カスミの真実見抜く\nボンボン・ショコラ", 10, 52021, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 2, 0, 7, 500, 9, "	平凡な未鑑定トレジャーを2個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (3, 0, 1, "アキノのゲヘニアンチキンプレート", 1, 51022, "アキノのゲヘニアン\nチキンプレート", 0, 52022, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 20008, 0, 0, 500, 2, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, "ミミのコロコロお手玉おにぎり", 1, 51023, "ミミのコロコロ\nお手玉おにぎり", 10, 52023, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「6」に、その次に振るダイスの目を必ず「3」にする。", 1, 1, 1, 500, 7, "平凡な未鑑定トレジャーを1個獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 0, 3, "アユミの隠密ガトーインビジブル", 3, 51024, "アユミの隠密\nガトーインビジブル", 11, 52024, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", "RANK2のマイルショップを開く。", 2, 0, 3, 500, 8, "次にダイスを振った際、3マス多く進める。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (1, 0, 2, "アネモネの花爛漫ステーキサラダ", 3, 51025, "アネモネの\n花爛漫ステーキサラダ", 0, 52025, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 3, 0, 0, 500, 9, "なし");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (0, 0, 3, "アンナの煉獄蕃茄煮混沌卵", 1, 51026, "アンナの\n煉獄蕃茄煮混沌卵", 10, 52026, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", "ランダムなイベントを発生させる。", 1, 0, 8, 500, 4, "福引券を2枚獲得する。");
INSERT INTO `v1_c841fbdb8a5d91b341d4588e4782a494122bcd24030e24355eb66506fe541350` VALUES (2, 0, 1, "ユイの栄養満点まごころ弁当", 3, 51027, "ユイの\n栄養満点まごころ弁当", 0, 52027, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 20004, 0, 0, 500, 2, "なし");
