INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 0.0, 24, 10046, 24001, 102301, "2020/07/31 12:00:00", "アヤネ", 2, "2030/12/17 14:59:59", 401, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 0.0, 24, 10046, 24002, 102301, "2020/07/31 12:00:00", "アヤネ", 1, "2030/12/17 14:59:59", 402, 2.21);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 0.0, 24, 10046, 24003, 102301, "2020/07/31 12:00:00", "アヤネ", 2, "2030/12/17 14:59:59", 403, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 0.0, 24, 10046, 24004, 192201, "2020/07/31 12:00:00", "アサヒ", 2, "2030/12/17 14:59:59", 701, 1.99);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 0.0, 24, 10046, 24005, 192201, "2020/07/31 12:00:00", "アサヒ", 1, "2030/12/17 14:59:59", 702, 2.21);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 0.0, 24, 10046, 24006, 192201, "2020/07/31 12:00:00", "アサヒ", 2, "2030/12/17 14:59:59", 703, 3.33);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 0.0, 24, 10046, 24007, 192301, "2020/07/31 12:00:00", "部員３", 2, "2030/12/17 14:59:59", 801, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 0.0, 24, 10046, 24008, 192301, "2020/07/31 12:00:00", "部員３", 1, "2030/12/17 14:59:59", 802, 2.68);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 0.0, 24, 10046, 24009, 192301, "2020/07/31 12:00:00", "部員３", 2, "2030/12/17 14:59:59", 803, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "やった！", 0.0, 24, 10046, 24010, 192501, "2020/07/31 12:00:00", "", 1, "2030/12/17 14:59:59", 501, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "勝ったよ！", 0.0, 24, 10046, 24011, 192501, "2020/07/31 12:00:00", "", 1, "2030/12/17 14:59:59", 502, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "いい調子！", 0.0, 24, 10046, 24012, 192501, "2020/07/31 12:00:00", "", 1, "2030/12/17 14:59:59", 503, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 0.0, 24, 10046, 24013, 113501, "2020/07/31 12:00:00", "ミサト", 6, "2030/12/17 14:59:59", 111, 5.33);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 0.0, 24, 10046, 24014, 113501, "2020/07/31 12:00:00", "ミサト", 1, "2030/12/17 14:59:59", 112, 6.85);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 0.0, 24, 10046, 24015, 113501, "2020/07/31 12:00:00", "ミサト", 1, "2030/12/17 14:59:59", 113, 3.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 0.0, 24, 10046, 24016, 192401, "2020/07/31 12:00:00", "ダイチ", 1, "2030/12/17 14:59:59", 611, 6.11);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 0.0, 24, 10046, 24017, 192401, "2020/07/31 12:00:00", "ダイチ", 1, "2030/12/17 14:59:59", 612, 6.75);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 0.0, 24, 10046, 24018, 192401, "2020/07/31 12:00:00", "ダイチ", 2, "2030/12/17 14:59:59", 613, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 0.0, 24, 10046, 24019, 113401, "2020/07/31 12:00:00", "ハツネ", 2, "2030/12/17 14:59:59", 211, 4.52);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 0.0, 24, 10046, 24020, 113401, "2020/07/31 12:00:00", "ハツネ", 2, "2030/12/17 14:59:59", 212, 3.89);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 0.0, 24, 10046, 24021, 113401, "2020/07/31 12:00:00", "ハツネ", 1, "2030/12/17 14:59:59", 213, 13.49);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 0.0, 24, 10046, 24022, 106501, "2020/07/31 12:00:00", "カヤ", 1, "2030/12/17 14:59:59", 311, 3.17);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, 0.0, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 0.0, 24, 10046, 24023, 106501, "2020/07/31 12:00:00", "カヤ", 1, "2030/12/17 14:59:59", 312, 4.42);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 0.0, 24, 10046, 24024, 106501, "2020/07/31 12:00:00", "カヤ", 6, "2030/12/17 14:59:59", 313, 2.89);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (3, 0.0, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 0.0, 24, 10046, 24025, 102301, "2020/07/31 12:00:00", "アヤネ", 4, "2030/12/17 14:59:59", 421, 3.71);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 0.0, 24, 10046, 24026, 192201, "2020/07/31 12:00:00", "アサヒ", 2, "2030/12/17 14:59:59", 721, 5.1);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (3, 0.0, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 0.0, 24, 10046, 24027, 192301, "2020/07/31 12:00:00", "部員３", 1, "2030/12/17 14:59:59", 821, 4.34);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, 0.0, "いい勝負だったよ\nまたやろうね", 0.0, 24, 10046, 24028, 192501, "2020/07/31 12:00:00", "", 1, "2030/12/17 14:59:59", 521, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, -360.0, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 0.0, 24, 10046, 24029, 113501, "2020/07/31 12:00:00", "ミサト", 1, "2030/12/17 14:59:59", 101, 5.62);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, -360.0, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 0.0, 24, 10046, 24030, 113501, "2020/07/31 12:00:00", "ミサト", 1, "2030/12/17 14:59:59", 102, 9.58);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, -360.0, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 0.0, 24, 10046, 24031, 113501, "2020/07/31 12:00:00", "ミサト", 2, "2030/12/17 14:59:59", 103, 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (1, -360.0, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 0.0, 24, 10046, 24032, 113501, "2020/07/31 12:00:00", "ミサト", 2, "2030/12/17 14:59:59", 104, 9.13);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES (2, -360.0, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 0.0, 24, 10046, 24033, 113501, "2020/07/31 12:00:00", "ミサト", 1, "2030/12/17 14:59:59", 105, 8.27);
