INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 102301, 0.0, "アヤネ", 2, 24001, 401, 0.0, 24, 10046, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 102301, 0.0, "アヤネ", 1, 24002, 402, 2.21, 24, 10046, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 102301, 0.0, "アヤネ", 2, 24003, 403, 0.0, 24, 10046, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 192201, 0.0, "アサヒ", 2, 24004, 701, 1.99, 24, 10046, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 1, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 192201, 0.0, "アサヒ", 1, 24005, 702, 2.21, 24, 10046, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 192201, 0.0, "アサヒ", 2, 24006, 703, 3.33, 24, 10046, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 1, "2020/07/31 12:00:00", 0.0);
