INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 192301, 0.0, "部員３", 1, 24008, 802, 2.68, 24, 10046, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 192301, 0.0, "部員３", 2, 24009, 803, 0.0, 24, 10046, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 2, "2020/07/31 12:00:00", 0.0);
