INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 113501, 0.0, "ミサト", 6, 24013, 111, 5.33, 24, 10046, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 113501, 0.0, "ミサト", 1, 24014, 112, 6.85, 24, 10046, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 113501, 0.0, "ミサト", 1, 24015, 113, 3.0, 24, 10046, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 192401, 0.0, "ダイチ", 1, 24016, 611, 6.11, 24, 10046, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 192401, 0.0, "ダイチ", 1, 24017, 612, 6.75, 24, 10046, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 192401, 0.0, "ダイチ", 2, 24018, 613, 0.0, 24, 10046, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 113401, 0.0, "ハツネ", 2, 24019, 211, 4.52, 24, 10046, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 1, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 113401, 0.0, "ハツネ", 2, 24020, 212, 3.89, 24, 10046, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 1, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 113401, 0.0, "ハツネ", 1, 24021, 213, 13.49, 24, 10046, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 106501, 0.0, "カヤ", 1, 24022, 311, 3.17, 24, 10046, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 2, "2020/07/31 12:00:00", 0.0);
INSERT INTO `v1_c85603f6750c0194e95058dd744a227769867aceaf83f4e3daf4e05833629faf` VALUES ("2030/12/17 14:59:59", 106501, 0.0, "カヤ", 1, 24023, 312, 4.42, 24, 10046, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 2, "2020/07/31 12:00:00", 0.0);
