INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1, 100101, 1, 1, 1, 0, 1, "ここに困ってる人は\nいないよね…？\nよかった♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2, 100101, 1, 2, 1, 0, 1, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3, 100101, 1, 3, 1, 0, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4, 100101, 1, 4, 1, 0, 1, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5, 100101, 1, 5, 1, 0, 1, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6, 100101, 1, 101, 4, 0, 1, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7, 100101, 1, 102, 8, 0, 1, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8, 100101, 2, 1, 1, 0, 1, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (9, 100101, 2, 2, 1, 0, 1, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (10, 100101, 2, 3, 1, 0, 1, "えっとね…しっぽは\n触っちゃダメだよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (11, 100101, 2, 4, 1, 0, 1, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (12, 100101, 2, 101, 4, 0, 1, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (13, 100101, 2, 102, 8, 0, 1, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (14, 100101, 2, 1, 1, 1, 1, "おはよー！\n今日も元気に\nいっちゃうよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (15, 100101, 2, 2, 1, 2, 1, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (16, 100101, 2, 3, 1, 3, 1, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (17, 100101, 3, 1, 1, 0, 1, "ちゃんとケアして\n傷が残らないように\nしないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (18, 100101, 3, 2, 1, 0, 1, "一回休むと\nこのままずるずる\nいきそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (19, 100101, 3, 3, 1, 0, 1, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (20, 100101, 3, 4, 1, 0, 1, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (21, 100101, 3, 5, 1, 0, 1, "あったかい所に\nいると体が丸く\nなっちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (22, 100101, 3, 6, 1, 0, 1, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (23, 100101, 3, 7, 1, 0, 1, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (24, 100101, 3, 8, 1, 0, 1, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (26, 100102, 1, 1, 1, 0, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (27, 100102, 1, 2, 1, 0, 1, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (28, 100102, 1, 3, 1, 0, 1, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (29, 100102, 1, 4, 1, 0, 1, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (30, 100102, 1, 5, 1, 0, 1, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (31, 100102, 1, 101, 8, 0, 1, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (32, 100102, 1, 102, 8, 0, 1, "…なんであたし\n騎士くんばっかり\n見てるんだろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (33, 100102, 2, 1, 1, 0, 1, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (34, 100102, 2, 2, 1, 0, 1, "触るのって楽しい？\n…そっか\n少しだけだよ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (35, 100102, 2, 3, 1, 0, 1, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (36, 100102, 2, 4, 1, 0, 1, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (37, 100102, 2, 101, 8, 0, 1, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (38, 100102, 2, 102, 8, 0, 1, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (39, 100102, 2, 1, 1, 1, 1, "朝ごはんと運動！\nこれがあたしの\n元気の素だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (40, 100102, 2, 2, 1, 2, 1, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (41, 100102, 2, 3, 1, 3, 1, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (42, 100102, 3, 1, 1, 0, 1, "これでよし♪\n騎士くん\n気づくかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (43, 100102, 3, 2, 1, 0, 1, "一回休むと\nこのままずるずる\nいきそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (44, 100102, 3, 3, 1, 0, 1, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (45, 100102, 3, 4, 1, 0, 1, "こういうの\nなんか気になって\nじーって見ちゃうんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (46, 100102, 3, 5, 1, 0, 1, "あったかい所に\nいると体が丸く\nなっちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (47, 100102, 3, 6, 1, 0, 1, "いつ何が起きるか\n分からないし\nもっと力をつけないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (48, 100102, 3, 7, 1, 0, 1, "お腹が減ると\nなんか力が\n出ないんだよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (49, 100102, 3, 8, 1, 0, 1, "色んなことを\n知っておくのは\n大事だよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (51, 100201, 1, 1, 1, 0, 1, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (52, 100201, 1, 2, 1, 0, 1, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (53, 100201, 1, 3, 1, 0, 1, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (54, 100201, 1, 4, 1, 0, 1, "戦いは\n得意じゃないけど\nお料理なら…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (55, 100201, 1, 5, 1, 0, 1, "こういう平和な\n時間が、もっと\n続くといいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (56, 100201, 1, 101, 4, 0, 1, "わたし…\nもうちょっとだけ勇気\n出してみようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (57, 100201, 1, 102, 8, 0, 1, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (58, 100201, 2, 1, 1, 0, 1, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (59, 100201, 2, 2, 1, 0, 1, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (60, 100201, 2, 3, 1, 0, 1, "困ったことが\nあったら\nいつでも話してね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (61, 100201, 2, 4, 1, 0, 1, "え！　む、虫っ！？\n取って取って！\nはやく～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (62, 100201, 2, 101, 4, 0, 1, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (63, 100201, 2, 102, 8, 0, 1, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (64, 100201, 2, 1, 1, 1, 1, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (65, 100201, 2, 2, 1, 2, 1, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (66, 100201, 2, 3, 1, 3, 1, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (67, 100201, 3, 1, 1, 0, 1, "よし…\nこれなら\n褒めてもらえるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (68, 100201, 3, 2, 1, 0, 1, "がんばることも\n必要だけど、\nお休みも大切だよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (69, 100201, 3, 3, 1, 0, 1, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (70, 100201, 3, 4, 1, 0, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (71, 100201, 3, 5, 1, 0, 1, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (72, 100201, 3, 6, 1, 0, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (73, 100201, 3, 7, 1, 0, 1, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (74, 100201, 3, 8, 1, 0, 1, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (76, 100202, 1, 1, 1, 0, 1, "洗濯と掃除\nどっちから\nはじめよう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (77, 100202, 1, 2, 1, 0, 1, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (78, 100202, 1, 3, 1, 0, 1, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (79, 100202, 1, 4, 1, 0, 1, "今日はどんな料理に\n挑戦しよう…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (80, 100202, 1, 5, 1, 0, 1, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (81, 100202, 1, 101, 8, 0, 1, "みんなのことが大好き\nも、もちろん\n騎士クンのことも…\nな、仲間として！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (82, 100202, 1, 102, 8, 0, 1, "騎士クンのそばに\nいられるだけで\n幸せだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (83, 100202, 2, 1, 1, 0, 1, "えっと、その…\nこんな所で\nこういうことされると\n恥ずかしいよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (84, 100202, 2, 2, 1, 0, 1, "え！　む、虫っ！？\n取って取って！\nはやく～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (85, 100202, 2, 3, 1, 0, 1, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (86, 100202, 2, 4, 1, 0, 1, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (87, 100202, 2, 101, 8, 0, 1, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (88, 100202, 2, 102, 8, 0, 1, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (89, 100202, 2, 1, 1, 1, 1, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (90, 100202, 2, 2, 1, 2, 1, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (91, 100202, 2, 3, 1, 3, 1, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (92, 100202, 3, 1, 1, 0, 1, "よし…\nこれなら\n褒めてもらえるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (93, 100202, 3, 2, 1, 0, 1, "がんばることも\n必要だけど\nお休みも大切だよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (94, 100202, 3, 3, 1, 0, 1, "かわいい～♪\n騎士クンは\nこういうのって好き？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (95, 100202, 3, 4, 1, 0, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (96, 100202, 3, 5, 1, 0, 1, "あの…！\nよ、良かったら\n一緒に…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (97, 100202, 3, 6, 1, 0, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (98, 100202, 3, 7, 1, 0, 1, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (99, 100202, 3, 8, 1, 0, 1, "…やっぱり\n月刊恋愛指南は\nためになる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (101, 100301, 1, 1, 1, 0, 1, "装備の手入れでも\nしようかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (102, 100301, 1, 2, 1, 0, 1, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (103, 100301, 1, 3, 1, 0, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (104, 100301, 1, 4, 1, 0, 1, "仲間なんていらない\nそう思ってた頃も\nあったな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (105, 100301, 1, 5, 1, 0, 1, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (106, 100301, 1, 101, 4, 0, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (107, 100301, 1, 102, 8, 0, 1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (108, 100301, 2, 1, 1, 0, 1, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (109, 100301, 2, 2, 1, 0, 1, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (110, 100301, 2, 3, 1, 0, 1, "なに？　私に相談？\n戦いについてなら\n喜んで", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (111, 100301, 2, 4, 1, 0, 1, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (112, 100301, 2, 101, 4, 0, 1, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (113, 100301, 2, 102, 8, 0, 1, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (114, 100301, 2, 1, 1, 1, 1, "おはよう\n今日も充実した\n一日にしていこう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (115, 100301, 2, 2, 1, 2, 1, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (116, 100301, 2, 3, 1, 3, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (117, 100301, 3, 1, 1, 0, 1, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (118, 100301, 3, 2, 1, 0, 1, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (119, 100301, 3, 3, 1, 0, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (120, 100301, 3, 4, 1, 0, 1, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (121, 100301, 3, 5, 1, 0, 1, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (122, 100301, 3, 6, 1, 0, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (123, 100301, 3, 7, 1, 0, 1, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (124, 100301, 3, 8, 1, 0, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (126, 100302, 1, 1, 1, 0, 1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (127, 100302, 1, 2, 1, 0, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (128, 100302, 1, 3, 1, 0, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (129, 100302, 1, 4, 1, 0, 1, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (130, 100302, 1, 5, 1, 0, 1, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (131, 100302, 1, 101, 8, 0, 1, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (132, 100302, 1, 102, 8, 0, 1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (133, 100302, 2, 1, 1, 0, 1, "どうしたの？\nなんでもない？\nそれなら良かった", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (134, 100302, 2, 2, 1, 0, 1, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (135, 100302, 2, 3, 1, 0, 1, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (136, 100302, 2, 4, 1, 0, 1, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (137, 100302, 2, 101, 8, 0, 1, "ひょっとして\n今のも一本だなんて\n言わないよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (138, 100302, 2, 102, 8, 0, 1, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (139, 100302, 2, 1, 1, 1, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (140, 100302, 2, 2, 1, 2, 1, "休息も時には\n必要だと、仲間から\n教わったよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (141, 100302, 2, 3, 1, 3, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (142, 100302, 3, 1, 1, 0, 1, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (143, 100302, 3, 2, 1, 0, 1, "休息があってこそ\n万全な状態で\n戦えるんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (144, 100302, 3, 3, 1, 0, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (145, 100302, 3, 4, 1, 0, 1, "体と同様に\n心もほぐれるね…\nはぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (146, 100302, 3, 5, 1, 0, 1, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (147, 100302, 3, 6, 1, 0, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (148, 100302, 3, 7, 1, 0, 1, "修行のせいか\n最近食べる量が\n増えてきたような…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (149, 100302, 3, 8, 1, 0, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (151, 100401, 1, 1, 1, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (152, 100401, 1, 2, 1, 0, 1, "落とし穴…どこに\n作ったんだっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (153, 100401, 1, 3, 1, 0, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (154, 100401, 1, 4, 1, 0, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (155, 100401, 1, 5, 1, 0, 1, "かくれんぼする人\nこの指、と～まれ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (156, 100401, 1, 101, 4, 0, 1, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (157, 100401, 1, 102, 8, 0, 1, "ミソギと遊ぶ？\nミソギに遊ばれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (158, 100401, 2, 1, 1, 0, 1, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (159, 100401, 2, 2, 1, 0, 1, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (160, 100401, 2, 3, 1, 0, 1, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (161, 100401, 2, 4, 1, 0, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (162, 100401, 2, 101, 4, 0, 1, "ここ、ミソギ探検隊の\n基地にしようよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (163, 100401, 2, 102, 8, 0, 1, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (164, 100401, 2, 1, 1, 1, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (165, 100401, 2, 2, 1, 2, 1, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (166, 100401, 2, 3, 1, 3, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (167, 100401, 3, 1, 1, 0, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (168, 100401, 3, 2, 1, 0, 1, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (169, 100401, 3, 3, 1, 0, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (170, 100401, 3, 4, 1, 0, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (171, 100401, 3, 5, 1, 0, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (172, 100401, 3, 6, 1, 0, 1, "探検家は\n体力勝負だよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (173, 100401, 3, 7, 1, 0, 1, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (174, 100401, 3, 8, 1, 0, 1, "こういう所に\n宝の地図が\n隠れてるんだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (176, 100402, 1, 1, 1, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (177, 100402, 1, 2, 1, 0, 1, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (178, 100402, 1, 3, 1, 0, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (179, 100402, 1, 4, 1, 0, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (180, 100402, 1, 5, 1, 0, 1, "かくれんぼする人\nこの指、と～まれ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (181, 100402, 1, 101, 8, 0, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (182, 100402, 1, 102, 8, 0, 1, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (183, 100402, 2, 1, 1, 0, 1, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (184, 100402, 2, 2, 1, 0, 1, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (185, 100402, 2, 3, 1, 0, 1, "将来の夢は探検家！\n探したい物はないけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (186, 100402, 2, 4, 1, 0, 1, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (187, 100402, 2, 101, 8, 0, 1, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (188, 100402, 2, 102, 8, 0, 1, "ここ、ミソギ探検隊の\n基地にしようよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (189, 100402, 2, 1, 1, 1, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (190, 100402, 2, 2, 1, 2, 1, "にいちゃんが昼寝したら\nイタズラし放題♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (191, 100402, 2, 3, 1, 3, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (192, 100402, 3, 1, 1, 0, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (193, 100402, 3, 2, 1, 0, 1, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (194, 100402, 3, 3, 1, 0, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (195, 100402, 3, 4, 1, 0, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (196, 100402, 3, 5, 1, 0, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (197, 100402, 3, 6, 1, 0, 1, "探検家は\n体力勝負だよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (198, 100402, 3, 7, 1, 0, 1, "探検のおやつに\nぴったりかも～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (199, 100402, 3, 8, 1, 0, 1, "こういう所に\n宝の地図が\n隠れてるんだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (201, 100601, 1, 1, 1, 0, 1, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (202, 100601, 1, 2, 1, 0, 1, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (203, 100601, 1, 3, 1, 0, 1, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (204, 100601, 1, 4, 1, 0, 1, "お姉ちゃんのマネ\nしちゃおうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (205, 100601, 1, 5, 1, 0, 1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (206, 100601, 1, 101, 4, 0, 1, "お兄ちゃんと私は\n相性抜群です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (207, 100601, 1, 102, 8, 0, 1, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (208, 100601, 2, 1, 1, 0, 1, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (209, 100601, 2, 2, 1, 0, 1, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (210, 100601, 2, 3, 1, 0, 1, "そんなところ\n触っちゃダメ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (211, 100601, 2, 4, 1, 0, 1, "お兄ちゃん、イジワル\nしないでください…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (212, 100601, 2, 101, 4, 0, 1, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (213, 100601, 2, 102, 8, 0, 1, "コーチ、\n今日も特訓\nお願いします！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (214, 100601, 2, 1, 1, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (215, 100601, 2, 2, 1, 2, 1, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (216, 100601, 2, 3, 1, 3, 1, "夜はこれからですよ\nお兄ちゃん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (217, 100601, 3, 1, 1, 0, 1, "世界一、かわいく\nなるんだから♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (218, 100601, 3, 2, 1, 0, 1, "気持ちいい♪\nもう立てないよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (219, 100601, 3, 3, 1, 0, 1, "かわいい♪\nアカリのライバル\nだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (220, 100601, 3, 4, 1, 0, 1, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (221, 100601, 3, 5, 1, 0, 1, "はぁ…アカリ、\nとろけちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (222, 100601, 3, 6, 1, 0, 1, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (223, 100601, 3, 7, 1, 0, 1, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (224, 100601, 3, 8, 1, 0, 1, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (226, 100602, 1, 1, 1, 0, 1, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (227, 100602, 1, 2, 1, 0, 1, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (228, 100602, 1, 3, 1, 0, 1, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (229, 100602, 1, 4, 1, 0, 1, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (230, 100602, 1, 5, 1, 0, 1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (231, 100602, 1, 101, 8, 0, 1, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (232, 100602, 1, 102, 8, 0, 1, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (233, 100602, 2, 1, 1, 0, 1, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (234, 100602, 2, 2, 1, 0, 1, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (235, 100602, 2, 3, 1, 0, 1, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (236, 100602, 2, 4, 1, 0, 1, "お兄ちゃん、イジワル\nしないでください…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (237, 100602, 2, 101, 8, 0, 1, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (238, 100602, 2, 102, 8, 0, 1, "コーチ、\n今日も特訓\nお願いします！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (239, 100602, 2, 1, 1, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (240, 100602, 2, 2, 1, 2, 1, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (241, 100602, 2, 3, 1, 3, 1, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (242, 100602, 3, 1, 1, 0, 1, "お姉ちゃんのマネ\nしちゃおうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (243, 100602, 3, 2, 1, 0, 1, "気持ちいい♪\nもう立てないよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (244, 100602, 3, 3, 1, 0, 1, "かわいい♪\nアカリのライバル\nだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (245, 100602, 3, 4, 1, 0, 1, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (246, 100602, 3, 5, 1, 0, 1, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (247, 100602, 3, 6, 1, 0, 1, "汗をかくと\n気持ちいいですよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (248, 100602, 3, 7, 1, 0, 1, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (249, 100602, 3, 8, 1, 0, 1, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (251, 100701, 1, 1, 1, 0, 1, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (252, 100701, 1, 2, 1, 0, 1, "う～ら～め～し～や～\nなの～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (253, 100701, 1, 3, 1, 0, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (254, 100701, 1, 4, 1, 0, 1, "幽霊の正体見たり、\nミヤコなの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (255, 100701, 1, 5, 1, 0, 1, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (256, 100701, 1, 101, 4, 0, 1, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (257, 100701, 1, 102, 8, 0, 1, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (258, 100701, 2, 1, 1, 0, 1, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (259, 100701, 2, 2, 1, 0, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (260, 100701, 2, 3, 1, 0, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (261, 100701, 2, 4, 1, 0, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (262, 100701, 2, 101, 4, 0, 1, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (263, 100701, 2, 102, 8, 0, 1, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (264, 100701, 2, 1, 1, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (265, 100701, 2, 2, 1, 2, 1, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (266, 100701, 2, 3, 1, 3, 1, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (267, 100701, 3, 1, 1, 0, 1, "体が透けちゃうから\nうまく使えないの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (268, 100701, 3, 2, 1, 0, 1, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (269, 100701, 3, 3, 1, 0, 1, "む～…かわいいけど\nプリンは分けて\nあげないの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (270, 100701, 3, 4, 1, 0, 1, "は～　いい気分なの…\n成仏しそうなの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (271, 100701, 3, 5, 1, 0, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (272, 100701, 3, 6, 1, 0, 1, "幽霊には、体力も\n筋力も関係ないの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (273, 100701, 3, 7, 1, 0, 1, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (274, 100701, 3, 8, 1, 0, 1, "プリンの写真集が\nあればいいのに\nなの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (276, 100702, 1, 1, 1, 0, 1, "うらめしや～…\n裏の飯より\nプリンがいいの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (277, 100702, 1, 2, 1, 0, 1, "プリン♪　プリン♪\nプリン♪　なの～♪　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (278, 100702, 1, 3, 1, 0, 1, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (279, 100702, 1, 4, 1, 0, 1, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (280, 100702, 1, 5, 1, 0, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (281, 100702, 1, 101, 8, 0, 1, "アイツがいて\nプリンもある\nきっとここが天国なの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (282, 100702, 1, 102, 8, 0, 1, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (283, 100702, 2, 1, 1, 0, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (284, 100702, 2, 2, 1, 0, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (285, 100702, 2, 3, 1, 0, 1, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (286, 100702, 2, 4, 1, 0, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (287, 100702, 2, 101, 8, 0, 1, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (288, 100702, 2, 102, 8, 0, 1, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (289, 100702, 2, 1, 1, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの… ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (290, 100702, 2, 2, 1, 2, 1, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (291, 100702, 2, 3, 1, 3, 1, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (292, 100702, 3, 1, 1, 0, 1, "体が透けちゃうから\nうまく使えないの～ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (293, 100702, 3, 2, 1, 0, 1, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (294, 100702, 3, 3, 1, 0, 1, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (295, 100702, 3, 4, 1, 0, 1, "は～　いい気分なの…\n成仏しそうなの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (296, 100702, 3, 5, 1, 0, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (297, 100702, 3, 6, 1, 0, 1, "幽霊には、体力も\n筋力も関係ないの～！　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (298, 100702, 3, 7, 1, 0, 1, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (299, 100702, 3, 8, 1, 0, 1, "プリンの写真集が\nあればいいのに\nなの～ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (301, 100801, 1, 1, 1, 0, 1, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (302, 100801, 1, 2, 1, 0, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (303, 100801, 1, 3, 1, 0, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (304, 100801, 1, 4, 1, 0, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (305, 100801, 1, 5, 1, 0, 1, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (306, 100801, 1, 101, 4, 0, 1, "このボクがいるだけで\nあの人への\nおもてなしだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (307, 100801, 1, 102, 8, 0, 1, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (308, 100801, 2, 1, 1, 0, 1, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (309, 100801, 2, 2, 1, 0, 1, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (310, 100801, 2, 3, 1, 0, 1, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (311, 100801, 2, 4, 1, 0, 1, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (312, 100801, 2, 101, 4, 0, 1, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (313, 100801, 2, 102, 8, 0, 1, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (314, 100801, 2, 1, 1, 1, 1, "美しいボクの\n美しい一日が\n始まるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (315, 100801, 2, 2, 1, 2, 1, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (316, 100801, 2, 3, 1, 3, 1, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (317, 100801, 3, 1, 1, 0, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (318, 100801, 3, 2, 1, 0, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (319, 100801, 3, 3, 1, 0, 1, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (320, 100801, 3, 4, 1, 0, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (321, 100801, 3, 5, 1, 0, 1, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (322, 100801, 3, 6, 1, 0, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (323, 100801, 3, 7, 1, 0, 1, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (324, 100801, 3, 8, 1, 0, 1, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (326, 100802, 1, 1, 1, 0, 1, "ボクの美しさは\nみんなのために\nあるんだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (327, 100802, 1, 2, 1, 0, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (328, 100802, 1, 3, 1, 0, 1, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (329, 100802, 1, 4, 1, 0, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (330, 100802, 1, 5, 1, 0, 1, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (331, 100802, 1, 101, 8, 0, 1, "このボクがいるだけで\nあの人への\nおもてなしだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (332, 100802, 1, 102, 8, 0, 1, "あの人も今\n可愛いボクのこと\n考えてるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (333, 100802, 2, 1, 1, 0, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (334, 100802, 2, 2, 1, 0, 1, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (335, 100802, 2, 3, 1, 0, 1, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (336, 100802, 2, 4, 1, 0, 1, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (337, 100802, 2, 101, 8, 0, 1, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (338, 100802, 2, 102, 8, 0, 1, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (339, 100802, 2, 1, 1, 1, 1, "朝日と共に\nボクの美しさも\n周りを照らすよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (340, 100802, 2, 2, 1, 2, 1, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (341, 100802, 2, 3, 1, 3, 1, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (342, 100802, 3, 1, 1, 0, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (343, 100802, 3, 2, 1, 0, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (344, 100802, 3, 3, 1, 0, 1, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (345, 100802, 3, 4, 1, 0, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (346, 100802, 3, 5, 1, 0, 1, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (347, 100802, 3, 6, 1, 0, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (348, 100802, 3, 7, 1, 0, 1, "ボクが食べると\n食べ物まで\n可愛いなあ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (349, 100802, 3, 8, 1, 0, 1, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (351, 100901, 1, 1, 1, 0, 1, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (352, 100901, 1, 2, 1, 0, 1, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (353, 100901, 1, 3, 1, 0, 1, "ここでは風が\n吹いてこないな…\n換気したい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (354, 100901, 1, 4, 1, 0, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (355, 100901, 1, 5, 1, 0, 1, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (356, 100901, 1, 101, 4, 0, 1, "シグルド…\n今頃何処で何を\nしているのだろう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (357, 100901, 1, 102, 8, 0, 1, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (358, 100901, 2, 1, 1, 0, 1, "辞書を知らないか？\n…探している\n理由は聞くな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (359, 100901, 2, 2, 1, 0, 1, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (360, 100901, 2, 3, 1, 0, 1, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (361, 100901, 2, 4, 1, 0, 1, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (362, 100901, 2, 101, 4, 0, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (363, 100901, 2, 102, 8, 0, 1, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (364, 100901, 2, 1, 1, 1, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (365, 100901, 2, 2, 1, 2, 1, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (366, 100901, 2, 3, 1, 3, 1, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (367, 100901, 3, 1, 1, 0, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (368, 100901, 3, 2, 1, 0, 1, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (369, 100901, 3, 3, 1, 0, 1, "このようなものに\n懐柔されるものか！\n…されるものか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (370, 100901, 3, 4, 1, 0, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (371, 100901, 3, 5, 1, 0, 1, "これが我らの求めた\n安寧か…\n悪くない…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (372, 100901, 3, 6, 1, 0, 1, "この程度\n前世での激闘に\n比べれば…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (373, 100901, 3, 7, 1, 0, 1, "最近金欠でな…\nいやぁありがたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (374, 100901, 3, 8, 1, 0, 1, "ここにも\n前世の記憶が\n眠っていたとは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (376, 100902, 1, 1, 1, 0, 1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (377, 100902, 1, 2, 1, 0, 1, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (378, 100902, 1, 3, 1, 0, 1, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (379, 100902, 1, 4, 1, 0, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (380, 100902, 1, 5, 1, 0, 1, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (381, 100902, 1, 101, 1, 0, 1, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (382, 100902, 1, 102, 8, 0, 1, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (383, 100902, 2, 1, 8, 0, 1, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (384, 100902, 2, 2, 1, 0, 1, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (385, 100902, 2, 3, 1, 0, 1, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (386, 100902, 2, 4, 1, 0, 1, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (387, 100902, 2, 101, 1, 0, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (388, 100902, 2, 102, 8, 0, 1, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (389, 100902, 2, 1, 8, 1, 1, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (390, 100902, 2, 2, 1, 2, 1, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (391, 100902, 2, 3, 1, 3, 1, "夜になると、力が\n増した気分になるな\n…なりませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (392, 100902, 3, 1, 1, 0, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (393, 100902, 3, 2, 1, 0, 1, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (394, 100902, 3, 3, 1, 0, 1, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (395, 100902, 3, 4, 1, 0, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (396, 100902, 3, 5, 1, 0, 1, "これが我らの求めた\n安寧か…\n悪くない…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (397, 100902, 3, 6, 1, 0, 1, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (398, 100902, 3, 7, 1, 0, 1, "食事は魔力に\n変換する体力の\n元になるのだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (399, 100902, 3, 8, 1, 0, 1, "フフフ…\nこれを覚えれば\nもっと強く…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (401, 101001, 1, 1, 1, 0, 1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (402, 101001, 1, 2, 1, 0, 1, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (403, 101001, 1, 3, 1, 0, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (404, 101001, 1, 4, 1, 0, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (405, 101001, 1, 5, 1, 0, 1, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (406, 101001, 1, 101, 4, 0, 1, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (407, 101001, 1, 102, 8, 0, 1, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (408, 101001, 2, 1, 1, 0, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (409, 101001, 2, 2, 1, 0, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (410, 101001, 2, 3, 1, 0, 1, "王子はんは、\n絵本から出てきた人\nみたいやね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (411, 101001, 2, 4, 1, 0, 1, "うちの話し方\nのんびりしてます\nやろか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (412, 101001, 2, 101, 4, 0, 1, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (413, 101001, 2, 102, 8, 0, 1, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (414, 101001, 2, 1, 1, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (415, 101001, 2, 2, 1, 2, 1, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (416, 101001, 2, 3, 1, 3, 1, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (417, 101001, 3, 1, 1, 0, 1, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (418, 101001, 3, 2, 1, 0, 1, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (419, 101001, 3, 3, 1, 0, 1, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (420, 101001, 3, 4, 1, 0, 1, "心地ええどす。まるで\nお花畑にいるようやね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (421, 101001, 3, 5, 1, 0, 1, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (422, 101001, 3, 6, 1, 0, 1, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (423, 101001, 3, 7, 1, 0, 1, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (424, 101001, 3, 8, 1, 0, 1, "絵本、絵本～\n幸せやわぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (426, 101002, 1, 1, 1, 0, 1, "王子はんにとって\n自慢の姫になるえ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (427, 101002, 1, 2, 1, 0, 1, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (428, 101002, 1, 3, 1, 0, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (429, 101002, 1, 4, 1, 0, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (430, 101002, 1, 5, 1, 0, 1, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (431, 101002, 1, 101, 8, 0, 1, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (432, 101002, 1, 102, 8, 0, 1, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (433, 101002, 2, 1, 1, 0, 1, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (434, 101002, 2, 2, 1, 0, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (435, 101002, 2, 3, 1, 0, 1, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (436, 101002, 2, 4, 1, 0, 1, "絵本読むんも\nええなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (437, 101002, 2, 101, 8, 0, 1, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (438, 101002, 2, 102, 8, 0, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (439, 101002, 2, 1, 1, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (440, 101002, 2, 2, 1, 2, 1, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (441, 101002, 2, 3, 1, 3, 1, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (442, 101002, 3, 1, 1, 0, 1, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (443, 101002, 3, 2, 1, 0, 1, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (444, 101002, 3, 3, 1, 0, 1, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (445, 101002, 3, 4, 1, 0, 1, "癒しアイテムって\nほんま、好っきゃわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (446, 101002, 3, 5, 1, 0, 1, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (447, 101002, 3, 6, 1, 0, 1, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (448, 101002, 3, 7, 1, 0, 1, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (449, 101002, 3, 8, 1, 0, 1, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (451, 101101, 1, 1, 1, 0, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (452, 101101, 1, 2, 1, 0, 1, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (453, 101101, 1, 3, 1, 0, 1, "い、いけない！\n縫い針落としちゃった", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (454, 101101, 1, 4, 1, 0, 1, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (455, 101101, 1, 5, 1, 0, 1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (456, 101101, 1, 101, 4, 0, 1, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (457, 101101, 1, 102, 8, 0, 1, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (458, 101101, 2, 1, 1, 0, 1, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (459, 101101, 2, 2, 1, 0, 1, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (460, 101101, 2, 3, 1, 0, 1, "勉強、見てくれるん\nですか？　やった～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (461, 101101, 2, 4, 1, 0, 1, "服がほつれてますよ？\n直してあげます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (462, 101101, 2, 101, 4, 0, 1, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (463, 101101, 2, 102, 8, 0, 1, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (464, 101101, 2, 1, 1, 1, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (465, 101101, 2, 2, 1, 2, 1, "お昼休憩に\n甘いものが\n食べたいです～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (466, 101101, 2, 3, 1, 3, 1, "ねむいです～\nでももうちょっと\n勉強しないと～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (467, 101101, 3, 1, 1, 0, 1, "この服、かわいい♪\n今度作って\nみよっと～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (468, 101101, 3, 2, 1, 0, 1, "勉強で疲れたから\n休憩しよ～っと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (469, 101101, 3, 3, 1, 0, 1, "かわいい♪\n私が服を作って\nあげよっか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (470, 101101, 3, 4, 1, 0, 1, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (471, 101101, 3, 5, 1, 0, 1, "のんびりしたら、\nまた勉強しよっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (472, 101101, 3, 6, 1, 0, 1, "運動すると勉強も\nはかどるよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (473, 101101, 3, 7, 1, 0, 1, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (474, 101101, 3, 8, 1, 0, 1, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (476, 101102, 1, 1, 1, 0, 1, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (477, 101102, 1, 2, 1, 0, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (478, 101102, 1, 3, 1, 0, 1, "い、いけない！\n縫い針落としちゃった", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (479, 101102, 1, 4, 1, 0, 1, "笑う時には服着ます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (480, 101102, 1, 5, 1, 0, 1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (481, 101102, 1, 101, 8, 0, 1, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (482, 101102, 1, 102, 8, 0, 1, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (483, 101102, 2, 1, 1, 0, 1, "勉強、見てくれるん\nですか？　やった～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (484, 101102, 2, 2, 1, 0, 1, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (485, 101102, 2, 3, 1, 0, 1, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (486, 101102, 2, 4, 1, 0, 1, "勉強は…\n明日にしようっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (487, 101102, 2, 101, 8, 0, 1, "妹だけど、大人な\nレディですからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (488, 101102, 2, 102, 8, 0, 1, "な、ナデナデとか\n恥ずかしいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (489, 101102, 2, 1, 1, 1, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (490, 101102, 2, 2, 1, 2, 1, "お昼休憩に\n甘いものが\n食べたいです～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (491, 101102, 2, 3, 1, 3, 1, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (492, 101102, 3, 1, 1, 0, 1, "服装や髪形が変わると\n気分も変わるよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (493, 101102, 3, 2, 1, 0, 1, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (494, 101102, 3, 3, 1, 0, 1, "かわいい♪\n私が服を作って\nあげよっか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (495, 101102, 3, 4, 1, 0, 1, "いまならどんな試験も\n合格できそうです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (496, 101102, 3, 5, 1, 0, 1, "のんびりしたら\nまた勉強しよっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (497, 101102, 3, 6, 1, 0, 1, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (498, 101102, 3, 7, 1, 0, 1, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (499, 101102, 3, 8, 1, 0, 1, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (501, 101201, 1, 1, 1, 0, 1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (502, 101201, 1, 2, 1, 0, 1, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (503, 101201, 1, 3, 1, 0, 1, "…はっ！？\nね、寝てないよ？\nホントだよっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (504, 101201, 1, 4, 1, 0, 1, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (505, 101201, 1, 5, 1, 0, 1, "もし、超能力のことが\nバレたら…ブルブル", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (506, 101201, 1, 101, 4, 0, 1, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (507, 101201, 1, 102, 8, 0, 1, "キミを抱き枕に…\nじょ、冗談冗談！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (508, 101201, 2, 1, 1, 0, 1, "ん～、なになに？\n一緒にお昼寝する？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (509, 101201, 2, 2, 1, 0, 1, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (510, 101201, 2, 3, 1, 0, 1, "おおっ、相談ごと？\nなにかな\nなにかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (511, 101201, 2, 4, 1, 0, 1, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (512, 101201, 2, 101, 4, 0, 1, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (513, 101201, 2, 102, 8, 0, 1, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (514, 101201, 2, 1, 1, 1, 1, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (515, 101201, 2, 2, 1, 2, 1, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (516, 101201, 2, 3, 1, 3, 1, "どんなにお昼寝しても\n夜は眠いんだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (517, 101201, 3, 1, 1, 0, 1, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (518, 101201, 3, 2, 1, 0, 1, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (519, 101201, 3, 3, 1, 0, 1, "これ…可愛い！\nシオリンに\n届けてあげたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (520, 101201, 3, 4, 1, 0, 1, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (521, 101201, 3, 5, 1, 0, 1, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (522, 101201, 3, 6, 1, 0, 1, "眠くならないように、\n運動しよっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (523, 101201, 3, 7, 1, 0, 1, "お腹いっぱいになると\n眠くなるよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (524, 101201, 3, 8, 1, 0, 1, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (526, 101202, 1, 1, 1, 0, 1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (527, 101202, 1, 2, 1, 0, 1, "もっといっぱい\n超能力使えたら\nいいのにな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (528, 101202, 1, 3, 1, 0, 1, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (529, 101202, 1, 4, 1, 0, 1, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (530, 101202, 1, 5, 1, 0, 1, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (531, 101202, 1, 101, 8, 0, 1, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (532, 101202, 1, 102, 8, 0, 1, "キミを抱き枕に…\nじょ、冗談冗談！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (533, 101202, 2, 1, 1, 0, 1, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (534, 101202, 2, 2, 1, 0, 1, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (535, 101202, 2, 3, 1, 0, 1, "一緒に\nお出かけしたいなー\nデートしよっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (536, 101202, 2, 4, 1, 0, 1, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (537, 101202, 2, 101, 8, 0, 1, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (538, 101202, 2, 102, 8, 0, 1, "う～だんだん眠く…\nねぇ一緒に寝よ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (539, 101202, 2, 1, 1, 1, 1, "キミって早起きだね！\n私は寝ようかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (540, 101202, 2, 2, 1, 2, 1, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (541, 101202, 2, 3, 1, 3, 1, "どんなにお昼寝しても\n夜は眠いんだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (542, 101202, 3, 1, 1, 0, 1, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (543, 101202, 3, 2, 1, 0, 1, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (544, 101202, 3, 3, 1, 0, 1, "これ…可愛い！\nシオリンに\n届けてあげたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (545, 101202, 3, 4, 1, 0, 1, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (546, 101202, 3, 5, 1, 0, 1, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (547, 101202, 3, 6, 1, 0, 1, "よーし\n困ってる人のために\nパワーアップ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (548, 101202, 3, 7, 1, 0, 1, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (549, 101202, 3, 8, 1, 0, 1, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (551, 101601, 1, 1, 1, 0, 1, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (552, 101601, 1, 2, 1, 0, 1, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (553, 101601, 1, 3, 1, 0, 1, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (554, 101601, 1, 4, 1, 0, 1, "ここで撮影会、\nしようかな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (555, 101601, 1, 5, 1, 0, 1, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (556, 101601, 1, 101, 4, 0, 1, "やば…\nこのままじゃ、うち\nキュン死にかも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (557, 101601, 1, 102, 8, 0, 1, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (558, 101601, 2, 1, 1, 0, 1, "うちの写真撮るの？\nどんなポーズ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (559, 101601, 2, 2, 1, 0, 1, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (560, 101601, 2, 3, 1, 0, 1, "宿題見てくんない？\nひとりじゃムリ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (561, 101601, 2, 4, 1, 0, 1, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (562, 101601, 2, 101, 4, 0, 1, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (563, 101601, 2, 102, 8, 0, 1, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (564, 101601, 2, 1, 1, 1, 1, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (565, 101601, 2, 2, 1, 2, 1, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (566, 101601, 2, 3, 1, 3, 1, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (567, 101601, 3, 1, 1, 0, 1, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (568, 101601, 3, 2, 1, 0, 1, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (569, 101601, 3, 3, 1, 0, 1, "きゃわたーん☆\n君もおしゃれしよっか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (570, 101601, 3, 4, 1, 0, 1, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (571, 101601, 3, 5, 1, 0, 1, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (572, 101601, 3, 6, 1, 0, 1, "あはは、なんか\nウォーキングの練習\nしてるみたーい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (573, 101601, 3, 7, 1, 0, 1, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (574, 101601, 3, 8, 1, 0, 1, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (576, 101602, 1, 1, 1, 0, 1, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (577, 101602, 1, 2, 1, 0, 1, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (578, 101602, 1, 3, 1, 0, 1, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (579, 101602, 1, 4, 1, 0, 1, "体形維持とか\n気にしてるんだよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (580, 101602, 1, 5, 1, 0, 1, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (581, 101602, 1, 101, 8, 0, 1, "やば…\nこのままじゃ、うち\nキュン死にかも…　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (582, 101602, 1, 102, 8, 0, 1, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (583, 101602, 2, 1, 1, 0, 1, "うちの写真撮るの？\nどんなポーズ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (584, 101602, 2, 2, 1, 0, 1, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (585, 101602, 2, 3, 1, 0, 1, "この雑誌の服\nガチめにほしぃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (586, 101602, 2, 4, 1, 0, 1, "うちのコーデで\nヒデサイをオシャンに\nしてあげるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (587, 101602, 2, 101, 8, 0, 1, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (588, 101602, 2, 102, 8, 0, 1, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (589, 101602, 2, 1, 1, 1, 1, "今日は早起き\nできたんだー\nメイクもばっち\nきまってるっしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (590, 101602, 2, 2, 1, 2, 1, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (591, 101602, 2, 3, 1, 3, 1, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (592, 101602, 3, 1, 1, 0, 1, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (593, 101602, 3, 2, 1, 0, 1, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (594, 101602, 3, 3, 1, 0, 1, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (595, 101602, 3, 4, 1, 0, 1, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (596, 101602, 3, 5, 1, 0, 1, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (597, 101602, 3, 6, 1, 0, 1, "食べた分は\n消費しないとね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (598, 101602, 3, 7, 1, 0, 1, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (599, 101602, 3, 8, 1, 0, 1, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (601, 101701, 1, 1, 1, 0, 1, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (602, 101701, 1, 2, 1, 0, 1, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (603, 101701, 1, 3, 1, 0, 1, "ハイターイ！　元気な\nあいさつが一番さ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (604, 101701, 1, 4, 1, 0, 1, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (605, 101701, 1, 5, 1, 0, 1, "さぁ、みんなで歌って\n踊るさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (606, 101701, 1, 101, 4, 0, 1, "毎日、最高に幸せさ～\nキミのおかげだねー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (607, 101701, 1, 102, 8, 0, 1, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (608, 101701, 2, 1, 1, 0, 1, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (609, 101701, 2, 2, 1, 0, 1, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (610, 101701, 2, 3, 1, 0, 1, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (611, 101701, 2, 4, 1, 0, 1, "空手の型の\n練習するさー\nキミもどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (612, 101701, 2, 101, 4, 0, 1, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (613, 101701, 2, 102, 8, 0, 1, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (614, 101701, 2, 1, 1, 1, 1, "ハイターイ！\n今日も一日、\nがんばるさー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (615, 101701, 2, 2, 1, 2, 1, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (616, 101701, 2, 3, 1, 3, 1, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (617, 101701, 3, 1, 1, 0, 1, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (618, 101701, 3, 2, 1, 0, 1, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (619, 101701, 3, 3, 1, 0, 1, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (620, 101701, 3, 4, 1, 0, 1, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (621, 101701, 3, 5, 1, 0, 1, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (622, 101701, 3, 6, 1, 0, 1, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (623, 101701, 3, 7, 1, 0, 1, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (624, 101701, 3, 8, 1, 0, 1, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (626, 101702, 1, 1, 1, 0, 1, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (627, 101702, 1, 2, 1, 0, 1, "さぁ、みんなで歌って\n踊るさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (628, 101702, 1, 3, 1, 0, 1, "元気がないときは\n私のところに来るさー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (629, 101702, 1, 4, 1, 0, 1, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (630, 101702, 1, 5, 1, 0, 1, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (631, 101702, 1, 101, 8, 0, 1, "毎日、最高に幸せさ～\nキミのおかげだねー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (632, 101702, 1, 102, 8, 0, 1, "幸せすぎて踊らずには\nいられないさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (633, 101702, 2, 1, 1, 0, 1, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (634, 101702, 2, 2, 1, 0, 1, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (635, 101702, 2, 3, 1, 0, 1, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (636, 101702, 2, 4, 1, 0, 1, "ハイターイ！　元気な\nあいさつが一番さ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (637, 101702, 2, 101, 8, 0, 1, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (638, 101702, 2, 102, 8, 0, 1, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (639, 101702, 2, 1, 1, 1, 1, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (640, 101702, 2, 2, 1, 2, 1, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (641, 101702, 2, 3, 1, 3, 1, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (642, 101702, 3, 1, 1, 0, 1, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (643, 101702, 3, 2, 1, 0, 1, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (644, 101702, 3, 3, 1, 0, 1, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (645, 101702, 3, 4, 1, 0, 1, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (646, 101702, 3, 5, 1, 0, 1, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (647, 101702, 3, 6, 1, 0, 1, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (648, 101702, 3, 7, 1, 0, 1, "もぐもぐ…もっと\nおかわりがほしいさー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (649, 101702, 3, 8, 1, 0, 1, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (651, 101801, 1, 1, 1, 0, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (652, 101801, 1, 2, 1, 0, 1, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (653, 101801, 1, 3, 1, 0, 1, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (654, 101801, 1, 4, 1, 0, 1, "キンコンカンコ～ン♪\n授業をはじめま～す", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (655, 101801, 1, 5, 1, 0, 1, "はー…\nちょっと肩が\nこっちゃった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (656, 101801, 1, 101, 4, 0, 1, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (657, 101801, 1, 102, 8, 0, 1, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (658, 101801, 2, 1, 1, 0, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (659, 101801, 2, 2, 1, 0, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (660, 101801, 2, 3, 1, 0, 1, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (661, 101801, 2, 4, 1, 0, 1, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (662, 101801, 2, 101, 4, 0, 1, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (663, 101801, 2, 102, 8, 0, 1, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (664, 101801, 2, 1, 1, 1, 1, "学校に\n遅刻しないように\n早く準備しないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (665, 101801, 2, 2, 1, 2, 1, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (666, 101801, 2, 3, 1, 3, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (667, 101801, 3, 1, 1, 0, 1, "もしかして…\n少し露出多いかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (668, 101801, 3, 2, 1, 0, 1, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (669, 101801, 3, 3, 1, 0, 1, "かわいい♪　あなたも\n私の生徒になる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (670, 101801, 3, 4, 1, 0, 1, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (671, 101801, 3, 5, 1, 0, 1, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (672, 101801, 3, 6, 1, 0, 1, "運動部の顧問も\nできるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (673, 101801, 3, 7, 1, 0, 1, "学校の給食でも\n食べたいわぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (674, 101801, 3, 8, 1, 0, 1, "いい参考書を\n見つけちゃった♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (676, 101802, 1, 1, 1, 0, 1, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (677, 101802, 1, 2, 1, 0, 1, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (678, 101802, 1, 3, 1, 0, 1, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (679, 101802, 1, 4, 1, 0, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (680, 101802, 1, 5, 1, 0, 1, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (681, 101802, 1, 101, 8, 0, 1, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (682, 101802, 1, 102, 8, 0, 1, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (683, 101802, 2, 1, 1, 0, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (684, 101802, 2, 2, 1, 0, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (685, 101802, 2, 3, 1, 0, 1, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (686, 101802, 2, 4, 1, 0, 1, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (687, 101802, 2, 101, 8, 0, 1, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (688, 101802, 2, 102, 8, 0, 1, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (689, 101802, 2, 1, 1, 1, 1, "朝の特別授業\nしてあげましょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (690, 101802, 2, 2, 1, 2, 1, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (691, 101802, 2, 3, 1, 3, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (692, 101802, 3, 1, 1, 0, 1, "もしかして…\n少し露出多いかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (693, 101802, 3, 2, 1, 0, 1, "私の膝枕と、どっちが\nフカフカかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (694, 101802, 3, 3, 1, 0, 1, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (695, 101802, 3, 4, 1, 0, 1, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (696, 101802, 3, 5, 1, 0, 1, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (697, 101802, 3, 6, 1, 0, 1, "運動部の顧問も\nできるかしら？　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (698, 101802, 3, 7, 1, 0, 1, "あん！\nお胸にこぼしちゃった", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (699, 101802, 3, 8, 1, 0, 1, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (701, 102001, 1, 1, 1, 0, 1, "おにいちゃんは、\nにんじん食べられる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (702, 102001, 1, 2, 1, 0, 1, "えへへ、\nお絵かきしよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (703, 102001, 1, 3, 1, 0, 1, "あのね、ミミと\nあそんでほしいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (704, 102001, 1, 4, 1, 0, 1, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (705, 102001, 1, 5, 1, 0, 1, "うさぎさん、\nぴょんぴょん\nぴょーん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (706, 102001, 1, 101, 4, 0, 1, "おにいちゃんの絵、\nかいてあげるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (707, 102001, 1, 102, 8, 0, 1, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (708, 102001, 2, 1, 1, 0, 1, "ふわぁ！\nおにいちゃんの手、\nおっきいね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (709, 102001, 2, 2, 1, 0, 1, "やった～！\nおにいちゃんが\nほめてくれた～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (710, 102001, 2, 3, 1, 0, 1, "ねえねえ\nおにいちゃん、\n一緒にあそぼっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (711, 102001, 2, 4, 1, 0, 1, "おにいちゃん、\n遊んでくれるの？\n　わぁい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (712, 102001, 2, 101, 4, 0, 1, "わぁい！\nおにいちゃんだ～♪\nぎゅ～っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (713, 102001, 2, 102, 8, 0, 1, "あのねあのね、\nミミの頭ポンポンって\nしてほしいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (714, 102001, 2, 1, 1, 1, 1, "おひさまさん、\nおはよ～！\n毎朝、早おきだね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (715, 102001, 2, 2, 1, 2, 1, "うわぁ！\nお日さまがすごーく\n高いところにある～", 0);
