INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (806, 102201, 1, 101, 4, 0, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (807, 102201, 1, 102, 8, 0, 1, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (808, 102201, 2, 1, 1, 0, 1, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (809, 102201, 2, 2, 1, 0, 1, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (810, 102201, 2, 3, 1, 0, 1, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (811, 102201, 2, 4, 1, 0, 1, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (812, 102201, 2, 101, 4, 0, 1, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (813, 102201, 2, 102, 8, 0, 1, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (814, 102201, 2, 1, 1, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (815, 102201, 2, 2, 1, 2, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (816, 102201, 2, 3, 1, 3, 1, "夜はこれからよ！\n朝まで\nゲームするからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (817, 102201, 3, 1, 1, 0, 1, "アカリみたいに\n可愛くなるのは\n難しいかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (818, 102201, 3, 2, 1, 0, 1, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (819, 102201, 3, 3, 1, 0, 1, "可愛い…けど、\n私には似合わないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (820, 102201, 3, 4, 1, 0, 1, "はあ、リラックスして\nゲームができるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (821, 102201, 3, 5, 1, 0, 1, "ん…暖かくて、\n寝落ちしちゃいそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (822, 102201, 3, 6, 1, 0, 1, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (823, 102201, 3, 7, 1, 0, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (824, 102201, 3, 8, 1, 0, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (826, 102202, 1, 1, 1, 0, 1, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (827, 102202, 1, 2, 1, 0, 1, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (828, 102202, 1, 3, 1, 0, 1, "レアカードをゲット！\nすごいでしょ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (829, 102202, 1, 4, 1, 0, 1, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (830, 102202, 1, 5, 1, 0, 1, "楽しいゲームって\nやめどきが\n見つからないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (831, 102202, 1, 101, 8, 0, 1, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (832, 102202, 1, 102, 8, 0, 1, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (833, 102202, 2, 1, 1, 0, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (834, 102202, 2, 2, 1, 0, 1, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (835, 102202, 2, 3, 1, 0, 1, "ねえ、見て！\n新しいゲームを\n買って来たの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (836, 102202, 2, 4, 1, 0, 1, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (837, 102202, 2, 101, 8, 0, 1, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (838, 102202, 2, 102, 8, 0, 1, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (839, 102202, 2, 1, 1, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (840, 102202, 2, 2, 1, 2, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (841, 102202, 2, 3, 1, 3, 1, "夜はこれからよ！\n朝まで\nゲームするからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (842, 102202, 3, 1, 1, 0, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (843, 102202, 3, 2, 1, 0, 1, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (844, 102202, 3, 3, 1, 0, 1, "可愛い…けど、\n私には似合わないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (845, 102202, 3, 4, 1, 0, 1, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (846, 102202, 3, 5, 1, 0, 1, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (847, 102202, 3, 6, 1, 0, 1, "最近ゲームばかりだし\nたまにはいいかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (848, 102202, 3, 7, 1, 0, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (849, 102202, 3, 8, 1, 0, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (851, 102301, 1, 1, 1, 0, 1, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (852, 102301, 1, 2, 1, 0, 1, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (853, 102301, 1, 3, 1, 0, 1, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (854, 102301, 1, 4, 1, 0, 1, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (855, 102301, 1, 5, 1, 0, 1, "この部屋、ぷうきちを\n置く場所はないのかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (856, 102301, 1, 101, 4, 0, 1, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (857, 102301, 1, 102, 8, 0, 1, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (858, 102301, 2, 1, 1, 0, 1, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (859, 102301, 2, 2, 1, 0, 1, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (860, 102301, 2, 3, 1, 0, 1, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (861, 102301, 2, 4, 1, 0, 1, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (862, 102301, 2, 101, 4, 0, 1, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (863, 102301, 2, 102, 8, 0, 1, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (864, 102301, 2, 1, 1, 1, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (865, 102301, 2, 2, 1, 2, 1, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (866, 102301, 2, 3, 1, 3, 1, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (868, 102301, 3, 2, 1, 0, 1, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (869, 102301, 3, 3, 1, 0, 1, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (870, 102301, 3, 4, 1, 0, 1, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (871, 102301, 3, 5, 1, 0, 1, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (873, 102301, 3, 7, 1, 0, 1, "おいしい～\nぷうきちも\n食べてみる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (874, 102301, 3, 8, 1, 0, 1, "ぷうきち！\nこの本、読んで～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (901, 102501, 1, 1, 1, 0, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (902, 102501, 1, 2, 1, 0, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (903, 102501, 1, 3, 1, 0, 1, "どうしたら転ばなく\nなるんでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (904, 102501, 1, 4, 1, 0, 1, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (905, 102501, 1, 5, 1, 0, 1, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (906, 102501, 1, 101, 4, 0, 1, "おいしい料理で\nお出迎えしなくちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (907, 102501, 1, 102, 8, 0, 1, "一生懸命\nご奉仕しますよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (908, 102501, 2, 1, 1, 0, 1, "耳かきしましょうか？\nえ？　こわい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (909, 102501, 2, 2, 1, 0, 1, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (910, 102501, 2, 3, 1, 0, 1, "おかえりなさいませ\nご主人さま♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (911, 102501, 2, 4, 1, 0, 1, "すみません\nすみません\nすみません～～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (912, 102501, 2, 101, 4, 0, 1, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (913, 102501, 2, 102, 8, 0, 1, "私のこと、\n専属メイドと思って\n頼ってくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (914, 102501, 2, 1, 1, 1, 1, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (915, 102501, 2, 2, 1, 2, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (916, 102501, 2, 3, 1, 3, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (917, 102501, 3, 1, 1, 0, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (918, 102501, 3, 2, 1, 0, 1, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (919, 102501, 3, 3, 1, 0, 1, "かわいい♪\nあとでお洗濯\nしてあげますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (920, 102501, 3, 4, 1, 0, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (921, 102501, 3, 5, 1, 0, 1, "ここでお洗濯ものを\n乾かしましょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (922, 102501, 3, 6, 1, 0, 1, "あわわわっ…\nこ、転びそうです～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (923, 102501, 3, 7, 1, 0, 1, "これくらい\nおいしいものを\n作ってみたいです～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (924, 102501, 3, 8, 1, 0, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (926, 102502, 1, 1, 1, 0, 1, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (927, 102502, 1, 2, 1, 0, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (928, 102502, 1, 3, 1, 0, 1, "どうやったら、立派な\nメイドになれるのかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (929, 102502, 1, 4, 1, 0, 1, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (930, 102502, 1, 5, 1, 0, 1, "ご用があれば\nなんでも言って\nくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (931, 102502, 1, 101, 8, 0, 1, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (932, 102502, 1, 102, 8, 0, 1, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (933, 102502, 2, 1, 1, 0, 1, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (934, 102502, 2, 2, 1, 0, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (935, 102502, 2, 3, 1, 0, 1, "私、またなにか\nやっちゃいましたか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (936, 102502, 2, 4, 1, 0, 1, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (937, 102502, 2, 101, 8, 0, 1, "転んだふりであの人に\n…って！\nなんでもないですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (938, 102502, 2, 102, 8, 0, 1, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (939, 102502, 2, 1, 1, 1, 1, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (940, 102502, 2, 2, 1, 2, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (941, 102502, 2, 3, 1, 3, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (942, 102502, 3, 1, 1, 0, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (943, 102502, 3, 2, 1, 0, 1, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (944, 102502, 3, 3, 1, 0, 1, "かわいい♪\nあとでお洗濯\nしてあげますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (945, 102502, 3, 4, 1, 0, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (946, 102502, 3, 5, 1, 0, 1, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (947, 102502, 3, 6, 1, 0, 1, "運動しようとすると\nいつも怪我するん\nですよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (948, 102502, 3, 7, 1, 0, 1, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (949, 102502, 3, 8, 1, 0, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (951, 102701, 1, 1, 1, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (952, 102701, 1, 2, 1, 0, 1, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (953, 102701, 1, 3, 1, 0, 1, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (954, 102701, 1, 4, 1, 0, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (955, 102701, 1, 5, 1, 0, 1, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (956, 102701, 1, 101, 4, 0, 1, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (957, 102701, 1, 102, 8, 0, 1, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (958, 102701, 2, 1, 1, 0, 1, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (959, 102701, 2, 2, 1, 0, 1, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (960, 102701, 2, 3, 1, 0, 1, "お願いがあるのです。\n新しい研究の実験台に\n…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (961, 102701, 2, 4, 1, 0, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (962, 102701, 2, 101, 4, 0, 1, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (963, 102701, 2, 102, 8, 0, 1, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (964, 102701, 2, 1, 1, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (965, 102701, 2, 2, 1, 2, 1, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (966, 102701, 2, 3, 1, 3, 1, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (967, 102701, 3, 1, 1, 0, 1, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (968, 102701, 3, 2, 1, 0, 1, "はっ！\nリラックスしたら\n閃きましたわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (969, 102701, 3, 3, 1, 0, 1, "あら、キメラの素体に\nぴったりですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (970, 102701, 3, 4, 1, 0, 1, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (971, 102701, 3, 5, 1, 0, 1, "あたたかい…\n加熱調合にも\n使えそうですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (972, 102701, 3, 6, 1, 0, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (973, 102701, 3, 7, 1, 0, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (974, 102701, 3, 8, 1, 0, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (976, 102702, 1, 1, 1, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (977, 102702, 1, 2, 1, 0, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (978, 102702, 1, 3, 1, 0, 1, "新しい薬\nどうやってあの方に\n飲ませようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (979, 102702, 1, 4, 1, 0, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (980, 102702, 1, 5, 1, 0, 1, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (981, 102702, 1, 101, 8, 0, 1, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (982, 102702, 1, 102, 8, 0, 1, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (983, 102702, 2, 1, 1, 0, 1, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (984, 102702, 2, 2, 1, 0, 1, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (985, 102702, 2, 3, 1, 0, 1, "お願いがあるのです\n新しい研究の\n実験台に…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (986, 102702, 2, 4, 1, 0, 1, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (987, 102702, 2, 101, 8, 0, 1, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (988, 102702, 2, 102, 8, 0, 1, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (989, 102702, 2, 1, 1, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (990, 102702, 2, 2, 1, 2, 1, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (991, 102702, 2, 3, 1, 3, 1, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (992, 102702, 3, 1, 1, 0, 1, "私ももう少し\n外見に気を配る\nべきでしょうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (993, 102702, 3, 2, 1, 0, 1, "はっ！\nリラックスしたら\n閃きましたわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (994, 102702, 3, 3, 1, 0, 1, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (995, 102702, 3, 4, 1, 0, 1, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (996, 102702, 3, 5, 1, 0, 1, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (997, 102702, 3, 6, 1, 0, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (998, 102702, 3, 7, 1, 0, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (999, 102702, 3, 8, 1, 0, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1001, 102801, 1, 1, 1, 0, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1002, 102801, 1, 2, 1, 0, 1, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1003, 102801, 1, 3, 1, 0, 1, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1004, 102801, 1, 4, 1, 0, 1, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1005, 102801, 1, 5, 1, 0, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1006, 102801, 1, 101, 4, 0, 1, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1007, 102801, 1, 102, 8, 0, 1, "焼きそばパンが\n食べたいな～\nあいつと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1008, 102801, 2, 1, 1, 0, 1, "ねぇあんた、どこか\n安いお店知らない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1009, 102801, 2, 2, 1, 0, 1, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1010, 102801, 2, 3, 1, 0, 1, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1011, 102801, 2, 4, 1, 0, 1, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1012, 102801, 2, 101, 4, 0, 1, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1013, 102801, 2, 102, 8, 0, 1, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1014, 102801, 2, 1, 1, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1015, 102801, 2, 2, 1, 2, 1, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1016, 102801, 2, 3, 1, 3, 1, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1017, 102801, 3, 1, 1, 0, 1, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1018, 102801, 3, 2, 1, 0, 1, "これで仕事は一段落ね\nちょっと休憩…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1019, 102801, 3, 3, 1, 0, 1, "うふふ、\nかわいいものを\n見るのは幸せね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1020, 102801, 3, 4, 1, 0, 1, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1021, 102801, 3, 5, 1, 0, 1, "風邪を引かないように\n身体を温めなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1022, 102801, 3, 6, 1, 0, 1, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1023, 102801, 3, 7, 1, 0, 1, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1024, 102801, 3, 8, 1, 0, 1, "知識から過去を学んで\n未来に活かすわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1026, 102802, 1, 1, 1, 0, 1, "安売りのお店の情報\n整理しないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1027, 102802, 1, 2, 1, 0, 1, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1028, 102802, 1, 3, 1, 0, 1, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1029, 102802, 1, 4, 1, 0, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1030, 102802, 1, 5, 1, 0, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1031, 102802, 1, 101, 8, 0, 1, "焼きそばパンが\n食べたいな～\nあいつと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1032, 102802, 1, 102, 8, 0, 1, "あいつと一緒に…\n色々なところに\nいきたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1033, 102802, 2, 1, 1, 0, 1, "今度買い物に\n付き合いなさい\nセールがあるのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1034, 102802, 2, 2, 1, 0, 1, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1035, 102802, 2, 3, 1, 0, 1, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1036, 102802, 2, 4, 1, 0, 1, "あんたとこうして\nのんびりするのも\nいいものね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1037, 102802, 2, 101, 8, 0, 1, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1038, 102802, 2, 102, 8, 0, 1, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1039, 102802, 2, 1, 1, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1040, 102802, 2, 2, 1, 2, 1, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1041, 102802, 2, 3, 1, 3, 1, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1042, 102802, 3, 1, 1, 0, 1, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1043, 102802, 3, 2, 1, 0, 1, "これで仕事は一段落ね\nちょっと休憩…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1044, 102802, 3, 3, 1, 0, 1, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1045, 102802, 3, 4, 1, 0, 1, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1046, 102802, 3, 5, 1, 0, 1, "風邪を引かないように\n身体を温めなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1047, 102802, 3, 6, 1, 0, 1, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1048, 102802, 3, 7, 1, 0, 1, "しっかり食べて\n栄養補給、栄養補給！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1049, 102802, 3, 8, 1, 0, 1, "知識から過去を学んで\n未来に活かすわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1051, 102901, 1, 1, 1, 0, 1, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1052, 102901, 1, 2, 1, 0, 1, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1053, 102901, 1, 3, 1, 0, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1054, 102901, 1, 4, 1, 0, 1, "彼とデュエット\nしてみたいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1055, 102901, 1, 5, 1, 0, 1, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1056, 102901, 1, 101, 4, 0, 1, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1057, 102901, 1, 102, 8, 0, 1, "写真集見なくても、\n本物がここに\nいるのにな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1058, 102901, 2, 1, 1, 0, 1, "握手会の練習、\n付き合ってくれない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1059, 102901, 2, 2, 1, 0, 1, "写真集のポーズ\n練習中～！\nど、どうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1060, 102901, 2, 3, 1, 0, 1, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1061, 102901, 2, 4, 1, 0, 1, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1062, 102901, 2, 101, 4, 0, 1, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1063, 102901, 2, 102, 8, 0, 1, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1064, 102901, 2, 1, 1, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1065, 102901, 2, 2, 1, 2, 1, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1066, 102901, 2, 3, 1, 3, 1, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1067, 102901, 3, 1, 1, 0, 1, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1068, 102901, 3, 2, 1, 0, 1, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1069, 102901, 3, 3, 1, 0, 1, "かわいい～♪\nこれは強力な\nライバル登場かな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1070, 102901, 3, 4, 1, 0, 1, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1071, 102901, 3, 5, 1, 0, 1, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1072, 102901, 3, 6, 1, 0, 1, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1073, 102901, 3, 7, 1, 0, 1, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1074, 102901, 3, 8, 1, 0, 1, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1076, 102902, 1, 1, 1, 0, 1, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1077, 102902, 1, 2, 1, 0, 1, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1078, 102902, 1, 3, 1, 0, 1, "みんながくれた\nファンレター…\n私の大事な宝物…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1079, 102902, 1, 4, 1, 0, 1, "彼とデュエット\nしてみたいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1080, 102902, 1, 5, 1, 0, 1, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1081, 102902, 1, 101, 8, 0, 1, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1082, 102902, 1, 102, 8, 0, 1, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1083, 102902, 2, 1, 1, 0, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1084, 102902, 2, 2, 1, 0, 1, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1085, 102902, 2, 3, 1, 0, 1, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1086, 102902, 2, 4, 1, 0, 1, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1087, 102902, 2, 101, 8, 0, 1, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1088, 102902, 2, 102, 8, 0, 1, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1089, 102902, 2, 1, 1, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1090, 102902, 2, 2, 1, 2, 1, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1091, 102902, 2, 3, 1, 3, 1, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1092, 102902, 3, 1, 1, 0, 1, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1093, 102902, 3, 2, 1, 0, 1, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1094, 102902, 3, 3, 1, 0, 1, "かわいい～♪\nこれは強力な\nライバル登場かな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1095, 102902, 3, 4, 1, 0, 1, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1096, 102902, 3, 5, 1, 0, 1, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1097, 102902, 3, 6, 1, 0, 1, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1098, 102902, 3, 7, 1, 0, 1, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1099, 102902, 3, 8, 1, 0, 1, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1101, 103001, 1, 1, 1, 0, 1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1102, 103001, 1, 2, 1, 0, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1103, 103001, 1, 3, 1, 0, 1, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1104, 103001, 1, 4, 1, 0, 1, "むむ、アクダイカンの\n気配がするデース", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1105, 103001, 1, 5, 1, 0, 1, "クニトリ合戦、\n楽しみデース！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1106, 103001, 1, 101, 4, 0, 1, "ショーグンと\n出会えて、\nワタシは果報者デス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1107, 103001, 1, 102, 8, 0, 1, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1108, 103001, 2, 1, 1, 0, 1, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1109, 103001, 2, 2, 1, 0, 1, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1110, 103001, 2, 3, 1, 0, 1, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1111, 103001, 2, 4, 1, 0, 1, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1112, 103001, 2, 101, 4, 0, 1, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1113, 103001, 2, 102, 8, 0, 1, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1114, 103001, 2, 1, 1, 1, 1, "おはようデス！\n今日も一日\n忍法日和デス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1115, 103001, 2, 2, 1, 2, 1, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1116, 103001, 2, 3, 1, 3, 1, "明日もきっと\nいい日デスよ、\nショーグン！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1117, 103001, 3, 1, 1, 0, 1, "ちょんまげに\nしてみるのも\n面白いかもデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1118, 103001, 3, 2, 1, 0, 1, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1119, 103001, 3, 3, 1, 0, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1120, 103001, 3, 4, 1, 0, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1121, 103001, 3, 5, 1, 0, 1, "身体を暖めて\nイクサに向かうデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1122, 103001, 3, 6, 1, 0, 1, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1123, 103001, 3, 7, 1, 0, 1, "うーん、\n美味しいデス…\n幸せデス～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1124, 103001, 3, 8, 1, 0, 1, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1126, 103002, 1, 1, 1, 0, 1, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1127, 103002, 1, 2, 1, 0, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1128, 103002, 1, 3, 1, 0, 1, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1129, 103002, 1, 4, 1, 0, 1, "むむ、アクダイカンの\n気配がするデース", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1130, 103002, 1, 5, 1, 0, 1, "クニトリ合戦\n楽しみデース！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1131, 103002, 1, 101, 8, 0, 1, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1132, 103002, 1, 102, 8, 0, 1, "ショーグンの身は\n何があってもワタシが\n守るデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1133, 103002, 2, 1, 1, 0, 1, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1134, 103002, 2, 2, 1, 0, 1, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1135, 103002, 2, 3, 1, 0, 1, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1136, 103002, 2, 4, 1, 0, 1, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1137, 103002, 2, 101, 8, 0, 1, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1138, 103002, 2, 102, 8, 0, 1, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1139, 103002, 2, 1, 1, 1, 1, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1140, 103002, 2, 2, 1, 2, 1, "もうお昼デス\nお昼寝するデスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1141, 103002, 2, 3, 1, 3, 1, "そろそろおネム\nデース…\n一緒に寝るデス？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1142, 103002, 3, 1, 1, 0, 1, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1143, 103002, 3, 2, 1, 0, 1, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1144, 103002, 3, 3, 1, 0, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1145, 103002, 3, 4, 1, 0, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1146, 103002, 3, 5, 1, 0, 1, "身体を暖めてイクサに\n向かうデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1147, 103002, 3, 6, 1, 0, 1, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1148, 103002, 3, 7, 1, 0, 1, "美味しいデス…\n酢昆布を和えて\nみたいデス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1149, 103002, 3, 8, 1, 0, 1, "物事を知って\n来たるべきイクサに\n活かすデス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1151, 103101, 1, 1, 1, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1152, 103101, 1, 2, 1, 0, 1, "父は、いつも男性に\nきびしいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1153, 103101, 1, 3, 1, 0, 1, "占ってほしい人は、\nいつでも言って\nください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1154, 103101, 1, 4, 1, 0, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1155, 103101, 1, 5, 1, 0, 1, "この部屋、風水的にも\n運気が上がりそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1156, 103101, 1, 101, 4, 0, 1, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1157, 103101, 1, 102, 8, 0, 1, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1158, 103101, 2, 1, 1, 0, 1, "女難の相が\n出ていますね…\n気をつけてください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1159, 103101, 2, 2, 1, 0, 1, "今日のあなたの運勢は\n大きょ…\n大吉です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1160, 103101, 2, 3, 1, 0, 1, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1161, 103101, 2, 4, 1, 0, 1, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1162, 103101, 2, 101, 4, 0, 1, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1163, 103101, 2, 102, 8, 0, 1, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1164, 103101, 2, 1, 1, 1, 1, "おはようございます\n今日の運勢を\n占いましょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1165, 103101, 2, 2, 1, 2, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1166, 103101, 2, 3, 1, 3, 1, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1167, 103101, 3, 1, 1, 0, 1, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1168, 103101, 3, 2, 1, 0, 1, "一休みしましょう。\n憑かれて疲れました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1169, 103101, 3, 3, 1, 0, 1, "かわいい…\n付喪神が宿らないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1170, 103101, 3, 4, 1, 0, 1, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1171, 103101, 3, 5, 1, 0, 1, "気持ちいいです…\n極楽浄土みたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1172, 103101, 3, 6, 1, 0, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1173, 103101, 3, 7, 1, 0, 1, "お父さんも\n食べたいの？\n死んでるのに？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1174, 103101, 3, 8, 1, 0, 1, "この本…\nもしかして予言書？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1176, 103102, 1, 1, 1, 0, 1, "ここにいる幽霊は\nみんな幸せそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1177, 103102, 1, 2, 1, 0, 1, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1178, 103102, 1, 3, 1, 0, 1, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1179, 103102, 1, 4, 1, 0, 1, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1180, 103102, 1, 5, 1, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1181, 103102, 1, 101, 8, 0, 1, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1182, 103102, 1, 102, 8, 0, 1, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1183, 103102, 2, 1, 1, 0, 1, "この部屋、風水的にも\n運気が上がりそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1184, 103102, 2, 2, 1, 0, 1, "シノブには指一本\n触れさせん！\nガブッ！！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1185, 103102, 2, 3, 1, 0, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1186, 103102, 2, 4, 1, 0, 1, "ラップ音？\nいえ、父のいびきです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1187, 103102, 2, 101, 8, 0, 1, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1188, 103102, 2, 102, 8, 0, 1, "前世って信じますか？\n私とあなたは\nもしかすると…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1189, 103102, 2, 1, 1, 1, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1190, 103102, 2, 2, 1, 2, 1, "明るい時間は\nあまり得意では\nないんです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1191, 103102, 2, 3, 1, 3, 1, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1192, 103102, 3, 1, 1, 0, 1, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1193, 103102, 3, 2, 1, 0, 1, "うとうと…\nあれ…\n走馬灯が見える…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1194, 103102, 3, 3, 1, 0, 1, "かわいい…\nお父さんみたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1195, 103102, 3, 4, 1, 0, 1, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1196, 103102, 3, 5, 1, 0, 1, "快適です…\n幽霊もどんどん\n集まってきますね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1197, 103102, 3, 6, 1, 0, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1198, 103102, 3, 7, 1, 0, 1, "お父さんの分も、\n私が食べるね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1199, 103102, 3, 8, 1, 0, 1, "この本の占い\n私の占いと違う…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1201, 103201, 1, 1, 1, 0, 1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1202, 103201, 1, 2, 1, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1203, 103201, 1, 3, 1, 0, 1, "一日も早く、\n一人前の商人に\nなりたいものです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1204, 103201, 1, 4, 1, 0, 1, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1205, 103201, 1, 5, 1, 0, 1, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1206, 103201, 1, 101, 4, 0, 1, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1207, 103201, 1, 102, 8, 0, 1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1208, 103201, 2, 1, 1, 0, 1, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1209, 103201, 2, 2, 1, 0, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1210, 103201, 2, 3, 1, 0, 1, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1211, 103201, 2, 4, 1, 0, 1, "お金で解決するのは\n悪いことでは\nありませんわよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1212, 103201, 2, 101, 4, 0, 1, "その、よろしければ\n私とお散歩など\nいかがです？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1213, 103201, 2, 102, 8, 0, 1, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1214, 103201, 2, 1, 1, 1, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1215, 103201, 2, 2, 1, 2, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1216, 103201, 2, 3, 1, 3, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1217, 103201, 3, 1, 1, 0, 1, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1218, 103201, 3, 2, 1, 0, 1, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1219, 103201, 3, 3, 1, 0, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1220, 103201, 3, 4, 1, 0, 1, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1221, 103201, 3, 5, 1, 0, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1222, 103201, 3, 6, 1, 0, 1, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1223, 103201, 3, 7, 1, 0, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1224, 103201, 3, 8, 1, 0, 1, "経済の仕組みを\nしっかりと\n学ばなくては…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1226, 103202, 1, 1, 1, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1227, 103202, 1, 2, 1, 0, 1, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1228, 103202, 1, 3, 1, 0, 1, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1229, 103202, 1, 4, 1, 0, 1, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1230, 103202, 1, 5, 1, 0, 1, "この狭さも\n慣れれば\n快適ですわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1231, 103202, 1, 101, 8, 0, 1, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1232, 103202, 1, 102, 8, 0, 1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1233, 103202, 2, 1, 1, 0, 1, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1234, 103202, 2, 2, 1, 0, 1, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1235, 103202, 2, 3, 1, 0, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1236, 103202, 2, 4, 1, 0, 1, "今度一緒に…\nパーティにでも\n行きませんこと？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1237, 103202, 2, 101, 8, 0, 1, "その、よろしければ\n私とお散歩など\nいかがです？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1238, 103202, 2, 102, 8, 0, 1, "あなたさまの優しさは\nお金には\n代えられませんわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1239, 103202, 2, 1, 1, 1, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1240, 103202, 2, 2, 1, 2, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1241, 103202, 2, 3, 1, 3, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1242, 103202, 3, 1, 1, 0, 1, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1243, 103202, 3, 2, 1, 0, 1, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1244, 103202, 3, 3, 1, 0, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1245, 103202, 3, 4, 1, 0, 1, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1246, 103202, 3, 5, 1, 0, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1247, 103202, 3, 6, 1, 0, 1, "目標は遠くに\n置いた方が励みに\nなりますわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1248, 103202, 3, 7, 1, 0, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1249, 103202, 3, 8, 1, 0, 1, "商人って、物語では\n悪役にされやすいの\nですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1251, 103301, 1, 1, 1, 0, 1, "家の中のものを使って\n物ボケ、考えるべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1252, 103301, 1, 2, 1, 0, 1, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1253, 103301, 1, 3, 1, 0, 1, "うちに塀ができたら、\nへー、って言いたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1254, 103301, 1, 4, 1, 0, 1, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1255, 103301, 1, 5, 1, 0, 1, "エリザベスも\n連れてきてぇ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1256, 103301, 1, 101, 4, 0, 1, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1257, 103301, 1, 102, 8, 0, 1, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1258, 103301, 2, 1, 1, 0, 1, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1259, 103301, 2, 2, 1, 0, 1, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1260, 103301, 2, 3, 1, 0, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1261, 103301, 2, 4, 1, 0, 1, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1262, 103301, 2, 101, 4, 0, 1, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1263, 103301, 2, 102, 8, 0, 1, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1264, 103301, 2, 1, 1, 1, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1265, 103301, 2, 2, 1, 2, 1, "お昼だ～腹が減っては\n笑いはできぬ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1266, 103301, 2, 3, 1, 3, 1, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1267, 103301, 3, 1, 1, 0, 1, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1268, 103301, 3, 2, 1, 0, 1, "ナイスなイスに\n座りたい、\nなんちゃって！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1269, 103301, 3, 3, 1, 0, 1, "めんこいのは、\nスベっても許される\nからずるいべな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1270, 103301, 3, 4, 1, 0, 1, "必要以上に\n癒されちまうべや～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1271, 103301, 3, 5, 1, 0, 1, "ぽかぽかだ…\nウケた会場みてぇだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1272, 103301, 3, 6, 1, 0, 1, "お笑い芸人は、\n体が資本だべさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1273, 103301, 3, 7, 1, 0, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1274, 103301, 3, 8, 1, 0, 1, "ネタ作りのヒントが\n載ってねぇか探すべ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1276, 103302, 1, 1, 1, 0, 1, "さぁ今日も張り切って\n漫才の練習するべさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1277, 103302, 1, 2, 1, 0, 1, "この部屋でも、商品\n売っていいんだべか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1278, 103302, 1, 3, 1, 0, 1, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1279, 103302, 1, 4, 1, 0, 1, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1280, 103302, 1, 5, 1, 0, 1, "エリザベスも\n連れてきてぇ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1281, 103302, 1, 101, 8, 0, 1, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1282, 103302, 1, 102, 8, 0, 1, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1283, 103302, 2, 1, 1, 0, 1, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1284, 103302, 2, 2, 1, 0, 1, "あんちゃん、百連\nツッコミ特訓だ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1285, 103302, 2, 3, 1, 0, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1286, 103302, 2, 4, 1, 0, 1, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1287, 103302, 2, 101, 8, 0, 1, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1288, 103302, 2, 102, 8, 0, 1, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1289, 103302, 2, 1, 1, 1, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1290, 103302, 2, 2, 1, 2, 1, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1291, 103302, 2, 3, 1, 3, 1, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1292, 103302, 3, 1, 1, 0, 1, "うーん…衣装、\nずれてねぇべか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1293, 103302, 3, 2, 1, 0, 1, "ナイスなイスに\n座りたい\nなんちゃって！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1294, 103302, 3, 3, 1, 0, 1, "はう～\nなまらめんこいべや～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1295, 103302, 3, 4, 1, 0, 1, "必要以上に\n癒されちまうべや～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1296, 103302, 3, 5, 1, 0, 1, "笑いをとることを\n忘れるほど\nあったけぇべ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1297, 103302, 3, 6, 1, 0, 1, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1298, 103302, 3, 7, 1, 0, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1299, 103302, 3, 8, 1, 0, 1, "幅広く教養を\n身につけて\nいいネタ作るべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1301, 103401, 1, 1, 1, 0, 1, "通りにいる\nカップル…爆発\nすればいいのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1302, 103401, 1, 2, 1, 0, 1, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1303, 103401, 1, 3, 1, 0, 1, "今日は一日、\n外に出ない！\nもう決めたわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1304, 103401, 1, 4, 1, 0, 1, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1305, 103401, 1, 5, 1, 0, 1, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1306, 103401, 1, 101, 4, 0, 1, "デートとか…\n誘っても嫌がられ\nないかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1307, 103401, 1, 102, 8, 0, 1, "もしかして運命の\n相手だったり…\nするのかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1308, 103401, 2, 1, 1, 0, 1, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1309, 103401, 2, 2, 1, 0, 1, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1310, 103401, 2, 3, 1, 0, 1, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1311, 103401, 2, 4, 1, 0, 1, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1312, 103401, 2, 101, 4, 0, 1, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1313, 103401, 2, 102, 8, 0, 1, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1314, 103401, 2, 1, 1, 1, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1315, 103401, 2, 2, 1, 2, 1, "おっひるー♪\nなに食べよっかな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1316, 103401, 2, 3, 1, 3, 1, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1317, 103401, 3, 1, 1, 0, 1, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1318, 103401, 3, 2, 1, 0, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1319, 103401, 3, 3, 1, 0, 1, "こういうの男の子に\nプレゼントして\nもらいたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1320, 103401, 3, 4, 1, 0, 1, "うわあ、\nこれ、私の好きな\n香りだー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1321, 103401, 3, 5, 1, 0, 1, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1322, 103401, 3, 6, 1, 0, 1, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1323, 103401, 3, 7, 1, 0, 1, "もぐもぐ…\nおーいしー！\n…太らないよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1324, 103401, 3, 8, 1, 0, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1326, 103402, 1, 1, 1, 0, 1, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1327, 103402, 1, 2, 1, 0, 1, "今日は\nのんびり\n過ごそうかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1328, 103402, 1, 3, 1, 0, 1, "あ…\nお皿、洗って\nなかった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1329, 103402, 1, 4, 1, 0, 1, "もう少し、大人の\n魅力が出せるように\nなりたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1330, 103402, 1, 5, 1, 0, 1, "素敵な彼氏…\n欲しいなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1331, 103402, 1, 101, 8, 0, 1, "彼と私\n釣り合うかな…\n大丈夫よね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1332, 103402, 1, 102, 8, 0, 1, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1333, 103402, 2, 1, 1, 0, 1, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1334, 103402, 2, 2, 1, 0, 1, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1335, 103402, 2, 3, 1, 0, 1, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1336, 103402, 2, 4, 1, 0, 1, "お姉さんが～\n可愛がって\nあげようか～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1337, 103402, 2, 101, 8, 0, 1, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1338, 103402, 2, 102, 8, 0, 1, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1339, 103402, 2, 1, 1, 1, 1, "さーて、今日も\nいっちょやるぞー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1340, 103402, 2, 2, 1, 2, 1, "後でのんびり\nストレッチでも\nしようっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1341, 103402, 2, 3, 1, 3, 1, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1342, 103402, 3, 1, 1, 0, 1, "仕事の前に、髪とか\n整えないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1343, 103402, 3, 2, 1, 0, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1344, 103402, 3, 3, 1, 0, 1, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1345, 103402, 3, 4, 1, 0, 1, "うわあ\nこれ、私の好きな\n香りだー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1346, 103402, 3, 5, 1, 0, 1, "あったかーい…\nだ、だらけ\nちゃいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1347, 103402, 3, 6, 1, 0, 1, "地道な努力が\nリア充への\n第一歩！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1348, 103402, 3, 7, 1, 0, 1, "おーいしっ♪\nこのために\n生きてるわねえ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1349, 103402, 3, 8, 1, 0, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1351, 103801, 1, 1, 1, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1352, 103801, 1, 2, 1, 0, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1353, 103801, 1, 3, 1, 0, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1354, 103801, 1, 4, 1, 0, 1, "ちょっと、休憩…\n本でも読もうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1355, 103801, 1, 5, 1, 0, 1, "もっとたくさんの本に\n囲まれて生活したいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1356, 103801, 1, 101, 4, 0, 1, "むかしむかしある所に\nシオリと\nあの人が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1357, 103801, 1, 102, 8, 0, 1, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1358, 103801, 2, 1, 1, 0, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1359, 103801, 2, 2, 1, 0, 1, "あ…お姉ちゃんと\n約束があったんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1360, 103801, 2, 3, 1, 0, 1, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1361, 103801, 2, 4, 1, 0, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1362, 103801, 2, 101, 4, 0, 1, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1363, 103801, 2, 102, 8, 0, 1, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1364, 103801, 2, 1, 1, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1365, 103801, 2, 2, 1, 2, 1, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1366, 103801, 2, 3, 1, 3, 1, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1367, 103801, 3, 1, 1, 0, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1368, 103801, 3, 2, 1, 0, 1, "少し休もう\n…やっぱり\nたくさん休もう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1369, 103801, 3, 3, 1, 0, 1, "かわいいものが\n枕元にあると\n安心できるんですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1370, 103801, 3, 4, 1, 0, 1, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1371, 103801, 3, 5, 1, 0, 1, "こうして温まって\nいると、病気も\n治る気がします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1372, 103801, 3, 6, 1, 0, 1, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1373, 103801, 3, 7, 1, 0, 1, "少食ですけど\nこれならたくさん\n食べられそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1374, 103801, 3, 8, 1, 0, 1, "続きが気になって\n読むのを止める\nタイミングが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1376, 103802, 1, 1, 1, 0, 1, "しっかり休まないと\n早く回復するように", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1377, 103802, 1, 2, 1, 0, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1378, 103802, 1, 3, 1, 0, 1, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1379, 103802, 1, 4, 1, 0, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1380, 103802, 1, 5, 1, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1381, 103802, 1, 101, 8, 0, 1, "むかしむかしある所に\nシオリと\nあの人が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1382, 103802, 1, 102, 8, 0, 1, "あの人の\nためにも早く\n病気を治さないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1383, 103802, 2, 1, 1, 0, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1384, 103802, 2, 2, 1, 0, 1, "新記録です！\n一週間連続、\n平熱を保ちました！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1385, 103802, 2, 3, 1, 0, 1, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1386, 103802, 2, 4, 1, 0, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1387, 103802, 2, 101, 8, 0, 1, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1388, 103802, 2, 102, 8, 0, 1, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1389, 103802, 2, 1, 1, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1390, 103802, 2, 2, 1, 2, 1, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1391, 103802, 2, 3, 1, 3, 1, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1392, 103802, 3, 1, 1, 0, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1393, 103802, 3, 2, 1, 0, 1, "少し休もう\n…やっぱり\nたくさん休もう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1394, 103802, 3, 3, 1, 0, 1, "この子がいれば\n病室に一人でも\n寂しくないですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1395, 103802, 3, 4, 1, 0, 1, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1396, 103802, 3, 5, 1, 0, 1, "こうして温まって\nいると、病気も\n治る気がします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1397, 103802, 3, 6, 1, 0, 1, "私も、少しは\n体力をつけないと\nいけませんよね　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1398, 103802, 3, 7, 1, 0, 1, "いっぱい食べて\n健康な体にならないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1399, 103802, 3, 8, 1, 0, 1, "続きが気になって\n読むのを止める\nタイミングが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1401, 104001, 1, 1, 1, 0, 1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1402, 104001, 1, 2, 1, 0, 1, "あ、忘れずに\nお花に水を\nあげなきゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1403, 104001, 1, 3, 1, 0, 1, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1404, 104001, 1, 4, 1, 0, 1, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1405, 104001, 1, 5, 1, 0, 1, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1406, 104001, 1, 101, 4, 0, 1, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1407, 104001, 1, 102, 8, 0, 1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1408, 104001, 2, 1, 1, 0, 1, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1409, 104001, 2, 2, 1, 0, 1, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1410, 104001, 2, 3, 1, 0, 1, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1411, 104001, 2, 4, 1, 0, 1, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1412, 104001, 2, 101, 4, 0, 1, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1413, 104001, 2, 102, 8, 0, 1, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1414, 104001, 2, 1, 1, 1, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1415, 104001, 2, 2, 1, 2, 1, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1416, 104001, 2, 3, 1, 3, 1, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1417, 104001, 3, 1, 1, 0, 1, "私がもっと\n可愛ければ、\n友達できるかなあ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1418, 104001, 3, 2, 1, 0, 1, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1419, 104001, 3, 3, 1, 0, 1, "かわいいなあ。\nこの子とも\n友達になりたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1420, 104001, 3, 4, 1, 0, 1, "いい香りです…\n精神統一して\n友達のイメトレ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1421, 104001, 3, 5, 1, 0, 1, "灯りを見てると、\n心がなごみます～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1422, 104001, 3, 6, 1, 0, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1423, 104001, 3, 7, 1, 0, 1, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1424, 104001, 3, 8, 1, 0, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1426, 104002, 1, 1, 1, 0, 1, "友達作りは、焦らず\n慌てず、じっくりと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1427, 104002, 1, 2, 1, 0, 1, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1428, 104002, 1, 3, 1, 0, 1, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1429, 104002, 1, 4, 1, 0, 1, "今度、みなさんと\nピクニックとか…\nいってみたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1430, 104002, 1, 5, 1, 0, 1, "ぼっちでもいい\nたくましく育てば\n…なんて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1431, 104002, 1, 101, 8, 0, 1, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1432, 104002, 1, 102, 8, 0, 1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1433, 104002, 2, 1, 1, 0, 1, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1434, 104002, 2, 2, 1, 0, 1, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1435, 104002, 2, 3, 1, 0, 1, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1436, 104002, 2, 4, 1, 0, 1, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1437, 104002, 2, 101, 8, 0, 1, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1438, 104002, 2, 102, 8, 0, 1, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1439, 104002, 2, 1, 1, 1, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1440, 104002, 2, 2, 1, 2, 1, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1441, 104002, 2, 3, 1, 3, 1, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1442, 104002, 3, 1, 1, 0, 1, "私…見た目\nそこまで変じゃ\nないですよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1443, 104002, 3, 2, 1, 0, 1, "ほわあ～…\nふかふかって\nいいものですね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1444, 104002, 3, 3, 1, 0, 1, "この子みたいな\nあいくるしさを\n目指したいです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1445, 104002, 3, 4, 1, 0, 1, "灯りを見てると\n心がなごみます～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1446, 104002, 3, 5, 1, 0, 1, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1447, 104002, 3, 6, 1, 0, 1, "身体を鍛えて\nみなさんを\n守ります！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1448, 104002, 3, 7, 1, 0, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1449, 104002, 3, 8, 1, 0, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1451, 104201, 1, 1, 1, 0, 1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1452, 104201, 1, 2, 1, 0, 1, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1453, 104201, 1, 3, 1, 0, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1454, 104201, 1, 4, 1, 0, 1, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1455, 104201, 1, 5, 1, 0, 1, "あとで曲を復習して\nおかないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1456, 104201, 1, 101, 4, 0, 1, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1457, 104201, 1, 102, 8, 0, 1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1458, 104201, 2, 1, 1, 0, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1459, 104201, 2, 2, 1, 0, 1, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1460, 104201, 2, 3, 1, 0, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1461, 104201, 2, 4, 1, 0, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1462, 104201, 2, 101, 4, 0, 1, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1463, 104201, 2, 102, 8, 0, 1, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1464, 104201, 2, 1, 1, 1, 1, "おはようございます♪\n少し歌の練習を\nしてきますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1465, 104201, 2, 2, 1, 2, 1, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1466, 104201, 2, 3, 1, 3, 1, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1467, 104201, 3, 1, 1, 0, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1468, 104201, 3, 2, 1, 0, 1, "歌の調子をよくする\nためには休息も\n必要ですよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1469, 104201, 3, 3, 1, 0, 1, "この子たちを\n相手に練習して\nみようかな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1470, 104201, 3, 4, 1, 0, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1471, 104201, 3, 5, 1, 0, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1472, 104201, 3, 6, 1, 0, 1, "歌うためには\n体力づくりは\n欠かせません", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1473, 104201, 3, 7, 1, 0, 1, "歌った後は\nお腹が空いて\nしまうんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1474, 104201, 3, 8, 1, 0, 1, "音楽について\n理論も勉強\nしないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1476, 104202, 1, 1, 1, 0, 1, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1477, 104202, 1, 2, 1, 0, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1478, 104202, 1, 3, 1, 0, 1, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1479, 104202, 1, 4, 1, 0, 1, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1480, 104202, 1, 5, 1, 0, 1, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1481, 104202, 1, 101, 8, 0, 1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1482, 104202, 1, 102, 8, 0, 1, "私の中にある\n私だけの歌…\n作ってみようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1483, 104202, 2, 1, 1, 0, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1484, 104202, 2, 2, 1, 0, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1485, 104202, 2, 3, 1, 0, 1, "魔物退治ですか！？\n…違います？\n良かった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1486, 104202, 2, 4, 1, 0, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1487, 104202, 2, 101, 8, 0, 1, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1488, 104202, 2, 102, 8, 0, 1, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1489, 104202, 2, 1, 1, 1, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1490, 104202, 2, 2, 1, 2, 1, "少し休んだら\n午後の練習に\n行ってきますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1491, 104202, 2, 3, 1, 3, 1, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1492, 104202, 3, 1, 1, 0, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1493, 104202, 3, 2, 1, 0, 1, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1494, 104202, 3, 3, 1, 0, 1, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1495, 104202, 3, 4, 1, 0, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1496, 104202, 3, 5, 1, 0, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1497, 104202, 3, 6, 1, 0, 1, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1498, 104202, 3, 7, 1, 0, 1, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1499, 104202, 3, 8, 1, 0, 1, "読書は歌に\n新しい風を\n吹き込んでくれます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1501, 104301, 1, 1, 1, 0, 1, "店の新メニューでも\n考えるかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1502, 104301, 1, 2, 1, 0, 1, "あたしの店から、\n出前を届けようか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1503, 104301, 1, 3, 1, 0, 1, "パトロール、\nパトロール…\nここは異常なしかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1504, 104301, 1, 4, 1, 0, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1505, 104301, 1, 5, 1, 0, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1506, 104301, 1, 101, 4, 0, 1, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1507, 104301, 1, 102, 8, 0, 1, "今日の献立は…\nあいつの好きなものに\nしてやるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1508, 104301, 2, 1, 1, 0, 1, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1509, 104301, 2, 2, 1, 0, 1, "天気がいい日は\n外で運動したく\nならねぇか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1510, 104301, 2, 3, 1, 0, 1, "腹減ったか？　なんか\n作ってやろうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1511, 104301, 2, 4, 1, 0, 1, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1512, 104301, 2, 101, 4, 0, 1, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1513, 104301, 2, 102, 8, 0, 1, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1514, 104301, 2, 1, 1, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1515, 104301, 2, 2, 1, 2, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1516, 104301, 2, 3, 1, 3, 1, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1517, 104301, 3, 1, 1, 0, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1518, 104301, 3, 2, 1, 0, 1, "ふわぁ、\n一休みすっかなぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1519, 104301, 3, 3, 1, 0, 1, "うっ！　か、かわいい\nじゃねぇか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1520, 104301, 3, 4, 1, 0, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1521, 104301, 3, 5, 1, 0, 1, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1522, 104301, 3, 6, 1, 0, 1, "オラオラオラーーー！\n燃えるぜーーー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1523, 104301, 3, 7, 1, 0, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1524, 104301, 3, 8, 1, 0, 1, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1526, 104302, 1, 1, 1, 0, 1, "パトロール終了～\n本日も異常なしっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1527, 104302, 1, 2, 1, 0, 1, "あたしの店から\n出前を届けようか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1528, 104302, 1, 3, 1, 0, 1, "あいつ、早く\n帰ってこないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1529, 104302, 1, 4, 1, 0, 1, "あいつ\n腹すかせてないと\nいいんだけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1530, 104302, 1, 5, 1, 0, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1531, 104302, 1, 101, 8, 0, 1, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1532, 104302, 1, 102, 8, 0, 1, "今日の献立は…\nあいつの好きなものに\nしてやるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1533, 104302, 2, 1, 1, 0, 1, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1534, 104302, 2, 2, 1, 0, 1, "天気がいい日は\n外で運動したく\nならねぇか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1535, 104302, 2, 3, 1, 0, 1, "腹減ったか？　なんか\n作ってやろうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1536, 104302, 2, 4, 1, 0, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1537, 104302, 2, 101, 8, 0, 1, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1538, 104302, 2, 102, 8, 0, 1, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1539, 104302, 2, 1, 1, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1540, 104302, 2, 2, 1, 2, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1541, 104302, 2, 3, 1, 3, 1, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1542, 104302, 3, 1, 1, 0, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1543, 104302, 3, 2, 1, 0, 1, "ふぅ～♪\n疲れがとれるな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1544, 104302, 3, 3, 1, 0, 1, "うっ！　か、かわいい\nじゃねぇか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1545, 104302, 3, 4, 1, 0, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1546, 104302, 3, 5, 1, 0, 1, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1547, 104302, 3, 6, 1, 0, 1, "オラオラオラーーー！\n燃えるぜーーー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1548, 104302, 3, 7, 1, 0, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1549, 104302, 3, 8, 1, 0, 1, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1551, 104501, 1, 1, 1, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1552, 104501, 1, 2, 1, 0, 1, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1553, 104501, 1, 3, 1, 0, 1, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1554, 104501, 1, 4, 1, 0, 1, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1555, 104501, 1, 5, 1, 0, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1556, 104501, 1, 101, 4, 0, 1, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1557, 104501, 1, 102, 8, 0, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1558, 104501, 2, 1, 1, 0, 1, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1559, 104501, 2, 2, 1, 0, 1, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1560, 104501, 2, 3, 1, 0, 1, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1561, 104501, 2, 4, 1, 0, 1, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1562, 104501, 2, 101, 4, 0, 1, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1563, 104501, 2, 102, 8, 0, 1, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1564, 104501, 2, 1, 1, 1, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1565, 104501, 2, 2, 1, 2, 1, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1566, 104501, 2, 3, 1, 3, 1, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1567, 104501, 3, 1, 1, 0, 1, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1568, 104501, 3, 2, 1, 0, 1, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1569, 104501, 3, 3, 1, 0, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1570, 104501, 3, 4, 1, 0, 1, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1571, 104501, 3, 5, 1, 0, 1, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1572, 104501, 3, 6, 1, 0, 1, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1573, 104501, 3, 7, 1, 0, 1, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1574, 104501, 3, 8, 1, 0, 1, "この本の下に、\nそういう本を\nカムフラージュ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1576, 104502, 1, 1, 1, 0, 1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1577, 104502, 1, 2, 1, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1578, 104502, 1, 3, 1, 0, 1, "も、妄想が\n足りません…\n禁断症状がっ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1579, 104502, 1, 4, 1, 0, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1580, 104502, 1, 5, 1, 0, 1, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1581, 104502, 1, 101, 8, 0, 1, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1582, 104502, 1, 102, 8, 0, 1, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1583, 104502, 2, 1, 1, 0, 1, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1584, 104502, 2, 2, 1, 0, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1585, 104502, 2, 3, 1, 0, 1, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1586, 104502, 2, 4, 1, 0, 1, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1587, 104502, 2, 101, 8, 0, 1, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1588, 104502, 2, 102, 8, 0, 1, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1589, 104502, 2, 1, 1, 1, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1590, 104502, 2, 2, 1, 2, 1, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1591, 104502, 2, 3, 1, 3, 1, "今夜は一晩中…\nなんですね？\nぐふふ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1592, 104502, 3, 1, 1, 0, 1, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1593, 104502, 3, 2, 1, 0, 1, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1594, 104502, 3, 3, 1, 0, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1595, 104502, 3, 4, 1, 0, 1, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1596, 104502, 3, 5, 1, 0, 1, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1597, 104502, 3, 6, 1, 0, 1, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1598, 104502, 3, 7, 1, 0, 1, "こんなにいっぱい\n入りません…\nお腹にっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1599, 104502, 3, 8, 1, 0, 1, "どんな本も\nよく見ると\nいやらしいです…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1601, 104601, 1, 1, 1, 0, 1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1602, 104601, 1, 2, 1, 0, 1, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1603, 104601, 1, 3, 1, 0, 1, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1604, 104601, 1, 4, 1, 0, 1, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1605, 104601, 1, 5, 1, 0, 1, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1606, 104601, 1, 101, 4, 0, 1, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1607, 104601, 1, 102, 8, 0, 1, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1608, 104601, 2, 1, 1, 0, 1, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1609, 104601, 2, 2, 1, 0, 1, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1610, 104601, 2, 3, 1, 0, 1, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1611, 104601, 2, 4, 1, 0, 1, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1612, 104601, 2, 101, 4, 0, 1, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1613, 104601, 2, 102, 8, 0, 1, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1614, 104601, 2, 1, 1, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1615, 104601, 2, 2, 1, 2, 1, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1616, 104601, 2, 3, 1, 3, 1, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1617, 104601, 3, 1, 1, 0, 1, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1618, 104601, 3, 2, 1, 0, 1, "うにゃ～！\nお昼寝に\nぴったりにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1619, 104601, 3, 3, 1, 0, 1, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1620, 104601, 3, 4, 1, 0, 1, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1621, 104601, 3, 5, 1, 0, 1, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1622, 104601, 3, 6, 1, 0, 1, "本当は外で\n運動したいん\nだけどにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1623, 104601, 3, 7, 1, 0, 1, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1624, 104601, 3, 8, 1, 0, 1, "ふむふむ…猫の\nしつけ方は、と…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1626, 104602, 1, 1, 1, 0, 1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1627, 104602, 1, 2, 1, 0, 1, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1628, 104602, 1, 3, 1, 0, 1, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1629, 104602, 1, 4, 1, 0, 1, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1630, 104602, 1, 5, 1, 0, 1, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1631, 104602, 1, 101, 8, 0, 1, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1632, 104602, 1, 102, 8, 0, 1, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1633, 104602, 2, 1, 1, 0, 1, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1634, 104602, 2, 2, 1, 0, 1, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1635, 104602, 2, 3, 1, 0, 1, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1636, 104602, 2, 4, 1, 0, 1, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1637, 104602, 2, 101, 8, 0, 1, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1638, 104602, 2, 102, 8, 0, 1, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1639, 104602, 2, 1, 1, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1640, 104602, 2, 2, 1, 2, 1, "そろそろおっひる～\nにゃーんか\n食べたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1641, 104602, 2, 3, 1, 3, 1, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1642, 104602, 3, 1, 1, 0, 1, "にゃにゃ…\n髪が乱れてるにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1643, 104602, 3, 2, 1, 0, 1, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1644, 104602, 3, 3, 1, 0, 1, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1645, 104602, 3, 4, 1, 0, 1, "猫でもこわくない\n優しい灯りにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1646, 104602, 3, 5, 1, 0, 1, "ぽっかぽか\nしあわせな気分にゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1647, 104602, 3, 6, 1, 0, 1, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1648, 104602, 3, 7, 1, 0, 1, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1649, 104602, 3, 8, 1, 0, 1, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1651, 104801, 1, 1, 1, 0, 1, "内職手伝える人\nいないかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1652, 104801, 1, 2, 1, 0, 1, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1653, 104801, 1, 3, 1, 0, 1, "次の特売セールに\n向けて、しっかり\n準備しないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1654, 104801, 1, 4, 1, 0, 1, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1655, 104801, 1, 5, 1, 0, 1, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1656, 104801, 1, 101, 4, 0, 1, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1657, 104801, 1, 102, 8, 0, 1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1658, 104801, 2, 1, 1, 0, 1, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1659, 104801, 2, 2, 1, 0, 1, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1660, 104801, 2, 3, 1, 0, 1, "背後の気配なら\nすぐ分かるわ\n試してみる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1661, 104801, 2, 4, 1, 0, 1, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1662, 104801, 2, 101, 4, 0, 1, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1663, 104801, 2, 102, 8, 0, 1, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1664, 104801, 2, 1, 1, 1, 1, "おはよう！\n今日も朝から\nバイトよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1665, 104801, 2, 2, 1, 2, 1, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1666, 104801, 2, 3, 1, 3, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1667, 104801, 3, 1, 1, 0, 1, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1668, 104801, 3, 2, 1, 0, 1, "はぁ～バイトで\n疲れたから癒される～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1669, 104801, 3, 3, 1, 0, 1, "これを内職で\n作ったら\n儲かるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1670, 104801, 3, 4, 1, 0, 1, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1671, 104801, 3, 5, 1, 0, 1, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1672, 104801, 3, 6, 1, 0, 1, "働き続けるには\n健康第一！　それには\n運動が一番よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1673, 104801, 3, 7, 1, 0, 1, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1674, 104801, 3, 8, 1, 0, 1, "日々絶えず学んで、\n生活に活かすのよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1676, 104802, 1, 1, 1, 0, 1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1677, 104802, 1, 2, 1, 0, 1, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1678, 104802, 1, 3, 1, 0, 1, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1679, 104802, 1, 4, 1, 0, 1, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1680, 104802, 1, 5, 1, 0, 1, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1681, 104802, 1, 101, 8, 0, 1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1682, 104802, 1, 102, 8, 0, 1, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1683, 104802, 2, 1, 1, 0, 1, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1684, 104802, 2, 2, 1, 0, 1, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1685, 104802, 2, 3, 1, 0, 1, "今から\n休憩するところなの\n一緒にどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1686, 104802, 2, 4, 1, 0, 1, "ん？　どうしたの？\n私のことが\n気になるの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1687, 104802, 2, 101, 8, 0, 1, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1688, 104802, 2, 102, 8, 0, 1, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1689, 104802, 2, 1, 1, 1, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1690, 104802, 2, 2, 1, 2, 1, "お昼時ね\n少し休憩したら\nまた働くわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1691, 104802, 2, 3, 1, 3, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1692, 104802, 3, 1, 1, 0, 1, "女の子らしく…\nどうも忘れちゃう\nのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1693, 104802, 3, 2, 1, 0, 1, "ここで少し\n休んだら\nまたバイトよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1694, 104802, 3, 3, 1, 0, 1, "これを内職で\n作ったら\n儲かるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1695, 104802, 3, 4, 1, 0, 1, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1696, 104802, 3, 5, 1, 0, 1, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1697, 104802, 3, 6, 1, 0, 1, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1698, 104802, 3, 7, 1, 0, 1, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1699, 104802, 3, 8, 1, 0, 1, "日々絶えず学んで\n生活に活かすのよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1701, 104901, 1, 1, 1, 0, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1702, 104901, 1, 2, 1, 0, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1703, 104901, 1, 3, 1, 0, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1704, 104901, 1, 4, 1, 0, 1, "くんくん、\n弟くんのにおい…\n近くにいるね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1705, 104901, 1, 5, 1, 0, 1, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1706, 104901, 1, 101, 4, 0, 1, "この部屋を、\n私と弟くんの\n愛の巣に…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1707, 104901, 1, 102, 8, 0, 1, "はぁ～…弟くん\n禁断症状だよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1708, 104901, 2, 1, 1, 0, 1, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1709, 104901, 2, 2, 1, 0, 1, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1710, 104901, 2, 3, 1, 0, 1, "姉と弟の外出は\nデートって\nいうのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1711, 104901, 2, 4, 1, 0, 1, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1712, 104901, 2, 101, 4, 0, 1, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1713, 104901, 2, 102, 8, 0, 1, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1714, 104901, 2, 1, 1, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1715, 104901, 2, 2, 1, 2, 1, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1716, 104901, 2, 3, 1, 3, 1, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1717, 104901, 3, 1, 1, 0, 1, "きれいなお姉ちゃん\n好きだよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1718, 104901, 3, 2, 1, 0, 1, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 0);
