INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1728, 104902, 1, 3, 1, 0, 1, "弟くんのお友達は\nみんな私の妹だよ♪", 0);
