INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1731, 104902, 1, 101, 8, 0, 1, "はぁ～…弟くん、\n禁断症状だよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1732, 104902, 1, 102, 8, 0, 1, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1733, 104902, 2, 1, 1, 0, 1, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 0);
