INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1736, 104902, 2, 4, 1, 0, 1, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1737, 104902, 2, 101, 8, 0, 1, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1738, 104902, 2, 102, 8, 0, 1, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1739, 104902, 2, 1, 1, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 0);
