INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1749, 104902, 3, 8, 1, 0, 1, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1751, 105001, 1, 1, 1, 0, 1, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1752, 105001, 1, 2, 1, 0, 1, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 0);
