INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1758, 105001, 2, 1, 1, 0, 1, "わーい、アメだー♪\n食べる食べるー♪", 0);
