INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1807, 105101, 1, 102, 8, 0, 1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1808, 105101, 2, 1, 1, 0, 1, "あら、ヒマなら\n薬草の整理を\n手伝って", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1809, 105101, 2, 2, 1, 0, 1, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1810, 105101, 2, 3, 1, 0, 1, "具合が悪い？\nなら診てあげる\nまずは触診から…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1811, 105101, 2, 4, 1, 0, 1, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1812, 105101, 2, 101, 4, 0, 1, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1813, 105101, 2, 102, 8, 0, 1, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1814, 105101, 2, 1, 1, 1, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1815, 105101, 2, 2, 1, 2, 1, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1816, 105101, 2, 3, 1, 3, 1, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1817, 105101, 3, 1, 1, 0, 1, "医者たるもの\nいつだって\n清潔に…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1818, 105101, 3, 2, 1, 0, 1, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1819, 105101, 3, 3, 1, 0, 1, "ぬいぐるみなら\n動物も…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1820, 105101, 3, 4, 1, 0, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1821, 105101, 3, 5, 1, 0, 1, "ここにいると\nついウトウト\nしちゃうわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1822, 105101, 3, 6, 1, 0, 1, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1823, 105101, 3, 7, 1, 0, 1, "研究していると\nつい食べるのを\n忘れちゃうのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1824, 105101, 3, 8, 1, 0, 1, "実験もいいけど\nいつか成果を\n本にしないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1826, 105102, 1, 1, 1, 0, 1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1827, 105102, 1, 2, 1, 0, 1, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1828, 105102, 1, 3, 1, 0, 1, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1829, 105102, 1, 4, 1, 0, 1, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1830, 105102, 1, 5, 1, 0, 1, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1831, 105102, 1, 101, 8, 0, 1, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1832, 105102, 1, 102, 8, 0, 1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1833, 105102, 2, 1, 1, 0, 1, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1834, 105102, 2, 2, 1, 0, 1, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1835, 105102, 2, 3, 1, 0, 1, "欲しい薬が\nあったら言ってね\n調合してみるから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1836, 105102, 2, 4, 1, 0, 1, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1837, 105102, 2, 101, 8, 0, 1, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1838, 105102, 2, 102, 8, 0, 1, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1839, 105102, 2, 1, 1, 1, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1840, 105102, 2, 2, 1, 2, 1, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1841, 105102, 2, 3, 1, 3, 1, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1842, 105102, 3, 1, 1, 0, 1, "身なりについて\n研究するのも\n楽しそうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1843, 105102, 3, 2, 1, 0, 1, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1844, 105102, 3, 3, 1, 0, 1, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1845, 105102, 3, 4, 1, 0, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1846, 105102, 3, 5, 1, 0, 1, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1847, 105102, 3, 6, 1, 0, 1, "運動しないと\n体型の維持が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1848, 105102, 3, 7, 1, 0, 1, "適度な食事も\n大切よね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1849, 105102, 3, 8, 1, 0, 1, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1851, 105201, 1, 1, 1, 0, 1, "ぺっぺっ！\n…い、いけないっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1852, 105201, 1, 2, 1, 0, 1, "いっけな～い！\nひづめで床に傷が！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1853, 105201, 1, 3, 1, 0, 1, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1854, 105201, 1, 4, 1, 0, 1, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1855, 105201, 1, 5, 1, 0, 1, "みんな、体重\nどれくらいなのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1856, 105201, 1, 101, 4, 0, 1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1857, 105201, 1, 102, 8, 0, 1, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1858, 105201, 2, 1, 1, 0, 1, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1859, 105201, 2, 2, 1, 0, 1, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1860, 105201, 2, 3, 1, 0, 1, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1861, 105201, 2, 4, 1, 0, 1, "力仕事ならまかせて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1862, 105201, 2, 101, 4, 0, 1, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1863, 105201, 2, 102, 8, 0, 1, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1864, 105201, 2, 1, 1, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1865, 105201, 2, 2, 1, 2, 1, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1866, 105201, 2, 3, 1, 3, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1867, 105201, 3, 1, 1, 0, 1, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1868, 105201, 3, 2, 1, 0, 1, "ミシッっていった！\n私、重すぎるかも！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1869, 105201, 3, 3, 1, 0, 1, "あ！　あなたも\n美人さんね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1870, 105201, 3, 4, 1, 0, 1, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1871, 105201, 3, 5, 1, 0, 1, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1872, 105201, 3, 6, 1, 0, 1, "な、なんだか野生に\n帰っちゃいそう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1873, 105201, 3, 7, 1, 0, 1, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1874, 105201, 3, 8, 1, 0, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1876, 105202, 1, 1, 1, 0, 1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1877, 105202, 1, 2, 1, 0, 1, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1878, 105202, 1, 3, 1, 0, 1, "動物苑…って\n嫌なギルド名ね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1879, 105202, 1, 4, 1, 0, 1, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1880, 105202, 1, 5, 1, 0, 1, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1881, 105202, 1, 101, 8, 0, 1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1882, 105202, 1, 102, 8, 0, 1, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1883, 105202, 2, 1, 1, 0, 1, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1884, 105202, 2, 2, 1, 0, 1, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1885, 105202, 2, 3, 1, 0, 1, "ぺっぺっ！\n…い、いけないっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1886, 105202, 2, 4, 1, 0, 1, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1887, 105202, 2, 101, 8, 0, 1, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1888, 105202, 2, 102, 8, 0, 1, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1889, 105202, 2, 1, 1, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1890, 105202, 2, 2, 1, 2, 1, "ダイエット中だから\nお昼は抜いてるの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1891, 105202, 2, 3, 1, 3, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1892, 105202, 3, 1, 1, 0, 1, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1893, 105202, 3, 2, 1, 0, 1, "ミシッっていった！\n私、重すぎるかも！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1894, 105202, 3, 3, 1, 0, 1, "あ！　あなたも\n美人さんね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1895, 105202, 3, 4, 1, 0, 1, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1896, 105202, 3, 5, 1, 0, 1, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1897, 105202, 3, 6, 1, 0, 1, "な、なんだか野生に\n帰っちゃいそう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1898, 105202, 3, 7, 1, 0, 1, "何を食べるかより\n誰と食べるかが\n大事なのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1899, 105202, 3, 8, 1, 0, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1901, 105301, 1, 1, 1, 0, 1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1902, 105301, 1, 2, 1, 0, 1, "私は子供ではない…！\nが、お菓子は\nいただこう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1903, 105301, 1, 3, 1, 0, 1, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1904, 105301, 1, 4, 1, 0, 1, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1905, 105301, 1, 5, 1, 0, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1906, 105301, 1, 101, 4, 0, 1, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1907, 105301, 1, 102, 8, 0, 1, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1908, 105301, 2, 1, 1, 0, 1, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1909, 105301, 2, 2, 1, 0, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1910, 105301, 2, 3, 1, 0, 1, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1911, 105301, 2, 4, 1, 0, 1, "私ともっと高く、\n共に飛ぼう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1912, 105301, 2, 101, 4, 0, 1, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1913, 105301, 2, 102, 8, 0, 1, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1914, 105301, 2, 1, 1, 1, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1915, 105301, 2, 2, 1, 2, 1, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1916, 105301, 2, 3, 1, 3, 1, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1917, 105301, 3, 1, 1, 0, 1, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1918, 105301, 3, 2, 1, 0, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1919, 105301, 3, 3, 1, 0, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1920, 105301, 3, 4, 1, 0, 1, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1921, 105301, 3, 5, 1, 0, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1922, 105301, 3, 6, 1, 0, 1, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1923, 105301, 3, 7, 1, 0, 1, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1924, 105301, 3, 8, 1, 0, 1, "これを読んで、新たな\n戦術を編み出すぞっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1926, 105302, 1, 1, 1, 0, 1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1927, 105302, 1, 2, 1, 0, 1, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1928, 105302, 1, 3, 1, 0, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1929, 105302, 1, 4, 1, 0, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1930, 105302, 1, 5, 1, 0, 1, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1931, 105302, 1, 101, 8, 0, 1, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1932, 105302, 1, 102, 8, 0, 1, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1933, 105302, 2, 1, 1, 0, 1, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1934, 105302, 2, 2, 1, 0, 1, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1935, 105302, 2, 3, 1, 0, 1, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1936, 105302, 2, 4, 1, 0, 1, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1937, 105302, 2, 101, 8, 0, 1, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1938, 105302, 2, 102, 8, 0, 1, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1939, 105302, 2, 1, 1, 1, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1940, 105302, 2, 2, 1, 2, 1, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1941, 105302, 2, 3, 1, 3, 1, "寝る前に…あと少し\nだけ、鍛錬を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1942, 105302, 3, 1, 1, 0, 1, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1943, 105302, 3, 2, 1, 0, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1944, 105302, 3, 3, 1, 0, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1945, 105302, 3, 4, 1, 0, 1, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1946, 105302, 3, 5, 1, 0, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1947, 105302, 3, 6, 1, 0, 1, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1948, 105302, 3, 7, 1, 0, 1, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1949, 105302, 3, 8, 1, 0, 1, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1951, 105701, 1, 1, 1, 0, 1, "私たちの騎空艇\nどんなデザインに\nしよう…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1952, 105701, 1, 2, 1, 0, 1, "団員募集の\nポスター\n作ろうっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1953, 105701, 1, 3, 1, 0, 1, "父さん…\n今頃どこで\n何してるんだろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1954, 105701, 1, 4, 1, 0, 1, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1955, 105701, 1, 5, 1, 0, 1, "父さん以上の\n世界一の\n騎空士になりたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1956, 105701, 1, 101, 4, 0, 1, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1957, 105701, 1, 102, 8, 0, 1, "父さん…\n私にもね、大切な\n仲間ができたよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1958, 105701, 2, 1, 1, 0, 1, "仕事の依頼？\nすぐに支度\nしてくるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1959, 105701, 2, 2, 1, 0, 1, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1960, 105701, 2, 3, 1, 0, 1, "なーにー？\nどうしたの？\n今呼んだよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1961, 105701, 2, 4, 1, 0, 1, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1962, 105701, 2, 101, 4, 0, 1, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1963, 105701, 2, 102, 8, 0, 1, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1964, 105701, 2, 1, 1, 1, 1, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1965, 105701, 2, 2, 1, 2, 1, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1966, 105701, 2, 3, 1, 3, 1, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1967, 105701, 3, 1, 1, 0, 1, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1968, 105701, 3, 2, 1, 0, 1, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1969, 105701, 3, 3, 1, 0, 1, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1970, 105701, 3, 4, 1, 0, 1, "いい香り～♪\n気持ちが\n落ち着く～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1971, 105701, 3, 5, 1, 0, 1, "これって\n騎空艇に\n置けるかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1972, 105701, 3, 6, 1, 0, 1, "どんな仕事でも\nこなせるような\n体力がないとね…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1973, 105701, 3, 7, 1, 0, 1, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1974, 105701, 3, 8, 1, 0, 1, "空を冒険するには\n色んなことを\n知っておかないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1976, 105702, 1, 1, 1, 0, 1, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1977, 105702, 1, 2, 1, 0, 1, "暇だよ…\nどこかに強い人は\nいないか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1978, 105702, 1, 3, 1, 0, 1, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1979, 105702, 1, 4, 1, 0, 1, "今のうちに、\n魔物退治の\n仕事に行くね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1980, 105702, 1, 5, 1, 0, 1, "ハァ～…\nうん、気の巡りは\n問題ないね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1981, 105702, 1, 101, 8, 0, 1, "私の力の源は\n仲間との\n絆だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1982, 105702, 1, 102, 8, 0, 1, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1983, 105702, 2, 1, 1, 0, 1, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1984, 105702, 2, 2, 1, 0, 1, "私はいつも通りに\n喋ってるつもり\nだよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1985, 105702, 2, 3, 1, 0, 1, "どうした？\nもしかして手合せ\nしたいか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1986, 105702, 2, 4, 1, 0, 1, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1987, 105702, 2, 101, 8, 0, 1, "背中はあなたに\n預けたよ！\nよろしくね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1988, 105702, 2, 102, 8, 0, 1, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1989, 105702, 2, 1, 1, 1, 1, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1990, 105702, 2, 2, 1, 2, 1, "腹減ったね…\nなにか食べに\n行くよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1991, 105702, 2, 3, 1, 3, 1, "修行のしすぎは\n良くないよ？\n気をつけるね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1992, 105702, 3, 1, 1, 0, 1, "美しい技は\n美しい体が\nあってこそ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1993, 105702, 3, 2, 1, 0, 1, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1994, 105702, 3, 3, 1, 0, 1, "…練習に\n使うのはさすがに\n可哀そうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1995, 105702, 3, 4, 1, 0, 1, "これは…\n瞑想の時にも\n使ってみたいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1996, 105702, 3, 5, 1, 0, 1, "出来ればもっと\n熱いほうが\n嬉しいよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1997, 105702, 3, 6, 1, 0, 1, "ここでも\n修行出来るなんて\n最高だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1998, 105702, 3, 7, 1, 0, 1, "気力の源は体力！\n体力の源は\n健康とご飯！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (1999, 105702, 3, 8, 1, 0, 1, "んー…\n難しいことは\nわかんないよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2001, 105801, 1, 1, 1, 0, 1, "わたしたちの\nギルドハウスへ\nようこそ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2002, 105801, 1, 2, 1, 0, 1, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2003, 105801, 1, 3, 1, 0, 1, "ん？\n食べ物のにおい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2004, 105801, 1, 4, 1, 0, 1, "みんなと仲良く食卓を\n囲みたいですね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2005, 105801, 1, 5, 1, 0, 1, "この部屋が、わたしの\n帰る場所です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2006, 105801, 1, 101, 4, 0, 1, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2007, 105801, 1, 102, 8, 0, 1, "あの人、\n大食いの子って\n嫌いかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2008, 105801, 2, 1, 1, 0, 1, "食べもの、くれるん\nですか？　わーい！\n恋しちゃいそう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2009, 105801, 2, 2, 1, 0, 1, "つ、つまみ食いなんて\nしてませんよ？\nこれは味見です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2010, 105801, 2, 3, 1, 0, 1, "おやつは別腹、\nごはんも別腹、\n魔物も別腹～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2011, 105801, 2, 4, 1, 0, 1, "好きなものを、\n好きな人達と食べる！\n最高の贅沢ですね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2012, 105801, 2, 101, 4, 0, 1, "あなたは、\nごはん王子ですね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2013, 105801, 2, 102, 8, 0, 1, "大好きです～\n愛してます～\nえへへ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2014, 105801, 2, 1, 1, 1, 1, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2015, 105801, 2, 2, 1, 2, 1, "お昼ご飯、\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2016, 105801, 2, 3, 1, 3, 1, "お夕飯も、晩ごはんも\n食べたし…さぁ！\nお夜食の時間ですよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2017, 105801, 3, 1, 1, 0, 1, "身だしなみ、\n身だしなみ…\nきちんとしないとね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2018, 105801, 3, 2, 1, 0, 1, "ふい～\n消化中です～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2019, 105801, 3, 3, 1, 0, 1, "おいしそ…\nか、かわいいです～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2020, 105801, 3, 4, 1, 0, 1, "いい香りですね～\n空気までおいしそう～\nじゅるり♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2021, 105801, 3, 5, 1, 0, 1, "ほっかほか～♪\n魔物の丸焼きが\n作れちゃいますね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2022, 105801, 3, 6, 1, 0, 1, "走って食べて！\n走って食べて！\n\この無限ループです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2023, 105801, 3, 7, 1, 0, 1, "いくつでも\n食べられますよ！\n幸せ～☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2024, 105801, 3, 8, 1, 0, 1, "料理の絵を\n見ているだけで、\nよ、よだれが～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2026, 105802, 1, 1, 1, 0, 1, "この部屋が、わたしの\n帰る場所です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2027, 105802, 1, 2, 1, 0, 1, "ギルドハウス\nどんどん素敵に\nなっていくな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2028, 105802, 1, 3, 1, 0, 1, "「やばい」とか\n「おいっす」とか…\n民の言葉って難しい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2029, 105802, 1, 4, 1, 0, 1, "お菓子の家に\n住みたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2030, 105802, 1, 5, 1, 0, 1, "あ、頼んでた出前が\n届いたかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2031, 105802, 1, 101, 8, 0, 1, "大切な人がいて\n大切な場所がある\nすごく幸せです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2032, 105802, 1, 102, 8, 0, 1, "あの人\n大食いの子って\n嫌いかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2033, 105802, 2, 1, 1, 0, 1, "ごはんに行きますか？\n作りましょうか？\n店屋物もいいですね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2034, 105802, 2, 2, 1, 0, 1, "モグモグ…\nあげませんよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2035, 105802, 2, 3, 1, 0, 1, "おやつは別腹\nごはんも別腹\n魔物も別腹～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2036, 105802, 2, 4, 1, 0, 1, "好きなものを\n好きな人達と食べる！\n最高の贅沢ですね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2037, 105802, 2, 101, 8, 0, 1, "大好きです～\n愛してます～\nえへへ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2038, 105802, 2, 102, 8, 0, 1, "わたし食べても\n太らないんです♪\nほらほら～触って\nみてくださいよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2039, 105802, 2, 1, 1, 1, 1, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2040, 105802, 2, 2, 1, 2, 1, "お昼ご飯\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2041, 105802, 2, 3, 1, 3, 1, "お夕飯も、晩ごはんも\n食べたし～。さぁ！\nお夜食の時間ですよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2042, 105802, 3, 1, 1, 0, 1, "お姫さまみたいに\nきれいに\nならないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2043, 105802, 3, 2, 1, 0, 1, "のんびりしていても\nお腹がすいちゃうん\nですよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2044, 105802, 3, 3, 1, 0, 1, "おいしそ…\nか、かわいいです～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2045, 105802, 3, 4, 1, 0, 1, "アップル系、ピーチ系\nオレンジ系…\n香りも色々ですね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2046, 105802, 3, 5, 1, 0, 1, "ここでお魚を\n焼けたら\n最高ですね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2047, 105802, 3, 6, 1, 0, 1, "身体も鍛えなきゃ～！\nお腹がすいちゃいます\nけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2048, 105802, 3, 7, 1, 0, 1, "もぐもぐもぐもぐもぐ\nもぐもぐもぐもぐもぐ\nもぐもぐもぐもぐ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2049, 105802, 3, 8, 1, 0, 1, "流行語発表…\nこんな言葉遣いが！\nやばいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2051, 106001, 1, 1, 1, 0, 1, "いろいろ美味しいもの\n食べられるし…\nけっこう幸せかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2052, 106001, 1, 2, 1, 0, 1, "んーー、あたし\nなんで、このギルドに\n入っちゃったんだろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2053, 106001, 1, 3, 1, 0, 1, "あいつが来たら、\n尾行しなくちゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2054, 106001, 1, 4, 1, 0, 1, "この部屋のどこかに、\nあいつの秘密が\n隠されてるかも？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2055, 106001, 1, 5, 1, 0, 1, "ま、まずいわ…\nギルド活動、\nけっこう楽しい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2056, 106001, 1, 101, 4, 0, 1, "あいつを監視してる\nと…な、なんか\n変な気分", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2057, 106001, 1, 102, 8, 0, 1, "主さま…\nだ、誰も聞いてない\nわよね！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2058, 106001, 2, 1, 1, 0, 1, "気まぐれは\nチャームポイント\nみたいなもんよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2059, 106001, 2, 2, 1, 0, 1, "うひゃっ！？\nど、どうしてあんたは\nいつも驚かせるのよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2060, 106001, 2, 3, 1, 0, 1, "ぎゃー！\n虫もって近づくなー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2061, 106001, 2, 4, 1, 0, 1, "この部屋って、\n野良猫を拾ってきたら\nだめかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2062, 106001, 2, 101, 4, 0, 1, "遊びにいくわよ！\nあんたはあたしの\n言うとおりにすれば\nいいの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2063, 106001, 2, 102, 8, 0, 1, "あんた、あたしに\n操られてみない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2064, 106001, 2, 1, 1, 1, 1, "美食殿としては、\n朝食もこだわらないと\nねぇ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2065, 106001, 2, 2, 1, 2, 1, "ギルド活動だもん！\n美味しいランチを\n食べましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2066, 106001, 2, 3, 1, 3, 1, "ディナーに行くわよ！\nギルドの目的、\n食べ歩きだもんね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2067, 106001, 3, 1, 1, 0, 1, "今日もしっぽが\nいい感じ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2068, 106001, 3, 2, 1, 0, 1, "こういう所だと、\n丸くなっちゃうのよね\nうにゃぁ～～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2069, 106001, 3, 3, 1, 0, 1, "これ、あたしの能力で\n操れないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2070, 106001, 3, 4, 1, 0, 1, "うにゃぁ～♪\nにゃん、にゃお～ん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2071, 106001, 3, 5, 1, 0, 1, "はぁ～ゴロゴロ\nしたくなるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2072, 106001, 3, 6, 1, 0, 1, "特訓特訓！\nあたしは\nやればできる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2073, 106001, 3, 7, 1, 0, 1, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2074, 106001, 3, 8, 1, 0, 1, "魔法を使うにも、\n知識が必要だものね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2076, 106002, 1, 1, 1, 0, 1, "ま、まずいわ…\nギルド活動\nけっこう楽しい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2077, 106002, 1, 2, 1, 0, 1, "いろいろ美味しいもの\n食べられるし…\nけっこう幸せかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2078, 106002, 1, 3, 1, 0, 1, "主さま…\nだ、誰も聞いてない\nわよね！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2079, 106002, 1, 4, 1, 0, 1, "監視監視…\nうん！　あたしは\nなまけてないわよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2080, 106002, 1, 5, 1, 0, 1, "へ、陛下に\nみつからないように\nしなくっちゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2081, 106002, 1, 101, 8, 0, 1, "あいつが来たら、\n尾行しなくちゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2082, 106002, 1, 102, 8, 0, 1, "あいつと会ってから\n調子くるったな…\n楽しいけどさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2083, 106002, 2, 1, 1, 0, 1, "食わず嫌いって\nあるのよね\n食べ物もだけど\n人も…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2084, 106002, 2, 2, 1, 0, 1, "うひゃっ！？\nど、どうしてあんたは\nいつも驚かせるのよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2085, 106002, 2, 3, 1, 0, 1, "話し相手になって\nやるわよ…情報\n聞き出せるかもだし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2086, 106002, 2, 4, 1, 0, 1, "この部屋って\n野良猫を拾ってきたら\nだめかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2087, 106002, 2, 101, 8, 0, 1, "遊びにいくわよ！\nあんたはあたしの\n言うとおりにすれば\nいいの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2088, 106002, 2, 102, 8, 0, 1, "あんた、あたしに\n操られてみない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2089, 106002, 2, 1, 1, 1, 1, "美食殿としては\n朝食もこだわらないと\nねぇ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2090, 106002, 2, 2, 1, 2, 1, "ギルド活動だもん！\n美味しいランチを\n食べましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2091, 106002, 2, 3, 1, 3, 1, "ディナーに行くわよ！\nギルドの目的\n食べ歩きだもんね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2092, 106002, 3, 1, 1, 0, 1, "今日もしっぽが\nいい感じ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2093, 106002, 3, 2, 1, 0, 1, "うにゃ～\nどんどん眠く\nなる～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2094, 106002, 3, 3, 1, 0, 1, "これ、あたしの能力で\n操れないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2095, 106002, 3, 4, 1, 0, 1, "あたし、みんなや\nあいつと同じ香りを\n身に着けてるんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2096, 106002, 3, 5, 1, 0, 1, "うっ、のんびり\nしたくなる…\n特訓しなきゃ\nいけないのに～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2097, 106002, 3, 6, 1, 0, 1, "特訓特訓！\nあたしは\nやればできる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2098, 106002, 3, 7, 1, 0, 1, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2099, 106002, 3, 8, 1, 0, 1, "魔法を使うにも\n知識が必要だものね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2101, 105901, 1, 1, 1, 0, 1, "この空間は主さまの\n香りがします\n…ふふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2102, 105901, 1, 2, 1, 0, 1, "炊事、洗濯、お掃除、\nお買いものもしなくて\nはいけませんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2103, 105901, 1, 3, 1, 0, 1, "アメスさまへの、\nお祈りができそうな\n場所は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2104, 105901, 1, 4, 1, 0, 1, "さすが都会…\nおいしいものが\nたくさんあるのですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2105, 105901, 1, 5, 1, 0, 1, "主さまが不自由なく\n使える部屋に\nしなくては…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2106, 105901, 1, 101, 4, 0, 1, "できるなら…\n主さまと二人だけの\n部屋の方が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2107, 105901, 1, 102, 8, 0, 1, "主さまの隣には\nわたくしが…\nわたくしの隣には、\n主さまが…幸せです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2108, 105901, 2, 1, 1, 0, 1, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2109, 105901, 2, 2, 1, 0, 1, "もしお疲れでしたら\n肩でも\nお揉みしましょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2110, 105901, 2, 3, 1, 0, 1, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2111, 105901, 2, 4, 1, 0, 1, "今日のおこづかいです\n何かおいしいものを\n召し上がってください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2112, 105901, 2, 101, 4, 0, 1, "主さまのおてて\n温かいですね…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2113, 105901, 2, 102, 8, 0, 1, "主さまのおそばに\nいられて、わたくし、\n嬉しいです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2114, 105901, 2, 1, 1, 1, 1, "おはようございます。\n洗濯をしなければ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2115, 105901, 2, 2, 1, 2, 1, "そろそろお昼ですね。\nおいしいものを\n食べましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2116, 105901, 2, 3, 1, 3, 1, "おやすみになる時は、\nお声をかけてください\nお供致します", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2117, 105901, 3, 1, 1, 0, 1, "もう少し…\nおっきくなりたい\nです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2118, 105901, 3, 2, 1, 0, 1, "このふかふかの優しさ\n主さまのようです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2119, 105901, 3, 3, 1, 0, 1, "かわいい♪\n主さまにそっくり♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2120, 105901, 3, 4, 1, 0, 1, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2121, 105901, 3, 5, 1, 0, 1, "ここで洗濯物を\n乾かせるでしょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2122, 105901, 3, 6, 1, 0, 1, "都会の人々は\nこんなふうに\n運動を…！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2123, 105901, 3, 7, 1, 0, 1, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2124, 105901, 3, 8, 1, 0, 1, "よく学んで、\n主さまのお役に\n立たなければ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2126, 105902, 1, 1, 1, 0, 1, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2127, 105902, 1, 2, 1, 0, 1, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2128, 105902, 1, 3, 1, 0, 1, "アメスさまへの\nお祈りができそうな\n場所は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2129, 105902, 1, 4, 1, 0, 1, "瞑想をすると\n心が安らぎます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2130, 105902, 1, 5, 1, 0, 1, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2131, 105902, 1, 101, 8, 0, 1, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2132, 105902, 1, 102, 8, 0, 1, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2133, 105902, 2, 1, 1, 0, 1, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2134, 105902, 2, 2, 1, 0, 1, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2135, 105902, 2, 3, 1, 0, 1, "お出かけですか？\nわたくしもお供\nいたします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2136, 105902, 2, 4, 1, 0, 1, "ひゃん！\n主さま…\nいじわるです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2137, 105902, 2, 101, 8, 0, 1, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2138, 105902, 2, 102, 8, 0, 1, "主さまの優しさに\nわたくしはいつも\n癒されています…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2139, 105902, 2, 1, 1, 1, 1, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2140, 105902, 2, 2, 1, 2, 1, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2141, 105902, 2, 3, 1, 3, 1, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2142, 105902, 3, 1, 1, 0, 1, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2143, 105902, 3, 2, 1, 0, 1, "このふかふかの優しさ\n主さまのようです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2144, 105902, 3, 3, 1, 0, 1, "かわいい♪\n主さまにそっくり♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2145, 105902, 3, 4, 1, 0, 1, "この心地よさ…\n主さまのようです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2146, 105902, 3, 5, 1, 0, 1, "ここで洗濯物を\n乾かせるでしょうか　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2147, 105902, 3, 6, 1, 0, 1, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2148, 105902, 3, 7, 1, 0, 1, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2149, 105902, 3, 8, 1, 0, 1, "都会のことを\n学ばないと…\nふむふむ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2151, 190801, 2, 1, 1, 0, 1, "ふぁ～…あっ\n見られちゃい\nましたか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2152, 190801, 2, 2, 1, 0, 1, "ギルドハウスの\n使い心地は\nどうですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2153, 190801, 2, 3, 1, 0, 1, "ご用があれば\nなんでも言って\nくださいね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2154, 190801, 2, 4, 1, 0, 1, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2155, 190801, 2, 1, 1, 1, 1, "おはようございます！\nお掃除の方、\nはじめていきますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2156, 190801, 2, 2, 1, 2, 1, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2157, 190801, 2, 3, 1, 3, 1, "ここは夜でも\nにぎやかですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2158, 104701, 1, 1, 1, 0, 1, "…\n少しうとうと\nしてしまった", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2159, 104701, 1, 2, 1, 0, 1, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2160, 104701, 1, 3, 1, 0, 1, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2161, 104701, 1, 4, 1, 0, 1, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2162, 104701, 1, 5, 1, 0, 1, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2163, 104701, 1, 101, 4, 0, 1, "少年のことを考えると\n顔がにやけてしまう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2164, 104701, 1, 102, 8, 0, 1, "少年は今頃\n平和に過ごせて\nいるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2165, 104701, 2, 1, 1, 0, 1, "ん？　悪いが\n兜は脱がないぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2166, 104701, 2, 2, 1, 0, 1, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2167, 104701, 2, 3, 1, 0, 1, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2168, 104701, 2, 4, 1, 0, 1, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2169, 104701, 2, 101, 4, 0, 1, "君といると、兜の中が\nいつもより暑いんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2170, 104701, 2, 102, 8, 0, 1, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2171, 104701, 2, 1, 1, 1, 1, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2172, 104701, 2, 2, 1, 2, 1, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2173, 104701, 2, 3, 1, 3, 1, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2175, 104701, 3, 2, 1, 0, 1, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2176, 104701, 3, 3, 1, 0, 1, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2177, 104701, 3, 4, 1, 0, 1, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2178, 104701, 3, 5, 1, 0, 1, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2180, 104701, 3, 7, 1, 0, 1, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2181, 104701, 3, 8, 1, 0, 1, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2183, 102601, 1, 1, 1, 0, 1, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2184, 102601, 1, 2, 1, 0, 1, "ドングリの木\n近くに生えてない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2185, 102601, 1, 3, 1, 0, 1, "快適な寝床があれば\nそれだけで幸せだねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2186, 102601, 1, 4, 1, 0, 1, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2187, 102601, 1, 5, 1, 0, 1, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2188, 102601, 1, 101, 4, 0, 1, "あんパン買ったし\nあいつにも\n分けてあげよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2189, 102601, 1, 102, 8, 0, 1, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2190, 102601, 2, 1, 1, 0, 1, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2191, 102601, 2, 2, 1, 0, 1, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2192, 102601, 2, 3, 1, 0, 1, "ドングリあげるから\nあんパン買ってきて～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2193, 102601, 2, 4, 1, 0, 1, "う～\n疲れちゃったよ～\nおんぶして運んで～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2194, 102601, 2, 101, 4, 0, 1, "あんたといると\nリア充って\n感じがするよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2195, 102601, 2, 102, 8, 0, 1, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2196, 102601, 2, 1, 1, 1, 1, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2197, 102601, 2, 2, 1, 2, 1, "そろそろお昼寝でも\nしたい気分だねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2198, 102601, 2, 3, 1, 3, 1, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2200, 102601, 3, 2, 1, 0, 1, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2201, 102601, 3, 3, 1, 0, 1, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2202, 102601, 3, 4, 1, 0, 1, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2203, 102601, 3, 5, 1, 0, 1, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2205, 102601, 3, 7, 1, 0, 1, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2206, 102601, 3, 8, 1, 0, 1, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2208, 106301, 1, 1, 1, 0, 1, "ランドソル…\nおっきな街だなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2209, 106301, 1, 2, 1, 0, 1, "ロザリア…\nきっと、いるよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2210, 106301, 1, 3, 1, 0, 1, "最近、弓の練習\nサボっちゃってるな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2211, 106301, 1, 4, 1, 0, 1, "今日の私は\n森番じゃなくて\n家番だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2212, 106301, 1, 5, 1, 0, 1, "ちょっとだけ\n慣れてきたかも？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2213, 106301, 1, 101, 4, 0, 1, "いつか、\nあの人と一緒に…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2214, 106301, 1, 102, 8, 0, 1, "…なんか最近、\nあの人のこと\nばっかり…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2215, 106301, 2, 1, 1, 0, 1, "もしよかったら\n街に出かけない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2216, 106301, 2, 2, 1, 0, 1, "この部屋、\n妖精さんも\n気に入るかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2217, 106301, 2, 3, 1, 0, 1, "今日は\nなににしよっか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2218, 106301, 2, 4, 1, 0, 1, "今日はどこ行く？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2219, 106301, 2, 101, 4, 0, 1, "たまには二人で…\nな、なんちゃって！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2220, 106301, 2, 102, 8, 0, 1, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2221, 106301, 2, 1, 1, 1, 1, "おはよう！\n今日も一緒に\n頑張ろうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2222, 106301, 2, 2, 1, 2, 1, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2223, 106301, 2, 3, 1, 3, 1, "ランドソルは\n夜でも賑やかだね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2225, 106301, 3, 2, 1, 0, 1, "わぁっ！\nすっごく\nふわふわだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2226, 106301, 3, 3, 1, 0, 1, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2227, 106301, 3, 4, 1, 0, 1, "くんくん\nなんか…いい匂い？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2228, 106301, 3, 5, 1, 0, 1, "すっごいなー\nあったかいなー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2230, 106301, 3, 7, 1, 0, 1, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2231, 106301, 3, 8, 1, 0, 1, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2233, 103601, 1, 1, 1, 0, 1, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2234, 103601, 1, 2, 1, 0, 1, "カップケーキが\n食べたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2235, 103601, 1, 3, 1, 0, 1, "予習が終わりました！\nさて、次は復習を\nしないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2236, 103601, 1, 4, 1, 0, 1, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2237, 103601, 1, 5, 1, 0, 1, "男の人は、みんな\nへんたいだと\n聞きました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2238, 103601, 1, 101, 4, 0, 1, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2239, 103601, 1, 102, 8, 0, 1, "早く大人になりたいな\n…あの人と同じくらい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2240, 103601, 2, 1, 1, 0, 1, "ぼ、防犯魔石を\n鳴らしますよ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2241, 103601, 2, 2, 1, 0, 1, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2242, 103601, 2, 3, 1, 0, 1, "これ宿題です\nやっておいて\nくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2243, 103601, 2, 4, 1, 0, 1, "あ、遅刻ですよ\n待っていたんですから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2244, 103601, 2, 101, 4, 0, 1, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2245, 103601, 2, 102, 8, 0, 1, "あの…何歳まで\n子供だと思いますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2246, 103601, 2, 1, 1, 1, 1, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2247, 103601, 2, 2, 1, 2, 1, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2248, 103601, 2, 3, 1, 3, 1, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2250, 103601, 3, 2, 1, 0, 1, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2251, 103601, 3, 3, 1, 0, 1, "つ、つぶらな瞳には\n弱いんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2252, 103601, 3, 4, 1, 0, 1, "リラックスできますね\n集中力も\n上がりそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2253, 103601, 3, 5, 1, 0, 1, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2255, 103601, 3, 7, 1, 0, 1, "ピーマンもこれくらい\nおいしければ\nいいのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2256, 103601, 3, 8, 1, 0, 1, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2258, 101501, 1, 1, 1, 0, 1, "今日はみんなに\nなにを読んで\nあげようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2259, 101501, 1, 2, 1, 0, 1, "子どもたちと同じ\n目線で考えるのが\n大事よね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2260, 101501, 1, 3, 1, 0, 1, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2261, 101501, 1, 4, 1, 0, 1, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2262, 101501, 1, 5, 1, 0, 1, "お遊戯会の配役、\n考え出すと\n大変だわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2263, 101501, 1, 101, 4, 0, 1, "彼は優しいから、\nきっと良い\nお父さんになるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2264, 101501, 1, 102, 8, 0, 1, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2265, 101501, 2, 1, 1, 0, 1, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2266, 101501, 2, 2, 1, 0, 1, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2267, 101501, 2, 3, 1, 0, 1, "よかったら一緒に\n絵本を作らない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2268, 101501, 2, 4, 1, 0, 1, "あなたはいつも元気ね\n先生も見習わないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2269, 101501, 2, 101, 4, 0, 1, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2270, 101501, 2, 102, 8, 0, 1, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2271, 101501, 2, 1, 1, 1, 1, "おはよう～\nちゃんと朝ごはんは\n食べた？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2272, 101501, 2, 2, 1, 2, 1, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2273, 101501, 2, 3, 1, 3, 1, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2275, 101501, 3, 2, 1, 0, 1, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2276, 101501, 3, 3, 1, 0, 1, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2277, 101501, 3, 4, 1, 0, 1, "いい香りね～\n保育園にも\n置こうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2278, 101501, 3, 5, 1, 0, 1, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2280, 101501, 3, 7, 1, 0, 1, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2281, 101501, 3, 8, 1, 0, 1, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2283, 104401, 1, 1, 1, 0, 1, "世界の前に、\nこの部屋を征服\nしてやろう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2284, 104401, 1, 2, 1, 0, 1, "拠点とはやはり\n斯くあるべきじゃの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2285, 104401, 1, 3, 1, 0, 1, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2286, 104401, 1, 4, 1, 0, 1, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2287, 104401, 1, 5, 1, 0, 1, "うるさいのう～\nまったく、近頃の\n若いもんは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2288, 104401, 1, 101, 4, 0, 1, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2289, 104401, 1, 102, 8, 0, 1, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2290, 104401, 2, 1, 1, 0, 1, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2291, 104401, 2, 2, 1, 0, 1, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2292, 104401, 2, 3, 1, 0, 1, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2293, 104401, 2, 4, 1, 0, 1, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2294, 104401, 2, 101, 4, 0, 1, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2295, 104401, 2, 102, 8, 0, 1, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2296, 104401, 2, 1, 1, 1, 1, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2297, 104401, 2, 2, 1, 2, 1, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2298, 104401, 2, 3, 1, 3, 1, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2300, 104401, 3, 2, 1, 0, 1, "よい座り心地じゃ\nわらわの玉座としよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2301, 104401, 3, 3, 1, 0, 1, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2302, 104401, 3, 4, 1, 0, 1, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2303, 104401, 3, 5, 1, 0, 1, "あたたかい…\n身も心も\n浄化されるようじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2305, 104401, 3, 7, 1, 0, 1, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2306, 104401, 3, 8, 1, 0, 1, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2308, 105401, 1, 1, 1, 0, 1, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2309, 105401, 1, 2, 1, 0, 1, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2310, 105401, 1, 3, 1, 0, 1, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2311, 105401, 1, 4, 1, 0, 1, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2312, 105401, 1, 5, 1, 0, 1, "ノゾミさんの\n衣装はああして\nチカさんのは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2313, 105401, 1, 101, 4, 0, 1, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2314, 105401, 1, 102, 8, 0, 1, "まさか私が\nアイドルになるなんて\n思ってなかったです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2315, 105401, 2, 1, 1, 0, 1, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2316, 105401, 2, 2, 1, 0, 1, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2317, 105401, 2, 3, 1, 0, 1, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2318, 105401, 2, 4, 1, 0, 1, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2319, 105401, 2, 101, 4, 0, 1, "どうしました？\n新しい服が\n欲しくなりましたか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2320, 105401, 2, 102, 8, 0, 1, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2321, 105401, 2, 1, 1, 1, 1, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2322, 105401, 2, 2, 1, 2, 1, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2323, 105401, 2, 3, 1, 3, 1, "夜の方が\n作業が捗るのは\n私だけですかね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2325, 105401, 3, 2, 1, 0, 1, "このふかふか具合\nいい素材を\n使ってますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2326, 105401, 3, 3, 1, 0, 1, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2327, 105401, 3, 4, 1, 0, 1, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2328, 105401, 3, 5, 1, 0, 1, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2330, 105401, 3, 7, 1, 0, 1, "何をするにも\n食事は大切！\n体力が資本です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2331, 105401, 3, 8, 1, 0, 1, "私もいつか\nデザインを\nまとめて本に…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2333, 107501, 1, 1, 1, 0, 1, "またお魚を\n捕りにいこうかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2334, 107501, 1, 2, 1, 0, 1, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2335, 107501, 1, 3, 1, 0, 1, "ここなら、\n日焼けの心配は\nありませんね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2336, 107501, 1, 4, 1, 0, 1, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2337, 107501, 1, 5, 1, 0, 1, "泳ぐのも食べるのも\n大得意です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2338, 107501, 1, 101, 4, 0, 1, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2339, 107501, 1, 102, 8, 0, 1, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2340, 107501, 2, 1, 1, 0, 1, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2341, 107501, 2, 2, 1, 0, 1, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2342, 107501, 2, 3, 1, 0, 1, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2343, 107501, 2, 4, 1, 0, 1, "あなたの水着、\n私が選んであげます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2344, 107501, 2, 101, 4, 0, 1, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2345, 107501, 2, 102, 8, 0, 1, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2346, 107501, 2, 1, 1, 1, 1, "朝の海も\n幻想的で\nいいですよねぇ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2347, 107501, 2, 2, 1, 2, 1, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2348, 107501, 2, 3, 1, 3, 1, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2350, 107501, 3, 2, 1, 0, 1, "泳ぎ疲れたので\nちょっと休憩です～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2351, 107501, 3, 3, 1, 0, 1, "あなたにも、\n可愛い水着を\n着せてあげたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2352, 107501, 3, 4, 1, 0, 1, "なんだか、\nリゾート気分です…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2353, 107501, 3, 5, 1, 0, 1, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2355, 107501, 3, 7, 1, 0, 1, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2356, 107501, 3, 8, 1, 0, 1, "南国のフルーツ図鑑\n…じゅるり…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2358, 107501, 3, 10, 1, 0, 1, "わ～♪　おいしそ…\nか、かわいいですね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2359, 107501, 3, 11, 1, 0, 1, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2360, 107601, 1, 1, 1, 0, 1, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2361, 107601, 1, 2, 1, 0, 1, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2362, 107601, 1, 3, 1, 0, 1, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2363, 107601, 1, 4, 1, 0, 1, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2364, 107601, 1, 5, 1, 0, 1, "クロールの練習なら、\n部屋でもできそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2365, 107601, 1, 101, 4, 0, 1, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2366, 107601, 1, 102, 8, 0, 1, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2367, 107601, 2, 1, 1, 0, 1, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2368, 107601, 2, 2, 1, 0, 1, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2369, 107601, 2, 3, 1, 0, 1, "海の美しさは、\n森とはちがう\n魅力がありました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2370, 107601, 2, 4, 1, 0, 1, "この髪飾りですか？\n造花ですから\n水の中も平気です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2371, 107601, 2, 101, 4, 0, 1, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2372, 107601, 2, 102, 8, 0, 1, "はじめての海が、\n主さまと一緒で\n幸せです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2373, 107601, 2, 1, 1, 1, 1, "おはようございます。\n泳ぎに行く準備は\n万端です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2374, 107601, 2, 2, 1, 2, 1, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2375, 107601, 2, 3, 1, 3, 1, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2377, 107601, 3, 2, 1, 0, 1, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2378, 107601, 3, 3, 1, 0, 1, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2379, 107601, 3, 4, 1, 0, 1, "この香り…\nきっと主さまも\n気に入ります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2380, 107601, 3, 5, 1, 0, 1, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2382, 107601, 3, 7, 1, 0, 1, "泳いだ後の食事は\n一段と美味しく\n感じます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2383, 107601, 3, 8, 1, 0, 1, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2385, 107601, 3, 10, 1, 0, 1, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2386, 107601, 3, 11, 1, 0, 1, "ふかふかですね\n主さまのようです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2387, 107701, 1, 1, 1, 0, 1, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2388, 107701, 1, 2, 1, 0, 1, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2389, 107701, 1, 3, 1, 0, 1, "今日はドジを\nしませんように！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2390, 107701, 1, 4, 1, 0, 1, "接客業を極めれば\nメイドとして\n成長できるかも？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2391, 107701, 1, 5, 1, 0, 1, "水着って\n動きやすいんですけど\n人の目が気になります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2392, 107701, 1, 101, 4, 0, 1, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2393, 107701, 1, 102, 8, 0, 1, "水着でご奉仕したら、\nあの人は喜ぶかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2394, 107701, 2, 1, 1, 0, 1, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2395, 107701, 2, 2, 1, 0, 1, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2396, 107701, 2, 3, 1, 0, 1, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2397, 107701, 2, 4, 1, 0, 1, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2398, 107701, 2, 101, 4, 0, 1, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2399, 107701, 2, 102, 8, 0, 1, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2400, 107701, 2, 1, 1, 1, 1, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2401, 107701, 2, 2, 1, 2, 1, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2402, 107701, 2, 3, 1, 3, 1, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2404, 107701, 3, 2, 1, 0, 1, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2405, 107701, 3, 3, 1, 0, 1, "きれいにすれば\nかわいさも倍増\nですよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2406, 107701, 3, 4, 1, 0, 1, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2407, 107701, 3, 5, 1, 0, 1, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2409, 107701, 3, 7, 1, 0, 1, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2410, 107701, 3, 8, 1, 0, 1, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2412, 107701, 3, 10, 1, 0, 1, "えへへ♪\n南の島まで\n乗せてください！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2413, 107701, 3, 11, 1, 0, 1, "なんだか、\n人魚姫に\nなった気分です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2414, 107801, 1, 1, 1, 0, 1, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2415, 107801, 1, 2, 1, 0, 1, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2416, 107801, 1, 3, 1, 0, 1, "ビーチにいると\n日焼けが心配だわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2417, 107801, 1, 4, 1, 0, 1, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2418, 107801, 1, 5, 1, 0, 1, "お、泳ぎの練習なんて\nしないからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2419, 107801, 1, 101, 4, 0, 1, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2420, 107801, 1, 102, 8, 0, 1, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2421, 107801, 2, 1, 1, 0, 1, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2422, 107801, 2, 2, 1, 0, 1, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2423, 107801, 2, 3, 1, 0, 1, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2424, 107801, 2, 4, 1, 0, 1, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2425, 107801, 2, 101, 4, 0, 1, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2426, 107801, 2, 102, 8, 0, 1, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2427, 107801, 2, 1, 1, 1, 1, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2428, 107801, 2, 2, 1, 2, 1, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2429, 107801, 2, 3, 1, 3, 1, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2431, 107801, 3, 2, 1, 0, 1, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2432, 107801, 3, 3, 1, 0, 1, "こんな魔物がいたら\n操るのも楽しいかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2433, 107801, 3, 4, 1, 0, 1, "にゃう～♪\nまるで南国にいる\n気分ね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2434, 107801, 3, 5, 1, 0, 1, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2436, 107801, 3, 7, 1, 0, 1, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2437, 107801, 3, 8, 1, 0, 1, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2439, 107801, 3, 10, 1, 0, 1, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2440, 107801, 3, 11, 1, 0, 1, "リゾートリゾートっ\nのんびりしましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2441, 108001, 1, 1, 1, 0, 1, "ここなら思う存分\nデスクワークに\n励めるわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2442, 108001, 1, 2, 1, 0, 1, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2443, 108001, 1, 3, 1, 0, 1, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2444, 108001, 1, 4, 1, 0, 1, "少し休んだら\n次のバイトに\n行ってくるわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2445, 108001, 1, 5, 1, 0, 1, "この格好でここに\nいるのって\n気恥ずかしいわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2446, 108001, 1, 101, 4, 0, 1, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2447, 108001, 1, 102, 8, 0, 1, "キミだけね…\n私のことをここまで\n理解してくれるのは", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2448, 108001, 2, 1, 1, 0, 1, "普段と違う格好\nだからって\nそんなに見ないでよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2449, 108001, 2, 2, 1, 0, 1, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2450, 108001, 2, 3, 1, 0, 1, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2451, 108001, 2, 4, 1, 0, 1, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2452, 108001, 2, 101, 4, 0, 1, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2453, 108001, 2, 102, 8, 0, 1, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2454, 108001, 2, 1, 1, 1, 1, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2455, 108001, 2, 2, 1, 2, 1, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2456, 108001, 2, 3, 1, 3, 1, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2458, 108001, 3, 2, 1, 0, 1, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2459, 108001, 3, 3, 1, 0, 1, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2460, 108001, 3, 4, 1, 0, 1, "いい香りね…\n働く意欲が\n湧いてくるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2461, 108001, 3, 5, 1, 0, 1, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2463, 108001, 3, 7, 1, 0, 1, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2464, 108001, 3, 8, 1, 0, 1, "座学も大切ね\nもっと知識を\n身につけないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2466, 108001, 3, 10, 1, 0, 1, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2467, 108001, 3, 11, 1, 0, 1, "落ち着くわ…\n海とひとつになった\n気分ね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2468, 107901, 1, 1, 1, 0, 1, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2469, 107901, 1, 2, 1, 0, 1, "猫グッズで\n海の家を飾りたいにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2470, 107901, 1, 3, 1, 0, 1, "海の幸は美味しいけど\n水はイヤだにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2471, 107901, 1, 4, 1, 0, 1, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2472, 107901, 1, 5, 1, 0, 1, "海と言えば水着にゃ\n似合ってるかにゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2473, 107901, 1, 101, 4, 0, 1, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2474, 107901, 1, 102, 8, 0, 1, "あたしの宝は\nここにあるにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2475, 107901, 2, 1, 1, 0, 1, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2476, 107901, 2, 2, 1, 0, 1, "今からキミを\nあたしの相棒に\n任命するにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2477, 107901, 2, 3, 1, 0, 1, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2478, 107901, 2, 4, 1, 0, 1, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2479, 107901, 2, 101, 4, 0, 1, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2480, 107901, 2, 102, 8, 0, 1, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2481, 107901, 2, 1, 1, 1, 1, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2482, 107901, 2, 2, 1, 2, 1, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2483, 107901, 2, 3, 1, 3, 1, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2485, 107901, 3, 2, 1, 0, 1, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2486, 107901, 3, 3, 1, 0, 1, "にゃにゃ～\nなんてかわいさにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2487, 107901, 3, 4, 1, 0, 1, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2488, 107901, 3, 5, 1, 0, 1, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2490, 107901, 3, 7, 1, 0, 1, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2491, 107901, 3, 8, 1, 0, 1, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2493, 107901, 3, 10, 1, 0, 1, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2494, 107901, 3, 11, 1, 0, 1, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2495, 103701, 1, 1, 1, 0, 1, "人の家って、道場とは\n違った香りがするね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2496, 103701, 1, 2, 1, 0, 1, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2497, 103701, 1, 3, 1, 0, 1, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2498, 103701, 1, 4, 1, 0, 1, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2499, 103701, 1, 5, 1, 0, 1, "もっと\n上手く立ち回って\n出世したいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2500, 103701, 1, 101, 4, 0, 1, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2501, 103701, 1, 102, 8, 0, 1, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2502, 103701, 2, 1, 1, 0, 1, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2503, 103701, 2, 2, 1, 0, 1, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2504, 103701, 2, 3, 1, 0, 1, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2505, 103701, 2, 4, 1, 0, 1, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2506, 103701, 2, 101, 4, 0, 1, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2507, 103701, 2, 102, 8, 0, 1, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2508, 103701, 2, 1, 1, 1, 1, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2509, 103701, 2, 2, 1, 2, 1, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2510, 103701, 2, 3, 1, 3, 1, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2512, 103701, 3, 2, 1, 0, 1, "いざという時のため\nにも、休憩は大事だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2513, 103701, 3, 3, 1, 0, 1, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2514, 103701, 3, 4, 1, 0, 1, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2515, 103701, 3, 5, 1, 0, 1, "あったかいなぁ。\nたまにはのんびり\nしようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2517, 103701, 3, 7, 1, 0, 1, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2518, 103701, 3, 8, 1, 0, 1, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2520, 100501, 1, 1, 1, 0, 1, "正義の刃で\n悪を挫く！\n…キマったッス♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2521, 100501, 1, 2, 1, 0, 1, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2522, 100501, 1, 3, 1, 0, 1, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2523, 100501, 1, 4, 1, 0, 1, "一日一善！\nヒーローだったら\n当たり前ッス♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2524, 100501, 1, 5, 1, 0, 1, "どうッスか\nこのポーズ！\nイイ感じッスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2525, 100501, 1, 101, 4, 0, 1, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2526, 100501, 1, 102, 8, 0, 1, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2527, 100501, 2, 1, 1, 0, 1, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2528, 100501, 2, 2, 1, 0, 1, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2529, 100501, 2, 3, 1, 0, 1, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2530, 100501, 2, 4, 1, 0, 1, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2531, 100501, 2, 101, 4, 0, 1, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2532, 100501, 2, 102, 8, 0, 1, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2533, 100501, 2, 1, 1, 1, 1, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2534, 100501, 2, 2, 1, 2, 1, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2535, 100501, 2, 3, 1, 3, 1, "夜のパトロールで\n一緒に平和を\n守るッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2537, 100501, 3, 2, 1, 0, 1, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2538, 100501, 3, 3, 1, 0, 1, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2539, 100501, 3, 4, 1, 0, 1, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2540, 100501, 3, 5, 1, 0, 1, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2542, 100501, 3, 7, 1, 0, 1, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2543, 100501, 3, 8, 1, 0, 1, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2545, 108101, 1, 1, 1, 0, 1, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2546, 108101, 1, 2, 1, 0, 1, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2547, 108101, 1, 3, 1, 0, 1, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2548, 108101, 1, 4, 1, 0, 1, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2549, 108101, 1, 5, 1, 0, 1, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2550, 108101, 1, 101, 4, 0, 1, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2551, 108101, 1, 102, 8, 0, 1, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2552, 108101, 2, 1, 1, 0, 1, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2553, 108101, 2, 2, 1, 0, 1, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2554, 108101, 2, 3, 1, 0, 1, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2555, 108101, 2, 4, 1, 0, 1, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2556, 108101, 2, 101, 4, 0, 1, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2557, 108101, 2, 102, 8, 0, 1, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2558, 108101, 2, 1, 1, 1, 1, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2559, 108101, 2, 2, 1, 2, 1, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2560, 108101, 2, 3, 1, 3, 1, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2562, 108101, 3, 2, 1, 0, 1, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2563, 108101, 3, 3, 1, 0, 1, "つん、つん。ふふっ♪\n癒やされます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2564, 108101, 3, 4, 1, 0, 1, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2565, 108101, 3, 5, 1, 0, 1, "あったかいね\nお父さん…\nえ？　分からない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2567, 108101, 3, 7, 1, 0, 1, "霊のみなさんは\n食べられないので…\n私がいただきます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2568, 108101, 3, 8, 1, 0, 1, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2570, 108101, 3, 12, 1, 0, 1, "わぁ、お菓子が沢山…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2572, 108201, 1, 1, 1, 0, 1, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2573, 108201, 1, 2, 1, 0, 1, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2574, 108201, 1, 3, 1, 0, 1, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2575, 108201, 1, 4, 1, 0, 1, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2576, 108201, 1, 5, 1, 0, 1, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2577, 108201, 1, 101, 4, 0, 1, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2578, 108201, 1, 102, 8, 0, 1, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2579, 108201, 2, 1, 1, 0, 1, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2580, 108201, 2, 2, 1, 0, 1, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2581, 108201, 2, 3, 1, 0, 1, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2582, 108201, 2, 4, 1, 0, 1, "プリンの可能性は\n無限大なの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2583, 108201, 2, 101, 4, 0, 1, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2584, 108201, 2, 102, 8, 0, 1, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2585, 108201, 2, 1, 1, 1, 1, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2586, 108201, 2, 2, 1, 2, 1, "ランチは豪華に\nプリンアラモードなの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2587, 108201, 2, 3, 1, 3, 1, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2589, 108201, 3, 2, 1, 0, 1, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2590, 108201, 3, 3, 1, 0, 1, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2591, 108201, 3, 4, 1, 0, 1, "癒されるの～\nまるで天国なの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2592, 108201, 3, 5, 1, 0, 1, "ここでなら、\n焼きプリンが\n作れるの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2594, 108201, 3, 7, 1, 0, 1, "腹が減っては\nハロウィンは\nできぬなの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2595, 108201, 3, 8, 1, 0, 1, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2597, 108201, 3, 12, 1, 0, 1, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2599, 108301, 1, 1, 1, 0, 1, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2600, 108301, 1, 2, 1, 0, 1, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2601, 108301, 1, 3, 1, 0, 1, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2602, 108301, 1, 4, 1, 0, 1, "この格好で学院に\n行ったら、\nみんな驚くかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2603, 108301, 1, 5, 1, 0, 1, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2604, 108301, 1, 101, 4, 0, 1, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2605, 108301, 1, 102, 8, 0, 1, "アイツを魅了する\n魔法とかないかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2606, 108301, 2, 1, 1, 0, 1, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2607, 108301, 2, 2, 1, 0, 1, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2608, 108301, 2, 3, 1, 0, 1, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2609, 108301, 2, 4, 1, 0, 1, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2610, 108301, 2, 101, 4, 0, 1, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2611, 108301, 2, 102, 8, 0, 1, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2612, 108301, 2, 1, 1, 1, 1, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2613, 108301, 2, 2, 1, 2, 1, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2614, 108301, 2, 3, 1, 3, 1, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2616, 108301, 3, 2, 1, 0, 1, "あう～、お菓子の\n食べすぎで\n動けな～い", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2617, 108301, 3, 3, 1, 0, 1, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2618, 108301, 3, 4, 1, 0, 1, "大人な香りに\n包まれてる～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2619, 108301, 3, 5, 1, 0, 1, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2621, 108301, 3, 7, 1, 0, 1, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2622, 108301, 3, 8, 1, 0, 1, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2624, 108301, 3, 12, 1, 0, 1, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2626, 107101, 1, 1, 1, 0, 1, "次はどんな策で団長を\nからかってみようか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2627, 107101, 1, 2, 1, 0, 1, "む？\n血の臭いがするな\n祭りの時間か☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2628, 107101, 1, 3, 1, 0, 1, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2629, 107101, 1, 4, 1, 0, 1, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2630, 107101, 1, 5, 1, 0, 1, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2631, 107101, 1, 101, 4, 0, 1, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2632, 107101, 1, 102, 8, 0, 1, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2633, 107101, 2, 1, 1, 0, 1, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2634, 107101, 2, 2, 1, 0, 1, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2635, 107101, 2, 3, 1, 0, 1, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2636, 107101, 2, 4, 1, 0, 1, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2637, 107101, 2, 101, 4, 0, 1, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2638, 107101, 2, 102, 8, 0, 1, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2639, 107101, 2, 1, 1, 1, 1, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2640, 107101, 2, 2, 1, 2, 1, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2641, 107101, 2, 3, 1, 3, 1, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2643, 107101, 3, 2, 1, 0, 1, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2644, 107101, 3, 3, 1, 0, 1, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2645, 107101, 3, 4, 1, 0, 1, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2646, 107101, 3, 5, 1, 0, 1, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2648, 107101, 3, 7, 1, 0, 1, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2649, 107101, 3, 8, 1, 0, 1, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2651, 105601, 1, 1, 1, 0, 1, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2652, 105601, 1, 2, 1, 0, 1, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2653, 105601, 1, 3, 1, 0, 1, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2654, 105601, 1, 4, 1, 0, 1, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2655, 105601, 1, 5, 1, 0, 1, "ふあぁ～…\n昼寝でもするか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2656, 105601, 1, 101, 4, 0, 1, "ふふっ…\nあの子、見た目より\n根性あるんだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2657, 105601, 1, 102, 8, 0, 1, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2658, 105601, 2, 1, 1, 0, 1, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2659, 105601, 2, 2, 1, 0, 1, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2660, 105601, 2, 3, 1, 0, 1, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2661, 105601, 2, 4, 1, 0, 1, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2662, 105601, 2, 101, 4, 0, 1, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2663, 105601, 2, 102, 8, 0, 1, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2664, 105601, 2, 1, 1, 1, 1, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2665, 105601, 2, 2, 1, 2, 1, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2666, 105601, 2, 3, 1, 3, 1, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2668, 105601, 3, 2, 1, 0, 1, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2669, 105601, 3, 3, 1, 0, 1, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2670, 105601, 3, 4, 1, 0, 1, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2671, 105601, 3, 5, 1, 0, 1, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2673, 105601, 3, 7, 1, 0, 1, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2674, 105601, 3, 8, 1, 0, 1, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2676, 101301, 1, 1, 1, 0, 1, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2677, 101301, 1, 2, 1, 0, 1, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2678, 101301, 1, 3, 1, 0, 1, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2679, 101301, 1, 4, 1, 0, 1, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2680, 101301, 1, 5, 1, 0, 1, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2681, 101301, 1, 101, 4, 0, 1, "次はどの漫画を\n貸そうかなー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2682, 101301, 1, 102, 8, 0, 1, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2683, 101301, 2, 1, 1, 0, 1, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2684, 101301, 2, 2, 1, 0, 1, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2685, 101301, 2, 3, 1, 0, 1, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2686, 101301, 2, 4, 1, 0, 1, "新魔法思いついた！\n私って天才！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2687, 101301, 2, 101, 4, 0, 1, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2688, 101301, 2, 102, 8, 0, 1, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2689, 101301, 2, 1, 1, 1, 1, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2690, 101301, 2, 2, 1, 2, 1, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2691, 101301, 2, 3, 1, 3, 1, "夜からが、\nオタ活本番でしょ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2693, 101301, 3, 2, 1, 0, 1, "ふいー！\nくつろいで本を読む！\n最高だねー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2694, 101301, 3, 3, 1, 0, 1, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2695, 101301, 3, 4, 1, 0, 1, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2696, 101301, 3, 5, 1, 0, 1, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2698, 101301, 3, 7, 1, 0, 1, "ウマ―！\nこれの詳細きぼんぬ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2699, 101301, 3, 8, 1, 0, 1, "こ、古代語の本！？\n現代語でおk…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2701, 190901, 1, 1, 1, 0, 1, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2702, 190901, 1, 2, 1, 0, 1, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2703, 190901, 1, 3, 1, 0, 1, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2704, 190901, 1, 4, 1, 0, 1, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2705, 190901, 1, 5, 1, 0, 1, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2706, 190901, 2, 1, 1, 0, 1, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2707, 190901, 2, 2, 1, 0, 1, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2708, 190901, 2, 3, 1, 0, 1, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2709, 190901, 2, 4, 1, 0, 1, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2710, 190901, 2, 1, 1, 1, 1, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2711, 190901, 2, 2, 1, 2, 1, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2712, 190901, 2, 3, 1, 3, 1, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2714, 190901, 3, 2, 1, 0, 1, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2715, 190901, 3, 3, 1, 0, 1, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2716, 190901, 3, 4, 1, 0, 1, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2717, 190901, 3, 5, 1, 0, 1, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2719, 190901, 3, 7, 1, 0, 1, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2720, 190901, 3, 8, 1, 0, 1, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2722, 108401, 1, 1, 1, 0, 1, "メリークリスマス！\n歌で幸せを\n届けます…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2723, 108401, 1, 2, 1, 0, 1, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2724, 108401, 1, 3, 1, 0, 1, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2725, 108401, 1, 4, 1, 0, 1, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2726, 108401, 1, 5, 1, 0, 1, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2727, 108401, 1, 101, 4, 0, 1, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2728, 108401, 1, 102, 8, 0, 1, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2729, 108401, 2, 1, 1, 0, 1, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2730, 108401, 2, 2, 1, 0, 1, "歌を届ける\nサンタさんに\nなりたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2731, 108401, 2, 3, 1, 0, 1, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2732, 108401, 2, 4, 1, 0, 1, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2733, 108401, 2, 101, 4, 0, 1, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2734, 108401, 2, 102, 8, 0, 1, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2735, 108401, 2, 1, 1, 1, 1, "今日も一日\n幸せな歌で世界が\n包まれますように…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2736, 108401, 2, 2, 1, 2, 1, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2737, 108401, 2, 3, 1, 3, 1, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2739, 108401, 3, 2, 1, 0, 1, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2740, 108401, 3, 3, 1, 0, 1, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2741, 108401, 3, 4, 1, 0, 1, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2742, 108401, 3, 5, 1, 0, 1, "この光みたいに\n灯火になるような\n歌を歌いたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2744, 108401, 3, 7, 1, 0, 1, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2745, 108401, 3, 8, 1, 0, 1, "物語には\n曲作りのヒントが\n詰まっているんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2747, 108401, 3, 14, 1, 0, 1, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2748, 108401, 3, 15, 1, 0, 1, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2749, 108501, 1, 1, 1, 0, 1, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2750, 108501, 1, 2, 1, 0, 1, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2751, 108501, 1, 3, 1, 0, 1, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2752, 108501, 1, 4, 1, 0, 1, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2753, 108501, 1, 5, 1, 0, 1, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2754, 108501, 1, 101, 4, 0, 1, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2755, 108501, 1, 102, 8, 0, 1, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2756, 108501, 2, 1, 1, 0, 1, "メリー\nクリスマスです、\nお兄ちゃん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2757, 108501, 2, 2, 1, 0, 1, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2758, 108501, 2, 3, 1, 0, 1, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2759, 108501, 2, 4, 1, 0, 1, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2760, 108501, 2, 101, 4, 0, 1, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2761, 108501, 2, 102, 8, 0, 1, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2762, 108501, 2, 1, 1, 1, 1, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2763, 108501, 2, 2, 1, 2, 1, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2764, 108501, 2, 3, 1, 3, 1, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2766, 108501, 3, 2, 1, 0, 1, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2767, 108501, 3, 3, 1, 0, 1, "この子と一緒に\nおままごとしたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2768, 108501, 3, 4, 1, 0, 1, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2769, 108501, 3, 5, 1, 0, 1, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2771, 108501, 3, 7, 1, 0, 1, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2772, 108501, 3, 8, 1, 0, 1, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2774, 108501, 3, 14, 1, 0, 1, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2775, 108501, 3, 15, 1, 0, 1, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2776, 108601, 1, 1, 1, 0, 1, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2777, 108601, 1, 2, 1, 0, 1, "毎日が\nクリスマスだったら\nいいのにな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2778, 108601, 1, 3, 1, 0, 1, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2779, 108601, 1, 4, 1, 0, 1, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2780, 108601, 1, 5, 1, 0, 1, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2781, 108601, 1, 101, 4, 0, 1, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2782, 108601, 1, 102, 8, 0, 1, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2783, 108601, 2, 1, 1, 0, 1, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2784, 108601, 2, 2, 1, 0, 1, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2785, 108601, 2, 3, 1, 0, 1, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2786, 108601, 2, 4, 1, 0, 1, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2787, 108601, 2, 101, 4, 0, 1, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2788, 108601, 2, 102, 8, 0, 1, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2789, 108601, 2, 1, 1, 1, 1, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2790, 108601, 2, 2, 1, 2, 1, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2791, 108601, 2, 3, 1, 3, 1, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2793, 108601, 3, 2, 1, 0, 1, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2794, 108601, 3, 3, 1, 0, 1, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2795, 108601, 3, 4, 1, 0, 1, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2796, 108601, 3, 5, 1, 0, 1, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2798, 108601, 3, 7, 1, 0, 1, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2799, 108601, 3, 8, 1, 0, 1, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2801, 108601, 3, 14, 1, 0, 1, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2802, 108601, 3, 15, 1, 0, 1, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2803, 108901, 1, 1, 1, 0, 1, "着物も慣れると\n動きやすいものさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2804, 108901, 1, 2, 1, 0, 1, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2805, 108901, 1, 3, 1, 0, 1, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2806, 108901, 1, 4, 1, 0, 1, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2807, 108901, 1, 5, 1, 0, 1, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2808, 108901, 1, 101, 4, 0, 1, "これからもずっと\n仲間と、彼と\n一緒に…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2809, 108901, 1, 102, 8, 0, 1, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2810, 108901, 2, 1, 1, 0, 1, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2811, 108901, 2, 2, 1, 0, 1, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2812, 108901, 2, 3, 1, 0, 1, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2813, 108901, 2, 4, 1, 0, 1, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2814, 108901, 2, 101, 4, 0, 1, "キミといると\n気持ちが段々と\n安らいでいくよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2815, 108901, 2, 102, 8, 0, 1, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2816, 108901, 2, 1, 1, 1, 1, "おはよう\n朝の修行を始めるけど\nキミもどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2817, 108901, 2, 2, 1, 2, 1, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2818, 108901, 2, 3, 1, 3, 1, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2820, 108901, 3, 2, 1, 0, 1, "…疲れていたのかな\nもう少しで眠る\nところだった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2821, 108901, 3, 3, 1, 0, 1, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2822, 108901, 3, 4, 1, 0, 1, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2823, 108901, 3, 5, 1, 0, 1, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2825, 108901, 3, 7, 1, 0, 1, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2826, 108901, 3, 8, 1, 0, 1, "外を出歩くのも\nいいけど、中で\n読書もありだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2828, 108801, 1, 1, 1, 0, 1, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2829, 108801, 1, 2, 1, 0, 1, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2830, 108801, 1, 3, 1, 0, 1, "今年は\nどんな福袋を\n買おうかな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2831, 108801, 1, 4, 1, 0, 1, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2832, 108801, 1, 5, 1, 0, 1, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2833, 108801, 1, 101, 4, 0, 1, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2834, 108801, 1, 102, 8, 0, 1, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2835, 108801, 2, 1, 1, 0, 1, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2836, 108801, 2, 2, 1, 0, 1, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2837, 108801, 2, 3, 1, 0, 1, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2838, 108801, 2, 4, 1, 0, 1, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2839, 108801, 2, 101, 4, 0, 1, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2840, 108801, 2, 102, 8, 0, 1, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2841, 108801, 2, 1, 1, 1, 1, "おはよう\n太陽の光に当たると\n元気が出るよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2842, 108801, 2, 2, 1, 2, 1, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2843, 108801, 2, 3, 1, 3, 1, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2845, 108801, 3, 2, 1, 0, 1, "ふぅ…\n帯も少し\nゆるめちゃおう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2846, 108801, 3, 3, 1, 0, 1, "お正月用に\n着物を縫って\nあげたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2847, 108801, 3, 4, 1, 0, 1, "これでお肌が\n保湿出来たら\nいいのになぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2848, 108801, 3, 5, 1, 0, 1, "ん～…\nぬくぬくしてて\n動きたくないよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2850, 108801, 3, 7, 1, 0, 1, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2851, 108801, 3, 8, 1, 0, 1, "年末年始は\n告白のチャンス…\nむ、無理無理！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2853, 108701, 1, 1, 1, 0, 1, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2854, 108701, 1, 2, 1, 0, 1, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2855, 108701, 1, 3, 1, 0, 1, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2856, 108701, 1, 4, 1, 0, 1, "初詣楽しみー！\n出店はどこから\n行こうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2857, 108701, 1, 5, 1, 0, 1, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2858, 108701, 1, 101, 4, 0, 1, "うーん…\n騎士くんはお正月\n何してるんだろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2859, 108701, 1, 102, 8, 0, 1, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2860, 108701, 2, 1, 1, 0, 1, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2861, 108701, 2, 2, 1, 0, 1, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2862, 108701, 2, 3, 1, 0, 1, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2863, 108701, 2, 4, 1, 0, 1, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2864, 108701, 2, 101, 4, 0, 1, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2865, 108701, 2, 102, 8, 0, 1, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2866, 108701, 2, 1, 1, 1, 1, "おはよー！\n今日も明るく元気に\nがんばろー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2867, 108701, 2, 2, 1, 2, 1, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2868, 108701, 2, 3, 1, 3, 1, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2870, 108701, 3, 2, 1, 0, 1, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2871, 108701, 3, 3, 1, 0, 1, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2872, 108701, 3, 4, 1, 0, 1, "はぁー♪\nいい香りー…\n気分スッキリ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2873, 108701, 3, 5, 1, 0, 1, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2875, 108701, 3, 7, 1, 0, 1, "ど、どうしよう…！\n手が、止まんないよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2876, 108701, 3, 8, 1, 0, 1, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2878, 109001, 1, 1, 1, 0, 1, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2879, 109001, 1, 2, 1, 0, 1, "ここを私のチョコで\n埋め尽くしますわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2880, 109001, 1, 3, 1, 0, 1, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2881, 109001, 1, 4, 1, 0, 1, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2882, 109001, 1, 5, 1, 0, 1, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2883, 109001, 1, 101, 4, 0, 1, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2884, 109001, 1, 102, 8, 0, 1, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2885, 109001, 2, 1, 1, 0, 1, "あなた様。私に\n隠しているものなど\nありませんよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2886, 109001, 2, 2, 1, 0, 1, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2887, 109001, 2, 3, 1, 0, 1, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2888, 109001, 2, 4, 1, 0, 1, "解毒薬はどこに\nいれたかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2889, 109001, 2, 101, 4, 0, 1, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2890, 109001, 2, 102, 8, 0, 1, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2891, 109001, 2, 1, 1, 1, 1, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2892, 109001, 2, 2, 1, 2, 1, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2893, 109001, 2, 3, 1, 3, 1, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2895, 109001, 3, 2, 1, 0, 1, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2896, 109001, 3, 3, 1, 0, 1, "この中、何か\n入りそうですわね\n…チョコレート、とか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2897, 109001, 3, 4, 1, 0, 1, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2898, 109001, 3, 5, 1, 0, 1, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2900, 109001, 3, 7, 1, 0, 1, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2901, 109001, 3, 8, 1, 0, 1, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2903, 109101, 1, 1, 1, 0, 1, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2904, 109101, 1, 2, 1, 0, 1, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2905, 109101, 1, 3, 1, 0, 1, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2906, 109101, 1, 4, 1, 0, 1, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2907, 109101, 1, 5, 1, 0, 1, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2908, 109101, 1, 101, 4, 0, 1, "お姉ちゃんのチョコ\n食べさせてあげたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2909, 109101, 1, 102, 8, 0, 1, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2910, 109101, 2, 1, 1, 0, 1, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2911, 109101, 2, 2, 1, 0, 1, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2912, 109101, 2, 3, 1, 0, 1, "ナデナデするのは\nお姉ちゃんの仕事！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2913, 109101, 2, 4, 1, 0, 1, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2914, 109101, 2, 101, 4, 0, 1, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2915, 109101, 2, 102, 8, 0, 1, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2916, 109101, 2, 1, 1, 1, 1, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2917, 109101, 2, 2, 1, 2, 1, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2918, 109101, 2, 3, 1, 3, 1, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2920, 109101, 3, 2, 1, 0, 1, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2921, 109101, 3, 3, 1, 0, 1, "このかわいさは\nお姉ちゃんも大満足♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2922, 109101, 3, 4, 1, 0, 1, "この香り、チョコに\n使えるかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2923, 109101, 3, 5, 1, 0, 1, "弟くんと一緒に\nぬくぬくしたいな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2925, 109101, 3, 7, 1, 0, 1, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2926, 109101, 3, 8, 1, 0, 1, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2928, 106101, 1, 1, 1, 0, 1, "アタシって…\nここにいても\nいいのか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2929, 106101, 1, 2, 1, 0, 1, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2930, 106101, 1, 3, 1, 0, 1, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2931, 106101, 1, 4, 1, 0, 1, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2932, 106101, 1, 5, 1, 0, 1, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2933, 106101, 1, 101, 4, 0, 1, "みんなが早く\nアタシのこと\n思い出さないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2934, 106101, 1, 102, 8, 0, 1, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2935, 106101, 2, 1, 1, 0, 1, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2936, 106101, 2, 2, 1, 0, 1, "どうした？\n戦うのか！？\n…なんだ、違うのか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2937, 106101, 2, 3, 1, 0, 1, "身長のことは\nあんまり言うな！\n気にしてるからな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2938, 106101, 2, 4, 1, 0, 1, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2939, 106101, 2, 101, 4, 0, 1, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2940, 106101, 2, 102, 8, 0, 1, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2941, 106101, 2, 1, 1, 1, 1, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2942, 106101, 2, 2, 1, 2, 1, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2943, 106101, 2, 3, 1, 3, 1, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2945, 106101, 3, 2, 1, 0, 1, "ふぃ～…\n極楽極楽…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2946, 106101, 3, 3, 1, 0, 1, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2947, 106101, 3, 4, 1, 0, 1, "くんくん…\nたまにはこういうのも\n悪くないかもな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2948, 106101, 3, 5, 1, 0, 1, "あー…\nもっと前にコイツと\n出会いたかった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2950, 106101, 3, 7, 1, 0, 1, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2951, 106101, 3, 8, 1, 0, 1, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2953, 101401, 1, 1, 1, 0, 1, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2954, 101401, 1, 2, 1, 0, 1, "事件のオチは\n往々にして\n取るに足らないものさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2955, 101401, 1, 3, 1, 0, 1, "読書で得る知識が\n探偵の\n血肉になるんだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2956, 101401, 1, 4, 1, 0, 1, "この前の\n捜査資料…\nどこにあったかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2957, 101401, 1, 5, 1, 0, 1, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2958, 101401, 1, 101, 4, 0, 1, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2959, 101401, 1, 102, 8, 0, 1, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2960, 101401, 2, 1, 1, 0, 1, "珈琲、淹れたてだよ\n一杯どうだい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2961, 101401, 2, 2, 1, 0, 1, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2962, 101401, 2, 3, 1, 0, 1, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2963, 101401, 2, 4, 1, 0, 1, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2964, 101401, 2, 101, 4, 0, 1, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2965, 101401, 2, 102, 8, 0, 1, "助手くんは\n私にとって\n最高の相棒だよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2966, 101401, 2, 1, 1, 1, 1, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2967, 101401, 2, 2, 1, 2, 1, "一休みしたら\n推理の答え合わせを\nしてみようか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2968, 101401, 2, 3, 1, 3, 1, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2970, 101401, 3, 2, 1, 0, 1, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2971, 101401, 3, 3, 1, 0, 1, "この愛らしさ…\n要捜査対象だね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2972, 101401, 3, 4, 1, 0, 1, "思考が活性化される\n素敵な香りだね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2973, 101401, 3, 5, 1, 0, 1, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2975, 101401, 3, 7, 1, 0, 1, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2976, 101401, 3, 8, 1, 0, 1, "探偵だからね\nミステリには\n一家言あるんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2978, 105501, 1, 1, 1, 0, 1, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2979, 105501, 1, 2, 1, 0, 1, "せ、先輩がいた部屋！\n私には刺激が強い…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2980, 105501, 1, 3, 1, 0, 1, "先輩を観察しやすい\n場所はあるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2981, 105501, 1, 4, 1, 0, 1, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2982, 105501, 1, 5, 1, 0, 1, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2983, 105501, 1, 101, 4, 0, 1, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2984, 105501, 1, 102, 8, 0, 1, "せ、先輩になら\n観察されてもいいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2985, 105501, 2, 1, 1, 0, 1, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2986, 105501, 2, 2, 1, 0, 1, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2987, 105501, 2, 3, 1, 0, 1, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2988, 105501, 2, 4, 1, 0, 1, "ま、また…\n先輩と一緒に\nバイトしたい…です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2989, 105501, 2, 101, 4, 0, 1, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2990, 105501, 2, 102, 8, 0, 1, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2991, 105501, 2, 1, 1, 1, 1, "せ、先輩…！\nおひゃっ！\nおはようございます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2992, 105501, 2, 2, 1, 2, 1, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2993, 105501, 2, 3, 1, 3, 1, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2995, 105501, 3, 2, 1, 0, 1, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2996, 105501, 3, 3, 1, 0, 1, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2997, 105501, 3, 4, 1, 0, 1, "ここから先輩の\n香りがすればなあ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (2998, 105501, 3, 5, 1, 0, 1, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3000, 105501, 3, 7, 1, 0, 1, "おいし～\nこれは\n先輩も好きな味かな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3001, 105501, 3, 8, 1, 0, 1, "す、好きな人に\n近づく方法…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3003, 109201, 1, 1, 1, 0, 1, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3004, 109201, 1, 2, 1, 0, 1, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3005, 109201, 1, 3, 1, 0, 1, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3006, 109201, 1, 4, 1, 0, 1, "わー！\nあれもこれも\n気になっちゃうな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3007, 109201, 1, 5, 1, 0, 1, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3008, 109201, 1, 101, 4, 0, 1, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3009, 109201, 1, 102, 8, 0, 1, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3010, 109201, 2, 1, 1, 0, 1, "歩き方がきれい？\nえへへ、そうかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3011, 109201, 2, 2, 1, 0, 1, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3012, 109201, 2, 3, 1, 0, 1, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3013, 109201, 2, 4, 1, 0, 1, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3014, 109201, 2, 101, 4, 0, 1, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3015, 109201, 2, 102, 8, 0, 1, "お茶の時間に\nしたいな～…\n一緒にどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3016, 109201, 2, 1, 1, 1, 1, "おはようっ！\n今日も素敵な\n一日にしようね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3017, 109201, 2, 2, 1, 2, 1, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3018, 109201, 2, 3, 1, 3, 1, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3020, 109201, 3, 2, 1, 0, 1, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3021, 109201, 3, 3, 1, 0, 1, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3022, 109201, 3, 4, 1, 0, 1, "いい香り～！\nなんだか気持ちが\n落ち着くね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3023, 109201, 3, 5, 1, 0, 1, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3025, 109201, 3, 7, 1, 0, 1, "こ、これ美味し～！\n止められないよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3026, 109201, 3, 8, 1, 0, 1, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3028, 109301, 1, 1, 1, 0, 1, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3029, 109301, 1, 2, 1, 0, 1, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3030, 109301, 1, 3, 1, 0, 1, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3031, 109301, 1, 4, 1, 0, 1, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3032, 109301, 1, 5, 1, 0, 1, "なにか面白そうなもの\nないですかね～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3033, 109301, 1, 101, 4, 0, 1, "ここのみんなとも\nもうお友だちですぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3034, 109301, 1, 102, 8, 0, 1, "おなかが減ったです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3035, 109301, 2, 1, 1, 0, 1, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3036, 109301, 2, 2, 1, 0, 1, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3037, 109301, 2, 3, 1, 0, 1, "あなたもお勉強\n苦手ですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3038, 109301, 2, 4, 1, 0, 1, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3039, 109301, 2, 101, 4, 0, 1, "お友だちいっぱいで、\nルゥはうれしいです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3040, 109301, 2, 102, 8, 0, 1, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3041, 109301, 2, 1, 1, 1, 1, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3042, 109301, 2, 2, 1, 2, 1, "午後もいっぱい\n遊ぶですよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3043, 109301, 2, 3, 1, 3, 1, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3045, 109301, 3, 2, 1, 0, 1, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3046, 109301, 3, 3, 1, 0, 1, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3047, 109301, 3, 4, 1, 0, 1, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3048, 109301, 3, 5, 1, 0, 1, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3050, 109301, 3, 7, 1, 0, 1, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3051, 109301, 3, 8, 1, 0, 1, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3053, 109401, 1, 1, 1, 0, 1, "ピアノ…\nどこかにないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3054, 109401, 1, 2, 1, 0, 1, "ふぅ…\n落ち着くな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3055, 109401, 1, 3, 1, 0, 1, "マナリアはいつも\n賑やかなんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3056, 109401, 1, 4, 1, 0, 1, "みんな…\n今頃何してるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3057, 109401, 1, 5, 1, 0, 1, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3058, 109401, 1, 101, 4, 0, 1, "人と話すのは\n得意じゃないな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3059, 109401, 1, 102, 8, 0, 1, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3060, 109401, 2, 1, 1, 0, 1, "やっぱり、私の翼や\n尻尾…気になる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3061, 109401, 2, 2, 1, 0, 1, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3062, 109401, 2, 3, 1, 0, 1, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3063, 109401, 2, 4, 1, 0, 1, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3064, 109401, 2, 101, 4, 0, 1, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3065, 109401, 2, 102, 8, 0, 1, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3066, 109401, 2, 1, 1, 1, 1, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3067, 109401, 2, 2, 1, 2, 1, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3068, 109401, 2, 3, 1, 3, 1, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3070, 109401, 3, 2, 1, 0, 1, "羽を伸ばしたく\nなっちゃうね…ふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3071, 109401, 3, 3, 1, 0, 1, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3072, 109401, 3, 4, 1, 0, 1, "いい香り…\nこれ、\nアンも好きそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3073, 109401, 3, 5, 1, 0, 1, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3075, 109401, 3, 7, 1, 0, 1, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3076, 109401, 3, 8, 1, 0, 1, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3078, 109501, 1, 1, 1, 0, 1, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3079, 109501, 1, 2, 1, 0, 1, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3080, 109501, 1, 3, 1, 0, 1, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3081, 109501, 1, 4, 1, 0, 1, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3082, 109501, 1, 5, 1, 0, 1, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3083, 109501, 1, 101, 4, 0, 1, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3084, 109501, 1, 102, 8, 0, 1, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3085, 109501, 2, 1, 1, 0, 1, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3086, 109501, 2, 2, 1, 0, 1, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3087, 109501, 2, 3, 1, 0, 1, "こんな場所で\nなんというプレイを！\n…妄想でした", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3088, 109501, 2, 4, 1, 0, 1, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3089, 109501, 2, 101, 4, 0, 1, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3090, 109501, 2, 102, 8, 0, 1, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3091, 109501, 2, 1, 1, 1, 1, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3092, 109501, 2, 2, 1, 2, 1, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3093, 109501, 2, 3, 1, 3, 1, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3095, 109501, 3, 2, 1, 0, 1, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3096, 109501, 3, 3, 1, 0, 1, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3097, 109501, 3, 4, 1, 0, 1, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3098, 109501, 3, 5, 1, 0, 1, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3100, 109501, 3, 7, 1, 0, 1, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3101, 109501, 3, 8, 1, 0, 1, "クウカの探している\nジャンルはどこに\nありますかね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3103, 109601, 1, 1, 1, 0, 1, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3104, 109601, 1, 2, 1, 0, 1, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3105, 109601, 1, 3, 1, 0, 1, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3106, 109601, 1, 4, 1, 0, 1, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3107, 109601, 1, 5, 1, 0, 1, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3108, 109601, 1, 101, 4, 0, 1, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3109, 109601, 1, 102, 8, 0, 1, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3110, 109601, 2, 1, 1, 0, 1, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3111, 109601, 2, 2, 1, 0, 1, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3112, 109601, 2, 3, 1, 0, 1, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3113, 109601, 2, 4, 1, 0, 1, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3114, 109601, 2, 101, 4, 0, 1, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3115, 109601, 2, 102, 8, 0, 1, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3116, 109601, 2, 1, 1, 1, 1, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3117, 109601, 2, 2, 1, 2, 1, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3118, 109601, 2, 3, 1, 3, 1, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3120, 109601, 3, 2, 1, 0, 1, "天下分け目の\n戦いに備えて…\nマッタリデース", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3121, 109601, 3, 3, 1, 0, 1, "口寄せの術で\n召喚したいくらい\nかわいいデース", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3122, 109601, 3, 4, 1, 0, 1, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3123, 109601, 3, 5, 1, 0, 1, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3125, 109601, 3, 7, 1, 0, 1, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3126, 109601, 3, 8, 1, 0, 1, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3128, 109701, 1, 1, 1, 0, 1, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3129, 109701, 1, 2, 1, 0, 1, "異世界の演劇…\n許されるならば、\n観てみたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3130, 109701, 1, 3, 1, 0, 1, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3131, 109701, 1, 4, 1, 0, 1, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3132, 109701, 1, 5, 1, 0, 1, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3133, 109701, 1, 101, 4, 0, 1, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3134, 109701, 1, 102, 8, 0, 1, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3135, 109701, 2, 1, 1, 0, 1, "お出かけでしたら、\nレムも同行\nさせてくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3136, 109701, 2, 2, 1, 0, 1, "レムにできること\nでしたら、なんでも\nお申し付けください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3137, 109701, 2, 3, 1, 0, 1, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3138, 109701, 2, 4, 1, 0, 1, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3139, 109701, 2, 101, 4, 0, 1, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3140, 109701, 2, 102, 8, 0, 1, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3141, 109701, 2, 1, 1, 1, 1, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3142, 109701, 2, 2, 1, 2, 1, "昼食の時間ですね\n何か食べたいものは\nありますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3143, 109701, 2, 3, 1, 3, 1, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3145, 109701, 3, 2, 1, 0, 1, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3146, 109701, 3, 3, 1, 0, 1, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3147, 109701, 3, 4, 1, 0, 1, "よい香りですね\n魔女教の悪臭とは\n大違いです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3148, 109701, 3, 5, 1, 0, 1, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3150, 109701, 3, 7, 1, 0, 1, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3151, 109701, 3, 8, 1, 0, 1, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3153, 109801, 1, 1, 1, 0, 1, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3154, 109801, 1, 2, 1, 0, 1, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3155, 109801, 1, 3, 1, 0, 1, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3156, 109801, 1, 4, 1, 0, 1, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3157, 109801, 1, 5, 1, 0, 1, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3158, 109801, 1, 101, 4, 0, 1, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3159, 109801, 1, 102, 8, 0, 1, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3160, 109801, 2, 1, 1, 0, 1, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3161, 109801, 2, 2, 1, 0, 1, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3162, 109801, 2, 3, 1, 0, 1, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3163, 109801, 2, 4, 1, 0, 1, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3164, 109801, 2, 101, 4, 0, 1, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3165, 109801, 2, 102, 8, 0, 1, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3166, 109801, 2, 1, 1, 1, 1, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3167, 109801, 2, 2, 1, 2, 1, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3168, 109801, 2, 3, 1, 3, 1, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3170, 109801, 3, 2, 1, 0, 1, "…ここから動かない\nという、誓いを\n立てたくなるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3171, 109801, 3, 3, 1, 0, 1, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3172, 109801, 3, 4, 1, 0, 1, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3173, 109801, 3, 5, 1, 0, 1, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3175, 109801, 3, 7, 1, 0, 1, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3176, 109801, 3, 8, 1, 0, 1, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3178, 109901, 1, 1, 1, 0, 1, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3179, 109901, 1, 2, 1, 0, 1, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3180, 109901, 1, 3, 1, 0, 1, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3181, 109901, 1, 4, 1, 0, 1, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3182, 109901, 1, 5, 1, 0, 1, "今日のご飯…\nピーマルは\n入ってないわよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3183, 109901, 1, 101, 4, 0, 1, "また、みんなで\n一緒に買い物に\n行きたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3184, 109901, 1, 102, 8, 0, 1, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3185, 109901, 2, 1, 1, 0, 1, "今からパックの\n毛繕いをするけど…\n一緒にする？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3186, 109901, 2, 2, 1, 0, 1, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3187, 109901, 2, 3, 1, 0, 1, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3188, 109901, 2, 4, 1, 0, 1, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3189, 109901, 2, 101, 4, 0, 1, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3190, 109901, 2, 102, 8, 0, 1, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3191, 109901, 2, 1, 1, 1, 1, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3192, 109901, 2, 2, 1, 2, 1, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3193, 109901, 2, 3, 1, 3, 1, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3195, 109901, 3, 2, 1, 0, 1, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3196, 109901, 3, 3, 1, 0, 1, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3197, 109901, 3, 4, 1, 0, 1, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3198, 109901, 3, 5, 1, 0, 1, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3200, 109901, 3, 7, 1, 0, 1, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3201, 109901, 3, 8, 1, 0, 1, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3203, 110001, 1, 1, 1, 0, 1, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3204, 110001, 1, 2, 1, 0, 1, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3205, 110001, 1, 3, 1, 0, 1, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3206, 110001, 1, 4, 1, 0, 1, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3207, 110001, 1, 5, 1, 0, 1, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3208, 110001, 1, 101, 4, 0, 1, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3209, 110001, 1, 102, 8, 0, 1, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3210, 110001, 2, 1, 1, 0, 1, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3211, 110001, 2, 2, 1, 0, 1, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3212, 110001, 2, 3, 1, 0, 1, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3213, 110001, 2, 4, 1, 0, 1, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3214, 110001, 2, 101, 4, 0, 1, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3215, 110001, 2, 102, 8, 0, 1, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3216, 110001, 2, 1, 1, 1, 1, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3217, 110001, 2, 2, 1, 2, 1, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3218, 110001, 2, 3, 1, 3, 1, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3220, 110001, 3, 2, 1, 0, 1, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3221, 110001, 3, 3, 1, 0, 1, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3222, 110001, 3, 4, 1, 0, 1, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3223, 110001, 3, 5, 1, 0, 1, "あったか～い♪\n夏の太陽みたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3225, 110001, 3, 7, 1, 0, 1, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3226, 110001, 3, 8, 1, 0, 1, "夏のために\n最新ファッションを\n勉強ちう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3228, 110001, 3, 10, 1, 0, 1, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3229, 110001, 3, 11, 1, 0, 1, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3230, 110101, 1, 1, 1, 0, 1, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3231, 110101, 1, 2, 1, 0, 1, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3232, 110101, 1, 3, 1, 0, 1, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3233, 110101, 1, 4, 1, 0, 1, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3234, 110101, 1, 5, 1, 0, 1, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3235, 110101, 1, 101, 4, 0, 1, "海が楽しいって\n思えたのも\nあの子のおかげね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3236, 110101, 1, 102, 8, 0, 1, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3237, 110101, 2, 1, 1, 0, 1, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3238, 110101, 2, 2, 1, 0, 1, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3239, 110101, 2, 3, 1, 0, 1, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3240, 110101, 2, 4, 1, 0, 1, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3241, 110101, 2, 101, 4, 0, 1, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3242, 110101, 2, 102, 8, 0, 1, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3243, 110101, 2, 1, 1, 1, 1, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3244, 110101, 2, 2, 1, 2, 1, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3245, 110101, 2, 3, 1, 3, 1, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3247, 110101, 3, 2, 1, 0, 1, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3248, 110101, 3, 3, 1, 0, 1, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3249, 110101, 3, 4, 1, 0, 1, "この匂い…\nなんだか夏の海を\n思い出すわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3250, 110101, 3, 5, 1, 0, 1, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3252, 110101, 3, 7, 1, 0, 1, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3253, 110101, 3, 8, 1, 0, 1, "夏休み中でも\nしっかりお勉強\nしないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3255, 110101, 3, 10, 1, 0, 1, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3256, 110101, 3, 11, 1, 0, 1, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3257, 110301, 1, 1, 1, 0, 1, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3258, 110301, 1, 2, 1, 0, 1, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3259, 110301, 1, 3, 1, 0, 1, "子どもたちのために\n浮き輪を用意して\nおかないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3260, 110301, 1, 4, 1, 0, 1, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3261, 110301, 1, 5, 1, 0, 1, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3262, 110301, 1, 101, 4, 0, 1, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3263, 110301, 1, 102, 8, 0, 1, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3264, 110301, 2, 1, 1, 0, 1, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3265, 110301, 2, 2, 1, 0, 1, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3266, 110301, 2, 3, 1, 0, 1, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3267, 110301, 2, 4, 1, 0, 1, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3268, 110301, 2, 101, 4, 0, 1, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3269, 110301, 2, 102, 8, 0, 1, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3270, 110301, 2, 1, 1, 1, 1, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3271, 110301, 2, 2, 1, 2, 1, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3272, 110301, 2, 3, 1, 3, 1, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3274, 110301, 3, 2, 1, 0, 1, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3275, 110301, 3, 3, 1, 0, 1, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3276, 110301, 3, 4, 1, 0, 1, "夏らしい\nいい香りね…\nなんの香りかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3277, 110301, 3, 5, 1, 0, 1, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3279, 110301, 3, 7, 1, 0, 1, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3280, 110301, 3, 8, 1, 0, 1, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3282, 110301, 3, 10, 1, 0, 1, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3283, 110301, 3, 11, 1, 0, 1, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3284, 110401, 1, 1, 1, 0, 1, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3285, 110401, 1, 2, 1, 0, 1, "いてて…\nちょっと日焼け\nしすぎたかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3286, 110401, 1, 3, 1, 0, 1, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3287, 110401, 1, 4, 1, 0, 1, "夏ならではの\n料理…か\n何がいいかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3288, 110401, 1, 5, 1, 0, 1, "やっぱりここは\n涼しくて快適だな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3289, 110401, 1, 101, 4, 0, 1, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3290, 110401, 1, 102, 8, 0, 1, "水着で部屋の中に\nいるのって\n変な感じだな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3291, 110401, 2, 1, 1, 0, 1, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3292, 110401, 2, 2, 1, 0, 1, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3293, 110401, 2, 3, 1, 0, 1, "夏バテしてないか？\nここで休んでけよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3294, 110401, 2, 4, 1, 0, 1, "…そう見られると\n照れるっていうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3295, 110401, 2, 101, 4, 0, 1, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3296, 110401, 2, 102, 8, 0, 1, "おまえと\n夏の思い出ができて\nよかったよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3297, 110401, 2, 1, 1, 1, 1, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3298, 110401, 2, 2, 1, 2, 1, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3299, 110401, 2, 3, 1, 3, 1, "今日もお疲れ\n明日のパトロールも\nよろしくな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3301, 110401, 3, 2, 1, 0, 1, "泳ぎ疲れたからな\n少し休憩だ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3302, 110401, 3, 3, 1, 0, 1, "か、かわいい…\nいや、なんでもねぇ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3303, 110401, 3, 4, 1, 0, 1, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3304, 110401, 3, 5, 1, 0, 1, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3306, 110401, 3, 7, 1, 0, 1, "海で食べると\nまた一味違うよな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3307, 110401, 3, 8, 1, 0, 1, "夏のサバイバル特集…\nちょっと読んでみるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3309, 110401, 3, 10, 1, 0, 1, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3310, 110401, 3, 11, 1, 0, 1, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3311, 110501, 1, 1, 1, 0, 1, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3312, 110501, 1, 2, 1, 0, 1, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3313, 110501, 1, 3, 1, 0, 1, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3314, 110501, 1, 4, 1, 0, 1, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3315, 110501, 1, 5, 1, 0, 1, "クロールも\n犬かきも\n大得意さ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3316, 110501, 1, 101, 4, 0, 1, "なんだか歌いたい\n気分さ～\nキミもどう～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3317, 110501, 1, 102, 8, 0, 1, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3318, 110501, 2, 1, 1, 0, 1, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3319, 110501, 2, 2, 1, 0, 1, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3320, 110501, 2, 3, 1, 0, 1, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3321, 110501, 2, 4, 1, 0, 1, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3322, 110501, 2, 101, 4, 0, 1, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3323, 110501, 2, 102, 8, 0, 1, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3324, 110501, 2, 1, 1, 1, 1, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3325, 110501, 2, 2, 1, 2, 1, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3326, 110501, 2, 3, 1, 3, 1, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3328, 110501, 3, 2, 1, 0, 1, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3329, 110501, 3, 3, 1, 0, 1, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3330, 110501, 3, 4, 1, 0, 1, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3331, 110501, 3, 5, 1, 0, 1, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3333, 110501, 3, 7, 1, 0, 1, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3334, 110501, 3, 8, 1, 0, 1, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3336, 110501, 3, 10, 1, 0, 1, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3337, 110501, 3, 11, 1, 0, 1, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3338, 105801, 1, 103, 12, 0, 1, "あの人が\nわたしの騎士だったら\nいいのにな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3339, 105801, 2, 103, 12, 0, 1, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3340, 105901, 1, 103, 12, 0, 1, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3341, 105901, 2, 103, 12, 0, 1, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3342, 106001, 1, 103, 12, 0, 1, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3343, 106001, 2, 103, 12, 0, 1, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3344, 110601, 1, 1, 1, 0, 1, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3345, 110601, 1, 2, 1, 0, 1, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3346, 110601, 1, 3, 1, 0, 1, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3347, 110601, 1, 4, 1, 0, 1, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3348, 110601, 1, 5, 1, 0, 1, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3349, 110601, 1, 101, 4, 0, 1, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3350, 110601, 1, 102, 8, 0, 1, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3351, 110601, 2, 1, 1, 0, 1, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3352, 110601, 2, 2, 1, 0, 1, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3353, 110601, 2, 3, 1, 0, 1, "日が落ちたら\n線香花火\nやりたいわぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3354, 110601, 2, 4, 1, 0, 1, "木陰でのんびり\nお昼寝…\n最高やね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3355, 110601, 2, 101, 4, 0, 1, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3356, 110601, 2, 102, 8, 0, 1, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3357, 110601, 2, 1, 1, 1, 1, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3358, 110601, 2, 2, 1, 2, 1, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3359, 110601, 2, 3, 1, 3, 1, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3361, 110601, 3, 2, 1, 0, 1, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3362, 110601, 3, 3, 1, 0, 1, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3363, 110601, 3, 4, 1, 0, 1, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3364, 110601, 3, 5, 1, 0, 1, "これで\n冷えた身体も\nポカポカやねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3366, 110601, 3, 7, 1, 0, 1, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3367, 110601, 3, 8, 1, 0, 1, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3369, 110601, 3, 10, 1, 0, 1, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3370, 110601, 3, 11, 1, 0, 1, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3371, 107001, 1, 1, 1, 0, 1, "真似したくなる\n人がここには\n大勢いますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3372, 107001, 1, 2, 1, 0, 1, "偽物を一人\n紛れこませました\n…冗談です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3373, 107001, 1, 3, 1, 0, 1, "動物に変身して\nその辺りを\n散歩してきます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3374, 107001, 1, 4, 1, 0, 1, "失った記憶は\nどの程度回復するの\nでしょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3375, 107001, 1, 5, 1, 0, 1, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3376, 107001, 1, 101, 4, 0, 1, "次は彼に\nどんなことを\n教えましょうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3377, 107001, 1, 102, 8, 0, 1, "知識はともかく\n私の力はまだ\n必要なようですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3378, 107001, 2, 1, 1, 0, 1, "どうしました？\n…何でもない？\n…そうですか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3379, 107001, 2, 2, 1, 0, 1, "退屈しています\n芸でもして\n楽しませなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3380, 107001, 2, 3, 1, 0, 1, "研究のためです\n少し話し相手に\nなりなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3381, 107001, 2, 4, 1, 0, 1, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3382, 107001, 2, 101, 4, 0, 1, "貴方にはまだ色々と\n教えるべきことが\nありますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3383, 107001, 2, 102, 8, 0, 1, "随分多くの女性と\n知り合いなのですね\n驚きました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3384, 107001, 2, 1, 1, 1, 1, "…\nおはようございます\n…まだ朝ですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3385, 107001, 2, 2, 1, 2, 1, "食事にします\n…どうしました？\n早く持ってきなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3386, 107001, 2, 3, 1, 3, 1, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3388, 107001, 3, 2, 1, 0, 1, "マサキより\n座り心地は\nいいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3389, 107001, 3, 3, 1, 0, 1, "意外に複雑な\n構造ですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3390, 107001, 3, 4, 1, 0, 1, "真似るなら\n香りまで\n再現しなくては", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3391, 107001, 3, 5, 1, 0, 1, "発火現象の\n模倣…なかなか\n難しいものです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3393, 107001, 3, 7, 1, 0, 1, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3394, 107001, 3, 8, 1, 0, 1, "暇つぶしには\nちょうどよさそうです\n…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3396, 110701, 1, 1, 1, 0, 1, "学院かぁ…\n友達の作り方の\n授業はないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3397, 110701, 1, 2, 1, 0, 1, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3398, 110701, 1, 3, 1, 0, 1, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3399, 110701, 1, 4, 1, 0, 1, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3400, 110701, 1, 5, 1, 0, 1, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3401, 110701, 1, 101, 4, 0, 1, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3402, 110701, 1, 102, 8, 0, 1, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3403, 110701, 2, 1, 1, 0, 1, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3404, 110701, 2, 2, 1, 0, 1, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3405, 110701, 2, 3, 1, 0, 1, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3406, 110701, 2, 4, 1, 0, 1, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3407, 110701, 2, 101, 4, 0, 1, "学院でも、\nBB団の活動を\nしていきましょうね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3408, 110701, 2, 102, 8, 0, 1, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3409, 110701, 2, 1, 1, 1, 1, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3410, 110701, 2, 2, 1, 2, 1, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3411, 110701, 2, 3, 1, 3, 1, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3413, 110701, 3, 2, 1, 0, 1, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3414, 110701, 3, 3, 1, 0, 1, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3415, 110701, 3, 4, 1, 0, 1, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3416, 110701, 3, 5, 1, 0, 1, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3418, 110701, 3, 7, 1, 0, 1, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3419, 110701, 3, 8, 1, 0, 1, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3421, 110801, 1, 1, 1, 0, 1, "あんた、何してんの。\nいいけど、なんでも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3422, 110801, 1, 2, 1, 0, 1, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3423, 110801, 1, 3, 1, 0, 1, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3424, 110801, 1, 4, 1, 0, 1, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3425, 110801, 1, 5, 1, 0, 1, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3426, 110801, 1, 101, 4, 0, 1, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3427, 110801, 1, 102, 8, 0, 1, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3428, 110801, 2, 1, 1, 0, 1, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3429, 110801, 2, 2, 1, 0, 1, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3430, 110801, 2, 3, 1, 0, 1, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3431, 110801, 2, 4, 1, 0, 1, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3432, 110801, 2, 101, 4, 0, 1, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3433, 110801, 2, 102, 8, 0, 1, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3434, 110801, 2, 1, 1, 1, 1, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3435, 110801, 2, 2, 1, 2, 1, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3436, 110801, 2, 3, 1, 3, 1, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3438, 110801, 3, 2, 1, 0, 1, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3439, 110801, 3, 3, 1, 0, 1, "なんだこれ\nかわいいかよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3440, 110801, 3, 4, 1, 0, 1, "ふーん。\nこの匂い…\nなんかいいじゃん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3441, 110801, 3, 5, 1, 0, 1, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3443, 110801, 3, 7, 1, 0, 1, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3444, 110801, 3, 8, 1, 0, 1, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3446, 111101, 1, 1, 1, 0, 1, "イタズラってなにを\nしたらいいのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3447, 111101, 1, 2, 1, 0, 1, "カップケーキ\n上手に作れました\n食べてくれるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3448, 111101, 1, 3, 1, 0, 1, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3449, 111101, 1, 4, 1, 0, 1, "オバケ…いないよね？\n怖くなんてないけど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3450, 111101, 1, 5, 1, 0, 1, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3451, 111101, 1, 101, 4, 0, 1, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3452, 111101, 1, 102, 8, 0, 1, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3453, 111101, 2, 1, 1, 0, 1, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3454, 111101, 2, 2, 1, 0, 1, "触りましたね？\nイタズラしましたね？\nへんた～い！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3455, 111101, 2, 3, 1, 0, 1, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3456, 111101, 2, 4, 1, 0, 1, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3457, 111101, 2, 101, 4, 0, 1, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3458, 111101, 2, 102, 8, 0, 1, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3459, 111101, 2, 1, 1, 1, 1, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3460, 111101, 2, 2, 1, 2, 1, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3461, 111101, 2, 3, 1, 3, 1, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3463, 111101, 3, 2, 1, 0, 1, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3464, 111101, 3, 3, 1, 0, 1, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3465, 111101, 3, 4, 1, 0, 1, "甘い匂いがいっぱい…\nお菓子の香りかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3466, 111101, 3, 5, 1, 0, 1, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3468, 111101, 3, 7, 1, 0, 1, "ニンジンもこれくらい\nおいしければ\nいいのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3469, 111101, 3, 8, 1, 0, 1, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3471, 111101, 3, 12, 1, 0, 1, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3473, 111201, 1, 1, 1, 0, 1, "たくさんいたずら\nできるから\nハロウィン大好き！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3474, 111201, 1, 2, 1, 0, 1, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3475, 111201, 1, 3, 1, 0, 1, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3476, 111201, 1, 4, 1, 0, 1, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3477, 111201, 1, 5, 1, 0, 1, "どうすればみんなを\nびっくりさせられる\nかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3478, 111201, 1, 101, 4, 0, 1, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3479, 111201, 1, 102, 8, 0, 1, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3480, 111201, 2, 1, 1, 0, 1, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3481, 111201, 2, 2, 1, 0, 1, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3482, 111201, 2, 3, 1, 0, 1, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3483, 111201, 2, 4, 1, 0, 1, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3484, 111201, 2, 101, 4, 0, 1, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3485, 111201, 2, 102, 8, 0, 1, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3486, 111201, 2, 1, 1, 1, 1, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3487, 111201, 2, 2, 1, 2, 1, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3488, 111201, 2, 3, 1, 3, 1, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3490, 111201, 3, 2, 1, 0, 1, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3491, 111201, 3, 3, 1, 0, 1, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3492, 111201, 3, 4, 1, 0, 1, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3493, 111201, 3, 5, 1, 0, 1, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3495, 111201, 3, 7, 1, 0, 1, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3496, 111201, 3, 8, 1, 0, 1, "本のなかに\nいたずらのヒント\nないかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3498, 111201, 3, 12, 1, 0, 1, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3500, 101101, 1, 103, 12, 0, 1, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3501, 101101, 2, 103, 12, 0, 1, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3502, 111301, 1, 1, 1, 0, 1, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3503, 111301, 1, 2, 1, 0, 1, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3504, 111301, 1, 3, 1, 0, 1, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3505, 111301, 1, 4, 1, 0, 1, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3506, 111301, 1, 5, 1, 0, 1, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3507, 111301, 1, 101, 4, 0, 1, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3508, 111301, 1, 102, 8, 0, 1, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3509, 111301, 2, 1, 1, 0, 1, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3510, 111301, 2, 2, 1, 0, 1, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3511, 111301, 2, 3, 1, 0, 1, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3512, 111301, 2, 4, 1, 0, 1, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3513, 111301, 2, 101, 4, 0, 1, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3514, 111301, 2, 102, 8, 0, 1, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3515, 111301, 2, 1, 1, 1, 1, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3516, 111301, 2, 2, 1, 2, 1, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3517, 111301, 2, 3, 1, 3, 1, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3519, 111301, 3, 2, 1, 0, 1, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3520, 111301, 3, 3, 1, 0, 1, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3521, 111301, 3, 4, 1, 0, 1, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3522, 111301, 3, 5, 1, 0, 1, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3524, 111301, 3, 7, 1, 0, 1, "おいしい～♪\nおにいちゃんも\n食べようよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3525, 111301, 3, 8, 1, 0, 1, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3527, 111301, 3, 12, 1, 0, 1, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3529, 111401, 1, 1, 1, 0, 1, "ここ、好きだよ。\nみんな優しいから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3530, 111401, 1, 2, 1, 0, 1, "なんかさみしいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3531, 111401, 1, 3, 1, 0, 1, "知らない人怖いな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3532, 111401, 1, 4, 1, 0, 1, "お部屋おっきいねー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3533, 111401, 1, 5, 1, 0, 1, "ルナね…\nお昼寝したいかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3534, 111401, 1, 101, 4, 0, 1, "おにいさん！\n今日は何して遊ぶの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3535, 111401, 1, 102, 8, 0, 1, "あのね、\nお料理教えてほしい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3536, 111401, 2, 1, 1, 0, 1, "あっ、おにいさんだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3537, 111401, 2, 2, 1, 0, 1, "ルナは元気だよ！\nいいこだもん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3538, 111401, 2, 3, 1, 0, 1, "今日は何しようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3539, 111401, 2, 4, 1, 0, 1, "ルナと遊んでよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3540, 111401, 2, 101, 4, 0, 1, "ねーねー、\nルナとお出かけして！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3541, 111401, 2, 102, 8, 0, 1, "おにいさんと、\n遊びたいなー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3542, 111401, 2, 1, 1, 1, 1, "ふふっ！\nなんかまぶしいね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3543, 111401, 2, 2, 1, 2, 1, "ルナ、お腹空いたな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3544, 111401, 2, 3, 1, 3, 1, "どこに行こうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3546, 111401, 3, 2, 1, 0, 1, "ルナ、これ好きだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3547, 111401, 3, 3, 1, 0, 1, "かわいい！　好き！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3548, 111401, 3, 4, 1, 0, 1, "なんかいい匂い！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3549, 111401, 3, 5, 1, 0, 1, "あったかいねー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3551, 111401, 3, 7, 1, 0, 1, "ふふっ\nおいしいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3552, 111401, 3, 8, 1, 0, 1, "ルナ、\n眠くなっちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3554, 105201, 1, 103, 12, 0, 1, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3555, 105201, 2, 103, 12, 0, 1, "あなたとなら\nどんなことだって\nできる気がするわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3556, 106501, 1, 1, 1, 0, 1, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3557, 106501, 1, 2, 1, 0, 1, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3558, 106501, 1, 3, 1, 0, 1, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3559, 106501, 1, 4, 1, 0, 1, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3560, 106501, 1, 5, 1, 0, 1, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3561, 106501, 1, 101, 4, 0, 1, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3562, 106501, 1, 102, 8, 0, 1, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3563, 106501, 2, 1, 1, 0, 1, "よっす！\nなんかオイシイ話でも\nあったりしない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3564, 106501, 2, 2, 1, 0, 1, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3565, 106501, 2, 3, 1, 0, 1, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3566, 106501, 2, 4, 1, 0, 1, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3567, 106501, 2, 101, 4, 0, 1, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3568, 106501, 2, 102, 8, 0, 1, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3569, 106501, 2, 1, 1, 1, 1, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3570, 106501, 2, 2, 1, 2, 1, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3571, 106501, 2, 3, 1, 3, 1, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3573, 106501, 3, 2, 1, 0, 1, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3574, 106501, 3, 3, 1, 0, 1, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3575, 106501, 3, 4, 1, 0, 1, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3576, 106501, 3, 5, 1, 0, 1, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3578, 106501, 3, 7, 1, 0, 1, "いつでも全力で\nケンカするために\n力を蓄えないとな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3579, 106501, 3, 8, 1, 0, 1, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3581, 111501, 1, 1, 1, 0, 1, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3582, 111501, 1, 2, 1, 0, 1, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3583, 111501, 1, 3, 1, 0, 1, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3584, 111501, 1, 4, 1, 0, 1, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3585, 111501, 1, 5, 1, 0, 1, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3586, 111501, 1, 101, 4, 0, 1, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3587, 111501, 1, 102, 8, 0, 1, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3588, 111501, 2, 1, 1, 0, 1, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3589, 111501, 2, 2, 1, 0, 1, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3590, 111501, 2, 3, 1, 0, 1, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3591, 111501, 2, 4, 1, 0, 1, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3592, 111501, 2, 101, 4, 0, 1, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3593, 111501, 2, 102, 8, 0, 1, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3594, 111501, 2, 1, 1, 1, 1, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3595, 111501, 2, 2, 1, 2, 1, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3596, 111501, 2, 3, 1, 3, 1, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3598, 111501, 3, 2, 1, 0, 1, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3599, 111501, 3, 3, 1, 0, 1, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3600, 111501, 3, 4, 1, 0, 1, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3601, 111501, 3, 5, 1, 0, 1, "体が温まってくると\nひと暴れしたくなるな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3603, 111501, 3, 7, 1, 0, 1, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3604, 111501, 3, 8, 1, 0, 1, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3606, 111501, 3, 14, 1, 0, 1, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3607, 111501, 3, 15, 1, 0, 1, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3608, 111601, 1, 1, 1, 0, 1, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3609, 111601, 1, 2, 1, 0, 1, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3610, 111601, 1, 3, 1, 0, 1, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3611, 111601, 1, 4, 1, 0, 1, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3612, 111601, 1, 5, 1, 0, 1, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3613, 111601, 1, 101, 4, 0, 1, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3614, 111601, 1, 102, 8, 0, 1, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3615, 111601, 2, 1, 1, 0, 1, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3616, 111601, 2, 2, 1, 0, 1, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3617, 111601, 2, 3, 1, 0, 1, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3618, 111601, 2, 4, 1, 0, 1, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3619, 111601, 2, 101, 4, 0, 1, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3620, 111601, 2, 102, 8, 0, 1, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3621, 111601, 2, 1, 1, 1, 1, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3622, 111601, 2, 2, 1, 2, 1, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3623, 111601, 2, 3, 1, 3, 1, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3625, 111601, 3, 2, 1, 0, 1, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3626, 111601, 3, 3, 1, 0, 1, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3627, 111601, 3, 4, 1, 0, 1, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3628, 111601, 3, 5, 1, 0, 1, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3630, 111601, 3, 7, 1, 0, 1, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3631, 111601, 3, 8, 1, 0, 1, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3633, 111601, 3, 14, 1, 0, 1, "面白ーい！\nこの演出、ライブでも\nできないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3634, 111601, 3, 15, 1, 0, 1, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3635, 101801, 1, 103, 12, 0, 1, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3636, 101801, 2, 103, 12, 0, 1, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3637, 111701, 1, 1, 1, 0, 1, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3638, 111701, 1, 2, 1, 0, 1, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3639, 111701, 1, 3, 1, 0, 1, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3640, 111701, 1, 4, 1, 0, 1, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3641, 111701, 1, 5, 1, 0, 1, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3642, 111701, 1, 101, 4, 0, 1, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3643, 111701, 1, 102, 8, 0, 1, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3644, 111701, 2, 1, 1, 0, 1, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3645, 111701, 2, 2, 1, 0, 1, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3646, 111701, 2, 3, 1, 0, 1, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3647, 111701, 2, 4, 1, 0, 1, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3648, 111701, 2, 101, 4, 0, 1, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3649, 111701, 2, 102, 8, 0, 1, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3650, 111701, 2, 1, 1, 1, 1, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3651, 111701, 2, 2, 1, 2, 1, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3652, 111701, 2, 3, 1, 3, 1, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3654, 111701, 3, 2, 1, 0, 1, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3655, 111701, 3, 3, 1, 0, 1, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3656, 111701, 3, 4, 1, 0, 1, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3657, 111701, 3, 5, 1, 0, 1, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3659, 111701, 3, 7, 1, 0, 1, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3660, 111701, 3, 8, 1, 0, 1, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3662, 111701, 3, 14, 1, 0, 1, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3663, 111701, 3, 15, 1, 0, 1, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3664, 112001, 1, 1, 1, 0, 1, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3665, 112001, 1, 2, 1, 0, 1, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3666, 112001, 1, 3, 1, 0, 1, "初日の出って\n特別な感じがして\nいいわよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3667, 112001, 1, 4, 1, 0, 1, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3668, 112001, 1, 5, 1, 0, 1, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3669, 112001, 1, 101, 4, 0, 1, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3670, 112001, 1, 102, 8, 0, 1, "今年もあいつと\n居られること…\n結構うれしいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3671, 112001, 2, 1, 1, 0, 1, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3672, 112001, 2, 2, 1, 0, 1, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3673, 112001, 2, 3, 1, 0, 1, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3674, 112001, 2, 4, 1, 0, 1, "門松飾るから\nちょっと手伝って\nくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3675, 112001, 2, 101, 4, 0, 1, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3676, 112001, 2, 102, 8, 0, 1, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3677, 112001, 2, 1, 1, 1, 1, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3678, 112001, 2, 2, 1, 2, 1, "おせちに飽きたら\n次は何を食べよう\nかしら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3679, 112001, 2, 3, 1, 3, 1, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3681, 112001, 3, 2, 1, 0, 1, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3682, 112001, 3, 3, 1, 0, 1, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3683, 112001, 3, 4, 1, 0, 1, "いい匂いね\n落ち着いて\n眠れそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3684, 112001, 3, 5, 1, 0, 1, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3686, 112001, 3, 7, 1, 0, 1, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3687, 112001, 3, 8, 1, 0, 1, "魔物を使わない\nおせちを\n調べておかなきゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3689, 112101, 1, 1, 1, 0, 1, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3690, 112101, 1, 2, 1, 0, 1, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3691, 112101, 1, 3, 1, 0, 1, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3692, 112101, 1, 4, 1, 0, 1, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3693, 112101, 1, 5, 1, 0, 1, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3694, 112101, 1, 101, 4, 0, 1, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3695, 112101, 1, 102, 8, 0, 1, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3696, 112101, 2, 1, 1, 0, 1, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3697, 112101, 2, 2, 1, 0, 1, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3698, 112101, 2, 3, 1, 0, 1, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3699, 112101, 2, 4, 1, 0, 1, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3700, 112101, 2, 101, 4, 0, 1, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3701, 112101, 2, 102, 8, 0, 1, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3702, 112101, 2, 1, 1, 1, 1, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3703, 112101, 2, 2, 1, 2, 1, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3704, 112101, 2, 3, 1, 3, 1, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3706, 112101, 3, 2, 1, 0, 1, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3707, 112101, 3, 3, 1, 0, 1, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3708, 112101, 3, 4, 1, 0, 1, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3709, 112101, 3, 5, 1, 0, 1, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3711, 112101, 3, 7, 1, 0, 1, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3712, 112101, 3, 8, 1, 0, 1, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3714, 103401, 1, 103, 12, 0, 1, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3715, 103401, 2, 103, 12, 0, 1, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3716, 111901, 1, 1, 1, 0, 1, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3717, 111901, 1, 2, 1, 0, 1, "年を越したあとに\n食べるおそばも\nおいしいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3718, 111901, 1, 3, 1, 0, 1, "甘酒を飲むと\n心まで温まる\n気がいたします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3719, 111901, 1, 4, 1, 0, 1, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3720, 111901, 1, 5, 1, 0, 1, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3721, 111901, 1, 101, 4, 0, 1, "主さまの温もりは\n心まで温めてくれる\nようです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3722, 111901, 1, 102, 8, 0, 1, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3723, 111901, 2, 1, 1, 0, 1, "主さま、この晴れ着は\n似合っている\nでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3724, 111901, 2, 2, 1, 0, 1, "お餅はゆっくりと\n召し上がって\nくださいまし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3725, 111901, 2, 3, 1, 0, 1, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3726, 111901, 2, 4, 1, 0, 1, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3727, 111901, 2, 101, 4, 0, 1, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3728, 111901, 2, 102, 8, 0, 1, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3729, 111901, 2, 1, 1, 1, 1, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3730, 111901, 2, 2, 1, 2, 1, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3731, 111901, 2, 3, 1, 3, 1, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3733, 111901, 3, 2, 1, 0, 1, "ふかふか…\nこのまま\n寝てしまいそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3734, 111901, 3, 3, 1, 0, 1, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3735, 111901, 3, 4, 1, 0, 1, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3736, 111901, 3, 5, 1, 0, 1, "風邪をひきませんよう\n暖かくいたしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3738, 111901, 3, 7, 1, 0, 1, "食べ過ぎは\n正月太りの\nもとだというのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3739, 111901, 3, 8, 1, 0, 1, "主さまのために\n参拝の作法を\n予習しておきましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3741, 112201, 1, 1, 1, 0, 1, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3742, 112201, 1, 2, 1, 0, 1, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3743, 112201, 1, 3, 1, 0, 1, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3744, 112201, 1, 4, 1, 0, 1, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3745, 112201, 1, 5, 1, 0, 1, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3746, 112201, 1, 101, 4, 0, 1, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3747, 112201, 1, 102, 8, 0, 1, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3748, 112201, 2, 1, 1, 0, 1, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3749, 112201, 2, 2, 1, 0, 1, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3750, 112201, 2, 3, 1, 0, 1, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3751, 112201, 2, 4, 1, 0, 1, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3752, 112201, 2, 101, 4, 0, 1, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3753, 112201, 2, 102, 8, 0, 1, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3754, 112201, 2, 1, 1, 1, 1, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3755, 112201, 2, 2, 1, 2, 1, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3756, 112201, 2, 3, 1, 3, 1, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3758, 112201, 3, 2, 1, 0, 1, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3759, 112201, 3, 3, 1, 0, 1, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3760, 112201, 3, 4, 1, 0, 1, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3761, 112201, 3, 5, 1, 0, 1, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3763, 112201, 3, 7, 1, 0, 1, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3764, 112201, 3, 8, 1, 0, 1, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3766, 112301, 1, 1, 1, 0, 1, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3767, 112301, 1, 2, 1, 0, 1, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3768, 112301, 1, 3, 1, 0, 1, "ふふ…\n今日はなんだか\n調子がいいかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3769, 112301, 1, 4, 1, 0, 1, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3770, 112301, 1, 5, 1, 0, 1, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3771, 112301, 1, 101, 4, 0, 1, "あの人がいると\nなんだかホッとする\n不思議…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3772, 112301, 1, 102, 8, 0, 1, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3773, 112301, 2, 1, 1, 0, 1, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3774, 112301, 2, 2, 1, 0, 1, "よかったら魔法の\n練習に付き合って\nくれませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3775, 112301, 2, 3, 1, 0, 1, "あの…\nこの格好どうですか？\n似合ってると…いいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3776, 112301, 2, 4, 1, 0, 1, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3777, 112301, 2, 101, 4, 0, 1, "私、あなたのそばに\nいられたら、それで…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3778, 112301, 2, 102, 8, 0, 1, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3779, 112301, 2, 1, 1, 1, 1, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3780, 112301, 2, 2, 1, 2, 1, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3781, 112301, 2, 3, 1, 3, 1, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3783, 112301, 3, 2, 1, 0, 1, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3784, 112301, 3, 3, 1, 0, 1, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3785, 112301, 3, 4, 1, 0, 1, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3786, 112301, 3, 5, 1, 0, 1, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3788, 112301, 3, 7, 1, 0, 1, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3789, 112301, 3, 8, 1, 0, 1, "ふふ…\n魔法少女になっても\n読書はやめられないな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3791, 101001, 1, 103, 12, 0, 1, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3792, 101001, 2, 103, 12, 0, 1, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3793, 180401, 1, 1, 1, 0, 1, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3794, 180401, 1, 2, 1, 0, 1, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3795, 180401, 1, 3, 1, 0, 1, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3796, 180401, 1, 4, 1, 0, 1, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3797, 180401, 1, 5, 1, 0, 1, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3798, 180401, 1, 101, 4, 0, 1, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3799, 180401, 1, 102, 8, 0, 1, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3800, 180401, 2, 1, 1, 0, 1, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3801, 180401, 2, 2, 1, 0, 1, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3802, 180401, 2, 3, 1, 0, 1, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3803, 180401, 2, 4, 1, 0, 1, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3804, 180401, 2, 101, 4, 0, 1, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3805, 180401, 2, 102, 8, 0, 1, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3806, 180401, 2, 1, 1, 1, 1, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3807, 180401, 2, 2, 1, 2, 1, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3808, 180401, 2, 3, 1, 3, 1, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3810, 180401, 3, 2, 1, 0, 1, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3811, 180401, 3, 3, 1, 0, 1, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3812, 180401, 3, 4, 1, 0, 1, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3813, 180401, 3, 5, 1, 0, 1, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3815, 180401, 3, 7, 1, 0, 1, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3816, 180401, 3, 8, 1, 0, 1, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3818, 112401, 1, 1, 1, 0, 1, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3819, 112401, 1, 2, 1, 0, 1, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3820, 112401, 1, 3, 1, 0, 1, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3821, 112401, 1, 4, 1, 0, 1, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3822, 112401, 1, 5, 1, 0, 1, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3823, 112401, 1, 101, 4, 0, 1, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3824, 112401, 1, 102, 8, 0, 1, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3825, 112401, 2, 1, 1, 0, 1, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3826, 112401, 2, 2, 1, 0, 1, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3827, 112401, 2, 3, 1, 0, 1, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3828, 112401, 2, 4, 1, 0, 1, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3829, 112401, 2, 101, 4, 0, 1, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3830, 112401, 2, 102, 8, 0, 1, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3831, 112401, 2, 1, 1, 1, 1, "おはようございます！\n今日も一日\n頑張りましょうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3832, 112401, 2, 2, 1, 2, 1, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3833, 112401, 2, 3, 1, 3, 1, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3835, 112401, 3, 2, 1, 0, 1, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3836, 112401, 3, 3, 1, 0, 1, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3837, 112401, 3, 4, 1, 0, 1, "リラックスできる\nいい香りですね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3838, 112401, 3, 5, 1, 0, 1, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3840, 112401, 3, 7, 1, 0, 1, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3841, 112401, 3, 8, 1, 0, 1, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3843, 112601, 1, 1, 1, 0, 1, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3844, 112601, 1, 2, 1, 0, 1, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3845, 112601, 1, 3, 1, 0, 1, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3846, 112601, 1, 4, 1, 0, 1, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3847, 112601, 1, 5, 1, 0, 1, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3848, 112601, 1, 101, 4, 0, 1, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3849, 112601, 1, 102, 8, 0, 1, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3850, 112601, 2, 1, 1, 0, 1, "なになにー？\nミオちゃんに\n何か用かな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3851, 112601, 2, 2, 1, 0, 1, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3852, 112601, 2, 3, 1, 0, 1, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3853, 112601, 2, 4, 1, 0, 1, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3854, 112601, 2, 101, 4, 0, 1, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3855, 112601, 2, 102, 8, 0, 1, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3856, 112601, 2, 1, 1, 1, 1, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3857, 112601, 2, 2, 1, 2, 1, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3858, 112601, 2, 3, 1, 3, 1, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3860, 112601, 3, 2, 1, 0, 1, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3861, 112601, 3, 3, 1, 0, 1, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3862, 112601, 3, 4, 1, 0, 1, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3863, 112601, 3, 5, 1, 0, 1, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3865, 112601, 3, 7, 1, 0, 1, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3866, 112601, 3, 8, 1, 0, 1, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3868, 100101, 1, 103, 12, 0, 1, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3869, 100101, 2, 103, 12, 0, 1, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3870, 100201, 1, 103, 12, 0, 1, "騎士クンの前で\n好きって言えるように\n練習しないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3871, 100201, 2, 103, 12, 0, 1, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3872, 100301, 1, 103, 12, 0, 1, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3873, 100301, 2, 103, 12, 0, 1, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3874, 112501, 1, 1, 1, 0, 1, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3875, 112501, 1, 2, 1, 0, 1, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3876, 112501, 1, 3, 1, 0, 1, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3877, 112501, 1, 4, 1, 0, 1, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3878, 112501, 1, 5, 1, 0, 1, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3879, 112501, 1, 101, 4, 0, 1, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3880, 112501, 1, 102, 8, 0, 1, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3881, 112501, 2, 1, 1, 0, 1, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3882, 112501, 2, 2, 1, 0, 1, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3883, 112501, 2, 3, 1, 0, 1, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3884, 112501, 2, 4, 1, 0, 1, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3885, 112501, 2, 101, 4, 0, 1, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3886, 112501, 2, 102, 8, 0, 1, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3887, 112501, 2, 1, 1, 1, 1, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3888, 112501, 2, 2, 1, 2, 1, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3889, 112501, 2, 3, 1, 3, 1, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3891, 112501, 3, 2, 1, 0, 1, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3892, 112501, 3, 3, 1, 0, 1, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3893, 112501, 3, 4, 1, 0, 1, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3894, 112501, 3, 5, 1, 0, 1, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3896, 112501, 3, 7, 1, 0, 1, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3897, 112501, 3, 8, 1, 0, 1, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3899, 111001, 1, 1, 1, 0, 1, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3900, 111001, 1, 2, 1, 0, 1, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3901, 111001, 1, 3, 1, 0, 1, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3902, 111001, 1, 4, 1, 0, 1, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3903, 111001, 1, 5, 1, 0, 1, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3904, 111001, 1, 101, 4, 0, 1, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3905, 111001, 1, 102, 8, 0, 1, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3906, 111001, 2, 1, 1, 0, 1, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3907, 111001, 2, 2, 1, 0, 1, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3908, 111001, 2, 3, 1, 0, 1, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3909, 111001, 2, 4, 1, 0, 1, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3910, 111001, 2, 101, 4, 0, 1, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3911, 111001, 2, 102, 8, 0, 1, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3912, 111001, 2, 1, 1, 1, 1, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3913, 111001, 2, 2, 1, 2, 1, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3914, 111001, 2, 3, 1, 3, 1, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3916, 111001, 3, 2, 1, 0, 1, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3917, 111001, 3, 3, 1, 0, 1, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3918, 111001, 3, 4, 1, 0, 1, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3919, 111001, 3, 5, 1, 0, 1, "炎はいいぞ\nそこには人類発展の\nドラマがある", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3921, 111001, 3, 7, 1, 0, 1, "フオオー！\n次から次へと固形食物\n原理気になるー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3922, 111001, 3, 8, 1, 0, 1, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3924, 101201, 1, 103, 12, 0, 1, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3925, 101201, 2, 103, 12, 0, 1, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3933, 192101, 2, 1, 1, 0, 1, "ちぇるーん☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3951, 110901, 1, 1, 1, 0, 1, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3952, 110901, 1, 2, 1, 0, 1, "どこかに楽しいもの\n落ちてないかにゃー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3953, 110901, 1, 3, 1, 0, 1, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3954, 110901, 1, 4, 1, 0, 1, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3955, 110901, 1, 5, 1, 0, 1, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3956, 110901, 1, 101, 4, 0, 1, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3957, 110901, 1, 102, 8, 0, 1, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3958, 110901, 2, 1, 1, 0, 1, "え、なんですか？\n告白だったら\n間に合ってますけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3959, 110901, 2, 2, 1, 0, 1, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3960, 110901, 2, 3, 1, 0, 1, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3961, 110901, 2, 4, 1, 0, 1, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3962, 110901, 2, 101, 4, 0, 1, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3963, 110901, 2, 102, 8, 0, 1, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3964, 110901, 2, 1, 1, 1, 1, "おはちぇる～ん☆\nハイ、ご一緒に～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3965, 110901, 2, 2, 1, 2, 1, "今日のランチは\nどこで\n映えよっかにゃー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3966, 110901, 2, 3, 1, 3, 1, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3968, 110901, 3, 2, 1, 0, 1, "ふかふかチエルン\n略して、ふかルン♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3969, 110901, 3, 3, 1, 0, 1, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3970, 110901, 3, 4, 1, 0, 1, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3971, 110901, 3, 5, 1, 0, 1, "マシュマロは\n焼かない方のが\nかわいくないです？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3973, 110901, 3, 7, 1, 0, 1, "チエル食べても\n太らない体質\nなんでぇ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3974, 110901, 3, 8, 1, 0, 1, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3976, 112701, 1, 1, 1, 0, 1, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3977, 112701, 1, 2, 1, 0, 1, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3978, 112701, 1, 3, 1, 0, 1, "あたしの代わりに誰か\n働いてくれないかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3979, 112701, 1, 4, 1, 0, 1, "労働の後に食べる\nあんパン最高～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3980, 112701, 1, 5, 1, 0, 1, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3981, 112701, 1, 101, 4, 0, 1, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3982, 112701, 1, 102, 8, 0, 1, "次の休みはあいつと\n一日中だらだら\nしたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3983, 112701, 2, 1, 1, 0, 1, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3984, 112701, 2, 2, 1, 0, 1, "お、差し入れ？\n気が利くねぇ\nあんがと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3985, 112701, 2, 3, 1, 0, 1, "新しい依頼？\n今日はもう無理～\n閉店で～す", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3986, 112701, 2, 4, 1, 0, 1, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3987, 112701, 2, 101, 4, 0, 1, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3988, 112701, 2, 102, 8, 0, 1, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3989, 112701, 2, 1, 1, 1, 1, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3990, 112701, 2, 2, 1, 2, 1, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3991, 112701, 2, 3, 1, 3, 1, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3993, 112701, 3, 2, 1, 0, 1, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3994, 112701, 3, 3, 1, 0, 1, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3995, 112701, 3, 4, 1, 0, 1, "あんパンの香りの\nアロマとか\nあればいいのにな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3996, 112701, 3, 5, 1, 0, 1, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3998, 112701, 3, 7, 1, 0, 1, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (3999, 112701, 3, 8, 1, 0, 1, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4001, 104601, 1, 103, 12, 0, 1, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4002, 104601, 2, 103, 12, 0, 1, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4003, 112801, 1, 1, 1, 0, 1, "フロンティア\nスピリッツを\nお見せするべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4004, 112801, 1, 2, 1, 0, 1, "この鎧を着ると\n気が引き締まる\n感じがするべ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4005, 112801, 1, 3, 1, 0, 1, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4006, 112801, 1, 4, 1, 0, 1, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4007, 112801, 1, 5, 1, 0, 1, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4008, 112801, 1, 101, 4, 0, 1, "みんなオラより\n若ぇのに働いてて\n立派だべな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4009, 112801, 1, 102, 8, 0, 1, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4010, 112801, 2, 1, 1, 0, 1, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4011, 112801, 2, 2, 1, 0, 1, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4012, 112801, 2, 3, 1, 0, 1, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4013, 112801, 2, 4, 1, 0, 1, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4014, 112801, 2, 101, 4, 0, 1, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4015, 112801, 2, 102, 8, 0, 1, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4016, 112801, 2, 1, 1, 1, 1, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4017, 112801, 2, 2, 1, 2, 1, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4018, 112801, 2, 3, 1, 3, 1, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4020, 112801, 3, 2, 1, 0, 1, "牧草みたいに\nふっかふかだべ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4021, 112801, 3, 3, 1, 0, 1, "この子、目の感じが\nエリザベスに似てるべ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4022, 112801, 3, 4, 1, 0, 1, "いい匂いだべ～\n疲れた体に\nしみるべな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4023, 112801, 3, 5, 1, 0, 1, "あったけぇな～\n牧場にも\n置いてみっかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4025, 112801, 3, 7, 1, 0, 1, "うまいべ！\n生産者のこだわりを\n感じるべさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4026, 112801, 3, 8, 1, 0, 1, "酪農も戦いも\nまずは知識だべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4028, 113001, 1, 1, 1, 0, 1, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4029, 113001, 1, 2, 1, 0, 1, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4030, 113001, 1, 3, 1, 0, 1, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4031, 113001, 1, 4, 1, 0, 1, "なんだか先輩の\n気配がするような…\n気のせいかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4032, 113001, 1, 5, 1, 0, 1, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4033, 113001, 1, 101, 4, 0, 1, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4034, 113001, 1, 102, 8, 0, 1, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4035, 113001, 2, 1, 1, 0, 1, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4036, 113001, 2, 2, 1, 0, 1, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4037, 113001, 2, 3, 1, 0, 1, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4038, 113001, 2, 4, 1, 0, 1, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4039, 113001, 2, 101, 4, 0, 1, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4040, 113001, 2, 102, 8, 0, 1, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4041, 113001, 2, 1, 1, 1, 1, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4042, 113001, 2, 2, 1, 2, 1, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4043, 113001, 2, 3, 1, 3, 1, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4045, 113001, 3, 2, 1, 0, 1, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4046, 113001, 3, 3, 1, 0, 1, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4047, 113001, 3, 4, 1, 0, 1, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4048, 113001, 3, 5, 1, 0, 1, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4050, 113001, 3, 7, 1, 0, 1, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4051, 113001, 3, 8, 1, 0, 1, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4053, 180501, 1, 1, 1, 0, 1, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4054, 180501, 1, 2, 1, 0, 1, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4055, 180501, 1, 3, 1, 0, 1, "月々の食費が\n増えてしまいました\n申し訳ございません…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4056, 180501, 1, 4, 1, 0, 1, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4057, 180501, 1, 5, 1, 0, 1, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4058, 180501, 1, 101, 4, 0, 1, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4059, 180501, 1, 102, 8, 0, 1, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4060, 180501, 2, 1, 1, 0, 1, "おでかけですか？\nではわたくしも\nお供いたします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4061, 180501, 2, 2, 1, 0, 1, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4062, 180501, 2, 3, 1, 0, 1, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4063, 180501, 2, 4, 1, 0, 1, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4064, 180501, 2, 101, 4, 0, 1, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4065, 180501, 2, 102, 8, 0, 1, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4066, 180501, 2, 1, 1, 1, 1, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4067, 180501, 2, 2, 1, 2, 1, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4068, 180501, 2, 3, 1, 3, 1, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4070, 180501, 3, 2, 1, 0, 1, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4071, 180501, 3, 3, 1, 0, 1, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4072, 180501, 3, 4, 1, 0, 1, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4073, 180501, 3, 5, 1, 0, 1, "主さまの心と\n繋がったときのような\n温もりを感じます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4075, 180501, 3, 7, 1, 0, 1, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4076, 180501, 3, 8, 1, 0, 1, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4078, 104801, 1, 103, 12, 0, 1, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4079, 104801, 2, 103, 12, 0, 1, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4080, 112901, 1, 1, 1, 0, 1, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4081, 112901, 1, 2, 1, 0, 1, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4082, 112901, 1, 3, 1, 0, 1, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4083, 112901, 1, 4, 1, 0, 1, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4084, 112901, 1, 5, 1, 0, 1, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4085, 112901, 1, 101, 4, 0, 1, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4086, 112901, 1, 102, 8, 0, 1, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4087, 112901, 2, 1, 1, 0, 1, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4088, 112901, 2, 2, 1, 0, 1, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4089, 112901, 2, 3, 1, 0, 1, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4090, 112901, 2, 4, 1, 0, 1, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4091, 112901, 2, 101, 4, 0, 1, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4092, 112901, 2, 102, 8, 0, 1, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4093, 112901, 2, 1, 1, 1, 1, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4094, 112901, 2, 2, 1, 2, 1, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4095, 112901, 2, 3, 1, 3, 1, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4097, 112901, 3, 2, 1, 0, 1, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4098, 112901, 3, 3, 1, 0, 1, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4099, 112901, 3, 4, 1, 0, 1, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4100, 112901, 3, 5, 1, 0, 1, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4102, 112901, 3, 7, 1, 0, 1, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4103, 112901, 3, 8, 1, 0, 1, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4105, 106601, 1, 1, 1, 0, 1, "退屈です…\nなにか楽しいことは\nないですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4106, 106601, 1, 2, 1, 0, 1, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4107, 106601, 1, 3, 1, 0, 1, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4108, 106601, 1, 4, 1, 0, 1, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4109, 106601, 1, 5, 1, 0, 1, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4110, 106601, 1, 101, 4, 0, 1, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4111, 106601, 1, 102, 8, 0, 1, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4112, 106601, 2, 1, 1, 0, 1, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4113, 106601, 2, 2, 1, 0, 1, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4114, 106601, 2, 3, 1, 0, 1, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4115, 106601, 2, 4, 1, 0, 1, "お兄さんは\nヒマそうですね…\nうらやましいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4116, 106601, 2, 101, 4, 0, 1, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4117, 106601, 2, 102, 8, 0, 1, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4118, 106601, 2, 1, 1, 1, 1, "デキるギャングの\n朝は早いのです…\nふぁあ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4119, 106601, 2, 2, 1, 2, 1, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4120, 106601, 2, 3, 1, 3, 1, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4122, 106601, 3, 2, 1, 0, 1, "はぁ～…\nもうここから一歩も\n動きたくないです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4123, 106601, 3, 3, 1, 0, 1, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4124, 106601, 3, 4, 1, 0, 1, "なんかおしゃれな\n匂いがするですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4125, 106601, 3, 5, 1, 0, 1, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4127, 106601, 3, 7, 1, 0, 1, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4128, 106601, 3, 8, 1, 0, 1, "ふーむ…\nもっと楽しい本は\nないですかね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4130, 113201, 1, 1, 1, 0, 1, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4131, 113201, 1, 2, 1, 0, 1, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4132, 113201, 1, 3, 1, 0, 1, "アターック！\nいや、もっとこう…\nアタァーック！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4133, 113201, 1, 4, 1, 0, 1, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4134, 113201, 1, 5, 1, 0, 1, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4135, 113201, 1, 101, 4, 0, 1, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4136, 113201, 1, 102, 8, 0, 1, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4137, 113201, 2, 1, 1, 0, 1, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4138, 113201, 2, 2, 1, 0, 1, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4139, 113201, 2, 3, 1, 0, 1, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4140, 113201, 2, 4, 1, 0, 1, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4141, 113201, 2, 101, 4, 0, 1, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4142, 113201, 2, 102, 8, 0, 1, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4143, 113201, 2, 1, 1, 1, 1, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4144, 113201, 2, 2, 1, 2, 1, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4145, 113201, 2, 3, 1, 3, 1, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4147, 113201, 3, 2, 1, 0, 1, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4148, 113201, 3, 3, 1, 0, 1, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4149, 113201, 3, 4, 1, 0, 1, "心地よい香りで\n我が精神も安らぐ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4150, 113201, 3, 5, 1, 0, 1, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4152, 113201, 3, 7, 1, 0, 1, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4153, 113201, 3, 8, 1, 0, 1, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4155, 113201, 3, 10, 1, 0, 1, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4156, 113201, 3, 11, 1, 0, 1, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4157, 180201, 1, 1, 1, 0, 1, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4158, 180201, 1, 2, 1, 0, 1, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4159, 180201, 1, 3, 1, 0, 1, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4160, 180201, 1, 4, 1, 0, 1, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4161, 180201, 1, 5, 1, 0, 1, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4162, 180201, 1, 101, 4, 0, 1, "騎士クン…\n今どこにいるの…？\n…会いたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4163, 180201, 1, 102, 8, 0, 1, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4164, 180201, 2, 1, 1, 0, 1, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4165, 180201, 2, 2, 1, 0, 1, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4166, 180201, 2, 3, 1, 0, 1, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4167, 180201, 2, 4, 1, 0, 1, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4168, 180201, 2, 101, 4, 0, 1, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4169, 180201, 2, 102, 8, 0, 1, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4170, 180201, 2, 1, 1, 1, 1, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4171, 180201, 2, 2, 1, 2, 1, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4172, 180201, 2, 3, 1, 3, 1, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4174, 180201, 3, 2, 1, 0, 1, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4175, 180201, 3, 3, 1, 0, 1, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4176, 180201, 3, 4, 1, 0, 1, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4177, 180201, 3, 5, 1, 0, 1, "温かい…\n騎士クンがくれる\n力みたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4179, 180201, 3, 7, 1, 0, 1, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4180, 180201, 3, 8, 1, 0, 1, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4182, 104901, 1, 103, 12, 0, 1, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4183, 104901, 2, 103, 12, 0, 1, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4184, 113101, 1, 1, 1, 0, 1, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4185, 113101, 1, 2, 1, 0, 1, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4186, 113101, 1, 3, 1, 0, 1, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4187, 113101, 1, 4, 1, 0, 1, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4188, 113101, 1, 5, 1, 0, 1, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4189, 113101, 1, 101, 4, 0, 1, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4190, 113101, 1, 102, 8, 0, 1, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4191, 113101, 2, 1, 1, 0, 1, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4192, 113101, 2, 2, 1, 0, 1, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4193, 113101, 2, 3, 1, 0, 1, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4194, 113101, 2, 4, 1, 0, 1, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4195, 113101, 2, 101, 4, 0, 1, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4196, 113101, 2, 102, 8, 0, 1, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4197, 113101, 2, 1, 1, 1, 1, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4198, 113101, 2, 2, 1, 2, 1, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4199, 113101, 2, 3, 1, 3, 1, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4201, 113101, 3, 2, 1, 0, 1, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4202, 113101, 3, 3, 1, 0, 1, "あんたたちも\n海を楽しみたいって\n思っているのかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4203, 113101, 3, 4, 1, 0, 1, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4204, 113101, 3, 5, 1, 0, 1, "こういう格好だと\n少し冷えちまうんだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4206, 113101, 3, 7, 1, 0, 1, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4207, 113101, 3, 8, 1, 0, 1, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4209, 113101, 3, 10, 1, 0, 1, "へえ…\nなかなかいい\n乗り心地じゃないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4210, 113101, 3, 11, 1, 0, 1, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4211, 113301, 1, 1, 1, 0, 1, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4212, 113301, 1, 2, 1, 0, 1, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4213, 113301, 1, 3, 1, 0, 1, "今のナナカちゃんは\nたぶん水属性！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4214, 113301, 1, 4, 1, 0, 1, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4215, 113301, 1, 5, 1, 0, 1, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4216, 113301, 1, 101, 4, 0, 1, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4217, 113301, 1, 102, 8, 0, 1, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4218, 113301, 2, 1, 1, 0, 1, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4219, 113301, 2, 2, 1, 0, 1, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4220, 113301, 2, 3, 1, 0, 1, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4221, 113301, 2, 4, 1, 0, 1, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4222, 113301, 2, 101, 4, 0, 1, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4223, 113301, 2, 102, 8, 0, 1, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4224, 113301, 2, 1, 1, 1, 1, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4225, 113301, 2, 2, 1, 2, 1, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4226, 113301, 2, 3, 1, 3, 1, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4228, 113301, 3, 2, 1, 0, 1, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4229, 113301, 3, 3, 1, 0, 1, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4230, 113301, 3, 4, 1, 0, 1, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4231, 113301, 3, 5, 1, 0, 1, "夜になると急に冷えて\n肌寒いんだよね～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4233, 113301, 3, 7, 1, 0, 1, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4234, 113301, 3, 8, 1, 0, 1, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4236, 113301, 3, 10, 1, 0, 1, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4237, 113301, 3, 11, 1, 0, 1, "ナナカちゃんの\n誕生～\nなんちゃって", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4238, 113401, 1, 1, 1, 0, 1, "ファイトファイト☆\n一生懸命\n応援するね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4239, 113401, 1, 2, 1, 0, 1, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4240, 113401, 1, 3, 1, 0, 1, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4241, 113401, 1, 4, 1, 0, 1, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4242, 113401, 1, 5, 1, 0, 1, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4243, 113401, 1, 101, 4, 0, 1, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4244, 113401, 1, 102, 8, 0, 1, "キミへの応援\nちゃんと\n届いてるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4245, 113401, 2, 1, 1, 0, 1, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4246, 113401, 2, 2, 1, 0, 1, "どうしたの？\n元気がないなら\n私が応援するよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4247, 113401, 2, 3, 1, 0, 1, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4248, 113401, 2, 4, 1, 0, 1, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4249, 113401, 2, 101, 4, 0, 1, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4250, 113401, 2, 102, 8, 0, 1, "キミを応援\nすることができて…\n私、すごく幸せ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4251, 113401, 2, 1, 1, 1, 1, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4252, 113401, 2, 2, 1, 2, 1, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4253, 113401, 2, 3, 1, 3, 1, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4255, 113401, 3, 2, 1, 0, 1, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4256, 113401, 3, 3, 1, 0, 1, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4257, 113401, 3, 4, 1, 0, 1, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4258, 113401, 3, 5, 1, 0, 1, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4260, 113401, 3, 7, 1, 0, 1, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4261, 113401, 3, 8, 1, 0, 1, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4263, 113401, 3, 10, 1, 0, 1, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4264, 113401, 3, 11, 1, 0, 1, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4265, 113501, 1, 1, 1, 0, 1, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4266, 113501, 1, 2, 1, 0, 1, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4267, 113501, 1, 3, 1, 0, 1, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4268, 113501, 1, 4, 1, 0, 1, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4269, 113501, 1, 5, 1, 0, 1, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4270, 113501, 1, 101, 4, 0, 1, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4271, 113501, 1, 102, 8, 0, 1, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4272, 113501, 2, 1, 1, 0, 1, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4273, 113501, 2, 2, 1, 0, 1, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4274, 113501, 2, 3, 1, 0, 1, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4275, 113501, 2, 4, 1, 0, 1, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4276, 113501, 2, 101, 4, 0, 1, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4277, 113501, 2, 102, 8, 0, 1, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4278, 113501, 2, 1, 1, 1, 1, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4279, 113501, 2, 2, 1, 2, 1, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4280, 113501, 2, 3, 1, 3, 1, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4282, 113501, 3, 2, 1, 0, 1, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4283, 113501, 3, 3, 1, 0, 1, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4284, 113501, 3, 4, 1, 0, 1, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4285, 113501, 3, 5, 1, 0, 1, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4287, 113501, 3, 7, 1, 0, 1, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4288, 113501, 3, 8, 1, 0, 1, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4290, 113501, 3, 10, 1, 0, 1, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4291, 113501, 3, 11, 1, 0, 1, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4292, 102301, 1, 103, 12, 0, 1, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4293, 102301, 2, 103, 12, 0, 1, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4294, 113601, 1, 1, 1, 0, 1, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4295, 113601, 1, 2, 1, 0, 1, "この格好で\n過ごすのは\n少し勇気がいるな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4296, 113601, 1, 3, 1, 0, 1, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4297, 113601, 1, 4, 1, 0, 1, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4298, 113601, 1, 5, 1, 0, 1, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4299, 113601, 1, 101, 4, 0, 1, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4300, 113601, 1, 102, 8, 0, 1, "少年との\n海のバカンス…\nまた行きたい\nものだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4301, 113601, 2, 1, 1, 0, 1, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4302, 113601, 2, 2, 1, 0, 1, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4303, 113601, 2, 3, 1, 0, 1, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4304, 113601, 2, 4, 1, 0, 1, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4305, 113601, 2, 101, 4, 0, 1, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4306, 113601, 2, 102, 8, 0, 1, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4307, 113601, 2, 1, 1, 1, 1, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4308, 113601, 2, 2, 1, 2, 1, "昼食のお誘いかい？\nいいよ、付き合おう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4309, 113601, 2, 3, 1, 3, 1, "もう夜か…\n一日が経つのは\nはやいものだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4311, 113601, 3, 2, 1, 0, 1, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4312, 113601, 3, 3, 1, 0, 1, "うん…ふわふわの\n感触が\nよくわかるな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4313, 113601, 3, 4, 1, 0, 1, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4314, 113601, 3, 5, 1, 0, 1, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4316, 113601, 3, 7, 1, 0, 1, "兜を気にせず\n食べられるのは\n楽でいいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4317, 113601, 3, 8, 1, 0, 1, "ふむ…鎧がないと\nページがめくりやすい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4319, 113601, 3, 10, 1, 0, 1, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4320, 113601, 3, 11, 1, 0, 1, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4321, 106801, 1, 1, 1, 0, 1, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4322, 106801, 1, 2, 1, 0, 1, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4323, 106801, 1, 3, 1, 0, 1, "色んな人を観察できて\n眼福眼福…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4324, 106801, 1, 4, 1, 0, 1, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4325, 106801, 1, 5, 1, 0, 1, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4326, 106801, 1, 101, 4, 0, 1, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4327, 106801, 1, 102, 8, 0, 1, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4328, 106801, 2, 1, 1, 0, 1, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4329, 106801, 2, 2, 1, 0, 1, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4330, 106801, 2, 3, 1, 0, 1, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4331, 106801, 2, 4, 1, 0, 1, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4332, 106801, 2, 101, 4, 0, 1, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4333, 106801, 2, 102, 8, 0, 1, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4334, 106801, 2, 1, 1, 1, 1, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4335, 106801, 2, 2, 1, 2, 1, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4336, 106801, 2, 3, 1, 3, 1, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4338, 106801, 3, 2, 1, 0, 1, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4339, 106801, 3, 3, 1, 0, 1, "…これ\nネネカが変身\nしてないよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4340, 106801, 3, 4, 1, 0, 1, "いい香りだ…\n解析完了\n今度生成しようっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4341, 106801, 3, 5, 1, 0, 1, "安心するな～…\nアジトに置けないから\n羨ましいよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4343, 106801, 3, 7, 1, 0, 1, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4344, 106801, 3, 8, 1, 0, 1, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4346, 113701, 1, 1, 1, 0, 1, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4347, 113701, 1, 2, 1, 0, 1, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4348, 113701, 1, 3, 1, 0, 1, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4349, 113701, 1, 4, 1, 0, 1, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4350, 113701, 1, 5, 1, 0, 1, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4351, 113701, 1, 101, 4, 0, 1, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4352, 113701, 1, 102, 8, 0, 1, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4353, 113701, 2, 1, 1, 0, 1, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4354, 113701, 2, 2, 1, 0, 1, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4355, 113701, 2, 3, 1, 0, 1, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4356, 113701, 2, 4, 1, 0, 1, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4357, 113701, 2, 101, 4, 0, 1, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4358, 113701, 2, 102, 8, 0, 1, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4359, 113701, 2, 1, 1, 1, 1, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4360, 113701, 2, 2, 1, 2, 1, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4361, 113701, 2, 3, 1, 3, 1, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4363, 113701, 3, 2, 1, 0, 1, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4364, 113701, 3, 3, 1, 0, 1, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4365, 113701, 3, 4, 1, 0, 1, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4366, 113701, 3, 5, 1, 0, 1, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4368, 113701, 3, 7, 1, 0, 1, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4369, 113701, 3, 8, 1, 0, 1, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4371, 103201, 1, 103, 12, 0, 1, "あの方の前では\n常に気高く\nありたいですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4372, 103201, 2, 103, 12, 0, 1, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4373, 102801, 1, 103, 12, 0, 1, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4374, 102801, 2, 103, 12, 0, 1, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4375, 113801, 1, 1, 1, 0, 1, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4376, 113801, 1, 2, 1, 0, 1, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4377, 113801, 1, 3, 1, 0, 1, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4378, 113801, 1, 4, 1, 0, 1, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4379, 113801, 1, 5, 1, 0, 1, "エンジェルになると\n人見知りがなおる！\n…わけないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4380, 113801, 1, 101, 4, 0, 1, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4381, 113801, 1, 102, 8, 0, 1, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4382, 113801, 2, 1, 1, 0, 1, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4383, 113801, 2, 2, 1, 0, 1, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4384, 113801, 2, 3, 1, 0, 1, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4385, 113801, 2, 4, 1, 0, 1, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4386, 113801, 2, 101, 4, 0, 1, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4387, 113801, 2, 102, 8, 0, 1, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4388, 113801, 2, 1, 1, 1, 1, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4389, 113801, 2, 2, 1, 2, 1, "ふぅ、いいことした\n後のランチは\nおいしいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4390, 113801, 2, 3, 1, 3, 1, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4392, 113801, 3, 2, 1, 0, 1, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4393, 113801, 3, 3, 1, 0, 1, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4394, 113801, 3, 4, 1, 0, 1, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4395, 113801, 3, 5, 1, 0, 1, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4397, 113801, 3, 7, 1, 0, 1, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4398, 113801, 3, 8, 1, 0, 1, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4400, 113901, 1, 1, 1, 0, 1, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4401, 113901, 1, 2, 1, 0, 1, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4402, 113901, 1, 3, 1, 0, 1, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4403, 113901, 1, 4, 1, 0, 1, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4404, 113901, 1, 5, 1, 0, 1, "マントは動きに\nメリハリがついて\nいいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4405, 113901, 1, 101, 4, 0, 1, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4406, 113901, 1, 102, 8, 0, 1, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4407, 113901, 2, 1, 1, 0, 1, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4408, 113901, 2, 2, 1, 0, 1, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4409, 113901, 2, 3, 1, 0, 1, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4410, 113901, 2, 4, 1, 0, 1, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4411, 113901, 2, 101, 4, 0, 1, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4412, 113901, 2, 102, 8, 0, 1, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4413, 113901, 2, 1, 1, 1, 1, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4414, 113901, 2, 2, 1, 2, 1, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4415, 113901, 2, 3, 1, 3, 1, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4417, 113901, 3, 2, 1, 0, 1, "このフィット感\nどうにか服作りにも\n活かせれば…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4418, 113901, 3, 3, 1, 0, 1, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4419, 113901, 3, 4, 1, 0, 1, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4420, 113901, 3, 5, 1, 0, 1, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4422, 113901, 3, 7, 1, 0, 1, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4423, 113901, 3, 8, 1, 0, 1, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4425, 113901, 3, 12, 1, 0, 1, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4427, 114101, 1, 1, 1, 0, 1, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4428, 114101, 1, 2, 1, 0, 1, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4429, 114101, 1, 3, 1, 0, 1, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4430, 114101, 1, 4, 1, 0, 1, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4431, 114101, 1, 5, 1, 0, 1, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4432, 114101, 1, 101, 4, 0, 1, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4433, 114101, 1, 102, 8, 0, 1, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4434, 114101, 2, 1, 1, 0, 1, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4435, 114101, 2, 2, 1, 0, 1, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4436, 114101, 2, 3, 1, 0, 1, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4437, 114101, 2, 4, 1, 0, 1, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4438, 114101, 2, 101, 4, 0, 1, "あんたも一緒に\n新しい技の練習\nどうッスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4439, 114101, 2, 102, 8, 0, 1, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4440, 114101, 2, 1, 1, 1, 1, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4441, 114101, 2, 2, 1, 2, 1, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4442, 114101, 2, 3, 1, 3, 1, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4444, 114101, 3, 2, 1, 0, 1, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4445, 114101, 3, 3, 1, 0, 1, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4446, 114101, 3, 4, 1, 0, 1, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4447, 114101, 3, 5, 1, 0, 1, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4449, 114101, 3, 7, 1, 0, 1, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4450, 114101, 3, 8, 1, 0, 1, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4452, 114101, 3, 12, 1, 0, 1, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4454, 103001, 1, 103, 12, 0, 1, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4455, 103001, 2, 103, 12, 0, 1, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4456, 114001, 1, 1, 1, 0, 1, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4457, 114001, 1, 2, 1, 0, 1, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4458, 114001, 1, 3, 1, 0, 1, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4459, 114001, 1, 4, 1, 0, 1, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4460, 114001, 1, 5, 1, 0, 1, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4461, 114001, 1, 101, 4, 0, 1, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4462, 114001, 1, 102, 8, 0, 1, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4463, 114001, 2, 1, 1, 0, 1, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4464, 114001, 2, 2, 1, 0, 1, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4465, 114001, 2, 3, 1, 0, 1, "どうしたの？\n出かけるなら\n私も同行しようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4466, 114001, 2, 4, 1, 0, 1, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4467, 114001, 2, 101, 4, 0, 1, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4468, 114001, 2, 102, 8, 0, 1, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4469, 114001, 2, 1, 1, 1, 1, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4470, 114001, 2, 2, 1, 2, 1, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4471, 114001, 2, 3, 1, 3, 1, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4473, 114001, 3, 2, 1, 0, 1, "ハロウィンはまだ\nこれからだ\n一休みしようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4474, 114001, 3, 3, 1, 0, 1, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4475, 114001, 3, 4, 1, 0, 1, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4476, 114001, 3, 5, 1, 0, 1, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4478, 114001, 3, 7, 1, 0, 1, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4479, 114001, 3, 8, 1, 0, 1, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4481, 114001, 3, 12, 1, 0, 1, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4483, 114201, 1, 1, 1, 0, 1, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4484, 114201, 1, 2, 1, 0, 1, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4485, 114201, 1, 3, 1, 0, 1, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4486, 114201, 1, 4, 1, 0, 1, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4487, 114201, 1, 5, 1, 0, 1, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4488, 114201, 1, 101, 4, 0, 1, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4489, 114201, 1, 102, 8, 0, 1, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4490, 114201, 2, 1, 1, 0, 1, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4491, 114201, 2, 2, 1, 0, 1, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4492, 114201, 2, 3, 1, 0, 1, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4493, 114201, 2, 4, 1, 0, 1, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4494, 114201, 2, 101, 4, 0, 1, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4495, 114201, 2, 102, 8, 0, 1, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4496, 114201, 2, 1, 1, 1, 1, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4497, 114201, 2, 2, 1, 2, 1, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4498, 114201, 2, 3, 1, 3, 1, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4500, 114201, 3, 2, 1, 0, 1, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4501, 114201, 3, 3, 1, 0, 1, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4502, 114201, 3, 4, 1, 0, 1, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4503, 114201, 3, 5, 1, 0, 1, "この温かさ…\n愛と希望に\n通じるものがある…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4505, 114201, 3, 7, 1, 0, 1, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4506, 114201, 3, 8, 1, 0, 1, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4508, 103301, 1, 103, 12, 0, 1, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4509, 103301, 2, 103, 12, 0, 1, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4510, 192501, 1, 1, 1, 0, 1, "ぴっぷる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4511, 192501, 1, 2, 1, 0, 1, "ぴっぷるぴっぷる♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4512, 192501, 1, 3, 1, 0, 1, "ぴっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4513, 192501, 1, 4, 1, 0, 1, "ぷっぷー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4514, 192501, 1, 5, 1, 0, 1, "ぴぃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4515, 192501, 2, 1, 1, 0, 1, "ぴっぷるんるん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4516, 192501, 2, 2, 1, 0, 1, "ぷぅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4517, 192501, 2, 3, 1, 0, 1, "ぴぴっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4518, 192501, 2, 4, 1, 0, 1, "ぷぅー…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4519, 192501, 2, 1, 1, 1, 1, "ちゅ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4520, 192501, 2, 2, 1, 2, 1, "ぷはー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4521, 192501, 2, 3, 1, 3, 1, "ぴぇー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4531, 114301, 1, 1, 1, 0, 1, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4532, 114301, 1, 2, 1, 0, 1, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4533, 114301, 1, 3, 1, 0, 1, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4534, 114301, 1, 4, 1, 0, 1, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4535, 114301, 1, 5, 1, 0, 1, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4536, 114301, 1, 101, 4, 0, 1, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4537, 114301, 1, 102, 8, 0, 1, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4538, 114301, 2, 1, 1, 0, 1, "もし時間があるなら、\n私と街の見回りに\n行かない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4539, 114301, 2, 2, 1, 0, 1, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4540, 114301, 2, 3, 1, 0, 1, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4541, 114301, 2, 4, 1, 0, 1, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4542, 114301, 2, 101, 4, 0, 1, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4543, 114301, 2, 102, 8, 0, 1, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4544, 114301, 2, 1, 1, 1, 1, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4545, 114301, 2, 2, 1, 2, 1, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4546, 114301, 2, 3, 1, 3, 1, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4548, 114301, 3, 2, 1, 0, 1, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4549, 114301, 3, 3, 1, 0, 1, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4550, 114301, 3, 4, 1, 0, 1, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4551, 114301, 3, 5, 1, 0, 1, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4553, 114301, 3, 7, 1, 0, 1, "こ、これは…\nおいしすぎて手が\n止まらないかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4554, 114301, 3, 8, 1, 0, 1, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4556, 114401, 1, 1, 1, 0, 1, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4557, 114401, 1, 2, 1, 0, 1, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4558, 114401, 1, 3, 1, 0, 1, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4559, 114401, 1, 4, 1, 0, 1, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4560, 114401, 1, 5, 1, 0, 1, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4561, 114401, 1, 101, 4, 0, 1, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4562, 114401, 1, 102, 8, 0, 1, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4563, 114401, 2, 1, 1, 0, 1, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4564, 114401, 2, 2, 1, 0, 1, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4565, 114401, 2, 3, 1, 0, 1, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4566, 114401, 2, 4, 1, 0, 1, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4567, 114401, 2, 101, 4, 0, 1, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4568, 114401, 2, 102, 8, 0, 1, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4569, 114401, 2, 1, 1, 1, 1, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4570, 114401, 2, 2, 1, 2, 1, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4571, 114401, 2, 3, 1, 3, 1, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4573, 114401, 3, 2, 1, 0, 1, "適度な休息は\n成功の秘訣ですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4574, 114401, 3, 3, 1, 0, 1, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4575, 114401, 3, 4, 1, 0, 1, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4576, 114401, 3, 5, 1, 0, 1, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4578, 114401, 3, 7, 1, 0, 1, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4579, 114401, 3, 8, 1, 0, 1, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4581, 114401, 3, 14, 1, 0, 1, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4582, 114401, 3, 15, 1, 0, 1, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4583, 114601, 1, 1, 1, 0, 1, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4584, 114601, 1, 2, 1, 0, 1, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4585, 114601, 1, 3, 1, 0, 1, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4586, 114601, 1, 4, 1, 0, 1, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4587, 114601, 1, 5, 1, 0, 1, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4588, 114601, 1, 101, 4, 0, 1, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4589, 114601, 1, 102, 8, 0, 1, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4590, 114601, 2, 1, 1, 0, 1, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4591, 114601, 2, 2, 1, 0, 1, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4592, 114601, 2, 3, 1, 0, 1, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4593, 114601, 2, 4, 1, 0, 1, "お姉さんの\n頼れるところ\n見せてあげるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4594, 114601, 2, 101, 4, 0, 1, "何かあったら\n頼ってくれても\nいいんだからね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4595, 114601, 2, 102, 8, 0, 1, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4596, 114601, 2, 1, 1, 1, 1, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4597, 114601, 2, 2, 1, 2, 1, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4598, 114601, 2, 3, 1, 3, 1, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4600, 114601, 3, 2, 1, 0, 1, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4601, 114601, 3, 3, 1, 0, 1, "私だって…\nかわいいものは\n好きでいいわよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4602, 114601, 3, 4, 1, 0, 1, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4603, 114601, 3, 5, 1, 0, 1, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4605, 114601, 3, 7, 1, 0, 1, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4606, 114601, 3, 8, 1, 0, 1, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4608, 114601, 3, 14, 1, 0, 1, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4609, 114601, 3, 15, 1, 0, 1, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4610, 101601, 1, 103, 12, 0, 1, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4611, 101601, 2, 103, 12, 0, 1, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4612, 114501, 1, 1, 1, 0, 1, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4613, 114501, 1, 2, 1, 0, 1, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4614, 114501, 1, 3, 1, 0, 1, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4615, 114501, 1, 4, 1, 0, 1, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4616, 114501, 1, 5, 1, 0, 1, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4617, 114501, 1, 101, 4, 0, 1, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4618, 114501, 1, 102, 8, 0, 1, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4619, 114501, 2, 1, 1, 0, 1, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4620, 114501, 2, 2, 1, 0, 1, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4621, 114501, 2, 3, 1, 0, 1, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4622, 114501, 2, 4, 1, 0, 1, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4623, 114501, 2, 101, 4, 0, 1, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4624, 114501, 2, 102, 8, 0, 1, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4625, 114501, 2, 1, 1, 1, 1, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4626, 114501, 2, 2, 1, 2, 1, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4627, 114501, 2, 3, 1, 3, 1, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4629, 114501, 3, 2, 1, 0, 1, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4630, 114501, 3, 3, 1, 0, 1, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4631, 114501, 3, 4, 1, 0, 1, "華やかな香りね\nクリスマスに\nピッタリだわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4632, 114501, 3, 5, 1, 0, 1, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4634, 114501, 3, 7, 1, 0, 1, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4635, 114501, 3, 8, 1, 0, 1, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4637, 114501, 3, 14, 1, 0, 1, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4638, 114501, 3, 15, 1, 0, 1, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4639, 114701, 1, 1, 1, 0, 1, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4640, 114701, 1, 2, 1, 0, 1, "今年こそ、もう少し\n身長が伸びます\nように…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4641, 114701, 1, 3, 1, 0, 1, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4642, 114701, 1, 4, 1, 0, 1, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4643, 114701, 1, 5, 1, 0, 1, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4644, 114701, 1, 101, 4, 0, 1, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4645, 114701, 1, 102, 8, 0, 1, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4646, 114701, 2, 1, 1, 0, 1, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4647, 114701, 2, 2, 1, 0, 1, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4648, 114701, 2, 3, 1, 0, 1, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4649, 114701, 2, 4, 1, 0, 1, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4650, 114701, 2, 101, 4, 0, 1, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4651, 114701, 2, 102, 8, 0, 1, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4652, 114701, 2, 1, 1, 1, 1, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4653, 114701, 2, 2, 1, 2, 1, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4654, 114701, 2, 3, 1, 3, 1, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4656, 114701, 3, 2, 1, 0, 1, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4657, 114701, 3, 3, 1, 0, 1, "なんだ？\nオマエも初詣に\n行きたいのか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4658, 114701, 3, 4, 1, 0, 1, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4659, 114701, 3, 5, 1, 0, 1, "コイツがあれば\nうまい魚が\n焼けそうだな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4661, 114701, 3, 7, 1, 0, 1, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4662, 114701, 3, 8, 1, 0, 1, "おみくじって\n書いてあることが\nむずかしいんだよな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4664, 180101, 1, 1, 1, 0, 1, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4665, 180101, 1, 2, 1, 0, 1, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4666, 180101, 1, 3, 1, 0, 1, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4667, 180101, 1, 4, 1, 0, 1, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4668, 180101, 1, 5, 1, 0, 1, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4669, 180101, 1, 101, 4, 0, 1, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4670, 180101, 1, 102, 8, 0, 1, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4671, 180101, 2, 1, 1, 0, 1, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4672, 180101, 2, 2, 1, 0, 1, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4673, 180101, 2, 3, 1, 0, 1, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4674, 180101, 2, 4, 1, 0, 1, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4675, 180101, 2, 101, 4, 0, 1, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4676, 180101, 2, 102, 8, 0, 1, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4677, 180101, 2, 1, 1, 1, 1, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4678, 180101, 2, 2, 1, 2, 1, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4679, 180101, 2, 3, 1, 3, 1, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4681, 180101, 3, 2, 1, 0, 1, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4682, 180101, 3, 3, 1, 0, 1, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4683, 180101, 3, 4, 1, 0, 1, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4684, 180101, 3, 5, 1, 0, 1, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4686, 180101, 3, 7, 1, 0, 1, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4687, 180101, 3, 8, 1, 0, 1, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4689, 111801, 1, 1, 1, 0, 1, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4690, 111801, 1, 2, 1, 0, 1, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4691, 111801, 1, 3, 1, 0, 1, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4692, 111801, 1, 4, 1, 0, 1, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4693, 111801, 1, 5, 1, 0, 1, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4694, 111801, 1, 101, 4, 0, 1, "ずっとこうして\n年の始まりを\n過ごしたいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4695, 111801, 1, 102, 8, 0, 1, "いつか二人きりで\n初日の出を\n見たいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4696, 111801, 2, 1, 1, 0, 1, "お餅おかわりです！\nいくらでも\n食べられますよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4697, 111801, 2, 2, 1, 0, 1, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4698, 111801, 2, 3, 1, 0, 1, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4699, 111801, 2, 4, 1, 0, 1, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4700, 111801, 2, 101, 4, 0, 1, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4701, 111801, 2, 102, 8, 0, 1, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4702, 111801, 2, 1, 1, 1, 1, "年明けの朝は\n心なしか\n気持ちいいですね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4703, 111801, 2, 2, 1, 2, 1, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4704, 111801, 2, 3, 1, 3, 1, "あたたかくして\n幸せな初夢を\n見ましょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4706, 111801, 3, 2, 1, 0, 1, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4707, 111801, 3, 3, 1, 0, 1, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4708, 111801, 3, 4, 1, 0, 1, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4709, 111801, 3, 5, 1, 0, 1, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4711, 111801, 3, 7, 1, 0, 1, "おいしすぎて\n食べる手が\n止まりません～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4712, 111801, 3, 8, 1, 0, 1, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4714, 100601, 1, 103, 12, 0, 1, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4715, 100601, 2, 103, 12, 0, 1, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4716, 115001, 1, 1, 1, 0, 1, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4717, 115001, 1, 2, 1, 0, 1, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4718, 115001, 1, 3, 1, 0, 1, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4719, 115001, 1, 4, 1, 0, 1, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4720, 115001, 1, 5, 1, 0, 1, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4721, 115001, 1, 101, 4, 0, 1, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4722, 115001, 1, 102, 8, 0, 1, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4723, 115001, 2, 1, 1, 0, 1, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4724, 115001, 2, 2, 1, 0, 1, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4725, 115001, 2, 3, 1, 0, 1, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4726, 115001, 2, 4, 1, 0, 1, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4727, 115001, 2, 101, 4, 0, 1, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4728, 115001, 2, 102, 8, 0, 1, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4729, 115001, 2, 1, 1, 1, 1, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4730, 115001, 2, 2, 1, 2, 1, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4731, 115001, 2, 3, 1, 3, 1, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4733, 115001, 3, 2, 1, 0, 1, "少し休みます…\n時間が来たら\n起こすように", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4734, 115001, 3, 3, 1, 0, 1, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4735, 115001, 3, 4, 1, 0, 1, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4736, 115001, 3, 5, 1, 0, 1, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4738, 115001, 3, 7, 1, 0, 1, "いかなる強者でも\n兵站、補給は\n必要なものです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4739, 115001, 3, 8, 1, 0, 1, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4741, 115501, 1, 1, 1, 0, 1, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4742, 115501, 1, 2, 1, 0, 1, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4743, 115501, 1, 3, 1, 0, 1, "主さまも精霊の召喚を\n試してみますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4744, 115501, 1, 4, 1, 0, 1, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4745, 115501, 1, 5, 1, 0, 1, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4746, 115501, 1, 101, 4, 0, 1, "進化した精霊に\n負けぬよう\n修行に励みます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4747, 115501, 1, 102, 8, 0, 1, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4748, 115501, 2, 1, 1, 0, 1, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4749, 115501, 2, 2, 1, 0, 1, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4750, 115501, 2, 3, 1, 0, 1, "里で採れた果物を\n持って参りました\n召し上がってください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4751, 115501, 2, 4, 1, 0, 1, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4752, 115501, 2, 101, 4, 0, 1, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4753, 115501, 2, 102, 8, 0, 1, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4754, 115501, 2, 1, 1, 1, 1, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4755, 115501, 2, 2, 1, 2, 1, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4756, 115501, 2, 3, 1, 3, 1, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4758, 115501, 3, 2, 1, 0, 1, "この癒し…\n主さまにも味わって\nいただきたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4759, 115501, 3, 3, 1, 0, 1, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4760, 115501, 3, 4, 1, 0, 1, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4761, 115501, 3, 5, 1, 0, 1, "瞑想するのに\nちょうどよい暖かさで\nございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4763, 115501, 3, 7, 1, 0, 1, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4764, 115501, 3, 8, 1, 0, 1, "風の精霊について\nもっと詳しく知って\nおかなければ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4766, 115601, 1, 1, 1, 0, 1, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4767, 115601, 1, 2, 1, 0, 1, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4768, 115601, 1, 3, 1, 0, 1, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4769, 115601, 1, 4, 1, 0, 1, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4770, 115601, 1, 5, 1, 0, 1, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4771, 115601, 1, 101, 4, 0, 1, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4772, 115601, 1, 102, 8, 0, 1, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4773, 115601, 2, 1, 1, 0, 1, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4774, 115601, 2, 2, 1, 0, 1, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4775, 115601, 2, 3, 1, 0, 1, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4776, 115601, 2, 4, 1, 0, 1, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4777, 115601, 2, 101, 4, 0, 1, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4778, 115601, 2, 102, 8, 0, 1, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4779, 115601, 2, 1, 1, 1, 1, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4780, 115601, 2, 2, 1, 2, 1, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4781, 115601, 2, 3, 1, 3, 1, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4783, 115601, 3, 2, 1, 0, 1, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4784, 115601, 3, 3, 1, 0, 1, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4785, 115601, 3, 4, 1, 0, 1, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4786, 115601, 3, 5, 1, 0, 1, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4788, 115601, 3, 7, 1, 0, 1, "自然の恵みに\n感謝して…\nいただきます♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4789, 115601, 3, 8, 1, 0, 1, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4791, 102201, 1, 103, 12, 0, 1, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4792, 102201, 2, 103, 12, 0, 1, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4793, 106401, 1, 1, 1, 0, 1, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4794, 106401, 1, 2, 1, 0, 1, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4795, 106401, 1, 3, 1, 0, 1, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4796, 106401, 1, 4, 1, 0, 1, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4797, 106401, 1, 5, 1, 0, 1, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4798, 106401, 1, 101, 4, 0, 1, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4799, 106401, 1, 102, 8, 0, 1, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4800, 106401, 2, 1, 1, 0, 1, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4801, 106401, 2, 2, 1, 0, 1, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4802, 106401, 2, 3, 1, 0, 1, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4803, 106401, 2, 4, 1, 0, 1, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4804, 106401, 2, 101, 4, 0, 1, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4805, 106401, 2, 102, 8, 0, 1, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4806, 106401, 2, 1, 1, 1, 1, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4807, 106401, 2, 2, 1, 2, 1, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4808, 106401, 2, 3, 1, 3, 1, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4810, 106401, 3, 2, 1, 0, 1, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4811, 106401, 3, 3, 1, 0, 1, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4812, 106401, 3, 4, 1, 0, 1, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4813, 106401, 3, 5, 1, 0, 1, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4815, 106401, 3, 7, 1, 0, 1, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4816, 106401, 3, 8, 1, 0, 1, "ん…なるほどね。\n勉強になるわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4818, 115701, 1, 1, 1, 0, 1, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4819, 115701, 1, 2, 1, 0, 1, "依頼があれば\n海でも山でも\n赴くさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4820, 115701, 1, 3, 1, 0, 1, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4821, 115701, 1, 4, 1, 0, 1, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4822, 115701, 1, 5, 1, 0, 1, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4823, 115701, 1, 101, 4, 0, 1, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4824, 115701, 1, 102, 8, 0, 1, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4825, 115701, 2, 1, 1, 0, 1, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4826, 115701, 2, 2, 1, 0, 1, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4827, 115701, 2, 3, 1, 0, 1, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4828, 115701, 2, 4, 1, 0, 1, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4829, 115701, 2, 101, 4, 0, 1, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4830, 115701, 2, 102, 8, 0, 1, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4831, 115701, 2, 1, 1, 1, 1, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4832, 115701, 2, 2, 1, 2, 1, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4833, 115701, 2, 3, 1, 3, 1, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4835, 115701, 3, 2, 1, 0, 1, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4836, 115701, 3, 3, 1, 0, 1, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4837, 115701, 3, 4, 1, 0, 1, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4838, 115701, 3, 5, 1, 0, 1, "ポカポカするね…\n助手くん\n君もおいでよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4840, 115701, 3, 7, 1, 0, 1, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4841, 115701, 3, 8, 1, 0, 1, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4843, 115701, 3, 10, 1, 0, 1, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4844, 115701, 3, 11, 1, 0, 1, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4845, 180601, 1, 1, 1, 0, 1, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4846, 180601, 1, 2, 1, 0, 1, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4847, 180601, 1, 3, 1, 0, 1, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4848, 180601, 1, 4, 1, 0, 1, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4849, 180601, 1, 5, 1, 0, 1, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4850, 180601, 1, 101, 4, 0, 1, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4851, 180601, 1, 102, 8, 0, 1, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4852, 180601, 2, 1, 1, 0, 1, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4853, 180601, 2, 2, 1, 0, 1, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4854, 180601, 2, 3, 1, 0, 1, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4855, 180601, 2, 4, 1, 0, 1, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4856, 180601, 2, 101, 4, 0, 1, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4857, 180601, 2, 102, 8, 0, 1, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4858, 180601, 2, 1, 1, 1, 1, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4859, 180601, 2, 2, 1, 2, 1, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4860, 180601, 2, 3, 1, 3, 1, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4862, 180601, 3, 2, 1, 0, 1, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4863, 180601, 3, 3, 1, 0, 1, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4864, 180601, 3, 4, 1, 0, 1, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4865, 180601, 3, 5, 1, 0, 1, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4867, 180601, 3, 7, 1, 0, 1, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4868, 180601, 3, 8, 1, 0, 1, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4870, 115801, 1, 1, 1, 0, 1, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4871, 115801, 1, 2, 1, 0, 1, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4872, 115801, 1, 3, 1, 0, 1, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4873, 115801, 1, 4, 1, 0, 1, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4874, 115801, 1, 5, 1, 0, 1, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4875, 115801, 1, 101, 4, 0, 1, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4876, 115801, 1, 102, 8, 0, 1, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4877, 115801, 2, 1, 1, 0, 1, "どうかしら？\n私、きちんとドレス\n着られてる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4878, 115801, 2, 2, 1, 0, 1, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4879, 115801, 2, 3, 1, 0, 1, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4880, 115801, 2, 4, 1, 0, 1, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4881, 115801, 2, 101, 4, 0, 1, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4882, 115801, 2, 102, 8, 0, 1, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4883, 115801, 2, 1, 1, 1, 1, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4884, 115801, 2, 2, 1, 2, 1, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4885, 115801, 2, 3, 1, 3, 1, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4887, 115801, 3, 2, 1, 0, 1, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4888, 115801, 3, 3, 1, 0, 1, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4889, 115801, 3, 4, 1, 0, 1, "すてきな香り…\nお花かしら？\n森で探してみようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4890, 115801, 3, 5, 1, 0, 1, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4892, 115801, 3, 7, 1, 0, 1, "レッスンのために\n栄養補給して\nおかないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4893, 115801, 3, 8, 1, 0, 1, "教養を身に着ける\nことも大切よね！\nなになに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4895, 115901, 1, 1, 1, 0, 1, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4896, 115901, 1, 2, 1, 0, 1, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4897, 115901, 1, 3, 1, 0, 1, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4898, 115901, 1, 4, 1, 0, 1, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4899, 115901, 1, 5, 1, 0, 1, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4900, 115901, 1, 101, 4, 0, 1, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4901, 115901, 1, 102, 8, 0, 1, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4902, 115901, 2, 1, 1, 0, 1, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4903, 115901, 2, 2, 1, 0, 1, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4904, 115901, 2, 3, 1, 0, 1, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4905, 115901, 2, 4, 1, 0, 1, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4906, 115901, 2, 101, 4, 0, 1, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4907, 115901, 2, 102, 8, 0, 1, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4908, 115901, 2, 1, 1, 1, 1, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4909, 115901, 2, 2, 1, 2, 1, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4910, 115901, 2, 3, 1, 3, 1, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4912, 115901, 3, 2, 1, 0, 1, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4913, 115901, 3, 3, 1, 0, 1, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4914, 115901, 3, 4, 1, 0, 1, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4915, 115901, 3, 5, 1, 0, 1, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4917, 115901, 3, 7, 1, 0, 1, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4918, 115901, 3, 8, 1, 0, 1, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4920, 102001, 1, 103, 12, 0, 1, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4921, 102001, 2, 103, 12, 0, 1, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4922, 100401, 1, 103, 12, 0, 1, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4923, 100401, 2, 103, 12, 0, 1, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4924, 103601, 1, 103, 12, 0, 1, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4925, 103601, 2, 103, 12, 0, 1, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4926, 116001, 1, 1, 1, 0, 1, "くるくる〜♪\nうちのドレス\nかわええなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4927, 116001, 1, 2, 1, 0, 1, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4928, 116001, 1, 3, 1, 0, 1, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4929, 116001, 1, 4, 1, 0, 1, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4930, 116001, 1, 5, 1, 0, 1, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4931, 116001, 1, 101, 4, 0, 1, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4932, 116001, 1, 102, 8, 0, 1, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4933, 116001, 2, 1, 1, 0, 1, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4934, 116001, 2, 2, 1, 0, 1, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4935, 116001, 2, 3, 1, 0, 1, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4936, 116001, 2, 4, 1, 0, 1, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4937, 116001, 2, 101, 4, 0, 1, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4938, 116001, 2, 102, 8, 0, 1, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4939, 116001, 2, 1, 1, 1, 1, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4940, 116001, 2, 2, 1, 2, 1, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4941, 116001, 2, 3, 1, 3, 1, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4943, 116001, 3, 2, 1, 0, 1, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4944, 116001, 3, 3, 1, 0, 1, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4945, 116001, 3, 4, 1, 0, 1, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4946, 116001, 3, 5, 1, 0, 1, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4948, 116001, 3, 7, 1, 0, 1, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4949, 116001, 3, 8, 1, 0, 1, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4951, 116301, 1, 1, 1, 0, 1, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4952, 116301, 1, 2, 1, 0, 1, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4953, 116301, 1, 3, 1, 0, 1, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4954, 116301, 1, 4, 1, 0, 1, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4955, 116301, 1, 5, 1, 0, 1, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4956, 116301, 1, 101, 4, 0, 1, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4957, 116301, 1, 102, 8, 0, 1, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4958, 116301, 2, 1, 1, 0, 1, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4959, 116301, 2, 2, 1, 0, 1, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4960, 116301, 2, 3, 1, 0, 1, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4961, 116301, 2, 4, 1, 0, 1, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4962, 116301, 2, 101, 4, 0, 1, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4963, 116301, 2, 102, 8, 0, 1, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4964, 116301, 2, 1, 1, 1, 1, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4965, 116301, 2, 2, 1, 2, 1, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4966, 116301, 2, 3, 1, 3, 1, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4968, 116301, 3, 2, 1, 0, 1, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4969, 116301, 3, 3, 1, 0, 1, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4970, 116301, 3, 4, 1, 0, 1, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4971, 116301, 3, 5, 1, 0, 1, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4973, 116301, 3, 7, 1, 0, 1, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4974, 116301, 3, 8, 1, 0, 1, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4976, 102901, 1, 103, 12, 0, 1, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4977, 102901, 2, 103, 12, 0, 1, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4978, 116201, 1, 1, 1, 0, 1, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4979, 116201, 1, 2, 1, 0, 1, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4980, 116201, 1, 3, 1, 0, 1, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4981, 116201, 1, 4, 1, 0, 1, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4982, 116201, 1, 5, 1, 0, 1, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4983, 116201, 1, 101, 4, 0, 1, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4984, 116201, 1, 102, 8, 0, 1, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4985, 116201, 2, 1, 1, 0, 1, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4986, 116201, 2, 2, 1, 0, 1, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4987, 116201, 2, 3, 1, 0, 1, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4988, 116201, 2, 4, 1, 0, 1, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4989, 116201, 2, 101, 4, 0, 1, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4990, 116201, 2, 102, 8, 0, 1, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4991, 116201, 2, 1, 1, 1, 1, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4992, 116201, 2, 2, 1, 2, 1, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4993, 116201, 2, 3, 1, 3, 1, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4995, 116201, 3, 2, 1, 0, 1, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4996, 116201, 3, 3, 1, 0, 1, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4997, 116201, 3, 4, 1, 0, 1, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (4998, 116201, 3, 5, 1, 0, 1, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5000, 116201, 3, 7, 1, 0, 1, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5001, 116201, 3, 8, 1, 0, 1, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5003, 116501, 1, 1, 1, 0, 1, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5004, 116501, 1, 2, 1, 0, 1, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5005, 116501, 1, 3, 1, 0, 1, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5006, 116501, 1, 4, 1, 0, 1, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5007, 116501, 1, 5, 1, 0, 1, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5008, 116501, 1, 101, 4, 0, 1, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5009, 116501, 1, 102, 8, 0, 1, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5010, 116501, 2, 1, 1, 0, 1, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5011, 116501, 2, 2, 1, 0, 1, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5012, 116501, 2, 3, 1, 0, 1, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5013, 116501, 2, 4, 1, 0, 1, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5014, 116501, 2, 101, 4, 0, 1, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5015, 116501, 2, 102, 8, 0, 1, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5016, 116501, 2, 1, 1, 1, 1, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5017, 116501, 2, 2, 1, 2, 1, "お腹が空きました…\nご飯食べにいきますよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5018, 116501, 2, 3, 1, 3, 1, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5020, 116501, 3, 2, 1, 0, 1, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5021, 116501, 3, 3, 1, 0, 1, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5022, 116501, 3, 4, 1, 0, 1, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5023, 116501, 3, 5, 1, 0, 1, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5025, 116501, 3, 7, 1, 0, 1, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5026, 116501, 3, 8, 1, 0, 1, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5028, 102501, 1, 103, 12, 0, 1, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5029, 102501, 2, 103, 12, 0, 1, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5030, 116601, 1, 1, 1, 0, 1, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5031, 116601, 1, 2, 1, 0, 1, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5032, 116601, 1, 3, 1, 0, 1, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5033, 116601, 1, 4, 1, 0, 1, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5034, 116601, 1, 5, 1, 0, 1, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5035, 116601, 1, 101, 4, 0, 1, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5036, 116601, 1, 102, 8, 0, 1, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5037, 116601, 2, 1, 1, 0, 1, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5038, 116601, 2, 2, 1, 0, 1, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5039, 116601, 2, 3, 1, 0, 1, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5040, 116601, 2, 4, 1, 0, 1, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5041, 116601, 2, 101, 4, 0, 1, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5042, 116601, 2, 102, 8, 0, 1, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5043, 116601, 2, 1, 1, 1, 1, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5044, 116601, 2, 2, 1, 2, 1, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5045, 116601, 2, 3, 1, 3, 1, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5047, 116601, 3, 2, 1, 0, 1, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5048, 116601, 3, 3, 1, 0, 1, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5049, 116601, 3, 4, 1, 0, 1, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5050, 116601, 3, 5, 1, 0, 1, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5052, 116601, 3, 7, 1, 0, 1, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5053, 116601, 3, 8, 1, 0, 1, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5055, 116701, 1, 1, 1, 0, 1, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5056, 116701, 1, 2, 1, 0, 1, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5057, 116701, 1, 3, 1, 0, 1, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5058, 116701, 1, 4, 1, 0, 1, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5059, 116701, 1, 5, 1, 0, 1, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5060, 116701, 1, 101, 4, 0, 1, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5061, 116701, 1, 102, 8, 0, 1, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5062, 116701, 2, 1, 1, 0, 1, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5063, 116701, 2, 2, 1, 0, 1, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5064, 116701, 2, 3, 1, 0, 1, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5065, 116701, 2, 4, 1, 0, 1, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5066, 116701, 2, 101, 4, 0, 1, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5067, 116701, 2, 102, 8, 0, 1, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5068, 116701, 2, 1, 1, 1, 1, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5069, 116701, 2, 2, 1, 2, 1, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5070, 116701, 2, 3, 1, 3, 1, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5072, 116701, 3, 2, 1, 0, 1, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5073, 116701, 3, 3, 1, 0, 1, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5074, 116701, 3, 4, 1, 0, 1, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5075, 116701, 3, 5, 1, 0, 1, "ぽかぽか…\n幸せなひとときですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5077, 116701, 3, 7, 1, 0, 1, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5078, 116701, 3, 8, 1, 0, 1, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5080, 116801, 1, 1, 1, 0, 1, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5081, 116801, 1, 2, 1, 0, 1, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5082, 116801, 1, 3, 1, 0, 1, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5083, 116801, 1, 4, 1, 0, 1, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5084, 116801, 1, 5, 1, 0, 1, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5085, 116801, 1, 101, 4, 0, 1, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5086, 116801, 1, 102, 8, 0, 1, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5087, 116801, 2, 1, 1, 0, 1, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5088, 116801, 2, 2, 1, 0, 1, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5089, 116801, 2, 3, 1, 0, 1, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5090, 116801, 2, 4, 1, 0, 1, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5091, 116801, 2, 101, 4, 0, 1, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5092, 116801, 2, 102, 8, 0, 1, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5093, 116801, 2, 1, 1, 1, 1, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5094, 116801, 2, 2, 1, 2, 1, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5095, 116801, 2, 3, 1, 3, 1, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5097, 116801, 3, 2, 1, 0, 1, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5098, 116801, 3, 3, 1, 0, 1, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5099, 116801, 3, 4, 1, 0, 1, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5100, 116801, 3, 5, 1, 0, 1, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5102, 116801, 3, 7, 1, 0, 1, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5103, 116801, 3, 8, 1, 0, 1, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5105, 102101, 1, 103, 12, 0, 1, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5106, 102101, 2, 103, 12, 0, 1, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5107, 116901, 1, 1, 1, 0, 1, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5108, 116901, 1, 2, 1, 0, 1, "他のバイトとの\nスケジュールを\n調整しないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5109, 116901, 1, 3, 1, 0, 1, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5110, 116901, 1, 4, 1, 0, 1, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5111, 116901, 1, 5, 1, 0, 1, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5112, 116901, 1, 101, 4, 0, 1, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5113, 116901, 1, 102, 8, 0, 1, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5114, 116901, 2, 1, 1, 0, 1, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5115, 116901, 2, 2, 1, 0, 1, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5116, 116901, 2, 3, 1, 0, 1, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5117, 116901, 2, 4, 1, 0, 1, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5118, 116901, 2, 101, 4, 0, 1, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5119, 116901, 2, 102, 8, 0, 1, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5120, 116901, 2, 1, 1, 1, 1, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5121, 116901, 2, 2, 1, 2, 1, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5122, 116901, 2, 3, 1, 3, 1, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5124, 116901, 3, 2, 1, 0, 1, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5125, 116901, 3, 3, 1, 0, 1, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5126, 116901, 3, 4, 1, 0, 1, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5127, 116901, 3, 5, 1, 0, 1, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5129, 116901, 3, 7, 1, 0, 1, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5130, 116901, 3, 8, 1, 0, 1, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5132, 180301, 1, 1, 1, 0, 1, "変身している間に\n色々と試しておこう\nまずは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5133, 180301, 1, 2, 1, 0, 1, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5134, 180301, 1, 3, 1, 0, 1, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5135, 180301, 1, 4, 1, 0, 1, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5136, 180301, 1, 5, 1, 0, 1, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5137, 180301, 1, 101, 4, 0, 1, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5138, 180301, 1, 102, 8, 0, 1, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5139, 180301, 2, 1, 1, 0, 1, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5140, 180301, 2, 2, 1, 0, 1, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5141, 180301, 2, 3, 1, 0, 1, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5142, 180301, 2, 4, 1, 0, 1, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5143, 180301, 2, 101, 4, 0, 1, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5144, 180301, 2, 102, 8, 0, 1, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5145, 180301, 2, 1, 1, 1, 1, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5146, 180301, 2, 2, 1, 2, 1, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5147, 180301, 2, 3, 1, 3, 1, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5149, 180301, 3, 2, 1, 0, 1, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5150, 180301, 3, 3, 1, 0, 1, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5151, 180301, 3, 4, 1, 0, 1, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5152, 180301, 3, 5, 1, 0, 1, "ありがたいな\n少し足元が\n冷えていたところだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5154, 180301, 3, 7, 1, 0, 1, "これは…！\n持ち運べるように\n改良できないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5155, 180301, 3, 8, 1, 0, 1, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5157, 117001, 1, 1, 1, 0, 1, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5158, 117001, 1, 2, 1, 0, 1, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5159, 117001, 1, 3, 1, 0, 1, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5160, 117001, 1, 4, 1, 0, 1, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5161, 117001, 1, 5, 1, 0, 1, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5162, 117001, 1, 101, 4, 0, 1, "あのおかたの全てが\n早く私のものに\nなりますように…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5163, 117001, 1, 102, 8, 0, 1, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5164, 117001, 2, 1, 1, 0, 1, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5165, 117001, 2, 2, 1, 0, 1, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5166, 117001, 2, 3, 1, 0, 1, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5167, 117001, 2, 4, 1, 0, 1, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5168, 117001, 2, 101, 4, 0, 1, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5169, 117001, 2, 102, 8, 0, 1, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5170, 117001, 2, 1, 1, 1, 1, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5171, 117001, 2, 2, 1, 2, 1, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5172, 117001, 2, 3, 1, 3, 1, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5174, 117001, 3, 2, 1, 0, 1, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5175, 117001, 3, 3, 1, 0, 1, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5176, 117001, 3, 4, 1, 0, 1, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5177, 117001, 3, 5, 1, 0, 1, "あのおかたの\nぬくもりを\n思い出しますわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5179, 117001, 3, 7, 1, 0, 1, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5180, 117001, 3, 8, 1, 0, 1, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5182, 117001, 3, 10, 1, 0, 1, "二人乗りができると\nいいですわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5183, 117001, 3, 11, 1, 0, 1, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5184, 101701, 1, 103, 12, 0, 1, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5185, 101701, 2, 103, 12, 0, 1, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5186, 117101, 1, 1, 1, 0, 1, "この水着…\n一番に弟くんに\n見せたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5187, 117101, 1, 2, 1, 0, 1, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5188, 117101, 1, 3, 1, 0, 1, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5189, 117101, 1, 4, 1, 0, 1, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5190, 117101, 1, 5, 1, 0, 1, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5191, 117101, 1, 101, 4, 0, 1, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5192, 117101, 1, 102, 8, 0, 1, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5193, 117101, 2, 1, 1, 0, 1, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5194, 117101, 2, 2, 1, 0, 1, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5195, 117101, 2, 3, 1, 0, 1, "海で迷子に\nならないように\n手を繋ごうね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5196, 117101, 2, 4, 1, 0, 1, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5197, 117101, 2, 101, 4, 0, 1, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5198, 117101, 2, 102, 8, 0, 1, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5199, 117101, 2, 1, 1, 1, 1, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5200, 117101, 2, 2, 1, 2, 1, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5201, 117101, 2, 3, 1, 3, 1, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5203, 117101, 3, 2, 1, 0, 1, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5204, 117101, 3, 3, 1, 0, 1, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5205, 117101, 3, 4, 1, 0, 1, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5206, 117101, 3, 5, 1, 0, 1, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5208, 117101, 3, 7, 1, 0, 1, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5209, 117101, 3, 8, 1, 0, 1, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5211, 117101, 3, 10, 1, 0, 1, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5212, 117101, 3, 11, 1, 0, 1, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5213, 117201, 1, 1, 1, 0, 1, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5214, 117201, 1, 2, 1, 0, 1, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5215, 117201, 1, 3, 1, 0, 1, "とっておきの\nサマーソングを\nみんなに届けるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5216, 117201, 1, 4, 1, 0, 1, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5217, 117201, 1, 5, 1, 0, 1, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5218, 117201, 1, 101, 4, 0, 1, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5219, 117201, 1, 102, 8, 0, 1, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5220, 117201, 2, 1, 1, 0, 1, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5221, 117201, 2, 2, 1, 0, 1, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5222, 117201, 2, 3, 1, 0, 1, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5223, 117201, 2, 4, 1, 0, 1, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5224, 117201, 2, 101, 4, 0, 1, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5225, 117201, 2, 102, 8, 0, 1, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5226, 117201, 2, 1, 1, 1, 1, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5227, 117201, 2, 2, 1, 2, 1, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5228, 117201, 2, 3, 1, 3, 1, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5230, 117201, 3, 2, 1, 0, 1, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5231, 117201, 3, 3, 1, 0, 1, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5232, 117201, 3, 4, 1, 0, 1, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5233, 117201, 3, 5, 1, 0, 1, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5235, 117201, 3, 7, 1, 0, 1, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5236, 117201, 3, 8, 1, 0, 1, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5238, 117201, 3, 10, 1, 0, 1, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5239, 117201, 3, 11, 1, 0, 1, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5240, 117401, 1, 1, 1, 0, 1, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5241, 117401, 1, 2, 1, 0, 1, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5242, 117401, 1, 3, 1, 0, 1, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5243, 117401, 1, 4, 1, 0, 1, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5244, 117401, 1, 5, 1, 0, 1, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5245, 117401, 1, 101, 4, 0, 1, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5246, 117401, 1, 102, 8, 0, 1, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5247, 117401, 2, 1, 1, 0, 1, "騎士さんいいところに\nこの衣装\nどう思います？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5248, 117401, 2, 2, 1, 0, 1, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5249, 117401, 2, 3, 1, 0, 1, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5250, 117401, 2, 4, 1, 0, 1, "仕立て屋も\nアイドルも\n諦めません！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5251, 117401, 2, 101, 4, 0, 1, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5252, 117401, 2, 102, 8, 0, 1, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5253, 117401, 2, 1, 1, 1, 1, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5254, 117401, 2, 2, 1, 2, 1, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5255, 117401, 2, 3, 1, 3, 1, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5257, 117401, 3, 2, 1, 0, 1, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5258, 117401, 3, 3, 1, 0, 1, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5259, 117401, 3, 4, 1, 0, 1, "いい香りですね～\n波の音が聞こえて\nきそうです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5260, 117401, 3, 5, 1, 0, 1, "泳いだ後は\n少し寒いので\nちょうどいいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5262, 117401, 3, 7, 1, 0, 1, "夏バテしないように\n栄養はしっかり\n取らないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5263, 117401, 3, 8, 1, 0, 1, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5265, 117401, 3, 10, 1, 0, 1, "デザインはもちろん\n触り心地も\n最高ですね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5266, 117401, 3, 11, 1, 0, 1, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5267, 104001, 1, 103, 12, 0, 1, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5268, 104001, 2, 103, 12, 0, 1, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5269, 117301, 1, 1, 1, 0, 1, "この衣装で\nライブするのは\n恥ずかしいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5270, 117301, 1, 2, 1, 0, 1, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5271, 117301, 1, 3, 1, 0, 1, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5272, 117301, 1, 4, 1, 0, 1, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5273, 117301, 1, 5, 1, 0, 1, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5274, 117301, 1, 101, 4, 0, 1, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5275, 117301, 1, 102, 8, 0, 1, "水着、似合ってるって\n言ってもらえるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5276, 117301, 2, 1, 1, 0, 1, "サインして、ですか？\nいいですけど…\n少し照れますね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5277, 117301, 2, 2, 1, 0, 1, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5278, 117301, 2, 3, 1, 0, 1, "なんでしょう？\nもしかして…\nボイトレに興味が？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5279, 117301, 2, 4, 1, 0, 1, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5280, 117301, 2, 101, 4, 0, 1, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5281, 117301, 2, 102, 8, 0, 1, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5282, 117301, 2, 1, 1, 1, 1, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5283, 117301, 2, 2, 1, 2, 1, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5284, 117301, 2, 3, 1, 3, 1, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5286, 117301, 3, 2, 1, 0, 1, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5287, 117301, 3, 3, 1, 0, 1, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5288, 117301, 3, 4, 1, 0, 1, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5289, 117301, 3, 5, 1, 0, 1, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5291, 117301, 3, 7, 1, 0, 1, "アイドルとして、\n食べすぎには\n気をつけないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5292, 117301, 3, 8, 1, 0, 1, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5294, 117301, 3, 10, 1, 0, 1, "この子に乗りながら\nライブをする…\nというのはどうですか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5295, 117301, 3, 11, 1, 0, 1, "まるで人魚になった\n気分…なんて、ふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5296, 118301, 1, 1, 1, 0, 1, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5297, 118301, 1, 2, 1, 0, 1, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5298, 118301, 1, 3, 1, 0, 1, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5299, 118301, 1, 4, 1, 0, 1, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5300, 118301, 1, 5, 1, 0, 1, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5301, 118301, 1, 101, 4, 0, 1, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5302, 118301, 1, 102, 8, 0, 1, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5303, 118301, 2, 1, 1, 0, 1, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5304, 118301, 2, 2, 1, 0, 1, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5305, 118301, 2, 3, 1, 0, 1, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5306, 118301, 2, 4, 1, 0, 1, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5307, 118301, 2, 101, 4, 0, 1, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5308, 118301, 2, 102, 8, 0, 1, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5309, 118301, 2, 1, 1, 1, 1, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5310, 118301, 2, 2, 1, 2, 1, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5311, 118301, 2, 3, 1, 3, 1, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5313, 118301, 3, 2, 1, 0, 1, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5314, 118301, 3, 3, 1, 0, 1, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5315, 118301, 3, 4, 1, 0, 1, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5316, 118301, 3, 5, 1, 0, 1, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5318, 118301, 3, 7, 1, 0, 1, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5319, 118301, 3, 8, 1, 0, 1, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5322, 118401, 1, 1, 1, 0, 1, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5323, 118401, 1, 2, 1, 0, 1, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5324, 118401, 1, 3, 1, 0, 1, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5325, 118401, 1, 4, 1, 0, 1, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5326, 118401, 1, 5, 1, 0, 1, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5327, 118401, 1, 101, 4, 0, 1, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5328, 118401, 1, 102, 8, 0, 1, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5329, 118401, 2, 1, 1, 0, 1, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5330, 118401, 2, 2, 1, 0, 1, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5331, 118401, 2, 3, 1, 0, 1, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5332, 118401, 2, 4, 1, 0, 1, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5333, 118401, 2, 101, 4, 0, 1, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5334, 118401, 2, 102, 8, 0, 1, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5335, 118401, 2, 1, 1, 1, 1, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5336, 118401, 2, 2, 1, 2, 1, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5337, 118401, 2, 3, 1, 3, 1, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5339, 118401, 3, 2, 1, 0, 1, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5340, 118401, 3, 3, 1, 0, 1, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5341, 118401, 3, 4, 1, 0, 1, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5342, 118401, 3, 5, 1, 0, 1, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5344, 118401, 3, 7, 1, 0, 1, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5345, 118401, 3, 8, 1, 0, 1, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5348, 117501, 1, 1, 1, 0, 1, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5349, 117501, 1, 2, 1, 0, 1, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5350, 117501, 1, 3, 1, 0, 1, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5351, 117501, 1, 4, 1, 0, 1, "新薬開発のために\n薬草を採取して\nおこうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5352, 117501, 1, 5, 1, 0, 1, "いつ急患が来ても\n対応できるように\nしておきましょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5353, 117501, 1, 101, 4, 0, 1, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5354, 117501, 1, 102, 8, 0, 1, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5355, 117501, 2, 1, 1, 0, 1, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5356, 117501, 2, 2, 1, 0, 1, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5357, 117501, 2, 3, 1, 0, 1, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5358, 117501, 2, 4, 1, 0, 1, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5359, 117501, 2, 101, 4, 0, 1, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5360, 117501, 2, 102, 8, 0, 1, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5361, 117501, 2, 1, 1, 1, 1, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5362, 117501, 2, 2, 1, 2, 1, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5363, 117501, 2, 3, 1, 3, 1, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5365, 117501, 3, 2, 1, 0, 1, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5366, 117501, 3, 3, 1, 0, 1, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5367, 117501, 3, 4, 1, 0, 1, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5368, 117501, 3, 5, 1, 0, 1, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5370, 117501, 3, 7, 1, 0, 1, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5371, 117501, 3, 8, 1, 0, 1, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5400, 117601, 1, 1, 1, 0, 1, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5401, 117601, 1, 2, 1, 0, 1, "美しさとは\n日々の努力が\n作るものなんだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5402, 117601, 1, 3, 1, 0, 1, "大変だ…\nボクは美しく\nなりすぎたかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5403, 117601, 1, 4, 1, 0, 1, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5404, 117601, 1, 5, 1, 0, 1, "どんな服装をしても\nボクの美しさは\n変わらないね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5405, 117601, 1, 101, 4, 0, 1, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5406, 117601, 1, 102, 8, 0, 1, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5407, 117601, 2, 1, 1, 0, 1, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5408, 117601, 2, 2, 1, 0, 1, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5409, 117601, 2, 3, 1, 0, 1, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5410, 117601, 2, 4, 1, 0, 1, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5411, 117601, 2, 101, 4, 0, 1, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5412, 117601, 2, 102, 8, 0, 1, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5413, 117601, 2, 1, 1, 1, 1, "さすがボク\n目が覚めるような\n美しさでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5414, 117601, 2, 2, 1, 2, 1, "美は食で作られる\n美容にいいものを\n食べようよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5415, 117601, 2, 3, 1, 3, 1, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5417, 117601, 3, 2, 1, 0, 1, "ふう～…\nリラックスする\nボクも美しい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5418, 117601, 3, 3, 1, 0, 1, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5419, 117601, 3, 4, 1, 0, 1, "美しいボクは\n常にいい香りを\nしていなくちゃね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5420, 117601, 3, 5, 1, 0, 1, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5422, 117601, 3, 7, 1, 0, 1, "バランスのいい食事が\n美しいボクを\n作るのさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5423, 117601, 3, 8, 1, 0, 1, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5425, 105701, 1, 103, 12, 0, 1, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5426, 105701, 2, 103, 12, 0, 1, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5427, 118001, 1, 1, 1, 0, 1, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5428, 118001, 1, 2, 1, 0, 1, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5429, 118001, 1, 3, 1, 0, 1, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5430, 118001, 1, 4, 1, 0, 1, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5431, 118001, 1, 5, 1, 0, 1, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5432, 118001, 1, 101, 4, 0, 1, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5433, 118001, 1, 102, 8, 0, 1, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5434, 118001, 2, 1, 1, 0, 1, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5435, 118001, 2, 2, 1, 0, 1, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5436, 118001, 2, 3, 1, 0, 1, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5437, 118001, 2, 4, 1, 0, 1, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5438, 118001, 2, 101, 4, 0, 1, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5439, 118001, 2, 102, 8, 0, 1, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5440, 118001, 2, 1, 1, 1, 1, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5441, 118001, 2, 2, 1, 2, 1, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5442, 118001, 2, 3, 1, 3, 1, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5444, 118001, 3, 2, 1, 0, 1, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5445, 118001, 3, 3, 1, 0, 1, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5446, 118001, 3, 4, 1, 0, 1, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5447, 118001, 3, 5, 1, 0, 1, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5449, 118001, 3, 7, 1, 0, 1, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5450, 118001, 3, 8, 1, 0, 1, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5452, 117701, 1, 1, 1, 0, 1, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5453, 117701, 1, 2, 1, 0, 1, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5454, 117701, 1, 3, 1, 0, 1, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5455, 117701, 1, 4, 1, 0, 1, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5456, 117701, 1, 5, 1, 0, 1, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5457, 117701, 1, 101, 4, 0, 1, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5458, 117701, 1, 102, 8, 0, 1, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5459, 117701, 2, 1, 1, 0, 1, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5460, 117701, 2, 2, 1, 0, 1, "この制服、\nハロウィンらしくて\n大好きさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5461, 117701, 2, 3, 1, 0, 1, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5462, 117701, 2, 4, 1, 0, 1, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5463, 117701, 2, 101, 4, 0, 1, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5464, 117701, 2, 102, 8, 0, 1, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5465, 117701, 2, 1, 1, 1, 1, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5466, 117701, 2, 2, 1, 2, 1, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5467, 117701, 2, 3, 1, 3, 1, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5469, 117701, 3, 2, 1, 0, 1, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5470, 117701, 3, 3, 1, 0, 1, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5471, 117701, 3, 4, 1, 0, 1, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5472, 117701, 3, 5, 1, 0, 1, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5474, 117701, 3, 7, 1, 0, 1, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5475, 117701, 3, 8, 1, 0, 1, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5477, 117701, 3, 12, 1, 0, 1, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5479, 117801, 1, 1, 1, 0, 1, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5480, 117801, 1, 2, 1, 0, 1, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5481, 117801, 1, 3, 1, 0, 1, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5482, 117801, 1, 4, 1, 0, 1, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5483, 117801, 1, 5, 1, 0, 1, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5484, 117801, 1, 101, 4, 0, 1, "幽霊になる前に\n天下統一を\n果たしたいデスね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5485, 117801, 1, 102, 8, 0, 1, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5486, 117801, 2, 1, 1, 0, 1, "ショーグン！\n悪霊退治に\n出かけるデース！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5487, 117801, 2, 2, 1, 0, 1, "妖魔を家来に\nしてみるのは\nどうデス？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5488, 117801, 2, 3, 1, 0, 1, "マントを使って\nウツセミの術の精度を\n高めるデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5489, 117801, 2, 4, 1, 0, 1, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5490, 117801, 2, 101, 4, 0, 1, "あの世でも\nショーグンに\nお仕えしマスよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5491, 117801, 2, 102, 8, 0, 1, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5492, 117801, 2, 1, 1, 1, 1, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5493, 117801, 2, 2, 1, 2, 1, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5494, 117801, 2, 3, 1, 3, 1, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5496, 117801, 3, 2, 1, 0, 1, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5497, 117801, 3, 3, 1, 0, 1, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5498, 117801, 3, 4, 1, 0, 1, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5499, 117801, 3, 5, 1, 0, 1, "パレードの熱気に\n負けないくらい\n熱いデス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5501, 117801, 3, 7, 1, 0, 1, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5502, 117801, 3, 8, 1, 0, 1, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5504, 117801, 3, 12, 1, 0, 1, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5531, 101301, 1, 103, 12, 0, 1, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5532, 101301, 2, 103, 12, 0, 1, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5533, 117901, 1, 1, 1, 0, 1, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5534, 117901, 1, 2, 1, 0, 1, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5535, 117901, 1, 3, 1, 0, 1, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5536, 117901, 1, 4, 1, 0, 1, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5537, 117901, 1, 5, 1, 0, 1, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5538, 117901, 1, 101, 4, 0, 1, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5539, 117901, 1, 102, 8, 0, 1, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5540, 117901, 2, 1, 1, 0, 1, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5541, 117901, 2, 2, 1, 0, 1, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5542, 117901, 2, 3, 1, 0, 1, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5543, 117901, 2, 4, 1, 0, 1, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5544, 117901, 2, 101, 4, 0, 1, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5545, 117901, 2, 102, 8, 0, 1, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5546, 117901, 2, 1, 1, 1, 1, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5547, 117901, 2, 2, 1, 2, 1, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5548, 117901, 2, 3, 1, 3, 1, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5550, 117901, 3, 2, 1, 0, 1, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5551, 117901, 3, 3, 1, 0, 1, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5552, 117901, 3, 4, 1, 0, 1, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5553, 117901, 3, 5, 1, 0, 1, "やばばば…\nあったかくて\n眠くなっちゃう～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5555, 117901, 3, 7, 1, 0, 1, "これ食べて\nもうひとがんばり\nしよーっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5556, 117901, 3, 8, 1, 0, 1, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5558, 117901, 3, 12, 1, 0, 1, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5560, 118501, 1, 1, 1, 0, 1, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5561, 118501, 1, 2, 1, 0, 1, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5562, 118501, 1, 3, 1, 0, 1, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5563, 118501, 1, 4, 1, 0, 1, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5564, 118501, 1, 5, 1, 0, 1, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5565, 118501, 1, 101, 4, 0, 1, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5566, 118501, 1, 102, 8, 0, 1, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5567, 118501, 2, 1, 1, 0, 1, "何かわからないことが\nあるときは私に\n聞いてくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5568, 118501, 2, 2, 1, 0, 1, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5569, 118501, 2, 3, 1, 0, 1, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5570, 118501, 2, 4, 1, 0, 1, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5571, 118501, 2, 101, 4, 0, 1, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5572, 118501, 2, 102, 8, 0, 1, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5573, 118501, 2, 1, 1, 1, 1, "今日も一日\n管理人として\nがんばります♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5574, 118501, 2, 2, 1, 2, 1, "お腹がすきました…\nそろそろお昼に\nしませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5575, 118501, 2, 3, 1, 3, 1, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5577, 118501, 3, 2, 1, 0, 1, "良質なお仕事は\n良質な休憩から\nです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5578, 118501, 3, 3, 1, 0, 1, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5579, 118501, 3, 4, 1, 0, 1, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5580, 118501, 3, 5, 1, 0, 1, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5582, 118501, 3, 7, 1, 0, 1, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5583, 118501, 3, 8, 1, 0, 1, "新条例はこのように\n活用されている、と…\nふむふむ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5585, 106701, 1, 1, 1, 0, 1, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5586, 106701, 1, 2, 1, 0, 1, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5587, 106701, 1, 3, 1, 0, 1, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5588, 106701, 1, 4, 1, 0, 1, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5589, 106701, 1, 5, 1, 0, 1, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5590, 106701, 1, 101, 4, 0, 1, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5591, 106701, 1, 102, 8, 0, 1, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5592, 106701, 2, 1, 1, 0, 1, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5593, 106701, 2, 2, 1, 0, 1, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5594, 106701, 2, 3, 1, 0, 1, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5595, 106701, 2, 4, 1, 0, 1, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5596, 106701, 2, 101, 4, 0, 1, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5597, 106701, 2, 102, 8, 0, 1, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5598, 106701, 2, 1, 1, 1, 1, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5599, 106701, 2, 2, 1, 2, 1, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5600, 106701, 2, 3, 1, 3, 1, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5602, 106701, 3, 2, 1, 0, 1, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5603, 106701, 3, 3, 1, 0, 1, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5604, 106701, 3, 4, 1, 0, 1, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5605, 106701, 3, 5, 1, 0, 1, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5607, 106701, 3, 7, 1, 0, 1, "この味付け…\nあっちでも再現\nできるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5608, 106701, 3, 8, 1, 0, 1, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5610, 119001, 1, 1, 1, 0, 1, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5611, 119001, 1, 2, 1, 0, 1, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5612, 119001, 1, 3, 1, 0, 1, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5613, 119001, 1, 4, 1, 0, 1, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5614, 119001, 1, 5, 1, 0, 1, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5615, 119001, 1, 101, 4, 0, 1, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5616, 119001, 1, 102, 8, 0, 1, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5617, 119001, 2, 1, 1, 0, 1, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5618, 119001, 2, 2, 1, 0, 1, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5619, 119001, 2, 3, 1, 0, 1, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5620, 119001, 2, 4, 1, 0, 1, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5621, 119001, 2, 101, 4, 0, 1, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5622, 119001, 2, 102, 8, 0, 1, "君との秘密が\nまた一つ\n増えちゃったね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5623, 119001, 2, 1, 1, 1, 1, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5624, 119001, 2, 2, 1, 2, 1, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5625, 119001, 2, 3, 1, 3, 1, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5627, 119001, 3, 2, 1, 0, 1, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5628, 119001, 3, 3, 1, 0, 1, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5629, 119001, 3, 4, 1, 0, 1, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5630, 119001, 3, 5, 1, 0, 1, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5632, 119001, 3, 7, 1, 0, 1, "生徒の見本に\nなれるように\n好き嫌いはしないの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5633, 119001, 3, 8, 1, 0, 1, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5635, 106301, 1, 103, 12, 0, 1, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5636, 106301, 2, 103, 12, 0, 1, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5637, 105301, 1, 103, 12, 0, 1, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5638, 105301, 2, 103, 12, 0, 1, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5639, 119101, 1, 1, 1, 0, 1, "ああ～\nドＭミストが\nあふれてしまいます～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5640, 119101, 1, 2, 1, 0, 1, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5641, 119101, 1, 3, 1, 0, 1, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5642, 119101, 1, 4, 1, 0, 1, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5643, 119101, 1, 5, 1, 0, 1, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5644, 119101, 1, 101, 4, 0, 1, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5645, 119101, 1, 102, 8, 0, 1, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5646, 119101, 2, 1, 1, 0, 1, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5647, 119101, 2, 2, 1, 0, 1, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5648, 119101, 2, 3, 1, 0, 1, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5649, 119101, 2, 4, 1, 0, 1, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5650, 119101, 2, 101, 4, 0, 1, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5651, 119101, 2, 102, 8, 0, 1, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5652, 119101, 2, 1, 1, 1, 1, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5653, 119101, 2, 2, 1, 2, 1, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5654, 119101, 2, 3, 1, 3, 1, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5656, 119101, 3, 2, 1, 0, 1, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5657, 119101, 3, 3, 1, 0, 1, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5658, 119101, 3, 4, 1, 0, 1, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5659, 119101, 3, 5, 1, 0, 1, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5661, 119101, 3, 7, 1, 0, 1, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5662, 119101, 3, 8, 1, 0, 1, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5664, 119901, 1, 1, 1, 0, 1, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5665, 119901, 1, 2, 1, 0, 1, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5666, 119901, 1, 3, 1, 0, 1, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5667, 119901, 1, 4, 1, 0, 1, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5668, 119901, 1, 5, 1, 0, 1, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5669, 119901, 1, 101, 4, 0, 1, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5670, 119901, 1, 102, 8, 0, 1, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5671, 119901, 2, 1, 1, 0, 1, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5672, 119901, 2, 2, 1, 0, 1, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5673, 119901, 2, 3, 1, 0, 1, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5674, 119901, 2, 4, 1, 0, 1, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5675, 119901, 2, 101, 4, 0, 1, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5676, 119901, 2, 102, 8, 0, 1, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5677, 119901, 2, 1, 1, 1, 1, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5678, 119901, 2, 2, 1, 2, 1, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5679, 119901, 2, 3, 1, 3, 1, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5681, 119901, 3, 2, 1, 0, 1, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5682, 119901, 3, 3, 1, 0, 1, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5683, 119901, 3, 4, 1, 0, 1, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5684, 119901, 3, 5, 1, 0, 1, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5686, 119901, 3, 7, 1, 0, 1, "これはオマエがくれる\nプリンとは\n別腹なの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5687, 119901, 3, 8, 1, 0, 1, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5689, 119901, 3, 14, 1, 0, 1, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5690, 119901, 3, 15, 1, 0, 1, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5691, 119201, 1, 1, 1, 0, 1, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5692, 119201, 1, 2, 1, 0, 1, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5693, 119201, 1, 3, 1, 0, 1, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5694, 119201, 1, 4, 1, 0, 1, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5695, 119201, 1, 5, 1, 0, 1, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5696, 119201, 1, 101, 4, 0, 1, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5697, 119201, 1, 102, 8, 0, 1, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5698, 119201, 2, 1, 1, 0, 1, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5699, 119201, 2, 2, 1, 0, 1, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5700, 119201, 2, 3, 1, 0, 1, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5701, 119201, 2, 4, 1, 0, 1, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5702, 119201, 2, 101, 4, 0, 1, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5703, 119201, 2, 102, 8, 0, 1, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5704, 119201, 2, 1, 1, 1, 1, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5705, 119201, 2, 2, 1, 2, 1, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5706, 119201, 2, 3, 1, 3, 1, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5708, 119201, 3, 2, 1, 0, 1, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5709, 119201, 3, 3, 1, 0, 1, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5710, 119201, 3, 4, 1, 0, 1, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5711, 119201, 3, 5, 1, 0, 1, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5713, 119201, 3, 7, 1, 0, 1, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5714, 119201, 3, 8, 1, 0, 1, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5716, 119201, 3, 14, 1, 0, 1, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5717, 119201, 3, 15, 1, 0, 1, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5718, 105001, 1, 103, 12, 0, 1, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5719, 105001, 2, 103, 12, 0, 1, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5720, 119301, 1, 1, 1, 0, 1, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5721, 119301, 1, 2, 1, 0, 1, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5722, 119301, 1, 3, 1, 0, 1, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5723, 119301, 1, 4, 1, 0, 1, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5724, 119301, 1, 5, 1, 0, 1, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5725, 119301, 1, 101, 4, 0, 1, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5726, 119301, 1, 102, 8, 0, 1, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5727, 119301, 2, 1, 1, 0, 1, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5728, 119301, 2, 2, 1, 0, 1, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5729, 119301, 2, 3, 1, 0, 1, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5730, 119301, 2, 4, 1, 0, 1, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5731, 119301, 2, 101, 4, 0, 1, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5732, 119301, 2, 102, 8, 0, 1, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5733, 119301, 2, 1, 1, 1, 1, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5734, 119301, 2, 2, 1, 2, 1, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5735, 119301, 2, 3, 1, 3, 1, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5737, 119301, 3, 2, 1, 0, 1, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5738, 119301, 3, 3, 1, 0, 1, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5739, 119301, 3, 4, 1, 0, 1, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5740, 119301, 3, 5, 1, 0, 1, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5742, 119301, 3, 7, 1, 0, 1, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5743, 119301, 3, 8, 1, 0, 1, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5745, 119301, 3, 14, 1, 0, 1, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5746, 119301, 3, 15, 1, 0, 1, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5747, 120601, 1, 1, 1, 0, 1, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5748, 120601, 1, 2, 1, 0, 1, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5749, 120601, 1, 3, 1, 0, 1, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5750, 120601, 1, 4, 1, 0, 1, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5751, 120601, 1, 5, 1, 0, 1, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5752, 120601, 1, 101, 4, 0, 1, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5753, 120601, 1, 102, 8, 0, 1, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5754, 120601, 2, 1, 1, 0, 1, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5755, 120601, 2, 2, 1, 0, 1, "きゃあっ！？\nへ、変なところを\n触らないでください！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5756, 120601, 2, 3, 1, 0, 1, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5757, 120601, 2, 4, 1, 0, 1, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5758, 120601, 2, 101, 4, 0, 1, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5759, 120601, 2, 102, 8, 0, 1, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5760, 120601, 2, 1, 1, 1, 1, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5761, 120601, 2, 2, 1, 2, 1, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5762, 120601, 2, 3, 1, 3, 1, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5764, 120601, 3, 2, 1, 0, 1, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5765, 120601, 3, 3, 1, 0, 1, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5766, 120601, 3, 4, 1, 0, 1, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5767, 120601, 3, 5, 1, 0, 1, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5769, 120601, 3, 7, 1, 0, 1, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5770, 120601, 3, 8, 1, 0, 1, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5773, 120401, 1, 1, 1, 0, 1, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5774, 120401, 1, 2, 1, 0, 1, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5775, 120401, 1, 3, 1, 0, 1, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5776, 120401, 1, 4, 1, 0, 1, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5777, 120401, 1, 5, 1, 0, 1, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5778, 120401, 1, 101, 4, 0, 1, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5779, 120401, 1, 102, 8, 0, 1, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5780, 120401, 2, 1, 1, 0, 1, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5781, 120401, 2, 2, 1, 0, 1, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5782, 120401, 2, 3, 1, 0, 1, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5783, 120401, 2, 4, 1, 0, 1, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5784, 120401, 2, 101, 4, 0, 1, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5785, 120401, 2, 102, 8, 0, 1, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5786, 120401, 2, 1, 1, 1, 1, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5787, 120401, 2, 2, 1, 2, 1, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5788, 120401, 2, 3, 1, 3, 1, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5790, 120401, 3, 2, 1, 0, 1, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5791, 120401, 3, 3, 1, 0, 1, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5792, 120401, 3, 4, 1, 0, 1, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5793, 120401, 3, 5, 1, 0, 1, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5795, 120401, 3, 7, 1, 0, 1, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5796, 120401, 3, 8, 1, 0, 1, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5799, 120501, 1, 1, 1, 0, 1, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5800, 120501, 1, 2, 1, 0, 1, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5801, 120501, 1, 3, 1, 0, 1, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5802, 120501, 1, 4, 1, 0, 1, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5803, 120501, 1, 5, 1, 0, 1, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5804, 120501, 1, 101, 4, 0, 1, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5805, 120501, 1, 102, 8, 0, 1, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5806, 120501, 2, 1, 1, 0, 1, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5807, 120501, 2, 2, 1, 0, 1, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5808, 120501, 2, 3, 1, 0, 1, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5809, 120501, 2, 4, 1, 0, 1, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5810, 120501, 2, 101, 4, 0, 1, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5811, 120501, 2, 102, 8, 0, 1, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5812, 120501, 2, 1, 1, 1, 1, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5813, 120501, 2, 2, 1, 2, 1, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5814, 120501, 2, 3, 1, 3, 1, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5816, 120501, 3, 2, 1, 0, 1, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5817, 120501, 3, 3, 1, 0, 1, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5818, 120501, 3, 4, 1, 0, 1, "においをうまく使った\nイタズラって…\nなにかないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5819, 120501, 3, 5, 1, 0, 1, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5821, 120501, 3, 7, 1, 0, 1, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5822, 120501, 3, 8, 1, 0, 1, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5825, 120801, 1, 1, 1, 0, 1, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5826, 120801, 1, 2, 1, 0, 1, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5827, 120801, 1, 3, 1, 0, 1, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5828, 120801, 1, 4, 1, 0, 1, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5829, 120801, 1, 5, 1, 0, 1, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5830, 120801, 1, 101, 4, 0, 1, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5831, 120801, 1, 102, 8, 0, 1, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5832, 120801, 2, 1, 1, 0, 1, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5833, 120801, 2, 2, 1, 0, 1, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5834, 120801, 2, 3, 1, 0, 1, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5835, 120801, 2, 4, 1, 0, 1, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5836, 120801, 2, 101, 4, 0, 1, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5837, 120801, 2, 102, 8, 0, 1, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5838, 120801, 2, 1, 1, 1, 1, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5839, 120801, 2, 2, 1, 2, 1, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5840, 120801, 2, 3, 1, 3, 1, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5842, 120801, 3, 2, 1, 0, 1, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5843, 120801, 3, 3, 1, 0, 1, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5844, 120801, 3, 4, 1, 0, 1, "これは冬の花の香り…\n風情があって\nいいじゃないか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5845, 120801, 3, 5, 1, 0, 1, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5847, 120801, 3, 7, 1, 0, 1, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5848, 120801, 3, 8, 1, 0, 1, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5850, 120701, 1, 1, 1, 0, 1, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5851, 120701, 1, 2, 1, 0, 1, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5852, 120701, 1, 3, 1, 0, 1, "この格好も\n慣れてくれば\n動きやすいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5853, 120701, 1, 4, 1, 0, 1, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5854, 120701, 1, 5, 1, 0, 1, "おせち料理\nネビアの分も\n用意しなくちゃね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5855, 120701, 1, 101, 4, 0, 1, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5856, 120701, 1, 102, 8, 0, 1, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5857, 120701, 2, 1, 1, 0, 1, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5858, 120701, 2, 2, 1, 0, 1, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5859, 120701, 2, 3, 1, 0, 1, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5860, 120701, 2, 4, 1, 0, 1, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5861, 120701, 2, 101, 4, 0, 1, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5862, 120701, 2, 102, 8, 0, 1, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5863, 120701, 2, 1, 1, 1, 1, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5864, 120701, 2, 2, 1, 2, 1, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5865, 120701, 2, 3, 1, 3, 1, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5867, 120701, 3, 2, 1, 0, 1, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5868, 120701, 3, 3, 1, 0, 1, "ふふっ\nかわいいものって\n癒されるわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5869, 120701, 3, 4, 1, 0, 1, "…ん\nこれが梅の香り？\n…いい匂いね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5870, 120701, 3, 5, 1, 0, 1, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5872, 120701, 3, 7, 1, 0, 1, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5873, 120701, 3, 8, 1, 0, 1, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5875, 104201, 1, 103, 12, 0, 1, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5876, 104201, 2, 103, 12, 0, 1, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5877, 120901, 1, 1, 1, 0, 1, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5878, 120901, 1, 2, 1, 0, 1, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5879, 120901, 1, 3, 1, 0, 1, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5880, 120901, 1, 4, 1, 0, 1, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5881, 120901, 1, 5, 1, 0, 1, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5882, 120901, 1, 101, 4, 0, 1, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5883, 120901, 1, 102, 8, 0, 1, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5884, 120901, 2, 1, 1, 0, 1, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5885, 120901, 2, 2, 1, 0, 1, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5886, 120901, 2, 3, 1, 0, 1, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5887, 120901, 2, 4, 1, 0, 1, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5888, 120901, 2, 101, 4, 0, 1, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5889, 120901, 2, 102, 8, 0, 1, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5890, 120901, 2, 1, 1, 1, 1, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5891, 120901, 2, 2, 1, 2, 1, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5892, 120901, 2, 3, 1, 3, 1, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5894, 120901, 3, 2, 1, 0, 1, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5895, 120901, 3, 3, 1, 0, 1, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5896, 120901, 3, 4, 1, 0, 1, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5897, 120901, 3, 5, 1, 0, 1, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5899, 120901, 3, 7, 1, 0, 1, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5900, 120901, 3, 8, 1, 0, 1, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5902, 121001, 1, 1, 1, 0, 1, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5903, 121001, 1, 2, 1, 0, 1, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5904, 121001, 1, 3, 1, 0, 1, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5905, 121001, 1, 4, 1, 0, 1, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5906, 121001, 1, 5, 1, 0, 1, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5907, 121001, 1, 101, 4, 0, 1, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5908, 121001, 1, 102, 8, 0, 1, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5909, 121001, 2, 1, 1, 0, 1, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5910, 121001, 2, 2, 1, 0, 1, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5911, 121001, 2, 3, 1, 0, 1, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5912, 121001, 2, 4, 1, 0, 1, "あなたの想いが\n全力全開を超える\n力をくれました！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5913, 121001, 2, 101, 4, 0, 1, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5914, 121001, 2, 102, 8, 0, 1, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5915, 121001, 2, 1, 1, 1, 1, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5916, 121001, 2, 2, 1, 2, 1, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5917, 121001, 2, 3, 1, 3, 1, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5919, 121001, 3, 2, 1, 0, 1, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5920, 121001, 3, 3, 1, 0, 1, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5921, 121001, 3, 4, 1, 0, 1, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5922, 121001, 3, 5, 1, 0, 1, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5924, 121001, 3, 7, 1, 0, 1, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5925, 121001, 3, 8, 1, 0, 1, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5927, 121201, 1, 1, 1, 0, 1, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5928, 121201, 1, 2, 1, 0, 1, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5929, 121201, 1, 3, 1, 0, 1, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5930, 121201, 1, 4, 1, 0, 1, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5931, 121201, 1, 5, 1, 0, 1, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5932, 121201, 1, 101, 4, 0, 1, "少年が\n目を覚ましてくれて\n本当によかった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5933, 121201, 1, 102, 8, 0, 1, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5934, 121201, 2, 1, 1, 0, 1, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5935, 121201, 2, 2, 1, 0, 1, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5936, 121201, 2, 3, 1, 0, 1, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5937, 121201, 2, 4, 1, 0, 1, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5938, 121201, 2, 101, 4, 0, 1, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5939, 121201, 2, 102, 8, 0, 1, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5940, 121201, 2, 1, 1, 1, 1, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5941, 121201, 2, 2, 1, 2, 1, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5942, 121201, 2, 3, 1, 3, 1, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5944, 121201, 3, 2, 1, 0, 1, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5945, 121201, 3, 3, 1, 0, 1, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5946, 121201, 3, 4, 1, 0, 1, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5947, 121201, 3, 5, 1, 0, 1, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5949, 121201, 3, 7, 1, 0, 1, "食の大切さは\nいつの食事からも\n感じられるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5950, 121201, 3, 8, 1, 0, 1, "ふむ…面白い！\n今度全巻\n借りにこようっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5952, 116401, 1, 1, 1, 0, 1, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5953, 116401, 1, 2, 1, 0, 1, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5954, 116401, 1, 3, 1, 0, 1, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5955, 116401, 1, 4, 1, 0, 1, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5956, 116401, 1, 5, 1, 0, 1, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5957, 116401, 1, 101, 4, 0, 1, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5958, 116401, 1, 102, 8, 0, 1, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5959, 116401, 2, 1, 1, 0, 1, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5960, 116401, 2, 2, 1, 0, 1, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5961, 116401, 2, 3, 1, 0, 1, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5962, 116401, 2, 4, 1, 0, 1, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5963, 116401, 2, 101, 4, 0, 1, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5964, 116401, 2, 102, 8, 0, 1, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5965, 116401, 2, 1, 1, 1, 1, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5966, 116401, 2, 2, 1, 2, 1, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5967, 116401, 2, 3, 1, 3, 1, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5969, 116401, 3, 2, 1, 0, 1, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5970, 116401, 3, 3, 1, 0, 1, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5971, 116401, 3, 4, 1, 0, 1, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5972, 116401, 3, 5, 1, 0, 1, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5974, 116401, 3, 7, 1, 0, 1, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5975, 116401, 3, 8, 1, 0, 1, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5977, 121101, 1, 1, 1, 0, 1, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5978, 121101, 1, 2, 1, 0, 1, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5979, 121101, 1, 3, 1, 0, 1, "あいつのこと、\n全部受け入れるって\n決めたから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5980, 121101, 1, 4, 1, 0, 1, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5981, 121101, 1, 5, 1, 0, 1, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5982, 121101, 1, 101, 4, 0, 1, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5983, 121101, 1, 102, 8, 0, 1, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5984, 121101, 2, 1, 1, 0, 1, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5985, 121101, 2, 2, 1, 0, 1, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5986, 121101, 2, 3, 1, 0, 1, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5987, 121101, 2, 4, 1, 0, 1, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5988, 121101, 2, 101, 4, 0, 1, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5989, 121101, 2, 102, 8, 0, 1, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5990, 121101, 2, 1, 1, 1, 1, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5991, 121101, 2, 2, 1, 2, 1, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5992, 121101, 2, 3, 1, 3, 1, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5994, 121101, 3, 2, 1, 0, 1, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5995, 121101, 3, 3, 1, 0, 1, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5996, 121101, 3, 4, 1, 0, 1, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5997, 121101, 3, 5, 1, 0, 1, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (5999, 121101, 3, 7, 1, 0, 1, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6000, 121101, 3, 8, 1, 0, 1, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6002, 118101, 1, 1, 1, 0, 1, "お友だち…\nできるかしら…？\nこんなわたしでも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6003, 118101, 1, 2, 1, 0, 1, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6004, 118101, 1, 3, 1, 0, 1, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6005, 118101, 1, 4, 1, 0, 1, "～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6006, 118101, 1, 5, 1, 0, 1, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6007, 118101, 1, 101, 4, 0, 1, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6008, 118101, 1, 102, 8, 0, 1, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6009, 118101, 2, 1, 1, 0, 1, "ええと…\nお腹、空いてる？\nおねむじゃない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6010, 118101, 2, 2, 1, 0, 1, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6011, 118101, 2, 3, 1, 0, 1, "お喋り…？\nわたしでいいの…？\nうれしい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6012, 118101, 2, 4, 1, 0, 1, "あなたの好きな歌…\n教えて…？\n練習、するから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6013, 118101, 2, 101, 4, 0, 1, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6014, 118101, 2, 102, 8, 0, 1, "安心、して…\nわたしの歌で、\nあなたを守るから…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6015, 118101, 2, 1, 1, 1, 1, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6016, 118101, 2, 2, 1, 2, 1, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6017, 118101, 2, 3, 1, 3, 1, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6019, 118101, 3, 2, 1, 0, 1, "こんな特等席…\n恐縮、です…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6020, 118101, 3, 3, 1, 0, 1, "い、今のうちに、\nこの子でお世話の\n練習を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6021, 118101, 3, 4, 1, 0, 1, "いい香り…\n…つらい気持ちも\n癒してくれる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6022, 118101, 3, 5, 1, 0, 1, "あったかい…\nいつまでも、\nこうしていられそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6024, 118101, 3, 7, 1, 0, 1, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6025, 118101, 3, 8, 1, 0, 1, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6027, 121301, 1, 1, 1, 0, 1, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6028, 121301, 1, 2, 1, 0, 1, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6029, 121301, 1, 3, 1, 0, 1, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6030, 121301, 1, 4, 1, 0, 1, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6031, 121301, 1, 5, 1, 0, 1, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6032, 121301, 1, 101, 4, 0, 1, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6033, 121301, 1, 102, 8, 0, 1, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6034, 121301, 2, 1, 1, 0, 1, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6035, 121301, 2, 2, 1, 0, 1, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6036, 121301, 2, 3, 1, 0, 1, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6037, 121301, 2, 4, 1, 0, 1, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6038, 121301, 2, 101, 4, 0, 1, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6039, 121301, 2, 102, 8, 0, 1, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6040, 121301, 2, 1, 1, 1, 1, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6041, 121301, 2, 2, 1, 2, 1, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6042, 121301, 2, 3, 1, 3, 1, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6044, 121301, 3, 2, 1, 0, 1, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6045, 121301, 3, 3, 1, 0, 1, "今日も演技の\n練習に付き合ってね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6046, 121301, 3, 4, 1, 0, 1, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6047, 121301, 3, 5, 1, 0, 1, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6049, 121301, 3, 7, 1, 0, 1, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6050, 121301, 3, 8, 1, 0, 1, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6052, 107501, 1, 103, 12, 0, 1, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6053, 107501, 2, 103, 12, 0, 1, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6054, 107601, 1, 103, 12, 0, 1, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6055, 107601, 2, 103, 12, 0, 1, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6056, 107801, 1, 103, 12, 0, 1, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6057, 107801, 2, 103, 12, 0, 1, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6058, 121401, 1, 1, 1, 0, 1, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6059, 121401, 1, 2, 1, 0, 1, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6060, 121401, 1, 3, 1, 0, 1, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6061, 121401, 1, 4, 1, 0, 1, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6062, 121401, 1, 5, 1, 0, 1, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6063, 121401, 1, 101, 4, 0, 1, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6064, 121401, 1, 102, 8, 0, 1, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6065, 121401, 2, 1, 1, 0, 1, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6066, 121401, 2, 2, 1, 0, 1, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6067, 121401, 2, 3, 1, 0, 1, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6068, 121401, 2, 4, 1, 0, 1, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6069, 121401, 2, 101, 4, 0, 1, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6070, 121401, 2, 102, 8, 0, 1, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6071, 121401, 2, 1, 1, 1, 1, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6072, 121401, 2, 2, 1, 2, 1, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6073, 121401, 2, 3, 1, 3, 1, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6075, 121401, 3, 2, 1, 0, 1, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6076, 121401, 3, 3, 1, 0, 1, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6077, 121401, 3, 4, 1, 0, 1, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6078, 121401, 3, 5, 1, 0, 1, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6080, 121401, 3, 7, 1, 0, 1, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6081, 121401, 3, 8, 1, 0, 1, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6083, 121501, 1, 1, 1, 0, 1, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6084, 121501, 1, 2, 1, 0, 1, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6085, 121501, 1, 3, 1, 0, 1, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6086, 121501, 1, 4, 1, 0, 1, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6087, 121501, 1, 5, 1, 0, 1, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6088, 121501, 1, 101, 4, 0, 1, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6089, 121501, 1, 102, 8, 0, 1, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6090, 121501, 2, 1, 1, 0, 1, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6091, 121501, 2, 2, 1, 0, 1, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6092, 121501, 2, 3, 1, 0, 1, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6093, 121501, 2, 4, 1, 0, 1, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6094, 121501, 2, 101, 4, 0, 1, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6095, 121501, 2, 102, 8, 0, 1, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6096, 121501, 2, 1, 1, 1, 1, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6097, 121501, 2, 2, 1, 2, 1, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6098, 121501, 2, 3, 1, 3, 1, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6100, 121501, 3, 2, 1, 0, 1, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6101, 121501, 3, 3, 1, 0, 1, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6102, 121501, 3, 4, 1, 0, 1, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6103, 121501, 3, 5, 1, 0, 1, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6105, 121501, 3, 7, 1, 0, 1, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6106, 121501, 3, 8, 1, 0, 1, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6108, 104501, 1, 103, 12, 0, 1, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6109, 104501, 2, 103, 12, 0, 1, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6110, 121601, 1, 1, 1, 0, 1, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6111, 121601, 1, 2, 1, 0, 1, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6112, 121601, 1, 3, 1, 0, 1, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6113, 121601, 1, 4, 1, 0, 1, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6114, 121601, 1, 5, 1, 0, 1, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6115, 121601, 1, 101, 4, 0, 1, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6116, 121601, 1, 102, 8, 0, 1, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6117, 121601, 2, 1, 1, 0, 1, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6118, 121601, 2, 2, 1, 0, 1, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6119, 121601, 2, 3, 1, 0, 1, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6120, 121601, 2, 4, 1, 0, 1, "しーっ！\n今、尾行中なんです\n話はあとで…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6121, 121601, 2, 101, 4, 0, 1, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6122, 121601, 2, 102, 8, 0, 1, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6123, 121601, 2, 1, 1, 1, 1, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6124, 121601, 2, 2, 1, 2, 1, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6125, 121601, 2, 3, 1, 3, 1, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6127, 121601, 3, 2, 1, 0, 1, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6128, 121601, 3, 3, 1, 0, 1, "くう…！\nこのかわいさは\n盗めないです…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6129, 121601, 3, 4, 1, 0, 1, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6130, 121601, 3, 5, 1, 0, 1, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6132, 121601, 3, 7, 1, 0, 1, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6133, 121601, 3, 8, 1, 0, 1, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6135, 121701, 1, 1, 1, 0, 1, "あら、サレンさんは\nどこに行かれた\nのかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6136, 121701, 1, 2, 1, 0, 1, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6137, 121701, 1, 3, 1, 0, 1, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6138, 121701, 1, 4, 1, 0, 1, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6139, 121701, 1, 5, 1, 0, 1, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6140, 121701, 1, 101, 4, 0, 1, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6141, 121701, 1, 102, 8, 0, 1, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6142, 121701, 2, 1, 1, 0, 1, "ふふ、ごきげんよう\n私、絶好調でしてよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6143, 121701, 2, 2, 1, 0, 1, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6144, 121701, 2, 3, 1, 0, 1, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6145, 121701, 2, 4, 1, 0, 1, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6146, 121701, 2, 101, 4, 0, 1, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6147, 121701, 2, 102, 8, 0, 1, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6148, 121701, 2, 1, 1, 1, 1, "さあ！\n今日も一日\n全力で参りますわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6149, 121701, 2, 2, 1, 2, 1, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6150, 121701, 2, 3, 1, 3, 1, "もう少しだけ…\n明日の準備をして\nおかないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6152, 121701, 3, 2, 1, 0, 1, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6153, 121701, 3, 3, 1, 0, 1, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6154, 121701, 3, 4, 1, 0, 1, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6155, 121701, 3, 5, 1, 0, 1, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6157, 121701, 3, 7, 1, 0, 1, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6158, 121701, 3, 8, 1, 0, 1, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6161, 121801, 1, 1, 1, 0, 1, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6162, 121801, 1, 2, 1, 0, 1, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6163, 121801, 1, 3, 1, 0, 1, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6164, 121801, 1, 4, 1, 0, 1, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6165, 121801, 1, 5, 1, 0, 1, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6166, 121801, 1, 101, 4, 0, 1, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6167, 121801, 1, 102, 8, 0, 1, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6168, 121801, 2, 1, 1, 0, 1, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6169, 121801, 2, 2, 1, 0, 1, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6170, 121801, 2, 3, 1, 0, 1, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6171, 121801, 2, 4, 1, 0, 1, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6172, 121801, 2, 101, 4, 0, 1, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6173, 121801, 2, 102, 8, 0, 1, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6174, 121801, 2, 1, 1, 1, 1, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6175, 121801, 2, 2, 1, 2, 1, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6176, 121801, 2, 3, 1, 3, 1, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6178, 121801, 3, 2, 1, 0, 1, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6179, 121801, 3, 3, 1, 0, 1, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6180, 121801, 3, 4, 1, 0, 1, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6181, 121801, 3, 5, 1, 0, 1, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6183, 121801, 3, 7, 1, 0, 1, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6184, 121801, 3, 8, 1, 0, 1, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6187, 122001, 1, 1, 1, 0, 1, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6188, 122001, 1, 2, 1, 0, 1, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6189, 122001, 1, 3, 1, 0, 1, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6190, 122001, 1, 4, 1, 0, 1, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6191, 122001, 1, 5, 1, 0, 1, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6192, 122001, 1, 101, 4, 0, 1, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6193, 122001, 1, 102, 8, 0, 1, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6194, 122001, 2, 1, 1, 0, 1, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6195, 122001, 2, 2, 1, 0, 1, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6196, 122001, 2, 3, 1, 0, 1, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6197, 122001, 2, 4, 1, 0, 1, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6198, 122001, 2, 101, 4, 0, 1, "船の上では食材が\n限られるので料理の\nいい練習になります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6199, 122001, 2, 102, 8, 0, 1, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6200, 122001, 2, 1, 1, 1, 1, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6201, 122001, 2, 2, 1, 2, 1, "船の上では\n規則正しい生活が\n重要ですから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6202, 122001, 2, 3, 1, 3, 1, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6204, 122001, 3, 2, 1, 0, 1, "海に揺られるような\n心地よさですね\n気が休まります…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6205, 122001, 3, 3, 1, 0, 1, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6206, 122001, 3, 4, 1, 0, 1, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6207, 122001, 3, 5, 1, 0, 1, "温かい…\n潮風で冷えた体に\n沁みますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6209, 122001, 3, 7, 1, 0, 1, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6210, 122001, 3, 8, 1, 0, 1, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6212, 102601, 1, 103, 12, 0, 1, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6213, 102601, 2, 103, 12, 0, 1, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6214, 121901, 1, 1, 1, 0, 1, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6215, 121901, 1, 2, 1, 0, 1, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6216, 121901, 1, 3, 1, 0, 1, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6217, 121901, 1, 4, 1, 0, 1, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6218, 121901, 1, 5, 1, 0, 1, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6219, 121901, 1, 101, 4, 0, 1, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6220, 121901, 1, 102, 8, 0, 1, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6221, 121901, 2, 1, 1, 0, 1, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6222, 121901, 2, 2, 1, 0, 1, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6223, 121901, 2, 3, 1, 0, 1, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6224, 121901, 2, 4, 1, 0, 1, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6225, 121901, 2, 101, 4, 0, 1, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6226, 121901, 2, 102, 8, 0, 1, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6227, 121901, 2, 1, 1, 1, 1, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6228, 121901, 2, 2, 1, 2, 1, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6229, 121901, 2, 3, 1, 3, 1, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6231, 121901, 3, 2, 1, 0, 1, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6232, 121901, 3, 3, 1, 0, 1, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6233, 121901, 3, 4, 1, 0, 1, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6234, 121901, 3, 5, 1, 0, 1, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6236, 121901, 3, 7, 1, 0, 1, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6237, 121901, 3, 8, 1, 0, 1, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6239, 122101, 1, 1, 1, 0, 1, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6240, 122101, 1, 2, 1, 0, 1, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6241, 122101, 1, 3, 1, 0, 1, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6242, 122101, 1, 4, 1, 0, 1, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6243, 122101, 1, 5, 1, 0, 1, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6244, 122101, 1, 101, 4, 0, 1, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6245, 122101, 1, 102, 8, 0, 1, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6246, 122101, 2, 1, 1, 0, 1, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6247, 122101, 2, 2, 1, 0, 1, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6248, 122101, 2, 3, 1, 0, 1, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6249, 122101, 2, 4, 1, 0, 1, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6250, 122101, 2, 101, 4, 0, 1, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6251, 122101, 2, 102, 8, 0, 1, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6252, 122101, 2, 1, 1, 1, 1, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6253, 122101, 2, 2, 1, 2, 1, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6254, 122101, 2, 3, 1, 3, 1, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6256, 122101, 3, 2, 1, 0, 1, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6257, 122101, 3, 3, 1, 0, 1, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6258, 122101, 3, 4, 1, 0, 1, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6259, 122101, 3, 5, 1, 0, 1, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6261, 122101, 3, 7, 1, 0, 1, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6262, 122101, 3, 8, 1, 0, 1, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6264, 122201, 1, 1, 1, 0, 1, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6265, 122201, 1, 2, 1, 0, 1, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6266, 122201, 1, 3, 1, 0, 1, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6267, 122201, 1, 4, 1, 0, 1, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6268, 122201, 1, 5, 1, 0, 1, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6269, 122201, 1, 101, 4, 0, 1, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6270, 122201, 1, 102, 8, 0, 1, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6271, 122201, 2, 1, 1, 0, 1, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6272, 122201, 2, 2, 1, 0, 1, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6273, 122201, 2, 3, 1, 0, 1, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6274, 122201, 2, 4, 1, 0, 1, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6275, 122201, 2, 101, 4, 0, 1, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6276, 122201, 2, 102, 8, 0, 1, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6277, 122201, 2, 1, 1, 1, 1, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6278, 122201, 2, 2, 1, 2, 1, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6279, 122201, 2, 3, 1, 3, 1, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6281, 122201, 3, 2, 1, 0, 1, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6282, 122201, 3, 3, 1, 0, 1, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6283, 122201, 3, 4, 1, 0, 1, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6284, 122201, 3, 5, 1, 0, 1, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6286, 122201, 3, 7, 1, 0, 1, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6287, 122201, 3, 8, 1, 0, 1, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6289, 100701, 1, 103, 12, 0, 1, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6290, 100701, 2, 103, 12, 0, 1, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6291, 122301, 1, 1, 1, 0, 1, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6292, 122301, 1, 2, 1, 0, 1, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6293, 122301, 1, 3, 1, 0, 1, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6294, 122301, 1, 4, 1, 0, 1, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6295, 122301, 1, 5, 1, 0, 1, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6296, 122301, 1, 101, 4, 0, 1, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6297, 122301, 1, 102, 8, 0, 1, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6298, 122301, 2, 1, 1, 0, 1, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6299, 122301, 2, 2, 1, 0, 1, "よしよし！\n今日のけんぞくも\nいい子だね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6300, 122301, 2, 3, 1, 0, 1, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6301, 122301, 2, 4, 1, 0, 1, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6302, 122301, 2, 101, 4, 0, 1, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6303, 122301, 2, 102, 8, 0, 1, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6304, 122301, 2, 1, 1, 1, 1, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6305, 122301, 2, 2, 1, 2, 1, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6306, 122301, 2, 3, 1, 3, 1, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6308, 122301, 3, 2, 1, 0, 1, "わー！\nふかふかだ！\nきもちぃー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6309, 122301, 3, 3, 1, 0, 1, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6310, 122301, 3, 4, 1, 0, 1, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6311, 122301, 3, 5, 1, 0, 1, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6313, 122301, 3, 7, 1, 0, 1, "はむはむ…\nんー！\nおいしー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6314, 122301, 3, 8, 1, 0, 1, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6316, 122501, 1, 1, 1, 0, 1, "夏といえば\nかき氷だよね\n近くにお店は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6317, 122501, 1, 2, 1, 0, 1, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6318, 122501, 1, 3, 1, 0, 1, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6319, 122501, 1, 4, 1, 0, 1, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6320, 122501, 1, 5, 1, 0, 1, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6321, 122501, 1, 101, 4, 0, 1, "終わらない夏は\nないけれど…\nつい願ってしまうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6322, 122501, 1, 102, 8, 0, 1, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6323, 122501, 2, 1, 1, 0, 1, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6324, 122501, 2, 2, 1, 0, 1, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6325, 122501, 2, 3, 1, 0, 1, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6326, 122501, 2, 4, 1, 0, 1, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6327, 122501, 2, 101, 4, 0, 1, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6328, 122501, 2, 102, 8, 0, 1, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6329, 122501, 2, 1, 1, 1, 1, "潮騒の音で\n目覚めるのも\nいいものだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6330, 122501, 2, 2, 1, 2, 1, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6331, 122501, 2, 3, 1, 3, 1, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6333, 122501, 3, 2, 1, 0, 1, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6334, 122501, 3, 3, 1, 0, 1, "肌触りのよさが\nこの格好だと\nよく分かるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6335, 122501, 3, 4, 1, 0, 1, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6336, 122501, 3, 5, 1, 0, 1, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6338, 122501, 3, 7, 1, 0, 1, "夏を楽しみきるには\nエネルギーが\n必要だからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6339, 122501, 3, 8, 1, 0, 1, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6341, 122501, 3, 10, 1, 0, 1, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6342, 122501, 3, 11, 1, 0, 1, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6343, 122401, 1, 1, 1, 0, 1, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6344, 122401, 1, 2, 1, 0, 1, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6345, 122401, 1, 3, 1, 0, 1, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6346, 122401, 1, 4, 1, 0, 1, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6347, 122401, 1, 5, 1, 0, 1, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6348, 122401, 1, 101, 4, 0, 1, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6349, 122401, 1, 102, 8, 0, 1, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6350, 122401, 2, 1, 1, 0, 1, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6351, 122401, 2, 2, 1, 0, 1, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6352, 122401, 2, 3, 1, 0, 1, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6353, 122401, 2, 4, 1, 0, 1, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6354, 122401, 2, 101, 4, 0, 1, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6355, 122401, 2, 102, 8, 0, 1, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6356, 122401, 2, 1, 1, 1, 1, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6357, 122401, 2, 2, 1, 2, 1, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6358, 122401, 2, 3, 1, 3, 1, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6360, 122401, 3, 2, 1, 0, 1, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6361, 122401, 3, 3, 1, 0, 1, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6362, 122401, 3, 4, 1, 0, 1, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6363, 122401, 3, 5, 1, 0, 1, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6365, 122401, 3, 7, 1, 0, 1, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6366, 122401, 3, 8, 1, 0, 1, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6368, 122401, 3, 10, 1, 0, 1, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6369, 122401, 3, 11, 1, 0, 1, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6370, 101501, 1, 103, 12, 0, 1, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6371, 101501, 2, 103, 12, 0, 1, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6372, 122601, 1, 1, 1, 0, 1, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6373, 122601, 1, 2, 1, 0, 1, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6374, 122601, 1, 3, 1, 0, 1, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6375, 122601, 1, 4, 1, 0, 1, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6376, 122601, 1, 5, 1, 0, 1, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6377, 122601, 1, 101, 4, 0, 1, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6378, 122601, 1, 102, 8, 0, 1, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6379, 122601, 2, 1, 1, 0, 1, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6380, 122601, 2, 2, 1, 0, 1, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6381, 122601, 2, 3, 1, 0, 1, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6382, 122601, 2, 4, 1, 0, 1, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6383, 122601, 2, 101, 4, 0, 1, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6384, 122601, 2, 102, 8, 0, 1, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6385, 122601, 2, 1, 1, 1, 1, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6386, 122601, 2, 2, 1, 2, 1, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6387, 122601, 2, 3, 1, 3, 1, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6389, 122601, 3, 2, 1, 0, 1, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6390, 122601, 3, 3, 1, 0, 1, "かわいい～♪\n日光浴させて\nあげたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6391, 122601, 3, 4, 1, 0, 1, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6392, 122601, 3, 5, 1, 0, 1, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6394, 122601, 3, 7, 1, 0, 1, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6395, 122601, 3, 8, 1, 0, 1, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6397, 122601, 3, 10, 1, 0, 1, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6398, 122601, 3, 11, 1, 0, 1, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6399, 122901, 1, 1, 1, 0, 1, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6400, 122901, 1, 2, 1, 0, 1, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6401, 122901, 1, 3, 1, 0, 1, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6402, 122901, 1, 4, 1, 0, 1, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6403, 122901, 1, 5, 1, 0, 1, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6404, 122901, 1, 101, 4, 0, 1, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6405, 122901, 1, 102, 8, 0, 1, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6406, 122901, 2, 1, 1, 0, 1, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6407, 122901, 2, 2, 1, 0, 1, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6408, 122901, 2, 3, 1, 0, 1, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6409, 122901, 2, 4, 1, 0, 1, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6410, 122901, 2, 101, 4, 0, 1, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6411, 122901, 2, 102, 8, 0, 1, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6412, 122901, 2, 1, 1, 1, 1, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6413, 122901, 2, 2, 1, 2, 1, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6414, 122901, 2, 3, 1, 3, 1, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6416, 122901, 3, 2, 1, 0, 1, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6417, 122901, 3, 3, 1, 0, 1, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6418, 122901, 3, 4, 1, 0, 1, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6419, 122901, 3, 5, 1, 0, 1, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6421, 122901, 3, 7, 1, 0, 1, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6422, 122901, 3, 8, 1, 0, 1, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6424, 122901, 3, 10, 1, 0, 1, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6425, 122901, 3, 11, 1, 0, 1, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6426, 122801, 1, 1, 1, 0, 1, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6427, 122801, 1, 2, 1, 0, 1, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6428, 122801, 1, 3, 1, 0, 1, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6429, 122801, 1, 4, 1, 0, 1, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6430, 122801, 1, 5, 1, 0, 1, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6431, 122801, 1, 101, 4, 0, 1, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6432, 122801, 1, 102, 8, 0, 1, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6433, 122801, 2, 1, 1, 0, 1, "にいちゃん！\n早く海に行く準備\nしなよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6434, 122801, 2, 2, 1, 0, 1, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6435, 122801, 2, 3, 1, 0, 1, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6436, 122801, 2, 4, 1, 0, 1, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6437, 122801, 2, 101, 4, 0, 1, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6438, 122801, 2, 102, 8, 0, 1, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6439, 122801, 2, 1, 1, 1, 1, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6440, 122801, 2, 2, 1, 2, 1, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6441, 122801, 2, 3, 1, 3, 1, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6443, 122801, 3, 2, 1, 0, 1, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6444, 122801, 3, 3, 1, 0, 1, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6445, 122801, 3, 4, 1, 0, 1, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6446, 122801, 3, 5, 1, 0, 1, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6448, 122801, 3, 7, 1, 0, 1, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6449, 122801, 3, 8, 1, 0, 1, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6451, 122801, 3, 10, 1, 0, 1, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6452, 122801, 3, 11, 1, 0, 1, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6453, 102701, 1, 103, 12, 0, 1, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6454, 102701, 2, 103, 12, 0, 1, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6455, 122701, 1, 1, 1, 0, 1, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6456, 122701, 1, 2, 1, 0, 1, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6457, 122701, 1, 3, 1, 0, 1, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6458, 122701, 1, 4, 1, 0, 1, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6459, 122701, 1, 5, 1, 0, 1, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6460, 122701, 1, 101, 4, 0, 1, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6461, 122701, 1, 102, 8, 0, 1, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6462, 122701, 2, 1, 1, 0, 1, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6463, 122701, 2, 2, 1, 0, 1, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6464, 122701, 2, 3, 1, 0, 1, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6465, 122701, 2, 4, 1, 0, 1, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6466, 122701, 2, 101, 4, 0, 1, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6467, 122701, 2, 102, 8, 0, 1, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6468, 122701, 2, 1, 1, 1, 1, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6469, 122701, 2, 2, 1, 2, 1, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6470, 122701, 2, 3, 1, 3, 1, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6472, 122701, 3, 2, 1, 0, 1, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6473, 122701, 3, 3, 1, 0, 1, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6474, 122701, 3, 4, 1, 0, 1, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6475, 122701, 3, 5, 1, 0, 1, "濡れた体を\n乾かしましょう\nタオル、タオル…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6477, 122701, 3, 7, 1, 0, 1, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6478, 122701, 3, 8, 1, 0, 1, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6480, 122701, 3, 10, 1, 0, 1, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6481, 122701, 3, 11, 1, 0, 1, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6482, 123001, 1, 1, 1, 0, 1, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6483, 123001, 1, 2, 1, 0, 1, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6484, 123001, 1, 3, 1, 0, 1, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6485, 123001, 1, 4, 1, 0, 1, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6486, 123001, 1, 5, 1, 0, 1, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6487, 123001, 1, 101, 4, 0, 1, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6488, 123001, 1, 102, 8, 0, 1, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6489, 123001, 2, 1, 1, 0, 1, "おいしい店とか\n知らないの？\n食べに行きましょ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6490, 123001, 2, 2, 1, 0, 1, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6491, 123001, 2, 3, 1, 0, 1, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6492, 123001, 2, 4, 1, 0, 1, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6493, 123001, 2, 101, 4, 0, 1, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6494, 123001, 2, 102, 8, 0, 1, "どこまでだって\nあんたと一緒に\nいてやるんだから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6495, 123001, 2, 1, 1, 1, 1, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6496, 123001, 2, 2, 1, 2, 1, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6497, 123001, 2, 3, 1, 3, 1, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6499, 123001, 3, 2, 1, 0, 1, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6500, 123001, 3, 3, 1, 0, 1, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6501, 123001, 3, 4, 1, 0, 1, "いい香りね\n塔にもこういうの\n置いておこうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6502, 123001, 3, 5, 1, 0, 1, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6504, 123001, 3, 7, 1, 0, 1, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6505, 123001, 3, 8, 1, 0, 1, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6507, 123101, 1, 1, 1, 0, 1, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6508, 123101, 1, 2, 1, 0, 1, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6509, 123101, 1, 3, 1, 0, 1, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6510, 123101, 1, 4, 1, 0, 1, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6511, 123101, 1, 5, 1, 0, 1, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6512, 123101, 1, 101, 4, 0, 1, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6513, 123101, 1, 102, 8, 0, 1, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6514, 123101, 2, 1, 1, 0, 1, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6515, 123101, 2, 2, 1, 0, 1, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6516, 123101, 2, 3, 1, 0, 1, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6517, 123101, 2, 4, 1, 0, 1, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6518, 123101, 2, 101, 4, 0, 1, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6519, 123101, 2, 102, 8, 0, 1, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6520, 123101, 2, 1, 1, 1, 1, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6521, 123101, 2, 2, 1, 2, 1, "支度が終わったら\n今日の冒険に\n出かけまひょか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6522, 123101, 2, 3, 1, 3, 1, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6524, 123101, 3, 2, 1, 0, 1, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6525, 123101, 3, 3, 1, 0, 1, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6526, 123101, 3, 4, 1, 0, 1, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6527, 123101, 3, 5, 1, 0, 1, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6529, 123101, 3, 7, 1, 0, 1, "この料理…\n冒険するときに\n作ってみまひょか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6530, 123101, 3, 8, 1, 0, 1, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6532, 103101, 1, 103, 12, 0, 1, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6533, 103101, 2, 103, 12, 0, 1, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6534, 104401, 1, 103, 12, 0, 1, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6535, 104401, 2, 103, 12, 0, 1, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6536, 123201, 1, 1, 1, 0, 1, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6537, 123201, 1, 2, 1, 0, 1, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6538, 123201, 1, 3, 1, 0, 1, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6539, 123201, 1, 4, 1, 0, 1, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6540, 123201, 1, 5, 1, 0, 1, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6541, 123201, 1, 101, 4, 0, 1, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6542, 123201, 1, 102, 8, 0, 1, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6543, 123201, 2, 1, 1, 0, 1, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6544, 123201, 2, 2, 1, 0, 1, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6545, 123201, 2, 3, 1, 0, 1, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6546, 123201, 2, 4, 1, 0, 1, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6547, 123201, 2, 101, 4, 0, 1, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6548, 123201, 2, 102, 8, 0, 1, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6549, 123201, 2, 1, 1, 1, 1, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6550, 123201, 2, 2, 1, 2, 1, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6551, 123201, 2, 3, 1, 3, 1, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6553, 123201, 3, 2, 1, 0, 1, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6554, 123201, 3, 3, 1, 0, 1, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6555, 123201, 3, 4, 1, 0, 1, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6556, 123201, 3, 5, 1, 0, 1, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6558, 123201, 3, 7, 1, 0, 1, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6559, 123201, 3, 8, 1, 0, 1, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6561, 123301, 1, 1, 1, 0, 1, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6562, 123301, 1, 2, 1, 0, 1, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6563, 123301, 1, 3, 1, 0, 1, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6564, 123301, 1, 4, 1, 0, 1, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6565, 123301, 1, 5, 1, 0, 1, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6566, 123301, 1, 101, 4, 0, 1, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6567, 123301, 1, 102, 8, 0, 1, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6568, 123301, 2, 1, 1, 0, 1, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6569, 123301, 2, 2, 1, 0, 1, "んー？\n今はだるいからパース\nあとでねー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6570, 123301, 2, 3, 1, 0, 1, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6571, 123301, 2, 4, 1, 0, 1, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6572, 123301, 2, 101, 4, 0, 1, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6573, 123301, 2, 102, 8, 0, 1, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6574, 123301, 2, 1, 1, 1, 1, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6575, 123301, 2, 2, 1, 2, 1, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6576, 123301, 2, 3, 1, 3, 1, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6578, 123301, 3, 2, 1, 0, 1, "あーラクー\nいっしょーここで\nキューケーできるわー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6579, 123301, 3, 3, 1, 0, 1, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6580, 123301, 3, 4, 1, 0, 1, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6581, 123301, 3, 5, 1, 0, 1, "あったか～♪\n溶けてスライム\n戻りそ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6583, 123301, 3, 7, 1, 0, 1, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6584, 123301, 3, 8, 1, 0, 1, "わ～なんかムズー\n読めなー\nウケるー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6586, 123501, 1, 1, 1, 0, 1, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6587, 123501, 1, 2, 1, 0, 1, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6588, 123501, 1, 3, 1, 0, 1, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6589, 123501, 1, 4, 1, 0, 1, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6590, 123501, 1, 5, 1, 0, 1, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6591, 123501, 1, 101, 4, 0, 1, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6592, 123501, 1, 102, 8, 0, 1, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6593, 123501, 2, 1, 1, 0, 1, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6594, 123501, 2, 2, 1, 0, 1, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6595, 123501, 2, 3, 1, 0, 1, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6596, 123501, 2, 4, 1, 0, 1, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6597, 123501, 2, 101, 4, 0, 1, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6598, 123501, 2, 102, 8, 0, 1, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6599, 123501, 2, 1, 1, 1, 1, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6600, 123501, 2, 2, 1, 2, 1, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6601, 123501, 2, 3, 1, 3, 1, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6603, 123501, 3, 2, 1, 0, 1, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6604, 123501, 3, 3, 1, 0, 1, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6605, 123501, 3, 4, 1, 0, 1, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6606, 123501, 3, 5, 1, 0, 1, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6608, 123501, 3, 7, 1, 0, 1, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6609, 123501, 3, 8, 1, 0, 1, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6611, 123501, 3, 12, 1, 0, 1, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6613, 123601, 1, 1, 1, 0, 1, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6614, 123601, 1, 2, 1, 0, 1, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6615, 123601, 1, 3, 1, 0, 1, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6616, 123601, 1, 4, 1, 0, 1, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6617, 123601, 1, 5, 1, 0, 1, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6618, 123601, 1, 101, 4, 0, 1, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6619, 123601, 1, 102, 8, 0, 1, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6620, 123601, 2, 1, 1, 0, 1, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6621, 123601, 2, 2, 1, 0, 1, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6622, 123601, 2, 3, 1, 0, 1, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6623, 123601, 2, 4, 1, 0, 1, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6624, 123601, 2, 101, 4, 0, 1, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6625, 123601, 2, 102, 8, 0, 1, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6626, 123601, 2, 1, 1, 1, 1, "おはよう！\n今日も朝から\nパトロールに行こう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6627, 123601, 2, 2, 1, 2, 1, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6628, 123601, 2, 3, 1, 3, 1, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6630, 123601, 3, 2, 1, 0, 1, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6631, 123601, 3, 3, 1, 0, 1, "…かわいい\nこの子もパレードに\n連れて行きたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6632, 123601, 3, 4, 1, 0, 1, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6633, 123601, 3, 5, 1, 0, 1, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6635, 123601, 3, 7, 1, 0, 1, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6636, 123601, 3, 8, 1, 0, 1, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6638, 123601, 3, 12, 1, 0, 1, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6640, 103701, 1, 103, 12, 0, 1, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6641, 103701, 2, 103, 12, 0, 1, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6642, 123701, 1, 1, 1, 0, 1, "やっぱり\nコスプレしてると\nテンションあがる～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6643, 123701, 1, 2, 1, 0, 1, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6644, 123701, 1, 3, 1, 0, 1, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6645, 123701, 1, 4, 1, 0, 1, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6646, 123701, 1, 5, 1, 0, 1, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6647, 123701, 1, 101, 4, 0, 1, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6648, 123701, 1, 102, 8, 0, 1, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6649, 123701, 2, 1, 1, 0, 1, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6650, 123701, 2, 2, 1, 0, 1, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6651, 123701, 2, 3, 1, 0, 1, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6652, 123701, 2, 4, 1, 0, 1, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6653, 123701, 2, 101, 4, 0, 1, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6654, 123701, 2, 102, 8, 0, 1, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6655, 123701, 2, 1, 1, 1, 1, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6656, 123701, 2, 2, 1, 2, 1, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6657, 123701, 2, 3, 1, 3, 1, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6659, 123701, 3, 2, 1, 0, 1, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6660, 123701, 3, 3, 1, 0, 1, "いや～\nかわいいものをみると\n癒されますな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6661, 123701, 3, 4, 1, 0, 1, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6662, 123701, 3, 5, 1, 0, 1, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6664, 123701, 3, 7, 1, 0, 1, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6665, 123701, 3, 8, 1, 0, 1, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6667, 123701, 3, 12, 1, 0, 1, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6669, 123801, 1, 1, 1, 0, 1, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6670, 123801, 1, 2, 1, 0, 1, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6671, 123801, 1, 3, 1, 0, 1, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6672, 123801, 1, 4, 1, 0, 1, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6673, 123801, 1, 5, 1, 0, 1, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6674, 123801, 1, 101, 4, 0, 1, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6675, 123801, 1, 102, 8, 0, 1, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6676, 123801, 2, 1, 1, 0, 1, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6677, 123801, 2, 2, 1, 0, 1, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6678, 123801, 2, 3, 1, 0, 1, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6679, 123801, 2, 4, 1, 0, 1, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6680, 123801, 2, 101, 4, 0, 1, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6681, 123801, 2, 102, 8, 0, 1, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6682, 123801, 2, 1, 1, 1, 1, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6683, 123801, 2, 2, 1, 2, 1, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6684, 123801, 2, 3, 1, 3, 1, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6686, 123801, 3, 2, 1, 0, 1, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6687, 123801, 3, 3, 1, 0, 1, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6688, 123801, 3, 4, 1, 0, 1, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6689, 123801, 3, 5, 1, 0, 1, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6691, 123801, 3, 7, 1, 0, 1, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6692, 123801, 3, 8, 1, 0, 1, "ウサギ族は地獄耳？\nワタシとの共通点だな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6694, 106401, 1, 103, 12, 0, 1, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6695, 106401, 2, 103, 12, 0, 1, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6696, 123901, 1, 1, 1, 0, 1, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6697, 123901, 1, 2, 1, 0, 1, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6698, 123901, 1, 3, 1, 0, 1, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6699, 123901, 1, 4, 1, 0, 1, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6700, 123901, 1, 5, 1, 0, 1, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6701, 123901, 1, 101, 4, 0, 1, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6702, 123901, 1, 102, 8, 0, 1, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6703, 123901, 2, 1, 1, 0, 1, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6704, 123901, 2, 2, 1, 0, 1, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6705, 123901, 2, 3, 1, 0, 1, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6706, 123901, 2, 4, 1, 0, 1, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6707, 123901, 2, 101, 4, 0, 1, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6708, 123901, 2, 102, 8, 0, 1, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6709, 123901, 2, 1, 1, 1, 1, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6710, 123901, 2, 2, 1, 2, 1, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6711, 123901, 2, 3, 1, 3, 1, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6713, 123901, 3, 2, 1, 0, 1, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6714, 123901, 3, 3, 1, 0, 1, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6715, 123901, 3, 4, 1, 0, 1, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6716, 123901, 3, 5, 1, 0, 1, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6718, 123901, 3, 7, 1, 0, 1, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6719, 123901, 3, 8, 1, 0, 1, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6721, 106901, 1, 1, 1, 0, 1, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6722, 106901, 1, 2, 1, 0, 1, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6723, 106901, 1, 3, 1, 0, 1, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6724, 106901, 1, 4, 1, 0, 1, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6725, 106901, 1, 5, 1, 0, 1, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6726, 106901, 1, 101, 4, 0, 1, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6727, 106901, 1, 102, 8, 0, 1, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6728, 106901, 2, 1, 1, 0, 1, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6729, 106901, 2, 2, 1, 0, 1, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6730, 106901, 2, 3, 1, 0, 1, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6731, 106901, 2, 4, 1, 0, 1, "視力は権能に\n影響しないわよ\n当たり前じゃない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6732, 106901, 2, 101, 4, 0, 1, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6733, 106901, 2, 102, 8, 0, 1, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6734, 106901, 2, 1, 1, 1, 1, "人生朝露…\n悔いのない一日を\n過ごしなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6735, 106901, 2, 2, 1, 2, 1, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6736, 106901, 2, 3, 1, 3, 1, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6738, 106901, 3, 2, 1, 0, 1, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6739, 106901, 3, 3, 1, 0, 1, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6740, 106901, 3, 4, 1, 0, 1, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6741, 106901, 3, 5, 1, 0, 1, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6743, 106901, 3, 7, 1, 0, 1, "栄養価はともかく\n味のバランスは\n悪くないわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6744, 106901, 3, 8, 1, 0, 1, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6746, 124001, 1, 1, 1, 0, 1, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6747, 124001, 1, 2, 1, 0, 1, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6748, 124001, 1, 3, 1, 0, 1, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6749, 124001, 1, 4, 1, 0, 1, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6750, 124001, 1, 5, 1, 0, 1, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6751, 124001, 1, 101, 4, 0, 1, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6752, 124001, 1, 102, 8, 0, 1, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6753, 124001, 2, 1, 1, 0, 1, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6754, 124001, 2, 2, 1, 0, 1, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6755, 124001, 2, 3, 1, 0, 1, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6756, 124001, 2, 4, 1, 0, 1, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6757, 124001, 2, 101, 4, 0, 1, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6758, 124001, 2, 102, 8, 0, 1, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6759, 124001, 2, 1, 1, 1, 1, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6760, 124001, 2, 2, 1, 2, 1, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6761, 124001, 2, 3, 1, 3, 1, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6763, 124001, 3, 2, 1, 0, 1, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6764, 124001, 3, 3, 1, 0, 1, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6765, 124001, 3, 4, 1, 0, 1, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6766, 124001, 3, 5, 1, 0, 1, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6768, 124001, 3, 7, 1, 0, 1, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6769, 124001, 3, 8, 1, 0, 1, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6771, 124001, 3, 14, 1, 0, 1, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6772, 124001, 3, 15, 1, 0, 1, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6773, 124101, 1, 1, 1, 0, 1, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6774, 124101, 1, 2, 1, 0, 1, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6775, 124101, 1, 3, 1, 0, 1, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6776, 124101, 1, 4, 1, 0, 1, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6777, 124101, 1, 5, 1, 0, 1, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6778, 124101, 1, 101, 4, 0, 1, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6779, 124101, 1, 102, 8, 0, 1, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6780, 124101, 2, 1, 1, 0, 1, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6781, 124101, 2, 2, 1, 0, 1, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6782, 124101, 2, 3, 1, 0, 1, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6783, 124101, 2, 4, 1, 0, 1, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6784, 124101, 2, 101, 4, 0, 1, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6785, 124101, 2, 102, 8, 0, 1, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6786, 124101, 2, 1, 1, 1, 1, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6787, 124101, 2, 2, 1, 2, 1, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6788, 124101, 2, 3, 1, 3, 1, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6790, 124101, 3, 2, 1, 0, 1, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6791, 124101, 3, 3, 1, 0, 1, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6792, 124101, 3, 4, 1, 0, 1, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6793, 124101, 3, 5, 1, 0, 1, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6795, 124101, 3, 7, 1, 0, 1, "お腹いっぱい\n食べられるのも\n幸せなことよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6796, 124101, 3, 8, 1, 0, 1, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6798, 124101, 3, 14, 1, 0, 1, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6799, 124101, 3, 15, 1, 0, 1, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6800, 105101, 1, 103, 12, 0, 1, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6801, 105101, 2, 103, 12, 0, 1, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6802, 124201, 1, 1, 1, 0, 1, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6803, 124201, 1, 2, 1, 0, 1, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6804, 124201, 1, 3, 1, 0, 1, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6805, 124201, 1, 4, 1, 0, 1, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6806, 124201, 1, 5, 1, 0, 1, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6807, 124201, 1, 101, 4, 0, 1, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6808, 124201, 1, 102, 8, 0, 1, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6809, 124201, 2, 1, 1, 0, 1, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6810, 124201, 2, 2, 1, 0, 1, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6811, 124201, 2, 3, 1, 0, 1, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6812, 124201, 2, 4, 1, 0, 1, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6813, 124201, 2, 101, 4, 0, 1, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6814, 124201, 2, 102, 8, 0, 1, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6815, 124201, 2, 1, 1, 1, 1, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6816, 124201, 2, 2, 1, 2, 1, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6817, 124201, 2, 3, 1, 3, 1, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6819, 124201, 3, 2, 1, 0, 1, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6820, 124201, 3, 3, 1, 0, 1, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6821, 124201, 3, 4, 1, 0, 1, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6822, 124201, 3, 5, 1, 0, 1, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6824, 124201, 3, 7, 1, 0, 1, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6825, 124201, 3, 8, 1, 0, 1, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6827, 124201, 3, 14, 1, 0, 1, "わっ！\n…………いや\n驚いてなどいない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6828, 124201, 3, 15, 1, 0, 1, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6829, 124301, 1, 1, 1, 0, 1, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6830, 124301, 1, 2, 1, 0, 1, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6831, 124301, 1, 3, 1, 0, 1, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6832, 124301, 1, 4, 1, 0, 1, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6833, 124301, 1, 5, 1, 0, 1, "グレアも誘って\nランドソルをもっと\n探索したい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6834, 124301, 1, 101, 4, 0, 1, "私たちも\nあの人を見習って\nバイトとかしよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6835, 124301, 1, 102, 8, 0, 1, "いつかあの人を\n私たちの学院に\n招待したいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6836, 124301, 2, 1, 1, 0, 1, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6837, 124301, 2, 2, 1, 0, 1, "これからなにするの？\n私たちもついて行って\nいいかな！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6838, 124301, 2, 3, 1, 0, 1, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6839, 124301, 2, 4, 1, 0, 1, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6840, 124301, 2, 101, 4, 0, 1, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6841, 124301, 2, 102, 8, 0, 1, "あなたは\nグレアと私の友達！\nこれからもずっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6842, 124301, 2, 1, 1, 1, 1, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6843, 124301, 2, 2, 1, 2, 1, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6844, 124301, 2, 3, 1, 3, 1, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6846, 124301, 3, 2, 1, 0, 1, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6847, 124301, 3, 3, 1, 0, 1, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6848, 124301, 3, 4, 1, 0, 1, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6849, 124301, 3, 5, 1, 0, 1, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6851, 124301, 3, 7, 1, 0, 1, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6852, 124301, 3, 8, 1, 0, 1, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6855, 124401, 1, 1, 1, 0, 1, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6856, 124401, 1, 2, 1, 0, 1, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6857, 124401, 1, 3, 1, 0, 1, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6858, 124401, 1, 4, 1, 0, 1, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6859, 124401, 1, 5, 1, 0, 1, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6860, 124401, 1, 101, 4, 0, 1, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6861, 124401, 1, 102, 8, 0, 1, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6862, 124401, 2, 1, 1, 0, 1, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6863, 124401, 2, 2, 1, 0, 1, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6864, 124401, 2, 3, 1, 0, 1, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6865, 124401, 2, 4, 1, 0, 1, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6866, 124401, 2, 101, 4, 0, 1, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6867, 124401, 2, 102, 8, 0, 1, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6868, 124401, 2, 1, 1, 1, 1, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6869, 124401, 2, 2, 1, 2, 1, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6870, 124401, 2, 3, 1, 3, 1, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6872, 124401, 3, 2, 1, 0, 1, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6873, 124401, 3, 3, 1, 0, 1, "これ…\nアンに見せたら\n喜ぶだろうな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6874, 124401, 3, 4, 1, 0, 1, "いい香り…\nアンの感想も\n聞いてみたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6875, 124401, 3, 5, 1, 0, 1, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6877, 124401, 3, 7, 1, 0, 1, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6878, 124401, 3, 8, 1, 0, 1, "アンがおススメ\nしてくれた本は\nこれかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6881, 124701, 1, 1, 1, 0, 1, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6882, 124701, 1, 2, 1, 0, 1, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6883, 124701, 1, 3, 1, 0, 1, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6884, 124701, 1, 4, 1, 0, 1, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6885, 124701, 1, 5, 1, 0, 1, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6886, 124701, 1, 101, 4, 0, 1, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6887, 124701, 1, 102, 8, 0, 1, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6888, 124701, 2, 1, 1, 0, 1, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6889, 124701, 2, 2, 1, 0, 1, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6890, 124701, 2, 3, 1, 0, 1, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6891, 124701, 2, 4, 1, 0, 1, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6892, 124701, 2, 101, 4, 0, 1, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6893, 124701, 2, 102, 8, 0, 1, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6894, 124701, 2, 1, 1, 1, 1, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6895, 124701, 2, 2, 1, 2, 1, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6896, 124701, 2, 3, 1, 3, 1, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6898, 124701, 3, 2, 1, 0, 1, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6899, 124701, 3, 3, 1, 0, 1, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6900, 124701, 3, 4, 1, 0, 1, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6901, 124701, 3, 5, 1, 0, 1, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6903, 124701, 3, 7, 1, 0, 1, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6904, 124701, 3, 8, 1, 0, 1, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6906, 124601, 1, 1, 1, 0, 1, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6907, 124601, 1, 2, 1, 0, 1, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6908, 124601, 1, 3, 1, 0, 1, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6909, 124601, 1, 4, 1, 0, 1, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6910, 124601, 1, 5, 1, 0, 1, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6911, 124601, 1, 101, 4, 0, 1, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6912, 124601, 1, 102, 8, 0, 1, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6913, 124601, 2, 1, 1, 0, 1, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6914, 124601, 2, 2, 1, 0, 1, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6915, 124601, 2, 3, 1, 0, 1, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6916, 124601, 2, 4, 1, 0, 1, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6917, 124601, 2, 101, 4, 0, 1, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6918, 124601, 2, 102, 8, 0, 1, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6919, 124601, 2, 1, 1, 1, 1, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6920, 124601, 2, 2, 1, 2, 1, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6921, 124601, 2, 3, 1, 3, 1, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6923, 124601, 3, 2, 1, 0, 1, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6924, 124601, 3, 3, 1, 0, 1, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6925, 124601, 3, 4, 1, 0, 1, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6926, 124601, 3, 5, 1, 0, 1, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6928, 124601, 3, 7, 1, 0, 1, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6929, 124601, 3, 8, 1, 0, 1, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6931, 104701, 1, 103, 12, 0, 1, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6932, 104701, 2, 103, 12, 0, 1, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6933, 124501, 1, 1, 1, 0, 1, "お正月だからって\nギャングのボスは\n油断できないよ～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6934, 124501, 1, 2, 1, 0, 1, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6935, 124501, 1, 3, 1, 0, 1, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6936, 124501, 1, 4, 1, 0, 1, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6937, 124501, 1, 5, 1, 0, 1, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6938, 124501, 1, 101, 4, 0, 1, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6939, 124501, 1, 102, 8, 0, 1, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6940, 124501, 2, 1, 1, 0, 1, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6941, 124501, 2, 2, 1, 0, 1, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6942, 124501, 2, 3, 1, 0, 1, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6943, 124501, 2, 4, 1, 0, 1, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6944, 124501, 2, 101, 4, 0, 1, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6945, 124501, 2, 102, 8, 0, 1, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6946, 124501, 2, 1, 1, 1, 1, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6947, 124501, 2, 2, 1, 2, 1, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6948, 124501, 2, 3, 1, 3, 1, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6950, 124501, 3, 2, 1, 0, 1, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6951, 124501, 3, 3, 1, 0, 1, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6952, 124501, 3, 4, 1, 0, 1, "いい香りだけど\n着物に移らないように\n気をつけないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6953, 124501, 3, 5, 1, 0, 1, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6955, 124501, 3, 7, 1, 0, 1, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6956, 124501, 3, 8, 1, 0, 1, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6958, 124801, 1, 1, 1, 0, 1, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6959, 124801, 1, 2, 1, 0, 1, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6960, 124801, 1, 3, 1, 0, 1, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6961, 124801, 1, 4, 1, 0, 1, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6962, 124801, 1, 5, 1, 0, 1, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6963, 124801, 1, 101, 4, 0, 1, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6964, 124801, 1, 102, 8, 0, 1, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6965, 124801, 2, 1, 1, 0, 1, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6966, 124801, 2, 2, 1, 0, 1, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6967, 124801, 2, 3, 1, 0, 1, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6968, 124801, 2, 4, 1, 0, 1, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6969, 124801, 2, 101, 4, 0, 1, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6970, 124801, 2, 102, 8, 0, 1, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6971, 124801, 2, 1, 1, 1, 1, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6972, 124801, 2, 2, 1, 2, 1, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6973, 124801, 2, 3, 1, 3, 1, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6975, 124801, 3, 2, 1, 0, 1, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6976, 124801, 3, 3, 1, 0, 1, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6977, 124801, 3, 4, 1, 0, 1, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6978, 124801, 3, 5, 1, 0, 1, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6980, 124801, 3, 7, 1, 0, 1, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6981, 124801, 3, 8, 1, 0, 1, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6983, 124901, 1, 1, 1, 0, 1, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6984, 124901, 1, 2, 1, 0, 1, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6985, 124901, 1, 3, 1, 0, 1, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6986, 124901, 1, 4, 1, 0, 1, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6987, 124901, 1, 5, 1, 0, 1, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6988, 124901, 1, 101, 4, 0, 1, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6989, 124901, 1, 102, 8, 0, 1, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6990, 124901, 2, 1, 1, 0, 1, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6991, 124901, 2, 2, 1, 0, 1, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6992, 124901, 2, 3, 1, 0, 1, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6993, 124901, 2, 4, 1, 0, 1, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6994, 124901, 2, 101, 4, 0, 1, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6995, 124901, 2, 102, 8, 0, 1, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6996, 124901, 2, 1, 1, 1, 1, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6997, 124901, 2, 2, 1, 2, 1, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (6998, 124901, 2, 3, 1, 3, 1, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7000, 124901, 3, 2, 1, 0, 1, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7001, 124901, 3, 3, 1, 0, 1, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7002, 124901, 3, 4, 1, 0, 1, "嗅いだことない\n香りだな\nなかなか気に入った！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7003, 124901, 3, 5, 1, 0, 1, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7005, 124901, 3, 7, 1, 0, 1, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7006, 124901, 3, 8, 1, 0, 1, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7008, 105501, 1, 103, 12, 0, 1, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7009, 105501, 2, 103, 12, 0, 1, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7010, 125001, 1, 1, 1, 0, 1, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7011, 125001, 1, 2, 1, 0, 1, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7012, 125001, 1, 3, 1, 0, 1, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7013, 125001, 1, 4, 1, 0, 1, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7014, 125001, 1, 5, 1, 0, 1, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7015, 125001, 1, 101, 4, 0, 1, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7016, 125001, 1, 102, 8, 0, 1, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7017, 125001, 2, 1, 1, 0, 1, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7018, 125001, 2, 2, 1, 0, 1, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7019, 125001, 2, 3, 1, 0, 1, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7020, 125001, 2, 4, 1, 0, 1, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7021, 125001, 2, 101, 4, 0, 1, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7022, 125001, 2, 102, 8, 0, 1, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7023, 125001, 2, 1, 1, 1, 1, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7024, 125001, 2, 2, 1, 2, 1, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7025, 125001, 2, 3, 1, 3, 1, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7027, 125001, 3, 2, 1, 0, 1, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7028, 125001, 3, 3, 1, 0, 1, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7029, 125001, 3, 4, 1, 0, 1, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7030, 125001, 3, 5, 1, 0, 1, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7032, 125001, 3, 7, 1, 0, 1, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7033, 125001, 3, 8, 1, 0, 1, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7035, 118201, 1, 1, 1, 0, 1, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7036, 118201, 1, 2, 1, 0, 1, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7037, 118201, 1, 3, 1, 0, 1, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7038, 118201, 1, 4, 1, 0, 1, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7039, 118201, 1, 5, 1, 0, 1, "オトモダチでも\n作ってみますかー\nくすっ★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7040, 118201, 1, 101, 4, 0, 1, "退屈ですねー\n騎士さん、構って\nくれないかなー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7041, 118201, 1, 102, 8, 0, 1, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7042, 118201, 2, 1, 1, 0, 1, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7043, 118201, 2, 2, 1, 0, 1, "お出かけします？\nそれともおうちで\nまったりしますー？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7044, 118201, 2, 3, 1, 0, 1, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7045, 118201, 2, 4, 1, 0, 1, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7046, 118201, 2, 101, 4, 0, 1, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7047, 118201, 2, 102, 8, 0, 1, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7048, 118201, 2, 1, 1, 1, 1, "おはようございます★\nあれれー？\nまだおねむですかー？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7049, 118201, 2, 2, 1, 2, 1, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7050, 118201, 2, 3, 1, 3, 1, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7052, 118201, 3, 2, 1, 0, 1, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7053, 118201, 3, 3, 1, 0, 1, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7054, 118201, 3, 4, 1, 0, 1, "くんくん★\nうーん…\nたぶん素敵な香り♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7055, 118201, 3, 5, 1, 0, 1, "あったかーい★\n心も体も\nぽっかぽかですねー★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7057, 118201, 3, 7, 1, 0, 1, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7058, 118201, 3, 8, 1, 0, 1, "へー★\nほほー★\nふんふーん★", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7060, 125101, 1, 1, 1, 0, 1, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7061, 125101, 1, 2, 1, 0, 1, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7062, 125101, 1, 3, 1, 0, 1, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7063, 125101, 1, 4, 1, 0, 1, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7064, 125101, 1, 5, 1, 0, 1, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7065, 125101, 1, 101, 4, 0, 1, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7066, 125101, 1, 102, 8, 0, 1, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7067, 125101, 2, 1, 1, 0, 1, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7068, 125101, 2, 2, 1, 0, 1, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7069, 125101, 2, 3, 1, 0, 1, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7070, 125101, 2, 4, 1, 0, 1, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7071, 125101, 2, 101, 4, 0, 1, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7072, 125101, 2, 102, 8, 0, 1, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7073, 125101, 2, 1, 1, 1, 1, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7074, 125101, 2, 2, 1, 2, 1, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7075, 125101, 2, 3, 1, 3, 1, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7077, 125101, 3, 2, 1, 0, 1, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7078, 125101, 3, 3, 1, 0, 1, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7079, 125101, 3, 4, 1, 0, 1, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7080, 125101, 3, 5, 1, 0, 1, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7082, 125101, 3, 7, 1, 0, 1, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7083, 125101, 3, 8, 1, 0, 1, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7085, 107101, 1, 103, 12, 0, 1, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7086, 107101, 2, 103, 12, 0, 1, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7087, 125201, 1, 1, 1, 0, 1, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7088, 125201, 1, 2, 1, 0, 1, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7089, 125201, 1, 3, 1, 0, 1, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7090, 125201, 1, 4, 1, 0, 1, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7091, 125201, 1, 5, 1, 0, 1, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7092, 125201, 1, 101, 4, 0, 1, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7093, 125201, 1, 102, 8, 0, 1, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7094, 125201, 2, 1, 1, 0, 1, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7095, 125201, 2, 2, 1, 0, 1, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7096, 125201, 2, 3, 1, 0, 1, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7097, 125201, 2, 4, 1, 0, 1, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7098, 125201, 2, 101, 4, 0, 1, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7099, 125201, 2, 102, 8, 0, 1, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7100, 125201, 2, 1, 1, 1, 1, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7101, 125201, 2, 2, 1, 2, 1, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7102, 125201, 2, 3, 1, 3, 1, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7104, 125201, 3, 2, 1, 0, 1, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7105, 125201, 3, 3, 1, 0, 1, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7106, 125201, 3, 4, 1, 0, 1, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7107, 125201, 3, 5, 1, 0, 1, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7109, 125201, 3, 7, 1, 0, 1, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7110, 125201, 3, 8, 1, 0, 1, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7112, 120001, 1, 1, 1, 0, 1, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7113, 120001, 1, 2, 1, 0, 1, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7114, 120001, 1, 3, 1, 0, 1, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7115, 120001, 1, 4, 1, 0, 1, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7116, 120001, 1, 5, 1, 0, 1, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7117, 120001, 1, 101, 4, 0, 1, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7118, 120001, 1, 102, 8, 0, 1, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7119, 120001, 2, 1, 1, 0, 1, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7120, 120001, 2, 2, 1, 0, 1, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7121, 120001, 2, 3, 1, 0, 1, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7122, 120001, 2, 4, 1, 0, 1, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7123, 120001, 2, 101, 4, 0, 1, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7124, 120001, 2, 102, 8, 0, 1, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7125, 120001, 2, 1, 1, 1, 1, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7126, 120001, 2, 2, 1, 2, 1, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7127, 120001, 2, 3, 1, 3, 1, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7129, 120001, 3, 2, 1, 0, 1, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7130, 120001, 3, 3, 1, 0, 1, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7131, 120001, 3, 4, 1, 0, 1, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7132, 120001, 3, 5, 1, 0, 1, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7134, 120001, 3, 7, 1, 0, 1, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7135, 120001, 3, 8, 1, 0, 1, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7137, 125301, 1, 1, 1, 0, 1, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7138, 125301, 1, 2, 1, 0, 1, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7139, 125301, 1, 3, 1, 0, 1, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7140, 125301, 1, 4, 1, 0, 1, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7141, 125301, 1, 5, 1, 0, 1, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7142, 125301, 1, 101, 4, 0, 1, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7143, 125301, 1, 102, 8, 0, 1, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7144, 125301, 2, 1, 1, 0, 1, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7145, 125301, 2, 2, 1, 0, 1, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7146, 125301, 2, 3, 1, 0, 1, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7147, 125301, 2, 4, 1, 0, 1, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7148, 125301, 2, 101, 4, 0, 1, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7149, 125301, 2, 102, 8, 0, 1, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7150, 125301, 2, 1, 1, 1, 1, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7151, 125301, 2, 2, 1, 2, 1, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7152, 125301, 2, 3, 1, 3, 1, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7154, 125301, 3, 2, 1, 0, 1, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7155, 125301, 3, 3, 1, 0, 1, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7156, 125301, 3, 4, 1, 0, 1, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7157, 125301, 3, 5, 1, 0, 1, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7159, 125301, 3, 7, 1, 0, 1, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7160, 125301, 3, 8, 1, 0, 1, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7162, 100901, 1, 103, 12, 0, 1, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7163, 100901, 2, 103, 12, 0, 1, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7164, 125401, 1, 1, 1, 0, 1, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7165, 125401, 1, 2, 1, 0, 1, "ふふ…\nこれがレンジャーの\n制服ですか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7166, 125401, 1, 3, 1, 0, 1, "そろそろ\nパトロールの\n時間だ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7167, 125401, 1, 4, 1, 0, 1, "魔物について\nもっと知りたいな\n本を探しに行こう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7168, 125401, 1, 5, 1, 0, 1, "レンジャーになって\n体調を崩す日が\n減った気がします…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7169, 125401, 1, 101, 4, 0, 1, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7170, 125401, 1, 102, 8, 0, 1, "こんな私にも\nできることはある…\nそう信じています", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7171, 125401, 2, 1, 1, 0, 1, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7172, 125401, 2, 2, 1, 0, 1, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7173, 125401, 2, 3, 1, 0, 1, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7174, 125401, 2, 4, 1, 0, 1, "ニャットちゃん\nあなたにも\n懐いてきましたね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7175, 125401, 2, 101, 4, 0, 1, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7176, 125401, 2, 102, 8, 0, 1, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7177, 125401, 2, 1, 1, 1, 1, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7178, 125401, 2, 2, 1, 2, 1, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7179, 125401, 2, 3, 1, 3, 1, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7181, 125401, 3, 2, 1, 0, 1, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7182, 125401, 3, 3, 1, 0, 1, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7183, 125401, 3, 4, 1, 0, 1, "森の香りがします…\n今日はよく\n眠れそうです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7184, 125401, 3, 5, 1, 0, 1, "ニャットちゃんと\n同じぐらい\n暖かいです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7186, 125401, 3, 7, 1, 0, 1, "レンジャーの仕事を\nしていると\nお腹が空きます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7187, 125401, 3, 8, 1, 0, 1, "どんなに忙しくても\n読書だけは\nやめられませんね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7189, 125801, 1, 1, 1, 0, 1, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7190, 125801, 1, 2, 1, 0, 1, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7191, 125801, 1, 3, 1, 0, 1, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7192, 125801, 1, 4, 1, 0, 1, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7193, 125801, 1, 5, 1, 0, 1, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7194, 125801, 1, 101, 4, 0, 1, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7195, 125801, 1, 102, 8, 0, 1, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7196, 125801, 2, 1, 1, 0, 1, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7197, 125801, 2, 2, 1, 0, 1, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7198, 125801, 2, 3, 1, 0, 1, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7199, 125801, 2, 4, 1, 0, 1, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7200, 125801, 2, 101, 4, 0, 1, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7201, 125801, 2, 102, 8, 0, 1, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7202, 125801, 2, 1, 1, 1, 1, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7203, 125801, 2, 2, 1, 2, 1, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7204, 125801, 2, 3, 1, 3, 1, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7206, 125801, 3, 2, 1, 0, 1, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7207, 125801, 3, 3, 1, 0, 1, "と、飛びかかっては\nこない…ですよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7208, 125801, 3, 4, 1, 0, 1, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7209, 125801, 3, 5, 1, 0, 1, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7211, 125801, 3, 7, 1, 0, 1, "こんなに食べていたら\nはしたない…\nでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7212, 125801, 3, 8, 1, 0, 1, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7214, 125501, 1, 1, 1, 0, 1, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7215, 125501, 1, 2, 1, 0, 1, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7216, 125501, 1, 3, 1, 0, 1, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7217, 125501, 1, 4, 1, 0, 1, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7218, 125501, 1, 5, 1, 0, 1, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7219, 125501, 1, 101, 4, 0, 1, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7220, 125501, 1, 102, 8, 0, 1, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7221, 125501, 2, 1, 1, 0, 1, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7222, 125501, 2, 2, 1, 0, 1, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7223, 125501, 2, 3, 1, 0, 1, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7224, 125501, 2, 4, 1, 0, 1, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7225, 125501, 2, 101, 4, 0, 1, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7226, 125501, 2, 102, 8, 0, 1, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7227, 125501, 2, 1, 1, 1, 1, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7228, 125501, 2, 2, 1, 2, 1, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7229, 125501, 2, 3, 1, 3, 1, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7231, 125501, 3, 2, 1, 0, 1, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7232, 125501, 3, 3, 1, 0, 1, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7233, 125501, 3, 4, 1, 0, 1, "いい匂い…\nそれになんだか\n集中力が増したかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7234, 125501, 3, 5, 1, 0, 1, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7236, 125501, 3, 7, 1, 0, 1, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7237, 125501, 3, 8, 1, 0, 1, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7239, 125701, 1, 1, 1, 0, 1, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7240, 125701, 1, 2, 1, 0, 1, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7241, 125701, 1, 3, 1, 0, 1, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7242, 125701, 1, 4, 1, 0, 1, "せっかくですから\nより良い素材を\n自分で集めたいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7243, 125701, 1, 5, 1, 0, 1, "このポーションが\nみなさんの役に立つと\nうれしいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7244, 125701, 1, 101, 4, 0, 1, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7245, 125701, 1, 102, 8, 0, 1, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7246, 125701, 2, 1, 1, 0, 1, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7247, 125701, 2, 2, 1, 0, 1, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7248, 125701, 2, 3, 1, 0, 1, "依頼で気になる\nところがあれば\n何でもご確認ください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7249, 125701, 2, 4, 1, 0, 1, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7250, 125701, 2, 101, 4, 0, 1, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7251, 125701, 2, 102, 8, 0, 1, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7252, 125701, 2, 1, 1, 1, 1, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7253, 125701, 2, 2, 1, 2, 1, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7254, 125701, 2, 3, 1, 3, 1, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7256, 125701, 3, 2, 1, 0, 1, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7257, 125701, 3, 3, 1, 0, 1, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7258, 125701, 3, 4, 1, 0, 1, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7259, 125701, 3, 5, 1, 0, 1, "この火力…\n錬金術の調合に\n使えそうですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7261, 125701, 3, 7, 1, 0, 1, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7262, 125701, 3, 8, 1, 0, 1, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7264, 106101, 1, 103, 12, 0, 1, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7265, 106101, 2, 103, 12, 0, 1, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7266, 125601, 1, 1, 1, 0, 1, "みんなー！\nギルドハウスで\nぼくと握手！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7267, 125601, 1, 2, 1, 0, 1, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7268, 125601, 1, 3, 1, 0, 1, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7269, 125601, 1, 4, 1, 0, 1, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7270, 125601, 1, 5, 1, 0, 1, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7271, 125601, 1, 101, 4, 0, 1, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7272, 125601, 1, 102, 8, 0, 1, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7273, 125601, 2, 1, 1, 0, 1, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7274, 125601, 2, 2, 1, 0, 1, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7275, 125601, 2, 3, 1, 0, 1, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7276, 125601, 2, 4, 1, 0, 1, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7277, 125601, 2, 101, 4, 0, 1, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7278, 125601, 2, 102, 8, 0, 1, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7279, 125601, 2, 1, 1, 1, 1, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7280, 125601, 2, 2, 1, 2, 1, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7281, 125601, 2, 3, 1, 3, 1, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7283, 125601, 3, 2, 1, 0, 1, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7284, 125601, 3, 3, 1, 0, 1, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7285, 125601, 3, 4, 1, 0, 1, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7286, 125601, 3, 5, 1, 0, 1, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7288, 125601, 3, 7, 1, 0, 1, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7289, 125601, 3, 8, 1, 0, 1, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7291, 126201, 1, 1, 1, 0, 1, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7292, 126201, 1, 2, 1, 0, 1, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7293, 126201, 1, 3, 1, 0, 1, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7294, 126201, 1, 4, 1, 0, 1, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7295, 126201, 1, 5, 1, 0, 1, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7296, 126201, 1, 101, 4, 0, 1, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7297, 126201, 1, 102, 8, 0, 1, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7298, 126201, 2, 1, 1, 0, 1, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7299, 126201, 2, 2, 1, 0, 1, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7300, 126201, 2, 3, 1, 0, 1, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7301, 126201, 2, 4, 1, 0, 1, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7302, 126201, 2, 101, 4, 0, 1, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7303, 126201, 2, 102, 8, 0, 1, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7304, 126201, 2, 1, 1, 1, 1, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7305, 126201, 2, 2, 1, 2, 1, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7306, 126201, 2, 3, 1, 3, 1, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7308, 126201, 3, 2, 1, 0, 1, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7309, 126201, 3, 3, 1, 0, 1, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7310, 126201, 3, 4, 1, 0, 1, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7311, 126201, 3, 5, 1, 0, 1, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7313, 126201, 3, 7, 1, 0, 1, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7314, 126201, 3, 8, 1, 0, 1, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7316, 100801, 1, 103, 12, 0, 1, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7317, 100801, 2, 103, 12, 0, 1, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7318, 126301, 1, 1, 1, 0, 1, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7319, 126301, 1, 2, 1, 0, 1, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7320, 126301, 1, 3, 1, 0, 1, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7321, 126301, 1, 4, 1, 0, 1, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7322, 126301, 1, 5, 1, 0, 1, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7323, 126301, 1, 101, 4, 0, 1, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7324, 126301, 1, 102, 8, 0, 1, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7325, 126301, 2, 1, 1, 0, 1, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7326, 126301, 2, 2, 1, 0, 1, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7327, 126301, 2, 3, 1, 0, 1, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7328, 126301, 2, 4, 1, 0, 1, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7329, 126301, 2, 101, 4, 0, 1, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7330, 126301, 2, 102, 8, 0, 1, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7331, 126301, 2, 1, 1, 1, 1, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7332, 126301, 2, 2, 1, 2, 1, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7333, 126301, 2, 3, 1, 3, 1, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7335, 126301, 3, 2, 1, 0, 1, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7336, 126301, 3, 3, 1, 0, 1, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7337, 126301, 3, 4, 1, 0, 1, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7338, 126301, 3, 5, 1, 0, 1, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7340, 126301, 3, 7, 1, 0, 1, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7341, 126301, 3, 8, 1, 0, 1, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7343, 126401, 1, 1, 1, 0, 1, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7344, 126401, 1, 2, 1, 0, 1, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7345, 126401, 1, 3, 1, 0, 1, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7346, 126401, 1, 4, 1, 0, 1, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7347, 126401, 1, 5, 1, 0, 1, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7348, 126401, 1, 101, 4, 0, 1, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7349, 126401, 1, 102, 8, 0, 1, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7350, 126401, 2, 1, 1, 0, 1, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7351, 126401, 2, 2, 1, 0, 1, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7352, 126401, 2, 3, 1, 0, 1, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7353, 126401, 2, 4, 1, 0, 1, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7354, 126401, 2, 101, 4, 0, 1, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7355, 126401, 2, 102, 8, 0, 1, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7356, 126401, 2, 1, 1, 1, 1, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7357, 126401, 2, 2, 1, 2, 1, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7358, 126401, 2, 3, 1, 3, 1, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7360, 126401, 3, 2, 1, 0, 1, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7361, 126401, 3, 3, 1, 0, 1, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7362, 126401, 3, 4, 1, 0, 1, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7363, 126401, 3, 5, 1, 0, 1, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7365, 126401, 3, 7, 1, 0, 1, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7366, 126401, 3, 8, 1, 0, 1, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7368, 126701, 1, 1, 1, 0, 1, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7369, 126701, 1, 2, 1, 0, 1, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7370, 126701, 1, 3, 1, 0, 1, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7371, 126701, 1, 4, 1, 0, 1, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7372, 126701, 1, 5, 1, 0, 1, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7373, 126701, 1, 101, 4, 0, 1, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7374, 126701, 1, 102, 8, 0, 1, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7375, 126701, 2, 1, 1, 0, 1, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7376, 126701, 2, 2, 1, 0, 1, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7377, 126701, 2, 3, 1, 0, 1, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7378, 126701, 2, 4, 1, 0, 1, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7379, 126701, 2, 101, 4, 0, 1, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7380, 126701, 2, 102, 8, 0, 1, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7381, 126701, 2, 1, 1, 1, 1, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7382, 126701, 2, 2, 1, 2, 1, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7383, 126701, 2, 3, 1, 3, 1, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7385, 126701, 3, 2, 1, 0, 1, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7386, 126701, 3, 3, 1, 0, 1, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7387, 126701, 3, 4, 1, 0, 1, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7388, 126701, 3, 5, 1, 0, 1, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7390, 126701, 3, 7, 1, 0, 1, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7391, 126701, 3, 8, 1, 0, 1, "夏のトレンドを\n押さえておくのも\n大事ですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7393, 126701, 3, 10, 1, 0, 1, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7394, 126701, 3, 11, 1, 0, 1, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7395, 126601, 1, 1, 1, 0, 1, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7396, 126601, 1, 2, 1, 0, 1, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7397, 126601, 1, 3, 1, 0, 1, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7398, 126601, 1, 4, 1, 0, 1, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7399, 126601, 1, 5, 1, 0, 1, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7400, 126601, 1, 101, 4, 0, 1, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7401, 126601, 1, 102, 8, 0, 1, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7402, 126601, 2, 1, 1, 0, 1, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7403, 126601, 2, 2, 1, 0, 1, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7404, 126601, 2, 3, 1, 0, 1, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7405, 126601, 2, 4, 1, 0, 1, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7406, 126601, 2, 101, 4, 0, 1, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7407, 126601, 2, 102, 8, 0, 1, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7408, 126601, 2, 1, 1, 1, 1, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7409, 126601, 2, 2, 1, 2, 1, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7410, 126601, 2, 3, 1, 3, 1, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7412, 126601, 3, 2, 1, 0, 1, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7413, 126601, 3, 3, 1, 0, 1, "なかなか気に入った…\nというのは\n誰にも秘密です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7414, 126601, 3, 4, 1, 0, 1, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7415, 126601, 3, 5, 1, 0, 1, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7417, 126601, 3, 7, 1, 0, 1, "私がどのような味が\n好きなのかは…\n当然秘密です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7418, 126601, 3, 8, 1, 0, 1, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7420, 126601, 3, 10, 1, 0, 1, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7421, 126601, 3, 11, 1, 0, 1, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7422, 105401, 1, 103, 12, 0, 1, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7423, 105401, 2, 103, 12, 0, 1, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7424, 126801, 1, 1, 1, 0, 1, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7425, 126801, 1, 2, 1, 0, 1, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7426, 126801, 1, 3, 1, 0, 1, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7427, 126801, 1, 4, 1, 0, 1, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7428, 126801, 1, 5, 1, 0, 1, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7429, 126801, 1, 101, 4, 0, 1, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7430, 126801, 1, 102, 8, 0, 1, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7431, 126801, 2, 1, 1, 0, 1, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7432, 126801, 2, 2, 1, 0, 1, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7433, 126801, 2, 3, 1, 0, 1, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7434, 126801, 2, 4, 1, 0, 1, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7435, 126801, 2, 101, 4, 0, 1, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7436, 126801, 2, 102, 8, 0, 1, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7437, 126801, 2, 1, 1, 1, 1, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7438, 126801, 2, 2, 1, 2, 1, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7439, 126801, 2, 3, 1, 3, 1, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7441, 126801, 3, 2, 1, 0, 1, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7442, 126801, 3, 3, 1, 0, 1, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7443, 126801, 3, 4, 1, 0, 1, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7444, 126801, 3, 5, 1, 0, 1, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7446, 126801, 3, 7, 1, 0, 1, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7447, 126801, 3, 8, 1, 0, 1, "試験に使った参考書、\nたまには読み返して\nおかないとね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7449, 126801, 3, 10, 1, 0, 1, "ふふ♪\nいい手触りね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7450, 126801, 3, 11, 1, 0, 1, "…はふう～…\nゴロゴロするのって\n最高ね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7451, 126901, 1, 1, 1, 0, 1, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7452, 126901, 1, 2, 1, 0, 1, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7453, 126901, 1, 3, 1, 0, 1, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7454, 126901, 1, 4, 1, 0, 1, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7455, 126901, 1, 5, 1, 0, 1, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7456, 126901, 1, 101, 4, 0, 1, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7457, 126901, 1, 102, 8, 0, 1, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7458, 126901, 2, 1, 1, 0, 1, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7459, 126901, 2, 2, 1, 0, 1, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7460, 126901, 2, 3, 1, 0, 1, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7461, 126901, 2, 4, 1, 0, 1, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7462, 126901, 2, 101, 4, 0, 1, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7463, 126901, 2, 102, 8, 0, 1, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7464, 126901, 2, 1, 1, 1, 1, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7465, 126901, 2, 2, 1, 2, 1, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7466, 126901, 2, 3, 1, 3, 1, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7468, 126901, 3, 2, 1, 0, 1, "素肌にふわふわが…\n気持ちいい…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7469, 126901, 3, 3, 1, 0, 1, "うふふ…\n一緒に海…行く？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7470, 126901, 3, 4, 1, 0, 1, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7471, 126901, 3, 5, 1, 0, 1, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7473, 126901, 3, 7, 1, 0, 1, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7474, 126901, 3, 8, 1, 0, 1, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7476, 126901, 3, 10, 1, 0, 1, "これで合ってる…？\n遊び方わからなくて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7477, 126901, 3, 11, 1, 0, 1, "ふわふわ…♪\n海の底にいるみたい…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7478, 127001, 1, 1, 1, 0, 1, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7479, 127001, 1, 2, 1, 0, 1, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7480, 127001, 1, 3, 1, 0, 1, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7481, 127001, 1, 4, 1, 0, 1, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7482, 127001, 1, 5, 1, 0, 1, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7483, 127001, 1, 101, 4, 0, 1, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7484, 127001, 1, 102, 8, 0, 1, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7485, 127001, 2, 1, 1, 0, 1, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7486, 127001, 2, 2, 1, 0, 1, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7487, 127001, 2, 3, 1, 0, 1, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7488, 127001, 2, 4, 1, 0, 1, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7489, 127001, 2, 101, 4, 0, 1, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7490, 127001, 2, 102, 8, 0, 1, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7491, 127001, 2, 1, 1, 1, 1, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7492, 127001, 2, 2, 1, 2, 1, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7493, 127001, 2, 3, 1, 3, 1, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7495, 127001, 3, 2, 1, 0, 1, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7496, 127001, 3, 3, 1, 0, 1, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7497, 127001, 3, 4, 1, 0, 1, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7498, 127001, 3, 5, 1, 0, 1, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7500, 127001, 3, 7, 1, 0, 1, "クウカの口の中に\nたくさん…♪\n止まりません…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7501, 127001, 3, 8, 1, 0, 1, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7503, 127001, 3, 10, 1, 0, 1, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7504, 127001, 3, 11, 1, 0, 1, "これは…\n妄想に役立ちそうな\n感触です…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7505, 100501, 1, 103, 12, 0, 1, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7506, 100501, 2, 103, 12, 0, 1, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7507, 127101, 1, 1, 1, 0, 1, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7508, 127101, 1, 2, 1, 0, 1, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7509, 127101, 1, 3, 1, 0, 1, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7510, 127101, 1, 4, 1, 0, 1, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7511, 127101, 1, 5, 1, 0, 1, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7512, 127101, 1, 101, 4, 0, 1, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7513, 127101, 1, 102, 8, 0, 1, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7514, 127101, 2, 1, 1, 0, 1, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7515, 127101, 2, 2, 1, 0, 1, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7516, 127101, 2, 3, 1, 0, 1, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7517, 127101, 2, 4, 1, 0, 1, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7518, 127101, 2, 101, 4, 0, 1, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7519, 127101, 2, 102, 8, 0, 1, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7520, 127101, 2, 1, 1, 1, 1, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7521, 127101, 2, 2, 1, 2, 1, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7522, 127101, 2, 3, 1, 3, 1, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7524, 127101, 3, 2, 1, 0, 1, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7525, 127101, 3, 3, 1, 0, 1, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7526, 127101, 3, 4, 1, 0, 1, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7527, 127101, 3, 5, 1, 0, 1, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7529, 127101, 3, 7, 1, 0, 1, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7530, 127101, 3, 8, 1, 0, 1, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7532, 127101, 3, 10, 1, 0, 1, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7533, 127101, 3, 11, 1, 0, 1, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7534, 126501, 1, 1, 1, 0, 1, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7535, 126501, 1, 2, 1, 0, 1, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7536, 126501, 1, 3, 1, 0, 1, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7537, 126501, 1, 4, 1, 0, 1, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7538, 126501, 1, 5, 1, 0, 1, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7539, 126501, 1, 101, 4, 0, 1, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7540, 126501, 1, 102, 8, 0, 1, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7541, 126501, 2, 1, 1, 0, 1, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7542, 126501, 2, 2, 1, 0, 1, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7543, 126501, 2, 3, 1, 0, 1, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7544, 126501, 2, 4, 1, 0, 1, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7545, 126501, 2, 101, 4, 0, 1, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7546, 126501, 2, 102, 8, 0, 1, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7547, 126501, 2, 1, 1, 1, 1, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7548, 126501, 2, 2, 1, 2, 1, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7549, 126501, 2, 3, 1, 3, 1, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7551, 126501, 3, 2, 1, 0, 1, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7552, 126501, 3, 3, 1, 0, 1, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7553, 126501, 3, 4, 1, 0, 1, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7554, 126501, 3, 5, 1, 0, 1, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7556, 126501, 3, 7, 1, 0, 1, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7557, 126501, 3, 8, 1, 0, 1, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7559, 127201, 1, 1, 1, 0, 1, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7560, 127201, 1, 2, 1, 0, 1, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7561, 127201, 1, 3, 1, 0, 1, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7562, 127201, 1, 4, 1, 0, 1, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7563, 127201, 1, 5, 1, 0, 1, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7564, 127201, 1, 101, 4, 0, 1, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7565, 127201, 1, 102, 8, 0, 1, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7566, 127201, 2, 1, 1, 0, 1, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7567, 127201, 2, 2, 1, 0, 1, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7568, 127201, 2, 3, 1, 0, 1, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7569, 127201, 2, 4, 1, 0, 1, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7570, 127201, 2, 101, 4, 0, 1, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7571, 127201, 2, 102, 8, 0, 1, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7572, 127201, 2, 1, 1, 1, 1, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7573, 127201, 2, 2, 1, 2, 1, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7574, 127201, 2, 3, 1, 3, 1, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7576, 127201, 3, 2, 1, 0, 1, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7577, 127201, 3, 3, 1, 0, 1, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7578, 127201, 3, 4, 1, 0, 1, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7579, 127201, 3, 5, 1, 0, 1, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7581, 127201, 3, 7, 1, 0, 1, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7582, 127201, 3, 8, 1, 0, 1, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7584, 104301, 1, 103, 12, 0, 1, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7585, 104301, 2, 103, 12, 0, 1, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7586, 127301, 1, 1, 1, 0, 1, "魔法の練習！\nむむむ～…\nそれっ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7587, 127301, 1, 2, 1, 0, 1, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7588, 127301, 1, 3, 1, 0, 1, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7589, 127301, 1, 4, 1, 0, 1, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7590, 127301, 1, 5, 1, 0, 1, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7591, 127301, 1, 101, 4, 0, 1, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7592, 127301, 1, 102, 8, 0, 1, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7593, 127301, 2, 1, 1, 0, 1, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7594, 127301, 2, 2, 1, 0, 1, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7595, 127301, 2, 3, 1, 0, 1, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7596, 127301, 2, 4, 1, 0, 1, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7597, 127301, 2, 101, 4, 0, 1, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7598, 127301, 2, 102, 8, 0, 1, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7599, 127301, 2, 1, 1, 1, 1, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7600, 127301, 2, 2, 1, 2, 1, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7601, 127301, 2, 3, 1, 3, 1, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7603, 127301, 3, 2, 1, 0, 1, "充電ちう～\n…ガッコーのイスも\nこれにして～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7604, 127301, 3, 3, 1, 0, 1, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7605, 127301, 3, 4, 1, 0, 1, "リフレッシュの\n魔法発見！\nいい香り～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7606, 127301, 3, 5, 1, 0, 1, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7608, 127301, 3, 7, 1, 0, 1, "超おいし～♪\n学食にあったら\n毎日リピする～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7609, 127301, 3, 8, 1, 0, 1, "ふむふむ…\nねむみパない…\n続きはまた今度～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7611, 127501, 1, 1, 1, 0, 1, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7612, 127501, 1, 2, 1, 0, 1, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7613, 127501, 1, 3, 1, 0, 1, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7614, 127501, 1, 4, 1, 0, 1, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7615, 127501, 1, 5, 1, 0, 1, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7616, 127501, 1, 101, 4, 0, 1, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7617, 127501, 1, 102, 8, 0, 1, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7618, 127501, 2, 1, 1, 0, 1, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7619, 127501, 2, 2, 1, 0, 1, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7620, 127501, 2, 3, 1, 0, 1, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7621, 127501, 2, 4, 1, 0, 1, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7622, 127501, 2, 101, 4, 0, 1, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7623, 127501, 2, 102, 8, 0, 1, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7624, 127501, 2, 1, 1, 1, 1, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7625, 127501, 2, 2, 1, 2, 1, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7626, 127501, 2, 3, 1, 3, 1, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7628, 127501, 3, 2, 1, 0, 1, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7629, 127501, 3, 3, 1, 0, 1, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7630, 127501, 3, 4, 1, 0, 1, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7631, 127501, 3, 5, 1, 0, 1, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7633, 127501, 3, 7, 1, 0, 1, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7634, 127501, 3, 8, 1, 0, 1, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7636, 101401, 1, 103, 12, 0, 1, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7637, 101401, 2, 103, 12, 0, 1, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7638, 127601, 1, 1, 1, 0, 1, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7639, 127601, 1, 2, 1, 0, 1, "たまには違う服装\n違う得物を使うのも\n悪くないね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7640, 127601, 1, 3, 1, 0, 1, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7641, 127601, 1, 4, 1, 0, 1, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7642, 127601, 1, 5, 1, 0, 1, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7643, 127601, 1, 101, 4, 0, 1, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7644, 127601, 1, 102, 8, 0, 1, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7645, 127601, 2, 1, 1, 0, 1, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7646, 127601, 2, 2, 1, 0, 1, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7647, 127601, 2, 3, 1, 0, 1, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7648, 127601, 2, 4, 1, 0, 1, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7649, 127601, 2, 101, 4, 0, 1, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7650, 127601, 2, 102, 8, 0, 1, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7651, 127601, 2, 1, 1, 1, 1, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7652, 127601, 2, 2, 1, 2, 1, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7653, 127601, 2, 3, 1, 3, 1, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7655, 127601, 3, 2, 1, 0, 1, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7656, 127601, 3, 3, 1, 0, 1, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7657, 127601, 3, 4, 1, 0, 1, "この香り…\nサラサリア土産の\n香木かい？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7658, 127601, 3, 5, 1, 0, 1, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7660, 127601, 3, 7, 1, 0, 1, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7661, 127601, 3, 8, 1, 0, 1, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7663, 126001, 1, 1, 1, 0, 1, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7664, 126001, 1, 2, 1, 0, 1, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7665, 126001, 1, 3, 1, 0, 1, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7666, 126001, 1, 4, 1, 0, 1, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7667, 126001, 1, 5, 1, 0, 1, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7668, 126001, 1, 101, 4, 0, 1, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7669, 126001, 1, 102, 8, 0, 1, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7670, 126001, 2, 1, 1, 0, 1, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7671, 126001, 2, 2, 1, 0, 1, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7672, 126001, 2, 3, 1, 0, 1, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7673, 126001, 2, 4, 1, 0, 1, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7674, 126001, 2, 101, 4, 0, 1, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7675, 126001, 2, 102, 8, 0, 1, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7676, 126001, 2, 1, 1, 1, 1, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7677, 126001, 2, 2, 1, 2, 1, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7678, 126001, 2, 3, 1, 3, 1, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7680, 126001, 3, 2, 1, 0, 1, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7681, 126001, 3, 3, 1, 0, 1, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7682, 126001, 3, 4, 1, 0, 1, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7683, 126001, 3, 5, 1, 0, 1, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7685, 126001, 3, 7, 1, 0, 1, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7686, 126001, 3, 8, 1, 0, 1, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7688, 103801, 1, 103, 12, 0, 1, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7689, 103801, 2, 103, 12, 0, 1, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7690, 126101, 1, 1, 1, 0, 1, "お兄の、けはい…\nん、みつけた\nぎゅ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7691, 126101, 1, 2, 1, 0, 1, "きょろきょろ\nここも\nぱとろる、するよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7692, 126101, 1, 3, 1, 0, 1, "おうち\nにぎやか\nさみしく、ない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7693, 126101, 1, 4, 1, 0, 1, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7694, 126101, 1, 5, 1, 0, 1, "はっぴーちゃん\nどこ？\nお兄、たべた？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7695, 126101, 1, 101, 4, 0, 1, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7696, 126101, 1, 102, 8, 0, 1, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7697, 126101, 2, 1, 1, 0, 1, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7698, 126101, 2, 2, 1, 0, 1, "くろぷれしあ\nかっこいい？\nん…あざます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7699, 126101, 2, 3, 1, 0, 1, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7700, 126101, 2, 4, 1, 0, 1, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7701, 126101, 2, 101, 4, 0, 1, "かみ、りぼん\nおてつだい\nよろ\nです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7702, 126101, 2, 102, 8, 0, 1, "ここ\nすむ\nね？\nまいにち、いっしょ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7703, 126101, 2, 1, 1, 1, 1, "ぐっもにん\nあさごはん\nあさごはん", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7704, 126101, 2, 2, 1, 2, 1, "お兄\nおつかれ？\nれっつ、まいぺーす", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7705, 126101, 2, 3, 1, 3, 1, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7707, 126101, 3, 2, 1, 0, 1, "ふかふか\nパパの、おなか\nみたい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7708, 126101, 3, 3, 1, 0, 1, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7709, 126101, 3, 4, 1, 0, 1, "いい、におい\nでも、お兄\nもっと、いい、におい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7710, 126101, 3, 5, 1, 0, 1, "とりま\nお肉、やこ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7712, 126101, 3, 7, 1, 0, 1, "もぐもぐ…\nむげん肉まん、も\nきぼう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7713, 126101, 3, 8, 1, 0, 1, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7715, 127901, 1, 1, 1, 0, 1, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7716, 127901, 1, 2, 1, 0, 1, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7717, 127901, 1, 3, 1, 0, 1, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7718, 127901, 1, 4, 1, 0, 1, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7719, 127901, 1, 5, 1, 0, 1, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7720, 127901, 1, 101, 4, 0, 1, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7721, 127901, 1, 102, 8, 0, 1, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7722, 127901, 2, 1, 1, 0, 1, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7723, 127901, 2, 2, 1, 0, 1, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7724, 127901, 2, 3, 1, 0, 1, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7725, 127901, 2, 4, 1, 0, 1, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7726, 127901, 2, 101, 4, 0, 1, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7727, 127901, 2, 102, 8, 0, 1, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7728, 127901, 2, 1, 1, 1, 1, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7729, 127901, 2, 2, 1, 2, 1, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7730, 127901, 2, 3, 1, 3, 1, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7732, 127901, 3, 2, 1, 0, 1, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7733, 127901, 3, 3, 1, 0, 1, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7734, 127901, 3, 4, 1, 0, 1, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7735, 127901, 3, 5, 1, 0, 1, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7737, 127901, 3, 7, 1, 0, 1, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7738, 127901, 3, 8, 1, 0, 1, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7740, 127901, 3, 14, 1, 0, 1, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7741, 127901, 3, 15, 1, 0, 1, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7742, 106601, 1, 103, 12, 0, 1, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7743, 106601, 2, 103, 12, 0, 1, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7744, 128001, 1, 1, 1, 0, 1, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7745, 128001, 1, 2, 1, 0, 1, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7746, 128001, 1, 3, 1, 0, 1, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7747, 128001, 1, 4, 1, 0, 1, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7748, 128001, 1, 5, 1, 0, 1, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7749, 128001, 1, 101, 4, 0, 1, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7750, 128001, 1, 102, 8, 0, 1, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7751, 128001, 2, 1, 1, 0, 1, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7752, 128001, 2, 2, 1, 0, 1, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7753, 128001, 2, 3, 1, 0, 1, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7754, 128001, 2, 4, 1, 0, 1, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7755, 128001, 2, 101, 4, 0, 1, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7756, 128001, 2, 102, 8, 0, 1, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7757, 128001, 2, 1, 1, 1, 1, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7758, 128001, 2, 2, 1, 2, 1, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7759, 128001, 2, 3, 1, 3, 1, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7761, 128001, 3, 2, 1, 0, 1, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7762, 128001, 3, 3, 1, 0, 1, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7763, 128001, 3, 4, 1, 0, 1, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7764, 128001, 3, 5, 1, 0, 1, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7766, 128001, 3, 7, 1, 0, 1, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7767, 128001, 3, 8, 1, 0, 1, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7769, 128001, 3, 14, 1, 0, 1, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7770, 128001, 3, 15, 1, 0, 1, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7771, 128101, 1, 1, 1, 0, 1, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7772, 128101, 1, 2, 1, 0, 1, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7773, 128101, 1, 3, 1, 0, 1, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7774, 128101, 1, 4, 1, 0, 1, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7775, 128101, 1, 5, 1, 0, 1, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7776, 128101, 1, 101, 4, 0, 1, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7777, 128101, 1, 102, 8, 0, 1, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7778, 128101, 2, 1, 1, 0, 1, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7779, 128101, 2, 2, 1, 0, 1, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7780, 128101, 2, 3, 1, 0, 1, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7781, 128101, 2, 4, 1, 0, 1, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7782, 128101, 2, 101, 4, 0, 1, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7783, 128101, 2, 102, 8, 0, 1, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7784, 128101, 2, 1, 1, 1, 1, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7785, 128101, 2, 2, 1, 2, 1, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7786, 128101, 2, 3, 1, 3, 1, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7788, 128101, 3, 2, 1, 0, 1, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7789, 128101, 3, 3, 1, 0, 1, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7790, 128101, 3, 4, 1, 0, 1, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7791, 128101, 3, 5, 1, 0, 1, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7793, 128101, 3, 7, 1, 0, 1, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7794, 128101, 3, 8, 1, 0, 1, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7797, 128201, 1, 1, 1, 0, 1, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7798, 128201, 1, 2, 1, 0, 1, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7799, 128201, 1, 3, 1, 0, 1, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7800, 128201, 1, 4, 1, 0, 1, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7801, 128201, 1, 5, 1, 0, 1, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7802, 128201, 1, 101, 4, 0, 1, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7803, 128201, 1, 102, 8, 0, 1, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7804, 128201, 2, 1, 1, 0, 1, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7805, 128201, 2, 2, 1, 0, 1, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7806, 128201, 2, 3, 1, 0, 1, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7807, 128201, 2, 4, 1, 0, 1, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7808, 128201, 2, 101, 4, 0, 1, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7809, 128201, 2, 102, 8, 0, 1, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7810, 128201, 2, 1, 1, 1, 1, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7811, 128201, 2, 2, 1, 2, 1, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7812, 128201, 2, 3, 1, 3, 1, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7814, 128201, 3, 2, 1, 0, 1, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7815, 128201, 3, 3, 1, 0, 1, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7816, 128201, 3, 4, 1, 0, 1, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7817, 128201, 3, 5, 1, 0, 1, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7819, 128201, 3, 7, 1, 0, 1, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7820, 128201, 3, 8, 1, 0, 1, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7823, 128301, 1, 1, 1, 0, 1, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7824, 128301, 1, 2, 1, 0, 1, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7825, 128301, 1, 3, 1, 0, 1, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7826, 128301, 1, 4, 1, 0, 1, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7827, 128301, 1, 5, 1, 0, 1, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7828, 128301, 1, 101, 4, 0, 1, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7829, 128301, 1, 102, 8, 0, 1, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7830, 128301, 2, 1, 1, 0, 1, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7831, 128301, 2, 2, 1, 0, 1, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7832, 128301, 2, 3, 1, 0, 1, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7833, 128301, 2, 4, 1, 0, 1, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7834, 128301, 2, 101, 4, 0, 1, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7835, 128301, 2, 102, 8, 0, 1, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7836, 128301, 2, 1, 1, 1, 1, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7837, 128301, 2, 2, 1, 2, 1, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7838, 128301, 2, 3, 1, 3, 1, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7840, 128301, 3, 2, 1, 0, 1, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7841, 128301, 3, 3, 1, 0, 1, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7842, 128301, 3, 4, 1, 0, 1, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7843, 128301, 3, 5, 1, 0, 1, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7845, 128301, 3, 7, 1, 0, 1, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7846, 128301, 3, 8, 1, 0, 1, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7848, 128401, 1, 1, 1, 0, 1, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7849, 128401, 1, 2, 1, 0, 1, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7850, 128401, 1, 3, 1, 0, 1, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7851, 128401, 1, 4, 1, 0, 1, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7852, 128401, 1, 5, 1, 0, 1, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7853, 128401, 1, 101, 4, 0, 1, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7854, 128401, 1, 102, 8, 0, 1, "来年も彼の隣で\n初日の出が\n見られるといいな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7855, 128401, 2, 1, 1, 0, 1, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7856, 128401, 2, 2, 1, 0, 1, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7857, 128401, 2, 3, 1, 0, 1, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7858, 128401, 2, 4, 1, 0, 1, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7859, 128401, 2, 101, 4, 0, 1, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7860, 128401, 2, 102, 8, 0, 1, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7861, 128401, 2, 1, 1, 1, 1, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7862, 128401, 2, 2, 1, 2, 1, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7863, 128401, 2, 3, 1, 3, 1, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7865, 128401, 3, 2, 1, 0, 1, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7866, 128401, 3, 3, 1, 0, 1, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7867, 128401, 3, 4, 1, 0, 1, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7868, 128401, 3, 5, 1, 0, 1, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7870, 128401, 3, 7, 1, 0, 1, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7871, 128401, 3, 8, 1, 0, 1, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7873, 106501, 1, 103, 12, 0, 1, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7874, 106501, 2, 103, 12, 0, 1, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7875, 128501, 1, 1, 1, 0, 1, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7876, 128501, 1, 2, 1, 0, 1, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7877, 128501, 1, 3, 1, 0, 1, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7878, 128501, 1, 4, 1, 0, 1, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7879, 128501, 1, 5, 1, 0, 1, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7880, 128501, 1, 101, 4, 0, 1, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7881, 128501, 1, 102, 8, 0, 1, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7882, 128501, 2, 1, 1, 0, 1, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7883, 128501, 2, 2, 1, 0, 1, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7884, 128501, 2, 3, 1, 0, 1, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7885, 128501, 2, 4, 1, 0, 1, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7886, 128501, 2, 101, 4, 0, 1, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7887, 128501, 2, 102, 8, 0, 1, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7888, 128501, 2, 1, 1, 1, 1, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7889, 128501, 2, 2, 1, 2, 1, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7890, 128501, 2, 3, 1, 3, 1, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7892, 128501, 3, 2, 1, 0, 1, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7893, 128501, 3, 3, 1, 0, 1, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7894, 128501, 3, 4, 1, 0, 1, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7895, 128501, 3, 5, 1, 0, 1, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7897, 128501, 3, 7, 1, 0, 1, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7898, 128501, 3, 8, 1, 0, 1, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7900, 128801, 1, 1, 1, 0, 1, "この新しい力…\nさらに使いこなせる\nようになるには…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7901, 128801, 1, 2, 1, 0, 1, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7902, 128801, 1, 3, 1, 0, 1, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7903, 128801, 1, 4, 1, 0, 1, "風の力の制御…\n段々とコツが\n掴めてきたな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7904, 128801, 1, 5, 1, 0, 1, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7905, 128801, 1, 101, 4, 0, 1, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7906, 128801, 1, 102, 8, 0, 1, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7907, 128801, 2, 1, 1, 0, 1, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7908, 128801, 2, 2, 1, 0, 1, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7909, 128801, 2, 3, 1, 0, 1, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7910, 128801, 2, 4, 1, 0, 1, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7911, 128801, 2, 101, 4, 0, 1, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7912, 128801, 2, 102, 8, 0, 1, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7913, 128801, 2, 1, 1, 1, 1, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7914, 128801, 2, 2, 1, 2, 1, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7915, 128801, 2, 3, 1, 3, 1, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7917, 128801, 3, 2, 1, 0, 1, "休める時は休む…\n戦い続けるには\n必要なことだからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7918, 128801, 3, 3, 1, 0, 1, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7919, 128801, 3, 4, 1, 0, 1, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7920, 128801, 3, 5, 1, 0, 1, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7922, 128801, 3, 7, 1, 0, 1, "この味付け…\n今度試してみようか\n味見は任せたからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7923, 128801, 3, 8, 1, 0, 1, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7925, 128901, 1, 1, 1, 0, 1, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7926, 128901, 1, 2, 1, 0, 1, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7927, 128901, 1, 3, 1, 0, 1, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7928, 128901, 1, 4, 1, 0, 1, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7929, 128901, 1, 5, 1, 0, 1, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7930, 128901, 1, 101, 4, 0, 1, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7931, 128901, 1, 102, 8, 0, 1, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7932, 128901, 2, 1, 1, 0, 1, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7933, 128901, 2, 2, 1, 0, 1, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7934, 128901, 2, 3, 1, 0, 1, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7935, 128901, 2, 4, 1, 0, 1, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7936, 128901, 2, 101, 4, 0, 1, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7937, 128901, 2, 102, 8, 0, 1, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7938, 128901, 2, 1, 1, 1, 1, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7939, 128901, 2, 2, 1, 2, 1, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7940, 128901, 2, 3, 1, 3, 1, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7942, 128901, 3, 2, 1, 0, 1, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7943, 128901, 3, 3, 1, 0, 1, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7944, 128901, 3, 4, 1, 0, 1, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7945, 128901, 3, 5, 1, 0, 1, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7947, 128901, 3, 7, 1, 0, 1, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7948, 128901, 3, 8, 1, 0, 1, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7950, 128701, 1, 1, 1, 0, 1, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7951, 128701, 1, 2, 1, 0, 1, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7952, 128701, 1, 3, 1, 0, 1, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7953, 128701, 1, 4, 1, 0, 1, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7954, 128701, 1, 5, 1, 0, 1, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7955, 128701, 1, 101, 4, 0, 1, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7956, 128701, 1, 102, 8, 0, 1, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7957, 128701, 2, 1, 1, 0, 1, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7958, 128701, 2, 2, 1, 0, 1, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7959, 128701, 2, 3, 1, 0, 1, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7960, 128701, 2, 4, 1, 0, 1, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7961, 128701, 2, 101, 4, 0, 1, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7962, 128701, 2, 102, 8, 0, 1, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7963, 128701, 2, 1, 1, 1, 1, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7964, 128701, 2, 2, 1, 2, 1, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7965, 128701, 2, 3, 1, 3, 1, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7967, 128701, 3, 2, 1, 0, 1, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7968, 128701, 3, 3, 1, 0, 1, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7969, 128701, 3, 4, 1, 0, 1, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7970, 128701, 3, 5, 1, 0, 1, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7972, 128701, 3, 7, 1, 0, 1, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7973, 128701, 3, 8, 1, 0, 1, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7975, 129001, 1, 1, 1, 0, 1, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7976, 129001, 1, 2, 1, 0, 1, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7977, 129001, 1, 3, 1, 0, 1, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7978, 129001, 1, 4, 1, 0, 1, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7979, 129001, 1, 5, 1, 0, 1, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7980, 129001, 1, 101, 4, 0, 1, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7981, 129001, 1, 102, 8, 0, 1, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7982, 129001, 2, 1, 1, 0, 1, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7983, 129001, 2, 2, 1, 0, 1, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7984, 129001, 2, 3, 1, 0, 1, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7985, 129001, 2, 4, 1, 0, 1, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7986, 129001, 2, 101, 4, 0, 1, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7987, 129001, 2, 102, 8, 0, 1, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7988, 129001, 2, 1, 1, 1, 1, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7989, 129001, 2, 2, 1, 2, 1, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7990, 129001, 2, 3, 1, 3, 1, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7992, 129001, 3, 2, 1, 0, 1, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7993, 129001, 3, 3, 1, 0, 1, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7994, 129001, 3, 4, 1, 0, 1, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7995, 129001, 3, 5, 1, 0, 1, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7997, 129001, 3, 7, 1, 0, 1, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (7998, 129001, 3, 8, 1, 0, 1, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8000, 129301, 1, 1, 1, 0, 1, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8001, 129301, 1, 2, 1, 0, 1, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8002, 129301, 1, 3, 1, 0, 1, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8003, 129301, 1, 4, 1, 0, 1, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8004, 129301, 1, 5, 1, 0, 1, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8005, 129301, 1, 101, 4, 0, 1, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8006, 129301, 1, 102, 8, 0, 1, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8007, 129301, 2, 1, 1, 0, 1, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8008, 129301, 2, 2, 1, 0, 1, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8009, 129301, 2, 3, 1, 0, 1, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8010, 129301, 2, 4, 1, 0, 1, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8011, 129301, 2, 101, 4, 0, 1, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8012, 129301, 2, 102, 8, 0, 1, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8013, 129301, 2, 1, 1, 1, 1, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8014, 129301, 2, 2, 1, 2, 1, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8015, 129301, 2, 3, 1, 3, 1, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8017, 129301, 3, 2, 1, 0, 1, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8018, 129301, 3, 3, 1, 0, 1, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8019, 129301, 3, 4, 1, 0, 1, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8020, 129301, 3, 5, 1, 0, 1, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8022, 129301, 3, 7, 1, 0, 1, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8023, 129301, 3, 8, 1, 0, 1, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8025, 129301, 3, 10, 1, 0, 1, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 0);
INSERT INTO `v1_c9931887bec97fb289068ada9e7bf037b812878740f23c13c8c699111474e332` VALUES (8026, 129301, 3, 11, 1, 0, 1, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 0);
