INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100202, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 0, 2, 88, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1, 2, 89, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 2, 2, 90, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 3, 2, 91, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "よし…\nこれなら\n褒めてもらえるかな？", 0, 3, 92, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "がんばることも\n必要だけど\nお休みも大切だよね", 0, 3, 93, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "かわいい～♪\n騎士クンは\nこういうのって好き？", 0, 3, 94, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "戦う前にもこうやって\n落ち着けたら\nいいのに", 0, 3, 95, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "あの…！\nよ、良かったら\n一緒に…！", 0, 3, 96, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 0, 3, 97, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 0, 3, 98, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100202, "…やっぱり\n月刊恋愛指南は\nためになる…", 0, 3, 99, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "装備の手入れでも\nしようかな…", 0, 1, 101, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 0, 1, 102, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0, 1, 103, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "仲間なんていらない\nそう思ってた頃も\nあったな…", 0, 1, 104, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 0, 1, 105, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100301, "彼は\nどこまで強くなるのか\n私も負けられないな", 0, 1, 106, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100301, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0, 1, 107, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 0, 2, 108, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 0, 2, 109, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "なに？　私に相談？\n戦いについてなら\n喜んで", 0, 2, 110, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 0, 2, 111, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100301, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 0, 2, 112, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100301, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0, 2, 113, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "おはよう\n今日も充実した\n一日にしていこう", 1, 2, 114, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 2, 2, 115, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 3, 2, 116, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 0, 3, 117, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 0, 3, 118, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0, 3, 119, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 0, 3, 120, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 0, 3, 121, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0, 3, 122, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 0, 3, 123, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100301, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0, 3, 124, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 0, 1, 126, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0, 1, 127, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "彼は\nどこまで強くなるのか\n私も負けられないな", 0, 1, 128, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 0, 1, 129, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 0, 1, 130, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100302, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 0, 1, 131, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100302, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0, 1, 132, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "どうしたの？\nなんでもない？\nそれなら良かった", 0, 2, 133, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 0, 2, 134, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 0, 2, 135, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 0, 2, 136, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100302, "ひょっとして\n今のも一本だなんて\n言わないよね？", 0, 2, 137, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100302, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0, 2, 138, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1, 2, 139, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "休息も時には\n必要だと、仲間から\n教わったよ", 2, 2, 140, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 3, 2, 141, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 0, 3, 142, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "休息があってこそ\n万全な状態で\n戦えるんだ", 0, 3, 143, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0, 3, 144, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "体と同様に\n心もほぐれるね…\nはぁ…", 0, 3, 145, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 0, 3, 146, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0, 3, 147, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "修行のせいか\n最近食べる量が\n増えてきたような…", 0, 3, 148, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100302, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0, 3, 149, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0, 1, 151, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "落とし穴…どこに\n作ったんだっけ？", 0, 1, 152, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0, 1, 153, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0, 1, 154, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "かくれんぼする人\nこの指、と～まれ！", 0, 1, 155, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100401, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 0, 1, 156, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100401, "ミソギと遊ぶ？\nミソギに遊ばれる？", 0, 1, 157, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 0, 2, 158, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 0, 2, 159, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 0, 2, 160, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0, 2, 161, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100401, "ここ、ミソギ探検隊の\n基地にしようよ！", 0, 2, 162, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100401, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0, 2, 163, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1, 2, 164, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 2, 2, 165, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 3, 2, 166, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "イタズラには\nお色気も必要…\nうん、完璧！", 0, 3, 167, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 0, 3, 168, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 0, 3, 169, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 0, 3, 170, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 0, 3, 171, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "探検家は\n体力勝負だよー！", 0, 3, 172, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 0, 3, 173, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100401, "こういう所に\n宝の地図が\n隠れてるんだよね～", 0, 3, 174, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0, 1, 176, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 0, 1, 177, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0, 1, 178, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0, 1, 179, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "かくれんぼする人\nこの指、と～まれ！", 0, 1, 180, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100402, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0, 1, 181, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100402, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 0, 1, 182, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 0, 2, 183, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 0, 2, 184, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "将来の夢は探検家！\n探したい物はないけど", 0, 2, 185, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 0, 2, 186, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100402, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0, 2, 187, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100402, "ここ、ミソギ探検隊の\n基地にしようよ！", 0, 2, 188, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1, 2, 189, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "にいちゃんが昼寝したら\nイタズラし放題♪", 2, 2, 190, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 3, 2, 191, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "イタズラには\nお色気も必要…\nうん、完璧！", 0, 3, 192, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 0, 3, 193, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 0, 3, 194, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 0, 3, 195, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 0, 3, 196, 5, 1, 0);
