INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100402, "こういう所に\n宝の地図が\n隠れてるんだよね～", 0, 3, 199, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 0, 1, 201, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 0, 1, 202, 2, 1, 0);
