INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100601, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 0, 1, 207, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 0, 2, 208, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 0, 2, 209, 2, 1, 0);
