INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "お兄ちゃん、イジワル\nしないでください…", 0, 2, 211, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100601, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 0, 2, 212, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100601, "コーチ、\n今日も特訓\nお願いします！", 0, 2, 213, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 2, 214, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 2, 2, 215, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "夜はこれからですよ\nお兄ちゃん♪", 3, 2, 216, 3, 1, 0);
